  [426] = {
    '제분소 습격',
    {'스컬지가 아가만드 제분소에 기지를 구축하려고 하네. 기지를 구축하고 나면 병력을 정비하여 티리스팔의 다른 지역을 공격하려 할 거야.$B$B제분소에 있는 이 스컬지들을 처치하라는 명령이 내려졌네. 제분소로 가서 덜그럭거리는 해골병사와 금이간 해골병사를 처치하여 톱니모양 늑골을 모으고, 검은눈 해골마법사를 처치하여 저주받은 해골을 모아오게.$B$B제분소로 가려면 서쪽 길을 따라가다가 다리를 건넌 후에는 세 갈래 길에서 북쪽 길을 택해 계속 북쪽으로 가게. 아가만드 농장의 풍차가 보이면 전투를 준비해야 할 거야.', },
    {'톱니모양 늑골 5개와 저주받은 해골 3개를 모은 다음 브릴에 있는 죽음경비병 딜링거에게 가야 합니다.', },
  },
  [427] = {
    '붉은십자군과의 전쟁',
    {'집행관 아렌이 보내준 문서 덕분에 이제 티리스팔 숲에 포진한 붉은십자군의 정확한 위치를 알게 되었네. 때문에 저 야비한 붉은십자군과의 전투에서 훨씬 유리해지겠군.$B$B하지만 우리 죽음경비병들에게 더 큰 걱정거리가 있네. 바로 리치 왕의 군대가 매일 밤 급속도로 늘어나고 있다는 거야. 그래서 붉은십자군 정도는 자네처럼 배포 있는 $C|1이;가; 처리해 줬으면 좋겠는데...$B$B어둠의 여왕 폐하를 섬길 만한 자격이 있는지 증명해 보여주지 않겠나? 솔리덴 농장을 지나 서쪽의 탑으로 가서 붉은십자군 전사 10명을 처치해 주게.', },
    {'브릴에 있는 집행관 자이건드가 붉은십자군 전사 10명을 처치해 달라고 했습니다.', },
  },
  [428] = {
    '실종된 죽음추적자',
    {'몇 주 전 우리 죽음추적자 중 레인 요릭과 퀸 요릭이라는 두 병사를 은빛소나무 숲 북부 지역으로 정찰 보냈는데 아직 소식이 없어 걱정일세.$B$B이 둘이 스컬지에게 희생된 건 아닌지 확인할 필요가 있으니 이들을 찾아 주게.$B$B그들의 첫 번째 목표는 은빛소나무 숲 북쪽에 있는 농장을 정찰하는 것이었으니 그곳을 먼저 수색하는 게 좋을 거야.', },
    {'죽음추적자 퀸 요릭과 레인 요릭을 찾아야 합니다.', },
  },
  [429] = {
    '늑대의 심장',
    {'제 오빠 퀸이 포악한 이바르라는 구울에게 심각한 부상을 입었어요. 적절한 마법 치료 없이는 가망이 없을 것 같아서 걱정이에요. 전 연금술사는 아니지만 그들이 검은늑대의 심장으로 치료 물약을 만들 수 있다는 건 알고 있답니다.$B$B이 짐승들의 심장을 모아서 남쪽의 공동묘지에 있는 연금술사 렌퍼럴에게 가져다주시고 치유 물약이 완성되면 여기로 다시 가져와 주세요. 검은늑대들은 이 근처나 동쪽의 말덴의 과수원에 많이 있을 거예요.', },
    {'변색된 검은늑대 심장 6개를 모아 공동묘지에 있는 연금술사 렌퍼럴에게 가져가야 합니다.', },
  },
  [432] = {
    '저주받을 트로그',
    {'빌어먹을 트로그들, 제 작업장을 난장판으로 만들어 버렸습니다! 저것 보십시오, 화약통을 또 하나 가져가고 있잖습니까! 당장 내려가서 모조리 해치워 버렸으면 좋겠군요! 전부 다 말입니다!$B$B으... 참아야지. 이렇게 스트레스받고 화내면 건강에 안 좋아... 그런데 이제 남은 게 뭐가 있지? 젠장, 아무것도 없네!$B$B제 손으로 다 없애버렸으면 좋겠지만 이제 저도 늙었습니다! 당신이 채석장으로 내려가서 저 더러운 것들을 해치워 주십시오! 보상은 꼭 해 드리겠습니다! 눈에 보이는 대로 없애버리십시오!', },
    nil,
  },
  [433] = {
    '공무 수행',
    nil,
    nil,
  },
  [435] = {
    '엘런드 경호',
    {'밖에 있는 늑대들은 떠날 생각을 하지 않고 날 사납게 노려보고 있소. 내가 저 문으로 한 발짝이라도 내딛으면 놈들이 덤벼들 거요. 놈들은 너무 굶주린 나머지 나를 맛있는 먹잇감으로 보고 있는 것이 분명하오.$B$B동료인 레인 요릭에게 보고서를 가져다줘야 하는데, 그러려면 아무래도 누군가 날 좀 보호해 줘야 할 것 같소.$B$B당신이 날 좀 도와주겠소? 늑대들이 너무 많아서 혼자서는 힘들지도 모르니 친구들이 더 있다면 도움이 될 것이오.', },
    {'레인 요릭이 있는 곳까지 늑대들을 물리치고 엘런드를 호위해야 합니다.', },
  },
  [436] = {
    '아이언밴드의 발굴현장',
    {'발굴조사단장 아이언밴드가 호수 동쪽의 고대 유적지에서 발굴 작업을 지휘하고 있소. 그런데 최근 작업 진행 속도가 더뎌지고 있소. 지금까지 우리가 제공해 준 보급품의 양을 고려하면 좀 이상한 일인데...$B$B아이언밴드는 결과를 중시하는 완고하고 정직한 드워프라서... 혹시 인부들이 그에게 반발하고 있는 건 아닌지 걱정이 되는군.$B$B아이언밴드의 발굴현장으로 가서 마그마 펠휴와 이야기해 보시오. 현장의 세부사항을 관리하니까 작업 속도가 느린 이유를 알고 있을 거요. 발굴현장으로 가려면 호수 남쪽 끝을 돌아 동쪽으로 가면 되오.', },
    {'탐험가 펠휴와 대화해야 합니다.', },
  },
  [437] = {
    '죽음의 농장',
    {'은빛소나무 숲의 초기 정찰 보고에 따르면 썩은가죽 일족이 북쪽의 오래된 농장인 죽음의 농장을 본거지로 삼고 있다고 하네. 그들에게 산발적인 공격을 시도했지만, 마법을 사용하여 우리 군을 궤멸시키는 밴시 나이트래쉬 때문에 번번이 실패하고 말았네.$B$B나이트래쉬는 반드시 없애야 해. 죽음의 농장으로 가서 썩은가죽 일족을 공격하게. 나이트래쉬가 나타날 때까지 썩은가죽 일족을 처치하고 그녀가 나타나면 같이 처치하도록 하게.$B$B이제 그녀의 정수를 한낱 먼지로 만들어 버릴 때가 온 게야. 그 증거로 내게 그 정수를 가져오게.', },
    {'죽음의 농장에서 썩은가죽 놀들을 처치하십시오. 나이트래쉬가 나타나면 그녀를 처치하십시오.$B$B공동묘지에 있는 고위집행관 하드렉에게 나이트래쉬의 정수를 가져가야 합니다.', },
  },
  [442] = {
    '펜리스 섬 공격',
    {'이제 은빛소나무 숲에 있는 스컬지의 본거지인 펜리스 섬을 공격할 때가 되었네.$B$B다시 펜리스 섬으로 돌아가 까마귀발톱 쑤울을 처치해야 하네. 그리고 그의 머리카락을 가져오게! 이번 임무는 매우 중요한 만큼 위험도 커. 반드시 성공해야 하네.', },
    {'공동묘지에 있는 고위집행관 하드렉에게 쑤울의 머리카락을 가져가야 합니다.', },
  },
  [443] = {
    '썩은가죽 영액',
    nil,
    nil,
  },
  [445] = {
    '은빛소나무 숲으로의 배달',
    {'지금쯤이면 자네도 확실히 알겠지만 실바나스 여왕님을 모시며 일하는 우리에게는 시간이 무엇보다 중요하네.$B$B왕립 연금술 학회의 회원으로서 내가 알아낸 지식을 동료와 교환해야 하는 것이 내 의무지. 그래야만 언젠가 어둠의 여왕님이 그토록 원하시는 새로운 역병을 만들어낼 수 있을 테니까 말일세.$B$B은빛소나무 숲의 공동묘지에 있는 연금술사 렌퍼럴에게 이 연구 결과를 전달해 주게. 공동묘지로 가려면 브릴에서 길을 따라 남쪽으로 가다가 언더시티가 나오면 거기서 남서쪽으로 가면 되네.', },
    nil,
  },
  [446] = {
    '까마귀발톱 쑤울',
    nil,
    nil,
  },
  [447] = {
    '죽음의 비법',
    {'아서스의 군대는 병력면에서 압도적인 우위를 차지하고 있네. 하지만 새로운 역병을 사용한다면 스컬지 군대와 인간의 영토를 모두 초토화할 수 있지. 내 연구 결과에 따르면 거미의 피와 불곰 심장에서 뽑아낸 독을 혼합하면 치명적인 비약을 만들 수 있다네.$B$B북서쪽에 위치한 암흑 땅거미굴의 거미와 은빛소나무 숲 전역에 돌아다니는 곰에게서 표본을 모아오도록 하게. 그리고 그 재료들을 언더시티에 있는 왕립 연금술 학회의 수석 연금술사 파라넬님께 전해 주게.', },
    {'불곰 심장 6개와 땅거미 점액 6개를 모아 언더시티에 있는 수석 연금술사 파라넬에게 가져가야 합니다.', },
  },
  [450] = {
    '죽음의 비법',
    nil,
    nil,
  },
  [451] = {
    '죽음의 비법',
    {'베랄드의 일지에 따르면 호수덤불괴물과 호수늪지괴물의 이끼가 새로운 역병 제조에 유용할지도 모르겠군. 이 짐승들은 동쪽의 로다미어 호수에 있는 섬에 살고 있지.$B$B또한 그는 일지에 썩은지느러미 멀록에게서 발견되는 매우 희귀한 굳어진 종양에 대해서도 언급하고 있네. 그 종양은 너무 희귀해서 베랄드도 하나밖에 입수하지 못한 듯해.$B$B이끼 표본과 함께 그 희귀하고 단단한 종양을 수집해서 언더시티에 있는 수석 연금술사 파라넬님께 가져가도록 하게.', },
    {'호수늪지이끼 6개, 호수덤불이끼 6개, 그리고 굳어진 종양 1개를 언더시티에 있는 수석 연금술사 파라넬에게 가져가야 합니다.', },
  },
  [452] = {
    '장작나무 마을 매복',
    {'오, 제때에 와줬군요!$B$B곧 장작나무 원로들이 돌아올 거예요. 아루갈이 성채에 머무르는 데는 다른 이유가 있답니다. 그는 원로들을 꼭두각시로 만들어 장작나무 마을 주민들을 마음대로 조종하면서 부하로 삼고 있어요. 장작나무 마을 원로들만 없다면, 아루갈의 부하 노릇을 하고 있는 마을 주민들도 모두 풀려나게 될 거예요.$B$B하지만 혼자서 모든 원로들을 상대할 수는 없으니 당신 도움이 필요해요. 나와 함께 싸워 주실 수 있나요? 우리 함께 실바나스 여왕님의 이름으로 원로들을 처단하자고요.', },
    {'죽음추적자 페를레이아를 도와 장작나무 마을 원로원들을 처치해야 합니다.', },
  },
  [453] = {
    '의문의 남자 찾기',
    {'행운이 있기를... 그 사람을 꼭 찾으시기 바랍니다. 그 친구가 살아 있어야 할 텐데... $B$B어둠골의 안전한 울타리를 벗어났으니 야생 짐승들의 먹이가 되었을 수도 있거든요.', },
    {'의문의 남자를 찾아야 합니다. 지금까지 얻은 단서는 다음과 같습니다.$B$B어둠골에서 태어난 사람이 아닙니다.$B$B신경질적이고 신경과민인 사람입니다.$B$B어둠골을 떠나 서쪽으로 갔습니다.', },
  },
  [455] = {
    '알가즈의 위기',
    {'용아귀 오크들이 저습지를 차지한 것도 모자라, 이제 던 알가즈에까지 손을 뻗어오고 있소! 이곳에 있는 놈들의 거점을 제거하지 않으면 저습지에 있는 우리의 항만 시설과 요새인, 메네실 항구에서 모단 호수에 이르는 길목을 차단당하게 되오.$B$B던 알가즈를 가로질러 저습지 너머로 이동하시오. 그리고 가는 길에 오크 녀석들도 좀 처치해 주시오. 그런 다음 메네실 항구에 있는 발스탁 아이언조에게 보고하도록 하시오.$B$B메네실은 던 알가즈를 지나 길을 따라 북쪽으로 간 다음 갈림길에서 서쪽으로 가면 되오.$B$B우린 당신만 믿겠으니 우릴 실망시키지 마시오.', },
    {'던 알가즈를 가로질러 가야 합니다.$B$B용아귀 정찰병 8명과 용아귀 그런트 6명을 처치해야 합니다.$B$B메네실 항구에 있는 발스탁 아이언조에게 보고하십시오.', },
  },
  [456] = {
    '자연의 균형',
    {'어서 오십시오. 저는 관리인 일사레인이라고 합니다. 그늘 협곡에서 자연의 균형을 유지하는 일을 맡고 있지요.$B$B올해는 유난히 봄비가 많이 내려서 많은 짐승이 피해를 입었습니다. 하지만 어떤 짐승에게는 오히려 좋은 일이 되기도 했지요. 특히 엉겅퀴멧돼지와 밤호랑이의 수가 눈에 띄게 늘었는데 우리에겐 골칫거리가 아닐 수 없습니다. 그늘 협곡에는 짐승을 먹일 식량이 한정되어 있기 때문에 생태계의 균형을 유지하는 것은 매우 중요한 일입니다.$B$B어서 가십시오, 젊은 $C|1이여;여;. 가서 멧돼지와 밤호랑이의 수를 줄여 자연의 균형을 지켜주십시오.', },
    nil,
  },
  [457] = {
    '자연의 균형',
    nil,
    nil,
  },
  [458] = {
    '숲의 수호자',
    nil,
    {'타린드렐라라고 알려진 드리아드를 찾아야 합니다.', },
  },
  [459] = {
    '숲의 수호자',
    nil,
    nil,
  },
  [463] = {
    '신록수호자',
    {'신록수호자에 대해 들어봤나? 혹시 그 짐승을 찾고 있는 건가? 흠, 그렇다면 당신 정신 나간게야. 뭐, 목숨 아까운 줄 모르는 바보를 구태여 내가 뜯어말릴 필요는 없겠지.$B$B그 짐승을 찾고 있다면 던 모드르 쪽으로 갈라지는 길 동쪽에 있는 습지 근처에 산다고 하니까 그쪽으로 가 보라고. 악어 사이에 숨어 있다고 하는 친구도 있었지... 그보다 더할 수도 있지!$B$B그리고 참, 돈은 여기 놓고 가라고. 거기 가 봤자 필요 없을 테니까 말이야. 그 늙은 녹색 짐승이 당신을 두 동강 내서 씹어 먹을 때 금화에 그 이빨 자국이 남게 되는 걸 바라지 않겠지?', },
    {'신록수호자를 찾아야 합니다.', },
  },
  [464] = {
    '전쟁 깃발',
    {'용아귀 오크는 자신들이 부리던 붉은용을 통제할 수 없게 되면서 2차 대전쟁이 종결될 즈음에 거의 섬멸됐었네. 용들은 옛 주인에게 불만이 많았었는지 무서운 기세로 용아귀 부족을 공격했지. 얼마 남지 않은 생존자들은 그림 바톨을 탈출하여 웰가르의 발굴현장 동쪽에 있는 산으로 피신했다네.$B$B그런데 그들이 힘을 길러 지금 메네실 북동쪽에 진을 치고 있네.$B$B자네가 놈들의 주둔지를 공격해 주게. 그리고 임무 완수의 증거로 놈들의 전쟁 깃발을 모아 내게 가져오게나.', },
    {'메네실 항구에 있는 경비대장 스타우트피스트에게 용아귀 전쟁 깃발 8개를 가져가야 합니다.', },
  },
  [466] = {
    '발연 광석을 찾아서',
    {'저습지의 알가즈 관문 북서쪽에 있는 동굴에서 발연 광석이라는 희귀한 광석이 발견되었다고 들었네. 발연 광석은 휘발성 물질이라 자칫 잘못 사용하면 폭발 사고를 당할 수 있지만, 제대로 용해한다면 엄청난 화력을 자랑하는 포탄을 만들 수 있을 걸세!$B$B내게 발연 광석을 가져다주면 그 광석으로 실험을 할 용감한 사람을 찾아보도록 하지.$B$B아, 광맥에서 광석을 추출하려면 채광 기술이 있어야 한다네. 자네가 기술을 익히지 않았다면 채광 기술을 익힌 동료를 데려가도록 하게나.', },
    {'던 모로에 있는 조종사 스톤기어에게 발연 광석 6개를 가져가야 합니다.', },
  },
  [467] = {
    '스톤기어의 광석',
    {'조종사 스톤기어는 여느 조종사들과 마찬가지로 자신의 증기 전차에 대한 애착이 무척 강하다오. 항상 전차의 화력을 올릴 수 있는 새로운 방법을 찾으려고 열심이지.$B$B지금 그는 어떤 희귀한 광석에 대한 소문을 듣고는 자신을 위해 그 광석을 찾아다 줄 이를 구하고 있소. 생각이 있다면 던 모로에 있는 스틸그릴의 정비소로 가서 스톤기어를 만나 그가 뭘 원하는지 알아보도록 하시오.', },
    {'조종사 스톤기어와 대화해야 합니다.', },
  },
  [468] = {
    '산악경비대 록가르에게 보고',
    {'산악경비대 록가르가 던 알가즈와 저습지에서 임무를 수행해 줄 지원자를 찾고 있다네.$B$B그 질퍽거리는 북쪽 지역에서 임무를 수행할 의향이 있다면 던 알가즈로 가서 록가르와 한번 얘기해 보게. 길을 따라 북쪽으로 가다 보면 경비탑이 나오는데 던 알가즈는 그 북쪽 경비탑 너머에 있네.', },
    {'산악경비대 록가르와 대화해야 합니다.', },
  },
  [469] = {
    '가죽 배달',
    {'안녕하시오!$B$B메네실 쪽으로 가는 것 같아 보이지는 않는군... 여유가 있다면 메네실에 한번 들러 보시기 바라오. 필요한 물건을 구입하는 건 물론이고 맥주 한 잔 하면서 쉬어가기에도 더할 나위 없이 좋은 곳이지.$B$B메네실 쪽으로 갈 거라면 무두장이 제임스 할로란에게 이 악어 껍질 한 묶음을 가져다주지 않겠소?$B$B내가 직접 가고 싶지만 요즘 우리 집사람 심기가 상당히 불편한 것 같아서 얼굴을 마주치기가 좀 그렇소이다.', },
    {'메네실 항구의 무두장이 제임스 할로란에게 악어 껍질 한 묶음을 전달해야 합니다.', },
  },
  [470] = {
    '가방 찾기',
    {'전 매주 아이언비어드의 고분에 방문하는데 한번은 흉측하고 물투성이 진흙괴물로부터 공격을 받았어요! 전 너무 무서워서 제정신이 아니었답니다.$B$B수액괴물 녀석 중 한 놈에게 가방을 던져 봤지만 가방이 바로 빨려 들어가서 아무 소용이 없었어요! 다행히도 그때를 틈타 도망갈 수 있었죠.$B$B그런데 그 가방에는 제게 꼭 필요한 물건들이 들어 있었어요. 수액괴물들은 전부 똑같이 생겨서 어느 놈에게 던졌는지도 알 수가 없네요! 아이언비어드의 고분은 던 모드르 근처, 이곳에서 북동쪽에 있답니다. 제 가방을 찾아주시면 정말 감사하겠어요!', },
    {'아이언비어드의 고분에 돌아다니는 수액괴물 중 하나가 시다의 가방을 갖고 있습니다. 그 가방을 구해 메네실 항구의 시다에게 가져가야 합니다.', },
  },
  [472] = {
    '던 모드르의 함락',
    {'던... 모드르가 검은무쇠 드워프의 손에 들어가고 말았소!$B$B우리 부대는 거의 전멸했고, 나... 난 심각한 부상을 입었지. 검은무쇠 악당들은 우리가 탄돌 교각의 매복지에서 부대를 재편성하기도 전에 공격해 왔소.$B$B우리 지휘관 롱브레이드 님께서는 퇴각 나팔을 불 수밖에 없었소. 우리가 마을에서 후퇴할 때 빗나간 도끼가 내 등에 꽂히고 난 정신을 잃었소.$B$B깨어나 보니 이곳 메네실에 와 있었지만 동료 걱정으로 마음이 편치 않다오. 아직 난 동료가 무사하다는 희망이 있소. 롱브레이드 님께서 아직 살아 계실지도 모르니, 던 모드르 근처에서 그분을 좀 찾아봐 주시오!', },
    {'던 모드르 마을 근처에서 롱브레이드를 찾아야 합니다.', },
  },
  [474] = {
    '네크로쉬 처치',
    {'투석기를 불태웠으니 이제 족장 네크로쉬를 찾아 처치해야 합니다.', },
    {'족장 네크로쉬를 처치해야 합니다.$B$B그런 다음 그 증거로 경비대장 스타우트피스트에게 네크로쉬의 머리카락을 가져가야 합니다.', },
  },
  [475] = {
    '혼란의 바람',
    {'숲에 혼란의 바람이 불고 있소.$B$B게롤라스 탈베드렌은 위대한 감시자의 역할을 맡아 바네실 지하굴에서 갈퀴발톱의 드루이드를 지킨다오. 게롤라스는 수면을 보호하도록 선택된 자로서, 이세라와 드루이드 사이에 맺은 협정이 유지되도록 드루이드를 안전하게 지킬 임무가 있소.$B$B그런데 최근 게롤라스에게서 연락이 늦어져 초조해지고 있소. 동쪽 별바람 마을로 가서 게롤라스의 보고서를 가지고 오시오. 우리 동족들이 무사하다는 사실을 확인하고 나면 나도 안심할 수 있소.', },
    {'별바람 마을에서 게롤라스 탈베드렌을 찾아야 합니다.', },
  },
  [476] = {
    '나무옹이 일족의 타락',
    nil,
    {'돌라나르에 있는 아스리다스 베어맨틀에게 가야 합니다.', },
  },
  [477] = {
    '마법사의 상자',
    {'자네도 알고 있다시피 호박색 농장에서는 대부분 달라란의 마법사들의 지원을 통해 아직 인간의 저항이 계속되고 있네.$B$B그들의 계획이 무엇인지는 모르겠지만, 호박색 농장처럼 고립된 마을에 관심을 보이는 것을 보면 그곳에 뭔가 중요한 것이 있는 것이 틀림없어.$B$B놈들은 언덕마루에서 마차를 이용해 상자들을 실어 나르고 있네. 상자들의 도착지는 장작나무 마을 북쪽에 있는 작은 야영지인 것 같은데, 자네가 상자의 내용물을 입수해 내게 가져와 주게.', },
    {'달라란 마술사의 상자 중 하나의 내용물을 찾아 와야 합니다. 장작나무 마을은 남쪽에서 찾을 수 있습니다.', },
  },
  [479] = {
    '호박색 농장 조사',
    {'달라는 그 마법사들이 사용하는 마법의 원천을 찾고 있네. 그리고 지금 우리는 모든 방법을 동원하여 놈들의 계획을 지연시켜야 해.$B$B분명히 마법사와 수호병들이 펜던트를 운반하고 있을 걸세.$B$B그 펜던트를 빼앗아 와야 하네. 길을 따라 남쪽으로 간 다음 길이 갈라지는 곳에서 호박색 농장으로 이어지는 동쪽으로 가게.', },
    {'공동묘지의 어둠의 사제 알리스터가 달라란 펜던트 8개를 가져와 달라고 했습니다.', },
  },
  [483] = {
    '깨움의 의식을 위한 재료',
    nil,
    nil,
  },
  [484] = {
    '새끼 악어 가죽',
    {'저는 무두장이 제임스 할로란이라고 합니다. 제가 가공한 악어 껍질은 인기가 많아 여기저기서 주문이 들어오고 있답니다. 제 제품들은 배에 실려 전 세계로 배달되기도 하지요.$B$B아이나르를 만나셨죠? 아이나르도 저한테 악어 껍질을 납품하는 사냥꾼 중 하나랍니다.$B$B그런데 요즘 새끼 악어의 부드러운 껍질이 좀 부족합니다. 그러니 새끼 악어 껍질을 구해 주신다면 값을 후하게 쳐 드리겠습니다. 마을 바로 바깥쪽에 있는 습지에 가면 새끼 악어를 찾을 수 있을 겁니다.', },
    {'메네실 항구의 제임스 할로란이 새끼 악어 껍질 4장을 가져와 달라고 했습니다.', },
  },
  [485] = {
    'OOX-09/HL 수색',
    {'금속으로 만들어진 이상한 알 모양의 장치를 찾았습니다. 손잡이 하나를 만지작거리다 보니 작동이 시작되면서 노움 로봇 장치로 변합니다! 로봇 속에서 소음과 함께 목소리가 들려옵니다.$B$B"나는 오글소프 오브노티쿠스라고 하는데, 불시착한 내 유도 로봇을 찾아주면 사례를 하겠소. 이 탐지기를 로봇이 있는 곳으로 가져가시오!"$B$B"방금 로봇의 현재 위치를 산출해 봤는데 굼벵이 바위굴 근처, 동부 내륙지의 강이 시작되는 곳에 추락한 것 같소!"', },
    {'굼벵이 바위굴 근처, 동부 내륙지의 강이 시작되는 곳에 추락한 오글소프의 유도 로봇에게 조난 탐지기를 가져가야 합니다.', },
  },
