  [647] = {
    '맥크릴의 밀주',
    {'어젯밤 오래 묵은 밀주를 좀 마셨지. 보초를 서는 동안 술에 취해 버린 걸 알면 롱브레이드가 날 죽이려 들 거요.$B$B아, 술 좀 마셨더니 지독히 괴롭군. 머리 위로 전차 부대가 하나 요란하게 지나가는 것 같아.$B$B오, 이런! 시간이 이렇게나 됐군! 남녘해안에 있는 양조업자 빌저에게 밀주를 보내 밀린 외상값을 갚기로 했는데...$B$B갚기로 한 시간이 15분밖에 안 남았잖아! 부탁이니 이걸 좀 전해 주시오. 서둘러야 하오!', },
    {'남녘해안에 있는 양조업자 빌저에게 맥크릴의 밀주를 가져가야 합니다.', },
  },
  [648] = {
    'OOX-17/TN 구출',
    nil,
    nil,
  },
  [649] = {
    '리플 회수',
    {'당신이 뭘 좀 찾아와 줬으면 하는데. 당신도 나처럼 리플 애호가라면 이 일이 맘에 쏙 들 거요! 리플이라니까!$B$B술 말이야 술! 이런 바보하고는...$B$B버려진 호드 전초기지에 녀석들이 장사를 할 때부터 숨겨두었던 동부 내륙지 벌꿀 리플이 있소이다. 그런 좋은 리플은 이제 더 이상 찾지도 못하지! 말톤의 멍청한 친구 녀석 중 하나가 어디 있는지 안다니까 가서 포장된 리플을 한 상자 가져다주시오. 아, 내 소장품으로 간직하게 두 개 더 가져오고! 말톤에게 얘기하면 자세히 알려줄 거요.', },
    {'오그리마에 있는 말톤 드로퍼스와 대화해야 합니다.', },
  },
  [650] = {
    '리플 회수',
    nil,
    nil,
  },
  [651] = {
    '마법진의 돌',
    {'아라시 고원 전역에는 4개의 봉인석이 흩어져 있습니다. 이 돌들이 저를 땅속에 묶어 놓고 있는 거랍니다.$B$B4개의 돌을 모두 빼내려면 가장 강한 내부 봉인석을 빼내야 한답니다. 그러기 위해서는 다른 세 개의 돌에서 열쇠를 구해야 합니다.$B$B이 돌들은 수호자가 지키고 있는데, 당신이 머리를 쓰거나 운이 좋다면 수호자를 내쫓을 수 있을 거예요.', },
    {'서부 봉인석, 동부 봉인석, 외부 봉인석에서 각각 불타는 열쇠, 용마루장식 열쇠, 천둥 열쇠를 구해야 합니다.$B$B세 개의 열쇠를 내부 봉인석으로 가져가야 합니다.', },
  },
  [652] = {
    '쐐기돌 깨기',
    {'봉인석을 열어서 몸이 훨씬 자유로워졌지만 아직도 족쇄가 채워져 있답니다. 저 원형 모양의 돌 사이에 있는 게 쐐기돌입니다. 열쇠는 저를 가둬 놓은 자 중 한 명인 포즈루크가 가지고 있어요.$B$B거인 포즈루크에게서 저들이 질서의 마법봉이라고 부르는 열쇠를 빼앗은 후 쐐기돌을 열어야 합니다.$B$B지금까지 절 많이 도와 주신 것 알고 있습니다. 이제 조금만 더 도와주시면... 제 감사의 마음이 온천하에 퍼지게 될 겁니다!', },
    {'포즈루크를 찾아 처치해야 합니다. 아라시 고원에 있는 쐐기돌에 질서의 마법봉을 가져가야 합니다.', },
  },
  [655] = {
    '망치 주둔지',
    nil,
    nil,
  },
  [656] = {
    '공주 소환',
    {'미즈라엘이 힘을 되찾기 전에 처치해야 해. 그렇지 않으면 힘을 회복한 후에 자기를 가둬 놓았던 자들을 해치러 갈 거야! 어떤 녀석들은 미즈라엘을 통제할 수 있다고 생각하는데, 그건 다 착각이라고. 그게 가능하다면 애당초 왜 거인들이 그녀를 묶어 놓았겠어??$B$B말도 안 되지, 안 그래?$B$B미즈라엘을 무찌르려면 그녀와 처음 얘기를 나눈 미즈라엘의 결정에서 이 두루마리로 그녀를 소환해야 해. 그녀를 무찌르고 기괴한 족쇄를 주은 다음 미즈라엘의 결정에 봉인해 놔.$B$B그러면 다시 갇히게 될 거야.', },
    {'미즈라엘의 결정으로 가서 그녀를 소환한 후에 처치해야 합니다.$B$B기괴한 족쇄를 주워서 다시 미즈라엘의 결정에 봉인해야 합니다.', },
  },
  [657] = {
    '새로운 역병의 징조?',
    nil,
    nil,
  },
  [658] = {
    '새로운 역병의 징조?',
    {'그러니까 핀이 우리가 잘못되지 않았는지 걱정돼서 당신을 보냈다고요? 하기야 놀라운 일도 아니죠. 핀은 워낙 걱정이 많은 성격이니까요.$B$B우리는 며칠 간 농장을 지켜보고 있었는데, 얼마 전에 키넬로리가 저기 남쪽에 있는 작은 집에서 급사가 빠져나가는 것을 봤다고 하더군요.$B$B키넬로리가 평소에 말이 많긴 하지만 위험한 지역에서는 매우 정확한 감각을 자랑한답니다.$B$B참, 혹시 당신도 실력 있는 수색자인가요? 우리 생각에는 급사가 타렌 제분소로 전갈을 가지고 간 것 같아요. 그 서류를 우리 손에 넣을 수 있으면 언덕마루에서 일어나는 일에 대한 단서를 얻을 수 있을지도 몰라요.', },
    {'포세이큰 급사를 찾아서 봉인된 서류를 고셰크 농장 근처에 있는 쿠에에게 가져가야 합니다.', },
  },
  [659] = {
    '새로운 역병의 징조?',
    {'만나서 반갑소. 난 이 지역 안에서 일어나는 이상한 일들을 조사하는 모험가 집단을 이끌고 있소이다.$B$B언덕마루에 사는 내 사촌은 최근에 기르고 있던 개, 스탠리를 죽여 버릴 수밖에 없었소. 이상하게 개의 몸집이 점점 거대해지더니 근처에 오는 모든 이들을 사납게 공격했기 때문이라오.$B$B내 생각에는 언데드가 이 일에 관련되어 있는 것 같아 이를 증명할 증거를 찾고 있소.$B$B내 동료 중 두 명, 쿠에와 키넬로리가 아라시 고원에 있는 고셰크 농장으로 갔는데 며칠 동안 아무 연락이 없다오. 그들을 찾아봐 주겠소? 점점 걱정이 되기 시작해서 말이오.', },
    {'아라시 고원에 있는 고셰크 농장 근처에서 쿠에와 키넬로리를 찾아야 합니다.', },
  },
  [660] = {
    '새로운 역병의 징조?',
    {'자, $N 님. 전 준비됐습니다.$B$B이제 가볼까요?', },
    {'쿠에에게 돌아갈 때까지 키넬로리를 보호해야 합니다.', },
  },
  [661] = {
    '새로운 역병의 징조?',
    nil,
    nil,
  },
  [663] = {
    '육지다!',
    {'롤로 눈에 작은 $R|1이;가; 보인다! 롤로 항상 망 보고 있다!$B$B팔디르의 만에 온 걸 환영한다. 오브린 선장이 여기 몇 시간만 있으면 된다고 했다. 보물을 모아서 무법항으로 가져가기만 하면 된다고 했다.$B$B하지만 롤로 생각하기에는 시간이 더 오래 걸릴 것 같다. 우리 함대에서 배 두 개 잃었다. 불쌍한 은빛소나무의 영혼호와 처녀의 망상호... 지금은 저 바다 밑에 자고 있을 거다!$B$B여기서 쉴 거면 오브린 선장과 얘기해 보는 게 좋을 거다.', },
    nil,
  },
  [664] = {
    '비통한 마음',
    {'아, 정말로 끔찍한 삶이로군. 난 지금 여기에 불가에 앉아 맑은 공기로 숨 쉬고 있을 게 아니라 진작에 물속에 빠져 죽었어야 해.$B$B선원들이 아름다운 처녀의 망상호를 지키기 위해 그렇게 노력했건만... 그들에게 명복을, 그들의 영혼에 평온이 있기를.$B$B오브린은 해안에 안전하게 다가갈 수 있었지만 우린 운이 나빴어. 바다가 우리를 위에서 덮쳤지, 정말일세.$B$B분명히 수면 위에 있었는데 갑자기 위에서 물이 우리를 덮쳤지 그리고 그 짐승들도! 그 짐승들이 생존자를 모두 죽여 버렸다네. 흉측한 나가 놈들 말일세. 난 정신을 잃었는데 깨어나 보니 이곳에 이렇게 살아 있더군.$B$B제발, 그 짐승들을 좀 죽여 주게.', },
    {'팔디르의 만에 있는 선장 스틸구트가 비수가시 침략자 10마리와 비수가시 여마법사 3마리를 처치해 달라고 했습니다.', },
  },
  [665] = {
    '가라앉은 보물',
    {'자, 이제 우리도 어엿한 검은바다 해적단이니까 오브린 선장이 잃어버린 엘프의 보물을 찾도록 도와주어야 해요.$B$B아무런 도움 없이 어두운 바닷속에서 보석을 찾는다는 건 거의 불가능한 일이죠. 드랙슬게이지가 고글을 몇 개 만들어 놓았어요. 언덕 바로 위의 동굴 속에 있는 마법의 돌에서 힘을 끌어와 고글에 불어넣어야 한다더군요.$B$B그런데 그 동굴에는 저주가 걸려 있어요! 가까이 다가가면 공격을 받을 거예요. 그러니까 당신은 제가 고글에 마법의 돌의 힘을 채울 수 있게 보호해 주면 돼요.', },
    {'학자 피즐솔프가 동굴에 들어갔다 나올 수 있도록 호위해야 합니다.', },
  },
  [666] = {
    '가라앉은 보물',
    {'보물이 해저에 가라앉은 지 하도 오래돼서 보석이 전부 단단한 돌로 굳어져 버렸어요. 하지만 이 고글에 담긴 힘 덕분에 보석을 쉽게 찾아낼 수 있을 거예요.$B$B이 키 작은 노움의 재간이 정말 뛰어나지 않나요?$B$B자, 그러니까 보물찾기 고글을 빌려 가서 오브린 선장을 위해 잃어버린 보물을 찾아오세요.$B$B제가 직접 아래로 헤엄쳐 갈 수도 있지만... 음... 그러니까, 안전한 육지에서 과학적으로 중요한 일이 자주 발생하니까요... 그러니까... 전 여기 자리를 지켜야 한단 말이에요.', },
    {'팔디르의 만에 있는 발명가 드랙슬게이지가 엘프의 보석 10개를 주워 오고 이 일을 끝낸 후 보물찾기 고글을 돌려 달라고 했습니다.', },
  },
  [667] = {
    '나가의 공격',
    {'큰일이군. 비수가시 일족 녀석들이 공격을 하려고 모이는 모양이야.$B$B나가는 전투에서 잔악하기로 유명한 녀석들이라 내가 죽을 때까지 계속해서 공격할 것이오.$B$B내가 내 선원들보다 내 목숨을 아끼는 건 아니지만 나만큼 바다에 대해 잘 아는 자가 없으니 내가 없으면 어차피 선원들도 무법항까지 돌아가지 못할 것이야. 그러니 날 꼭 지켜 줘야 해.$B$B나가 녀석들은 바다에서 접근해 올 것이오. 나와 함께 이 위에 남아 녀석들의 공격을 막아주시오. 배 위에 있는 대포를 이용해서 녀석들을 몰아내시오.$B$B준비는 됐소?', },
    {'나가의 공격으로부터 셰익스 오브린을 보호해야 합니다.', },
  },
  [668] = {
    '가라앉은 보물',
    {'오브린 선장을 오래 기다리게 하지 맙시다. 보석을 직접 눈으로 보고 싶어할 테니까요. 우리가 애초에 여기 온 것도 보석을 찾기 위해서잖아요!$B$B몇 주 동안 해적들과 어울리면서 피즐솔프와 제가 얻은 교훈은 이들의 보물을 오래 가지고 있으면 신상에 절대 이롭지 못하다는 거예요.$B$B자, 이 보석들을 오브린 선장에게 가져가세요.', },
