  [1011] = {
    '포세이큰의 역병',
    {'언데드 호드 군대인 포세이큰이 잿빛 골짜기로 숨어들어와 이곳 폐허와 언덕을 어슬렁거리고 있소. 우리 생각엔 그들이 나이트 엘프의 땅에 질병을 퍼뜨리려는 것 같소. 이건 꼭 확인해 봐야 할 문제요.$B$B포세이큰들은 남쪽의 도르다닐 지하굴 근처에 야영지를 설치하고 독약과 질병을 만들어 병 안에다 담고 있소. 그 야영지로 들어가 역병 견본을 훔쳐 가져다주시오. 그러면 내가 실험을 통해 그 용도를 밝혀내겠소.', },
    {'숲의 노래에 있는 케이네스 스틸윈드에게 역병 견본 하나를 가져가야 합니다.', },
  },
  [1012] = {
    '미쳐버린 드루이드',
    nil,
    nil,
  },
  [1013] = {
    '우르의 책',
    {'그림자송곳니 성채에 우르의 책이 보관되어 있소. 그 책은 내 소장 목록에 포함할 만한 가치가 충분한 것이지. 우르는 스컬지가 도래하기 전 달라란에서 가장 훌륭한 마법사였소이다. 다른 세계에 대한 그의 연구는 포세이큰의 몇몇 무리에게는 매우 가치가 높지.$B$B그림자송곳니 성채에 들어가 이 책을 찾아 주시오. 그걸 내게 가져오면 학문 연구에 공헌한 당신에 대해 어둠의 여왕님께 보고하도록 하겠소이다.', },
    {'언더시티의 연금술 실험실에 있는 관리인 벨두거에게 우르의 책을 가져가야 합니다.', },
  },
  [1014] = {
    '아루갈의 최후',
    {'아루갈이 아직 그림자송곳니 성채에 버젓이 살아 있소.$B$B아루갈을 처치하기 전에는 은빛소나무 숲을 어둠의 여왕님을 위한 전략 요새로 차지할 수 없소. 그의 마법은 내가 완전히 없애겠지만, 아루갈이 한 짓에 마땅한 형벌을 받도록 하는 것은 당신 손에 맡기겠소.$B$B그림자송곳니 성채로 가서 아루갈의 사악한 마법 주문을 영원히 잠재워 버리시오. 그리고 그 사악한 마법사의 머리카락을 내게 가져오도록 하시오.', },
    {'아루갈을 처치하고 그 증거로 그의 머리카락을 공동묘지에 있는 달라 돈위버에게 가져가야 합니다.', },
  },
  [1015] = {
    '새로운 미개척지',
    {'영웅은 들으십시오!$B$B세나리온 의회에서는 칼림도어의 광활한 땅을 개척할 건장하고 실력 있는 모험가들을 찾고 있습니다! 타나리스와 가젯잔에서 서쪽으로 멀리 떨어진 곳에 펼쳐져 있는 금단의 황무지에는 상상도 못할 위험이 도사리고 있습니다. 세나리온 의회를 대신해 개척 임무를 맡아줄 용맹스럽고 뜻있는 지원자들을 찾고 있습니다.$B$B관심이 있는 이들은 모두 세나리온 의회에 알현을 청하십시오. 다르나서스에 있는 대드루이드 판드랄 스태그헬름을 찾아가면 자세한 얘기를 들을 수 있습니다!', },
    {'다르나서스의 세나리온 자치령에 있는 대드루이드 판드랄 스태그헬름을 찾아가 세나리온 의회의 칼림도어 미개척지 탐험 지원자 모집에 대해 대화해야 합니다.', },
