  [1251] = {
    '검은 방패',
    {'그늘 쉼터 여관 건물의 얼마 남지 않은 부분 중 하나인 허물어진 굴뚝에 여관 전체를 휩쓸어 버린 불로 검게 그을린 철제 방패가 붙어 있습니다.$B$B굴뚝의 벽돌에서 방패를 떼어낼 수 있습니다.', },
    {'담쟁이 마을에 있는 크로그에게 검은 철제 방패를 가져가야 합니다.', },
  },
  [1252] = {
    '부관 파발 리테',
    {'잔해 밑에서 뭔가가 반짝이는 것이 눈에 띕니다. 그 물건을 덮고 있는 잔해와 재를 털어내자 표면에 에나멜을 입힌 흰색 바탕에 황금 닻이 그려진 배지가 드러납니다. 바로 테라모어의 상징입니다.$B$B닻 아래에는 \'부관 파발 리테\'라는 이름이 새겨져 있습니다.', },
    {'테라모어에 있는 사령관 개런 바임스에게 리테의 배지를 가져가야 합니다.', },
  },
  [1253] = {
    '검은 방패',
    {'그늘 쉼터 여관 건물의 얼마 남지 않은 부분 중 하나인 허물어진 굴뚝에 여관 전체를 휩쓸어 버린 불로 검게 그을린 철제 방패가 붙어 있습니다.$B$B굴뚝의 벽돌에서 방패를 떼어낼 수 있습니다.', },
    {'테라모어에 있는 사령관 개런 바임스에게 검은 철제 방패를 가져가야 합니다.', },
  },
  [1258] = {
    '... 그리고 벌레',
    nil,
    {'테라모어에 있는 모건 스턴에게 온전한 집게 다리 12개를 가져가야 합니다.', },
  },
  [1259] = {
    '부관 파발 리테',
    {'부관 파발 리테라는 이름은 처음 들어 보는데... 부관 테소렌을 만나 확인해 보는 게 현재로선 가장 현명한 선택일 듯하네. 테소렌은 개인 신상 정보에서 장비에 이르기까지 테라모어 경비병에 대한 모든 기록을 보관하고 있으니까 말일세.$B$B그라면 이 리테라는 자가 누구인지 자료를 찾아 금방 확인해 줄 수 있을 것일세.', },
    {'테라모어에 있는 부관 테소렌으로부터 리테에 대해 알아내야 합니다.', },
  },
  [1260] = {
    '모건 스턴',
    nil,
    nil,
