  [1418] = {
    '니카 블러드스카',
    {'황야의 땅은 거친 땅이며 얼라이언스의 영토인 모단 호수와 가깝지. 그래서 스토나드와 카르가스의 황야의 땅 전초기지 간의 연락이 종종 두절되곤 하오.$B$B자, 당신의 능력을 한번 증명해 보시오. 황야의 땅으로 가서 카르가스 전초기지의 정찰 대장인 니카 블러드스카를 만나 그녀와 대화한 다음 그녀의 보고 내용을 내게 다시 전하도록 하시오.', },
    {'니카 블러드스카와 대화해야 합니다.', },
  },
  [1419] = {
    '도둑 코요테',
    {'황야의 땅에서 지낸 지 오래되었습니까, $C? 그 열기를 느껴본 적 있나요? 저 먼지 가득한 평원을 어슬렁거리는 짐승들과 마주친 적이 있습니까? 이제 당신이 직접 겪어 봐야 할 때가 됐군요.$B$B바위산코요테는 사나운 종일 뿐 아니라 카르가스로 기어 들어와 우리의 식량을 훔쳐 갈 만큼 대담하기도 하답니다. 이 도둑들의 근거지를 제거하는 일 좀 도와주시기 바랍니다!$B$B바위산코요테를 사냥하여 그 턱뼈를 내게 가져오세요, $N. 충분한 양을 가져오면 당신이 황야의 땅에 대해서 잘 알고 있다는 걸 믿도록 하겠습니다.', },
    {'카르가스에 있는 니카 블러드스카에게 코요테 턱뼈 30개를 가져가야 합니다.', },
  },
  [1420] = {
    '헬그럼에게 보고',
    {'스토나드의 헬그럼에게 보낼 새로운 보고서를 작성했습니다. 이걸 그에게 전해 주시기 바랍니다.$B$B그리고 당신이 스스로 길잡이가 될 생각이라면 방향을 잃지 않도록 조심하세요. 그렇지 않으면 적의 손아귀에 떨어질 수도 있으니까 말입니다.', },
    {'스토나드에 있는 날쌘돌이 헬그럼에게 니카의 보고서를 가져가야 합니다.', },
  },
  [1423] = {
    '잃어버린 보급품',
    {'이 맥주통은 작지만 튼튼합니다. 나무가 이어진 부분은 자극적인 냄새가 나는 노란색 왁스로 봉해져 있습니다. 그리고 맥주통 밑바닥에는 다음과 같은 글귀가 찍혀 있습니다.$B$B병참장교 룬겔츠$B$B네더가드 요새', },
    {'병참장교 룬겔츠에게 잃어버린 보급품을 가져가야 합니다.', },
  },
