  [1860] = {
    '제니아와의 대화',
    {'우리 마법사들이 꼭 해야 할 임무가 있소. 스톰윈드와 엘윈 숲의 안전을 위해 아주 중요한 임무지.$B$B자세한 내용은 제니아 캐논이 알고 있소. 스톰윈드의 마법사 지구에 있는 마법사의 성소에서 공부를 하고 있으니 서둘러 찾아가 보시오.', },
    {'스톰윈드에 있는 제니아 캐논과 대화해야 합니다.', },
  },
  [1861] = {
    '거울 호수',
    {'이 세계는 거대하고 신비로운 힘들간의 전쟁에 피해를 받았습니다. 이 전쟁으로 인해 한때는 안정적이던 마법의 흐름에 커다란 문제가 생겼답니다. 우리는 이러한 변화가 가져온 결과를 알아내기 위해 노력하고 있는데 일부 마법사들은 최악의 결과가 아닐까 우려하고 있습니다!$B$B지금은 정보를 모아야 하니 자세한 얘기는 나중에 하도록 하겠습니다.$B$B자, 이 물통을 받으세요. 스톰윈드 성문의 남서쪽에 거울 호수 폭포 밑으로 가서 물을 담아 오세요. 그 물을 내게 가져오면 나쁜 마법에 오염되지 않았는지 실험해 보도록 하겠습니다.', },
    {'스톰윈드에 있는 제니아 캐논에게 거울 호수 견본을 가져가야 합니다.', },
  },
  [1878] = {
    '물주머니 현상금',
    nil,
    nil,
  },
  [1879] = {
    '빙크와의 대화',
    {'이보시오! 아이언포지에 있는 빙크라는 노움 친구가 소식을 전해왔소. 해야 할 임무가 있다는데 마법사가 아니면 맡기질 않겠다고 하오! 당신은 마법사지 않소?$B$B그렇다면 빙크와 대화해 보시오. 그녀는 아이언포지의 마법 지구에 있는 신비의 전당에서 주로 시간을 보내니 그쪽으로 가면 만날 수 있을 것이오.', },
    {'아이언포지에 있는 빙크와 대화해야 합니다.', },
  },
  [1880] = {
    '마법사의 기억장치',
    {'우리 마법사들은 손가락 하나 까딱거린다고 화려한 마법을 쓸 수 있는 게 아니에요! 이 세계를 이루는 기본 요소인 마법을 제어하는 것이죠! 그러려면 아주 신중해야 하고 굳건한 의지와 방대한 지식이 필요하지요!$B$B내 장치 중 마법사의 기억장치는 이처럼 방대한 양의 지식을 수집하고 보관할 수 있답니다! 그런데 그 장치를 잃어버렸지 뭐예요! 고향인 놈리건에서 도망 나오다 도시 외곽에 있는 숙소의 연장통 속에 숨겨 놨어요. 하지만 지금 그곳엔 오염된 노움들이 득실거리고 있어서 찾기가 어렵답니다!$B$B부탁이에요. 내 기억장치를 찾아주세요!', },
    {'빙크에게 마법사의 기억장치를 가져가야 합니다.', },
  },
  [1881] = {
    '아나스타샤와의 대화',
    {'아나스타샤 하트웰이 당신 같은 재능을 지닌 마법사에게 맡길 임무가 있다고 합니다. 언더시티의 마법 지구에서 그녀와 얘기를 나누고 포세이큰의 마법사들을 위한 첫 임무를 준비하도록 하시기 바랍니다.', },
    {'아나스타샤 하트웰과 대화해야 합니다.', },
  },
  [1882] = {
    '발니르 농장',
    {'동쪽에 있는 역병지대는 오염 정도가 심각해요. 리치 왕이 만들어낸 마법이 땅속에 퍼지면서 살아있는 것들을 모조리 오염시키고 죽이고 있어요. 역병의 파괴력을 막아주는 주문이 있긴 하지만 우리 포세이큰의 마법사들도 그 주문을 통제하기가 어려워 주문의 힘을 줄여야 한다고 생각하고 있지요.$B$B하지만 먼저 역병이 티리스팔에 얼마나 깊숙이 침투했는지 알아야 해요. 티리스팔 동쪽에 있는 발니르 농장에 가면 정원에 금어초가 있을 거예요. 그것들을 모아서 가져오세요.', },
    {'언더시티의 마법 지구에 있는 아나스타샤 하트웰에게 발니르 금어초를 가져가야 합니다.', },
  },
  [1883] = {
    '운투와와의 대화',
    {'센진 마을의 부족 마법사인 운투와가 듀로타의 마법 흐름을 방해하는 요소가 있다면서 도움을 요청하고 있습니다. 운투와와 대화를 하고 어떻게 해서든 그를 도와주도록 하십시오.$B$B그렇게 하면 머잖아 더 많은 비밀을 알게 될 것입니다.', },
    {'운투와와 대화해야 합니다.', },
  },
  [1884] = {
    '주주 해골',
    {'메아리 섬에 사는 흑마법사 잘라제인이 듀로타의 정상적인 마법 흐름을 흐트러뜨려 놓았네. 그는 주주 해골이라는 오염된 우상으로 이런 일을 벌이고 있는데 메아리 섬의 오두막에 그 우상을 두었다네.$B$B그 우상은 야생의 동물과 식물을 타락시키고 어둡고 외진 곳에 사는 흑마법사와 지옥의 생물에게 힘을 부여하고 있지.$B$B그 주주 해골을 파괴해야 하네. 마법의 흐름이 영원히 바뀌어버리기 전에...', },
    {'주주 해골 4개를 파괴하고 센진 마을에 있는 운투와에게 가야 합니다.', },
  },
