  [1918] = {
    '타락한 정령',
    nil,
    {'잿빛 골짜기 토막나무 주둔지에 있는 마스토크 윌리히스에게 더럽혀진 물구슬을 가져가야 합니다.', },
  },
  [1919] = {
    '제니아에게 보고',
    {'마법에 대한 지식이 점점 늘어가고 있군, 젊은 마법사여. 이제 진짜 훈련을 시작할 준비가 되었소? 우리가 치르고 있는 비밀 전쟁에 동참할 준비가 되었는가 말이오?$B$B그렇다면 스톰윈드 마법사 지구의 마법사의 성소에 있는 제니아 캐논과 대화하도록 하시오. 그녀가 보이지 않는 세계의 첫 창문을 열어 줄 임무를 알려줄 것이오.', },
    {'스톰윈드에 있는 제니아 캐논과 대화해야 합니다.', },
  },
  [1920] = {
    '고독한 은둔자 선술집 조사',
    {'마법사 지구에 있는 고독한 은둔자 선술집에서 소란이 있었다는 보고가 들어왔습니다. 눈에 보이지 않는 괴이한 생물들이 선술집을 어슬렁거린다는데 그곳에 마력의 균열이 생긴 게 아닌지 걱정입니다.$B$B가서 선술집을 조사해보고 그 생물들을 잡아오도록 하세요. 선술집에서 현신의 주문서를 사용해 생물들이 보이게 해서 제압한 다음 속박의 상자를 사용해 붙잡으세요. 충분히 잡았으면 정령이 든 속박의 상자와 빈 상자, 그리고 주문서를 가지고 제게 돌아오도록 하세요.', },
    {'제니아 캐논의 뒤에 있는 현신의 주문서와 속박의 상자를 획득하십시오. 마법사의 성소에 있는 제니아에게 채워진 속박의 상자 3개를 가져가야 합니다.', },
  },
  [1921] = {
    '재료 수집',
