  [2038] = {
    '사라진 빙글스의 물건들',
    {'나를 모단 호수에 추락시킨 것으로는 성에 안 찼는지 근처 섬에 살던 트로그들이 나를 덮쳤소. 도구들과 특제 깡그리싸그리 폭탄을 훔쳐가 버렸지 뭐요. 자이로콥터와 폭탄이 없으니 우리 노움에게 가망은 없소!$B$B자이로콥터를 수리할 수 있도록 내 도구들과 깡그리싸그리 폭탄을 찾아주시오. 놈리건을 위해서 말이오.', },
    {'빙글스의 물건들을 찾아서 돌아와야 합니다.$B$B빙글스의 스패너, 빙글스의 드라이버, 빙글스의 망치, 빙글스의 깡그리싸그리 폭탄을 찾아야 합니다.', },
  },
  [2039] = {
    '빙글스 찾기',
    {'빙글스에게서 연락이 끊겼소! 놈리건 정찰 임무를 띠고 수색을 나갔는데 일주일이 넘도록 아무런 보고가 없소이다. 어딘가에서 봉변을 당한 게 분명하오.$B$B그가 모단 호수 위를 날아가는 모습이 마지막으로 목격됐다 하는군. 당신이 빙글스를 찾아봐 주면 좋겠소.', },
    {'모단 호수에서 빙글스 블라스텐하이머를 찾아야 합니다.', },
  },
  [2040] = {
    '지하 공격',
    {'놈리건이 저 비열한 트로그 녀석들의 손아귀에 들어가다니! 위험한 상황이긴 하지만 어쩌면 당신이 우리를 도와주실 수 있을지도 모르겠군요.$B$B죽음의 폐광 깊은 곳에 아직 쓸 수 있는 고블린 벌목기가 있어요. 그 벌목기를 찾아 구동장치를 그대로 가져다주세요. 벌목기 구동장치의 전력을 우리의 회전천공식 발굴기에 사용해서 놈리건의 바위투성이 지하 경계를 부수고 공격 진입로를 뚫어야겠어요!', },
    {'죽음의 폐광에서 노암 톱니구동장치를 입수해 스톰윈드에 있는 쇼니에게 가져가야 합니다.', },
  },
  [2041] = {
    '쇼니와의 대화',
    {'어쩌면... 어쩌면 말이오. 당신이 놈리건을 되찾기 위한 전투에서 우리를 도울 수 있을 거요.$B$B스톰윈드에 가면 지하 공격조 지휘관인 쇼니가 있소. 쇼니가 회전천공식 발굴기를 사용하도록 도와주시오.$B$B아마 스톰윈드의 드워프들과 함께 있을 거요. 행운을 빌겠소.', },
    nil,
  },
  [2098] = {
    '자이로마스트의 수색',
    {'해적의 삶이란 쉬운 게 아냐, 애송이 친구. 약탈한 돈으로 술을 마시며 흥청대는 게 전부가 아니란 말이지! 아아... 내 배가 트레샤돈 녀석들 때문에 해안 바로 앞에서 침몰됐네. 빨리 다시 일을 시작해야 하는데!$B$B내가 해안으로 밀려왔을 때 일등항해사의 열쇠가 바위에 떨어져 부서졌지. 어둠해안에 사는 짐승들이 그걸 훔쳐간 게 분명하다네. 사나운 모래톱게, 거대한 숲타조, 그리고 멀록들이 난파된 우리 배를 휘젓고 다니는 걸 슬쩍 봤거든.', },
    {'겔카크의 열쇠 조각 3개를 찾아 겔카크 자이로마스트에게 가져가야 합니다.', },
  },
  [2118] = {
    '역병에 걸린 땅',
    {'어둠해안 전역에 역병이 퍼지면서 모든 이들의 몸과 마음을 앗아가고 있소.$B$B엉겅퀴곰들이 이 역병으로 가장 큰 타격을 받았다오. 한때는 고귀하던 짐승이 지금은 미친 듯이 날뛰는 파괴의 앞잡이가 되어 버렸소. 내 이 역병을 치료할 수 있을 것도 같소.$B$B이 덫을 숲으로 가지고 가서 땅에 놓으시오. 광포한 엉겅퀴곰이 덫에서 발하는 빛에 닿으면 잠시 동안이나마 온순하게 변할 거요. 곰이 온순해지면 잘 달래어 여기로 데려와 주시오.', },
    {'광포한 엉겅퀴곰을 생포해 타나리운에게 데려가야 합니다.$B$B광포한 엉겅퀴곰을 생포하는 데 실패하고 덫을 잃으면, 타나리운 트리텐더에게 돌아가 다른 덫을 받아야만 합니다.', },
  },
  [2139] = {
    '타나리운의 희망',
    {'우리 종족은 이 땅에서 생존을 위해 너무나 큰 희생을 치르고 있소.$B$B자연의 수하들, 수많은 병든 동물들이 그대의 손에 피를 흘리며 죽어갔소. 그래도 아우버다인의 주민들은 그대에게 은혜를 입은 셈이지. 비록 잠시 동안이긴 했지만 숲의 고통을 덜어주었으니 말이오.$B$B그대의 마지막 임무가 하나 남았소만 또 살생을 해야 한다는 것을 미리 말해 두겠소. 이런 일로 그대를 부르는 것이 마지막이었으면 하는 바람이오.$B$B바샬아란 근처의 북동쪽 동굴에 우두머리 엉겅퀴곰이 살고 있소. 그 곰을 처치해주시오.', },
    {'우두머리 엉겅퀴곰을 찾아서 처치해야 합니다.', },
  },
  [2158] = {
    '평안과 휴식',
    {'어떤 모험가든 힘이 떨어지면 휴식을 취해야지요. 편안히 쉴 곳이라면 사자무리 여관만한 곳이 없습니다!$B$B내 절친한 친구 팔레이가 사자무리 여관을 운영하고 있습니다. 내가 당신을 보냈다고 말하면 특별히 가격을 깎아 줄지도 모릅니다.$B$B사자무리 여관으로 가려면 여기서 길을 따라 남쪽으로 가면 바로 보일 겁니다.', },
    {'사자무리 여관에 있는 여관주인 팔레이와 대화해야 합니다.', },
  },
  [2159] = {
    '돌라나르 배달물',
    {'안녕하십니까, 젊은 $C님. 저 좀 도와주지 않으시겠습니까? 돌라나르 마을로 가져다줘야 하는 약초 보따리가 있는데, 그늘 협곡에서 드루이드와 볼일이 남아 아직 떠날 수가 없습니다.$B$B저 대신 이 배달물을 좀 전해주지 않으시겠습니까? 돌라나르 여관주인 켈다미르에게 전달해야 하는 물건입니다. 돌라나르 여관은 남쪽으로 가는 길에 있습니다.', },
    {'여관주인 켈다미르에게 돌라나르 배달물을 가져가야 합니다.', },
  },
  [2160] = {
    '탄노크에게 보급품 전달',
    {'안녕하세요! 무척 마음씨 좋은 모험가분이신 거 같군요. 혹시 위험을 무릅쓰고 여행을 하실 계획이시라면 카라노스에 있는 여관으로 이 소포를 좀 전해주지 않겠어요?$B$B도착하면 그 여관에서 좀 머무는 것도 좋을 거예요. 먼 길이라 피곤하실 테니까요.$B$B어쨌든, 이걸 여관 종업원 탄노크 프로스트해머에게 가져다주세요. 저는 이곳에서 멀리 갈 수 없는 몸인데다 며칠 후 도착할 산악경비대 호위병을 기다려야 해서 곤란하거든요.', },
    {'카라노스에 있는 탄노크 프로스트해머에게 여관 보급품 상자를 전해야 합니다.', },
  },
  [2161] = {
    '일꾼의 짐',
    {'저기요, 저 좀 도와주시겠어요?$B$B여기 식량 보따리가 하나 있어요. 전 이걸 들고 전갈, 거미, 그보다 더 끔찍한 것들과 싸우면서 걸었어요! 시험의 골짜기에 음식이 필요할 것 같아 돕고 싶은 마음에 가져갔죠. 그런데 음식은 필요하지도 않더라고요! 이제 칼바위 언덕으로 다시 가져가야 하는데 또 짐승들을 만날까봐 무서워서...$B$B저 대신 이 짐을 가져다주실 수 있나요? 저는 일개 짐꾼에 불과하지만 당신은 용감한 영웅이잖아요. 아무것도 두려워하지 않는 영웅!$B$B칼바위 언덕의 여관주인 그로스크에게 가져다주세요. 고맙습니다!', },
    {'칼바위 언덕에 있는 여관주인 그로스크에게 우코르의 짐을 가져가야 합니다.', },
  },
  [2198] = {
    '부서진 목걸이',
    {'빛바랜 목걸이를 찾았습니다. 비록 겉은 더럽혀져 있지만 정교하고 신비한 기술로 만들어진 것 같습니다. 목걸이는 부서졌는데 모양을 보아하니 보석 세 개가 사라진 듯합니다. 목걸이의 아래 부분에는 아이언포지의 도시 인장이 희미하게 찍혀 있습니다. 인장 밑에는 "탈바쉬"라는 작은 글씨가 새겨져 있습니다.$B$B이 목걸이에 대해 알려면 목걸이의 근원에 대해서 알아야 합니다. 인장과 작은 글씨가 그 출발점이 될 것 같습니다.', },
    {'부서진 목걸이의 잠재적 가치를 알아내기 위해 원 제작자를 찾아야 합니다.', },
  },
  [2199] = {
    '정보의 대가',
    {'저는 이곳 아이언포지에서 꽤 유명한 보석세공사라고요! 그러니까, 전에 꽤 유명했지요... 지금은 경쟁자의 거짓말 때문에 어렵게 지내고 있지만요! 이 바닥에서 평판이 나빠지면 곧장 돈줄이 끊기게 된답니다. 그러다 보니 한때는 유명했지만 지금은 더럽혀진 명성에 무일푼으로 지내고 있어요, 흑흑.$B$B우선 수주한 주문 하나를 마무리할 수 있게 은괴 다섯 개를 가져다주세요. 이게 얼마 만에 받아보는 주문인지 모르겠다니까요.$B$B당신이 이 은괴 다섯 개를 가져다주면 목걸이에 대한 질문에 답해 드리죠.', },
    {'아이언포지에 있는 탈바쉬 델 키젤에게 은괴 5개를 가져가야 합니다.', },
  },
  [2200] = {
    '울다만으로 돌아가기',
    {'제 경쟁자 녀석이 제 목걸이가 그 성기사에게 저주를 내린 거라며 소문을 퍼뜨리기 시작했어요! 저주받은 물건을 사려고 하는 자가 없으리라는 것은 말할 것도 없지요.$B$B제가 제안 하나 할게요. 만약 당신이 잃어버린 보석들을 찾아오면 목걸이를 다시 복원해줄 테니, 대신 저 탈바쉬 델 키젤이 절대로 저주받은 물건을 만들지 않는다고 선전해 주세요!$B$B울다만에서 단서를 찾아보세요. 어쩌면 성기사의 유해를 찾아보는 게 좋을지도 모르겠군요. 그가 마지막으로 그 목걸이를 지니고 있었으니까 말이지요! 보석을 찾으면 이걸 쓰세요. 그러면 이곳 아이언포지에 있는 저와 연락을 할 수 있을 거예요.', },
    nil,
  },
  [2201] = {
    '보석 찾기',
    {'성기사의 일지에 적힌 목걸이의 보석 위치가 울다만에서 찾을 수 있는 유일한 단서인 듯합니다. 이 성기사가 적어놓은 정보가 정확하다면 발굴현장을 차지하기 위해 싸우고 있는 어둠괴철로 드워프들과 트로그들을 처치해야 합니다.$B$B이 정보를 얻었으니 이제 본격적인 보석 찾기가 시작된 것입니다. 보석을 모두 손에 넣으면 탈바쉬의 지시대로 수정점 유리병을 사용하여 아이언포지에 있는 그에게 알려야 합니다.', },
    {'울다만에 흩어져 있는 루비, 사파이어, 토파즈를 찾아야 합니다. 모두 찾으면 탈바쉬가 준 수정점 유리병을 사용하여 그에게 연락해야 합니다.$B$B일지에 의하면... $B*루비는 어둠괴철로 드워프들이 있는 지역에 숨겨져 있습니다.$B*토파즈는 얼라이언스 드워프들이 있는 곳 근처에 트로그 지역에 있는 항아리 중 하나에 숨겨져 있습니다.$B$B*사파이어는 트로그 대장인 그림로크가 가지고 있습니다.', },
  },
  [2202] = {
    '울다만에서 재료 찾기',
    {'자홍버섯이라는 식물이 새로이 발견되었다는 얘기를 들었다오. 자홍버섯은 울다만 발굴현장에서 군생하고 너무 깊숙이 탐험하지 않아도 되는 지역 내에 긴 굴 입구에서 찾을 수 있다오. 난 그 버섯이 연금술에 사용할 잠재적인 가치가 있는지 연구하고 싶소. 그래서 당신의 도움이 필요하다오.$B$B자홍버섯을 찾아서 나에게 12개만 가져다주시오. 버섯송이에 가까이 가면 포자에서 독이 발산되니 조심하시오.$B$B날 위해 이 일을 맡아주시오. 돌아오면 내 당신을 격려하는 의미에서 유명한 나의 복원의 비약을 한 꾸러미 주겠소이다!', },
    nil,
  },
  [2203] = {
    '황야의 땅에서 재료 찾기 II',
    nil,
    nil,
  },
  [2204] = {
    '목걸이 복원',
    {'이제 목걸이에 필요한 "마력"을 찾아야 해요. 아마도 그 일에는 울다만이 최고의 장소일 걸요. 그곳에 피조물들이 꽤 많이 돌아다닐 텐데, 그렇지 않나요? 그중에서 제일 덩치 크고 악독해 보이는 놈을 잡아서 놈의 마력원천을 빼앗으세요! 제가 그 마력원천을 목걸이에 불어넣을 수 있을 거예요.$B$B그 마력원천이 없이 목걸이를 고친다는 것은 불가능해요. 그러니 마력원천을 찾으면 여기 아이언포지로 다시 돌아오세요. 당신을 위해 모두 고쳐 드릴게요!', },
    {'울다만에서 가장 강력한 피조물을 찾아 부서진 목걸이의 마력원천을 획득한 다음, 아이언포지에 있는 탈바쉬 델 키젤에게 목걸이와 함께 가져가야 합니다.', },
  },
  [2205] = {
    nil,
    {'배지? 오, 아무것도 아니에요. 어쨌든 당신이 처리 할 수있는 건 없어요. 당신의 외모로 말하면 마티아스는 치즈 배달에 대해 당신을 실수 할 것 같아요. $Gboy : girl; 적절한 도적 대신에. 그럼에도 불구하고 나는 일자리를 배정 받았고 그 일을 수행하겠다는 약속을했습니다. $B$B말하기,이 패키지를 마티아스에게 전달하면 그는 작은 일이나 두 가지를 줄 수 있습니다. $B$B금이 간다.', },
    nil,
  },
  [2206] = {
    '선적 일정 입수',
    nil,
    nil,
  },
  [2239] = {
    '오닌의 보고서',
    nil,
    nil,
  },
  [2240] = {
    '비밀 석실',
    {'자, 그럼 임무 얘기를 하지.$B$B카즈뮬의 석실이라는 비밀 석실이 있는데 그곳을 조사해야 하네. 문제는 이 방으로 들어가는 커다란 문이 잠겨져 있다는 것인데, 다행히 내가 그 문을 여는 방법을 안다네.$B$B내가 이 탁자 위에 있는 일지에 모두 적어뒀지. 지금 나와 내 동료는 이곳에서 꼼짝할 수가 없어서 말이야... 그러니 자네가 우리를 대신해 카즈뮬의 석실을 찾아야 하네!$B$B내 일지를 읽고 그 석실을 조사한 후 아이언포지에 있는 탐험가 연맹으로 가서 발굴조사단장 스톰파이크에게 보고하도록 하게.', },
    {'밸로그의 일지를 읽고 비밀 석실을 조사한 후 발굴조사단장 스톰파이크에게 보고해야 합니다.', },
  },
  [2258] = {
    '황야의 땅에서 재료 찾기',
    {'이보게, 자네만 괜찮다면 도움이 좀 필요하네. 나는 이름이 좀 알려진 연금술사인데 자네가 황야의 땅에서 재료 찾는 일을 도와주었으면 하네.$B$B재료를 넉넉히 비축해 두어야 하는데 몇 가지가 부족해서 말이야. 대머리수리 모래주머니 5개, 바위산코요테 송곳니 10개, 바위 정령 파편 5개가 필요하네. 물론 짐승들에게서 직접 얻어와야 하는 것도 있으니 자네가 싸움에 익숙한 체질이 아니라면 싸울 줄 아는 친구들을 찾아보도록 하게나.$B$B이 물건들을 가져오면 돈을 좀 주겠네. 해보겠나?', },
    {'황야의 땅에서 자칼 모스멜드에게 대머리수리 모래주머니 5개, 바위산코요테 송곳니 10개, 바위 정령 파편 5개를 가져가야 합니다.', },
  },
  [2259] = {
    '에리온 셰이드위스퍼',
    nil,
    nil,
  },
  [2278] = {
    '백금 원반',
    {'이 보물의 방 중앙에는 네 개의 거대한 백금 원반이 놓여 있는데, 각 원반은 근원을 알 수 없는 무수한 인장으로 꾸며져 있습니다. 이 원반을 손으로 건드리면 따끔거리는 느낌이 전해지면서 거대한 바위감시자의 모습이 앞으로 튀어나옵니다.$B$B"안녕하십니까. 저는 지식의 파수꾼입니다. 창조주의 토석인 창조와 관련된 정보가 이 노르간논의 원반에 기록되고 보관되어 있습니다."$B$B당신은 이 세상에서 일찍이 발견된 적 없는 지식을 알게 된 것입니다!', },
    {'바위감시자와 대화하고 그가 알고 있는 고대의 지식을 배워야 합니다. 지식을 배운 후에는 노르간논의 원반을 가동해야 합니다.', },
  },
