  [2981] = {
    '페랄라스의 위협',
    nil,
    {'벨그롬 록마울이 모자케 야영지에 있는 로크 올한과 대화해 달라고 했습니다.', },
  },
  [2982] = {
    '높은벌',
    {'트로야스와 나는 위험한 줄 알면서도 폐허가 된 페랄라스 숲을 조사해 왔습니다.$B$B그런데 요즘 들어 트로야스가 자꾸 혼자서 높은벌로 가는 게 너무 걱정이 되네요. 그곳은 골두니 오우거들이 판을 치고 있는데다 녀석들은 자기들 영토에 침입하는 자에게는 몹시 사납게 굴거든요.$B$B그대가 섬 너머 남동쪽에 있는 폐허로 가서 그 오우거들을 좀 처치해 줬으면 합니다. 우리 트로야스를 위해서 그곳을 조금 더 안전하게 해 주면 정말 고맙겠어요.', },
    {'안젤라스 문브리즈가 골두니 흑마법사 8명, 골두니 주술사 8명, 골두니 투사 8명을 퇴치해 달라고 했습니다.', },
