  [3088] = {
    '암호화된 양피지',
    {'$N, 자네가 자리를 비운 사이, 양피지가 하나 도착했네.$B$B시간이 날 때 한번 읽어보게. 내 기억이 맞는다면 상급 도적, 르와그가 보냈을 거야. 자네만 준비되면 이야기를 나누고 싶어하는 것 같네.', },
    {'암호화된 양피지를 읽어보고 시험의 골짜기에 있는 르와그와 대화해야 합니다.', },
  },
  [3089] = {
    '룬으로 새겨진 양피지',
    {'$N, 자네가 자리를 비운 사이, 양피지가 하나 도착했네.$B$B시간이 날 때 한번 읽어보게. 내 기억이 맞는다면 상급 주술사, 시크리크가 보냈을 거야. 자네만 준비되면 이야기를 나누고 싶은 것 같네.', },
    {'룬으로 새겨진 양피지를 읽어보고 시험의 골짜기에 있는 시크리크와 대화해야 합니다.', },
  },
  [3090] = {
    '오래된 양피지',
    {'$N, 자네가 자리를 비운 사이, 양피지가 하나 도착했네.$B$B시간이 날 때 한번 읽어보게. 내 기억이 맞는다면 상급 흑마법사, 나르톡이 보냈을 거야. 자네만 준비되면 이야기를 나누고 싶어하는 것 같네.', },
    nil,
  },
  [3091] = {
    '간결한 글쪽지',
    nil,
    {'간결한 글쪽지를 읽고 나라체 야영지에 있는 하루트 썬더혼과 대화해야 합니다.', },
