  [3094] = {
    '풀빛 쪽지',
    {'이보시오. 바로 몇 분 전에 전령이 그대를 찾았소. 내 생각엔 상급 드루이드인 가트 미스트러너 님이 보낸 것 같소이다. 내용이 그리 길지 않은 것 같으니 어서 읽어 보는 게 좋겠소.', },
    {'풀빛 쪽지를 읽고 나라체 야영지에 있는 가트 미스트러너와 대화해야 합니다.', },
  },
  [3095] = {
    '간결한 두루마리',
    {'아, 자네가 정신 나간 스컬지 놈들을 상대하러 나간 동안 이 간결한 두루마리가 도착했네. 두루마리에 상급 전사인 다날 스턴의 인장이 찍힌 것으로 보아 중요한 일이 있는 것 같네. 다시 여행을 떠나기 전에 시간을 내서 이 두루마리를 읽어보는 게 좋겠네.', },
    {'간결한 두루마리를 읽고 죽음의 종소리 마을에 있는 다날 스턴과 대화해야 합니다.', },
  },
  [3096] = {
    '암호화된 두루마리',
    nil,
    nil,
  },
  [3097] = {
    '성스러운 두루마리',
    {'아, 자네가 정신 나간 스컬지 놈들을 상대하러 나간 동안 이 성스러운 두루마리가 도착했네. 두루마리에 상급 사제 어둠의 사제 듀스텐의 인장이 찍힌 것으로 보아 중요한 일이 있는 것 같네. 다시 여행을 떠나기 전에 시간을 내서 이 편지를 읽어보는 게 좋겠네.', },
    {'성스러운 두루마리를 읽고 죽음의 종소리 마을의 예배당에 있는 어둠의 사제 듀스텐과 대화해야 합니다.', },
  },
  [3098] = {
    '상형 문자로 된 두루마리',
    {'아, 자네가 정신 나간 스컬지 놈들을 상대하러 나간 동안 이 상형 문자로 된 두루마리가 도착했네. 두루마리에 상급 마법사 이자벨라의 인장이 찍힌 것으로 보아 중요한 일이 있는 것 같네. 다시 여행을 떠나기 전에 시간을 내서 이 두루마리를 읽어보는 게 좋겠네.', },
    {'상형 문자로 된 두루마리를 읽고 죽음의 종소리 마을 예배당에 있는 이자벨라와 대화해야 합니다.', },
  },
  [3099] = {
    '오래된 두루마리',
    {'아, 자네가 정신 나간 스컬지 놈들을 상대하러 나간 동안 이 오래된 두루마리가 도착했네. 두루마리에 상급 흑마법사 맥시밀리언의 인장이 찍힌 것으로 보아 중요한 일이 있는 것 같네. 다시 여행을 떠나기 전에 시간을 내서 이 두루마리를 읽어보는 게 좋겠네.', },
    {'오래된 두루마리를 읽고 죽음의 종소리 마을에 있는 맥시밀리언과 대화해야 합니다.', },
  },
