  [3118] = {
    '암호화된 도장',
    {'이 도장은 상급 도적 프라훈께서 전령을 통해 전달한 것입니다. 시간이 날 때 한번 도장을 읽어보고 그분을 찾아가 보십시오. 프라훈께서 하실 말씀이 있는 것 같습니다.', },
