  [4023] = {
    '화염의 맛',
    {'용군단을 찾는 것인가? 내게 너의 가치를 입증하라, 나약한 $R.', },
    {'키루스 테레펜터스에게 자신의 가치를 증명해 보여야 합니다.$B$B키루스가 이미 당신이 가치가 없다는 사실을 알고 있는 것 같은 느낌이 듭니다.', },
