  [4492] = {
    '링고 찾기',
    {'절 좀 도와주세요! 제 친구 링고가 아까 남쪽에 있는 화산을 조사하러 갔는데, 시간이 지나도 돌아오지 않네요. 정말로 걱정돼 죽겠어요.$B$B당신이 링고를 찾을 수 있을까요? 제 수통을 가지고 가세요. 링고를 찾으면 이게 분명히 필요할 거예요.', },
    {'불기둥 마루에서 링고 스타를 찾아야 합니다.', },
  },
  [4493] = {
    '실리시드의 행진',
    {'실리시드의 존재 이유는 눈앞에 보이는 모든 생명체들을 동족이 아닌 한 모조리 집어삼켜 버리기 위함인 것 같소. 이 단 한 가지 목표에만 열중하는 녀석들이다 보니, 지금 아제로스 전체에 심각한 위협이 되고 있소.$B$B지금까지 우리가 보아 온 것은 초기 단계일 뿐이오. 녀석들의 힘은 칼림도어의 깊숙한 곳, 운고로 분화구로부터 나오는 듯하오. 타나리스 사막에 있는 가젯잔으로 가서 연금술사 페슬저그와 얘기해 보시오. 그에게 이 사실을 얘기해 주면 반드시 우리가 실리시드와 싸우는 데 도움을 줄 거요.', },
    {'가젯잔에 있는 연금술사 페슬저그와 대화해야 합니다.', },
  },
  [4494] = {
    '실리시드의 행진',
    {'실리시드의 존재 이유는 눈앞에 보이는 모든 생명체들을 동족이 아닌 한 모조리 집어삼켜 버리기 위함인 것 같소. 이 단 한 가지 목표에만 열중하는 녀석들이다 보니, 지금 아제로스 전체에 심각한 위협이 되고 있소.$B$B지금까지 우리가 보아 온 것은 초기 단계일 뿐이오. 녀석들의 힘은 칼림도어의 깊숙한 곳, 운고로 분화구로부터 나오는 듯하오. 타나리스 사막에 있는 가젯잔으로 가서 연금술사 페슬저그와 얘기해 보시오. 그에게 이 사실을 얘기해 주면 반드시 우리가 실리시드와 싸우는 데 도움을 줄 거요.', },
    {'가젯잔에 있는 연금술사 페슬저그와 대화해야 합니다.', },
  },
  [4495] = {
    '죽마고우',
    {'이베론이라는 친구가 매일 같은 시간에 저를 찾아오곤 하죠. 그런데 오늘은 코빼기도 안 보이니 이상한 일이에요. 벌써 몇 시간 전에는 왔어야 정상인데 말이죠.$B$B뭐, 제 걱정이 좀 지나치다는 건 인정해요. 이베론은 여기에서 북쪽에 있는 동굴 근처에서 많은 시간을 보내곤 해요. 거기는 사방에 거미들이 득실거리는 위험한 곳이죠.$B$B혹시 그곳을 지나면 그가 무사한지 한번 찾아봐 주시겠어요?', },
    {'북쪽의 동굴 근처에서 이베론을 찾아야 합니다.', },
  },
  [4496] = {
    '밀림 속 생물',
    {'조사해본 결과 실리시드는 벌과 비슷했습니다. 그리고 여왕을 제거하면 나머지는 모두 혼돈에 빠지게 된답니다. 여왕을 유인할 미끼를 만들어줄 테니 해치워 주십시오.$B$B하지만 먼저 미끼를 만들 재료가 좀 필요합니다. 아무런 준비도 없이 섣불리 밀림으로 들어갈 수는 없지 않소. 서쪽, 운고로 분화구에 있는 실리시드 소굴로 가 녀석들에게서 향낭 하나를 가져 오십시오. 그리고 운고로 토양 견본도 좀 필요합니다.', },
    {'가젯잔에 있는 연금술사 페슬저그에게 고리쉬 향낭 1개와 운고로 토양 견본 5개를 가져가야 합니다.', },
  },
  [4501] = {
    '테러닥스 주의',
    nil,
    nil,
  },
  [4502] = {
    '화산 활동',
    {'마본 리벳시커씨의 수습생으로 일하면서 많은 것을 배웠어요. 하지만 리벳시커씨가 너무 오래 자리를 비우고 계셔서 이제 직접 몇 가지 실험을 해볼까 생각 중이죠.$B$B가장 최근에 읽은 책이 운고로 분화구 중앙부에서 일어나고 있는 심상치 않은 화산 활동에 관한 것이었어요. 제 생각에 그곳의 정령들에게서 재를 조금만 구해온다면 많은 사실을 알아낼 수 있을 것 같아요.$B$B혹시 그곳으로 가실 계획이라면 절 위해서 재를 좀 구해다 주시겠어요?', },
    {'운고로 분화구의 화산 주위에 있는 불의 정령에게서 운고로 재 견본 9개를 모아 톱니항에 있는 리브 리즐픽스에게 가져가야 합니다.', },
  },
  [4503] = {
    '쉬즐의 비행선',
    {'난 지금 비행선을 만들고 있지!$B$B난 여기서 오도 가도 못하게 된 상황인데 워낙 길눈이 어두워서 말이야. 그리핀이나 박쥐를 타고 가젯잔으로 돌아갔으면 하는데, 그 녀석들을 타면 죽을까 무서워서 원...$B$B그래서 말인데 좀 도와주지 않겠나? 이제 날개만 만들면 되는데 테러닥스와 디메트로돈의 갈퀴 달린 비늘이면 딱 좋겠단 말이야. 나는 녀석들과 싸울만한 실력이 전혀 없지만 당신은 잘 싸울 수 있을 것 같으니 당신이 한 번 시도해 보지 않겠나?', },
    {'갈퀴 달린 디메트로돈 비늘 8개와 갈퀴 달린 테러닥스 비늘 8개를 모아 마샬의 야영지에 있는 쉬즐에게 가져가야 합니다.', },
  },
  [4504] = {
    '초강력 접착제',
    {'언제나 전 어떻게 하면 빨리 돈을 벌 수 있을까 고민한답니다. 참신한 아이디어가 하나 떠올랐는데 도움이 필요해요. 아, 물론 얼마간의 이익은 꼭 챙겨드리지요. 어때요?$B$B그래요! 제 청을 흔쾌히 들어주실 줄 알았어요!$B$B자, 그럼 운고로 분화구 북부에 있는 잿구덩이로 가서 타르를 좀 구해다 주세요. 아무 타르나 되는 건 아니고 반드시 그 잿구덩이 근처에서 사는 송진괴물들의 것이라야 합니다. 일반 타르는 점성이 부족한지 좀처럼 끈적거리지가 않거든요.$B$B그 식물처럼 생긴 생명체들의 피부에는 엽록소가 들어 있기 때문에 질이 아주 좋은 끈적거리는 타르를 얻을 수 있다고요! 그걸로 초강력 접착제를 만들어 볼 생각이에요!', },
    {'매우 끈적거리는 타르 12개를 모아 가젯잔에 있는 트란렉에게 가져가야 합니다.', },
  },
