  [4808] = {
    '펠노크 스틸스프링',
    {'당신에게 부탁하고 싶은 것이 있어요.$B$B당신이 가져다준 정수를 연구한 결과, 새끼용의 알도 냉동 보관할 수 있을 것 같아요! 이 가설을 실험해 보고 싶지만, 특별 제작한 기계 부품이 필요할 것 같네요. 내 동기인 펠노크 스틸스프링이 그것들을 만들 수 있을 거예요.$B$B여기, 이 편지를 펠노크에게 가져다주세요. 거기에 내가 필요한 게 뭔지 적혀있어요. 여명의 설원에 있는 눈망루 마을에서 펠노크를 만날 수 있을 거예요.', },
    nil,
  },
  [4809] = {
    '서리바람 키메라의 뿔',
    {'팅키가 필요로 하는 거의 모든 부품을 가지고 있지만 하나가 모자라는데 말이야. 바로 중요 부품인 서리바람 키메라의 뿔이지. 여명의 설원에 서식하는 서리바람 키메라의 뿔은 상당한 냉기를 품고 있지. 다만 멀쩡하고 금이 가지 않은 뿔에 한해서지만 말이야. 그것들이 좀 있어야 팅키의 부탁을 들어줄 수 있다고!$B$B온전한 서리바람 키메라의 뿔을 내게 가지고 와 줘. 그러면 내 팅키가 주문한 부품들을 준비하지.', },
    nil,
  },
  [4811] = {
    '붉은 수정',
    {'이곳에서 동쪽에 난폭한 달빛야수들이 출몰하고 있는데 그 수가 계속 늘어나고 있습니다. 한 때 달빛야수는 온화하고 신성한 동물로 여겨지기도 했습니다. 일부에서는 달빛야수를 여전히 신성시하고 있지만 저는 아우버다인의 안전을 위해 좀 더 현실적인 판단을 내려야 합니다.$B$B보고에 의하면 달빛야수들이 어둠해안, 동쪽 산맥을 따라 거대한 붉은 수정으로 향하고 있다고 합니다. 그런데 어느 누구도 그 수정이 어떤 물건인지, 심지어 실제로 존재하는지조차 모르고 있습니다. 그곳으로 가서 수정을 찾아 저에게 보고해 주셨으면 합니다.', },
    {'어둠해안의 동쪽 산맥을 따라 아우버다인 동쪽으로 가서 거대한 붉은 수정을 찾은 다음 아우버다인에 있는 파수꾼 글린다 날셰에게 보고해야 합니다.', },
  },
  [4812] = {
    '수정에 물이 흐르면',
    {'이 빈 시험관에 아우버다인의 달샘 물을 채우세요. 강력한 마법이 깃든 달샘의 물이라면 수정의 성분에 대한 단서를 얻을 수 있을 겁니다. 당신은 달샘의 물을 떠서 수정에 붓기만 하면 됩니다. 그다음은 달샘의 마법이 알아서 할 테니까.$B$B여기서 동쪽으로 올라가면 악령숲입니다. 그리 위험하진 않겠지만 그래도 조심하는 게 좋을 겁니다. 만약 제 생각대로 수정이 악령숲에 결속되어 있다면 아주 위험할 수도 있습니다.', },
    {'빈 시험관에 아우버다인의 달샘 물을 채운 후 어둠해안의 동쪽 산에 있는 붉은 수정을 찾아 조사해야 합니다.', },
  },
  [4821] = {
    '괴상한 알',
    {'이곳 버섯구름 봉우리에 괴상한 알이 있다는 소문이 돌기 시작했소. 그런데 이 알을 봤다는 자들도 가까이 접근하지는 못했기 때문에 자세히 살펴본 이는 아무도 없다고 하는군. 독사들이 마치 자기가 낳은 알인 양 보호하고 있기 때문이지.$B$B내가 조사를 좀 해볼 수 있게 그 괴상한 알을 가져와 주시오. 보고에 의하면 이 알은 뱀 동굴에 있다고 하오. 높새바람 봉우리의 남쪽으로, 그리고 북동쪽과 절벽을 따라가다 보면 그 밑부분에 뱀 동굴이 몇 개 있을 거요.', },
    {'높새바람 봉우리에 있는 하가르 라이트닝후프에게 괴상한 알을 가져가야 합니다.', },
  },
  [4822] = {
    '아이스크림! 아이스크림!',
    nil,
    nil,
  },
  [4841] = {
    '켄타우로스 처치',
    {'이 켄타우로스들을 처치하는 일을 부디 맡아주게. 분노한 우리가 복수를 맛볼 수 있게 해주게나. 높새바람 봉우리 북쪽에 있는 켄타우로스 야영지를 공격하게! 분노의 힘이 영웅의 손을 이끌어 줄 걸세!', },
    {'갈라크 척후병 12명, 갈라크 사냥꾼 10명, 갈라크 바람전사 6명을 처치한 후, 높새바람 봉우리에 있는 절벽파수꾼 롱혼에게 가야 합니다.', },
  },
  [4861] = {
    '성난 올빼미야수',
    nil,
    {'여명의 설원 남부 지역에서 야론 스톤셰이퍼의 야영지를 찾아서 부서진 보급품 상자를 되찾아 와야 합니다.', },
  },
  [4862] = {
    '더러운 거미알',
    {'믿거나 말거나 길들어진 흉악한 괴물들을 사려고 하는 자들이 있어요!$B$B검은바위 첨탑의 거미알들을 채집하려고 비쥬를 보냈는데, 웬일인지 소식이 없어요. 어쩌면 당신이 나를 도와줄 수 있을지도 모르겠군요?$B$B이봐요. 검은바위 첨탑으로 가서 거미 알들을 채집해다 주지 않겠어요? 그것들을 가지고 오면 보수도 주겠거니와, 괴물 한 마리를 길들여 주도록 하죠!', },
    {'검은바위 첨탑으로 가서 키블러를 위해 첨탑 거미알 15개를 수집해야 합니다.$B$B들리는 소문으로는 그 알들은 거미들이 서식하는 근처에서 찾을 수 있다고 합니다.', },
  },
  [4863] = {
    '성난 올빼미야수',
    nil,
    nil,
  },
  [4864] = {
    '성난 올빼미야수',
    nil,
    nil,
  },
  [4866] = {
    '여왕거미의 독',
    {'그건 세상에서 가장 강력한 독 중에 하나라고 할 수 있지!$B$B내가 첨탑에 갔을 때 검은바위 부족 녀석들이 이 독의 위력에 대해서 얘기하고 있던 것을 엿들었소. 그들은 그것을 그물걸이 통로의 거대하고 흉포한 여왕 불그물거미의 이름을 따서 \'여왕의 독\'이라고 부른다오.$B$B당신이 내게 구해다 주어야 할 것이 바로 그거요. 당신의 혈관에 \'흐르는\' 살아 있는 독 표본이지. 그저 분비물만으로는 충분하지 않을 테니까! 그 여왕 거미의 독에 걸린 상태로 내게 돌아오시오!$B$B그럼 그 독을 짜내도록 하지!$B$B<기대에 차서 손을 비비는 존>', },
    {'검은바위 첨탑 중심부에서 여왕 불그물거미를 찾을 수 있습니다. 여왕 불그물거미를 공격하거나 접근하여 그 독에 중독되어야 합니다. 독에 걸릴 확률을 높이기 위해서는 거미를 처치해야 할 수도 있습니다. 중독되면 털보 존에게 돌아가서 그가 그 독을 추출하게 해야 합니다.', },
  },
  [4867] = {
    '우로크 둠하울',
    {'으악! 말하기가 힘들다!$B$B이 쪽지를 가지고 가라! 내 부적을 가져와!', },
    {'와로쉬의 두루마리를 읽어야 합니다. 와로쉬에게 그의 부적을 가져가야 합니다.', },
  },
  [4881] = {
    '암살 음모',
    {'감히 카나티 그레이클라우드라는 타우렌이 버섯구름 봉우리에 사냥용 야영지를 세웠다. 우리가 가서 그를 처치하고 야영지와 그를 따르는 자들을 모두 없애 버려야 한다. 구름 승강장 남서쪽에 있는 작은 전초기지에서 모이도록 하라.', },
    {'백사장 야영지에 있는 카나티 그레이클라우드에게 가서 암살 음모에 대해 경고해 줘야 합니다.', },
  },
  [4882] = {
    '수호에 대한 비밀',
    {'얼마 전 쓰러뜨린 올빼미야수의 목에서 떨어진 목걸이가 얼마나 중요한 것인지는 확실치 않습니다. 목걸이는 주기적으로 밝게 빛났다 흐려졌다 합니다.$B$B올빼미야수들에 박식한 트룰 페일베인이 더 자세한 얘기를 해줄 수 있을지도 모릅니다. 그에게 물어보는 것이 좋겠습니다.', },
    {'피멍울 초소에 있는 트룰 페일베인에게 푸른 깃털달린 목걸이를 가져가야 합니다.', },
  },
  [4883] = {
