  [5713] = {
    '한 방에 한 놈씩',
    nil,
    {'파수꾼 아이나샤를 보호한 다음 잿빛 골짜기의 마에스트라 주둔지에 있는 오나에야와 대화해야 합니다.', },
  },
  [5721] = {
    '다로우골 전투',
    {'이제 당신은 다로우골 전투에 직접 참전해서 조셉 레드패스를 구해야 해요.$B$B이 유물 더미를 다로우골 마을 광장에 놓으면 다로우골의 영혼들이 깨어날 거예요. 그들과 함께 전투를 시작하세요. 그리고 두 가지를 명심하세요. 다빌은 호르구스가 죽기 전까지 살아남아 있어야 하고, 레드패스는 타락하기 전까지 보호해 주다가 타락한 후 처치해야 해요.$B$B타락한 레드패스를 물리치면 레드패스의 영혼을 구원할 수 있어요. 전투 후에 나타날 조셉 레드패스의 영혼과 대화를 나누고 그의 딸 파멜라에게 돌아가세요.', },
    nil,
  },
  [5722] = {
    '잃어버린 가방 찾기',
    {'마가타님께서 성난불길 협곡에 대해서 관심을 가지는 건 트로그뿐만이 아닙니다. 마가타님께서는 그분의 하인 중에 한 명인 마우르 그림토템이 협곡에 있는 동안 특별한 무엇인가를 발견했다는 소식을 들으셨는데 마우르는 어떤 물건인지 마가타님께 단 한 번도 보여주지 않았습니다. 마가타님께서는 마우르의 시체가 있다면 그걸 찾고 싶어하십니다. 그리고 마우르가 트로그와 대화를 시도하면서 발견한 어떤 흔적이든 찾고 싶어하십니다.$B$B마가타님께서 관심을 가질 만한 물건을 가져다 주면 분명 후하게 보상해 주시리라 믿습니다.$B$B다시 한번, 오그리마의 가장 어두운 지역으로 가서 협곡을 찾아보시기 바랍니다.', },
    {'성난불길 협곡에서 마우르 그림토템의 시체를 찾아 도움이 될 만한 것들을 찾아야 합니다.', },
  },
  [5723] = {
    '성난불길 협곡의 트로그',
    {'오그리마 지하의 트로그라고 알려진 강인한 생물들이 용암이 지하도를 가득 메운 깊숙한 곳에서 지상으로 나오기 시작했습니다. 자비로우신 마가타님께서는 트로그와 평화를 유지하려고 하셨지만 트로그는 사신을 공격해서 죽이고 말았습니다. 마가타님께서는 트로그의 그런 행동을 용납하지 않으시며 이제는 트로그를 호드를 위협하는 생물로 간주하고 계십니다.$B$B마가타님께서는 트로그가 호드를 해치기 전에 트로그의 위협을 종식해줄 것을 원하십니다. 성난불길 협곡을 찾아서 트로그들을 모조리 해치워 주십시오.', },
    {'오그리마에 있는 성난불길 협곡을 찾아 성난불길 트로그 8마리와 성난불길 주술사 8마리를 처치하고 썬더 블러프에 있는 라하우로에게 가야 합니다.', },
  },
  [5725] = {
    '파괴해야 할 힘',
    {'자네도 당연히 알겠지만 불타는 군단이 그들의 추종자들에게 선물을 주었다. 실바나스 여왕님께서는 그 선물 중 일부는 앞을 멀리 내다볼 줄 모르는 우매한 이들의 손에 있는 것보다는 여왕님의 손에 있는 것이 좋다고 생각하신다.$B$B오그리마 아래의 동굴에 숨어 있는 이글거리는 칼날단으로 알려진 어둠의 의회의 한 종파가 대표적인 예라고 할 수 있지. 어둠의 여왕님께서 말씀하시기를 이글거리는 칼날단의 일원들이 두 권의 강력한 마법책을 지니게 되었고 그 책을 빌리고 싶다고 하셨다. 어둠의 여왕님을 위해 책을 가지고 와라.', },
    {'언더시티에 있는 바리마트라스에게 어둠의 주문서와 황천의 마법서를 가져가야 합니다.', },
  },
  [5726] = {
    '내부의 배신자',
    {'우리 중 배신자가 있다는 건 절대 참을 수 없다... 하지만, 내가 직접 나서서 성급하게 실력을 행사하는 일은 현명하지 못해... 이 땅의 타락을 막기는커녕 오히려 악화시키고 말 것이다.$B$B하지만, 그대와 같이 혈기왕성한 모험가라면 나의 부관들이 갈 수 없는 곳까지 들어가서 진실을 밝혀내고 사나운 악마를 물리칠 수 있을 것이다.$B$B용기가 있다면 오그리마 동쪽의 해골 바위굴로 가서 불타는 칼날단 부관의 휘장을 찾아오라.', },
    {'오그리마에 있는 스랄에게 부관의 휘장을 가져가야 합니다.', },
  },
  [5727] = {
    '내부의 배신자',
