  [6261] = {
    '덩가 롱드링크',
    nil,
    {'그리핀 조련사 덩가 롱드링크에게 오스릭의 상자를 가져가야 합니다.', },
  },
  [6281] = {
    '스톰윈드로 이동',
    {'요금을 조금만 내시면 그리핀을 타고 스톰윈드로 가서 루이스의 쪽지를 오스릭에게 전할 수 있습니다. 다른 방법으론 이렇게 빨리 못 가실 겁니다.$B$B괜찮다 싶으면 준비를 끝내신 후 저에게 다시 오시기 바랍니다. 그리 비싸지 않은데다가 그만한 값어치는 될 겁니다!', },
    {'그리핀 조련사 토르에게 요금을 지급하고 그리핀을 탄 다음 루이스의 쪽지를 스톰윈드에 있는 오스릭 스트랑에게 가져가야 합니다. 오스릭은 스톰윈드의 구 시가지에 있는 품질보증 갑옷 상점에 있습니다.', },
  },
  [6282] = {
    '하피의 위협',
    {'타우렌은 이 땅의 수호자 역할을 매우 자랑스러워하지. 그런 의미에서 자네는 내가 왜 돌발톱 산맥에 서식하는 하피의 숫자가 늘어나고 있는 것에 민감한지 이유를 알 수 있을 걸세.$B$B칼림도어에서 그 수가 점차 느는 하피 중 가장 많은 것은 혈폭풍 일족이지. 그들이 모든 생물들을 해치는 형편이라 죄 없는 생물들이 둥지에 숨어서 밖으로 나오지도 못할 지경이라네.$B$B자네가 이 골칫덩이들을 좀 처리해주지 않겠나?', },
    {'해바위 야영지에 있는 마그그랜 어스바인더가 혈폭풍 하피, 복병, 암살자, 약탈자를 각각 7마리씩 처치해 달라고 했습니다.', },
  },
  [6283] = {
    '혈폭풍 일족의 혈통',
    {'혈폭풍 하피의 수가 줄긴 했지만 그것만으론 충분하지 않네. 진정한 해결책은 그들의 우두머리인 혈폭풍 갈퀴발톱을 처치하는 거지!$B$B그녀는 그 더러운 떼거리의 여왕 노릇을 하고 있다네. 갈퀴발톱만 없앨 수 있다면 하피들의 숫자는 확실히 줄어들 거야.$B$B잿더미 계곡으로 돌아가게. 서쪽 언덕에 그녀가 있을 거야. 쉽지는 않겠지만 훌륭한 보상이 기다리고 있으니 보람이 있을 거야.', },
    {'해바위 야영지에 있는 마그그랜 어스바인더가 혈폭풍 갈퀴발톱을 처치하고 그 증거로 그녀의 유해를 가져오라고 했습니다.', },
  },
  [6284] = {
    '거미의 공포',
    nil,
