  [6381] = {
    '새로운 생명',
    {'$C|1이여;여;, 당신이 보신 것처럼 고통받는 이 땅에서도 생태계의 힘은 아주 강력하게 순환하며 생명을 틔울 수 있답니다. 이러한 생명의 순환을 돕기 위해 우리는 맡은 소임을 다해야 해요. 서쪽에 있는 파괴의 중심지, 잿더미 계곡에 이 씨앗들을 심어야만 합니다. 아직까지 상처를 받고 있는 그땅에는 하피와 성난 불의 정령들이 몰려 다니고 있을 테니 조심하세요.$B$B자, 제가 마법을 부여한 이 가이아 씨앗들을 가지고 가서 비옥한 가이아 흙더미에 심도록 하세요. 생명의 순환은 반드시 계속돼야만 해요!', },
    {'가이아 흙더미에 가이아 씨앗 10개를 심은 후, 마력 깃든 가이아 씨앗을 해바위 야영지에 있는 탐라에게 가져가야 합니다.', },
  },
  [6382] = {
    '잿빛 골짜기 사냥 대회',
    {'$C|1이여;여;, 그대의 강인함에 깊은 감동을 받았네. 사냥에 대한 열정을 보니 이제 더 큰 사냥감... 그러니까 더욱더 어려운 도전을 해도 되겠다는 확신이 드네.$B$B잿빛 골짜기의 숲에는 야생동물들이 방대하게 서식하고 있는데 호드에서는 정치적, 영적 목적으로 그들을 조종하려고 하지. 아직 이렇다 할 영웅이 배출되지 않은 곳에서 자신을 증명해 보이고 싶다면 토막나무 주둔지에 있는 세나니 썬더하트의 인도를 받게. 불모의 땅에서 나오는 길을 따라 똑바로 북쪽으로 가면 된다네.', },
    {'잿빛 골짜기의 토막나무 주둔지에 있는 세나니 썬더하트와 대화해야 합니다.', },
