  [6393] = {
    '정령들의 전쟁',
    {'해일의 심판장에서는 이곳 돌발톱 산맥에 벌어지고 있는 불의 정령들과의 대전쟁을 위해 저를 보냈어요. 우리 물의 정령들의 군주, 바다사냥꾼 넵튤론께서는 이 세계에서 모든 불의 정령들이 사라지는 그날을 기다리고 계시죠.$B$B$N, 제가 해일의 심판장에서 인정을 받을 수 있도록 잿더미 계곡에 있는 불의 정령들을 파괴해 주셨으면 해요. 불의 정령들이 파괴되면서 남기는 발화석을 모아 주시면 마땅한 사례를 할게요.', },
    {'해바위 야영지에 있는 츄나만에게 발화석 10개를 가져가야 합니다.', },
  },
  [6394] = {
    '타즈릴의 곡괭이',
    {'$N, 당신은 이곳에서 믿을 만한 $R|1으로;로; 정평이 나 있소. 다른 임무도 좀 맡아 보시겠소?$B$B얼마 전 광물을 찾기 위해 북쪽에 있는 동굴을 조사하다가 그만 제일 아끼는 곡괭이를 두고 왔소이다. 나중에 찾으러 갔더니만 글쎄 포악한 짐승들로 동굴이 꽉 차있지 뭐요?! 그 동굴은 불타는 칼날단 소굴이라고 한다오. 거기 가서 내 곡괭이를 좀 가져다주시겠소?$B$B폭포가 있는 곳에 곡괭이를 두고 왔소이다. 그 곡괭이에는 마법이 걸려 있기 때문에 어둠 속에서도 볼 수 있을 거요. 당신이 장님만 아니라면 헛걸음할 걱정은 안 해도 된다는 얘기지. 단, 그걸 지키고 있는 짐승들은 걱정해야 할 거요!', },
    {'타즈릴의 곡괭이를 작업반장 타즈릴에게 가져가야 합니다.', },
  },
  [6395] = {
    '말라의 마지막 소원',
    {'제 친구 말라 핍스는 역병이 퍼지기 전에 남편 사무엘과 함께 살았답니다. 하지만 역병이 퍼지고 사무엘은 스컬지의 전사가 되고 말았지요.$B$B말라는 언데드가 되는 꼴은 면했지만 이성을 상실한 남편 손에 그만 죽고 말았답니다. 하지만 말라의 사랑이 너무 큰 나머지 사랑하는 남편과 함께 묻히고 싶다는 마지막 소원을 빌었어요.$B$B사무엘 핍스는 죽음의 종소리 마을 북동쪽 길에 있는 버려진 야영지를 배회하고 있답니다. 그와 싸워 이겨 말라의 소원을 들어주세요. 우리 묘지의 첫 번째 줄에 있는 말라의 묘에 그를 묻으시면 됩니다.', },
    {'사무엘 핍스를 처치하고 그의 유골을 말라의 묘로 옮긴 다음 수련사제 엘레스에게 가야 합니다.', },
  },
  [6402] = {
    nil,
    {'당신은 스톰윈드의 문에서 윈저를 만나야합니다. $B$B윈저가 공개하려고하는 것은 왕국의 기초를 뒤 흔들어 놓을 것입니다! $B$혼자서 벤처하려고하지 마십시오. $N. 당신은 군대를 모아야합니다! 당신의 동포들을 모으십시오. 팔에!', },
    nil,
  },
  [6421] = {
    '구릉바위 협곡',
    {'이곳 돌발톱 산맥에 뭔가 수상한 일이 벌어지고 있는 것 같아. 바람에 실려 전해져 오는 이 긴장을 자네도 느낄 수 있나?$B$B여기서 남동쪽으로 구릉바위 협곡에는 깊은 동굴이 하나 있네. 그곳에선 코볼트들이 리조나이트라는 희귀한 수정을 미친 듯이 캐고 있지. 그 표본이 좀 필요하네. 그리고, 동굴 깊숙한 곳까지 들어가서 뭐가 있는지 좀 조사해 주게.$B$B어서 떠나게. 난 이 산맥 깊은 곳에 어떤 악의 무리가 숨어 있는지 반드시 알아내야만 해.', },
    {'구릉바위 협곡에 있는 동굴을 탐사한 다음 해바위 야영지에 있는 모르로갈에게 리조나이트 수정 10개를 가져가야 합니다.', },
  },
  [6441] = {
    '사티로스의 뿔',
    {'안녕하세요! 전 잿빛 골짜기를 지나 톱니항으로 가던 길에 이곳 토막나무 주둔지에 잠깐 들렀지요. 돈벌이가 될 만한 걸 늘 찾아보는데, 요즘 톱니항에서는 사티로스 뿔의 수요가 아주 큰가 봐요.$B$B이 주변에서 물어봤더니 어둠의 터, 사티르나르, 그리고 사비안의 사티로스들이 제가 찾고 있는 뿔을 갖고 있는 것 같더군요. 전 그런 사티로스들의 상대로는 어림도 없지만... 당신은 해볼 만 할 것 같네요!$B$B제게 그 뿔을 모아주시면 후하게 사례를 하겠어요.', },
    {'사티로스의 뿔 16개를 모아 토막나무 주둔지에 있는 픽셀에게 가져가야 합니다.', },
  },
  [6442] = {
    '조람 해안의 나가',
    {'해안가에 악마가 출몰하고 있습니다.$B$B오래전 멸망하여 바다 밑에 가라앉은 도시, 조람이 묻혀 있는 곳에 말입니다. 세인들의 기억에서 거의 잊혀진 잃어버린 도시죠.$B$B최근 어떤 이유 때문인지는 모르지만, 나가들이 다시 나타났습니다. 이유야 어떻든 우리가 차지하기 위해 그렇게 열심히 애썼던 땅이니만큼 꼭 지켜내야 합니다.$B$B녀석들의 머리 20개를 가져오세요! 나가 녀석들을 다시 심연 속으로 처넣어 주십시오!', },
    {'조람 해안에 있는 마루카이에게 성난지느러미 머리 20개를 가져가야 합니다.', },
  },
  [6461] = {
    '식탐꾼들',
    {'이곳 말라카진에 사는 우리 트롤은 대지의 축복으로 번성해 왔습니다. 돌발톱 산맥은 자원을 넉넉히 공급해주는 거대한 사냥터였고요.$B$B그런데 최근 들어 저녁 식사 때쯤이면 어김없이 초대도 받지 않은 손님들이 나타난답니다. 돌발톱 산맥의 거미들이 어둑어둑해질 때만 되면 우리가 사냥해 놓은 음식을 노리고 공격하지 뭐예요.$B$B이 불쾌한 놈들을 좀 없애주신다면 우리 말라카진 트롤들이 충분한 보답을 하겠습니다. 돌발톱 산맥 어디를 가나 그 징글징글한 거미들을 쉽게 찾을 수 있답니다. 여기서 북쪽으로 조금만 가면 제 말이 사실이라는 걸 확인할 수 있을 거예요.', },
    {'말라카진에 있는 젠질라가 동굴이끼 땅거미 10마리와 동굴이끼 독거미 7마리를 처치해 달라고 했습니다.', },
  },
  [6462] = {
    '트롤 부적',
    nil,
    nil,
  },
  [6482] = {
    '루울에게 자유를',
    {'어? 자네는 누구인가?$B$B$N|1이라고;라고;? 휴, 펄볼그는 아니로군. 저주 받을 녀석들! 내가 곰인 줄 알고 잡아들이더니 이젠 쫄쫄 굶기고 있어! 점점 더 기운이 빠지는군...$B$B탈출할 수 있게 도와줘. 그리고 동쪽에 있는 토막나무 주둔지에서 내 누이 야마를 만나보게.$B$B$N! 나는 여기서 꼭 빠져나가야 해!', },
    {'루울을 호위해 탈출한 후, 토막나무 주둔지에 있는 야마 스노우후프와 대화해야 합니다.', },
  },
  [6501] = {
    '용의 눈',
    {'내가 줄 수 있는 거라곤 부서진 메달 조각 하나뿐이라네.$B$B자네는 새 열쇠를 만드는 걸 도와 줄 다른 용혈족을 찾아야만 해. 열쇠를 확보하면 먼지진흙 습지대에 있는 용의 둥지로 가야 하네. 그곳에서 여왕용의 둥지를 찾을 수 있을 거야.$B$B자네를 도와줄 다른 용군단은 어디서 찾느냐고? 안타깝게도 그건 나도 모르네... 다만 그들이 존재한다는 얘길 들었네. 심지어는 우리 사이에서 일하며 함께 산다고도 하지. 인간형 종족으로 위장한 채 말이지. 행운을 빌겠네.', },
    {'전 세계를 뒤져 용의눈 조각의 힘을 복원할 수 있는 자를 찾아야 합니다. 이와 관련해 알고 있는 유일한 정보는 그러한 자들이 존재한다는 것뿐입니다.', },
  },
  [6502] = {
    '비룡불꽃 아뮬렛',
    nil,
    nil,
  },
  [6503] = {
    '잿빛 골짜기 정찰꾼',
    nil,
    nil,
  },
  [6504] = {
    '잃어버린 열 두쪽',
    {'난 톱니항에서 이 벌목기를 사서 전쟁노래 벌목기지로 근무를 하러 돌아가던 길이었지. 벌목기 작동법이 적힌 조작설명서도 받았는데 바람이 워낙 심하게 불어서 그만 손에서 놓쳐 버렸지 뭐야. 책장들이 여기저기 날아가 버려서 남은 거라고는 표지뿐이더군.$B$B그 설명서가 없이는 이 벌목기를 어떻게 쓸 방도를 모르겠으니... 어디 있는지 모르겠지만 자네가 나 대신 좀 찾아 주지 않겠나?', },
    {'잃어버린 벌목기 조작설명서 열 두쪽을 찾아서 1장, 2장, 3장으로 합쳐야 합니다. 그런 다음 토막나무 주둔지 근처에 있는 구르다 레이지스카에게 돌려 줘야 합니다.', },
  },
  [6521] = {
    '사악한 동맹',
    {'말킨의 글에 의하면 지금쯤 가시덩굴 구릉 밖에 있겠군. 그 사절 놈을 해치워야 해. 리치 왕의 바로 코앞에서 임무에 실패했단 사실을 깨닫게 해주자고.$B$B그가 거기 있는 것을 보니 우리가 그 지역에서 발견한 사실도 설명이 되는군. 역병의 징후가 보이는 동안 일부 가시멧돼지들이 무서울 정도로 강력해지고 있다네. 그들 간에 모종의 계약이 있었을 것이 분명해.$B$B말킨을 찾아서 처치한 다음 그 증거를 가지고 다시 오도록 하게.', },
    nil,
  },
  [6522] = {
    '사악한 동맹',
    {'저희의 제안에 대해 잘 아시리라 생각합니다. 불모의 땅과 그 너머의 땅을 차지할 수 있는 이번 기회는 굉장한 겁니다. 대신에 저희는 당신의 도움을 바랍니다. 그저 이 지역에서 저희가 자유로이 행동을 취할 수 있게만 해주시면 됩니다.$B$B이 문제에 대해 좀 더 논의하게 해주십시오. 저희에게는 힘이 있으며 저희와 뜻을 같이하려는 현명한 자들과 함께할 의사가 있습니다. 당분간 가시덩굴 구릉 밖에 머무르겠습니다.$B$B옥체 보중하소서...$B사절 말킨', },
    nil,
  },
  [6523] = {
    '카야 구출',
    {'정말 고마워요! 당신 덕에 이젠 살았어요... 내가 자유의 몸이 된 걸 그들이 발견하기 전에 어서 이곳을 빠져나가야 해요. 아파라제 야영지까지만 지켜주시면, 거기서부터는 길을 아니까 저 혼자 갈 수 있어요.$B$B참, 제 아버지 마카바 플랫후프께 이 기쁜 소식을 좀 전해주시겠어요? 아마 제 소식을 애타게 기다리고 계실 거예요.', },
    {'카야 플랫후프를 아파라제 야영지까지 호위한 다음 돌발톱 산맥 남동쪽 가장자리에 있는 마카바 플랫후프에게 소식을 전하러 가야 합니다.', },
  },
  [6541] = {
    '카드라크에게 보고',
    {'자네, 북쪽에 있는 잿빛 골짜기에 가 본 적이 있나? 호드에서는 최근 그곳 조람 해안에 전초기지를 세웠지. 우린 늘 새로운 전선을 지킬 신병들이 필요하다네.$B$B관심이 있거든 불모의 땅 북부에 있는 카드라크를 찾아가보게. 감시탑에 있을 걸세. 그가 잿빛 골짜기 공격을 주도하고 있으니 자네에게 자세한 내용을 알려 줄 수도 있지.', },
    {'불모의 땅의 북부 감시탑에 있는 카드라크에게 보고해야 합니다.', },
  },
  [6542] = {
    '카드라크에게 보고',
    {'자네, 북쪽에 있는 잿빛 골짜기에 가 본 적이 있나? 호드에서는 최근 그곳 조람 해안에 전초기지를 세웠지. 우린 늘 새로운 전선을 지킬 신병들이 필요하다네.$B$B관심이 있거든 불모의 땅 북부에 있는 카드라크를 찾아가보게. 감시탑에 있을 걸세. 그가 잿빛 골짜기 공격을 주도하고 있으니 자네에게 자세한 내용을 알려 줄 수도 있지.', },
    {'불모의 땅의 북부 감시탑에 있는 카드라크에게 보고해야 합니다.', },
  },
  [6543] = {
    '전쟁노래 보고서',
    {'최근 잿빛 골짜기에 부하 몇을 보냈지... 그들에게 이 보고서를 전하고 정찰 내용에 대한 보고를 받아올 전령이 필요한데...$B$B먼저 서쪽 조람 해안가에 있는 조람가르 전초기지로 가도록. 그다음 북쪽 토막나무 주둔지에 들르고, 다시 동쪽으로 길을 따라 아즈샤라 경계 가까이 가야 하네. 그곳에 각기 길잡이, 정찰병, 그리고 척후병이 대기하고 있을 걸세.$B$B그들에게 내가 주는 이 보고서를 전하고 최신 보고 내용을 받아 오도록.', },
    {'먼저 보고서 뭉치를 여십시오. 전쟁노래 길잡이, 전쟁노래 정찰병, 그리고 전쟁노래 척후병에게 전쟁노래 보고서를 가져가야 합니다. 그들의 보고 내용을 담고 있는 최신정보 3개를 불모의 땅 북부 감시탑에 있는 카드라크에게 가져가야 합니다.', },
  },
  [6544] = {
    '토렉의 공격',
    {'이보게, $C 친구! 이리 와봐!$B$B난 우리 대원들과 곧 서쪽에 있는 은빛날개 전초기지를 공격할 생각일세. 거기 지도자인 두리엘 문파이어가 바로 우리의 목표인데, 정찰병의 보고에 의하면 파수꾼과 전사들이 철통같이 방어를 하고 있다는군. 아주 영광스러운 전투가 될 거야!$B$B우리를 도와 그 영광을 함께 나누지 않겠나, $N? 자네 임무는 일이 모두 끝났을 때 토막나무 주둔지에 있는 내 상관 에르톡 레이지터스크에게 임무의 성공을 보고해주면 되는 걸세.$B$B어쩌겠나? 우리와 함께하겠나?', },
    {'은빛날개 전초기지와 두리엘 문파이어를 공격하는 토렉을 호위한 후, 토막나무 주둔지에 있는 에르톡 레이지터스크에게 보고해야 합니다.', },
  },
