  [6961] = {
    '겨울 할아버지, 우리 마을에 오셨네!',
    {'즐거운 겨울맞이 축제가 되길! 겨울 할아버지에게 꼭 인사하는 것 잊지 말고! 올해 착한 일을 많이 했으면 겨울맞이 축제에 선물을 받게 될 거야. 겨울 할아버지에게 올해 갖고 싶은 게 뭔지 꼭 얘기하라고!$B$B참, 올 한해 고마웠던 친구들에게 한턱 내는 건 어때? 마침 내게 선물용으로 아주 괜찮은 물건이 있는데 말이야. 굴뚝나무 목장조합에서 직접 재배한 신선한 음식을 맛보게! 맛도 그만이지만 몸에도 아주 좋지!', },
    nil,
  },
  [6962] = {
    '겨울 할아버지 대접하기',
    {'거기 $G총각:아가씨;, 이 겨울 할아버지가 조그만 부탁이 하나 있는데 들어줄래요?$B$B당신이 객관적으로 봐도 여기 온종일 서 있는 게 그리 유쾌한 일은 아니겠지요? 달콤한 걸 좀 먹으면 기운이 날 것 같은데... 생강 과자빵이랑 시원한 우유만 있어도 참 좋겠어요. 이 할아버지랑 친구 하고 싶지요? 그렇다면 좀 갖다줄래요?', },
    {'오그리마의 겨울 할아버지에게 생강 과자빵 5개와 시원한 우유 한 잔을 가져가야 합니다.', },
  },
  [6964] = {
    '축제의 유래',
    {'선물이나 주고받고 흥청거리다니... 바보들!$B$B경박한 고블린들이 겨울맞이 축제의 진정한 의미를 퇴색시키고 있네. 겨울맞이는 곧 부활과 회복을 뜻하지. 대지는 겨울 할아버지께서 내리신 포근한 눈을 덮고 깊고 조용한 잠을 자는 거야. 저기 우스꽝스러운 빨간 옷을 입고 있는 멍청이가 진짜 겨울 할아버지일 리가 없잖아!$B$B축제의 진정한 의미를 알고 싶거든 사고른 크레스트스트라이더를 찾아가도록! 선물에 정신 팔린 놈들이 천지인 이 시기에 역사가 주는 진정한 교훈에 관심을 가진 자네 같은 이도 있다는 걸 알면 분명 놀랄 거야.', },
    {'오그리마의 지혜의 골짜기에 있는 사고른 크레스트스트라이더와 겨울맞이 축제에 대해 대화해야 합니다.', },
  },
  [6981] = {
    '빛나는 조각',
    {'이 조각은 밝은 녹색을 띠고 있습니다. 겉은 단단해 보이지만 수정 안에는 연기 같은 것이 소용돌이치고 있습니다. 그 연기는 눈길을 끌며 뭔가 굉장히 불길한 예감이 듭니다.$B$B혹시 이 이상한 조각에 대한 얘기를 해줄 이가 있는지 톱니항에서 찾아보는 게 좋을 것 같습니다.', },
    {'톱니항으로 가서 빛나는 조각에 대해 자세히 얘기해 줄 수 있는 이를 찾으십시오.$B$B그에게 지시를 받아, 빛나는 조각을 전달해야 합니다.', },
  },
  [6982] = {
    '얼음이빨 광산 보급품',
    {'얼음이빨 광산은 호드의 보급창고라오. 녀석들이 부대에 지급하는 식량은 소화가 안 되고 무기나 방어구 상태도 정말 형편없지! 하지만... 요즘처럼 보급품이 귀한 때에는 녀석들이 쓰는 것이라도 귀중한 자원이라오.$B$B얼음이빨 광산에 들어가서 녀석들의 보급품을 좀 가져오시오. 그렇게 하면 얼라이언스군이 계속 전투 태세를 유지하는 데 도움이 될 뿐 아니라, 호드 전사들에게서 녀석들이 꼭 필요로 하는 장비들을 빼앗게 되는 일거양득의 효과를 보게 될 것이오.$B$B얼음이빨 광산은 남쪽 멀리, 호드 진영 깊숙한 곳에 있소이다. 행운을 빌겠소.', },
    {'던 발다르에 있는 얼라이언스 병참장교에게 얼음이빨 광산 보급품 10개를 가져가야 합니다.', },
  },
  [6983] = {
    '고약한 녀석...',
    nil,
    nil,
  },
  [6984] = {
    '굴뚝나무 목장조합의 선물!',
    nil,
    nil,
  },
  [6985] = {
    '깊은무쇠 광산 보급품',
    {'스톰파이크 드워프들이 깊은무쇠 광산을 보급품 창고로 이용하고 있소. 그곳을 습격해 그 안에 있는 보급품을 가져오도록 하시오. 그렇게 하면 우린 알터랙 계곡에서 스톰파이크 경비대의 활동을 저지할 수 있을 뿐 아니라 필요한 물건들을 얻을 수도 있지!$B$B조심하시오, $N. 깊은무쇠 광산은 얼라이언스 영토 깊숙한 곳에 있는데다, 그 광산의 지배자는 알터랙 산맥에 쌓인 무수한 눈송이만큼이나 자주 바뀌니까 말이오.', },
    {'서리늑대 요새에 있는 서리늑대 병참장교에게 깊은무쇠 광산 보급품 10개를 가져가야 합니다.', },
  },
  [7001] = {
    '빈 우리',
    {'알터랙 계곡에서의 전투로 인해 우리 병사들 중 상당수가 희생되었습니다. 새롭게 전의를 북돋워줘야 할 때입니다!$B$B빈 우리를 가득 채울 수만 있다면 기병대가 다시 한번 전장에 나가 싸울 수 있을 겁니다!$B$B이 재갈을 가지고 가서 이 근방 숲에 살고 있는 서리늑대들을 찾아 길들이십시오. 길들인 서리늑대를 데리고 오면 제가 우리에 맡아 두겠습니다.$B$B우리에 서리늑대가 충분히 모이면 우리 기병대가 다시 한 번 출전하여 최전선에서 정찰을 돌고 있을 병력과 합류할 수 있을 겁니다.', },
    {'알터랙 계곡에서 서리늑대를 찾아야 합니다. 발견한 서리늑대 근처에서 서리늑대 재갈을 사용하십시오. 그러면 서리늑대는 당신을 따라다니기 시작할 것입니다. 서리늑대 야수 관리인에게 돌아가 서리늑대를 포획한 공로를 인정받으십시오.', },
  },
  [7002] = {
    '산양 통가죽 고삐',
    nil,
    nil,
  },
  [7003] = {
    '변형된 거인',
    {'이렇게 밖에서 지내려면 저 거인들에게 손을 좀 써야겠어요! 녀석들은 절대 장사에 도움이 안 되니까요. 안 그래요?$B$B저는 더욱 뛰어난 고블린 과학 기술을 이용해서 노움 난쟁이 광선을 변형하는 작업을 하고 있답니다! 전 죽는다든가 하는 불편은 피하고 싶으니까 당신이 가져가서 페랄라스에 사는 아무 거인들한테나 시험 삼아 이걸 써 봤으면 좋겠군요. 거인이 작아진 후에 축소화 잔류물을 채취할 수 있을 테니 그걸 좀 가져다주세요!', },
    {'페랄라스에서 눈에 띄는 아무 거인에게나 조르빈의 형상축소기를 사용해 좀 더 다루기 쉬운 형태로 변환하십시오. 페랄라스의 잊혀진 해안 부두에 있는 조르빈 팬대즐에게 변형된 거인들에게서 얻을 수 있는 축소화 잔류물 15개를 가져가야 합니다.', },
  },
  [7021] = {
    '겨울 할아버지, 우리 마을에 오셨네!',
    nil,
    nil,
  },
  [7022] = {
    '겨울 할아버지, 우리 마을에 오셨네!',
    {'즐거운 겨울맞이 축제가 되길! 겨울 할아버지에게 꼭 인사하는 것 잊지 말고! 올해 착한 일을 많이 했으면 겨울맞이 축제에 선물을 받게 될 거야. 겨울 할아버지에게 올해 갖고 싶은 게 뭔지 꼭 얘기하라고!$B$B참, 올 한해 고마웠던 친구들에게 한턱 내는 건 어때? 마침 내게 선물용으로 아주 괜찮은 물건이 있는데 말이야. 굴뚝나무 목장조합에서 직접 재배한 신선한 음식을 맛보게! 맛도 그만이지만 몸에도 아주 좋지!', },
    {'아이언포지 굴뚝나무 목장조합상인 근처에 있는 겨울 할아버지를 만나 대화해야 합니다.', },
  },
  [7023] = {
    '겨울 할아버지, 우리 마을에 오셨네!',
    {'즐거운 겨울맞이가 되라고! 지금 아이언포지에 와 계신 겨울 할아버지에게 인사하는 것도 잊지 말아야 해! 올 한해 동안 착하게 살았다면 겨울맞이 축제 날 선물을 많이 받게 될 거야. 올해엔 뭘 받고 싶은지 겨울 할아버지에게 꼭 이야기하고!$B$B그리고 올 한해 동안 착하게 지낸 사람들에게 줄 음식도 준비해야 겠지? 우리 가게에선 아주 다양한 음식을 팔고 있어. 농장에서 식탁 위 접시까지 신선하게! 굴뚝나무 목장조합의 영양 만점 요리를 맛보라고!', },
    {'아이언포지 굴뚝나무 목장조합상인 근처에 있는 겨울 할아버지를 만나 대화해야 합니다.', },
  },
  [7024] = {
    '겨울 할아버지, 우리 마을에 오셨네!',
    nil,
    nil,
  },
  [7025] = {
    '겨울 할아버지 대접하기',
    {'거기 $G총각:아가씨;, 이 겨울 할아버지가 조그만 부탁이 하나 있는데 들어줄래요?$B$B객관적으로 봐도 여기 온종일 서 있는 게 그리 유쾌한 일은 아니겠지요? 달콤한 걸 좀 먹으면 기운이 날 것 같은데... 생강 과자빵이랑 시원한 우유만 있어도 참 좋겠어요. 이 할아버지랑 친해지고 싶죠? 그렇다면 좀 갖다줄래요?', },
    {'아이언포지에 있는 겨울 할아버지에게 생강 과자빵 5개와 시원한 우유 한 잔을 가져가야 합니다.', },
  },
  [7026] = {
    '산양 통가죽 고삐',
    nil,
    nil,
  },
  [7027] = {
    '빈 우리',
    {'여기 마구간을 좀 보세요! 텅텅 비었어요! 스톰파이크 기병대는 탈 산양이 없어 기능을 완전히 상실한 상태입니다.$B$B우리를 도와주셔야 합니다, 얼라이언스의 병사여. 이 조련용 목줄을 가지고 계곡으로 들어가 그 일대를 돌아다니는 알터랙 산양을 찾으세요. 산양을 찾거든 이 목줄을 사용해 잡아서 내게 데리고 오세요. 그러면 내가 마구간에 넣어두겠습니다.$B$B산양이 충분히 모이면 산양기병대 지휘관님께 소식을 정식 통지할 겁니다. 당신이 도와준다면 우리 기병대는 다시 살아날 것입니다!', },
    {'알터랙 계곡에 돌아다니는 알터랙 산양을 찾아야 합니다. 알터랙 산양을 찾으면 그 근처에서 스톰파이크 조련용 목줄을 사용해 산양을 길들이십시오. 그러면 야수 관리인에게 돌아갈 때 따라올 것입니다. 알터랙 산양 포획을 인정받기 위해서는 야수 관리인과 대화해야 합니다.', },
  },
  [7028] = {
    '뒤틀린 악마',
    {'테라모크의 수정 동굴은 서쪽으로 펼쳐져 마라우돈의 깊은 곳까지 이어진다오. 그곳은 많은 역사를 간직한 고대의 장소이며 난 그 역사를 차지하고 싶소, $C. 거대한 문의 뒤에 고대 신의 유물이 감추어져 있소이다.$B$B마라우돈에서 발견된 뒤틀린 악마와 싸워 준다면 보답은 섭섭지 않도록 하지. 트라드릭 수정 조각상을 찾아서 내게 가져오시오.$B$B그러면 나도 연구를 시작할 수 있겠지... 그 대단한 힘들은 너무 오랫동안 잠들어 있었소이다...', },
    {'트라드릭 수정 조각상 15개를 모아서 잊혀진 땅에 있는 윌로우에게 가져가야 합니다.', },
  },
  [7029] = {
    '바일텅의 타락',
    {'바일텅으로 알려진 사티로스는 내부의 식물들의 성장을 방해하는 수법으로 마라우돈 동굴을 오염시키고 있네.$B$B이 약병을 받게. 동굴 안으로 가서 오염된 주황색 웅덩이의 물로 약병을 채우게. 약병 안에 칠한 것이 독을 제압해 치료제로 바꾸어 줄 것일세.$B$B동굴 안으로 들어간 다음 바일줄기 덩굴나무를 찾아서 정화된 액체를 그 위에 부어야 하네. 그러면 오염된 식물이 깨끗하게 치료될 것이네. 하지만 식물들은 여전히 바일텅의 지배 하에 있다는 걸 잊지 말게나. 바일텅이 그 식물을 이용해 자네를 공격할지도 몰라...', },
    {'마라우돈에 있는 주황색 수정 웅덩이에서 빈 감청석 약병을 채워야 합니다.$B$B오염된 녹시온의 후예가 나타나게 하려면 바일줄기 덩굴나무 위에 감청색 약병에 담긴 액체를 부어야 합니다.$B$B녹시온의 후예를 죽이는 방법으로 8개의 식물을 치료한 다음 그늘수렵 마을에 있는 바르크 배틀스카에게 가야 합니다.', },
  },
  [7041] = {
    '바일텅의 타락',
    {'바일텅으로 알려진 사티로스는 내부의 식물들의 성장을 방해하는 수법으로 마라우돈 동굴을 오염시키고 있습니다.$B$B이 약병을 받으세요. 동굴 안으로 가서 오염된 주황색 웅덩이에서 약병을 채우세요. 약병 안에 칠한 것이 독을 제압해 치료제로 바꾸어 줄 겁니다.$B$B동굴 안으로 들어간 다음 바일줄기 덩굴나무를 찾아서 정화된 액체를 그 위에 부으면 오염된 식물이 깨끗하게 치료될 겁니다. 하지만 식물들은 여전히 바일텅의 지배 하에 있다는 걸 알아 두세요. 바일텅이 그 식물을 이용해 그대를 공격할지도 모릅니다...', },
    {'마라우돈에 있는 주황색 수정 웅덩이에서 빈 감청석 약병을 채워야 합니다.$B$B오염된 녹시온의 후예가 나타나게 하려면 바일줄기 덩굴나무 위에 감청색 약병에 담긴 액체를 부어야 합니다.$B$B녹시온의 후예를 죽이는 방법으로 8개의 식물을 치료한 다음 나이젤의 야영지에 있는 탈렌드리아에게 가야 합니다.', },
  },
  [7043] = {
    '고약한 녀석...',
    nil,
    nil,
  },
  [7044] = {
    '마라우돈의 전설',
    {'바일텅이라 불리는 암흑의 사티로스는 이 뒤틀린 동굴 내부의 생물들의 마음을 타락시켜 악을 퍼뜨리고 있습니다. 그는 아직도 보라색 수정 건너편에 살고 있답니다.$B$B또한 그는 주황색 수정 건너에 살고 있는 녹시온이라고 부르는 오염된 생명을 창조했습니다. 그들이 힘을 합쳐서 제 형제의 홀을 훔쳐갔답니다. 제 형제 셀레브라스는 저주에 걸려 동굴 안에서 배회하고 있어요.$B$B제발 도와주세요! 홀 조각을 찾고 어떻게 해서든 제 형제에게 말을 전해주십시오.', },
    {'셀레브라스의 홀 조각인 셀레브리안 마법봉과 셀레브리안 다이아몬드를 찾아야 합니다.$B$B셀레브라스와 대화를 나눌 방법을 찾아야 합니다.', },
  },
  [7045] = {
    '굴뚝나무 목장조합의 선물!',
    nil,
    nil,
  },
  [7061] = {
