  [7785] = {
    '결정 조사',
    {'필멸의 생명체여, 환생의 관을 조사하십시오. 이 결정에서 바람추적자가 새로이 태어날 것입니다!', },
    {'환생의 관을 조사한 후, 대영주 데미트리안에게 가져가야 합니다.', },
  },
  [7786] = {
    '바람추적자 썬더란',
    {'제가 아는 건 모두 얘기해 드렸습니다, $N. 엘레멘티움을 찾는 건 당신 몫입니다. 불의 군주의 육체를 파괴하는 것도 그렇고요.$B$B그러면 당신은 썬더란의 축복을 얻게 될 것입니다.', },
    nil,
  },
  [7787] = {
    '일어나라, 우레폭풍이여!',
    {'바람추적자를 물리쳤습니다. 애처롭게 쓰러져 있는 데미트리안에게 잠들어 있는 성검을 주어 우레폭풍을 깨운 그의 배신 행위를 일깨워 주도록 하십시오.', },
    {'대영주 데미트리안에게 잠들어 있는 바람의 성검을 보여줘야 합니다.', },
  },
  [7788] = {
    '침략자 처단!',
    {'젊은 $C|1이여;여;, 이 터널 너머에서 그대는 투쟁과 동요의 현장을 경험하게 될 것입니다. 호드는 신성한 숲을 더럽히고 울창한 푸른 나무들을 베어내며 자신들의 전쟁 기계를 가동시키는 동력으로 사용하고 있습니다.$B$B이 길을 따라 전쟁노래 협곡으로 가서 은빛날개 요새를 지켜 주십시오. 침략자인 호드 군대를 이 땅에서 몰아내 주십시오!$B$B호드를 몰아내고 무공 훈장을 얻으십시오. 훈장을 가져오시면 보상을 지급하겠습니다.', },
    {'전쟁노래 협곡으로 들어가 호드를 무찌르고 전쟁노래 협곡 명예 훈장을 얻어 은빛날개 숲에 있는 파수꾼 파송에게 가야 합니다.', },
