  [8737] = {
    '청명 기사단',
    {'황혼의 망치단과 이들이 숭배하는 고위 정령들이 실리더스에서 수행 중인 우리 작전에 큰 위협이 되고 있습니다. 우리는 안퀴라즈 내부의 새로운 적과 대치 중인 상황이기 때문에 외부의 적에게 병력을 돌릴 여력이 없습니다.$B$B청명 기사단을 소환하여 이들을 해치운 후 보르 와일드메인에게 보고하십시오.', },
    {'청명 기사단을 소환하여 해치운 후 세나리온 요새에 있는 보르 와일드메인에게 보고해야 합니다. 또한, 이 퀘스트를 완료하려면 전술 임무 지령 I도 함께 가져가야 합니다.', },
  },
  [8738] = {
    '하이브레갈 정찰 보고서',
    {'우리는 최고의 정찰병들을 실리시드 곤충 집으로 침투시켰습니다. 그중 정찰병 란디온은 지금까지 며칠 동안 하이브레갈에서 실리시드의 움직임을 감시해 왔습니다. 그를 찾아 그동안의 경과를 기록해 놓은 보고서를 받아오십시오. 그 보고서의 정보를 바탕으로 최대한 신속하게 행동을 취해야 합니다.', },
    {'하이브레갈 안에 있는 세나리온 정찰병 란디온과 접선한 후 세나리온 요새에 있는 바람소환사 프라우드혼에게 하이브레갈 정찰 보고서를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 전술 임무 지령 VII도 함께 가져가야 합니다.', },
  },
  [8739] = {
    '하이브아쉬 정찰 보고서',
    {'우리는 최고의 정찰병들을 실리시드 곤충 집으로 침투시켰습니다. 그중 정찰병 잘리나는 지금까지 며칠 동안 하이브아쉬에서 실리시드의 동태를 감시해 왔습니다. 그녀를 찾아 그동안의 경과를 기록해 놓은 보고서를 입수해 오십시오. 우린 그 정보를 바탕으로 최대한 신속하게 행동을 취해야 합니다.', },
    {'하이브아쉬 안에 있는 세나리온 정찰병 잘리아와 접선한 다음 세나리온 요새에 있는 바람소환사 프라우드혼에게 하이브아쉬 정찰 보고서를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 전술 임무 지령 VIII도 함께 가져가야 합니다.', },
  },
  [8740] = {
    '황혼의 약탈자',
    {'말을 탄 황혼의 망치단 이교도들이 우리 군대가 주둔하고 있는 세나리온 요새의 외곽 지역에 출몰해 소규모 순찰대와 짐마차를 습격했습니다.$B$B황혼의 약탈자들과 녀석들의 우두머리인 모르나라고 하는 무시무시한 전사를 찾아 처치해 주십시오. 일을 마친 후에는 바람소환사 프라우드혼에게 보고하시면 됩니다.', },
    {'황혼의 약탈자 2명과 황혼의 약탈자 모르나를 처치한 후, 바람소환사 프라우드혼에게 보고해야 합니다. 또한, 이 퀘스트를 완료하려면 전술 임무 지령 IX도 함께 가져가야 합니다.', },
