  [8776] = {
    '목표: 암컷 하이브레갈',
    {'전쟁 준비가 진행되면서 이 지역에 존재하는 실리시드가 우리의 작전에 아주 큰 위협이 되고 있습니다. 당신은 하이브레갈 공격 작전에 선발되었으며 당신의 주요 목표는 일꾼인 암컷 하이브레갈입니다.$B$B임무를 완수하면 지휘관 마랄리스에게 보고하십시오.', },
    {'암컷 하이브레갈 30마리를 처치한 후 실리더스의 세나리온 요새에 있는 지휘관 마랄리스에게 전투 임무 지령 X을 가져가야 합니다.', },
  },
  [8777] = {
    '목표: 하이브레갈 땅무지',
    {'전쟁 준비가 진행되면서 이 지역에 존재하는 실리시드가 우리의 작전에 아주 큰 위협이 되고 있습니다. 당신은 하이브레갈 공격 작전에 선발되었으며 당신의 주요 목표는 하이브레갈 땅무지입니다.$B$B임무를 완수하면 지휘관 마랄리스에게 보고하십시오.', },
    {'하이브레갈 땅무지 30마리를 처치한 후 실리더스의 세나리온 요새에 있는 지휘관 마랄리스에게 전투 임무 지령 XI을 가져가야 합니다.', },
  },
  [8778] = {
    '아이언포지 여단에 폭탄 공급!',
    {'아이언포지 여단에서 폭발하기 아주 쉬운 재료를 보내 달라는 별난 요청을 해왔습니다. 재료를 모아서 아이언포지 여단에 있는 노즐스프링 씨에게 가져다주세요.', },
    {'실리더스의 하이브조라 근처에 있는 비전술사 노즐스프링에게 제물의 기름 6개, 고블린 로켓 연료 5개, 강도 높은 화약 10개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 IV도 함께 가져가야 합니다.', },
  },
  [8779] = {
    '수정점 재료',
    {'지질학자 락스베인의 연구가 실리더스에서 우리가 수행하는 작전에 엄청난 도움이 된다는 것이 입증되었습니다. 최근에 그녀는 수정점 연구에 사용할 재료를 요청했습니다.$B$B그녀가 연구를 계속할 수 있도록 재료를 모으는 즉시 가져다주십시오.', },
    {'실리더스의 세나리온 요새에 있는 지질학자 락스베인에게 눈부신 큰 결정, 찬란하게 빛나는 큰 결정, 거대 에메랄드를 각각 1개씩 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 V도 함께 가져가야 합니다.', },
  },
  [8780] = {
    '전장에 방어구 강화도구 공급',
    {'전장에 있는 아이언포지 여단의 방어구 강화도구 보유량이 매우 부족한 상황입니다. 방어구 강화도구를 구해서 하이브조라 외곽의 아이언포지 여단 주둔지에 있는 자넬라 스타우트해머에게 전달해 주십시오.', },
    {'실리더스의 하이브조라 근처 아이언포지 여단 주둔지에 있는 자넬라 스타우트해머에게 튼튼한 방어구 강화도구와 고급 방방어구 강화도구를 각각 8개씩 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 VII도 함께 가져가야 합니다.', },
  },
  [8781] = {
    '전장에 무기 공급',
    {'아이언포지 여단은 우리의 실리더스 전투에서 든든한 동맹의 역할을 수행해 왔습니다. 그런데 유감스럽게도 아이언포지 여단이 이곳으로 이동하는 도중 보급품의 상당 부분을 분실했습니다. 넓적검을 구해서 하이브조라 근처에 있는 자넬라 스타우트해머에게 전달해 주십시오.', },
    {'하이브조라 외곽의 아이언포지 여단 주둔지에 있는 자넬라 스타우트해머에게 달빛강철 넓적검 2개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 VI도 함께 가져가야 합니다.', },
  },
  [8782] = {
    '제복 공급',
    {'최근에 들어온 지원자들에게 지급할 제복이 부족합니다. 제복을 만들 재료를 구해서 바람소환사 프라우드혼에게 가져다주십시오.', },
    {'바람소환사 프라우드혼에게 달빛 옷감 1개, 룬무늬 두루마리 2개, 강철그물 거미줄 1개를 가져가야 합니다. 또한, 이 임무를 완료하려면 병참 임무 지령 VIII도 함께 가져가야 합니다.', },
  },
  [8783] = {
    '특별한 재료',
    {'세나리온 요새의 대장장이 바르구스가 우리 군대가 쓸 강력한 무기에 마력을 부여할 재료를 요청했습니다. 세나리온 요새의 군대가 완전히 무장할 수 있도록 이 재료들을 구하는 즉시 바르구스에게 가져다주십시오.', },
    {'실리더스의 세나리온 요새에 있는 바르구스에게 마력 깃든 토륨 주괴 2개, 마력 깃든 가죽 2개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 IX도 함께 가져가야 합니다.', },
  },
  [8784] = {
    '퀴라지의 비밀',
    {'당신이 발견한 것은 고대 퀴라지 유물입니다! 신전의 입구 근처에 있는 용들 중 하나가 이 유물의 사용법을 알고 있을 겁니다.', },
    {'신전 입구 근처에 숨어 있는 용족에게 고대 퀴라지 유물을 가져가야 합니다.', },
  },
  [8785] = {
    '오그리마 군단에 모조 공급!',
    {'오그리마 군단에서 몇 가지 특이한 재료를 요청했습니다. 현재 그들이 실리더스에서 수행하는 임무를 고려해 볼 때 최대한 신속하게 요청을 들어주는 것이 좋을 것 같습니다. 재료를 모아서 샤이라는 트롤에게 가져다주십시오.', },
    {'실리더스의 하이브레갈 근처에 있는 어둠의여사제 샤이에게 제물의 기름 8개, 왕모조 물약 6개, 강력한 모조 6개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 IV도 함께 가져가야 합니다.', },
  },
  [8786] = {
    '전장에 무기 공급',
    {'오그리마 군단에서 무기를 추가로 공급해 달라고 요청했을 때는 우리가 그럴 수 있는 형편이 아니었습니다. 이제 그들이 요청한 무기를 구하여 하이브레갈 외곽에 있는 메로크 롱스트라이드에게 가져다주십시오.', },
    {'하이브레갈 외곽 오그리마 군단 주둔지에 있는 메로크 롱스트라이드에게 큰 철제 도끼 3개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 VI도 함께 가져가야 합니다.', },
  },
  [8787] = {
    '전장에 방어구 강화도구 공급',
    {'오그리마 군단의 용감한 지원자들이 우리를 도와 대업을 달성하기 위해 실리더스에 도착했습니다. 그런데 여행 중에 그만 방어구 강화도구를 분실했다고 합니다. 방어구 강화도구를 모아 오그리마 군단 주둔지에 있는 메로크 롱스트라이드에게 가져다주십시오.', },
    {'하이브레갈 부근에 있는 메로크 롱스트라이드에게 튼튼한 방어구 강화도구와 고급 방어구 강화도구를 각각 8개씩 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 VII도 함께 가져가야 합니다.', },
  },
  [8788] = {
    '누가 살짝 흔들어 본 선물',
    nil,
    nil,
  },
  [8789] = {
    '제국의 퀴라지 무기',
    {'우리가 천 년 동안 놀고만 있었던 건 아니라네.$B$B우린 이 짐승들은 쓸모가 없다면 자신의 종족까지 잡아먹는다는 것을 목격했다네. 또한 우리는 그들이 이 세계를 파괴하려는 음모를 꾸미는 것을 들었지. 그리고 그들의 내부에 배신이 있었다는 것도 알아냈다네.$B$B그들에게 무슨 약점이 있으며 그 약점을 우리에게 유리한 방향으로 이용하는 방법을 알고 있단 말일세.$B$B그들이 의식에 사용하는 도구를 가지고 오게. 그 도구를 그들이 섬기는 신이 벌인 전쟁으로 인해 만들어진 물질과 합친다면 그들을 심판하는 강력한 무기가 될 것이야.', },
    nil,
  },
  [8790] = {
    '제국의 퀴라지 표장',
    {'아리고스는 고대 신과 그들의 부하들 간에 벌어진 충돌에서 발생한 물질이 그들 자신에게도 매우 치명적일 것이라는 이론을 세웠습니다. 제가 말하는 것은 바로 엘레멘티움입니다, 필멸의 존재여.$B$B제국의 퀴라지 표장과 엘레멘티움을 찾아서 가져오십시오. 내 강력한 힘을 가진 무기를 만들어 주도록 하겠습니다.', },
    nil,
  },
  [8791] = {
    '오시리안',
    {'지독하게 위험했고 잔인했던 오시리안이 패하고 그의 영혼은 안식을 찾았습니다.$B$B칼림도어의 모든 이들이 이 승리의 소식을 듣는다면 안도의 한숨을 쉬게 될 것입니다. 세나리온 요새의 지휘관 마랄리스에게 오시리안의 머리를 가지고 가십시오.', },
    {'실리더스의 세나리온 요새에 있는 지휘관 마랄리스에게 무적의 오시리안의 머리를 가져가야 합니다.', },
  },
  [8792] = {
    '호드 지원!',
    {'안녕하십니까. 제 부탁을 들어주시겠다니 고맙군요. 호드에서는 안퀴라즈 전쟁을 준비하기 위해 단 한 명의 도움이라도 절실한 상황입니다. 그러니까 당신의 도움이 꼭 필요하다는 말입니다! 우리가 대화를 나누고 있는 지금 이 순간에도 공식적으로 수집 임무를 맡은 이들이 다가올 전쟁에 필요한 물자를 모으고 있답니다. 그러나 $N, 당신의 도움이 없으면 우리는 목표를 달성할 수 없습니다!$B$B전쟁 물자 수집을 책임지고 있는 장군 고르척에게 가서 얘기를 나눠보십시오. 자, 어떻습니까? 호드의 생사가 걸린 전쟁 준비를 돕겠습니까?', },
    {'오그리마, 정기의 골짜기에 있는 장군 고르척과 대화해야 합니다.', },
  },
  [8793] = {
    '호드 지원!',
    {'안녕하십니까. 제 부탁을 들어주시겠다니 고맙군요. 호드에서는 안퀴라즈 전쟁을 준비하기 위해 단 한 명의 도움이라도 절실한 상황입니다. 그러니까 당신의 도움이 꼭 필요하다는 말입니다! 우리가 대화를 나누고 있는 지금 이 순간에도 공식적으로 수집 임무를 맡은 이들이 다가올 전쟁에 필요한 물자를 모으고 있답니다. 그러나 $N, 당신의 도움이 없으면 우리는 목표를 달성할 수 없습니다!$B$B전쟁 물자 수집을 책임지고 있는 장군 고르척에게 가서 얘기를 나눠보십시오. 자, 어떻습니까? 호드의 생사가 걸린 전쟁 준비를 돕겠습니까?', },
    {'오그리마, 정기의 골짜기에 있는 장군 고르척과 대화해야 합니다.', },
  },
  [8794] = {
    '호드 지원!',
    {'안녕하십니까. 제 부탁을 들어주시겠다니 고맙군요. 호드에서는 안퀴라즈 전쟁을 준비하기 위해 단 한 명의 도움이라도 절실한 상황입니다. 그러니까 당신의 도움이 꼭 필요하다는 말입니다! 우리가 대화를 나누고 있는 지금 이 순간에도 공식적으로 수집 임무를 맡은 이들이 다가올 전쟁에 필요한 물자를 모으고 있답니다. 그러나 $N, 당신의 도움이 없으면 우리는 목표를 달성할 수 없습니다!$B$B전쟁 물자 수집을 책임지고 있는 장군 고르척에게 가서 얘기를 나눠보십시오. 자, 어떻습니까? 호드의 생사가 걸린 전쟁 준비를 돕겠습니까?', },
    {'오그리마, 정기의 골짜기에 있는 장군 고르척과 대화해야 합니다.', },
  },
  [8795] = {
    '얼라이언스 지원자 모집!',
    {'안녕하십니까. 제 부탁을 들어주시겠다니 고맙습니다. 얼라이언스 연합에서는 안퀴라즈 전쟁을 준비하기 위해 단 한 명의 도움이라도 절실한 상황입니다. 그러니까 당신의 도움이 필요하다는 말입니다! 우리가 대화를 나누고 있는 지금 이 순간에도 정식으로 임무를 맡은 이들이 코앞에 닥친 전쟁에 필요한 물자를 모으고 있습니다. 그러나 당신의 도움이 없으면 우리의 목표를 달성할 수 없습니다!$B$B물자 보급을 책임지고 있는 야전사령관 스노우폴과 얘기해 보십시오. 어떻게 하시겠습니까? 아제로스의 모든 생명체의 생사가 걸린 전쟁 준비를 도와주시겠습니까?', },
    {'아이언포지의 군사 지구에 있는 야전사령관 스노우폴과 대화해야 합니다.', },
  },
  [8796] = {
    '얼라이언스 지원자 모집!',
    {'안녕하십니까. 제 부탁을 들어주시겠다니 고맙습니다. 얼라이언스 연합에서는 안퀴라즈 전쟁을 준비하기 위해 단 한 명의 도움이라도 절실한 상황입니다. 그러니까 당신의 도움이 필요하다는 말입니다! 우리가 대화를 나누고 있는 지금 이 순간에도 정식으로 임무를 맡은 이들이 코앞에 닥친 전쟁에 필요한 물자를 모으고 있습니다. 그러나 당신의 도움이 없으면 우리의 목표를 달성할 수 없습니다!$B$B물자 보급을 책임지고 있는 야전사령관 스노우폴과 얘기해 보십시오. 어떻게 하시겠습니까? 아제로스의 모든 생명체의 생사가 걸린 전쟁 준비를 도와주시겠습니까?', },
    {'아이언포지의 군사 지구에 있는 야전사령관 스노우폴과 대화해야 합니다.', },
  },
  [8797] = {
    '얼라이언스 지원자 모집!',
    {'안녕하십니까. 제 부탁을 들어주시겠다니 고맙습니다. 얼라이언스 연합에서는 안퀴라즈 전쟁을 준비하기 위해 단 한 명의 도움이라도 절실한 상황입니다. 그러니까 당신의 도움이 필요하다는 말입니다! 우리가 대화를 나누고 있는 지금 이 순간에도 정식으로 임무를 맡은 이들이 코앞에 닥친 전쟁에 필요한 물자를 모으고 있습니다. 그러나 당신의 도움이 없으면 우리의 목표를 달성할 수 없습니다!$B$B물자 보급을 책임지고 있는 야전사령관 스노우폴과 얘기해 보십시오. 어떻게 하시겠습니까? 아제로스의 모든 생명체의 생사가 걸린 전쟁 준비를 도와주시겠습니까?', },
    {'아이언포지의 군사 지구에 있는 야전사령관 스노우폴과 대화해야 합니다.', },
  },
  [8799] = {
    '오늘의 영웅',
    {'흠, 내가 이런 비밀을 자네에게 밝히리라고는 생각지도 않았는데... 그래도 자네가 오늘의 영웅이니까!$B$B매년 이맘때쯤엔 어디서나 싱싱한 호랑가시나무를 볼 수 있지만 오래가지는 못하지. 굴뚝나무 목장조합에서는 호랑가시나무가 절대 상하지 않도록 보존하는 기계를 이용해서 겨울맞이 축제의 분위기를 살리고 여름내 돈벌이를 하지!$B$B자네가 요리의 대가라면 보존기를 쓸 수 있게 해 주지. 단, 깊은바위 소금이 필요할 거야. 그리고 사용료는 1, 2, 3... 5골드로 하지. 싱싱하게 보존된 호랑가시나무 한 묶음을 얻을 수 있을 걸세.', },
    {'호랑가시나무 보존기에 깊은바위 소금 한 줌과 5골드를 넣으면 보존된 호랑가시나무를 받게 될 것입니다.', },
  },
  [8800] = {
    '세나리온 전투장비',
    {'전쟁 중에 그대에게 부여되는 임무를 완수하다 보면 대장장이 바르구스를 만나야 할 때가 올 것이오. 그때 임무를 완수하고 받은 휘장을 그에게 보여주시오. 이번 전쟁에 기여한 정도에 따라 그대에게 전투장비를 지급할 거요.', },
    {'실리더스의 세나리온 요새에 있는 바르구스와 대화해야 합니다.', },
  },
  [8801] = {
    '크툰의 유물',
    {'안퀴라즈의 성벽이 흔들리고 있습니다. 이 세계가 생겨나기 전, 태초부터 존재했던 악의 세력이 축출되었습니다.$B$B저주 받을 악마가 남기고 간 흔적을 본다면 당신의 심장은 거의 얼어붙을 것이며, 크툰이 남긴 악의 기운은 죽어서도 느낄 수 있을 것입니다.$B$B당신은 불가능이라고 생각했던 일을 해냈으며, 악마의 눈이 담긴 자루를 잡아채 안전하게 가방에 넣었습니다.$B$B이제 옆 방에 있는 용족에게 그 크툰의 눈을 가지고 가십시오.', },
    {'안퀴라즈 사원에 있는 캘레스트라즈에게 크툰의 눈을 가져가야 합니다.', },
  },
  [8802] = {
    '칼림도어의 구세주',
    nil,
    {'시간의 동굴에 있는 아나크로노스에게 크툰의 눈을 가져가야 합니다.', },
  },
  [8803] = {
    '축제 선물',
    nil,
    nil,
  },
  [8804] = {
    '사막 긴급구호 물자',
    {'세나리온 요새에 지금 신병들이 도착했는데 과연 사막에서 살아남을 수 있느냐가 문제입니다. 경험이 부족한 신병들에게는 맹독을 가진 다양한 생물뿐 아니라 탈수증도 심각한 위협이 될 수 있습니다. 사막 긴급구호 물자로 쓸만한 물건들을 모아서 세나리온 요새에 있는 캘랜드라스에게 가져가십시오.', },
    {'실리더스의 세나리온 요새 여관에 있는 캘랜드라스에게 물의 보주, 강력한 해독제, 훈제 사막 경단을 각각 4개씩 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 I도 함께 가져가야 합니다.', },
  },
  [8805] = {
    '경비대에 장화 공급',
    {'세나리온 요새를 수호하는 기병대에 화려한 미스릴 장화가 부족하다고 합니다. 화려한 미스릴 장화를 구하는 즉시 세나리온 요새의 경비대장 비쉬 코주스에게 전달해 주십시오.', },
    {'실리더스의 세나리온 요새에 있는 경비대장 비쉬 코주스에게 화려한 미스릴 장화 3개와 병참 임무 지령 II를 가져가야 합니다.', },
  },
  [8806] = {
    '경비대에 연마석 공급',
    {'세나리온 요새 경비대에 연마석이 부족하다고 합니다. 연마석을 구하는 즉시 세나리온 요새의 경비대장 비쉬 코주스에게 전달해 주십시오.', },
    {'실리더스의 세나리온 요새에 있는 경비대장 비쉬 코주스에게 강도 높은 연마석, 견고한 연마석, 단단한 연마석을 각각 10개씩 모아서 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 III도 함께 가져가야 합니다.', },
  },
  [8807] = {
    '수정점 재료',
    {'지질학자 락스베인의 연구가 실리더스에서 우리가 수행하는 작전에 엄청난 도움이 된다는 것이 입증되었습니다. 최근에 그녀는 수정점 연구에 사용할 재료를 요청했습니다.$B$B그녀가 연구를 계속할 수 있도록 재료를 모으는 즉시 가져다주십시오.', },
    {'실리더스의 세나리온 요새에 있는 지질학자 락스베인에게 눈부신 큰 결정, 찬란하게 빛나는 큰 결정, 거대 에메랄드를 각각 1개씩 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 V도 함께 가져가야 합니다.', },
  },
  [8808] = {
    '제복 공급',
    {'최근에 들어온 지원자들에게 지급할 제복이 부족합니다. 제복을 만들 재료를 구해서 바람소환사 프라우드혼에게 가져다주십시오.', },
    {'바람소환사 프라우드혼에게 달빛 옷감 1개, 룬무늬 두루마리 2개, 강철그물 거미줄 1개를 가져가야 합니다. 또한, 이 임무를 완료하려면 병참 임무 지령 VIII도 함께 가져가야 합니다.', },
  },
  [8809] = {
    '특별한 재료',
    {'세나리온 요새의 대장장이 바르구스가 우리 군대가 쓸 강력한 무기에 마력을 부여할 재료를 요청했습니다. 세나리온 요새의 군대가 완전히 무장할 수 있도록 이 재료들을 구하는 즉시 바르구스에게 가져다주십시오.', },
    {'실리더스의 세나리온 요새에 있는 바르구스에게 마력 깃든 토륨 주괴 2개, 마력 깃든 가죽 2개를 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 IX도 함께 가져가야 합니다.', },
  },
  [8810] = {
    '전장에 붕대 공급',
    {'최근 발생한 부상자로 인해 전장에서 붕대가 많이 부족하다고 합니다. 응급치료에 쓰는 붕대를 구해 세나리온 요새에 있는 바람소환사 프라우드혼에게 전달해 주십시오.', },
    {'실리더스 세나리온 요새에 있는 바람소환사 프라우드혼에게 두꺼운 룬무늬 붕대, 두꺼운 마법 붕대, 두꺼운 비단 붕대를 각각 30개씩 가져가야 합니다. 또한, 이 퀘스트를 완료하려면 병참 임무 지령 X도 함께 가져가야 합니다.', },
