  [8857] = {
    '거대괴수의 비밀 - 아쉬',
    {'저는 아쉬 거대괴수의 시체에서 채취한 신선한 껍질 표본을 무법항에 있는 오글소프에게 가져다줄 의지와 능력을 갖춘 전투에 강한 모험가들을 찾고 있습니다. 아니, 아니, 당신보고 그 시체에서 표본을 채취하라는 것이 아닙니다. 당신은 제 표본 중에 하나를 가지고 가시면 됩니다. 이것들이 썩어 버리면 소용없어지니까요!', },
    {'무법항에 있는 오글소프 오브노티쿠스에게 아쉬 거대괴수의 껍질을 가져가야 합니다.', },
  },
  [8858] = {
    '거대괴수의 비밀 - 레갈',
    {'오일피스트에게 이 벌레 껍질을 꼭 전달해야 합니다. 그것 말고는 더 바랄 게 없는데 말입니다. 저는 여기 있으면서 성장한 벌레의 표본을 수집해야 해서 직접 갈 수가 없습니다. 벌레가 그 정도로 성장하려면 이른 아침까지 여기서 기다려야 하거든요.$B$B제 대신 이 표본을 오일피스트에게 속히 전해 주시면 그가 큰 보상을 해 줄 것입니다!$B$B네, 맞습니다. 오일피스트는 이글거리는 협곡 안의 토륨 거점에 있을 겁니다.', },
    {'이글거리는 협곡에 있는 감독관 오일피스트에게 레갈 거대괴수의 껍질을 가져가야 합니다.', },
  },
  [8859] = {
    '거대괴수의 비밀 - 조라',
    {'제 부하들이 이 표본을 구하느라 거의 죽을 뻔했습니다. 맥스웰 티로서스 경이 신선한 껍질 표본을 눈이 빠져라 기다리고 있으니 서둘러야 합니다! 그는 이 거대괴수의 비밀이 스컬지와의 전쟁에서 도움이 될 거라 믿고 있습니다!$B$B아직도 안 가고 뭐 하십니까!? 희망의 빛 예배당으로 가십시오! 예, 이 일만 잘해 주시면 후한 보상이 있을 겁니다...', },
    {'동부 역병지대의 희망의 빛 예배당에 있는 맥스웰 티로서스 경에게 조라 거대괴수의 껍질을 가져가야 합니다.', },
  },
