  [8883] = {
    '발라다르 스타송',
    {'달의 축제 초대장도 받으셨으니 이제 달숲으로 여행을 떠나셔야죠! 거기서 축제를 즐기러 온 구경꾼들이나 다른 모험가 친구들도 만나고 특히 밤의 안식처 마을로 가면 발라다르 스타송과 그의 누이 파리엘 스타송이 여러분을 진심으로 반길 겁니다.$B$B눈부시도록 환한 달빛의 인도를 받아서 어서 달숲으로 날아가세요. 발라다르 스타송을 만나 이번 달의 축제 기간에 어떤 상품을 받을 수 있나 한번 알아보시고요!', },
    {'달빛 안에서 달의 축제 초대장을 사용하여 달숲으로 이동하고 밤의 안식처에 있는 발라다르 스타송과 대화해야 합니다.', },
  },
  [8884] = {
    '멀록의 머리',
    {'안녕하시오, $C|1이여;여;. 저 해안을 한번 보시오. 뭐가 보이시오? 그렇소이다. 우리 해변... 우리의 평온의 해변에 멀록이라는 냄새 나는 놈들이 득실대고 있소.$B$B이 사태를 그냥 보고만 있을 수는 없소!$B$B괜찮다면 한 가지 부탁을 하고 싶소이다. 난 그 물고기 인간들의 수를 줄였으면 하오! 놈들의 수를 크게 줄이면 어떻게 나올지 확인해 보고 싶군. 놈들도 생각이라는 게 있다면 원래 그들의 고향인 깊은 심연으로 되돌아가지 않겠소?', },
    {'성난비늘 멀록 머리 8개를 모아서 평온의 해변가 내려다보이는 절벽의 영원노래 숲에 있는 하스벨리온 선게이즈에게 가져가야 합니다.', },
  },
  [8885] = {
    '아옳옳옳옳옳의 반지',
    {'그대의 노력에도 성난비늘 멀록들은 조금도 겁을 먹지 않았소. 녀석들을 쫓아내고 사적인 원한을 갚을 방법이 있을 것도 같소.$B$B녀석들에겐 아옳옳옳옳옳이라고 하는 멀록 대장이 있다오. 녀석은 강 후미의 황금 해안 남쪽 해변을 돌아다니고 있소. 그 짐승 같은 놈은 내 동료를 살해한 것도 모자라 조롱하는 의미로 그의 반지를 끼고 있소.$B$B아옳옳옳옳옳을 해치우고 친구의 반지를 되찾아 주시오. 단, 모든 일을 마친 후라도 멀록들이 물러갔는지 확인해 봐야 할 거요!', },
    {'성난비늘 족장을 처치하여 아옳옳옳옳옳의 반지를 되찾은 후, 평온의 해변이 내려다보이는 절벽의 영원노래 숲에 있는 하스벨리온 선게이즈에게 가져가야 합니다.', },
  },
  [8886] = {
    '성난비늘 멀록의 약탈!',
    nil,
    {'선장 켈리센드라의 화물 상자 6개를 되찾은 후, 영원노래 숲에 해돋이 부두의 바로 동쪽, 방어 주둔지에 있는 선장 켈리센드라에게 가져가야 합니다.', },
  },
  [8887] = {
    '선장 켈리센드라의 잃어버린 항해도',
    {'재미있게 생긴 물건입니다... 방금 여기서 방수 처리된 가죽으로 만든 통을 발견했습니다. 그 안에는 양피지 몇 장이 들어 있습니다. 양피지에 적힌 기호들을 보아하니 이것은 항해도이며, 각각 선장 켈리센드라의 서명이 적혀 있습니다.$B$B해돋이 부두 근처로 가면 바로 동쪽에 작은 기지가 있다고 합니다. 선장은 아마 그곳에 있지 않을까요?', },
    {'선장 켈리센드라의 잃어버린 항해도를 가지고 영원노래 숲의 해돋이 부두의 바로 동쪽 기지로 선장 켈리센드라를 찾아가야 합니다.', },
  },
  [8888] = {
    '마법학자의 수습생',
    {'$C|1이여;여;, 내 부탁 좀 들어주시겠소? 내가 들어갈 수 없어서, 가까운 연구소의 문을 닫기로 했다오. 이 일로 며칠 전에 제자 중 한 명인 수습생 로랄탈리스를 보냈는데 그 이후로 아무 소식이 없소.$B$B더스크위더 첨탑으로 가서 그녀가 무사한지 확인해 주겠소? 원정순찰대 산장의 북쪽 길을 따라가다가 갈림길이 나올 때 오른쪽으로 틀면 더스크위더 첨탑을 찾을 수 있을 것이오.', },
    {'영원노래 숲의 더스크위더 첨탑에 있는 수습생 로랄탈리스를 찾아서 대화해야 합니다.', },
  },
  [8889] = {
    '첨탑 작동 중지',
    {'아시다시피 마법학자 더스크위더께서 날 여기로 다시 보내면서 첨탑에 힘을 공급하는 마력원천을 차단하라고 지시하셨답니다. 마력원천은 모두 3개인데 첨탑 내부의 각기 다른 위치에 있습니다.$B$B안타깝게도 난 몸놀림이 아주 둔하답니다. 게다가 더스크위더 첨탑에는 이상한 마법을 쓰는 생물들이 득실거리니... 이 임무를 맡기에는 아무래도 난 부족하지만, 그대 같은 $C에게는 아주 간단하겠죠?', },
    {'더스크위더 첨탑 안에 있는 3개의 더스크위더 첨탑 마력원천의 작동을 중지한 다음, 영원노래 숲에 있는 수습생 로랄탈리스에게 비활성 보석을 가져가야 합니다.', },
  },
  [8890] = {
    '첨탑에서 온 전갈',
    {'그대에게 마지막으로 부탁할 일이 있어요. 원정순찰대 산장으로 돌아가서 마법학자 더스크위더께 여기서 무슨 일이 벌어졌는지 전해 주시겠어요? 저는 잘 지낸다고 해주세요. 하지만, 수습생 몇 명이 버림받은 자로 전락했다는 사실만은 똑똑히 전해주십시오.$B$B도와주셔서 고맙습니다!', },
    {'영원노래 숲의 원정순찰대 산장에 있는 마법학자 더스크위더에게 가야 합니다.', },
  },
  [8891] = {
    '중단된 연구',
    {'마법학자 더스크위더의 연구 일지를 발견했습니다. 대강 읽어 보니 오염된 마법 액체에서 불순물을 걸러내기 위해 아주 놀라운 실험을 진행한 것 같습니다. 일지를 읽어보니 여기 더스크위더 첨탑에서 벌어진 사태가 모두 이해가 갑니다. 마법학자는 자신의 연구 일지를 되찾고 싶어할 것입니다.', },
    {'마법학자 더스크위더의 일지를 가지고 영원노래 숲의 원정순찰대 산장에 있는 마법학자 더스크위더에게 가야 합니다.', },
  },
  [8892] = {
    '해돋이 부두의 상황',
    {'해돋이 부두는 버림받은 자들에게 침입을 받아왔습니다. 지원 병력이 이곳으로 도착하는 즉시, 우린 그 흉악한 녀석에게 누가 이 땅의 지배자인지 보여줄 겁니다.$B$B그때까지 녀석들을 조금씩 손봐주는 일을 도와주십시오. 녀석들에게 우리가 장난이 아니라는 걸 보여주면 됩니다. 해돋이 부두는 산들바람 마을의 바로 서쪽에서 찾을 수 있습니다.', },
    {'버림받은 엘프 암살자 5명과 버림받은 엘프 난동자 5명을 처치한 후, 영원노래 숲에 있는 순찰자 데골리엔에게 보고해야 합니다.', },
  },
  [8893] = {
    '고성능 자동부화기',
    nil,
    nil,
  },
  [8894] = {
    '토양 정화',
    {'끔찍한 일입니다! 첨탑에서 이상한 일이 벌어지고 있습니다. 마법학자와 그 제자들이 도망쳐 나오더니 그다음에는 세상에 맙소사, 마력을 지닌 야수들이 온 대지를 오염시키기 시작했습니다! 누가 알려주었느냐고요? 아무도 알려주지 않았어요!$B$B$C님, 이것들을 제거하도록 절 좀 도와주셔야겠습니다... 이 \'변종\'들이 날뛰는 상황에서 제 힘만으로는 이 땅이 오염되는 것을 막을 수가 없거든요!', },
    {'마나 뱀과 에테르 마귀를 각각 6마리씩 해치운 후, 영원노래 숲의 더스크위더 정원에 있는 관리인 빌리덴에게 가야 합니다.', },
  },
  [8895] = {
    '북부 성소에 물건 배달',
    {'그런대로 유능한 $C|1을;를; 알게 되어 기쁘군요. 자신의 능력을 증명해 보이기를 원하는 것 같으니 다른 임무를 한번 맡아보십시오.$B$B여기 북부 성소의 관리를 맡고 있는 비전술사인 맥지기 케이다니스에게 전할 편지를 받으십시오. 매날개 광장에서 남서쪽으로 가면 그를 찾을 수 있습니다. 그는 아주 바쁜 몸이니 사소한 잡담으로 시간을 뺏지 않도록 주의하십시오.', },
    {'영원노래 숲의 북부 성소에 있는 맥지기 케이다니스에게 표시가 없는 편지를 전달해야 합니다.', },
  },
  [8896] = {
    '드워프 첩자',
    nil,
    nil,
  },
  [8897] = {
    '친애하는 콜라라',
    {'저는 항상 이곳을 지켜야 하는데, 제 애인 콜라라 때문에 늘 불안합니다. 그녀를 볼 수 있는 시간은 고작 몇 시간도 안 된답니다. 그것도 운이 좋으면 말이죠. 보시다시피 월급은 쥐꼬리만 하고 언제 최전선에 불려갈지 모르는 처지입니다.$B$B그녀가 절 잊으면 어쩌죠? 도시에는 돈 많은 남자들이 수두룩할 텐데...$B$B여하튼 뭘 좀 적은 게 있는데... 별 건 아니고, 이걸 그녀에게 전해 주실 수 있습니까?', },
    {'부관 조크린 헬드릭의 정성들여 쓴 쪽지를 스톰윈드 은행 밖에 있는 콜라라 딘에게 전해야 합니다.', },
  },
  [8898] = {
    '친애하는 콜라라',
    {'지난번 은행 업무차 스톰윈드에 들렀다가 한 아가씨를 만났죠. 전 그녀에게 마음을 빼앗겨 버렸어요. 이젠 그녀 생각을 멈출 수가 없어요! 그녀에게 이 편지를 전해 주고 싶어요. 열두 번도 더 고친 정성들인 편지랍니다! 이젠 쓸 자리조차 없어요.$B$B그녀가 이 편지를 꼭 받아야 할 텐데, 그쪽으로 가는 길이면 절 좀 도와주시겠어요?', },
    {'스톰윈드 은행 밖에 있는 콜라라 딘에게 반으로 접힌 편지를 전달해야 합니다.', },
  },
  [8899] = {
    '친애하는 콜라라',
    {'그녀의 아름다움을 어찌 말로 형용할 수 있으리오? 세상의 말로는 도무지 표현할 수가 없소.$B$B내 마음을 온통 뒤흔드는 그녀 앞에서 나는 말 더듬는 바보가 되어 버린다오.$B$B내 가슴속에 이 말들을 너무 오래 간직하고 있었소. 이보다 더 좋은 표현을 짜낼 수가 없소이다. 더 이상은 안 되겠소. 이제는 그녀에게 내 마음을 전해야 하오. 아니면 다른 누군가가 먼저 그녀의 마음을 가로챌지도 모르오. 날 좀 도와주시겠소?', },
    {'스톰윈드 은행 밖에 있는 콜라라 딘에게 깔끔한 장식의 편지를 전해야 합니다.', },
  },
  [8900] = {
    '친애하는 엘레니아에게',
    {'그녀의 눈망울은 정말 슬퍼 보였소. 그녀의 과거를 알았을 때 아픔이 있으리란 사실을 느낄 수 있었지...$B$B그녀와 이야기를 나누어 보니 우리에겐 뭔가 통하는 것이 있었소. 아무도 나를 이해하지 못할 거라 생각했는데 그녀는 달랐거든.$B$B다른 이들은 날 보고 비웃더군. 그래서 그녀와 다시 이야기를 나누지 못했소. 하지만, 그녀가 그리워... 이 편지를 좀 전해 주시겠소? 그녀의 가녀린 두 손에 꼭 쥐여주면 좋겠소이다.', },
    {'언더시티의 은행 근처에 있는 엘레니아 헤이돈에게 살짝 접힌 쪽지를 전해야 합니다.', },
  },
  [8901] = {
    '친애하는 엘레니아에게',
    nil,
    nil,
  },
  [8902] = {
    '친애하는 엘레니아에게',
    {'아주 오래전, 난 어릴 적부터 사랑했던 엘레니아와 약혼했지. 수년간 그녀를 잊지 않고 있었지만, 최근 들어 부쩍 그녀 생각이 자주 떠올라.$B$B숱하게 많은 일이 있었지만, 우리 사랑은 결코 지워지지 않을 숙명인 듯하네.$B$B그녀도 나처럼 많이 변했을 테지... 자네가 나를 위해 이 편지를 좀 전해주겠나? 너무 떨려서 내가 직접 가지는 못하겠어.', },
    {'언더시티의 은행 근처에 있는 엘레니아 헤이돈에게 사랑이 담긴 편지를 전해야 합니다.', },
  },
  [8903] = {
    '사랑이라는 위험한 병',
    nil,
    {'스톰윈드 경비병의 카드를 스톰윈드에 있는 아리스탄 모탈에게 가져가야 합니다.', },
  },
  [8904] = {
    '사랑이라는 위험한 병',
    {'지금 유행처럼 번지고 있는 사랑이란 병에 대한 나의 견해는 매우 부정적일세. 사실 아주 구역질이 나. 방어선을 취약하게 할 뿐만 아니라 우리 모두에게 위협이 될 수 있네.$B$B그 악영향이 너무 커서 많은 이들이 이상한 짓에 빠진 모양일세. 특히 그런 것에 무너지면 안 될 우리 경비대까지 말이야.$B$B자네에게 중요한 임무를 하나 맡기겠네. 우리 경비대 중에 그런 어리석은 감정에 빠진 녀석을 적발해주게나.', },
    {'언더시티 수호병의 곰팡이 슨 카드를 구해서 언더시티의 펜스타드 아가일에게 가져가야 합니다.', },
  },
  [8905] = {
    '진지한 제안',
    {'만나서 반가워요, $C님. 당신이 관심을 가질 만한 일이 있어요.$B$B저에게는 당신 같은 직업을 가진 전문가들이 입는 갑옷 한 세트가 있답니다. 물론 품질은 훨씬 더 좋지요. 작은 부탁 하나만 들어주시면 당신이 가진 평범한 팔보호구를 제가 가지고 있는 팔보호구와 교환해 드리겠어요.$B$B여명의 설원에 살고 있는 눈호랑이와 곰의 혈액에는 제가 지금 필요로 하는 성분이 들어 있어요. 혈액 견본을 많이 구해서 팔보호구와 약간의 돈과 함께 가지고 오시면 그에 합당한 보상을 드리도록 하지요..', },
