  [9292] = {
    '부서진 죽음의 수정',
    {'혐오스러운 언데드의 시체에서 죽음의 기운을 희미하게 발산하는 수정을 발견했습니다.', },
    {'스톰윈드 성문 외곽에 있는 부관 오린에게 부서진 죽음의 수정을 가져가야 합니다.', },
  },
  [9293] = {
    '반드시 행해져야 할 것...',
    {'인근의 호수가 배에 있던 힘의 수정 탓에 오염되어 암멘 골짜기에 돌연변이 동식물이 많이 생겨났습니다.$B$B전 호수를 정화할 수 있는 이론을 세워놓았습니다만, 분석을 위해 근처에 있는 돌연변이 덩굴손 표본이 있어야만 합니다. 덩굴손은 암멘 들판 서쪽에 있을 겁니다.$B$B$C님, 저를 좀 도와주시겠습니까?', },
    {'덩굴손 표본 10개를 수집한 다음 암멘 골짜기의 추락 지점에 있는 식물학자 태릭스에게 가져가야 합니다.', },
  },
  [9294] = {
    '호수 정화',
    {'신께 감사를. 효과가 있을 것 같군요! 제가 남쪽에 있는 희망의 호수에 뿌릴 중화제를 만들었습니다. 이것은 그곳에 뿌려진 오염된 비행선에서 나온 마력의 수정의 영향을 중화할 겁니다.$B$B비행선의 잔해가 있는 곳으로 헤엄쳐 가서 그 근처에 중화제를 뿌리세요. 당신이 오염될 정도로 노출 시간이 길지는 않을 테니 걱정하지 마십시오.', },
    {'방사능에 오염된 마력의 수정에 중화제를 뿌린 다음 암멘 골짜기의 추락 지점에 있는 식물학자 태릭스에게 가야 합니다.', },
  },
  [9295] = {
    '전방에서 온 편지',
    {'수많은 스컬지 앞잡이들의 시체 중 하나에서 편지를 발견했습니다. 편지의 상태나 냄새로 보아 언데드가 꽤 오랫동안 지니고 있었던 것 같습니다. 희망의 빛 예배당에는 이 편지를 좀 더 조사하려는 사람이 있을 것입니다.', },
    {'동부 역병지대의 희망의 빛 예배당에 있는 기록 보관원에게 찢어진 편지를 가져가야 합니다.', },
  },
  [9299] = {
    '전방에서 온 쪽지',
    {'수많은 스컬지 앞잡이들의 시체 중 하나에서 편지를 발견했습니다. 편지의 상태나 냄새로 보아 언데드가 꽤 오랫동안 지니고 있었던 것 같습니다. 희망의 빛 예배당에는 이 편지를 좀 더 조사하려는 사람이 있을 것입니다.', },
    {'동부 역병지대의 희망의 빛 예배당에 있는 기록 보관원에게 근심 가득한 쪽지를 가져가야 합니다.', },
  },
  [9300] = {
    '전방에서 온 찢어진 페이지',
    {'수많은 스컬지 앞잡이들의 시체 중 하나에서 편지를 발견했습니다. 편지의 상태나 냄새로 보아 언데드가 꽤 오랫동안 지니고 있었던 것 같습니다. 희망의 빛 예배당에는 이 편지를 좀 더 조사하려는 사람이 있을 것입니다.', },
