  [9345] = {
    '고약 재료 준비',
    {'제스고르가 시야에 들어오자마자 불타는 군단의 정찰병이 보였습니다. 조금 멀리 있는 탑으로 몸을 피하기 위해 있는 힘껏 달렸지만 놈들의 지옥사냥개에게 따라잡혀서 거의 만신창이가 되었습니다!$B$B가까스로 탈출은 했지만 다리는 흉측하게 변하고 말았네요. 투사 암벨란이 저를 발견하곤 이리로 데려왔습니다.$B$B이제 다리를 치료할 방법을 찾아야 합니다. 북쪽에 뼈의 골짜기 주변에는 가시돌기라는 식물이 자라는데 그걸로 고약를 만들어서 다리에 바르면 고통을 덜고 몸을 움직일 수 있을 겁니다.', },
    {'고약의 재료가 되는 지옥불 가시돌기 12개를 모아서 해골망치 초소의 그렐라그에게 가져가야 합니다.', },
  },
  [9349] = {
    '칼날발톱 알 수집',
    {'배고파 죽겠구먼!$B$B요리사가 배를 곪고 있다니... 좀 아이러니하지 않나?$B$B<실없이 웃고 나서는 주머니 속에 있는 접힌 양피지 조각을 가볍게 두드리는 레가시>$B$B비행선이 추락하기 전에 우리가 참고했던 이 여행 지도를 보면 말일세. 테로카르 숲 근처, 남서쪽에 서슬가시 고개라는 곳이 있다네. 아마 부화하지 않은 맛있는 칼날발톱 알이 넘쳐나는 곳일 거라고!$B$B아주 용감한 자가 그곳에 가서 그 알만 가져다준다면 푸짐하게 요리를 해서 잔치를 하겠는데 말이야!', },
    {'지옥불 반도의 비행선 추락지에 있는 레가시를 위해 칼날발톱 알 12개를 모아야 합니다.', },
  },
  [9351] = {
    '야생의 공허방랑자',
    {'비행선 몸체를 수리한다 해도 엔진을 가동할 방법을 찾아야 한다네. 연료는 바닥이 났는데 이 근처에서 연료를 채울 데가 있어야 말이지!$B$B여기서 남쪽으로 가면 일그러진 벌판이라고 파괴된 지대가 있네. 그곳에는 떠도는 공허방랑자와 구속이 풀린 공허방랑자들이 들끓고 있지. 난 그 공허방랑자들이 걸어다니는 건전지라고 생각해.$B$B자네가 그들의 정수를 충분히 가져다준다면 내가 그 정수로 비행선의 엔진을 가동할 수 있지. 5골드만 있으면 그걸 십분 활용할 수 있단 말이야!', },
    {'비행선 추락지에 있는 "호들갑쟁이" 스크리드 럭히드에게 응축된 공허방랑자 정수를 가져가야 합니다.', },
  },
  [9352] = {
    '나이트 엘프의 침입',
    {'계속되는 버림받은 자들의 위협 외에도 나이트 엘프와 관련된 무성한 소문의 진상을 밝혀내야 합니다.$B$B대부분의 보고서는 근거가 없는 내용이지만 최근 벌어진 한 사건은 좀 더 조사해 볼 필요가 있습니다. 우리 블러드 엘프의 주요한 힘의 원천 중 하나인 서부 성소에서 치명적인 고장이 일어났는데, 파괴의 주범이 나이트 엘프 정찰병이라는 소문이 파다합니다.$B$B그곳으로 가서 수상하게 보이는 자를 처리해 주십시오. 성소를 지키는 맥지기 벨라니아에게 보고하시면 됩니다.', },
    {'매날개 광장 남서쪽에 있는 서부 성소로 가서 그곳을 침입한 수상한 자를 처치한 다음, 맥지기 벨라니아에게 가서 발견한 사실을 보고해야 합니다.', },
  },
  [9355] = {
    '영리한 자를 위한 임무',
    {'$C|1이여;여;, 만나서 반갑네. 드디어 지원 병력이 도착하고 있으니 어느 정도는 일상적인 임무로 복귀할 수 있겠군.$B$B우리의 첫 번째 목표 중 하나는 남서쪽 길을 따라서 인력과 물자의 소통이 원활하게 이루어지도록 하는 것인데, 그러려면 그 지역을 침범한 바위갈이 미늘벌레를 처치해야 하네.$B$B이 위기를 벗어나도록 도와준다면 로서의 후예들이 그 은혜를 잊지 않을 것이야. 녀석들이 발자국을 남긴 어지러운 지역을 잘 살펴보면 녀석들을 찾을 수 있을 게야.', },
    {'명예의 요새에 있는 현장감독 비굼스가 약탈자 바위갈이 미늘벌레 15마리를 처치해 달라고 했습니다.', },
  },
  [9356] = {
    '버터처럼 부드러운...',
    {'스크리드는 자신의 비행 솜씨가 "버터처럼 부드럽다"고 말하는 걸 좋아한다네. 버터는 무슨... "어설프기 짝이 없다"가 더 정확한 표현이란 말이지!$B$B저기 흩어져 있는 잔해들 좀 보게나. 스크리드 본인은 그저 "거친" 착륙이었다고 말한단 말일세.$B$B물론 우리가 저격 당하긴 했지만 말이야. 스크리드가 비행 실력을 시도 때도 없이 자랑만 하지 않았어도 내 기꺼이 봐주려고 했단 말일세!$B$B아무래도 진짜 날개 요리를 만들어 저 친구에게 진정한 실력자란 어떤 건지 똑똑히 알려줘야겠네. 동쪽에 있는 뼈의 골짜기로 가면 뼈갈이대머리수리를 볼 수 있을 걸세. 녀석들의 날개는 먹음직스럽고 육즙이 가득하지. 내 요리 재료로 딱이라네.', },
    {'오동통한 대머리수리 날개 12개를 비행선 추락지에 있는 레가시에게 가져가야 합니다.', },
  },
  [9358] = {
    '순찰자 사레인',
    {'이 마을이라면 당연히 안전할 거라는 생각은 버리십시오. 동쪽으로는 스컬지, 서쪽으로는 버림받은 자들이 둘러싸고 있습니다.$B$B순찰자 사레인은 죽음의 흉터에서 밀려드는 언데드로부터 마을 경계선을 지키고 있습니다. 마을에서 동쪽으로 길을 따라가서 그녀를 도울 수 있는지 확인해 보십시오.', },
    {'영원노래 숲의 산들바람 마을 밖에 있는 순찰자 사레인에게 보고해야 합니다.', },
  },
  [9359] = {
    '원정순찰대 산장',
    {'버림받은 자들을 해치우는 데 정말 큰 도움을 주셨군요. 아마니 트롤과 전투를 벌이고 있는 우리 원정순찰대에겐 그대와 같은 인물이 꼭 필요합니다.$B$B여기서 북동쪽에 있는 원정순찰대 산장에서 부관 돈러너를 만나 도움이 필요한 일이 있는지 알아보십시오.', },
    {'영원노래 숲의 원정순찰대 산장에 있는 부관 돈러너에게 보고해야 합니다.', },
  },
  [9360] = {
    '아마니의 침공',
    {'이 계획서가 어떤 언어로 작성되었는지는 전혀 모르겠지만 원정순찰대 산장과 산들바람 마을의 위치가 표시된 지도는 정확하게 이해할 수 있습니다.$B$B원정순찰대 산장에 있는 부관 돈러너에게 이 사실을 전해 주십시오.', },
    {'원정순찰대 산장에 있는 부관 돈러너에게 아마니 침공 계획서를 전달해야 합니다.', },
  },
  [9361] = {
    '지옥멧돼지 고기 정화',
    {'으음... 이 알들은 최고야. 근데 말이지, 또 다른 최고급 재료를 구하는 일을 해보는 건 어떻겠나?$B$B삼겹살 조금만 있으면 아주 그럴듯한 식탁이 완성될 텐데 말이야. 어디에 가면 재료가 있는지도 알고 있단 말이지.$B$B바로 지옥멧돼지들이 바글바글한 곳이라네. 물론 이곳에는 그 고기의 질을 검사하고 알맞게 숙성해 줄 자는 없지만, 우린 추락에서도 살아남은 운 좋은 고블린들이 아니던가?$B$B<가방을 뒤진 후 병을 하나 꺼내는 레가시>$B$B이걸 돼지고기에 쓸려고 만든 건 아니지만 요리도 여느 것과 마찬가지로 즉석에서 조리하는 거니까 말이야!', },
    {'비행선 추락지에 있는 레가시가 정화된 지옥멧돼지 고기 8개를 모아 달라고 했습니다. 고기를 정화하려면 광기 어린 지옥멧돼지에게 얻은 오염된 지옥멧돼지 고기에 정화의 혼합물을 사용해야 합니다.', },
  },
  [9362] = {
    '장군 크렐리안',
    {'시간이 없네, $N! 난 수정점 마법으로 강력한 마력의 원천을 밝혀냈네. 나는 이 탑을 떠날 수 없으니 자네가 가서 그 유물을 가지고 있는 사악한 놈을 해치워주게나.$B$B그 불쌍한 바보는 크렐리안 장군이라고 하는데 진말로의 신전에서 경비병의 보호를 받고 있다네. 놈을 잿더미로 만들어 버린 후에 놈이 그토록 탐내던 프리즘 껍질을 가져오게.', },
    {'프리즘 껍질을 되찾아 아즈샤라 절벽 위 탑에 살고 있는 대마법사 실렘에게 돌려줘야 합니다.', },
  },
  [9363] = {
    '산들바람 마을에 경고 전하기',
    {'산들바람 마을로 가서 순찰자 데골리엔에게 아마니 침공 계획서에 대해 알려주십시오. 줄마로쉬가 죽었다고는 하지만 산들바람 마을은 트롤의 공격에 대비해야 합니다. 데골리엔에게 최대한 빨리 제가 지원군을 보내겠다고 전해 주십시오.', },
    {'영원노래 숲의 산들바람 마을로 가서 순찰자 데골리엔과 대화해야 합니다.', },
  },
  [9365] = {
    '도적의 보상',
    nil,
    nil,
  },
  [9366] = {
    '순수한 악마의 힘',
    {'여기서 북동쪽으로 가면 아고나르의 웅덩이라는 곳이 있습니다. 한때 드레나이의 성소였으나 악마 아고나르가 죽은 후 그의 피가 흘러들어 물이 오염됐지요.$B$B시간이 지남에 따라 악마의 피는 오래되고 희석됐으나 여전히 엄청난 힘을 지니고 있습니다. 지금은 최초의 피에 담긴 순수한 악마의 권능을 이어받은 공포의 마귀가 그 지역을 돌아다니고 있지요. 마귀들을 처치하고 가능한 많은 지옥피를 되찾아 오시면 그에 대한 보상을 충분히 드리도록 하겠습니다.', },
    {'지옥불 반도의 매의 감시탑에 있는 고뇌하는 리아센이 아고나르의 웅덩이에 있는 공포의 마귀에게서 지옥피 견본 6개를 가져다 달라고 했습니다.', },
  },
  [9367] = {
    '불의 축제',
    {'한여름 축제가 다시 우리를 찾아왔어요!$B$B우리는 매년 관습에 따라 우리 수도의 화톳불을 지킬 불꽃지기를 선정한답니다. 아제로스 전역에서 불꽃이 타오르고 있지만 축제에 대한 예를 제대로 표하기 위해서는 우리의 불꽃이 가장 뜨겁고 가장 밝게 타올라야 한답니다.$B$B괜찮다면 당신에게 맡길 일이 하나 있습니다. 아직 얼라이언스 연합의 모든 수도에서 불꽃이 제대로 타고 있는지 소식을 듣지 못했어요. 직접 그곳으로 가서 불을 쬐고 충분히 뜨거운지 확인해 주시겠어요?', },
    {'스톰윈드, 아이언포지, 다르나서스에 있는 화톳불을 만져보고 대도시 안에 있는 한여름 축제 현자와 대화해야 합니다.', },
  },
  [9368] = {
    '불의 축제',
    {'한여름 축제가 다시 우리를 찾아왔어요!$B$B우리는 매년 관습에 따라 우리 수도의 화톳불을 지킬 불꽃지기를 선정한답니다. 아제로스 전역에서 불꽃이 타오르고 있지만 축제에 대한 예를 제대로 표하기 위해서는 우리의 불꽃이 가장 뜨겁고 가장 밝게 타올라야 한답니다.$B$B괜찮다면 당신에게 맡길 일이 하나 있습니다. 아직 얼라이언스 연합의 모든 수도에서 불꽃이 제대로 타고 있는지 소식을 듣지 못했어요. 직접 그곳으로 가서 불을 쬐고 충분히 뜨거운지 확인해 주시겠어요?', },
    {'오그리마, 썬더 블러프, 언더시티에 화톳불을 만져보고 해당 대도시의 한여름 축제 이야기꾼과 대화해야 합니다.', },
  },
  [9369] = {
    '치유의 수정 보충',
    {'우리는 드레나이라네. 우리 비행선이 추락하는 바람에 부상자가 속출했네. 지금 가장 시급한 문제는 우리가 가진 치유의 수정이 고갈되고 있다는 것일세. 부상자들을 치료하려면 치유의 수정을 빨리 보충해야 하네!$B$B유감스럽게도 지금 치유의 수정을 보충하는 방법은 이 근방의 골짜기 나방의 피를 뽑는 것뿐이야. 그들의 피로 적합한 대용품을 만들 수 있을 것이야.$B$B골짜기 나방은 암멘 골짜기의 여러 곳에 있지만 대부분 북서쪽에 서식하니 그곳으로 가 보게나.', },
    nil,
  },
  [9370] = {
    '정화 의식 저지',
    {'우리 정찰병들이 최근 아고나르의 웅덩이에서 드레나이 성기사를 하나 생포했습니다. 그 성기사는 아고나르의 웅덩이를 정화하기 위한 의식에 참여하려고 했더군요. 우리가 드레나이의 정화 의식을 막지 못하면 지옥피의 공급이 끊어질 것입니다!$B$B이걸 보십시오. 그 성기사가 지니고 있던 신호의 보석인데, 사제 혹은 그들의 표현에 따르자면 수도자들이 안전하게 의식을 치를 수 있는 장소를 표시할 용도로 사용하는 물건인 듯합니다. 이걸 아고나르의 제단 근처에 놓은 후, 접근하는 드레나이가 있으면 공격할 수 있도록 매복 준비를 하시기 바랍니다.$B$B이번 임무를 함께할 친구를 몇 명 데리고 가는 게 좋겠습니다. 그럼 행운을 빕니다.', },
    {'아고나르의 웅덩이에 있는 아고나르의 제단에 신호의 보석을 놓은 후, 소환에 반응하는 모든 드레나이 수도자를 처치하십시오. 임무를 마친 다음 신호의 보석을 가지고 매의 감시탑에 있는 고뇌하는 리아센에게 가야 합니다.', },
  },
  [9371] = {
    '식물학자 태릭스',
    {'식물학자 태릭스가 당신의 도움을 필요로 합니다. 비행선의 서쪽으로 가서 그녀를 찾아보십시오.', },
    {'암멘 골짜기의 추락 지점에 있는 식물학자 태릭스와 대화해야 합니다.', },
  },
  [9372] = {
    '악마의 오염',
    {'$C|1이여;여;, 세나리온 초소에 오신 것을 환영합니다. 이곳에서 일하면서 손님을 맞이하는 건 드물기 때문에 방문자는 언제나 반갑답니다. 이 황량한 땅의 생물들을 연구하는 것이 제 일이다 보니 먼 곳까지 여행을 해야 한답니다.$B$B세나리온 원정대에서 아웃랜드의 가장 위험한 생물의 연구를 목적으로 절 이곳으로 보냈는데 놀라운 것을 발견했습니다. 이곳의 많은 생물들이 이미 급격한 변화를 겪었음에도, 계속해서 오염의 영향을 받아 돌연변이로 변하고 있습니다. 저는 그 돌연변이 변화 과정을 기록하기 위해 혈액 견본을 수집하고 있는데 도움을 주시겠다면 기꺼이 받겠습니다.', },
    {'지옥불 반도의 세나리온 초소에 있는 타이아 레드메인이 덩치 큰 지옥멧돼지로부터 지옥멧돼지 혈액 견본 6개를 가져다 달라고 했습니다.', },
  },
  [9373] = {
    '사라진 공문',
    {'낡은 가죽 용기를 검사해보니 벌레의 소화 기관에서 나온 부식성의 액체 때문에 손상된 것으로 보입니다. 이 용기는 한때 매우 정교한 세공을 자랑했을 것으로 여겨지지만, 그런 세밀한 장식은 부식하여 더는 알아보기 어렵습니다.$B$B용기의 외관에서 알아볼 수 있는 유일한 그림은 세나리온 의회를 상징하는 낡은 문양뿐입니다. 용기의 내용물을 보기보단 세나리온 의회에 이 용기를 그대로 가져다주는 것이 제일 좋겠다고 생각하며 자리를 털고 일어납니다.', },
    {'지옥불 반도의 서쪽의 샤나르 폐허 근처에서 야영을 하고 있는 타이아 레드메인에게 부식된 가죽 용기를 가져가야 합니다.', },
  },
  [9374] = {
    '아렐리온의 일지',
    {'제 남편인 아렐리온은 아웃랜드에 도착한 후로 줄곧 아고나르의 웅덩이에 매료되었었지요. 하필이면 남편은 그 웅덩이에 사는 별것도 아닌 진흙괴물 연구에 대부분의 시간을 할애했었답니다!$B$B그리고 며칠 전 남편이 그곳에서 실종됐습니다. 다른 자들은 남편은 죽었으니 제가 할 수 있는 일은 아무것도 없다고 말하더군요.$B$B하지만 전 마지막으로 남편을 위해 남편의 연구가 죽음과 함께 끝나지 않도록 했으면 합니다. 북동쪽에 있는 아고나르의 웅덩이에 가서 남편의 일지를 찾아봐 주십시오. 남편은 갈색 배낭안에 항상 일지를 가지고 다녔답니다.', },
    {'지옥불 반도의 매의 감시탑에 있는 마법학자 카린다가 아고나르의 웅덩이로 가서 아렐리온의 일지를 되찾아 달라고 했습니다.', },
  },
  [9375] = {
    '매의 감시탑으로 호위',
    {'<얼굴에 안도감을 표시하는 지친 블러드 엘프>$B$B$C님, 제발 이 우리에서 절 좀 꺼내 주세요! 저는 어리석게도 말티크 토르셀도리의 경고를 무시하고 다른 일행보다 먼저 스랄마로 출발했었어요.$B$B그리고 말티크가 말했던 그 갈퀴발톱이라고 밖에 볼 수 없는 무시무시한 괴물한테 공격을 당했지 뭐예요. 가까스로 녀석들을 따돌렸지만 그건 그저 제 고난의 시작에 불과했답니다.$B$B상처를 붕대로 감고 있을 때 저 날개 달린 녀석들이 절 발견하고는 이 동굴로 끌고 와서 이렇게 우리에 가둬 버렸어요! 전 반드시 매의 감시탑에 가야만 해요! 저 좀 꺼내 주세요!', },
    {'매의 감시탑까지 부상당한 블러드 엘프 순례자를 호위한 후, 탈레리스 돈게이저와 대화해야 합니다.', },
  },
  [9376] = {
    '순례자의 잃어버린 짐',
    {'저 부상당한 순례자가 짐 어쩌고 하면서 소리를 지르는 것 같군.$B$B그녀를 발견했을 때 그 근처에서 짐 보따리 하나 못 보셨소? 흠...$B$B그녀가 습격하던 자들에게서 달아날 때 그 물건을 두고 온 게 틀림없소. 이곳에서 남쪽으로 녀석들의 골짜기에 서식하는 깃송곳니 갈퀴발톱은 단독 여행자를 공격하는 걸로 알려져 있소이다.$B$B그녀가 그 짐 때문에 진정하지 않으면 내가 상처를 돌볼 수가 없소이다. 그녀의 완고함이 그녀의 목숨을 앗아갈 것이라는 거요.$B$B먼지깃 협곡으로 가서 그녀가 머물렀던 야영지를 찾아보시오. 그곳에 짐이 있을 것이오이다.', },
    {'부상당한 블러드 엘프 순례자가 먼지깃 협곡에서 야영할 때 깃송곳니 갈퀴발톱에게 공격받았습니다. 그녀의 야영지를 찾아서 잃어버린 짐이 있는지 수색해봐야 합니다.', },
  },
  [9378] = {
    '공포의 요새 낙스라마스',
    {'두껍게 펼쳐진 역병숲 속에 공포의 요새 낙스라마스로 가는 입구가 있어요. 지금까지 낙스라마스로 들어가려는 시도가 수차례 있었지만 룬 차원문에 설치된 마법 수호물 때문에 모두 실패하고 말았었죠. 지금까진 말이에요.$B$B지금은 비전 마법을 통한 은신술로 낙스라마스로 들어갈 수 있어요. 제가 키린 토의 고대 주문을 약간 수정하여 고안해 낸 것이죠. 키린 토의 고대 주문을 제가 약간 수정하는 것이랍니다. 이 방법은 돈이 상당히 많이 들어갑니다. 하지만 은빛 여명회를 위해 헌신하고자 하는 당신의 마음이 확고하니 이번 일에 필요한 비용을 일체 받지 않겠습니다!', },
    {'동부 역병지대의 희망의 빛 예배당에 있는 대마법사 안젤라 도산토스가 비전 은신 마법으로 낙스라마스로 들어갈 수 있도록 해 줄 것입니다. 은빛 여명회의 평판이 확고한 동맹이어야 합니다.', },
  },
  [9380] = {
    '박물관 전시물',
    nil,
    nil,
  },
  [9381] = {
    '명궁의 화살',
    {'순례자들이 유입되고 얼라이언스 군대의 공격이 시작되면서 최근에 매의 감시탑 주변의 상황이 바빠지고 있습니다. 시간이 남을 때 저는 보통 순찰대가 사용할 화살 제조 일을 돕곤 하는데 오랫동안 화살에 사용할 깃털을 모으지 못했답니다.$B$B그래서 말인데 뼈갈이식인독수리의 꼬리 날개를 모아다 주시면, 그에 합당한 보상을 해 드리도록 하겠습니다. 옛날 제가 깃털을 모으러 다닐 때, 매의 감시탑의 북쪽에 거대한 야수의 뼈 주위에 모여드는 뼈갈이식인독수리들을 본 적이 있으니 그리로 가 보시기 바랍니다.', },
    {'뼈갈이식인독수리로부터 뼈갈이식인독수리 꼬리 깃털 8개를 모은 후 매의 감시탑에 있는 매부리 드렌나 리버윈드에게 가져가야 합니다.', },
  },
  [9383] = {
    '야심찬 계획',
    {'블러드 엘프는 마법을 무책임하게 사용하는 전형적인 종족이랍니다, $C|1이여;여;. 그들은 마력을 조절하기는커녕 오히려 그 노예가 되어 아무 거리낌 없이 마법에만 집착하고 있지요. 게다가 자신들의 사악한 마력을 강화하기 위해 아웃랜드에 사는 공허방랑자의 힘을 흡수하고 있습니다.$B$B제가 준비한 이 수정에는 공허방랑자를 무력화시키고 파괴되었을 때 이 세계와의 연결고리를 끊어버리게 하는 주문이 담겨 있습니다. 지옥불 성채의 남동쪽에 일그러진 벌판에서 공허방랑자들을 보았으니 그리로 가보시기 바랍니다.', },
    {'구속이 풀린 공허방랑자에게 정화의 수정을 사용한 후, 텔하마트 사원에 있는 엘사아나에게 가야 합니다.', },
  },
  [9385] = {
    '광포한 갈퀴발톱',
    {'말씀드린 바와 같이 명예의 요새에서 최고의 사냥꾼인 우리를 보내 아라코아를 처리하라고 했지요. 그래서 여행 중에 이곳의 위치가 아주 좋아 야영지를 세웠는데 깃송곳니 갈퀴발톱이 얼마나 가까이 서식하는지 깨달았지 뭡니까!$B$B녀석들은 우리가 하는 일마다 훼방을 놓는 무자비한 약탈자와 같은 녀석들입니다. 우리를 위해 녀석들을 처리해 주신다면 약간의 답례를 해 드리도록 하죠. 자, 일을 맡으시겠습니까?', },
    {'깃송곳니 갈퀴발톱 10마리를 처치한 다음 지옥불 반도에 있는 그렘니 롱비어드에게 가야 합니다.', },
  },
  [9386] = {
    '어둠을 밝히는 한 줄기 빛',
    nil,
    nil,
  },
  [9387] = {
    '오염의 원인',
    {'여기 포로로 잡혀 있는 이 불행한 친구처럼 오염되지 않은 드레나이는 성스러운 빛을 수호하는 위대한 전사라오. 도대체 어떤 오염이기에 그 위대한 종족이 뒤틀린 드레나이와 잃어버린 드레나이로 전락하고 만 것인지 정말 궁금하오.$B$B돌연변이가 생긴 것은 그들이 지옥 마력에 노출되었기 때문이라는 이론을 들었는데 한번 직접 검증해 보고 싶소.$B$B이곳으로부터 북서쪽에 샤나르 폐허라는 곳이 있소. 그곳에 가면 일리다리 감독관의 지배를 받고 있는 뒤틀린 드레나이들을 볼 수 있을 거요. 감독관에게서 악마의 정수를 가져다주시오. 이 드레나이에게 시험을 해보고 싶군.', },
    {'지옥불 반도에 있는 샤나르 폐허로 가서 일리다리 감독관이 가지고 있는 악마의 정수 5개를 획득해야 합니다. 일을 마친 후 매의 감시탑에 있는 연금술사 아제센에게 돌아가십시오.', },
  },
  [9388] = {
    '칼림도어의 희미한 불꽃',
    {'지금 세계 곳곳에 많은 불꽃이 타오르고 있지만, 약한 불꽃은 녹색으로 타는 경향이 있지. 누군가 돌봐주지 않으면 오래가지 않아 꺼질 거야. 우리 불꽃지기를 위해 그 에메랄드빛 불꽃들이 칼림도어에서 계속 타오르는지 좀 봐주게.$B$B불모의 땅 톱니항 부근과 어둠해안에 있는 지배자의 고대검 근처, 잿빛 골짜기 숲속 은빛날개 전초기지 근처의 다리 옆, 그리고 돌발톱 산맥의 대로를 따라 있는 칼바람 바위산 길목에서 찾을 수 있을 걸세.', },
    {'잿빛 골짜기, 불모의 땅, 어둠해안, 돌발톱 산맥에 불꽃이 있는 장소를 방문한 다음 한여름 축제 불꽃지기와 대화해야 합니다.', },
  },
  [9389] = {
    '동부 왕국의 가물거리는 불꽃',
    {'한여름 불꽃축제는 무수한 화톳불로 대지를 밝히는 것이 전통이지. 특유의 빛깔을 지니고 불꽃을 유지하기가 가장 어려운 만큼 축제를 위해서는 녹색 불꽃이 한층 좋다는 언쟁들이 좀 있다네.$B$B그래서 말인데 우리 축제의 불꽃지기를 위해 동부 왕국에 불꽃이 아직 타고 있는지 확인해 주게나. 그들은 저습지의 던 모드르 근처와 은빛소나무 숲 안에 장작나무 마을, 서부 몰락지대에 있는 등대자리가 내려다보이는 절벽 근처와 언덕마루 구릉지에 폐허가 된 탑 근처에 자리 잡고 있네.', },
    {'언덕마루 구릉지와 은빛소나무 숲, 서부 몰락지대와 저습지 내에 있는 화톳불을 찾은 후, 한여름 축제 불꽃지기와 대화해야 합니다.', },
  },
  [9390] = {
    '세다이 찾기',
    {'$C|1이여;여;, 내가 처한 곤경은 이러하오. 내 형제 세다이가 오래전에 마그하르 거점의 오크들을 만나기 위해 떠났다오. 이상주의자였던 세다이는 마그하르 오크들에게 우리의 다름은 배제하고 평화 협상을 할 때가 됐다고 생각했었지.$B$B그의 행방이 며칠 동안 묘연한데 최악의 사태가 벌어진 것은 아닌지 걱정이오.$B$B내가 안심할 수 있도록 그를 찾아봐 주시겠소? 분명 동쪽 어딘가에 세다이가 있을 것이오.', },
    {'수도자 오베다이가 지옥불 반도의 동쪽 근처 어딘가에 있는 자신의 형제 세다이를 찾아 달라고 했습니다.', },
  },
  [9391] = {
    '순례자들을 위한 길',
    {'바위갈퀴 녀석들의 숫자가 줄었으니 우리 순례자들에게 안전한 길을 알려 주는 일만 남았군. 이 횃불을 가지고 거대한 균열로 돌아가서 동쪽으로부터 이어지는 길을 표시하는 봉화 3개를 찾으시오.$B$B이번 일은 그 봉화를 찾아 3개 모두에 불을 붙인 후 내게 돌아오는 간단한 임무라오. 봉화에 불을 붙이면 근처에 있는 바위갈퀴들이 그리로 몰려들 테니 조심하시오. 자, 그럼 임무를 다 마치고 돌아오길 기다리고 있겠소.', },
    {'매의 감시탑에 있는 순찰대장 벤렌이 지옥불 반도에 있는 거대한 균열로 가서 남쪽 봉화, 서쪽 봉화, 중앙 봉화에 불을 붙여 달라고 했습니다. 일을 마친 후 불붙인 횃불을 가지고 순찰대장 벤렌에게 가야 합니다.', },
  },
  [9392] = {
    '도적 훈련',
    {'그대는 실력이 향상될수록 상급자로부터 더욱 다양한 기술을 배우고 싶을 겁니다. 여기 선스트라이더 섬에 있는 젊은 도적들을 위해 추적자 카리엘이 훈련을 맡고 있습니다. 카리엘을 만나서 어떤 훈련을 받을 수 있는지 알아보십시오. 기술을 배우려면 돈을 조금 가져가십시오.$B$B선스트라이더 섬의 다른 직업 상급자들과 마찬가지로 카리엘 역시 태양 첨탑 하층에 있으니 그리로 가보십시오.', },
    {'선스트라이더 섬의 태양 첨탑 안에 있는 상급 도적 추적자 카리엘과 대화해야 합니다.', },
  },
  [9393] = {
    '사냥꾼 훈련',
    {'그대는 실력이 향상될수록 상급자로부터 더욱 다양한 기술을 배우고 싶을 겁니다. 여기 선스트라이더 섬에 있는 젊은 사냥꾼들을 위해 순찰자 살리나가 훈련을 맡고 있습니다. 살리나를 만나서 어떤 훈련을 받을 수 있는지 알아보십시오. 기술을 배우려면 돈을 조금 가져가십시오.$B$B선스트라이더 섬의 다른 직업 상급자들과 마찬가지로 살리나 역시 태양 첨탑 하층에 있습니다.', },
    {'선스트라이더 섬의 태양 첨탑 안에 있는 상급 사냥꾼 순찰자 살리나와 대화해야 합니다.', },
  },
  [9394] = {
    '빌리덴을 찾아라',
    {'더스크위더 정원의 관리인이 실종됐습니다! 마법학자와 제자들이 떠날 때 빌리덴을 깜빡 잊은 게 틀림없습니다. 그는 아직 더스크위더 정원 어딘가에 있을 겁니다. 하지만 그곳을 떠도는 마법 생물들 때문에 제가 직접 찾으러 갈 수는 없네요.$B$B$C님, 저 대신 그를 찾아서 무사한지 확인해 주시겠습니까?', },
    {'영원노래 숲의 더스크위더 정원에 있는 관리인 빌리덴을 찾아야 합니다.', },
  },
  [9395] = {
    '살데릴의 안식처',
    {'맹세컨대 앞으로 한 번만 더 살데릴 경의 파티 물품 요청서를 받으면 내 손에서 화염구가 터질 거요! 내가 파티 기획자로 보이나?! 우리끼리 이야긴데 그 바보와 아첨꾼들은 이곳이 공격받고 있다는 사실을 부정하고 있소!$B$B자세히는 모르지만 우리 중 일부는 쿠엘탈라스의 유산을 지키느라 정신이 없는데! $C|1이여;여;, 살데릴의 안식처로 가서 흥청망청 대는 파티를 멈춰주시겠소? 길을 따라 서쪽으로 가시오.', },
    {'영원노래 숲의 살데릴의 안식처에 있는 살데릴 경과 대화해야 합니다.', },
  },
  [9396] = {
    '아라코아의 마법',
    {'마법의 힘이 흐르는 이 아웃랜드에는 많은 비밀이 숨어있소이다. 모든 비밀을 밝혀내는 것은 단지 시간문제일 뿐이지.$B$B반은 인간이고 반은 새인 아라코아는 이 세계를 탐구하는 데 큰 도움이 될 만한 고대의 지식을 보유하고 있소이다. 여기서 남쪽에 있는 하알에쉬 소굴로 가시오. 하알에쉬 아라코아들이 지니고 있는 마법 두루마리를 구해서 내게 가져와 주시오.', },
    {'지옥불 반도의 매의 감시탑에 있는 비전술사 케일스트리스 돈스타에게 하알에쉬 두루마리 4개를 가져가야 합니다.', },
  },
  [9397] = {
    '유유상종',
    {'하알에쉬 아라코아는 칼리리라는 아름다운 새를 길들이곤 합니다.$B$B얼마전 간신히 새끼 칼리리 수컷을 잡았는데 길들일 수 있을 것 같더군요. 근데 새끼 칼리리 암컷은 희귀해서 말이죠. 혹시 제가 암컷 한 마리만 구할 수 있다면 칼리리 새끼를 볼 수 있을 같은데... 하지만 암컷은 반드시 어린 새끼여야 합니다. 성장한 야생의 새는 갇혀 지내면 오래 살지 못하기 때문이지요.$B$B이 새장을 가지고 남쪽에 있는 하알에쉬 소굴로 가십시오. 둥지를 찾아서 막 부화하려는 알이 있는지 잘 살펴보십시오. 암컷 새끼를 가져다주시면 후한 보상을 드리도록 하겠습니다.', },
    {'지옥불 반도에 있는 하알에쉬 소굴로 가서 칼리리 둥지를 뒤져 새끼 칼리리 암컷을 찾아야 합니다. 빈 새장으로 암컷을 잡아 매의 감시탑에 있는 매부리 드렌나 리버윈드에게 가져가야 합니다.', },
  },
  [9398] = {
    '치명적인 약탈자들',
    {'아웃랜드에 살고 있는 뒤틀린 드레나이들은 끝없는 폭력에 시달려 왔습니다. 그래서 어둠의 문이 열리자마자 이 땅을 벗어나 보겠다고 무리를 지어 어둠의 문에 들어간 것은 별로 놀랄만한 일이 아니지요.$B$B유감스럽게도 남쪽에서 오고 있는 우리의 가여운 형제와 자매들이 거대한 균열에 살고 있는 바위갈퀴의 두 눈에 먹잇감이 되어 놀아나고 있습니다. 아이들이 살아남을 수 있도록 그곳으로 가서 바위갈퀴의 숫자를 줄여 주시기 바랍니다.', },
    {'지옥불 반도의 텔하마트 사원에 있는 정찰병 바누라가 새끼 돌낫 바위갈퀴 8마리, 덩치 큰 돌낫 바위갈퀴 4마리를 처치해 달라고 했습니다.', },
  },
  [9399] = {
    '잔인한 감독관',
    {'당신은 아마 뒤틀린 드레나이들이 악마라고 생각하겠지, 그렇지 않소? 사실은 우리가 누구인지에 대한 안내가 부족해서 그런 것이라오.$B$B오메나이를 만나기 전에는 나도 그랬다오.$B$B우리는 오크의 사악한 마법 탓에 끔찍한 정신적 고통을 겪었소. 우리는 쉽게 속아넘어가고 쉽게 이용당하지.$B$B이젠 오메나이를 만났기에 희망이 생겼소이다. 일리단과 손잡은 악마들은 매일같이 지옥불을 습격해서 우리 동족들을 노예로 잡아가고 있소. 여기서 남서쪽에 있는 샤나르 폐허로 가서 잃어버린 드레나이가 자유를 찾을 수 있도록 일리다리 감독관을 처치해 주시오!', },
    {'샤나르 폐허에 있는 일리다리 감독관 4명을 처치한 후, 지옥불 반도의 텔하마트 사원에 있는 이칸에게 가야 합니다.', },
  },
  [9400] = {
    '암살자 처치',
    {'$N, 최근에 매의 감시탑으로 가는 우리 사절을 암살하려는 계획이 있다는 사실을 알아냈네. 지옥불 성채에 있는 녀석들이 우리의 계획을 알고 오늘 아침 일찍 그곳에 우리 병력을 보내 매복해 놓았지.$B$B우리 정찰병의 보고에 따르면 크룬 스파인브레이커라는 암살자가 성채의 북쪽 길에서 매복하고 있다고 하더군. 그곳에 가서 그 암살자를 처치해주게. 타락한 오크들에게 우리가 호락호락하지 않다는 걸 보여 주게나!', },
    {'나즈그렐이 지옥불 성채의 북쪽 길로 가서 크룬 스파인브레이커라는 이름의 암살자를 찾아봐 달라고 했습니다.', },
  },
  [9401] = {
    '이상한 무기',
    {'시체에서 돌도끼를 빼내 자세히 살펴봅니다. 돌도끼에 사용된 가공 기술이 뭔가 다릅니다.$B$B돌도끼의 재료는 지금까지 본 적이 없는 것이지만 오크의 가공 기술이 녹아난 무기인 것으로 보입니다. 나즈그렐이 이 무기를 보면 관심을 보일 것입니다.', },
    {'지옥불 반도의 스랄마에 있는 나즈그렐에게 무거운 돌도끼를 가져가야 합니다.', },
  },
  [9402] = {
    '물어와!',
    {'그러니까... $N|1이라고;라고;? 자네 이름을 알고 있다고 너무 좋아하진 말게나. 제자 하나가 자네 칭찬을 늘어놓는데 좀처럼 입을 다물지 않더군.$B$B$G그녀에게:그에게; 좀 더 감동을 주고 싶다면 내가 맡기는 일을 하게. 뒤에 있는 연못 바닥에 하늘색 유리병이 있네. 연못에 잠수해서 유리병을 찾아다 주게나.', },
    {'잔잔한 속삭임 연못 바닥으로 잠수해서 스승 안시올의 하늘색 유리병을 찾아야 합니다.', },
  },
  [9403] = {
    '가장 순수한 물',
    {'작은 교훈을 하나 깨우쳤으니 더 중요한 일을 맡기겠네. 근접 전투에서 자신을 방어할 수 있느냐는 실력이 결정하지. 매우 급한 상황에서는 당연히 난전을 벌여야지. 그렇다고 해서 평소에 아무 준비도 안 할 수는 없지 않은가?!$B$B자네 정도 실력의 마법사에게 어울리는 지팡이를 만들 수 있도록 도와주겠네. 자네가 찾아온 유리병을 가지고 엘렌다르 폭포 밑바닥으로 내려가서 물을 채워오면 되는 거야. 여기서 강을 따라 남동쪽으로 가게나.', },
    {'스승 안시올이 엘렌다르 폭포수로 하늘색 유리병을 채워달라고 했습니다.', },
  },
  [9404] = {
    '생명의 가지',
    {'엘렌다르 폭포 근처를 걸어다니는 나무들을 본 적이 있을 걸세. 그들은 자연의 수호자인 숲지기들로서 태초의 마력이 충만하지. 자네가 가져온 순수한 물을 사용하면 그 마력을 우리 목적에 맞게 변형할 수 있을 거야.$B$B유감스럽게도 그 숲지기들은 심성이 삐뚤어진 존재들이지. 그러니 쓸만한 가지를 찾으려면 몇 놈을 해치워야 할 거야.', },
    {'스승 안시올에게 가져갈 생명의 가지를 얻을 때까지 영원노래 숲지기들을 처치해야 합니다.', },
  },
  [9405] = {
    '대족장의 지령',
    {'자네도 알다시피 대족장님께서는 한 가지 이유 때문에 이 원정의 배후에서 지시를 내리고 계시다네. 이곳은 우리가 떠나온 세계이자 우리의 뿌리가 있는 곳이지.$B$B이번 원정은 오크족과 블러드 엘프족에게 서로 다른 의미가 있다네. 블러드 엘프 입장에서는 약속의 땅을 찾아가는 것이고, 오크 입장에서는 고향에 돌아가는 편에 더 가깝지.$B$B누가 되었든 간에 이 도끼를 만든 자는 전통적인 방법에 익숙할 테니 반드시 만나야만 하는 자일세. 이번 일은 보통의 추적 방식을 쓰기에는 일이 너무나 중하군. 예언자인 레굴쿠트에게 암살자의 목숨을 앗아간 이 무기를 보여 주게. 그녀는 어떻게 해야 할지 알고 있을 걸세.', },
    {'지옥불 반도의 스랄마에 있는 예언자 레굴쿠트에게 무거운 돌도끼를 가져가야 합니다.', },
  },
  [9406] = {
    '마그하르',
    nil,
    {'지옥불 반도의 스랄마에 있는 나즈그렐에게 마그하르로부터 온 편지를 전해야 합니다.', },
  },
  [9407] = {
    '어둠의 문을 지나서',
    nil,
    nil,
  },
  [9408] = {
    '잊혀진 영웅들',
    nil,
    nil,
  },
  [9409] = {
    '긴급 배달!',
    {'치유의 수정을 보충하는 데 필요한 재료는 모두 모았으니 우리의 사제인 잘둔에게 유리병을 전해 주겠나? 그 친구는 비행선 중앙에 있는 방에서 부상자들을 치료하고 있을 게야.', },
    {'암멘 골짜기의 추락 지점에 있는 잘둔에게 유리병이 든 가방을 가져가야 합니다.', },
  },
  [9410] = {
    '정령 길잡이',
    {'우리가 보유한 최고의 추적용 늑대의 감각으로도 이번 임무는 실패할 수 있습니다. 영혼은 우리와 다른 방법으로 세계를 인식하지요. 당신이 불러낼 존재가 당신이 찾는 자에게로 안내할 겁니다. 그들은 냄새가 아닌 본질을 추적하기 때문이지요.$B$B$C|1이여;여;, 이제 떠나십시오. 이 토템을 가지고 가서 돌도끼를 찾은 장소에서 사용하십시오.', },
    {'크룬 스파인브레이커의 시체를 발견한 장소에서 토템을 사용한 후, 고대의 늑대 정령을 따라가야 합니다.', },
  },
  [9415] = {
    '치안대장 블루월에게 보고',
    {'블루월 치안대장의 신병입니까? 물론 그렇겠죠.$B$B뭘 기다리죠? 훌륭한 병사들이 사막에서 죽어 가고 있습니다. 그들과 함께 싸울 생각을 하니 흥분되지 않습니까?$B$B가는 도중에 탈수증에 걸리지 않도록 조심하고 이제 치안대장에게로 가보십시오.', },
    {'하이브조라 근처의 얼라이언스 야영지에 있는 치안대장 블루월과 대화해야 합니다.', },
  },
  [9416] = {
    '사령관 키리카에게 보고',
    {'자네는 사령관 키리카의 신병 중 하나가 틀림없군. 이 맘 때쯤 도착할 것이라고 생각했소. 사막에서는 시체가 금방 사라지지.$B$B드워프 지원군은 나날이 불어가는 상황에서 실리더스 자원 쟁탈전의 기반을 잃어버려서는 안 되오.$B$B사령관을 만나 지시사항을 알려달라고 하시오. 당신을 만나는 것으로 내 임무는 끝났소. 훈련은 딴 데 가서 받으시오.', },
    {'하이브레갈 근처의 호드 야영지에 있는 사령관 키리카와 대화해야 합니다.', },
  },
  [9417] = {
    '아라코아의 위협',
    nil,
    {'지옥불 반도에 있는 그렘니 롱비어드가 하알에쉬 바람술사 4마리, 하알에쉬 갈퀴수호병 6마리를 처치해 달라고 했습니다.', },
  },
  [9418] = {
    '아브루의 구슬',
    {'<수정구를 가까이 가져가자 들려오는 속삭이는 목소리>$B$B네게서는 아라코아의 기운이 느껴지지 않는구나. 내가 자유를 찾을 수 있도록 도와다오. 난 아에라나스라고 한다. 한때 동쪽의 바람을 다스렸었지. 하알에쉬 마법사인 아브루가 나를 함정에 빠트려 이 구슬 안에 가두어 놓았다.$B$B내가 자유를 되찾으려면 아라코아가 나를 잡을 때 사용했던 제단을 찾아야 한다. 그 제단은 하알에쉬 소굴의 남동쪽에 숨겨져 있다. 이 구슬을 가지고 제단에 접근한 다음 내가 풀려나면 그 구슬을 다시 다오. 그러면 내 보답으로 강력한 보물을 주겠다!', },
    {'지옥불 반도의 하알에쉬 소굴의 남동쪽에 있는 하알에쉬 제단으로 아브루의 구슬을 가지고 간 다음 아에라나스가 풀려나면 그에게 줘야 합니다.', },
  },
  [9419] = {
    '사막 탐색',
    {'최근 호드 때문에 실리더스 사막의 자원이 고갈되고 있다는 사실을 발견했지만 크게 신경 쓰지는 않았습니다. 이 무더운 사막에 대규모 병력을 배치하는 것은 옳지 않다고 생각했거든요.$B$B하지만, 자원의 중요성이 강조되면서 우리는 호드를 따라잡기 위해 애쓰고 있습니다.$B$B저를 돕겠다면 사막으로 가서 실리시스트를 찾아오세요.', },
    {'실리시스트를 찾아서 실리시스트 추출기로 가져간 후, 하이브조라 근처 얼라이언스 주둔지에 있는 치안대장 블루월과 대화해야 합니다.', },
  },
  [9420] = {
    '질 좋은 깃털',
    {'이곳에서 남쪽에 있는 하알에쉬 소굴의 아라코아는 칼리리라는 거대한 종의 새를 기르고 있습니다. 이 새의 깃털을 아제로스 경매장에 내놓으면 꽤 큰돈이 될 겁니다. 그 돈이 있으면 내가 살 수 있는 맥주가 얼마나 될지 한번 생각해 보세요!!$B$B칼리리 깃털을 가지고 오면 제 모자를 드리도록 하죠. 엥? 이 모자가 별 게 아니라고 생각하시나 보죠? 이런 좋은 모자는 어딜 가도 찾을 수가 없을 거라고요! 자, 우리 거래는 성사된 건가요?', },
    {'지옥불 반도에 있는 미렌 롱비어드가 하알에쉬 소굴에 있는 여러 종의 칼리리에게서 칼리리 깃털 8개를 가져다 달라고 했습니다.', },
  },
  [9422] = {
    '사막 탐색',
    {'우린 요즘 실리더스의 사막에서 수정 자원을 추출하고 있다네. 이 지역을 서식지로 삼아 버린 곤충들이나 숨 막힐 듯한 더위와 먼지, 모래만 해도 충분히 위험한 상황이었네.$B$B그런데 이젠 얼라이언스 녀석들마저 우리가 이곳 실리더스에서 발견한 것들을 훔쳐가고 있는 것 같군그래. 이제 이곳의 천연자원은 누가 먼저 차지하고 지켜내느냐의 문제가 되었네. 그야말로 전쟁이지.$B$B가능한 모든 도움이 필요하니 자네가 나서줬으면 좋겠네.', },
    {'실리시스트를 찾아서 실리시스트 추출기로 운반한 후, 하이브레갈 근처의 호드 야영지에 있는 사령관 키리카와 대화해야 합니다.', },
  },
  [9423] = {
    '오베다이에게 전할 소식',
    {'이 시체가 세다이인 것을 확인하였으니 오베다이에게 돌아가 슬픈 소식을 전해야만 합니다.', },
    {'지옥불 반도의 텔하마트 사원에 있는 수도자 오베다이에게 돌아가 세다이의 죽음을 전해야 합니다.', },
  },
  [9424] = {
    '마쿠루의 복수',
    {'이 마쿠루에게 세다이는 형제나 다름없었소. 우리는 그에게 목숨 이상의 것들을 빚졌단 말이오! 다른 자들은 나를 미쳤다고 하지만 오메나이가 세다이의 죽음에 아무런 행동도 하지 않는다면 미친 건 바로 그들이오!$B$B나 마쿠루는 재산이라고 부를 만한 게 거의 없소. 하지만 당신이 세다이의 복수를 해 준다면 가지고 있는 유일한 물건을 주겠소이다.$B$B전에 북동쪽 언덕 위에서 갈색 오크 마그하르 그런트를 본 적이 있소. 오크는 우리를 증오하지! 녀석들은 자비를 구할 자격이 없소! 가서 세다이의 복수를 하고 녀석들의 부족 묵주를 증거로 가져다주시오!', },
    {'지옥불 반도의 텔하마트 사원에 있는 마쿠루가 마그하르 부족 묵주 10개를 가져다 달라고 했습니다.', },
  },
  [9426] = {
    '아고나르의 웅덩이',
    {'우리가 이 땅을 드레노어로 명명하기 시작한 이래로 많은 것이 변했네. 많은 지역이 악마의 타락에 의해 다시는 되돌릴 수 없을 정도로 오염되었지.$B$B아고나르의 웅덩이도 그런 곳 중 하나라네. 그곳은 한때 우리 종족의 신성한 샘이었네만 악마 아고나르의 피가 흘러내려 신성한 물의 근원이 오염되었지. 그 타락한 피가 사악한 생명체를 만들어 내고 있네. 웅덩이를 원래의 신성한 샘으로 되돌리려면 반드시 그 생명체들을 깨끗이 처리해야 하네. 여기서 동쪽으로 가서 공포의 마귀와 그곳을 돌아다니는 진흙괴물을 해치워 주게.', },
    {'현자 아만이 아고나르의 웅덩이로 가서 공포의 마귀 6마리와 기포나는 부패물 6마리를 처치해 달라고 했습니다.', },
  },
  [9427] = {
    '물 정화',
    {'자네가 지금 착수한 일을 마무리해 주었으면 하네. 사악한 생명체를 줄여 악마의 기운이 약해졌으니, 이 정화 약병을 가지고 가서 오염의 근원인 아고나르의 유해에 뿌리게나.$B$B웅덩이의 북쪽 끝으로 가면 악마의 뼈를 찾을 수 있을 걸세. 아고나르의 기운이 이 세계에서 완전히 사라진 것은 아니니 조심하게.', },
    {'아고나르의 웅덩이로 가서 아고나르의 유해에 정화의 약병을 뿌려 아고나르의 기운을 물에서 정화한 후, 현자 아만에게 가야 합니다.', },
  },
  [9428] = {
    '토막나무 주둔지로 보고',
    {'<당신에게 가까이 기대어 작은 목소리로 말하는 대사 돈싱어>$B$B$N, 그대의 명성은 참 대단하더군. 이제 잿빛 골짜기의 토막나무 주둔지로 이동해 주길 바라오. 토막나무 주둔지로 걸어서 가는 가장 쉬운 방법은 서쪽으로 듀로타와 불모의 땅을 지나 갈림길이 나오면 북쪽으로 가는 것이오.$B$B도착하면 조언자 선스원과 대화해 보시오. 여행하는 동안 조심해야 할 거요. 잿빛 골짜기는 매우 위험할 수도 있는 곳이니까!', },
    {'잿빛 골짜기의 토막나무 주둔지로 가서 조언자 선스원과 대화해야 합니다.', },
  },
  [9430] = {
    '샤나르 유물',
    {'음... 그런 일이 있었군. 전에 말한 것처럼 나는 다른 이들이 볼 수 없는 것을 보는 눈이 필요한 게 아니라네. 마쿠루를 대신해서 한 일로 자네를 판단하지는 않겠지만, 내 식견으로 자네는 눈에 보이는 것 이상의 것을 볼 필요가 있네.$B$B남서쪽에 샤나르 폐허로 가서 유물을 발견하는 대로 모두 가져오게나. 운명이 허락한다면 우리에게 필요한 것을 찾게 될 것일세.$B$B내가 준 임무의 진정한 의미가 금방 와 닿지는 않을 것이네만 믿음을 갖게나. 진실이란 인내하는 자의 것이라네.', },
    {'지옥불 반도의 텔하마트 사원에 있는 현자 아만이 샤나르 폐허로 가서 샤나르 유물 10개를 찾아 달라고 했습니다.', },
  },
  [9431] = {
    '또 다른 접근 방식',
    {'신도레이가 실버문의 전당 대신 버섯구름 봉우리의 이 우뚝 솟은 꼭대기에 서서 뭘 하고 있었느냐고요?$B$B대답은 간단합니다. 이제 형제들은 저를 받아주지 않을 거예요. 자연 치유법으로 우리 종족의 마법 중독을 고치는 내용의 제 논문 사본이 모두 불타버렸거든요.$B$B여기서 안전하게 연구를 계속할 수 있지만 재료가 없습니다. 버섯구름 봉우리의 서쪽 끝 부근에 있는 우레 바위 정령을 처치하고 정화의 토양을 모아 백사장 야영지의 제 동료에게 전해 주시면 정말 고맙겠습니다.', },
    {'우레 바위 정령을 처치하고 정화의 토양 2개를 모아서 백사장 야영지에 있는 위즐로 베어링샤이너에게 가져가야 합니다.', },
  },
  [9435] = {
    '잃어버린 수정',
    nil,
    nil,
  },
  [9436] = {
    '붉은머리 부족에게 한 수 배우기',
    {'$C|1이여;여;, 만나서 반갑소. 난 여행을 하면서 여러 트롤 부족의 전투 기술을 연구해 왔는데 어떤 법칙이 있음을 발견했지. 북쪽에 있는 트카시 폐허와 발랄 폐허의 붉은머리 트롤은 북쪽 숲의 아마니 트롤과 같은 전술을 사용하더군.$B$B하지만, 붉은머리 부족의 마법은 흥미로운 방향으로 갈라졌으며 그들은 마법을 이용해 그 지역에서 가장 강력한 부족으로 떠올랐소. 그 마법은 아마니 트롤을 상대할 때 사용하는 강력한 무기의 핵심을 이루었지. 하지만, 이 점은 좀 더 연구해야 하오.', },
    {'그롬골 주둔지의 네메스 호크아이가 붉은머리 주술사를 처치하고 붉은머리 토템을 가져다 달라고 했습니다.', },
  },
  [9437] = {
    '황혼의 새벽 물살호',
    {'이곳 오우거는 싱거운 농담, 김빠진 맥주, 박치기 대결보다 더 쓸모가 없는 놈들입니다. 제 새벽 물살호가 좌초됐을 때 테라모어의 악당들이 나타나 제 화물과 최후까지 살아남은 선원들을 몽땅 데려갔어요. 도움을 얻기 위해 이곳까지 왔지만 아무짝에도 쓸모없는 오우거들은 얘기를 다 듣고 멍하니 쳐다보기만 하더군요.$B$B믿을 만한 $R의 도움을 받아 북동쪽에 있는 북부 경비탑에서 내 화물을 되찾고 이사니아를 구하고 싶습니다.', },
    {'담쟁이 마을에 있는 발란다르 브라이트스타가 북부 경비탑에서 테라모어 군대를 물리치고 새벽 물살호 화물 상자를 되찾고 일등 항해사 이사니아를 구해달라고 했습니다.', },
  },
  [9439] = {
    '주인 없는 짐꾸러미',
    {'여기가 카르가스라고요? 오, 세상에! 전... 이 황야에 마을이나 문명의 흔적이 있을 것이라고 기대했습니다.$B$B뭔가 잘못된 것이 분명합니다. 촌스럽고, 지저분하고, 비웃음밖에 나오지 않는 미개인들로 가득한 이 돼지우리 같은 곳이 원정대의 기지일 리가 없습니다. 검은무쇠 드워프들이 제 짐을 몽땅 훔쳐서 고통의 요새로 사라진 이야기를 해줬더니 예절이라고는 눈곱만큼도 모르는 이곳 야만인들은 뻔뻔스럽게도 웃기만 하더군요. 그 짐을 되찾고 손상된 제 체면을 살려주실 분은 그대밖에 없습니다. 좀 도와주십시오.', },
    {'고통의 요새에서 조언자의 짐꾸러미와 조언자의 레이피어를 되찾은 후 카르가스에 있는 조언자 사로파스에게 가져가야 합니다.', },
  },
  [9441] = {
    '마그하르로 갈 사절',
    {'그대가 가져온 소식을 듣고 당장 아웃랜드로 떠나려고 했었으나, 듀로타에 내가 처리해야 할 중요한 문제들이 있어 자리를 뜨기가 어렵구나.$B$B내 즉시 마그하르에 적절한 감사의 표시를 할 것이며, 내가 가장 신뢰하는 사신 또한 함께 보낼 생각이다. 하지만 이 모든 것은 시간이 걸리는 일이니... 지금 당장 우리를 대표해 그곳에 갈 인물이 필요하다.$B$B그대에게 기대를 걸어 보겠다, $N. 지옥불 반도의 마그하르 거점으로 가서 나의 동족에게 최대한 많은 도움을 주도록 하라. 날 실망시키지 마라. 이번 일에 많은 것이 걸려 있다.', },
    {'대족장 스랄이 지옥불 반도의 마그하르 거점에 있는 고르칸 블러드피스트에게 돌아가서 도움을 주라고 했습니다.', },
  },
  [9442] = {
    '쇠약해지는 병',
    {'우리에게 도움의 손길을 보내줘서 정말 고맙소. 우리 그런트들이 오염된 물을 마시고 병에 걸렸는데, 치유의 연고 제조에 필요한 재료 중 하나가 없소이다. 내게 필요한 재료는 마른고깔이라는 희귀한 버섯이라오.$B$B우리는 최우선적으로 이곳의 위치를 비밀로 하라는 명령을 받았기에, 위치가 노출될 위험을 감수하면서까지 버섯을 찾으러 갈 수는 없소이다. 이런 명령만이 아니라면 내가 직접 버섯을 찾으러 갔을 것이오. 지옥불 반도에서 마른고깔버섯이 자라는 유일한 곳은 여기서 남쪽에 있는 거대한 균열의 한 동굴이오.$B$B버섯을 찾을 생각이라면 정말 조심해야 하오.', },
    {'지옥불 반도의 마그하르 거점에 있는 대지술사 리가가 거대한 균열의 한 동굴에서 마른고깔버섯 1개를 가져다 달라고 했습니다.', },
  },
