  [9452] = {
    '맛있는 붉은퉁돔',
    nil,
    {'드레나이 낚시 그물을 사용하여 붉은퉁돔 10마리를 잡은 후, 하늘안개 섬의 딕티나에게 잡은 붉은퉁돔과 드레나이 낚시 그물을 가져가야 합니다.', },
  },
  [9453] = {
    '붉은퉁돔 전달',
    {'정말 잘하셨어요. 우리 주민들이 베풀어주신 관대함에 감사할 겁니다. 관대함과 더불어 하늘 감시초소의 옥테온에게 이 붉은퉁돔을 전해 주시지 않으시겠어요?$B$B옥테온은 이 물건을 기다리고 있으니 찾기 어렵지 않을 겁니다.', },
    {'하늘 감시초소의 옥테온에게 붉은퉁돔 상자를 가져가야 합니다.', },
  },
  [9454] = {
    '달새김 사슴 사냥',
    {'자네, 이름을 날리고 있군그래. 이제 사냥을 더 해 보는 건 어떤가?$B$B드레노어로 돌아가면 우리의 가장 위대한 사냥꾼들은 탈부크라고 알려진 이 지역의 달새김 사슴과 비슷한 짐승을 사냥하곤 할 걸세. 자신을 진정한 드레나이라고 생각한다면, 숲으로 나아가 달새김 사슴류의 고기로 가방을 가득 채워 오게나.$B$B이곳 하늘 감시초소의 남쪽 숲에서는 사슴을 찾을 수 있을 걸세. 그들의 뿔을 조심하여 사냥하게나!', },
    {'하늘 감시초소에 있는 옥테온에게 달새김 사슴 안심 6개를 가져가야 합니다.', },
  },
  [9455] = {
    '희미하게 빛나는 수정',
    {'새끼 흑호의 시체에서 발견한 수정은 엑소다르에서 온 것 같습니다. 이번 일이 이 지역의 생물 일부가 평소와 다른 모습과 성향을 보이는 이유를 설명해 줄지도 모릅니다.$B$B하늘 감시초소로 수정을 가져가는 것이 좋겠습니다. 분명 총독이 이번에 발견한 물건에 대해 궁금해할 겁니다.', },
    {'하늘 감시초소의 총독 메넬라우스에게 희미하게 빛나는 수정을 가져가야 합니다.', },
  },
  [9456] = {
    '병든 새끼 흑호 해치우기',
    {'자네가 발견한 수정에 대해 어떤 변명도 하지 않겠네. 이 일은 분명 우리 드레나이의 잘못이니 말이야. 동쪽에 있는 강에서 수정 조각을 발견했다는 보고를 받았지만, 그 일의 의미를 무시하기로 결정했었지. 이제 이 섬에 가만히 앉아서 이 땅에 일어난 비극의 책임에 눈물을 흘리고만 있을 것인지, 아니면 손을 써서 오염의 확산을 막을 것인지를 선택해야 하네.$B$B괜찮다면 $G형제:자매;가 숲으로 돌아가 병든 새끼 흑호를 가능한 한 많이 처치해 주시게.', },
    {'하늘 감시초소의 총독 메넬라우스가 병든 새끼 흑호 8마리를 해치워 달라고 했습니다.', },
  },
  [9457] = {
    '예상치 못한 방문객',
    {'그러니까 독특한 마법을 사용하는 붉은머리 부족의 배후에 나이아스가 버티고 있다는 거요? 그렇다면 내 연구의 최종 목표는 나이아스가 되겠군. 어찌 됐건 우리가 발견한 토템이 이 일에 쓸모가 있을 거요.$B$B북서쪽 해안 근처에 하급 물의 정령들이 살고 있는 섬이 있소. 그곳에 트롤 놈들이 제단으로 사용하는 거친 석판이 있지. 그대가 발견한 토템을 그 제단으로 가져가시오. 나이아스를 불러내서 그의 힘을 우리가 사용하기 좋은 형태로 바꿀 수 있는지 확인해 보시오.', },
    {'나이아스의 제단 근처에서 나이아스의 선물을 사용하여 물의 정령 나이아스를 소환해야 합니다. 나이아스를 물리친 후 나이아스의 심장을 가지고 그롬골 주둔지에 있는 네메스 호크아이에게 돌아가십시오.', },
  },
