  [9469] = {
    '페더비어드의 보증',
    {'내가 폴스타트 와일드해머님을 알현하도록 승인을 받았을 때 와일드해머 드워프들 중 내 편에 서서 강력한 지지를 보내줄 드워프가 있으면 좋겠구려.$B$B페더비어드는 전설적인 그리핀 조련 솜씨와 지혜로 드워프 사이에서 크게 존경받는 인물이지. 폴스타트 와일드해머님께 영향을 줄 수 있는 인물이라면 바로 그밖에 없소.$B$B접견 승인을 기다리는 동안은 맹금의 봉우리를 벗어날 수 없다는 규정이 없었다면 내가 직접 그를 만나러 갔을 것이오. 그래서 말인데 당신이 남쪽에 있는 페더비어드의 집으로 가서 나를 도와줄 의향이 있는지 물어봐 주시겠소?', },
    {'페더비어드의 오두막으로 가서 그와 만나야 합니다. 만약 그가 집에 없다면 그가 있을 만한 장소를 알려 주는 물건을 찾아야 합니다.', },
  },
  [9470] = {
    '친선의 표시',
    {'바위투성이 땅인 동부 내륙지는 와일드해머 드워프와 그리핀의 고향이자 수많은 숲트롤 부족의 고향이라오.$B$B트롤들은 와일드해머 드워프들을 조상의 땅을 침입한 자들로 치부하고 그리핀 둥지를 공격하여 대화로 해결할 기회를 날려 버렸소. 와일드해머의 눈에 들 수 있는 방법의 하나는 이번 공격에서 마른나무껍질 부족의 지도자를 처치해 우리가 신뢰할 만하다는 걸 증명하는 것이오. 당신도 알다시피 그 지도자는 의술사 마이진이라고 하는데 샤드라알로의 서쪽에 보겐의 절벽에 있는 한 동굴에 숨어 있습니다. 그를 처치하여 친선의 표시를 할 수 있도록 도와주시오.', },
    {'맹금의 봉우리에 있는 대사 루알레스가 의술사 마이진과 그의 애완 거미 트차카즈를 처치해 달라고 했습니다.', },
  },
  [9471] = {
    '자연의 포식자',
    {'와일드해머 드워프들은 그리핀을 대할 때 동료로서, 관리인으로서, 조련사로서 행동한다오. 그리핀은 늘 적이 끊이지 않는데 특히 숲트롤 부족이 제일 큰 적이라오.$B$B그렇지만 가끔은 늑대와 같은 자연의 포식자가 위협이 될 때도 있지. 한 마리는 그리핀의 상대가 될 수 없으나 무리를 지을 경우 일반적인 사냥감으로는 양이 부족하기에 그리핀을 노리는 경우가 자주 있소이다. 늑대의 수를 줄여 놓으면 녀석들의 날카로운 이빨로부터 그리핀도 지키고 와일드해머의 환심을 살 수도 있소.', },
    {'맹금의 봉우리에 있는 대사 루알레스가 병든 은빛갈기늑대 10마리, 은빛갈기늑대 5마리를 처치해 달라고 했습니다.', },
  },
  [9472] = {
    '아렐리온의 연인',
    {'당장 처리해야 하는 중요한 일은 젖혀 놓고 남편이 여전히 애인을 만나는 데에만 시간을 할애하고 있었다니!$B$B점점 확실하게 남편의 바람이 사실로 드러나고 있군요. 비에라 선위스퍼, 그 나쁜 계집 같으니... 그 여자에게 다시는 마법학자에겐 가까이 가지 못하도록 뭔가 보여줘야 합니다.$B$B이 두루마리를 가지고 비에라를 찾아가십시오.$B$B아렐리온이 써놓길 그녀는 세나리온 드루이드들이 수확하는 특정한 포트 포도주를 거부하지 못한다고 하더군요. 이곳에서 서쪽에 있는 장가르 습지대 내 세나리온 야영지에서 포도주를 약간 구할 수 있을 겁니다.', },
    {'매의 감시탑에 있는 마법학자 카린다가 비에라 선위스퍼를 밖으로 유인한 다음, 그녀에게 카린다의 응보의 두루마리를 사용해 달라고 했습니다.', },
  },
  [9473] = {
    '양자택일',
    {'그녀의 상처가 너무 심해서 단지 연고만으로는 효과가 없소. 다른 방법이 있을 텐데...$B$B하늘안개의 숲에는 많은 비밀이 있는데 그중 하나가 희귀 식물인 하늘금어초지. 어떤 이들은 이 식물이 뭐든 치료하는 만병통치약이라고 하오.$B$B이 식물은 하늘안개의 다른 식물과는 확연하게 다른 모습을 하고 있고 푸른색 소나무 밑에서 자라기 때문에 쉽게 찾을 수 있을 거요. 서두르시오!$B$B그리고 그 지역에서 하늘금어초를 먹고 사는 덩이줄기 덩굴손을 조심하시오.', },
    {'하늘 감시초소의 대달이 하늘금어초 뿌리 5개를 가져다 달라고 했습니다.', },
  },
  [9474] = {
    '빛의 수호자의 징표',
    {'아제로스에서 지낸 짧은 시간 동안 나는 이곳의 영웅들에 대한 연구를 진행했소이다. 아제로스의 뛰어난 영웅들 중 빛의 수호자 우서 경이 최고의 위치를 차지하고 있소. 우서 경은 시대를 초월한 영웅이지만 결국 가장 아끼던 제자인 아서스에게 배신을 당했지.$B$B우서 경을 기리는 의미로 그가 잃어버린 물품을 그의 무덤에 돌려주고 싶소이다. 소문에 따르면 붉은십자군이 하스글렌의 문 근처에 있는 경비탑 꼭대기에서 우서 경의 신성한 유물 중의 하나를 지키고 있다고 하오.$B$B$C|1이여;여;, 부탁하니 가서 그 유물을 되찾아 오시오.', },
    {'빛의 수호자의 징표를 되찾은 후 서부 역병지대의 서리바람 야영지에 있는 수도자 트루엔에게 가져가야 합니다.', },
