  [9986] = {
    '아라코아 방해하기',
    {'우리 백성을 공격하면 반드시 대가를 치르게 된다는 교훈을 아라코아에게 알려줘야 하오. 이곳에서 그들을 막지 못하면 이 길을 이용할 우리 상인, 급사, 그리고 병사들의 안전을 확보할 수 없소.$B$B우리 정찰병들이 이 지역 주요 아라코아 정착지의 우두머리들을 알아냈소. 각각 장막의 레스크, 장막의 쉬에노르, 장막의 스키스에 있는 아쉬카즈, 아이트, 우르닥을 찾아서 해치우시오.', },
    {'장막의 레스크의 아쉬카즈, 장막의 쉬에노르의 아이트, 장막의 스키스의 우르닥을 처치한 후 알레리아 성채에 있는 탠더에게 가야 합니다.', },
  },
  [9987] = {
    '아라코아 방해하기',
    {'우리 백성을 공격하면 반드시 대가를 치르게 된다는 교훈을 아라코아에게 알려줘야 하오. 이곳에서 그들을 막지 못하면 이 길을 이용할 우리 상인, 급사, 그리고 병사들의 안전을 확보할 수 없소.$B$B우리 정찰병들이 이 지역 주요 아라코아 정착지의 우두머리들을 알아냈소. 각각 장막의 레스크, 장막의 쉬에노르, 장막의 스키스에 있는 아쉬카즈, 아이트, 우르닥을 찾아서 해치우시오.', },
    {'장막의 레스크의 아쉬카즈, 장막의 쉬에노르의 아이트, 장막의 스키스의 우르닥을 해치운 후 돌망치 요새에 있는 로카그에게 가야 합니다.', },
  },
  [9988] = {
