  [10078] = {
    '톱날 투척기 파괴',
    {'재활용 작전에 성공했으니 2단계 작전을 시작할 때가 되었군.$B$B영광의 길 건너편에는 호드의 톱날 투척기가 여전히 우리를 겨냥하고 있다네. 투척기가 구식 무기라고 무시하면 안 되네. 톱날 투척기는 여전히 매우 위협적인 무기니 말이야. 재생 가능한 자원을 최대한 회수하고 싶네만 호드가 자기들의 무기를 파괴할 리가 있겠나?$B$B이 횃불을 가지고 가서 남아 있는 공성 병기를 태워 버리게. 명예의 요새의 안전을 담보로 모험할 수는 없으니 말일세.', },
    {'영광의 길이 내려다보이는 곳에 있는 호드 톱날 투척기를 불타오르는 횃불을 사용하여 태운 후, 명예의 요새에 있는 덤프리에게 횃불을 가져가야 합니다.', },
  },
  [10079] = {
    '광산의 소동',
    {'광산이 간아그 무리에게 점령되었네!$B$B간아그 녀석들이 터널에서 일꾼들을 모두 쫓아냈을 뿐만 아니라, 그곳에서 밤낮으로 작업을 하고 있지뭔가! 더 안 좋은 소식은 다나스 님이 돌아오셨다는 것일세! 그분께선 내가 이 일을 빨리 처리하지 못하면 더는 맥주를 배급하지 않겠다고 하셨을 정도라네!$B$B광부들은 아무도 광산에 들어가지 않으려 하고 멍청한 경비병들은 술이나 퍼마시느라 정신이 없네.$B$B제발 부탁이니 여기 일을 좀 도와주게. 다나스 님께서 내 지위를 뺏어갈 수는 있어도 맥주는 아니 되네!', },
    {'간아그 공병 12명을 처치한 후 현장감독 비굼스에게 가야 합니다.', },
  },
  [10081] = {
    '대모 카슈르와 만남',
    nil,
    {'대모 게야가 대모 카슈르와 대화해 달라고 했습니다.', },
  },
  [10082] = {
    '불안한 영혼',
    {'이 주변에 걸어다니는 이들이 보이나? 선조의 터는 원래 조용하고 평온한 곳이었지.$B$B근래 와서 선조의 영혼이 깨어났는데 매우 불안해하고 있네. 무언가 영혼들을 매우 동요시키고 있어. 우린 그게 뭔지 알아내야 해. 하지만, 먼저 눈앞에 닥친 위기부터 해결해야지.$B$B이 주변에서 자네 눈에 보이는 유령들은 사실 선조가 아니라 그들의 불안한 영혼이 또 다른 모습으로 표출된 것일 뿐이지. 근심어린 오크 영혼을 해치워야 해. 일이 끝나면 돌아오게.', },
    nil,
  },
  [10085] = {
    '선조 방문',
    {'$N, 자네가 선조의 마을들을 방문해줘야겠어. 마을을 다니면서 선조들이 남아있는지 확인해 주게.$B$B북쪽에 있는 태양여울 주둔지, 북쪽으로 멀리 뒤에 산맥을 기대고 있는 웃는 해골 폐허, 북동쪽의 가라다르, 마지막으로 동쪽으로 나그란드와 테로카르 숲의 경계선에 있는 피눈물 폐허로 가게.$B$B고향에서 선조들을 찾아보고 돌아오게.', },
    {'나그란드의 선조의 터에 있는 대모 카슈르가 태양여울 주둔지, 웃는 해골 폐허, 가라다르, 피눈물 폐허를 방문하라고 했습니다.', },
  },
  [10086] = {
    '호드를 위해 일한다!',
    {'믿어지세요? 이곳 오크들이 손상된 요새들을 강화하고 폭발물을 만드는 일을 도와달라고 저를 고용했습니다. 정말 꿈만 같은 일이지요! 재료가 많이 부족해서 안타깝긴 하지만, 설마 저 같은 영리한 고블린이 앞으로 어떻게 해야 하는지 모르겠습니까?!$B$B도와주세요! 영광의 길을 따라 부서진 전차 근처에 널려 있는 재활용 목재와 금속이 필요합니다. 가서 물건을 구해다 주세요. 참, 살갗이 붉은 오크들을 조심하시고요!$B$B일을 마치고 돌아오시면 큰 보상을 드리겠습니다!', },
    {'스랄마에 있는 메그제그 너클버스트에게 재활용 목재와 재활용 금속을 8개씩 가져가야 합니다.', },
  },
  [10087] = {
    '불태우리... 호드를 위하여!',
    {'저는 정말 복 많은 고블린이랍니다. 아주 중요한 임무에 직접 쓰일 폭발물과 살인 무기를 제 손으로 만들어 냈죠!$B$B어떤 임무냐면... 스랄마 사령부에서 직접 받은 명령인데요. 영광의 길에 있는 얼라이언스 대포들을 태우는 것이랍니다. 호드 요새를 겨눈 그 무시무시한 대포들 말이죠. 이 횃불을 가지고 대포가 있는 곳으로 가십시오. 횃불에 불을 붙인 다음 불꽃이 타오르는 장관을 그냥 구경만 하시면 됩니다!', },
    {'불타오르는 횃불을 사용하여 동쪽 얼라이언스 대포와 서쪽 얼라이언스 대포를 태운 다음 스랄마에 있는 메그제그 너클버스트에게 가야 합니다.', },
  },
  [10088] = {
    '광산의 소동',
    nil,
    nil,
  },
  [10091] = {
    '영혼의 장치',
    nil,
    nil,
  },
  [10093] = {
    '텔하마트 사원',
    {'이 세계에 우리 종족의 흔적이 존재하고 있는 곳이 영광의 길만이 아니라는 것이 다행스럽다오. 서쪽으로 가면 비록 일부는 점령당했으나 우리의 성소 몇 군데가 남아 있지.$B$B벨렌 님께서 아제로스로의 원정을 준비하시는 동안, 우리는 성소의 모습을 재건하고자 하오.$B$B지옥불 반도의 서쪽에 가면 텔하마트 사원에서 현자 아만이 이끄는 소규모의 드레나이군을 만날 수 있을 것이오. 그가 당신을 만나고 싶어할 테니 그리로 가 보시오.', },
    {'지옥불 반도 서부의 텔하마트 사원에 있는 현자 아만과 대화해야 합니다.', },
  },
  [10094] = {
    '피의 고서',
    {'한때 번영을 꽃피웠던 이 묘지를 파괴하고 해골 무덤을 형성한 사건인 어둠의 의회 소환이 있은지도 2년이 지났군.$B$B저안에서 저들은 믿기지 않을 만큼 강력한 군대를 앞세워 전쟁을 벌이고 있네.$B$B어둠의 미궁으로 가서 그들이 누구와 싸우고 있고 그 싸움이 우리에게 위협이 되지는 않을지 알아보게나. 우리 첩보원에 의하면 놈들이 부정한 소환에 대한 금단의 책인 피의 고서를 사용하고 있다고 하는군.$B$B그 책에서 필요한 정보를 찾아보게.', },
    {'아킨둔의 어둠의 미궁에 있는 피의 고서를 읽어야 합니다.', },
  },
  [10095] = {
    '미궁의 심장부로',
    nil,
    nil,
  },
  [10096] = {
    '스포어로크 구출',
    {'이케옌이 말해준 저 아래 동굴에서 일어난 일은 정말 끔찍했어요!$B$B우리가 그 불쌍한 스포어로크들을 위해 뭔가 해야 합니다. 이케옌이 말하길 그들은 버섯 동굴에서 살고 있었는데 얼마 전 습지대 짐승들의 침략을 받았다고 하더군요.$B$B그곳으로 가서 습지대의 짐승들을 처치할 수 있는지 알아봐 주지 않겠어요? 녀석들을 찾으려면 남서쪽으로 가면 됩니다.', },
    {'갈퀴손 습지늪괴물, 수렁늪괴물을 각각 10마리씩 처치한 후 장가르 습지대의 세나리온 야영지에 있는 라우란나 사르웰에게 가야 합니다.', },
  },
  [10097] = {
    '형제 간의 싸움',
    {'아킨둔이 폭발했을 때 세데크는 유난을 떨면서 스케티스를 떠났지요. 물론 그건 우리의 주인께서 도착하신다는 것을 알리는 전조였습니다.$B$B그들의 지휘관 중 한 명이었던 내 형제 시스는 우리가 신전 폐허로 들어가서 신과 대면해야 한다고 했었습니다.$B$B폐허에 자리를 잡고 그곳을 세데크 전당이라고 부르면서 나는 내 형제와 그의 동맹인 이키스가 의심스러웠습니다.$B$B시간이 흐른 뒤 시스는 나를 세데크 전당에서 쫓아내면서 여동생인 라카도 데려가지 못하게 했습니다. 내 여동생을 구해 주십시오.', },
    {'세데크 전당에 있는 흑마술사 시스를 처치하고 붙잡혀 있는 라카를 구한 후, 세데크 전당 밖에 있는 이스파르에게 가야 합니다.', },
  },
  [10098] = {
    '테로크의 유산',
    {'우리 종족은 테로크라는 고대의 영웅을 섬깁니다. 어린 아라코아들은 그의 위대한 업적과 아름다운 깃털에 대한 얘기를 듣고 즐거워하지요.$B$B그분은 수백 년 동안 우리 종족과 함께 지내다가 어느 날 복면, 창, 일지만을 남긴 채 홀연히 사라지셨습니다.$B$B그가 남긴 물건들을 소중하게 보관해 온 아라코아들은 세데크가 떠나면서 스케티스에게서 그 유산들을 빼앗아 가자 슬픔에 잠겼습니다.$B$B그 전당 안 그들의 위치에 대해서 제가 아는 약간의 지식을 기록해 놓았습니다. 그들을 슬픔에서 벗어나게 해 주십시오.', },
    {'세데크 전당 밖에 있는 이스파르에게 테로크의 가면, 테로크의 깃창, 테로크의 전설이라는 책을 가져가야 합니다.', },
  },
  [10099] = {
    '배후의 지휘관',
    {'그 지긋지긋한 간아그 녀석들이 계속해서 광산을 침입하고 있네! 한 녀석을 처치하면 두세 녀석이 더 나타나는 것 같으니 이거 원!$B$B저 아래 가장 깊은 곳에 있는 누군가가 녀석들에게 지시를 내리는 것 같군. 지난번에 보니 자네는 녀석들을 어떻게 처리해야 하는지 잘 아는 것 같던데 말이야. 게다가 저 광산에 들어갈 만큼 용감한 자는 자네밖에 없다는 것을 신께서도 알고 계시지 않는가? 이번 일의 배후에 누가 있는지 알아내서 대가를 치르게 해 주게!', },
    {'명예의 요새 지하의 광산에 있는 즈크랄을 찾아서 처치한 후 현장감독 비굼스에게 보고해야 합니다.', },
  },
  [10100] = {
    '배후의 지휘관',
    nil,
    nil,
  },
  [10101] = {
    '영혼들이 말할 때',
    {'다이아몬드 산 오슈군은 한때 우리 종족에게 신성한 곳이었지. 매년 많은 부족이 그곳에 모여 봄의 축제를 즐기기도 했어. 주술사들이 위대한 선조와 교감하던 장소 역시 그곳일세. 악마들의 침략을 받기 전까지 그곳은 문화의 중심지였네. 그 부족들이 호드가 되자 오슈군의 영혼들은 입을 다물었네. 선조들이 우리에게 등을 돌린 거지.$B$B그런데 지금 뭔가가 선조의 안식을 방해하고 있네. 오슈군으로 가서 무엇이 영혼들을 괴롭히고 있는지 알아봐 주게나.', },
    {'나그란드의 선조의 터에 있는 대모 카슈르가 환영을 찾아달라고 했습니다. 오슈군에 있는 크우레를 찾아야 합니다.', },
  },
  [10102] = {
    '밝혀진 비밀',
    {'샤트라스에 있는 아달과 대화해야 합니다.', },
    {'샤트라스에 있는 아달과 대화해야 합니다.', },
  },
  [10103] = {
    '주라이에게 보고',
    {'로무스 야전사령관과 그의 군대가 이곳에 도착하면서 얼라이언스와 드레나이 동맹들이 대담한 공격을 감행하고 있소. 그들은 이곳에서부터 샤트라스에 있는 오래된 드레나이 폐허를 모두 군사기지로 수리하고 있소이다.$B$B이에 질세라 트롤들도 장가르 습지대의 동쪽 지역, 즉 죽음의 늪 바로 남쪽에 새로운 전초기지를 세웠는데 그곳을 늪쥐 감시초소라고 부른다오. 그들이 그런 이름도 괜찮다고 생각할 만큼 유머 감각이 있다니 다행이군. 나라면 안 살고 말 것 같거든.$B$B그 전초기지의 사령관이 내게 지원 인력을 보내달라고 요청해 왔으니 그리로 가서 무슨 일인지 알아보시오.', },
    {'장가르 습지대의 늪쥐 감시초소에 있는 주라이에게 보고해야 합니다.', },
  },
  [10104] = {
    '투렘의 운명',
    {'투렘에서 들려 오는 소문이 심상치 않소. 마을의 폐허에 실성한 뒤틀린 드레나이와 잃어버린 드레나이들이 들끓는 데다가, 기회를 엿보던 유물 사냥꾼들이 드레나이 유물을 훔치고 있다는 말도 간간이 들리고 있소.$B$B어떤 게 진실이고 어떤 게 거짓인지 분간하기 어렵지만, 우리에겐 폐허를 최대한 안전하게 지킬 의무가 있소. 테로카르 숲의 알레리아 성채에서 마법 유물을 연구하고 있는 안다를을 찾아가 보시오. 그는 다른 누구보다도 그곳에서 일어나는 일에 대해 잘 알고 있을 것이오.', },
    {'테로카르 숲의 알레리아 성채에 있는 안다를과 대화해야 합니다.', },
  },
  [10105] = {
    '라코리아에게 전할 소식',
    nil,
    {'테로카르 숲의 돌망치 요새에 있는 라코리아와 대화해야 합니다.', },
  },
  [10106] = {
    '지옥불 반도의 요새',
    {'우리 군대는 지옥불 성채에 있는 자들과 끊임없이 전투를 벌이고 있습니다. 지금까지는 외부의 도움 없이 어느 정도 버텨 왔으나, 아직 제대로 된 공격 한번 못하고 있는 실정입니다. 우리 군대가 공격다운 공격을 하려면 전방에 병력을 집결할 장소가 필요합니다.$B$B우리 정찰병들이 지옥불 성채 근처에 있는 요새 세 군데를 탐색해 두었으니, 당신이 그곳의 안전 확보에 힘써 주시기 바랍니다. 각 요새를 점령한 후 내게 돌아와 보고해 주십시오.$B$B분명히 같은 요새를 노리는 호드와 맞서 싸우게 될 것이니 조심하시기 바랍니다. 당신의 찬란한 빛의 길이 열리길 빌겠습니다.', },
    {'전망대, 경기장, 무너진 언덕을 점령한 후 지옥불 반도의 명예의 요새에 있는 준위 트레이시 프라우드웰에게 가야 합니다.', },
  },
  [10107] = {
    '외교적 수단',
    {'킬라스가 돌주먹 일족의 지도자에 대한 새로운 소식을 보내왔습니다. 그는 오크였습니다! 우리 아버지 세대에 어둠의 문으로 들어갔다가 나중에 고향으로 돌아간 고대의 오크입니다. 당신이 이 오크를 찾아가서 돌주먹 오우거들을 데리고 돌아가라고 설득해 주셨으면 좋겠습니다. 그는 남동쪽의 테로카르 숲 경계선 근처에 있는 불타는 칼날단 폐허 위층에 있습니다. 행운을 빕니다, $N!$B$B조심하십시오. 그는 굉장한 실력을 갖춘 검귀입니다...', },
    {'나그란드의 가라다르에 있는 요린 데드아이가 불타는 칼날단 폐허에 있는 칼날의 란트레서를 찾아서 대화해 달라고 했습니다.', },
  },
  [10108] = {
    '외교적 수단',
    {'여사냥꾼의 정보원이 돌주먹 일족의 지도자에 대한 새로운 소식을 보냈는데 그가 오크라는군요! 우리 아버지 세대에 어둠의 문으로 들어갔다가 나중에 고향으로 돌아간 고대의 오크라 합니다. 당신이 그 오크를 찾아가서 돌주먹 오우거들을 데리고 돌아가라고 설득해 줬으면 합니다. 그는 동쪽의 불타는 칼날단 폐허 위층에 있습니다.$B$B행운을 빕니다! 그리고 그는 검귀이니 조심하십시오.', },
    {'나그란드의 텔라아르에 있는 파괴자 모모르가 불타는 칼날단 폐허에 있는 칼날의 란트레서를 찾아 대화해 달라고 했습니다.', },
  },
  [10109] = {
    '누가 뭐래도 내 거야!',
    {'보이나? 가즈로의 끔찍한 입 냄새를 걸고 맹세하는데 무슨 일이 있어도 그 알을 차지하고 말 거야! 당신, 제법 쓸모가 있을 것 같이 생겼군.$B$B한 가지 제안을 하겠어. 내 방방이 4000을 작동시킬 몇 가지 물건을 모아주면 방방이 4000의 시동 열쇠를 주겠어! 그런데 방방이 4000으로 뭘 할 수 있느냐고? 할 수 없는 게 뭐냐고 물어보는 게 나을걸...$B$B난 나그란드의 바람의 정령으로부터 추출한 바람의 정령 기체가 필요해. 바람의 정령은 어디서나 찾을 수 있으니 어디로 가야 하느냐는 질문은 삼가도록!', },
    {'나그란드 남부의 부족 초소 건너편에 있는 와자트가 바람의 정령 기체 3개를 가져다 달라고 했습니다.', },
  },
  [10110] = {
    '지옥불 반도의 요새',
    {'우리가 태어난 이 땅에서 지옥불 성채에 가득한 타락한 오크들을 몰아내야 하네! 그들을 공격하려면 우선 지옥불 성채 근처에 있는 전방 집결지의 안전을 확보해야 하네.$B$B우리 길잡이들이 요새 내에 집결지에 딱 맞는 세 곳을 탐색해 놓았네만, 얼라이언스에서도 우리와 같은 생각을 하고 있다네. 그들은 지금 이 순간에도 그곳을 차지하기 위해 군대를 보내고 있네!$B$B자네가 얼라이언스의 의도를 저지하고 세 곳의 요새를 점령해 주게. 각 요새 점령에 도움을 준 후 내게 돌아오도록 하게.$B$B호드를 위하여!', },
    {'전망대, 경기장, 무너진 언덕을 점령한 후 스랄마에 있는 전투장교 블랙아이에게 가야 합니다.', },
  },
  [10111] = {
    '알을 가져와!',
    {'이왕 시작한 거 그 알을 구해다 줘! 방방이를 써서 둥지 한가운데에 착륙하면 되는 걸세. 하지만, 조심하게! 섣불리 뛰어내렸다가는 부상을 당하거나 심각한 후유증을 겪게 될지도 모르니까 말이야.', },
    {'나그란드의 와자트가 방방이 4000을 사용하여 근처 나무에 있는 둥지에 접근해서 수수께끼의 알을 훔쳐달라고 했습니다.', },
  },
  [10112] = {
    '사적인 부탁',
    {'네? 상자라고 했습니까? 아니면 상자들이었나요? 그 상자를 뭐로 만들었나요?$B$B그리고 그 상인의 이름이 뭐였죠? 엠푸르? 그가 뒤틀린 드레나이의 일원이란 것이 분명합니까? 저는 그 종족과는 거래를 별로 안 하는데...$B$B제 부탁을 들어주신다면 기억이 날 것도 같습니다.$B$B얼마 전 태초의 마법사들이자 아라코아인 스키시안이 운반 중이던 제 마법 물품을 훔쳐 갔습니다. 도둑맞은 물품을 모두 찾아주십시오!$B$B샤트라스의 남쪽으로 가면 장막의 스키스를 찾을 수 있을 겁니다.', },
    {'도둑맞은 라스라이의 물품 5개를 모아서 샤트라스의 고난의 거리에 있는 세상의 끝 선술집 근처의 바람의 무역상 라스라이에게 가져가야 합니다.', },
  },
  [10113] = {
    '네싱워리 탐험대',
    nil,
    nil,
  },
  [10114] = {
    '네싱워리 탐험대',
    nil,
    {'오로른 파스트라이더가 나그란드의 네싱워리 탐험대에 있는 샤도 \'피츠\' 파스트라이더와 대화해 달라고 했습니다.', },
  },
  [10115] = {
    '비수늪 일족의 일탈 행동',
    {'쿠레나이와 드레나이의 의견이 항상 일치하는 것은 아니오. 특히 뒤틀린 드레나이에 대한 문제가 그렇소이다. 우리에게 도움을 구하는 잃어버린 드레나이를 도와주는 것은 아무런 문제가 없지만 굳이 찾아서 돕지는 않을 것이오. 일부 드레나이는 그들을 모두 \'구원\'하려고 하니 말이야.$B$B하지만 때때로 비수늪 일족같이 잃어버린 드레나이가 선을 넘어버릴 때도 있다오. 서쪽에 있는 그들의 마을에 가면 우리 사이에서는 인정받을 수 없는 독, 속임수, 기술을 연구하고 있소.$B$B사악한 비수늪 일족이 그들의 영향력을 확대하기 전에 이 일을 마무리해야 하오.', },
    {'비수늪 암살자 3명, 비수늪 땅꾼 15명을 처치해야 합니다.', },
  },
  [10116] = {
    '현상 수배: 족장 뭄마키',
    nil,
    {'오레보르 피난처에 있는 이쿠티에게 족장 뭄마키의 토템을 가져가야 합니다.', },
  },
  [10117] = {
    '현상 수배: 족장 뭄마키',
    nil,
    {'어둠사냥꾼 덴자이에게 족장 뭄마키의 토템을 가져가야 합니다.', },
  },
  [10118] = {
    '비수늪 일족에게 보내는 메시지',
    {'장가르 습지대의 모든 잃어버린 드레나이 중에는 비수늪 일족이 가장 교활하고 공격적이라네. 놈들은 매우 조직적이며 습지대의 다른 드레나이 부족을 지배하려는 야욕을 노골적으로 드러내고 있네.$B$B이미 수많은 우리 정찰병과 전령들이 놈들의 공격을 받아 희생되었네. 더는 부하들을 잃을 수는 없어.$B$B조속히 조치를 취하지 않으면 놈들이 길을 완전히 점령할 것이고 자브라진은 포위될 거야.', },
    {'비수늪 암살자 3명, 비수늪 땅꾼을 15명 해치운 다음 자브라진의 어둠사냥꾼 덴자이에게 가야 합니다.', },
  },
  [10119] = {
    '어둠의 문을 지나서',
    {'20년 동안 어둠의 문은 잊혀진 후로 오랜 시간을 찬양하는 조용한 유적으로만 남아 있었습니다.$B$B<주위를 둘러보는 렐손>$B$B이런 날이 오리라고는 생각지도 못했으나 지금 우리는 저 문의 한없이 깊은 심연을 들여다보고 있습니다.$B$B이 문의 반대편에서 지원군 없이는 결코 승리할 수 없는 잔혹한 전쟁이 펼쳐지고 있습니다. $C|1이여;여;, 어둠의 문으로 들어가서 우리 군대를 도와주십시오.$B$B즉시 지휘관 두론에게 보고해 주시기 바랍니다. 그는 아웃랜드에서 당신을 기다리고 있습니다.', },
    nil,
  },
  [10120] = {
    '아웃랜드 도착',
    {'내 군대는 얼라이언스의 도움을 받아 여기에 진을 칠 것이니 자네가 스랄마에 계시는 사령관 크라콜크 님께 내 보고서를 전달해 주게. 그분의 도움을 받으면 이 악마들이 맨 처음 나온 심연으로 쫓아낼 수 있을 것이네! 우리 뒤쪽의 단상에 서 있는 블라가 프레이페더와 얘기해 보게. 자네가 좀 더 빨리 스랄마로 갈 수 있도록 도와줄 걸세.', },
    {'지옥불 반도의 어둠의 문에 있는 부사령관 오리온이 블라가 프레이페더에게 오리온의 보고서를 가져가 달라고 했습니다.', },
  },
  [10121] = {
    '불타는 군단 박멸',
    {'지금 이 땅은 새로운 적들로 가득 차 있지만 우리 원정대에 가장 큰 위협은 여전히 불타는 군단이라네. 내가 가장 신임하는 전사 중 한 명인 하사관 쉐터스컬은 불타는 군단의 전술에 대해 연구해 왔다네. 이 땅에 있는 악마들의 진짜 목표가 무엇인지 알아내려고 궁리하면서 말이지. 남부 성벽 바로 바깥에 있는 보급 짐마차에 서 있는 그를 찾게.$B$B그리고 $N, 불타는 군단을 조심하게. 놈들의 사악함을 결코 만만히 봐서는 안 되네.', },
    {'스랄마에서 동쪽으로 바로 바깥에 있는 보급 짐마차에 서 있는 하사관 쉐터스컬과 대화해야 합니다.', },
  },
  [10123] = {
    '지옥불꽃 협곡',
    {'아웃랜드의 불타는 군단은 파멸의 군주 카자크가 지휘하고 있네. 아직 카자크에게는 접근할 수 없지만 지상에서 불타는 군단의 부대를 지휘하고 있는 그의 부관들에게는 접근할 수 있고, 또 반드시 그렇게 해야 하네.$B$B그들은 여기서 동쪽에 있는 지옥불꽃 협곡에 공포의 군주 한 명을 배치했다네. 우리 머리 위로 쏟아지고 있는 이 지독한 불지옥 세례도 바로 그들 때문이지. 공포의 군주들과 그 부하들을 해치운다면 불타는 군단의 계획에 큰 타격을 입힐 수 있어.', },
    {'지옥불 반도의 스랄마 동쪽에 위치한 보급 짐마차에 서 있는 하사관 쉐터스컬이 공포의 소환사 1명과 임프 화염술사 4마리, 지옥불정령 전쟁인도자 6마리를 해치워 달라고 했습니다.', },
  },
  [10124] = {
    '전진기지: 지옥절단기 함락지',
    nil,
    {'지옥절단기 함락지에 있는 전방사령관 토아크에게 보고해야 합니다.', },
  },
  [10126] = {
    '전투대장 네크로그의 명령서',
    nil,
    nil,
  },
  [10129] = {
    '임무: 멀케스 관문과 샤드라즈 관문',
    {'불타는 군단이 지원 병력을 수송하는 데 주로 사용하는 2개의 순간이동기를 알아냈습니다. 바로 멀케스 관문과 샤드라즈 관문이에요.$B$B영광의 길의 동쪽으로 올라가면 길 양쪽에서 보일 것입니다. 폭탄을 투하해서 그 순간이동기를 모두 없애 주십시오!$B$B자, 여기 당신이 사용할 폭탄을 받으십시오. 52번 구역의 한 고블린이 특수 제작한 물건입니다. 이제 편대사령관 브락크에게 가면 당신을 와이번에 태워줄 것입니다.', },
    {'편대사령관 브락크와 대화하여 멀케스 관문과 샤드라즈 관문에 폭탄을 투하한 다음 지옥절단기 함락지에 있는 전방사령관 토아크에게 가야 합니다.', },
  },
  [10130] = {
    '서쪽 측면 지역',
    nil,
    nil,
  },
  [10132] = {
    '거대괴수의 위협',
    nil,
    {'분노한 거대괴수 5마리를 처치한 다음 지옥불 반도의 세나리온 초소에 있는 톨라시온에게 가야 합니다.', },
  },
  [10133] = {
    nil,
    {'당신의 수작업으로 이미 군단에게 큰 불편 함을 안겨줍니다, $N. 내 스카우터는 Legion이 택배를 통해 통신을 보내는 것에 의존했다는 사실을 알 렸습니다. 우리는 성공적으로 주인으로부터 군대를 줄였습니다!$B$B 내가 다음에 물어 보는 것은 평범한 병사에게 물어 보는 것 이상입니다.$B$BLegion 택배는 Aggonar 수영장에서 Forge Base : Mageddon , 여기 북쪽. Mageddon에서 택배를 가로 채서 처분하고 Legion Missive와 함께 나에게 돌아와야합니다.', },
    nil,
  },
  [10134] = {
    '진홍색 수정의 실마리',
    {'이번 공격으로 거대괴수가 쓰러질 때 진홍색 수정 조각을 지니고 있다는 사실을 눈치챘습니다.$B$B이런 거인의 손에 있어서 그런지 작고 잘 보이지도 않는 수정이지만 상당히 크고 무거운 조각입니다. 게다가 하늘 마루의 한가운데에 있는 거대한 수정의 조각처럼 보이기도 합니다.$B$B이 사실은 분명히 이곳에서 일어나고 있는 이상한 일을 밝혀 줄 단서이자, 세나리온 원정대의 드루이드들도 보고 싶어할 거라는 확신이 듭니다. 수정을 소지품에 넣고 남쪽의 세나리온 초소로 돌아가는 발길을 서두릅니다.', },
    {'지옥불 반도의 세나리온 초소에 있는 톨라시온에게 진홍색 수정 조각을 전해야 합니다.', },
  },
  [10136] = {
    '가차없는 처단',
    {'전에 자네가 찾아낸 군단 서한을 마법학자 블러드호크가 마침내 해독했네. 그 문서는 대군주 카자크의 부관 중 하나인 학살자 아라지우스가 보낸 공격 계획서로 밝혀졌어. 아라지우스는 지옥불 반도에서 불타는 군단 지상군에게 명령을 내리는 게 분명해. 이제 놈이 어떤 명령도 내리지 못하게 될 시간이 왔어, $N! 무조건 놈을 끝장내 버리게.$B$B서쪽에 있는 아고나르의 웅덩이로 가서 \'군단장\'이라 불리는 그놈을 처치하게. 오늘 호드는 반드시 지옥불 반도를 되찾을 거야. 불타는 군단에게 응징을 내리게나.', },
    {'학살자 아라지우스를 처치하고 스랄마에 있는 나즈그렐에게 가야 합니다.', },
  },
  [10140] = {
    '명예의 요새로 가는 길',
    {'준비를 마치시면 명예의 요새로 곧장 모셔다 드릴 수 있게 준비하겠습니다.$B$B도착하시면 치안대장이신 이실도르 님과 대화한 다음 이 두론의 보고서를 전해 주십시오. 그분께서 고향에서 또 신병이 왔다고 좋아할 겁니다. 또 그 보고서에 있는 정보도 물론 중요한 내용이니까요!', },
    {'명예의 요새로 날아가서 치안대장 이실도르에게 두론의 보고서를 전달해야 합니다.', },
  },
  [10141] = {
    '다시 나타난 불타는 군단',
    {'불타는 군단은 아제로스를 침략하면서 동시에 이곳 아웃랜드에도 공격을 가했습니다. 우리는 수년간 녀석들의 동태를 살펴 왔으나 최근의 활동은 정말이지 지금까지와는 전혀 다른 양상입니다.$B$B동쪽 보급 짐마차 근처에 있는 하사관 알투무스를 찾아가 보십시오. 그녀는 그 침략군들을 감시해 왔기 때문에 녀석들의 목표에 대해 잘 파악하고 있을 것입니다.$B$B$C|1이여;여;, 서두르십시오. 불타는 군단은 결코 만만하게 생각할 녀석들이 아니니 부디 몸조심하십시오.', },
    {'명예의 요새 바로 바깥 동쪽의 동쪽 보급 짐마차에 있는 하사관 알투무스와 대화해야 합니다.', },
  },
  [10142] = {
    '고통의 길',
    {'현재 아웃랜드에서 불타는 군단의 상황을 먼저 말씀드리면 파멸의 군주 카자크가 아웃랜드의 불타는 군단을 지휘하고 있습니다. 지금 상태로는 카자크에게 접근할 수 없지만, 지상에서 불타는 군단의 군대를 지휘하고 있는 그의 부관들에게는 접근할 수 있습니다.$B$B그들은 여기서 동쪽에 있는 고통의 길에 공포의 군주들을 배치했습니다. 우리 머리 위로 쏟아지고 있는 이 지독한 지옥불은 바로 그 공포의 군주들 때문이랍니다. 공포의 군주들과 그의 부하들을 처치한다면 불타는 군단의 계획에 큰 타격을 줄 수 있을 겁니다.', },
    {'지옥불 반도의 명예의 요새 동쪽에 위치한 동쪽 보급 짐마차에 있는 하사관 알투무스가 임프 화염술사 4마리, 지옥불정령 전쟁인도자 6마리, 공포의 소환사 1마리를 처치해 달라고 했습니다.', },
  },
  [10143] = {
    '원정대 거점',
    {'$C|1이여;여;, 당신의 전투 실력이 고위 사령관님들의 눈에 띄었습니다. 그분들은 당신이 전방으로 가서 로하무스 야전사령관의 부하들과 합류하기를 원하고 있답니다. 그들은 급진적이며, 불타는 군단을 완전히 뿌리 뽑기 위해 매진하고 있으니 아마 당신에게 딱 맞을 겁니다...$B$B이제 파괴된 지옥절단기를 만날 때까지 뼈의 골짜기를 지나 동쪽으로 약 400걸음을 가십시오. 예전 불타는 군단의 기지가 있던 곳에서 원정대 거점을 찾을 수 있을 겁니다. 그곳에 도착하면 킹스톤 전방사령관에게 보고하시면 됩니다.$B$B이제 출발하십시오!', },
    {'원정대 거점에 있는 전방사령관 킹스톤에게 보고해야 합니다.', },
  },
  [10144] = {
    '지원군 차단',
    {'북동쪽으로 가면 군단 전초지가 보일 겁니다. 그곳에서 우리가 파괴할 것은 아웃랜드 전역에서 지원군을 모으는 데 사용되는 불타는 군단의 차원문입니다!$B$B자, 이 뇌관을 받으십시오. 이 뇌관은 52번 구역에서 개발된 것으로 악마의 자체 마력을 폭파 장치로 사용합니다! 그림 차원문과 칼레즈 차원문 주위에 있는 불타는 군단을 처치하여 악마의 마법석을 모으십시오. 그리고 이 뇌관을 사용하여 차원문 내에서 마법석을 폭발시키고선 그저 지켜보기만 하면 됩니다!$B$B자, 이제 가십시오. 그리고 임무를 완수하면 다시 만납시다.', },
    {'그림 차원문과 칼레즈 차원문 주위의 적들을 처치하여 악마의 마법석을 모으십시오.$B$B그림 차원문과 칼레즈 차원문 내에서 킹스톤의 뇌관을 사용하십시오.$B$B임무를 완수하면 나머지 뇌관을 가지고 전방사령관 킹스톤에게 가야 합니다.', },
  },
  [10146] = {
    '임무: 멀케스 관문과 샤드라즈',
    {'어둠의 문에서 곧장 서쪽으로 가면 멀케스 관문과 샤드라즈 관문이라는 거대한 순간이동기가 있습니다.$B$B동쪽으로 영광의 길을 올려다보면 길의 양쪽에 순간이동기가 보일 테니, 그걸 모두 폭파시켜 산산조각내 주십시오!$B$B여기 52번 구역에 있는 한 고블린이 특별히 설계한 폭탄이 있으니 받으십시오. 이제 폭탄을 챙겼으니 다비리 편대사령관과 대화하면 그가 그리핀에 태워서 목적지까지 호송하여 임무를 수행하게 도와 줄 겁니다.', },
    {'편대사령관 다비리와 대화한 후 멀케스 관문과 샤드라즈 관문을 폭파시킨 다음 원정대 거점에 있는 전방사령관 킹스톤에게 가야 합니다.', },
  },
