  [10744] = {
    '승리의 소식',
    {'지금 즉시 쿠르드란 와일드해머님께 가셔서 우리 작전의 성공적인 완료 소식을 알리셔야 해요! 누아인 편대사령관님 휘하에 있는 병사들은 와일드해머 성채의 안전을 지키기 위해서 끊임없이 싸워 왔어요. 당신이 돌아가면 분명 병사들의 사기가 드높아질 거랍니다. 불타는 군단의 위협에서 벗어났으니 이제 쿠르드란님께서는 마침내 일리단의 군대를 처리하는 데 전력투구하실 수 있을 겁니다. 아직도 이곳은 온통 위협투성이지만 오늘 우리는 대단한 승리를 거둔 것이에요!', },
    {'와일드해머 성채로 돌아가서 쿠르드란 와일드해머와 대화해야 합니다.', },
  },
  [10745] = {
    '승리의 소식',
    {'지금 즉시 대군주 오르바로크에게 가서 우리의 성공 소식을 알리게! 우리가 불타는 군단을 섬멸하여 어둠달 골짜기에서 자취를 감추게 했으니 이제 대군주는 다른 적에게 집중할 수 있을 거야.$B$B코르크론 방어군에서는 쉽게 인정하려 들지 않겠지만 그들도 이제 우리가 두 전선에서 뛰지 않아도 된다는 사실에 기뻐할 걸세.', },
    {'어둠달 마을로 돌아가서 대군주 오르바로크와 대화해야 합니다.', },
  },
  [10747] = {
    '고룡교단 검은새끼용',
    {'저는 그들이 검은날개 소굴에서 기르는 새끼용들을 조사해야 해요.$B$B그들의 동굴 같은 기지 안에 가면 새끼용들이 많이 날아다니고 있을 거고 알도 많이 있을 겁니다. 그 알들은 가까이 다가가면 부화하게 되지요.$B$B그물을 가져가셔서 그곳에서 살아 있는 고룡교단 검은새끼용들을 한 무리 정도 잡아다 주세요.$B$B검은날개 소굴에 가시려면 고룡해골 다리를 지나 서쪽으로 가서 고룡해골 굴을 통과하면 됩니다. 길을 따라 그리쉬나스와 까마귀 숲을 지나서 끝까지 가세요.', },
    nil,
  },
  [10748] = {
    '막스나르를 없애라!',
    {'그들은 선택의 여지를 남겨 놓지 않는구려. 이제 그들의 지도자인 잿빛아귀 막스나르를 처치하여 검은날개 소굴의 위협에 종지부를 찍을 때가 왔소이다.$B$B회의에 참석했을 때 그를 언뜻 봤는지는 모르겠지만, 막스나르는 반은 사람이고 반은 용의 모습을 한 용족이라오. 용의 피를 타고난 자이니만큼 아주 강한 상대이니 만만하게 봐서는 안 될 것이오!$B$B운이 따라 일을 신속히 마친다면 당신이 영원의 숲과 루안 숲을 구하게 될 것이오.', },
    nil,
  },
  [10749] = {
    '남작 세이블메인의 독',
    {'이걸 자네를 보낸 이에게 가져가라. 렉사르의 의도는 잘 모르겠지만 분명 좋은 데 쓸 것이다.$B$B참, 그가 나한테 신세 졌다는 사실을 잊지 말고 강조하게.$B$B렉사르는 내가 뭘 원하는지 알지. 어떻게든 그걸 찾아줄 것이다. 생각보다 빨리 우리는 다시 만날 것 같군.$B$B자, 어서 이걸 렉사르에게 전하라.', },
    {'칼날 산맥의 천둥군주 요새에 있는 렉사르에게 남작 세이블메인의 독을 전해야 합니다.', },
  },
  [10750] = {
    '정복의 길',
    {'$N, 우리 와이번 기수들이 소식을 전해 왔소. 해그늘 블러드 엘프들이 엄청난 걸 준비하고 있다는군. 놈들의 군대가 멀리 남동쪽으로 해그늘 주둔지 근처, 정복의 길에 대형을 갖추기 시작했다오.$B$B정복의 길로 가서 상황을 파악하시오. 그런 다음 신호용 권총을 사용하여 하늘에 신호를 보내시오. 와이번 하나가 휙 하고 날아올 거요. 당신이 발견한 사실을 와이번 기수에게 알리고 다음 명령을 받으시오.', },
    nil,
  },
  [10751] = {
    '길 정리',
    {'우리에겐 시간이 좀 더 필요해. 우리 와이번 기수들은 더욱 멀리 날아가 볼 생각이네. 더 많은 정보를 얻으려고 가는 거지. 자네에게 부탁하고 싶은 일이 하나 있네. 우선적으로 길을 정리하는 임무야. 놈들의 화살이 우리를 겨냥하지 못하게 막는 아주 중요한 일일세!$B$B놈들의 수를 좀 줄이면 코르크론 신호용 권총을 사용해서 와이번 기수를 부르게.', },
    nil,
  },
  [10752] = {
    '잿빛 골짜기를 향한 전진',
    {'얼마 전 레이네 울프러너가 다르나서스에서 잿빛 골짜기로 가던 중 아우버다인을 지난 적이 있소. 그때 그녀는 펄볼그에 대한 얘기를 했었는데 그녀의 상황에 대해서는 더 물어볼 겨를도 없었소이다. 당신이 그녀를 찾아서 도와주시오. $C|1이여;여;, 아우버다인을 나와 남쪽 도로로 가시오. 잿빛 골짜기 경계선에 다다르면 길을 따라 계속 내려가면 되오. 그럼 그곳에 아스트라나르와 레이네 울프러너가 있을 테니 그녀를 만나보시오.', },
    {'아스트라나르에 있는 레이네 울프러너와 대화해야 합니다.', },
  },
  [10753] = {
    '야생동물의 저주',
    {'불타는 군단은 셀 수도 없이 많은 극악한 행위를 저질러 왔습니다. 우리가 얘기하고 있는 이 순간에도 그들은 이 땅에 있는 생물들을 없애기 위해 병력을 투입하고 있는 지경입니다. 그리고 그들의 힘을 당해내지 못한 생물들은 하나 둘 힘없이 쓰러져 가고 있어요. $C님, 자연을 오염시키고 파괴하는 이런 범죄를 막을 수 있게 도와주세요.$B$B여기서 동쪽에 있는 언덕을 지나면 칼날 협곡 전역에 퍼져 나간 오염 현상을 목격할 수 있을 겁니다. 그런 범죄를 주도하고 있는 지옥의 타락자들과 악마추종자들을 최대한 많이 처치해 주십시오.', },
    nil,
  },
  [10754] = {
    '지옥불 성채로',
    {'만든 지 얼마 안 되는 이 거푸집은 일종의 열쇠를 찍어내는 데 사용되는 것 같습니다. 뒤쪽에 새겨진 그림으로 봐서 지옥불 성채와 관련이 있는 것 같습니다.$B$B명예의 요새의 전투사령관 다나스가 이 물건에 관심을 가질 것 같습니다.', },
    {'지옥불 반도의 명예의 요새에 있는 전투사령관 다나스에게 준비된 열쇠 거푸집을 가져가야 합니다.', },
  },
  [10755] = {
    '지옥불 성채로',
    {'만든 지 얼마 안 된 것처럼 보이는 이 거푸집은 열쇠를 찍어내는 데 쓰던 물건 같습니다. 뒤쪽에 새겨진 문양으로 봐서 지옥불 성채와 관련이 있는 듯합니다.$B$B스랄마에서 호드군을 이끄는 나즈그렐이 이 물건에 관심을 보일지도 모릅니다.', },
    {'지옥불 반도의 스랄마에 있는 나즈그렐에게 준비된 열쇠 거푸집을 가져가야 합니다.', },
  },
  [10757] = {
    '로호크의 부탁',
    {'타락한 오크 대장장이들의 솜씨가 그렇게 빼어나지는 않지만 장인 정신은 있는 것 같군. 이 거푸집으로 제대로 작동하는 열쇠를 만들려면 지옥무쇠를 넣어야 하오. 다른 걸 쓰면 거푸집이 망가져 버릴 거요.$B$B지옥무쇠 주괴, 신비한 수정 가루, 그리고 불의 티끌을 구해 오시오. 그러면 한번 솜씨는 발휘해 보겠소. 하지만, 성공은 나도 장담 못하오.', },
    {'지옥불 반도의 스랄마에 있는 로호크에게 지옥무쇠 주괴 4개, 신비한 수정 가루 2개, 불의 티끌 4개를 가져가야 합니다.', },
  },
  [10758] = {
    '지옥보다 뜨거운',
    {'$N, 실패했소! 가열로에 아무리 석탄을 많이 넣어도 별로 뜨겁지가 않아.$B$B이 열쇠를 주조할 정도로 뜨거운 온도는 불타는 군단의 지옥절단기의 내부밖에는 생각나지 않는군.$B$B예전에 딱 한 번 쓰러진 지옥절단기를 본 적 있소. 안을 들여다 보니 불꽃이 튀기면서 연기가 나더군. 이 거푸집을 가져가서 쓰러진 지옥절단기 안에 넣으시오. 지옥절단기를 파괴하려면 친구들을 좀 데려가는 게 좋을 거요.', },
    {'지옥불 반도에서 지옥절단기 1대를 파괴한 후 그 안에 달궈지지 않은 열쇠 거푸집을 넣어야 합니다. 스랄마에 있는 로호크에게 그을린 열쇠 거푸집을 가져가야 합니다.', },
  },
  [10759] = {
    '탈영병 찾기',
    {'흠... 이 일지에 그 아라코아들에 대한 모든 답이 있다는 느낌은 드는데 말이야... 대체 어떻게 짜맞춰야 되는지 도무지 모르겠단 말일세.$B$B내 생각에는 이 탈영병을 찾는 게 가장 좋은 방법일 것 같군그래. 만일 그의 동료가 아직 야영지에 남아 있다면, 그를 설득해서 암흑 비밀결사 아라코아가 무슨 일을 꾸미고 있는지 알아내는 데 도움을 받을 수 있을 것일세.$B$B북서쪽의 용암 지대부터 수색을 해 보게나. 그가 숨어 있을 가능성이 큰 곳이니 말이야.', },
    {'용암 지대에 있는 파르쉬아를 찾아서 대화해야 합니다.', },
  },
  [10760] = {
    '스케슬론 폐허',
    {'여기서 바로 동쪽에 있는 스케슬론 폐허는 아주 으스스한 곳이지. 일꾼들은 그곳이 저주를 받았다고 확신한다네.$B$B이곳에는 나무가 별로 없어서 나무를 베려면 일꾼들은 어쩔 수 없이 그 아라코아 폐허로 가야 하지. 아라코아 영혼들은 일꾼들을 약탈하거나 생명을 빼앗아서 사악한 마법을 위한 재료로 사용한다네.$B$B놈들의 영향력이 더 확대되기 전에 무엇이 그 암흑 비밀결사들을 조종하는지 알아내서 막아야 하네. 스케슬론 폐허에서 놈들의 계획에 대한 단서를 찾게나.', },
    {'스케슬론 폐허에 있는 암흑 비밀결사 아라코아를 처치하고 스케슬론 사령관의 일지 - 1쪽, 2쪽, 3쪽을 모은 후 어둠달 마을에 있는 하사관 카르그룰에게 가져가야 합니다.', },
  },
  [10761] = {
    '탈영병 찾기',
    {'이 지휘관의 일지에는 그 아라코아 영혼들이 이 세계에 남은 이유나 그들이 오크의 피를 갈망하게 된 이유가 무엇인지 설명할 수 있는 정보는 전혀 없군.$B$B지휘관의 일지에 나온 탈영병을 찾는 게 가장 좋은 방법인 것 같아. 만일 그가 동료를 기지에 남기고 떠난 게 맞는다면 설득해서 암흑 비밀결사 아라코아들이 무슨 일을 꾸미고 있는지 알아낼 수 있을 걸세.$B$B남동쪽의 용암 지대부터 수색해 보게나.', },
    {'용암 지대에 있는 파르쉬아를 찾아서 대화해야 합니다.', },
  },
  [10763] = {
    '덤프리의 부탁',
    {'타락한 오크들은 완전한 야만인들이라고 할 수 있으나, 녀석들의 대장기술은 자네가 생각하는 것만큼 뛰어나지 않다네. 제대로 된 열쇠를 만들고 싶다면 이 거푸집에 지옥무쇠를 넣어야 하네. 다른 걸 사용하면 아마 거푸집이 다 망가져 버릴 거야.$B$B지옥무쇠 주괴, 신비한 수정 가루, 불의 티끌을 구해 오게나. 내 한번 솜씨는 발휘해 보겠지만 성공하리라곤 장담 못하네.', },
    {'지옥불 반도의 명예의 요새에 있는 덤프리에게 지옥무쇠 주괴 4개, 신비한 수정 가루 2개, 불의 티끌 4개를 가져가야 합니다.', },
  },
  [10764] = {
    '지옥보다 뜨거운',
    nil,
    {'지옥불 반도에서 지옥절단기 1대를 파괴한 후 그 안에 달궈지지 않은 열쇠 거푸집을 넣은 후, 그을린 열쇠 거푸집을 명예의 요새에 있는 덤프리에게 가져가야 합니다.', },
  },
  [10765] = {
    '탈 많은 정상 회담',
    {'정복의 길 남쪽 끝에 서 있는 마차 근처를 비행하다가 블러드 엘프 장관이 거인 하나와 회의 비슷한 걸 하는 광경을 목격했네. 자네도 정복의 길에서 아마 작은 거인들을 봤을 거야. 그런트여, 이 모든 게 뭔가 단단히 잘못 돌아간다는 뜻인 것 같아.$B$B이 회의가 진행되고 있는 곳으로 가서 회의를 중단시키게. 최대한 많은 정보를 알아낸 후 신호용 권총으로 우리에게 신호를 보내게.', },
    nil,
  },
  [10766] = {
    '대재앙의 침공 거점',
    {'대재앙의 침공 거점에 있는 불타는 군단의 기지에 접근했으니 이제 그들이 그곳에서 진행 중인 작전을 저지할 수 있는 방법을 찾아야 합니다. 어둠달 골짜기에서 불타는 군단을 몰아내기 전에는 와일드해머 성채의 안전을 얘기할 수가 없으니까요.$B$B당신이 불타는 군단의 보급을 차단할 계획을 조직적으로 운영할 수 있게 그 침공 거점에 미리 선발대를 보내놨습니다. 불타는 군단 요새로 돌아가서 녹색 수정이 떠 있는 순간이동기로 들어가십시오. 그러면 목적지에 도착할 것이고 그곳에서 플렉시가 당신을 기다리고 있을 겁니다.', },
    {'대재앙의 침공 거점에 있는 플렉시에게 보고해야 합니다.', },
  },
  [10767] = {
    '대재앙의 침공 거점',
    {'이제 대재앙의 침공 거점에 있는 불타는 군단 기지에 접근할 수 있으니 놈들이 그곳에서 진행 중인 작전을 저지할 방법을 찾아야 하네. 어둠달 마을에서 불타는 군단을 완전히 몰아내기 전까지는 우리 마을도 안전하다고 볼 수 없지.$B$B자네가 불타는 군단의 보급을 차단할 계획을 진행할 수 있게 침공 거점에 미리 요원을 보내놓았네. 불타는 군단 요새로 돌아가서 녹색으로 빛나는 순간이동기 안으로 걸어 들어가게. 도착하면 나칸시가 맞아줄 걸세.', },
    {'대재앙의 침공 거점에 있는 나칸시에게 보고해야 합니다.', },
  },
  [10768] = {
    '일리다리 휘장',
    {'우리 와이번 기수들은 어둠달 마을로 돌아가서 다음 공격 작전을 준비해야 하네. 자네는 블러드 엘프들이 달고 있는 휘장을 가져오게.$B$B휘장을 충분히 모은 후 코르크론 신호용 권총을 사용하여 와이번 기수를 부르게. 매복해 있는 놈들도 꽤 있을 테니까 늘 긴장을 늦추지 말게!', },
    nil,
  },
  [10769] = {
    '이간질...',
    {'연구원 티오루스가 친절하게도 마지막 공격에 쓰라고 혼합물을 만들어줬지. 자네가 준 휘장에 뿌리면 쓰레기 같은 일리다리 놈들의 정수가 스며들 거야.$B$B마력이 깃든 휘장을 가지고 해그늘 주둔지의 남서쪽에 있는 해그늘 벌판으로 가게. 도착하면 휘장을 사용해 일리단의 하수인으로 변장하고 변장이 끝나기 전에 그곳을 돌아다니는 광포한 거대괴수를 해치워. 그러면 거인들은 해그늘 엘프들의 짓이라 오해하고 모든 유대 관계를 끊을 테니까!', },
    {'일리다리 블러드 엘프로 변장한 동안 광포한 거대괴수 5마리를 처치한 후 어둠달 골짜기의 어둠달 마을에 있는 대군주 오르바로크에게 돌아가십시오. 코르크론 신호용 권총도 돌려줘야 합니다.', },
  },
  [10770] = {
    '작은 미련',
    {'여기서 북동쪽의 고원 지대에는 다 타고 재만 남은 내 오랜 고향이 있다. 그곳은 한때 큰 나무들이 무성했던 곳인데, 불타는 군단이 타오르는 임프를 보내 모두 불태워 버렸다. 그렇게 몇백 년이 흐르면서 수많은 동족이 죽었지만 내 늙은 심장은 아직도 복수에 굶주리고 있구나. 그곳을 되찾기에는 악마들의 수가 많다만 너라면 그들에게 두려움을 심어줄 수 있을 것이다.$B$B이 늙은이에게 도움의 손길을 보내주겠지?', },
    {'타오르는 임프 8마리를 처치한 다음 루안 숲에 있는 고대정령 나무이끼에게 가야 합니다.', },
  },
  [10771] = {
    '잿더미에 숲의 생명을',
    {'동쪽에 있는 길을 따라 산 쪽으로 올라간 다음 북쪽으로 가면 오래전에 잃어버린 내 고향이 나올 것이다.$B$B예전에 그곳은 숲이 울창했지만 이젠 그 숲의 이름도 잊은 지 오래구나. 오래전, 불타는 군단이 타오르는 임프들과 화염 생물들을 불러내 그 숲을 화염으로 휩쓸었단다. 내 고향은 파괴되고 동족들은 뿔뿔이 흩어져 버렸지. 보다시피 이제 남은 건 나뿐이란다.$B$B자, 받거라. 내가 기름진 화산 토양에 뿌릴 무쇠뿌리 씨앗을 모아놨단다. 이 씨앗엔 아주 강력한 마법이 깃들어 있지. 나를 위해 이번 일을 해 준다면 너에게 잃어버린 시대의 보물들을 주마.', },
    {'불타버린 언덕으로 가서 기름진 화산 토양 세 무더기에 무쇠뿌리 씨앗을 심은 다음, 루안 숲에 있는 고대정령 나무이끼에게 가야 합니다.', },
  },
  [10772] = {
    '정복의 길',
    {'내 그리핀 기수들이 흥미로운 소식을 가지고 돌아왔네! 해그늘 블러드 엘프들이 엄청난 걸 준비 중이라고 하는군. 그 녀석들이 여기서 동쪽, 해그늘 주둔지 부근에 있는 정복의 길에 군대를 집결하고 있다고 하네.$B$B자네가 정복의 길로 가서 사전 조사를 통해 상황을 파악하게. 도착하면 이 신호용 권총을 사용하여 그리핀 기수들에게 신호를 보내게나. 그러면 우리 그리핀 기수 중 한 명이 휙 하고 자네가 있는 곳으로 날아올 것이야. 자네가 발견한 사실을 그리핀 기수들에게 알리면 다음 명령을 받을 수 있을 것일세.', },
    nil,
  },
  [10773] = {
    '길 정리',
    {'$C|1이여;여;, 우리에겐 정보를 얻을 시간이 좀 더 필요합니다. 제가 다른 그리핀 기수들과 함께 좀 더 전방으로 수색을 가 보겠습니다. 이번 수색에서 더 많은 정보를 얻을 수 있으리라 봅니다. 그래서 당신에게 이번 수색 작전에 엄호를 부탁합니다. 우선 길을 정리하면서 블러드 엘프들이 수색을 펼치는 우리 그리핀 기수들에게 사격을 가하지 못하도록 차단해 주십시오!$B$B녀석들의 수를 줄이고 나면 그 신호용 권총을 사용해 우리를 부르시기 바랍니다.', },
    nil,
  },
  [10774] = {
    '블러드 엘프와 거인의 회담',
    {'아까 말씀드린 그 놀랄만한 소식이 무엇인지 이제 알려 드리지요. 정복의 길의 남쪽 끝에 서 있는 마차 근처에 갔다가 거인과 블러드 엘프의 고위 관료가 회담 비슷한 걸 하고 있는 걸 목격했습니다. 대체 거인은 그곳에서 뭘 하고 있었던 걸까요? 이 일 전부에 걸쳐 뭔가 단단히 잘못된 것이 분명합니다.$B$B그 회담이 진행되고 있는 곳으로 가서 회담을 중단시키십시오. 그리고 최대한 많은 정보를 알아낸 후 신호용 권총으로 우리에게 신호를 보내주시기 바랍니다.', },
    nil,
  },
  [10775] = {
    '일리다리 휘장',
    nil,
    nil,
  },
  [10776] = {
    '이간질...',
    {'와일드해머 성채에 있는 우리의 친절한 친구 노무스가 마지막 공격을 위해 이 혼합물을 만들어 뒀습니다. 당신이 가져온 휘장에 이 혼합물을 뿌리기만 하면 일리다리 타락의 정수가 스며들 것입니다.$B$B강화된 이 휘장을 가지고 해그늘 주둔지의 남서쪽에 있는 해그늘 벌판으로 가십시오. 그곳에 도착하면 휘장을 사용해서 일리단의 하수인의 모습으로 변장하십시오. 변장이 풀리기 전에 그곳을 돌아다니는 광포한 거대괴수를 처치하십시오. 그러면 괴수들이 이번 일이 해그늘 블러드 엘프의 짓이라 생각하게 될 것이고, 그 블러드 엘프간의 유대 관계를 끊게 될 겁니다!', },
    {'일리단의 블러드 엘프 하수인으로 변장한 동안 광포한 거대괴수 5마리를 처치한 후, 와일드해머 신호용 권총을 가지고 어둠달 골짜기의 와일드해머 성채에 있는 영주 요르가르에게 가야 합니다.', },
  },
  [10777] = {
    '아스가르의 토템',
    {'당신이 지휘관의 일지에서 읽은 내용은 모두 사실이에요. 오크 호드가 굴단 휘하에 집결했을 때 우리 동족은 굴단에게 대항하여 스스로 위협을 방어하려 했죠. 그 의도를 파악한 굴단은 강력한 마법으로 우리 야영지를 파괴하고 형제들의 영혼을 타락시켰어요.$B$B놈들이 더 강력한 악마를 풀어놓기 전에 막아야 해요. 북쪽의 스케슬론 폐허에서 아스가르를 찾아서 스케슬론 전투 토템을 가져다주세요. 그것을 시작으로 우린 암흑 비밀결사들의 계획을 방해할 수 있어요.', },
    {'용암 지대에 있는 파르쉬아에게 스케슬론 전투 토템을 가져가야 합니다.', },
  },
  [10778] = {
    '리안데의 마법봉',
    {'암흑 비밀결사들의 사악한 마법을 막아내기에 토템 하나는 턱없이 부족해요.$B$B하지만, 토템을 힘을 가진 다른 물건들과 결합하면 에너지를 한데 모으는 마법봉을 만들 수 있어요. 그러면 뿌리깊게 타락한 우리 동족을 구할 수는 없어도 막아낼 수는 있을 거예요.$B$B필요한 물건 중 하나는 리안데의 마법봉이에요. 남동쪽에 있는 해그늘 주둔지의 블러드 엘프들이 갖고 있죠. 그 폐허 꼭대기 층에 보관되어 있는데 경비가 굉장히 삼엄할 거예요. $N, 조심하세요.', },
    {'해그늘 주둔지의 블러드 엘프들을 처치하고 리안데의 열쇠를 입수해, 리안데의 금고를 열고 리안데의 마법봉을 빼내야 합니다. 용암 지대에 있는 파르쉬아에게 리안데의 마법봉을 가져가십시오.', },
  },
  [10779] = {
    '사냥꾼의 길',
    nil,
    nil,
  },
  [10780] = {
    '스케슬론 깃털',
    {'딱 한 가지 재료만 있으면 마법봉이 완성됩니다. 우리는 마법봉의 힘을 암흑 비밀결사 영혼들에게 사용할 거예요.$B$B스케슬론 주둔지에 흩어져 있는 깃털을 수집해서 마법봉에 사용해야 해요. 스케슬론 주둔지는 남쪽으로 와일드해머 성채의 동쪽에 있습니다.$B$B굴단의 마법은 우리 동족의 영혼만 남겨놓고 완전히 파괴해 버렸지만 그게 끝이 아니었습니다. 아라코아의 주문에 깃든 마법은 영혼을 빨아들일 만큼 사악한 힘을 만들었죠.', },
    {'스케슬론 깃털 8개를 모아서 용암 지대에 있는 파르쉬아에게 가져가야 합니다.', },
  },
  [10781] = {
    '붉은 감시초소 대전',
    {'우리가 대화를 나누는 지금 이 순간 놈들은 정복의 길에서 자네를 찾고 있을 걸세. $R|1이여;여;, 무조건 탈출해야 하네!$B$B난 놔두고 떠나게. 난 이미 늦었어... 놈들... 놈들이 오는 소리를 들은 것 같아. 지금이 아니면 영영 기회가 없어! 달아나! 나와 함께 남겠다면 놈들과 싸워야 하네!$B$B전투 끝에 살아남거든 여기서 알아낸 사실을 샤트라스의 아달에게 전해 주게.', },
    {'붉은 감시초소에 있는 일리단의 군대와 싸워서 살아남아야 합니다!$B$B전투에서 승리하면 샤트라스에 있는 아달에게 가십시오.', },
  },
  [10782] = {
    '머리조각에 정기 주입',
    {'마법봉이 거의 완성됐습니다!$B$B이 저주받은 곳에 울리는 굴단의 목소리를 타고 흐르는 힘을 마법봉의 머리조각에 주입하기만 하면 됩니다. 동쪽으로 지옥의 구덩이와 죽음의 괴철로 남쪽 부근에 저주의 제단이 있습니다.$B$B그곳에는 굴단의 과거의 잔재들이 갇혀 있습니다. 완성되지 않은 머리조각을 제단 가까이에서 사용할 때 놈들이 격렬히 반응하더라도 놀라지 마십시오. 당신의 손에 대재앙과 강하게 연결된 위험한 물건이 있기 때문이니까요.', },
    nil,
  },
  [10783] = {
    '남작 세이블메인',
    {'내 계획은 이렇네. 우리는 오우거들을 중독시킨 후 놈들의 우두머리인 그론을 제거할 거야.$B$B이 산맥을 여행하던 중 남작 세이블메인이라는 남자를 만났네. 그는 연금술의 대가인데 잘 설득하면 우리를 도와줄 거야.$B$B여기서 북쪽으로 계곡 아래 있는 피의 투기장으로 가면 그를 찾을 수 있네. 그가 어떤 것을 요구하든지 전적으로 자네 판단에 맡기겠네.', },
    {'칼날 산맥의 피의 투기장에 있는 남작 세이블메인과 대화해야 합니다.', },
  },
  [10784] = {
    '피망치 야영지를 박살 내라!',
    {'<몸을 가까이 기대며 속삭이는 토르청크>$B$B$N, 지금까지 우리를 위해 오우거와 싸워오면서 기대 이상의 큰 공을 세웠네. 렉사르의 계획이 다 맘에 들기는 한데 이왕이면 피망치 야영지에 있는 오우거들도 박살 내고 돌아왔으면 해.$B$B그게 바로 일석이조 아니겠나?!$B$B천둥군주 요새에서 북쪽으로 나가는 비탈진 길을 내려가게. 평지에 다다라서 용의 무덤을 통과한 후에 서쪽으로 간 다음 휘몰이 협곡과 비수아귀 협곡을 지나 북쪽으로 가게.', },
    nil,
  },
  [10785] = {
    '함정이다!',
    {'난 이 세상에서 그룰과 놈의 자식들이 제일 싫어! 놈들은 나의 동족과 사랑스러운 우리 아이들을 살해했지. 이제 난 복수를 맛볼 거야!$B$B용숨결로 덫을 만들었네. 자네가 충분히 영리하다면 이것을 이용해서 그론을 죽일 수 있을 거야.$B$B서둘러서 이 덫을 렉사르에게 가져가게. 복수를 향한 나의 갈증을 채워줘!', },
    {'용숨결 덫을 칼날 산맥의 천둥군주 요새에 있는 렉사르에게 전해야 합니다.', },
  },
  [10786] = {
    '볼더모크 대학살',
    {'렉사르가 자네를 볼더모크로 보낼 계획을 세워놓았다더군. 그곳으로 가면 날 위해 해줄 일이 하나 있네.$B$B기회가 생길 때마다 이 산맥의 오우거들을 최대한 많이 처치해야 해. 그렇지 않으면 놈들이 우리를 공격할 테고, 그러면 지금까지 우리가 전투에서 흘렸던 피와 수많은 용사가 목숨까지 버려가며 추진했던 일이 모두 수포로 돌아가겠지!$B$B볼더모크는 멀리 북서쪽에 있네. 북쪽으로 루안 숲까지 간 다음 굴을 통과해 서쪽으로 전진해 까마귀 숲을 지나면 바로 있네.$B$B$N, 볼더모크의 칼날첨탑 오우거들을 처치해 주게. 놈들을 쓸어버려!', },
    nil,
  },
  [10791] = {
    '늑대 정령 맞이',
    {'의식 때 입을 예복을 거의 다 완성해서 마무리 작업을 하려고 합니다. 바로 왼쪽의 마법석 아래 양초가 보일 겁니다. 그 양초에서 이 향을 피워주십시오.$B$B오크 부족들의 시대에 만든 향이니까 늑대 정령의 관심을 끌 겁니다. 제 생각에 우리 선조는 사냥에 성공하면 의식을 열고 향을 피워 늑대 정령에게 감사를 표했을 것 같아요.$B$B향을 다 피우면 의식을 시작하겠습니다.', },
    {'말루카즈의 오두막 안에 있는 양초 근처에서 의식용 향을 피운 후 의식을 지켜봐야 합니다. 의식이 끝나면 다시 말루카즈와 대화하십시오.', },
  },
  [10792] = {
    '제스고르를 불태워라!',
    {'피눈물 부족은 원래 고귀한 혈통을 저버렸지! 우리 오크는 사악한 만노로스의 기운으로부터 스스로 보호하려고 숱한 전투와 죽음을 마다지 않았는데 이 타락한 사촌들을 보면 나약함과 야비함밖에 찾아볼 수 없다니까... 그래서, 놈들을 불태워 버려야 한다는 거야!$B$B동쪽으로 놈들의 요새인 제스고르로 들어가서 놈들의 건물을 불태우게. 먼저 일꾼들을 처치해서 횃불을 빼앗아. 놈들의 가열로로 가져가서 불을 붙인 후 그 불타는 횃불을 놈들의 병영, 오두막, 그리고 늑대 우리에 던지게. 다 태워버린 다음 돌아오게!$B$B호드를 위하여!', },
    {'피눈물 일꾼들을 처치해서 빼앗은 횃불에 제스고르 가열로로 가져가 불을 붙인 다음, 불타는 횃불을 제스고르 건물들에 던져야 합니다.', },
  },
  [10793] = {
    '발자레크의 일지: 전쟁의 전조',
    {'이 일지에는 최근 일리단 군대의 동태가 자세히 나와 있습니다. 발자레크가 정복의 길을 누비고 다녔던 이유도 적혀 있습니다. 샤타르의 고위사령관을 사로잡는 임무를 개인적으로 맡았던 것으로 보입니다.$B$B그 고위사령관은 정복의 길에서 남쪽 끝에 있는 붉은 감시초소라는 초소에 잡혀 있다고 합니다. 그를 감금한 것은 스스로 붉은 인장대라고 부르는 수상쩍은 진영으로 추정됩니다. 자세히 조사를 하고 싶다면 같은 편을 데려가는 것이 현명한 판단입니다.', },
    {'붉은 감시초소에서 샤타르의 고위사령관인 마르커스 오랄리온을 찾아야 합니다.$B$B그는 수정 감옥 안에 있습니다.', },
  },
  [10794] = {
    '으스러진 손의 일원',
    {'$N, 우리가 알지 못하는 세계에는 여러 가지 비밀이 있습니다. 오그리마의 으스러진 손은 그대를 그런 비밀의 세계로 안내할 것입니다. 이를테면, 치명적인 독을 제조하는 방법 말이죠. 그들이 시키는 대로 하면 신뢰를 얻을 수 있습니다.$B$B오그리마에 가려면 실버문에 있는 차원문을 넘어서 언더시티로 가십시오. 언더시티에 도착하면 밖으로 나가서 고블린 비행선이 승객을 태우는 장소를 찾으세요. 그곳에서 오그리마로 날아가서 어둠의 틈에 있는 셴툴을 찾으십시오.', },
    {'오그리마에 있는 셴툴과 대화해야 합니다.', },
  },
  [10795] = {
    '도르고크와의 만남',
    {'반갑습니다. 안그래도 지나가다가 당신을 몇 번 봤었는데 아주 중요한 문제에 대해 상의하고 싶었답니다.$B$B피망치 오우거들이 어떤 이유 때문에 실바나르와 루안 숲에 대한 공격을 계획 중이라는 보고가 들어왔습니다. 누가 그런 일을 시킨 건지, 또 왜 그러는 건지는 모르겠으나 녀석들이 공격을 시작하기 전에 그 싹을 잘라 버리고자 합니다!$B$B정보에 의하면 공격이 계획되는 곳이 피망치 야영지라고 합니다. 루안 숲 바깥에서 경사로를 따라 북서쪽으로 내려가면 그 야영지에 도착할 수 있을 겁니다. 그곳에서 녀석들의 지도자인 도르고크를 처치해 주십시오!', },
    nil,
  },
  [10796] = {
    '피망치 야영지 초토화!',
    {'지금까지 저희를 위해 오우거와 싸워 주시며 저희에게 큰 자랑이 되어주셨다고 들었답니다. 근데 제가 다시 오우거들을 상대해 주셨으면 하는 부탁을 드리게 되었군요. 피망치 야영지에 있는 오우거들을 박살내 주세요!$B$B그곳에 가려면 루안 숲을 나가 경사도를 따라 북서쪽으로 내려가시면 됩니다. 그 오우거들은 그 경사도 아래에 있을 겁니다.$B$B$C님, 행운을 빕니다!', },
    nil,
  },
  [10797] = {
    '고르그롬의 조각상',
    {'죽은 도르고크의 움켜쥔 손을 펼치자 작은 조각상으로 된 징표가 떨어집니다. 이 조각상은 그론의 생김새와 굉장히 흡사합니다.$B$B분명히 동쪽 경사로 위, 루안 숲의 영원의 숲에 있는 지휘관 헤이푸스 스톤월이 이 물건에 관심이 있을 것이니 그에게 가져가야 합니다!', },
    {'칼날 산맥의 영원의 숲에 있는 지휘관 헤이푸스 스톤월에게 고르그롬의 조각상을 가져가야 합니다.', },
  },
  [10798] = {
    '남작 세이블메인 방문',
    {'생각해보니 정말 나쁜 소식이로군요. 피망치 일족의 실바나르 공격 감행 계획의 배후에 용포식자 고르그롬이 있다니...$B$B왜 그가 칼날첨탑 오우거들을 이용하지 않고 피망치 일족을 조종하는지 모르겠군요. 어쨌거나 그론을 처치하려면 도와줄 친구가 필요하다는 건 분명합니다.$B$B내가 아는 분 중에 온갖 독특한 마법에 능통한 남작 세이블메인이라는 분이 계십니다. 그를 만나려면 경사로를 따라 북서쪽으로 내려간 다음 협곡을 통과해서 남서쪽으로 피의 투기장에 가야 합니다.$B$B그에게 그론에게 사용할 함정을 만들어 줄 수 있는지 물어보십시오.', },
    nil,
  },
  [10799] = {
    '휘몰이 협곡으로',
    {'좋다. 내가 함정을 만들어 주기 전에 그에 합당한 답례를 먼저 해 줘야겠다.$B$B마침 이 근처에 내가 원하는 물건을 가진 그론이 있군그래. 뭐 어쨌거나 무턱대고 덤비면 곤란하지.$B$B그러니 이제 우리가 근처에 있는 바위갈이 미늘벌레의 독을 이용해서 졸음 가루를 만들 것이다.$B$B바위갈이 미늘벌레를 찾으려면 남서쪽에 있는 휘몰이 협곡으로 가면 된다. 녀석들의 독 분비선을 많이 모아서 가져오너라. 서둘러야 한다!', },
    nil,
  },
  [10800] = {
    '잘 자라, 그론',
    {'아, 난 정말 못하는 게 하나도 없군. 내가 갖고자 하는 물건을 수호하는 그 그론 녀석인 그룰록에게 사용할 졸음 가루를 만들었다.$B$B그룰록은 피의 투기장의 반대편에 있는 용의 무덤의 동쪽 끝에 위치한 저주받은 골짜기라는 곳에 있다.$B$B아, 한가지 조언을 해 주지. 나라면 말이야 일단 그룰록의 자루를 손에 넣은 후 재빨리 움직일 것이야. 졸음 가루의 효과는 그리 오래가지 않으니 말이야.', },
    nil,
  },
  [10801] = {
    '함정이다!',
    {'난 이 세상에서 그룰의 자식들과 그 아버지라는 녀석을 가장 증오한다! 녀석들은 내 동족은 물론이고 내 자식까지도 처참히 살해했다. 드디어 복수의 쓰디쓴 맛을 보게 되겠구나!$B$B내가 용숨결로 덫을 만들었다. 당신이 영리한 친구라면 이것을 사용해서 그론을 죽일 수 있겠지.$B$B분명 마음에 두고 있는 그론이 있을 테지만 어떤 그론을 죽이든 나는 상관없다. 서둘러 이 덫을 지휘관 헤이푸스 스톤월에게 가져가라.$B$B복수에 대한 나의 갈증을 채워다오!', },
    {'칼날 산맥의 영원의 숲에 있는 지휘관 헤이푸스 스톤월에게 용숨결 덫을 가져가야 합니다.', },
  },
  [10802] = {
    '용포식자 고르그롬',
    {'그 오우거 녀석들에게 우리가 호락호락하지 않다는 걸 보여줘야 합니다. 지금이 우리의 힘이 실린 강력한 메시지를 보내야 할 때인 것입니다.$B$B자, 볼더모크로 가서 그곳에 있는 오우거들을 처치해 주세요. 녀석들의 목을 취해야 한다는 걸 명심하셔야 합니다.$B$B그런 다음 고르그롬의 제단으로 가서 전에 가져온 덫을 놓으십시오. 그러고 나서 고르그롬이 나타나면 녀석을 함정으로 유인해야 합니다. 그가 죽으면 그 시체 근처에 소름 끼치는 토템을 박으세요. 그러면 오우거들이 자신의 지도자와 형제들의 죽음을 목격하게 될 것이고 우리를 건드린 대가가 얼마나 비싼지 알게 될 것입니다!', },
    nil,
  },
  [10803] = {
    '볼더모크 대학살',
    {'저희가 계획을 세워 놓았으니 볼더모크로 가 주세요. 그곳에서 당신이 해주셨으면 하는 일이 있습니다.$B$B이 산맥에서 그 오우거들을 물리칠 기회를 잡아서 녀석들이 우리를 침략할 수 없도록 해야 합니다. 그렇지 않으면 지금까지 우리가 피를 흘리고 목숨을 버려 가면서 했던 모든 일들이 수포로 돌아가고 말 테니까요!$B$B볼더모크는 이곳에서 서쪽에 있으니 남서쪽으로 가서 다리를 건넌 다음 굴을 통과하십시오. 그러면 까마귀 숲의 서쪽에 볼더모크 일족이 있을 겁니다.$B$B$N 님, 볼더모크에 있는 오우거들을 모두 쓸어버리고 돌아오십시오.', },
    nil,
  },
  [10804] = {
    '친절',
    {'뭔가가 그들을 놀라게 했어요.$B$B<머리 위를 나는 황천의 비룡을 가리키는 모르데나이>$B$B심지어 먹이를 먹으러 내려오지도 않아요. 비룡 몇 마리가 추락하는 광경을 봤는데, 땅에 부딪히기도 전에 이미 지쳐서 죽은 상태더군요.$B$B우리가 비룡들을 구해야 합니다! 수정 근처에서 찾을 수 있는 돌발톱 바위갈퀴는 비룡이 좋아하는 먹이죠. 돌발톱 바위갈퀴를 잡아서 시체를 모으십시오. 땅에 시체를 놓고 아주 조용히 계십시오. 먼저 공격적인 행동을 보이지 않는 이상 비룡들은 절대 그대를 공격하지 않습니다.', },
    {'어둠달 골짜기의 황천날개 벌판에 있는 모르데나이가 다 자란 황천날개 비룡 8마리에게 먹이를 주라고 했습니다.', },
  },
  [10805] = {
    '그룰의 둥지 대학살',
    {'당신은 기지가 있는 $C|1이라는;라는; 걸 입증해 보였다. 그룰과 그의 아들에 대항해 벌이고 있는 내 외로운 싸움에 개인적으로 당신의 힘을 좀 빌렸으면 한다.$B$B칼날첨탑 오우거는 그론이 총애하는 일족이지. 당신이 어떤 곳에서 녀석들의 숫자를 줄였는진 몰라도, 가장 강력한 녀석은 아직 맞닥뜨리지 못했다.$B$B그룰은 가장 강력한 정예 오우거들을 배치하여 둥지 외곽을 지키고 있는데, 당신이 처치할 녀석들이 바로 그 오우거들이야. 비수아귀 협곡을 통과해서 북쪽으로 가면 길의 끝에 그룰의 둥지가 나올 것이다.', },
    nil,
  },
  [10806] = {
    '대결',
    {'드디어 칼날첨탑 오우거들의 주인인 고크를 상대할 시간이 왔구나. 이번엔 이 몸도 그론의 최후를 보게 될 일생 일대의 사건에 직접 나서기로 했도다.$B$B내 인장 반지를 받아라. 이걸 고크의 제단에 도착해서 사용하면 내가 돕도록 하마.$B$B우리 함께 고크를 물리치는 것이야!', },
    nil,
  },
  [10807] = {
    '잿빛혓바닥 결사단 격파',
    {'잿빛혓바닥 결사단은 모든 뒤틀린 드레나이에게 두려움의 대상으로 각인되어 있소. 그들의 피에 굶주린 군대는 아카마가 손수 고른 아주 사악한 놈들로 구성되어 있소. 일리단의 군대에 있는 어떤 나가나 악마, 블러드 엘프 못지않게 사납고 잔인하기 짝이 없지. 여기서 북쪽에 있는 바아리 폐허에 대규모로 배치되어 있소. 놈들이 우리를 습격할 기회를 잡기 전에 그곳으로 가서 숫자를 좀 줄여주시오.', },
    {'투쟁자 바렌이 바아리 폐허로 가서 잿빛혓바닥 조련사 3명, 잿빛혓바닥 전사 4명, 잿빛혓바닥 주술사 6명을 처치해 달라고 했습니다.$B$B점술가 길드를 위한 퀘스트를 완료하면 알도르 사제회에 대판 평판은 떨어질 것입니다.', },
  },
  [10808] = {
    '암흑 비밀결사 저지',
    {'암흑 비밀결사 영혼들이 스케슬론 주둔지 남쪽의 용암 구덩이에서 강력한 고대 악마를 소환하는 의식을 벌이려 합니다.$B$B놈들의 의식에는 굴단의 마법까지 가세하여 이 세상에 그 생명체를 소환할 힘을 보태주고 있습니다! $N, 이 사태를 가만히 두고 볼 수는 없어요. 상상조차 할 수 없는 죽음과 파괴가 난무할 겁니다!$B$B마법봉을 소환 의식이 벌어지는 장소로 가져가서 암흑 비밀결사들을 영원히 막아내야 합니다!', },
    {'암흑 비밀결사 신도들을 처치한 후 파르쉬아의 마법봉을 사용하여 암흑 비밀결사의 소환 의식을 중단시킨 다음 용암 지대에 있는 파르쉬아에게 가야 합니다.', },
  },
  [10809] = {
    '현상 수배: 늑대조련사 크루쉬',
    {'전단에는 아래와 적혀 있습니다.$B$B"늑대조련사 크루쉬는 피눈물 부족 늑대기수들의 우두머리이다. 그들이 해골망치 초소로 가는 보급 행렬에 말도 안 되는 통행료를 요구하고 있기에 녀석을 처치하는 자에게 현상금을 주도록 하겠다."$B$B"정찰병의 보고에 의하면 놈은 제스고르 안에 있으니 이 악당을 처치하고 그 증거를 가져오는 영웅에게는 호드 전체를 대표한 고마움의 표시로서 보상금을 후하게 지급하겠다."$B$B"-대장 다크하울"', },
    {'늑대조련사 크루쉬를 처치한 후 해골망치 초소에 있는 대장 다크하울에게 그 증거를 가져가야 합니다.', },
  },
  [10810] = {
    '손상된 복면',
    {'손상된 복면을 살펴보자 그 안에서 이상한 소리가 들립니다. 만약 이 복면이 망가지지 않았다면 그 소리가 훨씬 분명하게 들렸을지도 모릅니다.$B$B루안 숲에 있는 오맬리 잽내버가 이 복면을 고칠 수 있을지도 모른다는 생각에 두건을 주머니에 넣고 다시 여정을 떠납니다.', },
    nil,
  },
  [10811] = {
    '넬타라쿠 찾기',
    {'그들의 지도자가 하늘 높이 활공하고 있습니다. 아마도 $N, 그대가 그의 자손에게 보여준 조그마한 친절에 감동했나 봅니다. 넬타라쿠를 찾아서 도움을 받으십시오. 곤경에 처한 황천날개 비룡들을 도와줄 수 있을지도 모릅니다.$B$B<하늘을 가리키는 모르데나이>$B$B높이 날아가십시오, 친구여...', },
    {'황천날개 용군단의 지도자인 넬타라쿠를 찾아야 합니다.', },
  },
  [10812] = {
    '수수께끼의 복면',
    {'자, 이제 다 고쳤어요. 뭐가 궁금하신 거예요? 아, 왜 그 바보 같은 친구들이 이 통신 장비로 교신을 하냐고요? 불타는 군단이 어떤 친구들인지 아시잖아요! 이 복면을 한번 야생의 군주 안텔라리온에게 가져가 보시는 건 어때요? 그는 저기서 돌아다니고 있는데 한시도 가만히 있질 않는답니다.$B$B자, 어서 가 보세요. 그는 일찍이 그 수수께끼의 복면을 손에 넣고 싶어했었어요.', },
    nil,
  },
  [10813] = {
    '그릴록의 눈',
    {'피눈물 오크들의 은신처인 무시무시한 제스고르까지 용감히 들어가서 살아서 돌아오다니! $N, 자네라면 나를 도와줄 능력이 있을지도 모르겠군. 나는 그곳에서 가장 사악한 마술사, 그릴록의 마음을 꿰뚫어 보고자 하네!$B$B이 결정을 받게. 이걸로 그릴록의 눈에 마법을 걸어서 흡수하게. 그런 다음 여기 내 가마솥으로 돌아오게. 그러면 그릴록의 눈이 밖으로 나올 걸세.$B$B출발하게, $N... 조심해서 사용하게. 조그만 빈틈이라도 보이면 그릴록의 눈은 자네를 배신하고 적에게 비밀을 폭로할 걸세!', },
    {'제자크의 결정을 사용하여 그릴록의 눈을 잡은 다음 제자크의 가마솥으로 가서 빼내야 합니다. 눈이 제거되면 제자크의 결정을 제자크에게 돌려줘야 합니다.', },
  },
  [10814] = {
    '넬타라쿠의 이야기',
    {'그대에게 도움이 될 정보를 알고 있노라. 나의 이야기를 귀 기울여 들어라.', },
    {'넬타라쿠와 대화해서 그의 이야기를 들어야 합니다.', },
  },
  [10815] = {
    '발자레크의 일지: 전쟁의 전조',
    {'이 일지에는 최근 일리단 군대의 동태가 자세히 나와 있습니다. 발자레크가 정복의 길을 누비고 다녔던 이유도 적혀 있습니다. 샤타르의 고위사령관을 사로잡는 임무를 개인적으로 맡았던 것으로 보입니다.$B$B그 고위사령관은 정복의 길에서 남쪽 끝에 있는 붉은 감시초소라는 초소에 잡혀 있다고 합니다. 그를 감금한 것은 스스로 붉은 인장대라고 부르는 수상쩍은 진영으로 추정됩니다. 자세히 조사를 하고 싶다면 같은 편을 데려가는 것이 현명한 판단입니다.', },
    {'붉은 감시초소에서 샤타르의 고위사령관인 마르커스 오랄리온을 찾아야 합니다.', },
  },
  [10816] = {
    '성스러운 대지 탈환',
    {'$N, 바아리 폐허의 뒤틀린 드레나이들에 맞서 잘 싸워주셨습니다. 이제 일리단의 가장 무시무시한 군대는 얼마나 잘 상대하실지 한번 보겠습니다.$B$B아타말 수정을 사용하여 영적 의식을 거행했던 성스러운 대지, 아타말 언덕을 어둠달 부족의 타락한 오크들이 점령했습니다.$B$B분명히 놈들은 그 근방에 아타말 수정을 숨기고 있을 텐데 수정점을 쳐봐도 아무것도 나오지 않습니다. 그래도 드레나이의 성스러운 대지에 있는 타락한 오크들은 반드시 처리해야 합니다.', },
    {'구원자 알루멘이 어둠달 학살자 8명, 어둠달 정예병 8명, 어둠달 흑마술사 4명을 처치해 달라고 했습니다.$B$B알도르 사제회를 위한 퀘스트를 완수하면 점술가 길드에 대한 평판은 떨어질 것입니다.', },
  },
  [10817] = {
    '통쾌한 복수',
    {'잿빛혓바닥 뒤틀린 드레나이를 상대로 잘 싸워주었소. $N, 그대가 원한다면 부탁할 임무가 더 있소.$B$B얼마전, 별의 성소는 일리단의 타락한 오크들로부터 엄청난 공격을 받았소.$B$B우린 아타말 언덕을 점령한 어둠달 오크들 짓이라고 단정을 내렸소.$B$B놈들에게 점술가 길드가 그리 만만한 상대가 아니라는 것을 보여주시오.', },
    {'투쟁자 바렌이 어둠달 학살자 8명, 어둠달 정예병 8명, 어둠달 흑마술사 4명을 처치해 달라고 했습니다.$B$B점술가 길드를 위한 퀘스트를 완수하면 알도르 사제회에 대한 평판은 떨어질 것입니다.', },
  },
  [10818] = {
    '남작 세이블메인의 초대',
    {'당신이 대단한 $C|1으로서;로서; 멋지게 싸우고 있을 때 남작 세이블메인이 자신의 새끼용을 보내 전갈을 전해왔습니다.$B$B당신에게 개인적인 부탁을 하고자 하더군요. 읽어 보니까 그론과의 외로운 싸움에 당신이 도움을 줬으면 좋겠다고 되어 있었습니다.$B$B이 아웃랜드에서 그를 도와줄 만한 자 중에 당신이 가장 적격이라고 생각합니다. 그래도 저 같으면 먼저 친구들 몇 명에게 연락을 해서 피의 투기장에 함께 가 달라고 부탁하겠습니다.', },
    nil,
  },
  [10819] = {
    '악마추종자의 방독면',
    nil,
    nil,
  },
  [10820] = {
    '교란 작전',
    {'그렇게 침묵으로 일관할 때마다 죽음에 더 가까워지는 줄 알아라! 말하란 말이다!$B$B뭐라고? 반란이 일어났다고? 내 부하들이 감히 내 권위에 도전하다니 그게 무슨 말도 안 되는 소리냐!$B$B좋다, 그렇다면 남아 있는 녀석들은 모두 배신에 대한 대가를 톡톡히 치르게 해 주마. 녀석들의 입을 막고 일을 진행해라! 명령을 수행한 후에 나에게 보고해라. 그렇지 않으면 너도 죽을 줄 알아라.', },
    nil,
  },
  [10821] = {
    '넌 해고야!',
    {'파멸의 지휘관이 반란을 가만히 보고만 있었단 말이지? 어떻게 감히! 그래, 전에도 몇 번씩 내 권위를 넘보려고 했었지. 하지만 이번이 마지막이 될 거다. 그녀를 소환해서 이번 반란을 개인적으로 처리하도록 해라.$B$B그녀의 고통의 경비병이 그녀를 고통의 괴철로 기지로 소환할 수 있는 열쇠를 지니고 있다. 5개의 열쇠를 회수하여 군단 방첨탑 5개를 작동하도록 하라. 군단 방첨탑은 지속 기간이 짧기에 동시에 5개를 모두 작동시켜야 한다. 아, 고통의 경비병은 더 많은 열쇠를 갖고 있다는 걸 명심하도록 하라.', },
    nil,
  },
  [10822] = {
    '성난태양 인장 하나',
    nil,
    nil,
  },
  [10823] = {
    '성난태양 인장 추가 수집',
    nil,
    nil,
  },
  [10824] = {
    '성난태양 인장',
    {'캘타스의 병력이 넘쳐 나고 있어서 우리 점술가 길드의 생존이 위태롭습니다. 쿠엘탈라스를 떠난 모든 엘프들이 놈과 결탁해 점점 더 승리의 기반을 다지고 있습니다. 하지만, 우리의 처지는 정반대죠.$B$B곧 우리 신병이 바닥날 형편이랍니다. 이제 더 빨리 죽어나가는 편이 패배하겠지요. 점술가 길드에 그대의 가치를 입증해 보이려면 캘타스, 일리단과 동맹을 맺은 블러드 엘프 고위층이 지니고 다니는 성난태양 인장을 가져오십시오. 서쪽으로 해그늘 주둔지와 북동쪽으로 카라보르 폐허에 있을 겁니다.', },
    {'별의 성소에 있는 전투마법사 비아라가 성난태양 인장 10개를 가져다 달라고 했습니다.$B$B점술가 길드를 위한 퀘스트를 수행하면 알도르 사제회에 대한 평판은 떨어질 것입니다.', },
  },
  [10825] = {
    '알 수 없는 진실',
    {'이 이상하게 생긴 마법의 보주는 죽은 아라코아의 외곽 곡선에 달려 있던 깃털의 바로 밑에서 떨어진 것입니다.$B$B이 보주는 기분 나쁜 자줏빛이 감돌고 들어 보지 못한 맥박 소리가 들립니다. 손으로 잡고 있자니 약간 불쾌한 기운이 느껴져서 재빨리 가방 안에 집어넣습니다.$B$B서쪽에서 굴을 지나 고룡해골 다리 건너 루안 숲에 있는 나무지기 숀이 이것을 보고 싶어할 거라는 생각에 아래로 이곳을 빠져나갑니다.', },
    nil,
  },
  [10826] = {
    '살게라스의 징표',
    {'자네도 지금쯤이면 우리가 불타는 군단과 그 추종자들을 상대로 싸우는 이유를 분명히 알 거라 믿네.$B$B알도르 사제회에 자네의 실력을 증명해 보이고 싶다면 불타는 군단의 고위 장교와 놈들을 받드는 어둠의 의회 일원들이 지니고 다니는 징표를 가져오게. 여기 어둠달 골짜기의 서쪽으로 한참을 가면 불타는 군단 요새와 죽음의 괴철로가 나오지. 그곳에서 불타는 군단을 발견할 수 있네.', },
    {'어둠달 골짜기에 있는 선구자 사로넨에게 살게라스의 징표 10개를 가져가야 합니다.$B$B알도르 사제회를 위한 퀘스트를 완료하면 점술가 길드에 대한 평판은 떨어질 것입니다.', },
  },
  [10827] = {
    '살게라스의 징표 추가 수집',
    nil,
    nil,
  },
  [10828] = {
    '살게라스의 징표 하나',
    nil,
    nil,
  },
  [10829] = {
    '나무줄기와의 대화',
    {'이거 골치 아프게 됐군! 아라코아 녀석들이 사악한 영혼으로 수호자들과 고대인들을 지배하고 있소이다!$B$B하지만 누구도 영향을 주지 못하는 고대정령 아직 남아 있소이다. 바로 나무줄기지. 까마귀 숲에 있는 나무들을 구하려면 지금 당장 그에게 가야 하오!$B$B이길로 까마귀 숲으로 돌아가시오. 길을 따라 그리쉬나스를 지나면 갈림길이 나오는데 그곳에서 그를 만날 수 있을 것이오.', },
    nil,
  },
  [10830] = {
    '나무의 악마 퇴치',
    {'그리쉬나 아라코아가 사악한 코이코이 영혼들로 하여금 잎사귀수염들과 돌나무껍질들을 지배하게 한 장본인이다.$B$B나무들을 구하지 못하면 곧 까마귀 숲의 모든 생물들이 영원히 사라질 것이다!$B$B너는 이 길로 광포한 까마귀에게서 얻은 깃털과 그리쉬나스 전역에서 찾을 수 있는 그리쉬나스 보주의 마법을 섞어서 퇴마술 깃털을 만들어야 한다.$B$B그 퇴마술 깃털을 사용하여 코이코이 영혼을 잎사귀수염에게서 끌어낸 후 가능한 한 빨리 처치하도록 하라. 이 퇴마술을 사용하기엔 돌나무껍질은 너무도 강력하다만 많은 수의 잎사귀수염들을 구하면 그들 역시 자유로워질 것이다.', },
    nil,
  },
  [10833] = {
    '그림자매듭 재봉술 전문가 되기',
    {'그림자매듭 재봉술 전문가가 되면 일반 재봉사의 두 배에 달하는 어둠 깃든 옷감을 만들 수 있고, 특수한 도안도 몇 가지 얻을 수 있습니다.$B$B물론 일반 재봉사도 어둠의 제단에 갈 수 있지만 그림자매듭 재봉사는 제단에 좀 더 깊이 조화되어 남들은 헤아릴 수조차 없는 무한한 힘을 사용할 수 있습니다.$B$B이 수정을 가지고 어둠달 골짜기의 동부로 가십시오. 황천날개 벌판에서 남쪽으로 이어지는 황천날개 고개로 가십시오. 세계의 끝까지 가면 어둠의 제단을 찾을 수 있을 겁니다. 그곳에서 수정을 사용하십시오.', },
    {'어둠의 제단 근처에서 깊은 어둠의 수정을 사용하여 제단과 깊이 조화한 후 샤트라스의 고난의 거리에 있는 안드리온 다크스피너에게 돌아가십시오.$B$B*경고!* 재봉술 전문화는 하나만 선택할 수 있습니다.', },
  },
  [10834] = {
    '검은눈 그릴록',
    nil,
    nil,
  },
  [10835] = {
    '연금술사 안토니비치',
    {'$N, 피눈물 오크들에 대한 나의 발견을 첫 번째 보고서에 정리했네. 스랄마의 동료 연금술사 안토니비치가 이걸 보면 무척 좋아할 거야. 그도 타락한 오크들에 대해 연구하고 있거든. 우리 오크 동맹에게 그런 비극이 다시는 일어나지 않기를 바라는 마음에서 말일세.$B$B그에게 이 보고서를 전해 주게. 나는 자네가 준 혈액을 계속 연구해야 해서 움직일 수 없군. 행운의 여신이 우리 편이라면 타락한 오크에게 닥친 불행의 원인을 알아낼 수 있겠지.', },
    nil,
  },
  [10836] = {
    '용아귀 요새 침입',
    {'나에겐 자손과 동족을 구할 계획을 세울 시간이 필요하다. 필멸의 존재여, 그대가 그 시간을 벌어주길 바란다.$B$B황천날개 벌판의 동쪽 끝에 있는 용아귀 요새로 가서 눈에 띄는 용아귀 오크를 모두 해치우도록. 잇따른 혼란에 놈들의 계획은 큰 차질을 빚을 수밖에 없을 것이다.$B$B한 가지 조심할 것은 우리 종족 중 일부가 용아귀 부족에게 정신이 오염되어 되돌리기에는 너무 늦은 상태라는 것이다. 그들을 구원할 유일한 방법은 죽음밖에 없다. 그러므로 그들의 목숨을 뺏는 것은 허락하겠노라.', },
    {'어둠달 골짜기, 황천날개 벌판의 창공을 나는 넬타라쿠가 용아귀 오크 15명을 처치해 달라고 했습니다.$B$B용아귀 요새에 있는 어떤 용아귀 오크라도 상관없습니다.', },
  },
  [10837] = {
    '황천날개 마루를 향해!',
    {'황천덩굴 수정이 있으면 나의 정수를 주입할 수 있다. 수정에 깃든 마력이면 포로로 사로잡힌 비룡들을 적의 손아귀에서 충분히 풀어줄 수 있을 것이다.$B$B어둠달 골짜기 반도의 남동쪽으로 떨어져 있는 섬, 황천날개 마루로 가라. 바로 그곳에 황천덩굴 수정들이 있다. 그대의 가방이 가득 찰 정도로 충분히 수정을 모아서 돌아오라.$B$B그리고 $N, 지금 용아귀 부족이 그 섬에 살고 있으니 조심하도록.', },
    nil,
  },
  [10838] = {
    '악마의 탐지기',
    {'이제 악마 탐지기 준비가 다 됐네. 지옥불 분지로 가면 보이는 지옥불 성채 뒤로 가서 탐지기를 땅 위로 소환하게. 그러면 지옥불 성채의 마력에 맞추어 자동으로 조정하기 시작할 거야. 끝나면 탐지기가 분석한 내용을 받아적어서 가져오게...$B$B$N, 준비가 충분히 끝나면 출발하게. 지옥불 성채에서 탐지기를 발견하면 파괴할 지옥 감시자들을 보낼 것은 두말할 필요도 없으니까!', },
    {'지옥불 분지에서 악마 탐지기를 사용해 조정이 끝날 때까지 기다렸다가 분석 결과를 받아적어 와야 합니다.$B$B스랄마에 있는 연금술사 안토니비치에게 악마 탐지기와 악마 탐지기 분석결과를 가져가야 합니다.', },
  },
  [10839] = {
    '장막의 스키스: 테로크의 암흑석',
    {'장막의 스키스는 이 행렬에서 북서쪽으로 테로카르의 외딴 숲에 있는 샤트라스에서는 남쪽에 있습니다. 그곳에 스케티스의 이단자들이 사악한 숭배 제단을 만들었다는 소식이 들려오고 있지요. 스키스의 아라코아를 구출하려면 테로크의 암흑석을 정화해야만 합니다.$B$B이 정화의 마법봉을 테로크의 암흑석에 사용하십시오. 아달의 축복이 있다면 마법봉의 성스러움이 테로크의 암흑석을 충분히 정화할 수 있을 겁니다. 자, 이제 가십시오. 성스러운 빛이 당신을 비추기를...', },
    nil,
  },
  [10840] = {
    '빛의 무덤',
    {'동쪽으로 가면 까맣게 탄 모래 아래에 빛의 무덤이 잠들어 있다네. 그곳은 죽어간 드레나이들의 잃어버린 사후 안식처이지. 우리 행렬의 마차가 공격받기 전만 해도 나는 그 무덤으로 가서 사악한 영혼들을 정화할 계획이었다네.$B$B내가 아는 것이라곤 그곳에 이미 또 다른 종류의 악마들이 가득했었다는 것일세. 심지어 지금도 에테리얼들이 몇 세기 전에 죽은 선조들의 장신구나 유품을 노리고 시체를 훔쳐 가고 있는 참혹한 현실이 계속되고 있다네.$B$B나는 우리 마차를 보호해야 하기 때문에 할 수 있는 일이 거의 없네만, 자네는 그 에테리얼들에게 정의의 심판을 할 수 있지 않겠나?', },
    nil,
  },
  [10842] = {
    '복수심에 불타는 영혼',
    nil,
    nil,
  },
  [10843] = {
    '언제부터인가...',
    {'언제부터인지는 잘 모르겠지만 우리는 오래전부터 칼날첨탑 오우거들과 싸워왔네.$B$B우리가 놈들을 가까스로 밀어낸 덕에 지금 놈들은 칼날첨탑 전초기지만 겨우 보전하고 있지. 자네가 온 길로 되돌아가면 칼날 마루의 남서쪽 끝에 놈들이 있다네.$B$B놈들의 지도자인 그노쉬 브로그내트를 처치해서 자네의 능력을 증명해 보이게.$B$B오우거를 처치하는 실력은 얼마나 되는지 좀 보자고.', },
    nil,
  },
  [10844] = {
    '고통의 괴철로 기지',
    nil,
    nil,
  },
  [10845] = {
    '여왕 처치',
    {'나의 사냥꾼들이 노래하는 마루에서 정찰 임무를 마치고 방금 돌아왔네. 황천의 비룡들이 그곳의 수정에 이끌려 왔는데 지금 숫자가 엄청나게 불어서 우리 마을까지 넘쳐날지도 모르는 위험한 상황일세.$B$B자네가 지금까지 증명해 보인 실력이 성장한 비룡에 맞설 정도인지 보고 싶군.$B$B비늘날개 절벽을 통과해서 남쪽으로 가면 노래하는 마루가 나올 걸세. 그곳으로 들어가서 놈들의 여왕인 공포의 날개를 처치하게.', },
    nil,
  },
  [10846] = {
    '모크나탈 이해하기',
    {'<영혼까지 얼어붙을 듯한 오싹한 눈빛으로 노려보는 레오록스>$B$B모크나탈 마을에 왔군. 하지만, 자네는 우리를 이해하지 못할 걸세. 우리가 죽기 살기로 싸우는 걸 자네는 모르지.$B$B자네가 우리를 이해하려면 전쟁의 쓴맛을 참아 내야 한다네.$B$B남쪽으로 가면 베크니르 아라코아들이 있네. 놈들은 오우거 무리를 잡아다 트로그마의 점령지라는 동굴 안에서 노예로 부리고 있네. 놈들의 지도자인 베크도 그곳에 있지.$B$B놈을 처치하게!', },
    nil,
  },
  [10847] = {
    '스케티스의 눈',
    {'스케티스의 절벽에서 운명의 전조가 시작되었소.$B$B내 동족인 아라코아의 운명은 그때부터 말로 표현할 수 없이 지독한 악마의 기운으로 뒤덮이게 되었지. 우리는 너무나 자주 나쁜 일에 암흑의 마법을 악용했소이다. 그럼에도 나는 아달을 통해서 성스러운 빛과 구원의 진정한 의미를 깨닫게 되어 구원받았소.$B$B테로크는 스케티스에서 암흑의 마법이 깃들어 있는 수정구를 통해 그의 식민지들을 감시하고 있소. 테로카르의 북쪽에 있는 장막의 레스크와 장막의 쉬에노르에서 그 수정구들을 훔쳐 오시오. 테로크가 무엇을 보는지 내가 확인하도록 해 주시오. 스케티스의 눈을 통해서 볼 수 있게 도와 주시오.', },
    nil,
  },
  [10848] = {
    '장막의 라제: 사악한 영혼',
    {'장막의 라제는 스케티스에서 특히나 사악한 곳이었지요.$B$B아킨둔에 일어난 재앙으로 마을이 폭발에 휩쓸려 사라졌지만, 테로크의 영향력은 그곳에 있는 사악한 아라코아의 영혼들을 조종하고 있습니다. 그러니 서쪽의 장막의 라제로 가서 그 잔인한 녀석들을 처치해 주십시오. 해골 무덤과 테로카르 숲의 경계선에 있는 어둠의 무덤의 서쪽에서 그들을 찾을 수 있을 겁니다.$B$B이번 임무가 테로크와 스케티스의 파수병에 대한 선제공격이 되겠군요.', },
    nil,
  },
  [10849] = {
    '키리크 찾기',
    {'다른 아라코아들도 테로크의 배신을 거들었었지.$B$B키리크는 테로크가 예언자가 아니라 사기꾼 같다고 생각했었다오. 실제로 실현된 단 하나의 예언마저도 그의 명령으로 말미암은 파멸뿐이었소.$B$B스케티스에서 용감하게 우리를 이끌고 나온 자도 키리크였지. 샤트라스로 이어진 우리의 순례 후에 키리크는 다른 자들을 찾아서 테로크의 식민지로 여정을 떠났소.$B$B이 서신은 장막의 눈들이 본 내용에 대해 기록되어 있소. 이 내용을 키리크에게 반드시 알려야 하오! 샤트라스의 외곽에서 남쪽으로 난 길을 따라 해골 무덤에 간 다음 피난민 행렬을 찾으시오.', },
    nil,
  },
  [10850] = {
    '지옥 불꽃 엔진에 황천 가스 주입',
    nil,
    nil,
  },
  [10851] = {
    '적군의 토템',
    {'선조를 기리는 방식에는 옳은 방법도 있고 그릇된 방법도 있다네.$B$B칼날첨탑 오우거들은 토템을 지니고 다니지만 존경의 표시로 사용하지는 않네. 대신에 누군가를 조종하고 지배하는 데 사용하지.$B$B우리가 그 토템을 훔쳐와야 하네. 그 따위 놈들에게는 필요 없으니까. 우린 더 좋은 목적에 토템을 사용할 걸세.$B$B서쪽으로 칼날 마루의 바로 남서쪽에 있는 칼날첨탑 전초기지로 가게. 칼날첨탑일족 비술사와 장사들을 처치하고 토템을 가져오게!', },
    nil,
  },
  [10852] = {
    '실종된 친구들',
    {'안녕하세요, $G형:누나;. 지난 밤에 나쁜 놈들이 와서 소란을 일으키는 바람에 마차가 고장 나버리고 말았어요. 정말로요, 너무 무서웠어요. 전 우리가 언제 다시 돌아갈지 잘 모르겠어요.$B$B게다가 제 친구들이 자꾸 사라지고 있어요. 걔들은 대체 어디로 간 걸까요? 그 무시무시한 새인간들이 북서쪽으로 제 친구들을 데려간 거 같아요. 새인간들이 잡아갔으니 아마 새장 같은 곳에 갇혀 있겠죠? $G형:누나;|1이;가; 제 친구들을 찾아주시면 좋겠는데... 잡혀간 친구들이 걱정이 되거든요.', },
    {'장막의 스키스에서 실종된 에단의 친구 12명을 찾은 후, 피난민 행렬에 있는 에단에게 가야 합니다.', },
  },
  [10853] = {
    '영혼 소환',
    {'노래하는 마루에 있는 황천의 비룡의 영혼들이 해방을 부르짖고 있네.$B$B황천의 비룡이 원래부터 황천의 비룡이었던 것은 아니지. 원래는 검은 비룡이었어.$B$B하지만, 이 세계가 파괴될 때 황천 에너지의 파동이 만물에 퍼지면서 비룡의 알이 변형되는 부작용이 생겼지.$B$B지금은 이전 모습에서 뒤틀린 형태라네. 그들에게 평화를 찾아주어야 하네.$B$B영혼 소환의 토템 근처에서 비룡을 처치한 다음 그 토템에서 영혼을 수집해 오게나.', },
    nil,
  },
  [10854] = {
    '넬타라쿠의 힘',
    {'이 마력 깃든 황천덩굴 수정을 가지고 용아귀 요새로 다시 들어가라. 그리고 사로잡힌 비룡들을 찾아서 수정을 사용하라. 수정에서 분출된 나의 힘이 황천날개 비룡과 그들을 사로잡은 녀석 사이의 결속을 풀 것이다. 그렇게 해서 비룡이 자유의 몸이 되면 그들을 사로잡은 자를 해치울 수 있도록 그대가 도와줘야 하노라! 오랜 노예 생활 때문에 분명히 약해져 있을 것이다.$B$B$N, 사로잡힌 황천날개 비룡들을 해방시킨 후 돌아오도록 하라.', },
    {'어둠달 골짜기, 황천날개 벌판의 창공을 나는 넬타라쿠가 사로잡힌 황천날개 비룡 5마리를 구출해 달라고 했습니다.', },
  },
  [10855] = {
    '지옥절단기여, 안녕!',
    {'아, 그 지옥절단기! 불타는 군단이 만든 가장 끔찍한 기계임이 틀림없지. 그 끔찍한 기계가 한 대도 아니고 세 대 이상이 이 폭풍 첨탑을 노리고 있소이다.$B$B다행히도 아직 작동하진 않고 있지만 말이오. 굳이 무력을 사용하지 않고 방해 공작만으로 그 무식한 기계를 처리할 수 있는 여유가 있다는 말이라오.$B$B망각의 괴철로 주둔지에 있는 간아그 녀석들에게서 응축된 황천 가스를 구해 근처에 있는 지옥절단기의 연료통에 채우시오. 아마 엔진이 그 연료를 감당하지 못할 것이오.', },
    {'폭풍 첨탑의 북서쪽으로 가면 망각의 괴철로 주둔지가 있습니다. 그곳에 있는 간아그에게서 응축된 황천 가스 5개를 입수하십시오. 입수한 가스를 근처에 있는 작동하지 않는 지옥절단기에 넣은 후, 황천의 추적자 나우디스에게 가야 합니다.', },
  },
  [10856] = {
    '최고의 방어',
    {'지난번 일은 당신의 도움이 주효했소. 이번에도 위험을 무릅쓰고 녀석들의 작업을 방해한다면, 우리에게 엄청난 도움이 될 것이오. 최고의 방어란 공격이 우선이 아니겠소?$B$B망각의 괴철로 주둔지가 지옥절단기들의 집합 장소로 이용되는 것과 같이, 지옥의 괴철로 주둔지는 대부분의 순간이동기들이 있어 불타는 군단의 군사 작전 지역으로 사용되고 있소이다.$B$B이번엔 지옥의 괴철로 주둔지로 가서 불타는 군단의 숫자를 줄여 주시오. 지금쯤이면 격노의 인도자들의 수가 아주 많을 것이나 그걸 가만히 놔둘 수는 없소이다!', },
    {'황천의 추적자 나우디스가 폭풍 첨탑 북쪽의 지옥의 괴철로 주둔지에 있는 격노의 인도자 12명을 처치해 달라고 했습니다.', },
  },
  [10857] = {
    '순간이동기 파괴',
    nil,
    {'황천의 추적자 나우디스가 지옥의 괴철로 주둔지에 있는 모아그에게 정신 간섭봉을 사용하여, 괴철로 기지에 있는 세 개의 순간이동기에서 폭파 기능을 작동시켜 달라고 했습니다.', },
  },
  [10858] = {
    '카리나쿠',
    nil,
    {'용아귀 요새에 있는 카리나쿠를 찾아야 합니다.', },
  },
  [10859] = {
    '빛구슬 모으기',
    nil,
    nil,
  },
  [10860] = {
    '모크나탈의 대접',
    {'$R, 배가 고픈가? 위대한 영혼은 스스로 돕는 자를 돕는다네. 음식을 구한다면 내 말을 잘 듣게. 브라고르는 절대 공짜로 주는 법이 없거든. 뭐 맛도 별로 없지만!$B$B칼날 마루와 비늘날개 절벽에 있는 랩터와 비늘날개 천둥매들을 재료로 아주 맛있는 요리 두 가지를 만들 수 있어. 그렇게 맛있는 건 아마 처음 먹어볼걸. 랩터 갈비와 천둥매 고기를 가져오면 자네에게 그 요리를 만들어 주겠네.$B$B빨리 구해오면 조리법을 가르쳐줄 수도 있지.', },
    nil,
  },
  [10861] = {
    '장막의 리디크: 선제공격',
    {'남서쪽에 장막의 리디크라고 알려진 암울한 곳에서 테로크의 하수인들이 자신과 같은 조류 형제들이 알을 깨고 세상에 나오기 전에 암흑 마법으로 그 알들을 오염시키고 있습니다. 리디크에 있는 암흑의 마법사들이 알에 어둠의 저주를 퍼붓는 일을 담당하고 있지요. 그렇게 오염된 알에서 나온 새끼들은 넋이 나간 채로 테로크에게 복종하게 되는 것입니다.$B$B이번에는 장막의 리디크로 가서 마법사의 저주가 깊숙이 스며들기 전에 미리 알을 깨 버리는 임무를 해 주셔야겠습니다. 알이 깨져 사악한 아라코아의 새끼가 나왔다면 처치하도록 하십시오.$B$B성스러운 빛은 암흑에서 가장 빛난다는 사실을 기억하시기 바랍니다.', },
    nil,
  },
  [10862] = {
    '호드에 항복하라',
    {'겁쟁이 아라코아가 매일같이 쳐들어오고 있어요. 우리 사냥꾼들을 공격하는 것도 모자라서 가끔은 성벽 안쪽까지 습격할 때도 있습니다. 온 힘을 다해 놈들을 해치우고는 있지만 강력한 마법 때문에 놓치는 경우가 허다합니다.$B$B호드와 우호 관계를 맺고 싶어하는 아라코아가 있다는 사실을 알아냈습니다. 그들은 북서쪽의 샤트라스에 살고 있습니다. 구원된 릴라크가 그들의 우두머리입니다. 고난의 거리에 있는 그를 찾아가서 항복을 권유해 보십시오.', },
    nil,
  },
  [10863] = {
    '아라코아의 비밀',
    {'이곳 국경 지대에서는 매일같이 아라코아를 상대하는 게 일입니다. 녀석들은 기분이 내킬 때마다 난입해서 보급선과 정찰병들을 공격하고 있습니다.$B$B하지만 우리는 이 수수께끼 같은 생물에 대해 아는 게 거의 없는 실정입니다. 여기서 북서쪽에 있는 샤트라스에 가면 우리에게 우호적인 아라코아가 있다는 소식을 들었는데, 그들에게라면 아라코아 종족의 비밀에 대해 뭔가 알아낼 수 있을지도 모릅니다.$B$B구원된 릴라크라는 아라코아가 그들의 우두머리이니 고난의 거리의 북쪽 지역에 가 그를 만나 보십시오.', },
    nil,
  },
  [10864] = {
    '영혼의 짐',
    {'내게 타락한 마력을 분석할 수 있는 악마 탐지기라는 장치가 있다네. 하지만, 탐지기를 사용하려면 먼저 그 장치를 달래줘야 한다네. 탐지기는 안에 아주 똑똑한 생명체 하나를 가둬놓고 있는데 굉장히 특이한 요구를 하고 있거든.$B$B그러니까, 오크들을 처치해서 탐지기를 달래야 해. 정확히 말해서 으스러진 손의 오크들이지. 탐지기는 놈들의 영혼을 원해.$B$B영혼들을 거둬서 나에게 돌아오게. 으스러진 손의 오크들은 북쪽과 남쪽 그리고... 지옥불 성채 주변에 있다네.', },
    {'으스러진 손 오크 20마리를 처치한 다음 스랄마에 있는 연금술사 안토니비치에게 가야 합니다.', },
  },
  [10865] = {
    '레오록스에게 보고!',
    {'어서 레오록스에게 가서 라잔의 영지에서 방출되는 물질에 대해 알려야 하네!$B$B그 빛구슬의 성분은 영혼인데 모크나탈도 포함되어 있어!', },
    nil,
  },
  [10866] = {
    '늙은 줄루헤드',
    {'필멸의 존재여, 놈은 내 생각 속에 숨어 있다. 내가 파괴되기를, 굴복하기를 기다리고 있노라.$B$B이제 놈을 파멸시키리라. 내가 놈을 소환하겠다. 그리고 놈이 바라는 대로 따르겠다고 거짓말을 하리라. 그대는 놈이 나타나면 처치해서 열쇠를 손에 넣도록!$B$B이것만은 알아두기를... 줄루헤드는 막대한 힘을 가진 고대의 오크이다. 분명히 일리단이 놈에게 힘을 부여했을 것이다. 내가 청한 일을 해내려면 나를 위해 목숨까지 걸어야 한다. 희생을 감내할 자신이 있나?', },
    {'늙은 줄루헤드를 처치한 후 손에 넣은 줄루헤드의 열쇠로 줄루헤드의 족쇄를 풀고 카리나쿠를 구출해야 합니다.', },
  },
  [10867] = {
    '눈에는 눈, 이에는 이',
    {'놈들이 우리 선조의 영혼을 모독했으니, 이제 우리가 할 일은 한 가지밖에 없군!$B$B$N, 라잔의 영지로 돌아가서 우리 선조의 영혼이 제자리를 찾고 명예를 회복하도록 도와주게.$B$B그곳으로 돌아가서 라자니 놈들을 계속 처치하다 보면 연합왕자 라잔이 비겁한 얼굴을 드러낼 걸세. 놈을 처치하고 영혼들을 되찾아서 영혼소환사 도가르에게 가져다주게!', },
    nil,
  },
  [10868] = {
    '아라코아의 전쟁 경로',
    nil,
    {'조언자 파일라가 쉬에노르 갈퀴전사 14명, 쉬에노르 마술사 6명을 해치워 달라고 했습니다. 일을 마친 후 돌망치 요새에 있는 그녀에게 가야 합니다.', },
  },
  [10869] = {
    '아라코아 수 줄이기',
    {'아라코아가 또다시 우리를 공격했네. 이번에는 야간을 틈타 공격해 왔다가 우리가 병사들이 출동하기도 전에 흑마법을 사용해서 달아났다네. 녀석들의 소굴을 찾아가서 우리의 무서움을 보여줘야 할 때야.$B$B장막의 레스크와 장막의 쉬에노르는 각각 북쪽과 북서쪽에 있으니 그 아라코아 둥지를 찾아서 최대한 녀석들을 많이 없애 주게나. 녀석들이 우리를 두려워하지 않을지는 몰라도 녀석들의 수는 최대한 줄일 수는 있다네.', },
    {'쉬에노르 갈퀴전사 14명, 쉬에노르 마술사 6명을 처치한 후 알레리아 성채에 있는 부관 그래블해머에게 가야 합니다.', },
  },
  [10870] = {
    '황천날개 용군단의 동맹',
    {'모르데나이에게 돌아가라. 분명 다시 그대를 보고 싶어할 것이다.', },
    {'카리나쿠의 인도를 받아 황천날개 벌판에 있는 모르데나이에게 가야 합니다.', },
  },
  [10871] = {
    '황천날개 용군단의 동맹',
    {'모르데나이에게 돌아가라. 분명 다시 그대를 보고 싶어할 것이다.', },
    {'카리나쿠의 인도를 받아 황천날개 벌판에 있는 모르데나이에게 가야 합니다.', },
  },
  [10872] = {
    '늙은 줄루헤드',
    {'필멸의 존재여, 놈은 내 생각 속에 숨어 있다. 내가 파괴되기를, 굴복하기를 기다리고 있노라.$B$B이제 놈을 파멸시키리라. 내가 놈을 소환하겠다. 그리고 놈이 바라는 대로 따르겠다고 거짓말을 하리라. 그대는 놈이 나타나면 처치해서 열쇠를 손에 넣도록!$B$B이것만은 알아두기를... 줄루헤드는 막대한 힘을 가진 고대의 오크이다. 분명히 일리단이 놈에게 힘을 부여했을 것이다. 내가 청한 일을 해내려면 나를 위해 목숨까지 걸어야 한다. 희생을 감내할 자신이 있나?', },
    {'늙은 줄루헤드를 처치한 후 손에 넣은 줄루헤드의 열쇠로 줄루헤드의 족쇄를 풀고 카리나쿠를 구출해야 합니다.', },
  },
  [10873] = {
    '간밤의 급습',
    nil,
    nil,
  },
  [10874] = {
    '장막의 샬라스: 봉화',
    nil,
    nil,
  },
  [10875] = {
    '나즈그렐에게 보고',
    {'$N, 자네가 악마 탐지기를 가지고 정탐하러 떠난 동안 타락한 오크들을 상대로 이룬 자네의 공적을 전했네. 스랄마의 훌륭한 지도자이신 나즈그렐님께서 자네와 대화하고 싶어하시네. 무척 힘에 겨울 임무를 염두에 두고 계신 듯하던데...', },
    nil,
  },
  [10876] = {
    '지옥불 성채 아래',
    nil,
    nil,
  },
  [10877] = {
    '공포의 유물',
    {'아주 오래전, 죽음의 요새 아래에 있는 고대 무덤에서 공포의 유물을 가져오려는 짐마차 한 대가 아킨둔으로 떠났었지요. 그들은 피하고자 했지만 그 공포의 유물은 어김없이 그들을 파멸의 길로 이끌었습니다. 그 유물은 손을 대기만 하면 악마를 소환하는 악마 그 자체입니다. 그런데 그 짐마차는 동쪽 어딘가에서 파괴되었다고 들었습니다.$B$B그 암흑의 유물은 해골 무덤을 날아다니는 저주받은 테리부스를 처치하는 가공할 계획을 실행할 수 있는 열쇠입니다. 괜찮으시다면 저를 좀 도와주십시오. 하지만 이번 일은 당신의 목숨과 영혼이 위험에 처할 수도 있습니다.', },
    {'동쪽에 있는 버려진 짐마차로 가서 공포의 유물을 입수한 후 오아쿤에게 가야 합니다.', },
  },
  [10878] = {
    '선제공격',
    {'어둠의 의회의 비밀결사단 이교도에게 마차를 습격당한 우리는 샤트라스로 정찰병을 보내 도움을 요청했습니다. 경비병들이 한동안은 안전할 거로 생각하지만 그건 그 미치광이들을 잘 몰라서 하는 말입니다.$B$B한밤중에 비밀결사단 수습생 놈의 칼에 목이 달아나고 싶은 생각은 추호도 없습니다. 부디 놈들이 쳐들어오기 전에 먼저 놈들을 처리해 주십시오.$B$B여기서 곧장 남쪽으로 아킨둔의 북쪽 입구로 가면 우리를 공격한 놈들을 찾을 수 있습니다.', },
    nil,
  },
  [10879] = {
    '스케티스의 공세',
    nil,
    nil,
  },
  [10880] = {
    '비밀결사단 명령서',
    {'검은 인장이 찍힌 이 명령서는 익숙한 언어처럼 보이긴 하지만 암호로 작성되어 있습니다.$B$B피난민 행렬로 가면 이 전갈에 대해 아는 이를 만날 수 있을지도 모릅니다.', },
    nil,
  },
  [10881] = {
    '어둠의 무덤',
    {'많은 이들은 아킨둔을 갈가리 찢어놓은 폭발의 주범이 어둠의 의회라고 믿고 있습니다. 또한, 그들이 그 안에서 굉장한 악마를 깨웠다는 소문도 있답니다.$B$B그게 사실이라면 그 악마는 어둠의 의회한테 등을 돌린 게 분명해요. 이 명령서에 따르면 일부 어둠의 의회 일원들이 아킨둔에서 달아나고 있습니다. 그들은 어둠의 무덤에 아킨둔의 강력한 유물들을 보관하고 있습니다.$B$B아루넨, 칼렌, 아탈로르라는 강력한 족장들이 소유했던 신성한 3가지 유물을 회수해 오십시오.', },
    {'피난민 행렬 서쪽에 있는 어둠의 무덤으로 가서 아루넨의 망토, 칼렌의 재판봉, 아탈로르의 두루마리를 찾은 후, 피난민 행렬에 있는 메케다에게 가야 합니다.', },
  },
  [10882] = {
    '파멸의 전조',
    {'<뭔가 이상한 느낌이 드는 아달의 목소리>$B$B캘타스 왕자가 교도관을 잘못 뽑는 바람에 현재 우리가 알카트라즈에 가둬 놓은 죄수들이 탈출하고 있소.$B$B특히 그중 선구자 스키리스라는 고대 신의 부하는 자신의 주인이 우주를 지배하게 하려는 야망을 품고 있다고 하오.$B$B$N, 녀석이 탈출하지 못하도록 막아야 하오. 폭풍우 요새로 돌아가시오. 알카트라즈로 들어가 선구자 스키리스를 처치하시오. 너무 늦기 전에!', },
    {'폭풍우 요새의 알카트라즈로 가서 선구자 스키리스를 처치해야 합니다. 일을 마친 후 샤트라스의 빛의 정원에 있는 아달에게 돌아가십시오.', },
  },
  [10883] = {
    '폭풍우 열쇠',
    nil,
    {'샤트라스에 있는 아달과 대화해야 합니다.', },
  },
  [10884] = {
    '나루의 시험: 자비',
    nil,
    {'샤트라스에 있는 아달이 지옥불 성채의 으스러진 손의 전당에서 사용되지 않은 집행자의 도끼를 가져다 달라고 했습니다.$B$B이 퀘스트는 던전 난이도를 영웅으로 설정한 후 수행해야 합니다.', },
  },
  [10885] = {
    '나루의 시험: 힘',
    {'나루에게 가치 있는 존재로 인정받으려면 4가지 시험에 모두 통과해야 한다오.$B$B힘의 시험은 두 가지로 나뉘어 있소. 갈퀴송곳니 저수지의 증기 저장고의 지휘관인 칼리스레쉬 장군을 처치한 후 칼리스레쉬의 삼지창을 가져오시오. 그리고 아킨둔의 어둠의 미궁에 가면 울림이라고 하는 다른 차원에서 온 존재가 있는데 그를 해치운 후 정수를 거둬 오시오.$B$B이 일을 마치면 그대는 두 가지 힘의 시험에 통과하게 되는 것이오.', },
    {'샤트라스에 있는 아달이 칼리스레쉬의 삼지창, 울림의 정수를 가져다 달라고 했습니다.$B$B이 퀘스트는 던전 난이도를 영웅으로 설정한 후 수행해야 합니다.', },
  },
  [10886] = {
    '나루의 시험: 끈기',
    {'나루에게 가치 있는 존재로 인정받으려면 4가지 시험에 통과해야 하오.$B$B우리는 이번 시험의 과정에서 수많은 위험한 존재들을 만났소이다. 그중 일부는 우리 나루족이 폭풍우 요새의 알카트라즈라고 알려진 곳에 감금해 두었지.$B$B그중 한 수감자의 경우는 우연한 사건으로 죄수가 되었다오. 그는 좋지 않은 시기에 잘못된 시간에 엉뚱한 나루 함선으로 밀항한 자였지.$B$B알카트라즈로 가서 그 노움을 구해 주시오. 그의 이름은 밀하우스 마나스톰이라고 하오이다.', },
    {'샤트라스에 있는 아달이 폭풍우 요새의 알카트라즈에서 밀하우스 마나스톰을 구출해 달라고 했습니다.$B$B이 퀘스트는 던전 난이도를 영웅으로 설정한 후 수행해야 합니다.', },
  },
  [10887] = {
    '무덤 탈출',
    {'$N, 도와주셔서 감사합니다. 맞아 죽는 줄로만 알았거든요.$B$B어서 여기서 나갑시다. 곧 더 많은 이교도가 우리를 쫓아올 겁니다.', },
    nil,
  },
  [10888] = {
    '나루의 시험: 마그테리돈',
    {'이제 나루에게 가치 있는 존재로 인정받기 위한 4가지 시험 중 마지막 시험만이 남았소.$B$B필멸의 존재여, 군대를 모아 지옥불 성채의 깊숙한 곳에 있는 마그테리돈의 둥지로 가시오. 그 둥지의 심장부로 가 사악한 파괴자를 처치하시오!', },
    {'샤트라스에 있는 아달이 마그테리돈을 처치해 달라고 했습니다.', },
  },
  [10889] = {
    '샤트라스로 돌아가기',
    {'당신의 용감무쌍한 행동이 테로크의 복수심을 불러일으켰을 겁니다.$B$B서둘러서 샤트라스로 가서 릴라크에게 소식을 전하십시오!', },
    nil,
  },
  [10893] = {
    '꼬리가 길면 잡히는 법',
    {'검은날개 고룡교단 녀석들이 굴을 가득 메우고 있기 때문에 사태를 해결하기가 훨씬 어렵게 됐습니다. 솔직히 말해 녀석들의 숫자가 너무 엄청납니다.$B$B지금까지는 가까이 접근해 오는 녀석들만 상대해 왔을 뿐입니다. 녀석들이 본격적으로 공격해 온다면 우리는 괴멸당할 게 분명합니다.$B$B상황이 이렇게 된 이상 누군가 굴 반대쪽으로 가서 녀석들의 지도자인 드라카 롱테일을 처치해야 합니다. 자, 이제 악의 무리를 처치하는 재미를 좀 보시렵니까?', },
    nil,
  },
  [10894] = {
    '고룡해골의 감시자',
    {'흠, 뭘 어떻게 해야 할지 모르겠군. 내가 뭐 대단한 군사 계획자도 아니고 말이오.$B$B아마도 감시자 문셰이드라면 어떻게 해야 할지 알 것이오. 그는 고룡해골 다리의 서쪽 끝에 있는 고룡해골 굴에서 방어 작전을 지휘하고 있소이다. 우리는 혹시라도 그 이단자들이 수상한 짓을 벌일지 몰라 그곳에 병력을 배치해 놓았지.$B$B자, 그에게 이 회의록을 최대한 빨리 가져다주시오.', },
    {'칼날 산맥의 고룡해골 굴 입구에 있는 감시자 문셰이드에게 회의록을 가져가야 합니다.', },
  },
  [10895] = {
    '제스고르를 불태워라!',
    {'드디어 제스고르를 파괴해야 할 때가 왔습니다! 우리 그리핀 기수들이 출격을 기다리고 있습니다만, 그들에게는 지상에서 공격을 안내해 줄 자가 필요합니다. 이 비밀 특명을 수행할 준비가 되었습니까?$B$B좋습니다, 이 연기 봉화를 가지고 서쪽에 있는 피눈물 부족의 요새, 제스고르로 가십시오. 그곳에는 모두 네 개의 경비탑이 있는데, 북쪽 성벽과 남쪽 성벽, 그리고 녀석들의 괴철로 근처에 하나, 구릉지 안에 또 하나가 있습니다. 각 경비탑 밑에 연기 봉화를 하나씩 놓으십시오. 그럼 우리가 그 표시를 보고 날아가서 폭탄을 투하하겠습니다!', },
    {'제스고르의 각 경비탑에 연기 봉화를 사용한 다음, 원정대 거점에 있는 편대사령관 다비리에게 가야 합니다.', },
  },
  [10896] = {
    '만연하는 수호자들',
    nil,
    nil,
  },
  [10897] = {
    '물약의 대가',
    {'그대도 알다시피 모든 식물은 각자의 특성이 있고, 연금술사는 그 고유의 특성을 이용하여 물약을 만들어 다른 이들을 되살리지요.$B$B아웃랜드에 서식하는 식물에 대한 제 해박한 지식이라면 연금술사들이 식물의 고유 특성을 최대한 활용하는 방법을 익힐 수 있을 겁니다. 물약에 대한 지식을 발전시키고 싶으시다면 제 부탁을 하나 들어주십시오.$B$B폭풍우 요새의 고위 식물학자 프레이윈이 이해하기 쉬운 식물학 도감을 저술했다고 합니다. 그대가 배울 표본과 함께 그 책을 가져다주시면 원하는 지식을 가르쳐 주겠습니다.', },
    {'라우란나 사르웰이 폭풍우 요새의 신록의 정원으로 가서 고위 식물학자 프레이윈에게 식물학자의 휴대용 도감을 얻어다 달라고 했습니다. 또한 특급 치유 물약, 5 특급 마나 물약, 일급 숙면의 물약을 각각 5개씩 가져다 달라고 했습니다.$B$B*경고!* 하나의 연금술 전문화만 선택할 수 있습니다.', },
  },
  [10898] = {
    '하늘날개',
    {'하늘날개가 당신 주변을 서성입니다.$B$B따라오라고 말하는 듯한 느낌이 듭니다.', },
    {'하늘날개를 따라가면서 호위한 후 샤트라스에 있는 구원된 릴라크에게 보고해야 합니다.', },
  },
  [10901] = {
    '카르데쉬의 곤봉',
    nil,
    {'갈퀴송곳니 저수지의 용사 강제 노역소에 있는 이단자 스카디스가 땅의 인장과 불의 인장을 가져다 달라고 했습니다.', },
  },
  [10902] = {
    '비약의 대가',
    {'최고의 비약을 만들기 위해 제 일생을 바쳤습니다. 하지만, 아직 영생의 비약 제조에는 성공하지 못했습니다.$B$B빠뜨린 재료가 뭔지 알 것 같아요. 아제로스에 시간의 동굴이라는 곳이 있는데 균열의 군주와 균열의 문지기라고 하는 놈들이 시간을 멈췄다 풀었다 한다고 들었습니다.$B$B놈들은 시간 자체를 찢고 무한히 왜곡해서 결정화된 정수로 만들고 있습니다. 정수와 함께 몇 가지 비약을 만들어서 가져오십시오. 그러면 제 지식을 전수해 드리겠습니다.', },
    {'시간의 동굴의 검은늪에 가서 균열의 군주와 균열의 문지기를 처치하고 무한의 정수 10개를 획득해야 합니다. 무한의 정수와 함께 일급 방어의 비약, 정통의 비약, 일급 민첩의 비약을 5개씩 만들어서 샤트라스, 고난의 거리에 있는 로로킴에게 가져가십시오.$B$B*경고!* 연금술 전문화는 하나만 선택할 수 있습니다.', },
  },
  [10903] = {
    '명예의 요새로 돌아가기',
    {'당신이 제스고르에서 임무를 수행하는 동안 난 내 상관이신 줄리스 대령님에 대한 소식을 받았소이다. 그분께서는 고르가즈 전초기지에서 전투를 벌이던 중, 마력에 오염되어 지금 매우 심각한 상황에 처해 있다고 하오이다.$B$B당신은 우리가 타락한 오크를 상대로 우리가 우위에 설 수 있도록 도움을 주었소이다. 그렇기에 당신의 전투 실력은 두말할 나위가 없다는 것을 알고 있소. 이번에는 당신의 영혼이 얼마나 강한지 보여주겠소?$B$B명예의 요새로 돌아가서 명예의 요새 여관 위층 방에 있는 클라투와 얘기를 나눠보시오.', },
    {'명예의 요새 여관에 있는 수행원 클라투와 대화해야 합니다.', },
  },
  [10904] = {
    '지옥 포탄 수집',
    {'불타는 군단이 죽음의 문에 접근하지 못하게 막으려면, 우선 그들의 공간이동 관문을 파괴해야 합니다!$B$B문제는 우리의 마법으로는 공간이동 관문에 흠집조차 낼 수가 없다는 것입니다. 따라서 그대가 그들의 무기로 공간이동 관문을 파괴해야 합니다.$B$B이곳의 죽음의 용사, 죽음의 감시자, 죽음의 괴철로 수석대장장이, 죽음의 괴철로 기술병이 그 지옥 대포의 탄약을 가지고 다닙니다.$B$B일반적인 방법으로는 공간이동 관문을 파괴할 수 없지만, 제게 그 포탄을 가져오시면 그 포탄에 자연의 분노를 부여하여 죽음의 문을 파괴할 수 있을 겁니다!', },
    nil,
  },
  [10905] = {
    '물약의 대가',
    nil,
    nil,
  },
  [10906] = {
    '비약의 대가',
    nil,
    nil,
  },
  [10908] = {
    '구원된 릴라크와의 대화',
    {'나와 같은 아라코아가 말을 걸어 놀란 것 같군요. 그렇다고 당신을 비난하진 않으니 걱정하지 마십시오. 내 동족인 아라코아의 모습과 행태가 다른 종족의 두려움을 살만 하다는 건 알고 있습니다.$B$B우리 아라코아에 대해서 더 알고 싶다면 샤트라스에 있는 구원된 릴라크와 얘기를 나눠 보십시오. 그 친구는 고난의 거리의 북쪽에서 스케티스에서 온 다른 추방자들과 함께 있을 겁니다.$B$B그리고 우린 아마 다시 만나게 될 겁니다.', },
    nil,
  },
  [10909] = {
    '타락한 영혼',
    {'줄리스 대령이 고르가즈 전초기지 근처에서 벌어진 전투에서 저주를 받아 으스러진 손의 타락한 오크에게 영혼을 사로잡히고 말았네. 내 스승이신 바라다님께서 그 가엾은 인간을 위해 온 힘을 다해 노력하고 계시지만 여전히 도움이 필요하다네. 고통에 빠진 줄리스 대령의 영혼은 평화를 애타게 갈구하고 있네. 그가 평화를 되찾는 길은 복수를 하는 것뿐이야.$B$B이 수도자의 유물을 가지고 고르가즈 전초기지로 가게나. 그것을 으스러진 손 오크 근처에 놓고 녀석들을 처치하게. 이렇게 하면 녀석들의 타락한 영혼이 풀려나게 될 것이야. 그 영혼들을 모두 없애야 줄리스 대령이 자신의 영혼을 되찾을 수가 있네.', },
    {'명예의 요새에 있는 클라투가 명예의 요새의 남서쪽에 있는 고르가즈 전초기지에서 수도자의 유물을 사용하여 근처에 있는 으스러진 손 광전사들을 처치한 다음, 풀려난 타락한 영혼 10마리를 처치하여 대령 줄리스의 복수를 해 달라고 했습니다.', },
  },
  [10910] = {
    '죽음의 문',
    {'불타는 군단에게서 비롯된 더욱 큰 위협이 다가오고 있소이다. 이곳에서 남쪽에 죽음의 문이라고 알려진 차원문이 있소. 그 문은 불타는 군단이 그들의 치명적인 사냥개를 기르는 끔찍한 세계로 연결되지.$B$B죽음의 문으로 가려면 동쪽에 있는 경사로를 올라가서 영원의 숲을 나가야 하오. 그리고 칼날 협곡, 라잔의 영지, 칼날 마루와 비늘날개 절벽을 지나서 남쪽 길을 따라가시오. 그러고 나서 베크하르 고원으로 들어가면 내리막으로 내려가 모크나탈 마을의 남쪽에 있는 굴을 통과하면 죽음의 문에 도착하게 되오.$B$B그곳에 가서 이 드루이드 신호탄을 사용하면 그대의 부름에 응하는 자가 있을 것이오.', },
    nil,
  },
  [10911] = {
    '자유 사격!',
    nil,
    nil,
  },
  [10912] = {
    '사냥개군주 바엘몬',
    {'이제 사냥개군주 바엘몬을 반드시 처치해야 합니다! 그는 차원문의 기능을 유지하는 의식의 책임자입니다.$B$B바엘몬이 의식을 치르는 죽음의 문은 계곡의 북쪽 끝에 있습니다. 차원문을 발견하면 그 앞에서 의식을 치르는 바엘몬을 볼 수 있을 겁니다.$B$B우리 일을 도와주셔야 하니 이 마법봉을 받으십시오. 사냥개군주와 싸울 때 그 마법봉을 사용하면 제 동료 몇 명이 그대를 도우러 올 겁니다.$B$B바엘몬을 처치하면 야생의 군주 안텔라리온에게 돌아가십시오.$B$B마지막으로 저희를 도우러 여기까지 와 주셔서 정말 고맙다는 말을 하고 싶군요.', },
    nil,
  },
  [10913] = {
    '부적절한 매장',
    {'오염이 퍼지는 걸 막을 방법은 정녕 없는 것입니까? 제 병사들이 며칠째 아키나이와 끝이 보이지 않는 싸움을 하고 있습니다. 그런데 아키나이든 샤타르든 전장에서 죽은 그들의 시체가 사라지고 있어 의혹이 생기고 있습니다. 사라진 시체들의 영혼을 없애고 어딘가로 운반한 것이 분명합니다.$B$B이런 일을 그냥 두고 볼 수는 없습니다! 예를 갖춰 시체를 매장하는 것은 불가능하지만, 그 악마들이 그들의 영혼을 차지하지 못하게 해야 합니다! 이 횃불을 가지고 여기서 바로 동쪽에 전투가 벌어졌던 곳으로 가십시오. 그곳에서 보이는 시체를 모조리 화장하십시오! 녀석들의 의식에 시체가 사용되도록 놔두진 않을 것입니다.', },
    nil,
  },
  [10914] = {
    '영웅 되기',
    {'이 정도로는 부족합니다. 아키나이의 숫자가 계속 늘어나고 있어서 우리 군대로는 역부족입니다. 이제 영웅이 필요한 때입니다.$B$B<당신을 바라보는 지휘관 라바즈>$B$B영웅이 되고 싶지 않습니까? 전장으로 돌아가서 아키나이 녀석들을 쓰러뜨려 주십시오. 녀석들의 이목이 쏠린 사이, 우리 요원들을 적진 후방으로 침투시켜서 그들이 뭘 만들고 있는지 알아내도록 하겠습니다.', },
    nil,
  },
  [10915] = {
    '죽은 총독',
    {'녀석들이 아키나이 납골당에서 끄집어낸 아키나이 석관에 훔쳐낸 영혼들을 집어넣고 있습니다. 제 정찰병들에 따르면 그 아키나이 석관에 납골당의 1대 총독의 영혼도 포함되어 있다고 하더군요. 녀석들이 고대의 악마를 부활시키려고 하는 것이 분명합니다!$B$B아킨둔의 서쪽으로 가서 녀석들이 아키나이 석관에 영혼들을 다 집어넣기 전에 그걸 파괴하십시오. 만약 길을 잃으면 떠돌아다니는 영혼들을 따라가면 됩니다.$B$B아키나이 석관 안에 있는 걸 모조리 파괴하십시오!', },
    nil,
  },
  [10916] = {
    '드레나이 묵주 찾기',
    {'바라다님께서는 줄리스 대령 안에 있는 타락한 영혼을 쫓아내는 계획을 세우고 계신데 그 일을 도와줄 조수를 찾고 계시네. 그런 일의 조수가 되려면 충분한 준비가 되어 있어야 하지...$B$B그런데 부끄럽게도 나는 명예의 요새 안마당에서 묵주를 잃어버렸다네. 명예의 요새에 사는 페이페이가 가져가서 어딘가에 파묻은 것이 분명한데, 어디에 묻었는지 도통 알 수가 없단 말이야. 강아지 주인인 트레이시 프라우드웰 준위에게 물어보면 페이페이가 훔친 보물을 어디에 숨기는지 알 수 있을지도 모르니 그녀를 만나보게.', },
    {'드레나이 묵주를 찾아 명예의 요새에 있는 수행원 클라투에게 가져가야 합니다.', },
  },
  [10917] = {
    '위기의 추방자',
    nil,
    {'샤트라스의 고난의 거리에 있는 베칵스에게 아라코아 깃털 30개를 가져가야 합니다.', },
  },
  [10918] = {
    '깃털 추가 수집',
    nil,
    nil,
  },
  [10919] = {
    '페이페이의 먹이',
    nil,
    nil,
  },
  [10920] = {
    '죽은 자들을 위해',
    {'공포의 송곳니 과부거미가 우리 전초기지를 습격했을 때, 우리를 전장으로 부르는 나루의 목소리가 들렸습니다. 우리는 밤새도록 그 거미들을 추적했습니다. 추적 중에 우리는 어둠 속에 죽어 있는 동족 일부를 발견했고, 나머지 인원들이 거미에 대항해서 싸우는 소리를 들을 수 있었지요.$B$B아침이 밝았을 때 우리는 이곳까지 밀려와서 격렬한 전투를 벌였지만 결국 패하였습니다. 저 옆에 상처입은 제 동료는 거미의 독에 곧 목숨을 잃을 것이고, 나도 얼마 버티지 못할 겁니다. 죽기 전에 공포의 송곳니 과부거미가 죽었다는 소식을 듣고 싶습니다. 이 드레나이의 죽기 전 소망을 들어주시겠습니까?', },
    {'공포의 송곳니 과부거미 20마리를 처치한 후, 황천그물 마루의 서쪽에 있는 구원자 헤이렌에게 가야 합니다.', },
  },
  [10921] = {
    '테로카란툴라',
    {'당신이 많은 공포의 송곳니 과부거미들을 처치해 줬지만, 그 거미들은 다시 번식을 해서 공격해 올 겁니다. 황천그물 마루 깊숙이 들어가면 그 소름끼치는 생물들의 여왕인 테로카란툴라의 둥지가 있습니다. 우리의 안전을 위해서는 그 거미를 처치해야 합니다.', },
    {'테로카란툴라를 처치한 다음 황천그물 마루의 서쪽에 있는 구원자 헤이렌에게 가야 합니다.', },
  },
  [10922] = {
    '해골 사이에서의 발굴 작업',
    nil,
    nil,
  },
  [10923] = {
    '다가오는 악마',
    {'저주받은 테리부스에게 도전하고 싶다면 사악한 강령술 의식에 이 공포의 유물을 사용해야만 합니다. 이곳에서 북동쪽으로 가면 아킨둔 너머에 강령술사들이 모이는 고뇌의 언덕이라는 곳이 있습니다.$B$B그곳에 있는 아키나이 죽음의 예언자와 파멸의 예언자들은 강력한 강령술 강화 마법이 걸려 있는 파멸의 해골을 가지고 다닙니다. 그 해골을 충분히 모으면 공포의 유물의 힘과 더불어 해골의 힘을 사용하여 테리부스를 불러낼 수 있을 겁니다. 테리부스를 처단하고 샤타르를 구원해 주십시오.', },
    {'오아쿤이 공포의 유물을 고뇌의 언덕으로 가지고 간 후, 아키나이 죽음의 예언자와 아키나이 파멸의 예언자에게서 파멸의 해골 20개를 모아달라고 했습니다. 해골을 모두 모으면 고뇌의 언덕 마법진에서 공포의 유물을 사용하여 저주받은 테리부스를 불러내서 처치한 후, 오아쿤에게 가야 합니다.', },
  },
  [10924] = {
    '차원의 추격자 피',
    {'이 지역에 있는 차원의 추격자 피는 강력하고 희귀한 재료이자 내가 파는 물건 중에 가장 잘나가는 것이기도 하답니다.$B$B보통은 내 임프 제피트를 데려가서 그 피를 모아오곤 하지요. 그런데 말입니다... 차원의 추격자 피는 부식성이 너무 강해서 당신이나 나로서는 안전하게 다룰 수가 없답니다. 하지만 제피트라면 아무런 문제가 없지요.$B$B여기 상점의 일이 점점 바빠져서 말인데 제피트를 딸려 보내 드릴 테니 가서 그 피를 좀 모아 주시겠습니까? 뭐, 임프가 다 그렇지만 녀석은 주의가 꽤 산만한 편이니 잘 관리하셔야 할 겁니다. 차원의 추격자가 죽으면 제피트가 작업을 할 수 있도록 차원의 추격자 가까이에 있도록 하십시오.', },
    {'비틀린 우주에 있는 사보스에게 차원의 추격자 피 8병을 가져가야 합니다.', },
  },
  [10925] = {
    '다가오는 악마',
    nil,
    nil,
  },
  [10926] = {
    '샤타리 주둔지로 돌아가기',
    {'으, 그 송곳니 거미들의 독이 너무나도 강하군요. 나와 우리 동료의 힘으로는 도저히 주둔지로 돌아갈 수 없을 것 같습니다. 당신이 우리를 위해 샤타리 주둔지로 돌아가서 정찰병 나브린을 만나 주십시오. 그러면 그가 구조대를 보낼 겁니다.', },
    nil,
  },
  [10927] = {
    '동굴 거미 처치',
    nil,
    nil,
  },
  [10928] = {
    '동굴 거미 처치',
    nil,
    nil,
  },
  [10929] = {
    '둥둥북',
    {'우리 탐험가 연맹을 위해 간단한 일 하나만 더 해주시겠어요? 우리는 그 뼈미늘벌레에 대해 관심이 많습니다.$B$B여기 북을 하나 드릴 테니 해골 무덤으로 가져가세요. 북을 치고서 어떤 일이 일어나는지 지켜보기만 하세요. 벌레가 나타나면 처치한 후 연구에 쓸 시체를 가져오세요. 실험에 쓸만한 표본을 얻으려면 시체가 많이 필요하답니다.', },
    {'해골 무덤의 샤타리 주둔지에 있는 드워포위츠가 땅굴 뼈미늘벌레 성충 시체 8개를 가져다 달라고 했습니다.', },
  },
  [10930] = {
    '뼈미늘벌레 성충',
    nil,
    nil,
  },
  [10931] = {
    '엑소다르로 돌아가기',
    {'엑소다르에 있는 당신의 상급자인 캐드모스가 당신에게 만나자는 전갈을 보내왔습니다. 훈련과 관련된 것 같은데 그 이상은 모르겠군요. 어서 그에게 돌아가 보는 게 좋겠습니다.', },
    {'엑소다르에 있는 캐드모스와 대화해야 합니다.', },
  },
  [10932] = {
    '엑소다르로 돌아가기',
    {'엑소다르에 있는 당신의 상급자인 캐드모스가 당신에게 만나자는 전갈을 보내왔습니다. 훈련과 관련된 것 같은데 그 이상은 모르겠군요. 어서 그에게 돌아가 보는 게 좋겠습니다.', },
    {'엑소다르에 있는 캐드모스와 대화해야 합니다.', },
  },
  [10933] = {
    '실버문으로 돌아가기',
    {'실버문에 있는 그대의 스승인 알드래가 그대를 만나고 싶어합니다. 훈련에 대한 것 같지만 그 이상은 잘 모르겠습니다.', },
    {'실버문에 있는 알드래와 대화해야 합니다.', },
  },
  [10934] = {
    '실버문으로 돌아가기',
    {'실버문에 있는 그대의 스승인 알드래가 그대를 만나고 싶어합니다. 훈련에 대한 것 같지만 그 이상은 잘 모르겠습니다.', },
    {'실버문에 있는 알드래와 대화해야 합니다.', },
  },
  [10935] = {
    '대령 줄리스의 퇴마 의식',
    {'이제 줄리스 대령을 구할 시간일세. 그의 상태는 더욱더 악화되었네. 바라다님께서는 우리가 줄리스 대령의 영혼을 정화하지 못해 그가 영영 우리에게서 떠나가게 될까 우려하고 계시다네.$B$B자네가 찾은 이 기원의 묵주를 가지고 가서 바라다님과 대화를 나눈 뒤 퇴마 의식을 치르도록 하게. 퇴마 의식을 막는 어떤 혼란도 일어나서는 안 된다는 것을 명심하게나. 모든 의식이 잘되면 줄리스 대령의 영혼을 구해낼 수 있을 걸세. 줄리스 대령을 구하고 나서 그와 얘기를 나눈 후 내게 돌아오게나.', },
    {'수도자 바라다와 대화한 후 기원의 묵주를 사용해서 퇴마 의식을 도우십시오. 대령 줄리스를 구해내 그와 얘기를 나눈 후, 수행원 클라투에게 가야 합니다.', },
  },
  [10936] = {
    '트롤베인의 부름',
    {'우리가 바쁘게 의식을 치르는 사이, 전령이 와서 여관 1층에서 기다리고 있었다네. 그런데 너무 피곤했던 건지 아니면 줄리스 대령이 방 안을 떠다니는 것을 보고 기겁을 했는지 갑자기 사라져 버렸지 뭔가.$B$B아무튼 전투사령관이신 다나스 트롤베인 님께서 자네를 찾고 계시네. 최대한 빨리 병영으로 가서 그분을 만나게나!', },
    {'수행원 클라투가 전투사령관 다나스 트롤베인이 당신을 찾고 있다는 소식을 전해 주었습니다. 지옥불 반도의 명예의 요새 병영 안에 있는 그와 대화해야 합니다.', },
  },
  [10937] = {
    '훈련교관 처치',
    {'지옥불 성채의 전방에서 으스러진 손의 타락한 오크들이 총공격을 준비하고 있는 것 같다는 보고를 받았네. 그 타락한 오크들은 그동안 모든 오크 부족들이 자네에게 패배한 것에 앙심을 품고 있는 것이 분명하네.$B$B영광의 길이 끝나는 곳에 있는 성채의 기지로 가게나. 그곳에서 타락한 오크들이 공격 준비에 여념이 없는 걸 볼 수 있을 게야. 이번에 자네가 할 일은 그곳의 오크들을 불시에 습격하는 것일세.$B$B부대로 잠입한 다음 부대를 이끌고 있는 자인 훈련교관 주록을 처치하고 돌아오게!', },
    {'최대한 빨리 훈련교관 주록을 처치한 후, 지옥불 반도의 명예의 요새 병영 안에 있는 전투사령관 다나스 트롤베인에게 가야 합니다.', },
  },
  [10938] = {
    '다크문 축복 카드 한 벌',
    nil,
    {'다크문 축제가 열리면 축복 카드 한 벌을 돌려줘야 합니다.', },
  },
  [10939] = {
    '다크문 폭풍 카드 한 벌',
    nil,
    {'다크문 축제가 열리면 폭풍 카드 한 벌을 돌려줘야 합니다.', },
  },
  [10940] = {
    '다크문 원한 카드 한 벌',
    nil,
    {'다크문 축제가 열리면 원한 카드 한 벌을 돌려줘야 합니다.', },
  },
  [10941] = {
    '다크문 열광 카드 한 벌',
    nil,
    {'다크문 축제가 열리면 열광 카드 한 벌을 돌려줘야 합니다.', },
  },
  [10942] = {
    '어린이 주간',
    {'요즘과 같은 어린이 주간에 한 특별한 여자아이가 얼마 동안이라도 자네를 친구로 삼으면 매우 기뻐할 걸세.$B$B자, 이 호루라기를 받게나. 이 호루라기를 사용하면 자네가 후원하기로 한 아이를 부를 수 있게 된다네. 사실 이 호루라기는 어린이 주간이 실시되는 제한된 기간 동안만 유지된다네.$B$B부탁하니 후원하기로 한 아이를 만나 주게. 그 아이가 자네를 만나면 정말 행복해 할 것임은 물론이고 아이에게 도시를 벗어난 모험을 떠날 기회를 줄 수 있는 걸세!', },
    nil,
  },
  [10943] = {
    '어린이 주간',
    {'요즘과 같은 어린이 주간에 한 특별한 여자아이가 얼마 동안이라도 자네를 친구로 삼으면 매우 기뻐할 걸세.$B$B자, 이 호루라기를 받게나. 이 호루라기를 사용하면 자네가 후원하기로 한 아이를 부를 수 있게 된다네. 사실 이 호루라기는 어린이 주간이 실시되는 제한된 기간 동안만 유지된다네.$B$B부탁하니 후원하기로 한 아이를 만나 주게. 그 아이가 자네를 만나면 정말 행복해 할 것임은 물론이고 아이에게 도시를 벗어난 모험을 떠날 기회를 줄 수 있는 걸세!', },
    nil,
  },
  [10944] = {
    '위기에 빠진 비밀',
    {'날 구해주어 다시 한번 감사 드립니다. 내 당신의 이름을 기억하고 있었습니다. 아카마가 일리단에게 대항하려는 그의 계획을 당신에게 맡겼었으니 나도 당신을 믿어보도록 하겠습니다.$B$B나가들은 내가 일리단에 대항하던 걸 알고는 날 고문하면서 비밀을 털어놓게 하려 했었지요. 혹독한 고문이었지만 아카마가 깊은 명상을 통해 큰 고통을 이겨내는 법을 가르쳐 주었기에 난 한마디도 발설하지 않았습니다.$B$B일리단이 그 나가들에게 날 데려오라고 명령했었지요. 그는 내 육체를 꿰뚫고 내 영혼 깊은 곳의 비밀을 들여다보고자 했었을 겁니다.$B$B아카마에게 우리의 계획이 위험에 처했다고 경고해야만 합니다!', },
    {'어둠달 골짜기에 있는 감시자의 수용소로 가서 아카마를 만나야 합니다.', },
  },
  [10945] = {
    '히추와 버섯 인간들',
    {'히추라는 여자 아이를 아는데요. 그 친구는 못된 곰팡이 거인한테 쫓기다 길을 잃어서 고아원으로 왔었지요.$B$B우린 정말 재미있게 놀았어요. 그런데 작년에 걔네 종족이 와서 데려갔어요. 스포어가르라는 곳으로 간다고 들은 것 같은데...$B$B그 마을로 가서 히추도 만나고 버섯도 구경하고 싶어요. 보육원 원장님 말씀으로는 장가르 습지대의 서쪽, 포자바람 호수랑 수렁등불 호수 사이에 있다고 했어요.$B$B저 좀 데려다주세요. 제발! 이렇게 부탁해요!', },
    {'고아 살란드리아를 데리고 장가르 습지대의 스포어가르로 가야 합니다. 도착했을 때 살란드리아가 보이지 않으면 호루라기로 다시 불러내십시오. 그런 다음, 히추와 대화해야 합니다.', },
  },
  [10946] = {
    '잿빛혓바닥의 환영',
    {'올룸의 희생은 실로 대단한 것이네. 난 절대 그에게 소중한 생명을 포기하면서까지 이 일을 하라고 청하진 않았을 것이네만 이젠 모두 끝이 났군.$B$B일리단이 우리가 그의 명령에 복종하고 있다고 생각하게 하려면, 적어도 지금부터는 그의 명령대로 해야 하네.$B$B난 잿빛혓바닥의 거대한 군대를 지휘하고 있으나 맹신도를 제외하곤 누구도 믿을 수가 없다네.$B$B자네가 일리단이 원하는 일을 해줘야겠네. 자, 이 수도두건을 받게. 이건 마법이 깃든 것이라네. 이 수도두건은 캘타스의 불사조인 알라르를 처치하는 자네를 누가 보든 내 부하 중 한 명으로 보이게 해 줄 걸세.', },
    {'폭풍우 요새로 가서 잿빛혓바닥 수도두건을 착용한 채로 알라르를 처치한 후, 어둠달 골짜기에 있는 아카마에게 가야 합니다.', },
  },
  [10947] = {
    '과거로부터 온 유물',
    {'자네 덕분에 일리단의 관심이 이제 캘타스에게로 넘어갔군. 이제 우리의 행동을 실행에 옮길 때가 왔네.$B$B난 최후의 대결에서 우리에게 도움을 줄 유물에 대한 환영을 보았다네. 바로 격노한 윈터칠이라고 알려진 리치가 지닌 성물함이지. 안타깝게도 그 성물함은 하이잘 산의 전투에서 그가 패한 후론 영원히 자취를 감췄다네.$B$B하지만 그 유물을 되찾을 방법이 하나 있네. 전설에 의하면 아제로스에 청동용군단이 수호하는 곳이 있다더군... 과거와 현재가 만나는 곳이지. 바로 그곳이 우리가 구하고자 하는 것을 찾을 수 있는 곳일 게야.', },
    {'타나리스에 있는 시간의 동굴로 가서 하이잘 산의 전투가 벌어지는 곳으로 진입하십시오. 전투에 참여하면 격노한 윈터칠을 처치하고 어둠달 골짜기에 있는 아카마에게 시간을 거스른 성물함을 가져가야 합니다.', },
  },
  [10948] = {
    '볼모가 된 영혼',
    {'자네는 내가 왜 내 진정한 목적을 감추려고 무슨 일이든 감행하는지 궁금할 것일세. 왜 내 운명을 나루와 함께하지 않았는지 말이야.$B$B내 귀중한 영혼은 검은 사원 내에 사로잡혀 있다네. 일리단은 마음만 먹으면 언제고 날 짓밟아버릴 수 있었지... 지금까지는 말이야! 이젠 이 성물함이 있으니 내 영혼을 되찾을 수 있을 걸세.$B$B이제 때가 된 거야. 내가 마이에브와 내 결사단을 이끌고 일리단의 사원 앞으로 가겠네.$B$B아달과 대화하여 우리의 계획을 알려주게나. 우리가 함께 공격에 나서게 된다면, 일리단의 방어선을 돌파하게 될 것일세.', },
    {'샤트라스로 가서 아달에게 아카마의 요청에 대해서 알려줘야 합니다.', },
  },
  [10949] = {
    '검은 사원 속으로',
    {'<아달이 검은 사원 외부에서 드레나이 구원자와 블러드 엘프 마법학자들과 함께 싸우고 있는 한 나루의 영상을 보여줍니다. 이 장면이 아카마가 기다리고 있는 소동이라는 걸 바로 깨닫습니다.>', },
    {'어둠달 골짜기에 있는 검은 사원의 입구로 간 후 지리와 대화해야 합니다.', },
  },
  [10950] = {
    '아킨둔과 규율의 광장',
    nil,
    {'고아 도르나를 데리고 아킨둔의 중앙, 규율의 광장에 있는 만남의 돌로 가야 합니다. 아킨둔은 테로카르 숲의 해골 무덤의 중앙에 있습니다. 도착했을 때 도르나가 보이지 않으면 호루라기로 다시 불러내십시오.', },
  },
  [10951] = {
    '어둠의 문으로의 여행',
    {'재미난 구경거리에서 어둠의 문을 빼놓을 수는 없죠?!$B$B어른들은 늘 어둠의 문에 대한 무시무시한 이야기를 들려주면서 악마랑 괴물들이 튀어나오는 위험한 장소라고 겁을 주셨죠.$B$B<초롱초롱한 눈빛으로 당신을 보는 살란드리아>$B$B하지만, <오빠/언니>처럼 든든한 보호자가 곁에 있다면 괜찮을 거예요. 제 말 맞죠?$B$B제 눈으로 직접 어둠의 문을 관찰하고 싶어요. 지옥불 반도 동쪽 끄트머리 어디쯤이라던데 혹시 기억나세요?', },
    {'고아 살란드리아를 데리고 지옥불 반도의 운명의 계단 위에 있는 어둠의 문 앞으로 가야 합니다. 도착했을 때 살란드리아가 보이지 않으면 호루라기로 다시 불러내십시오.', },
  },
  [10952] = {
    '어둠의 문으로의 여행',
    {'우리가 다른 곳에 가지 못한다고 해도 꼭 가야만 하는 곳이 있어요. 바로 어둠의 문 말이에요!$B$B보육원 원장님께서는 항상 수많은 악마들이 있는 어둠의 문이 얼마나 위험한지와 $G오빠:언니;와 같은 모험가분들이 그곳을 지나간 이야기들로 저희를 겁주신답니다.$B$B<눈을 굴리는 도르나>$B$B그곳이 무섭긴 해도 $G오빠:언니;가 계시니 무서운 일이 일어나진 않겠죠?$B$B전 어둠의 문의 광경을 제 눈으로 보고 싶어요. 어둠의 문이 지옥불 반도의 동쪽 끝자락에 있다는 건 기억하시죠?', },
    {'고아 도르나를 데리고 지옥불 반도의 운명의 계단 위에 있는 어둠의 문 앞으로 가야 합니다. 도착했을 때 도르나가 보이지 않으면 호루라기로 다시 불러내십시오.', },
  },
  [10953] = {
    '정령의 옥좌 탐방',
    {'정령의 옥좌라고 혹시 들어는 보셨나요? 나그란드에 있는 그 옥좌에는 강력한 정령들이 있다고 해요.$B$B이 다음에 커서 어른이 되면 강력한 마법사가 될 거예요. 아니면 흑마법사가 될지도 모르죠. 어쨌든, 정령의 힘에 대해 배우고 싶어요!$B$B우리, 거기 가볼 수 있을까요?$B$B<장난스러운 표정으로 당신을 쳐다보는 살란드리아>$B$B후견인으로서 제 학구열에 당연히 도움을 주셔야 하는 거 아닌가요? 그 옥좌는 가라다르 정북쪽에 있대요. 하늘노래 호수 반대편으로 가면 돼요.', },
    {'고아 살란드리아를 데리고 나그란드, 정령의 옥좌로 가야 합니다. 도착했을 때 살란드리아가 보이지 않으면 호루라기로 다시 불러내십시오. 그런 다음, 정령술사 샤르바크와 대화해야 합니다.', },
  },
  [10954] = {
    '에어리스 착륙지의 질!',
    {'$G오빠:언니;, 질 오빠가 보고 싶어요. 질 오빠는 저보다 몇 살 더 많은데요, 보육원에서 오빠를 나그란드로 보내 그곳의 에테리얼들에게서 수습 과정을 배우도록 했어요.$B$B질 오빠를 만나러 가면 너무 좋을 거예요. 오빠는 워낙 땅과 관련된 놀이를 좋아하니까 공부하면서 정말 재밌는 시간을 보내고 있을 거라 믿어요.$B$B보육원 원장님께서 그러시는데 질 오빠는 나그란드에 있는 에어리스 착륙지라 불리는 곳에서 훈련을 받고 있다고 하셨어요. 에어리스 착륙지는 텔라아르에서 서쪽으로 멀리 떨어진 오슈군이라는 거대한 산의 북서쪽의 길 옆에 있을 거예요.', },
    {'고아 도르나를 데리고 나그란드에 있는 에어리스 착륙지로 가야 합니다. 도착했을 때 도르나가 보이지 않으면 호루라기로 다시 불러내십시오. 그런 다음, 질과 대화해야 합니다.', },
  },
  [10955] = {
    '모르디스 위스퍼윙',
    nil,
    nil,
  },
  [10956] = {
    '나루의 보좌',
    nil,
    {'고아 도르나를 데리고 하늘안개 섬, 엑소다르 내의 나루의 보좌 아래쪽에 서 있는 오로스에게 가야 합니다. 도착했을 때 도르나가 보이지 않으면 호루라기로 다시 불러내십시오.$B$B도시 안에서는 지도를 보고 위치를 확인할 수 있습니다.', },
  },
  [10957] = {
    '잿빛혓바닥 결사단의 구원',
    {'일리단이 뒤틀린 드레나이들로부터 충성의 맹세를 얻었을 때, 아카마 님께서는 우리의 희망이셨습니다. 아카마 님께서는 배신자 일리단에 공공연하게 맞서면 그가 우리 종족을 멸종으로 치닫게 할거라는 걸 알고 계셨지요.$B$B그 대신에 그분께서는 극비리에 이 순간을 위해 계획을 세웠고 그 윤곽을 잡아오셨습니다. 이 계획이 실패한다면... 아카마 님과 뒤틀린 드레나이뿐만 아니라 아웃랜드의 모두가 파멸하게 될 겁니다. 그 어느 누구도 이만큼이나 치밀한 계획하에 일리단에게 이리도 큰 타격을 가져다준 자는 없었으니까요.$B$B당신이 아카마 님을 도와 그의 영혼을 해방시키면 그가 성소로 가는 길을 열어 줄 것입니다.', },
    {'아카마를 도와 검은 사원에서 아카마의 망령을 처치하여 그의 영혼을 다시 되돌린 후, 현자 카나이에게 가야 합니다.', },
  },
  [10958] = {
    '잿빛혓바닥 결사단 찾기',
    {'<지리를 쳐다보니 정신이 집중되고 의지가 확고해집니다. 이곳에서의 임무가 아직 끝나지 않았습니다. 검은 사원으로 가서 아카마의 추종자들을 찾아야만 합니다.>$B$B<아카마의 성공에 모든 것이 달려 있습니다.>', },
    {'검은 사원 내에서 아카마의 결사단을 찾아야 합니다.', },
  },
  [10959] = {
    '배신자의 몰락',
    {'좋습니다, 계획을 설명 드리지요. 일리단의 부관들을 처치하면 아카마님께서 일리단의 성소로 가는 문을 파괴할 수 있을 겁니다. 그러니 아카마님이 성소의 문을 열자마자 당신의 친구들을 불러들일 수 있도록 만반의 준비를 하시기 바랍니다.$B$B우린 아직 마이에브님께 무슨 일이 일어났는지 모르고 있습니다만, 그분이 계시지 않아도 이번 일을 해내야만 합니다.$B$B당신의 친구들이 이번 일을 이겨낼 준비가 되었으면 좋겠군요. 자신을 배신한 아카마님을 보면 일리단이 가만있지는 않을 테니 말입니다.', },
    {'현자 카나이가 검은 사원에 있는 일리단을 처치해 달라고 했습니다.', },
  },
  [10962] = {
    '시간의 동굴로',
    {'보육원에서 지나가다 들르신 나이 든 사제분께서 시간의 동굴에 대한 재미있는 얘기들을 들려주셨어요. 그분이 그러시는데 시간의 동굴은 흘러간 시간의 중심에 있고 세상 일이 똑바로 흘러가는데 매우 중요하다고 하셨지요.$B$B그게 무슨 말인진 모르겠지만 그곳에 이 세상을 지키는 데 힘을 쏟는 용들이 있다고 그러셨거든요. 그 말을 듣고는 줄곧 용들이 보고 싶었어요!$B$B$G오빠:언니;랑 저랑 시간의 동굴에 갈 수 있을까요? 그 사제분께선 시간의 동굴의 입구가 타나리스라는 큰 사막의 동쪽 끝에 있다고 하셨어요.$B$B우리가 그곳에 가면 $G오빠:언니;가 저에게 기념품을 사 주실 수 있지 않을까요?', },
    {'고아 도르나를 데리고 시간의 동굴 한가운데 잘라도르무가 있는 연단으로 가야 합니다. 도착했을 때 도르나가 보이지 않으면 호루라기로 다시 불러내십시오.$B$B그런 다음, 입구 쪽 굴의 아래 부근에 있는 시간의 수호자 병참장교인 알룰미에게 장난감 용을 사야 합니다.', },
  },
  [10963] = {
    '시간의 동굴로',
    {'보육원에 종종 들렀던 냄새 나는 늙은 사제분께서 시간의 동굴에 대한 이야기를 들려줬어요. 시간의 한가운데 자리한 그곳은 모든 역사가 똑바로 돌아가는데 매우 중요한 역할을 한다고 그러더군요.$B$B도대체 그게 무슨 소리인지는 잘 모르겠지만, 거기에 세계를 수호하는 용이 있다는 것만큼은 정말 굉장했어요. 전 용이 정말 보고 싶거든요!$B$B꼭 좀 데려다주세요. 그 사제가 말하기를 타나리스라는 거대한 사막의 동쪽 끝에 입구가 있다고 했어요.$B$B참, 거기 도착하면 저한테 기념품 선물 꼭 해주기에요!', },
    {'고아 살란드리아를 데리고 시간의 동굴 한가운데 잘라도르무가 있는 연단으로 가야 합니다. 도착했을 때 살란드리아가 보이지 않으면 호루라기로 다시 불러내십시오.$B$B입구 쪽 굴의 아래 부근에 있는 시간의 수호자 병참장교인 알룰미에게 장난감 용을 사야 합니다.', },
  },
  [10966] = {
    '보육원으로 돌아가는 길',
    {'힝! 아쉽지만 샤트라스에 있는 보육원으로 돌아가야 할 때인 거 같네요.$B$B그동안 정말 즐거운 시간을 보냈어요. $G오빠:언니;는 이 세상에서 제일 좋은 분이세요! $G오빠:언니;는 이제 제 친 $G오빠:언니;와 다름없다는 거 아시죠?$B$B<두 눈에 눈물이 고이는 도르나>$B$B$G오빠:언니;가 너무너무 보고 싶을 거예요! 제가 크면 꼭 $G오빠:언니;와 같은 멋진 $C|1이;가; 될 거랍니다!!!', },
    {'드레나이 고아 호루라기를 샤트라스 보육원 원장님 머시에게 건네주어, 고아 도르나를 고난의 거리 보육원으로 돌려보내야 합니다.', },
  },
  [10967] = {
    '보육원으로 돌아가는 길',
    {'이런! 이제 샤트라스 보육원으로 돌아갈 시간이네요.$B$B정말 굉장한 경험이었어요. $N, $G오빠:언니;가 이 세상에서 최고로 멋져요! 이제부터 제 진짜 $G오빠:언니;라고 생각해도 돼요?$B$B<눈물이 고여 촉촉해지기 시작하는 살란드리아의 커다란 눈망울>$B$B$G오빠:언니;가 정말 그리울 거예요!', },
    {'블러드 엘프 고아 호루라기를 샤트라스 보육원 원장님 머시에게 건네주어, 고아 살란드리아를 고난의 거리 보육원으로 돌려보내야 합니다.', },
  },
  [10969] = {
    '야미르와의 대화',
    nil,
    {'자유연합 경비초소에서 사령관 야미르를 찾아야 합니다.', },
  },
  [10970] = {
    '자비로움의 임무',
    {'우린 지난 에테리움 관할 지역으로의 정찰 임무에서 이 감옥 보주를 인양하였소. 희미하게 감지되었던 조난 신호가 아니었다면 이 보주를 절대 찾을 수 없었을 것이오.$B$B우린 우리 소속 판매업자 중 한 명이 그곳에 감금되어 있을 것이라 예상하고 있소이다. 우린 그들을 구출하기 위해선 이 보주를 확인해 봐야 한다오. 그러나 지금으로선 이 보주 안에 들어 있는 그 무언가에 해를 받지 않고 이 보주를 열 방법이 없소이다.$B$B에테리움에 대해서 우리가 아는 건 그들의 일원 중 누구든 이 보주를 열 열쇠를 지니고 다닌다는 것이오. 그 열쇠를 회수하여 내게로 돌아오면 우린 이 보주의 비밀을 풀 수 있을 것이오.', },
    {'인양된 에테리움 감옥 열쇠를 되찾아 황천의 폭풍에 있는 자유연합 경비초소의 사령관 야미르에게 가져가야 합니다.', },
  },
  [10971] = {
    '에테리움의 비밀',
    {'<인식표를 들고 있는 야미르 사령관>$B$B보시오, 죽은 선봉대원의 유해에서 죄수 인식표를 찾았소이다. 내가 추측할 수 있는 거라곤 에테리움이 잡고 있는 죄수들 모두 이와 같은 인식표를 달고 있을 것이라는 거요.$B$B이제부터 이 인식표들을 분류하여 녀석들의 형벌 구조에 대해 알 수 있는지 확인해 봐야겠소이다. 당신이 모험을 하는 동안 이 에테리움 감옥 열쇠를 더 찾아내면, 이곳에서 바로 남쪽에 있는 에테리움 작전 지역에서 에테리움 감옥을 여는 데 사용할 수 있을 것이오. 그러니 당신이 발견한 인식표를 내게 가져와 주시오.', },
    {'에테리움 죄수 인식표를 찾아서 황천의 폭풍에 있는 자유연합 경비초소의 사령관 야미르에게 가져가야 합니다.', },
  },
  [10972] = {
    '에테리움 죄수 분류',
    {'에테리움 죄수 분류 방법에 대해서 알아내려면 더 많은 죄수 인식표가 필요하오. 지금까지 내가 알아낸 사실은 이 인식표는 죄수 분류를 위한 수단뿐만 아니라 그들의 위치까지 상세하게 기록되어 있다는 것이오!$B$B이 암호를 해독하면 우리의 동맹들이 정확하게 어디에 잡혀 있는지 알 수 있게 될 테니 최소한의 출혈만으로도 그들을 구출할 수 있을 것이오.', },
    nil,
  },
  [10973] = {
    '무수한 세계',
    {'드디어 내가 인식표의 암호를 풀어냈소이다! 각 인식표가 여러 개의 조각으로 분해됐소. 인식표의 앞면에는 언제나 똑같이 "무수한 세계..."라고 쓰여 있더군. 이 경구에 대한 뜻이 무엇인지는 잘 모르겠으나 흉계가 있는 것 같소.$B$B한편 인식표의 뒷면에는 인구 통계가 기록되어 있소이다. 첫 번째 룬 문자는 위치를 나타내고 두 번째는 죄수의 유형을 보여주는 것이오. 게다가 감옥 열쇠를 합치면 또 다른 유형의 감옥을 열 수 있는 특수한 열쇠를 만들 수 있을 것 같소. 이제 내겐 열쇠가 더 필요하다오... 좀 가져다주겠소?', },
    {'황천의 폭풍에 있는 자유연합 경비초소의 사령관 야미르에게 에테리움 감옥 열쇠 5개를 가져가야 합니다.', },
  },
  [10974] = {
    '바시르 감금실',
    {'에테리움들이 칼날 산맥의 가장 북쪽 땅에 바시르 영지라는 정착지를 건설했소이다. 이 열쇠판에 따르면 훨씬 큰 규모의 감옥이 바시르 영지에 있소. 또한 에테리움이 그 감옥들을 감금실로 사용한다는 점을 주목해야 하오.$B$B그 열쇠판은 바시르 영지에 있는 알파 감금실이라는 매우 특수한 감금실에 접근할 수 있도록 되어 있소. 그 열쇠판을 찾아서 조사해 보시오.$B$B내 영상이 바시르 영지에서 당신이 오기를 기다리고 있을 것이오. 그곳에서 얘기하도록 합시다.', },
    {'알파 에테리움 열쇠판을 사용하여 칼날 산맥에 있는 바시르 영지의 알파 감금실을 열어야 합니다.$B$B알파 감금실 증거물을 가지고 바시르 영지에 있는 사령관 야미르의 영상에 돌아가십시오.', },
  },
  [10975] = {
    '바시르 감금실 정화',
    nil,
    nil,
  },
  [10976] = {
    '연합왕의 징표',
    {'내가 반드시 알아야 하는 것은 에테리움들이 아킨둔에 있는 마나 무덤 내에 무엇을 보관하고 있느냐는 것이오.$B$B바시르의 감금실에 있는 야수들과의 싸움에서 연합왕의 징표라는 물건을 우연히 발견하게 될 것이오. 그 징표는 마나 무덤에 있는 감금실에 접근할 수 있는 열쇠를 세공하는 데 필요한 마지막 조각이라오. 그러니 그 징표를 찾아내어 내게 즉시 가져오도록 하시오!', },
    {'칼날 산맥에 있는 바시르 영지에서 만날 수 있는 사령관 야미르의 영상에게 연합왕의 징표를 가져가야 합니다.', },
  },
  [10977] = {
    '마나 무덤의 감금실',
    {'당신이 발견한 연합왕의 징표로 마나 무덤 내에 있는 감금실의 열쇠를 세공했소.$B$B연합왕의 징표에 있는 문자로 보건대 감금실이 두 개 있소이다. 하나는 입구 근처에 있고 하나는 연합왕자 샤파르의 근처에 있소. 내가 당신에게 주려고 하는 이 열쇠로 마나 무덤의 입구 근처에 있는 감금실을 열 수 있을 거요.$B$B당신이 아킨둔의 마나 무덤으로 가서 마나 무덤 감금실을 열고 그 안에 도사리고 있는 공포를 확인해 주시기 바라오.', },
    {'칼날 산맥에 있는 바시르 영지에서 만날 수 있는 사령관 야미르의 영상이 마나 무덤 감금실 열쇠를 아킨둔의 마나 무덤 내의 감금실에 사용해 달라고 했습니다.$B$B이 퀘스트는 던전 난이도를 영웅으로 설정한 후 수행해야 합니다.', },
  },
  [10979] = {
    '영원의 숲으로...',
    {'그러니까 지하굴에서 까마귀의 상을 봤단 말인가?$B$B이거 참, 사태가 골치 아프게 돌아가는군. 직접적으로 클린타르와 꿈지기들을 도울 방법은 없지만, 에메랄드의 꿈을 위협하는 세력에 대한 정보를 좀 더 얻을 수 있다면 놈들을 저지할 수 있을 거야.$B$B요 근래 칼날 산맥에서 까마귀 신을 숭상하는 고대의 아라코아 이교도들이 부활했다는 걸 알았네. 영원의 숲으로 가서 아르쏜 윈드송을 찾게나. 에메랄드의 꿈 사태는 그 산맥에서 벌어지는 일들과 분명히 관련이 있을 걸세!', },
    {'칼날 산맥에 있는 영원의 숲에서 아르쏜 윈드송을 찾아서 대화해야 합니다.', },
  },
  [10980] = {
    '까마귀 경전',
    nil,
    nil,
  },
  [10982] = {
    '하라매드의 눈',
    nil,
    nil,
  },
  [10983] = {
    '주름투성이 모그도그',
    {'모그도그 무지 똑똑하다! 그가 도시에 가서 도움을 청하라고 그록을 보냈다. 피망치 일족 도움 필요해.$B$B모그도그가 그랬어. 작아도 힘세 보이는 자를 칼날 산맥에 있는 자기한테 보내라고. 모그도그 피의 투기장 바깥에 있는 탑 위에 서 있을 거야. 모그도그는 정말 똑똑해!', },
    {'칼날 산맥, 피의 투기장 바깥에 있는 탑들 중 한 곳의 꼭대기에 있는 주름투성이 모그도그를 찾아가야 합니다.', },
  },
  [10984] = {
    '오우거와 대화',
    {'<당신의 가슴속을 가득 채우는 수정처럼 맑고 또렷한 베루의 음성>$B$B한 오우거가 고난의 거리에서 행인들의 도움을 구하고 있노라. 하지만, 모두 그 오우거의 요청을 모른 척하고 있지.$B$B그대는 그 오우거를 도와줘야 한다고 생각하는가?$B$B그 오우거의 이름은 그록, 남동쪽에 있는 상인들 틈바구니에 있을 것이다.$B$B고맙도다.', },
    {'샤트라스의 고난의 거리에 있는 오우거, 그록과 대화해야 합니다.', },
  },
  [10985] = {
    '아카마를 위한 소동',
    {'<지리가 주위의 알도르 사제회와 점술가 길드의 군대에 그의 명령을 전하려고 빛의 힘을 전파합니다. 지리의 군대는 일리단의 군대를 물리치고 아카마를 위한 소동을 일으킬 준비가 되었습니다. 이 나루는 공격을 감행하기 위해 당신의 요청을 기다리고 있습니다.>', },
    {'지리의 군대가 소동을 일으킨 후에 마이에브와 아카마가 어둠달 골짜기의 검은 사원에 진입하도록 도와야 합니다.', },
  },
  [10986] = {
    '하늘의 눈',
    nil,
    nil,
  },
  [10989] = {
    '주름투성이 모그도그',
    {'주름투성이 모그도그가 탑의 꼭대기에서 당신 기다리고 있다. 모그도그가 말하길 당신과 같은 $R $C|1을;를; 찾고 있다고 하더군. 그에게 가서 아주 중요한 일에 대해서 얘기해 보도록 해라.$B$B바로 그를 만나러 가라. 하지만 누구에게도 말해서는 안 된다. 왜냐고? 비밀이니까!', },
    nil,
  },
  [10995] = {
    '그룰록이 아끼는 물건',
    nil,
    nil,
  },
  [10996] = {
    '마그고크의 보물 상자',
    {'남동쪽 위로 칼날 마루로 가면 마그고크가 순찰하고 있는 걸 볼 수 있을 게야. 녀석의 아비가 동부 산맥 지역의 감시자 일을 맡겼는데, 녀석은 지나가는 길에 밟히는 생물이라면 무조건 위협하며 즐거워하고 있다네.$B$B그룰의 아들인 그 녀석은 내 일족의 생존을 위해 사라져야만 하네. 이 일이 이번에 자네가 해야 할 일일세.$B$B마그고크는 항상 보물 상자를 지니고 다닌다고 하더군. 내게 녀석의 그 소중한 상자를 가져와서 녀석이 죽었다는 걸 증명해 보여주게나. 그 상자 안에서 찾은 게 뭐든 가져도 좋네.', },
    nil,
  },
  [10997] = {
    '그론에게도 깃발이...',
    {'$C 친구, 자네 샤트라스를 잘 아나? 샤트라스의 위 북서쪽에 울타리 언덕이라 불리는 곳이 있는 데 거긴 날아다니는 탈것으로만 갈 수 있다네.$B$B그룰의 또 다른 아들인 슬라그가 망명하여 그곳에 살고 있는 골두니 오우거들을 다스리고 있지. 녀석과 우리가 이렇게 멀리 떨어져 있어도 녀석의 나머지 형제들이 살해되면 그가 돌아오게 되니 녀석을 그대로 놔둘 순 없네.$B$B자네가 울타리 언덕으로 가서 그를 처치했다는 증거로 그의 깃발을 가져오게나.', },
    nil,
  },
  [10998] = {
    '흑마법서 회수',
    nil,
    nil,
  },
  [11000] = {
    '영혼분쇄자에게로',
    {'이 마법서는 스컬록을 파멸시킬 수 있는 열쇠라네.$B$B영혼분쇄자의 은신처에서 이 마법서를 읊어 의식을 시작하도록 하게. 그러면 제단에 영혼분쇄기라고 알려진 고리와 함께 스컬록에게 희생된 영혼들이 나타나 그걸 파괴하려고 할 것이야. 자네는 스컬록을 완전히 이 세계로 끌어들이기에 충분한 시간 동안 그들이 영혼분쇄기를 파괴하지 못하도록 해야 하네.$B$B그의 은신처는 피의 투기장의 다른 쪽에서 비수아귀 협곡을 지나 북쪽으로 가면 찾을 수 있을 거야. 바로 그룰의 둥지로 가기 전 오른쪽이지.$B$B스컬록의 영혼을 내게 가져오게나.', },
    nil,
  },
  [11001] = {
    '무찌르자, 까마귀 신!',
    nil,
    nil,
  },
  [11002] = {
    '마그테리돈의 죽음',
    nil,
    {'마그테리돈의 머리를 지옥불 반도의 명예의 요새에 있는 전투사령관 다나스 트롤베인에게 가져가야 합니다.', },
  },
  [11003] = {
    '마그테리돈의 죽음',
    nil,
    {'마그테리돈의 머리를 지옥불 반도의 스랄마에 있는 나즈그렐에게 가져가야 합니다.', },
  },
  [11004] = {
    '어둠의 세계',
    {'하늘경비대 사령관인 아다리스 님께서 스케티스의 습격으로 심각한 부상을 입었습니다. 그 이후로 사령관께서는 사경을 헤매고 계시답니다.$B$B사령관님께서 열로 인한 정신 착란상태 때문에 세상을 뜨실까 걱정입니다. 잠시 의식을 되찾으신 동안 사령관님께서는 아라코아들이 지니고 다니는 어둠 가루로 만든 비약을 준비하라고 하셨답니다. 사령관님께서는 그 비약으로 아라코아의 진정한 지도자들이 살고 있는 "어둠의 세계"를 조사할 수 있을 거라 믿고 계신 듯합니다.$B$B이 말도 안 되는 게 들으리라곤 절대 믿지 않습니다만, 제가 그런 비약을 얻기 전까진 편히 눈을 감지 못하실까 두렵습니다. 절 좀 도와주시겠습니까?', },
    {'세베린이 당신에게 스케티스로 들어가서 그곳에 서식하는 아라코아들을 처치하고 어둠 가루 6줌을 구해 오라고 했습니다.', },
  },
  [11005] = {
    '갈퀴사제의 비밀',
    {'정신 착란 상태에 있던 제 이야기를 잘 들어주셔서 감사합니다. 그동안 우리는 스케티스의 지휘 체계를 간파하여 무너뜨리고자 했었지요. 그 덕분에 세 명의 갈퀴사제의 존재를 알아냈습니다. 하지만 그들을 본 적이 없어 이상하게 생각했는데 이제 그 이유를 알 것 같군요.$B$B세베린과 대화하여 그가 발견한 비약을 충분히 준비해 놓도록 하십시오. 그 비약을 사용하여 이샤알, 스키지크, 젤레크라는 갈퀴사제들을 찾아 처치하십시오. 그들이 검은바람 호수의 동쪽 기슭에 있는 장막의 알라라크에 살고 있다는 소문이 있으니 그리로 가 보십시오.', },
    {'세베린에게서 어둠의 비약을 구해 스케티스에서 갈퀴사제 이샤알, 갈퀴사제 스키지크, 갈퀴사제 젤레크를 찾아 처치한 후, 하늘경비대 사령관 아다리스에게 가야 합니다.', },
  },
  [11006] = {
    '어둠 가루 추가 수집',
    nil,
    nil,
  },
  [11007] = {
    '캘타스와 신록의 구슬',
    {'주위의 공기가 온화한 기운을 뿜으며 넘실댑니다. 이 온화한 기운으로 보니 나루가 당신의 승리를 알고 있습니다. 아마 그들이 아직도 꿈틀대고 있는 캘타스 선스트라이더의 유해에서 이 구슬을 어쩔 수 없이 집어들어야만 하는 이유일 것입니다. 이 구슬이 지닌 비밀이 무엇일까요?$B$B아마도 샤트라스에 있는 아달이 이 구슬에 담긴 비밀을 알려줄 수 있을 것입니다.', },
    {'샤트라스에 있는 아달에게 신록의 구슬을 가져가야 합니다.', },
  },
  [11008] = {
    '스케티스 공습',
    {'제군은 우리 하늘경비대의 일원이 될 자격이 있다고 생각하나? 그렇다면, 하늘에서 얼마나 자유자재로 싸울 수 있는지 실력을 입증해 보이도록!$B$B아라코아가 거대한 변종 야수를 훈련시켜서 우리 정찰병들을 공격하게 하고 있어. 이 폭발물을 가지고 아라코아 서식지 위로 날아가서 부화장을 파괴하게. 부화장을 지키는 거대한 칼리리를 조심하도록. 눈도 깜짝 못할 사이에 날아다니는 탈것에서 제군을 떨어뜨릴 테니까...', },
    {'스케티스 서식지 상공에서 거대한 칼리리 알을 찾아서 하늘경비대 폭발물을 사용해야 합니다. 임무를 마친 다음 하늘경비대 하사관 도린에게 가야 합니다.', },
  },
  [11009] = {
    '오우거의 천국',
    {'네가 우릴 노예의 신분에서 벗어나게 해 주었으니, 우리 오우거 일족을 재통합하는 일에 착수할 수 있게 되었다. 일족 간의 오랜 원한은 쉽게 극복하기 어려운 것이니 시간은 좀 걸릴 것이지만 말이야.$B$B하지만 네가 서쪽 하늘에 있는 오그릴라로 가는 게 더 급선무다. 너는 에테리얼과 악마들, 무엇보다도 검은용군단의 손아귀에서 우리와 오그릴라를 보호해야 한다. 그론이 그랬던 것처럼 말이야.$B$B오우거의 천국에 가면, 이번 일을 어떻게 해야 하는지 추알로르의 지혜를 구해봐라.', },
    {'주름투성이 모그도그가 칼날 산맥의 오그릴라에 있는 추알로르와 대화해 달라고 했습니다.', },
  },
  [11010] = {
    '폭격',
    {'당신은 날아다니는 탈것이 있지만 말입니다... 그래서 그걸 타고 전투에서 뭘 할 수 있다고 생각하는 거죠! 탈것을 적절하게 사용하는 재능이 있다고 생각하나요? 그렇다면 좋아요. 이제 그런 무기를 드리죠.$B$B북쪽으로 격노의 괴철로 기지와 남쪽으로 공포의 괴철로 기지는 내 후방뿐만 아니라 평화로운 오그릴라에 위협이 되고 있는 큰 골칫거리입니다. 그 타락한 대포가 우리 하늘경비대를 좌우 연타로 공격하고 있기에 그들의 기지에 우리 임무를 수행하러 갈 수가 없습니다.$B$B당신 실력을 증명해 보이고 싶나요? 이 폭탄 가방을 가져가서 그들의 포탄 더미에 투하하십시오. 물론 당신이 그들에게 맞아서는 안 되겠죠!', },
    nil,
  },
  [11012] = {
    '황천날개의 피의 맹세',
    nil,
    nil,
  },
  [11013] = {
    '일리다리에 봉사',
    {'황천날개 마루... 한때 황천날개를 위한 성소의 보루였던 그곳은 이제 용아귀 부족의 고향이 되었습니다. 설령 오크들이 우리 영토를 장악하고 있다 하더라도, 그곳에 펼쳐진 우리 마법은 작업을 하는 데는 무리가 없지요. 그대의 진정한 자아를 은폐하고 용아귀 부족의 일원으로 변신하는 데 충분한 마법이 황천날개 마루에 퍼져 있단 말입니다.$B$B자, 받으십시오. 당신이 용아귀 부족의 대군주인 모르고르에게 가져갈 명령서를 준비해 놓았습니다. 적의 아귀로 신속하게 날아가서 일리단 스톰레이지의 타락한 오크로서의 새 삶을 시작하기 위해 용아귀 주둔지를 찾으십시오.', },
    nil,
  },
  [11014] = {
    '작업반장에게로',
    {'<신임장을 구겨서 내던지는 모르고르>$B$B좋다, 우선 먼저 해야 할 일부터 하도록 하지. 내가 아끼는 용아귀 군대에 들어오고자 한다면, 일을 해야 할 것이야. 모두 이곳 황천날개 마루에서 분주히 일하고 있단 말이다. 일자릴 구해서 망치지 않고 잘하면 추후에 자네가 더 관심을 두고 일할 수 있을 만한 것이 있는지 확인해 보도록 하지.$B$B이제 작업반장인 바르큘 드래곤브레스에게 가 보라고. 주둔지의 중앙으로 가면 그를 만날 수 있을 것이야. 그가 자네의 능력을 평가하여 적당한 자리에 배정할 것이다.$B$B이제 그 얼굴 치우고 어서 가 봐!', },
    {'용아귀 주둔지에 있는 작업반장 바르큘 드래곤브레스와 대화해야 합니다.', },
  },
  [11015] = {
    '황천날개 수정',
    nil,
    {'어둠달 골짜기의 용아귀 주둔지에 있는 작업반장 바르큘 드래곤브레스에게 황천날개 수정 30개를 가져가야 합니다.', },
  },
  [11016] = {
    '황천수정 바위갈퀴 가죽',
    {'당신 무두장이로군? 그렇다면 이 마루에서 무두질할 생물을 무척 많이 찾을 수 있을 것이오! 수정 구조물 근처에 있는 바위갈퀴를 찾아봐야 할 것이오. 그 녀석들을 처치하고 그 가죽을 무두질하다 보면 가끔은 녀석들이 보관해도 좋을 품질의 가죽을 주기도 한단 말이지. 그런 가죽을 내게 가져오면 되오.', },
    {'어둠달 골짜기의 용아귀 주둔지에 있는 작업반장 바르큘 드래곤브레스에게 황천수정 바위갈퀴 가죽 35장을 가져가야 합니다.', },
  },
  [11017] = {
    '황천티끌 꽃가루',
    {'당신 약초채집가로군?! 하하... 이거 어쩐다. 우리 마법사들과 주술사들이 당신 도움이 좀 필요한데 말이야. 이 마루에서 자라는 황천티끌 덤불에서 우리 마법사들과 주술사들의 마법 사용에 매우 유용한 특별한 꽃가루를 얻을 수 있다오. 그 꽃가루를 가져오면 오늘 하루 일과를 끝낸 것으로 하지.', },
    {'어둠달 골짜기의 용아귀 주둔지에 있는 작업반장 바르큘 드래곤브레스에게 황천티끌 꽃가루 40줌을 가져가야 합니다.', },
  },
  [11018] = {
    '황천연 광석',
    {'당신 광부로군!$B$B<반갑게 맞이하는 바르큘>$B$B좋아, 내 마음에 드는 오크야! 이 바르큘이 광부에서부터 일을 시작했지! 지저분한 광산에 들어가 일에 착수하는 것보다 더 좋은 게 없지, 그렇지 않소?$B$B이제 나가서 황천연 광맥을 찾아보시오. 전력을 다해서 캐고 캔 광석을 내게 가져오면 되오.', },
    {'어둠달 골짜기의 용아귀 주둔지에 있는 작업반장 바르큘 드래곤브레스에게 황천연 광석 40개를 가져가야 합니다.', },
  },
  [11019] = {
    '내부의 동료',
    nil,
    nil,
  },
  [11020] = {
    '서서히 죽음에 이르는 길',
    {'용아귀 부족은 이곳에 있는 모든 수정 근처에 일꾼들을 배치했네. 일꾼을 곧바로 없애버리는 건 불필요한 시선을 너무 많이 끌게 될 테니 다른 식으로 처리하는 게 좋겠군. 내게 일꾼들의 숫자를 줄이고 모르고르의 의심스러운 눈초리를 피할 방법이 있다네.$B$B어둠달 골짜기에 사는 지옥의 생물들을 없애게나. 그리고 그들의 분비샘을 가져다가 이 양고기에 뿌리라고. 그럼 근처의 일꾼들이 한 입씩 베어 물고는 치명적인 병에 걸려 죽게 될 것이야!', },
    nil,
  },
  [11021] = {
    '이샤알의 연감',
    {'갈퀴사제 이샤알의 몸에서 비밀스러운 묵직한 책을 발견했습니다. 이해할 수 없는 신비한 기호와 천체의 도표들이 눈에 들어옵니다.$B$B이 책에 쓰인 모든 글은 당신이 알지 못하는 아라코아의 고대 언어로 쓰여 있습니다. 하늘경비대 사령관 아다리스에게 이 책을 가져가기 위해 책을 집고 발길을 돌립니다.', },
    {'이샤알의 연감을 스케티스의 북쪽에 있는 하늘경비대 사령관 아다리스에게 가져가야 합니다.', },
  },
  [11023] = {
    '다시 폭탄 투하!',
    {'$C|1이여;여;, 아직도 그 대포 덩어리 덕분에 골치가 아프군요.$B$B아, 오해는 마세요. 지난번에 당신이 해 준 일은 굉장했으니까요. 하지만 그 저주받은 악마 녀석들은 우리가 포탄 더미를 파괴하는 건 티도 안 날 정도로 빠르게 탄약을 보충해대서 말이죠. 다시 말해 아직도 우리 경비대원들이 그곳을 뚫고 나가는데 엄청난 곤욕을 치르고 있다 이겁니다. 그놈의 악마들이 오그릴라에 어떤 위협이 되는지는 말 안 해도 알겠죠?$B$B어때요? 그 포탄 더미를 다시 날려버리는 일에 동참하시겠어요?', },
    nil,
  },
  [11024] = {
    '고난의 거리로',
    {'고난의 거리에 릴라크라 불리는 아라코아가 있는데... 믿을 만한 친구입니다. 이 책을... 릴라크에게 가져가십시오.$B$B<책을 돌려주고 의식이 혼미해지는 아다리스>', },
    nil,
  },
  [11025] = {
    '에펙시스 결정',
    {'이 산맥의 수정들을 올바르게 다루면 이상한 힘을 내뿜는다. 우리가 이곳 오그릴라에서 얻은 명석함과 같은 힘 말이지.$B$B칼날 산맥의 꼭대기에 사는 모든 생명체는 수정의 힘을 몹시도 원한다. 더 나아가서는 그들의 사악한 목표를 이루고자 그 힘을 사용하고 있단 말이다. 그 수정 때문에 물가가 상승하기까지 했지.$B$B우리를 도우려면, 먼저 에펙시스 결정에 대해서 배워야 할 것이다. 내게 결정을 좀 가져와라. 이곳에 있는 생명체들과 갈퀴발톱들이 큰 수정을 캐고 있는 곳에서 결정을 찾을 수 있을 것이다.', },
    {'칼날 산맥 꼭대기의 오그릴라에 있는 추알로르가 에펙시스 결정 5개를 모아와 달라고 했습니다.', },
  },
  [11026] = {
    '악마 추방',
    {'오, 더할 나위 없이 딱 제때에 도착했군, 친구! 그렇소, 우린 도움이 필요하다오. 대신에 보상으로 우리 둘 다 무척 귀중한 물건을 얻게 될 것이라오.$B$B우리는 이제 폭력으로 얼룩진 오우거는 아니지만, 근처의 악마들이 우리와 하늘경비대를 침략하려는 징후를 보이고 있어 무력을 사용하지 않을 수 없소이다. 그 악마들은 죽고 나면 바로 새로운 몸으로 다시 돌아와서 그 수가 줄지 않고 있다오!$B$B악마를 처치하고 그 근처에서 이 수정을 사용하여 추방의 차원문을 소환하면, 그들의 본질이 차원문으로 빨려 들어가고 그들이 온 곳으로 추방될 것이오!', },
    nil,
  },
  [11028] = {
    '파멸의 초읽기',
    {'이 연감은 아라코아의 역사에서 가장 강력하고 무서운 지도자였던 테로크의 귀환을 예언하고 있소.$B$B나조차도 이를 단순한 전설로만 생각하고 있었소만... 그 갈퀴사제들의 귀환과 이 연감의 발견은 내 생각이 틀렸음을 말하고 있소.$B$B날이 갈수록 테로크의 힘이 완전하게 회복되어 가고 있소. 지금 상태에서 우리의 유일한 희망은 테로크가 더 강력한 힘을 얻기 전에 세상에 모습을 드러내도록 하는 것이오. 스케티스로 돌아가 하늘경비대 사령관인 아다리스에게 이 이야기를 전하시오. 그가 올바른 결정을 내릴 만큼 지혜로운 자이기를 바라오만...', },
    nil,
  },
  [11029] = {
    '허술한 위장',
    {'가방을 열어 보면 위장복이 들어 있을 것이다. 그다지 위장 효과가 없어 보이지 않나?$B$B그 위장복은 진귀한 책을 찾는 에테리얼 밀수꾼들에게 만들어 주던 것이다. 테로크의 안식처인 스케티스 중앙에 있는 섬에 사하크라는 아라코아 학자가 살고 있다. 그는 너무 늙어 시력도 노화되어 지금은 장님이나 마찬가지지.$B$B이 위장복을 입고 그에게 말을 걸어 적의 혈통이라는 책을 받아 오너라. 위장을 했다고 해도 다른 아라코아들까지 속이지는 못할 테니 조심하는 게 좋을 거다.', },
    {'허술한 아라코아 위장복을 사용하여 사하크에게서 적의 혈통을 얻은 후 하지크에게 가야 합니다.', },
  },
  [11030] = {
    '하늘경비대 순찰자가 되고픈 오우거',
    {'$C|1이여;여;, 우린... 아이들이 너무 많아서 문제라오. 게다가 하늘경비대가 근처로 온 이후로 죽, 우리 아이들 모두가 하늘경비대의 일원이 되려고만 해서 골치가 아픈 지경이라오.$B$B자랑스러운 아버지로서, 난 아이들이 하늘경비대 대원으로 지원할 때 모든 이점을 누리기를 바라고 있소이다. 그래서 말인데, 남쪽과 북쪽에 있는 아무 괴철로 기지로든 가서 그곳의 지옥 수정괴철로를 이용해 우리 아들들을 위한 마법 영약을 구해다 주겠소? 괴철로 기지에서 녹색 연기가 피어나는 곳으로 가면 괴철로를 찾을 수 있을 것이오.$B$B우리가 생각하기에는 마법 영약을 만드는 데 10개의 에펙시스 결정이 들어가는 것 같소이다. 결정을 당신에게 주고 싶지만, 현재로선 가진 게 없구려.', },
    nil,
  },
  [11031] = {
    '대마법사의 길 포기',
    nil,
    nil,
  },
  [11033] = {
    '일급 암살자의 길 포기',
    nil,
    nil,
  },
  [11035] = {
    '하늘과 땅의 대립',
    {'이 섬의 북동쪽 끝자락에 황천날개 수정과 유물을 포장하고 나르는 데 이용되던 곳이 있네. 용아귀 선조가 다른 공격자들로부터 그곳을 수호하는 동안 용아귀 수송병이 용아귀 요새로 물품을 수송하는 것이지.$B$B용아귀 부족에게 타격을 입힐 수 있는 이번 일에 자리가 하나 났는데 말이야. 어때, 관심 있나?$B$B용아귀 하늘길의 부서진 섬으로 날아가서 운송을 담당하는 용아귀 수송병을 습격하여, 도둑맞은 우리의 유물을 되찾아오도록 하게나!', },
    {'어둠달 골짜기의 용아귀 주둔지에 있는 용병 야르질이 황천날개 유물 10점을 가져다 달라고 했습니다.', },
  },
  [11036] = {
    '최고급 과일!',
    {'흠... 도대체 뭔지도 모르는 이런 주문을 받은 건 내가 처음일 거요. 하지만 로켓발사단장 퓨즐라지씨가 워낙 자세하게 설명을 해줘서 말이오. 오래 항해하는 동안 먹을 수 있게 최상품의 과일만을 골라서 병에 담고 완벽하게 밀봉해 달라는 주문이었소이다.$B$B거기다가 어떤 건 중력을 없애고 어떤 건 기압을 낮추라는 등 도통 알 수 없는 얘기들이 담긴 주문이더군. 이제 더 아는 척은 하지 않겠소이다. 그래도 가격을 잘 쳐준다니까 납품 준비는 해 두었소. 이 상자를 52번 구역에 있는 퓨즐라지씨에게 가져가 주시오. 황천의 폭풍으로 넘어간 다음 길을 따라 계속 가다 보면 52번 구역에 도착할 것이오.', },
    nil,
  },
  [11037] = {
    '낯선 환영',
    {'정말 이상한 환영을 보았습니다. 무엇인지 잘 모르겠어요. 화살이 보였어요. 거대한 화살이 불을 내뿜으면서 하늘 위로 치솟았지요. 유능한 마법사에게는 하찮은 장난일 수도 있겠지만 제가 본 화살은 마법의 산물은 아니었던 것 같아요.$B$B아제로스의 주민이 만든 거 같지는 않았어요. 그걸 만든 자들에게 가서 그들을 도와주세요. 그렇지 않으면 그들의 노력이 수포로 돌아갈 수도 있습니다.', },
    {'52번 구역에 있는 로켓발사단장 퓨즐라지와 대화해야 합니다.', },
  },
  [11038] = {
    '총독 오렐리스 지원',
    {'얼마 전에, 이샤나 대여사제님께서 황천의 폭풍에 있는 오렐리스 총독의 휘하로 소규모의 수도자군을 보내셨네. 그들은 캘타스와 추종자들의 활동을 조사하려 하였지. 그런데 아직 발견한 내용에 대해서 어떤 보고도 받지 못했네.$B$B난 이번 임무를 수행하는데 황천의 폭풍에서 사제회의 힘이 그리 강하지 못한 게 아닐까 우려가 되네. 분명 자네의 지식과 경험이 오렐리스 총독에게 도움이 되리라 확신하네. 그러니 자네가 52번 구역으로 가서 그를 도와주겠는가?', },
    {'52번 구역으로 가서 총독 오렐리스와 대화해야 합니다.', },
  },
  [11039] = {
    '첩보단장 탈로디엔에게 보고',
    {'얼마 전 보렌살이 캘타스의 움직임을 조사하기 위해 황천의 폭풍으로 소규모 부대를 파견했소. 우리의 지도자께서 탈로디엔 첩보단장과 그의 동료를 신뢰하여 추가 파병 요구를 받아들이셨지.$B$B보고서의 내용 자체는 짧지만 탈로디엔이 중요한 정보를 알아낸 것이 분명하오. 52번 구역에 있는 탈로디엔에게 보고한 후 모든 방법을 동원하여 그를 돕도록 하시오.', },
    {'52번 구역에 있는 첩보단장 탈로디엔에게 보고해야 합니다.', },
  },
  [11040] = {
    '로켓발사단장을 위한 부품',
    {'제가 지금까지 고블린과 공동으로 작업한 적은 한 손으로 꼽을 정도밖에 안 됩니다. 고블린과 노움은 경쟁 관계에 더 가까우니까요. 하지만 로켓발사단장인 퓨즐라지씨의 요청을 받았을 때 거절할 수가 없었답니다.$B$B이 상자엔 제가 이곳에서 사용하지 않는 부품이 가득 들어 있어요. 하지만 로켓발사단장님이라면 그의 계획에 사용하실 수 있을지도 모르겠네요. 그래서 말인데요, 52번 구역에 가실 거라면 이 물품을 좀 배달해 주시는 친절함을 보여주시겠어요?$B$B품질 좋은 노움의 부품 덕에 그의 발명품이 폭발하지 않게 할 수 있을지 누가 알겠어요!', },
    nil,
  },
  [11041] = {
    '끝나지 않은 임무...',
    {'우리의 위대한 주인님의 이름으로... 일리단의 영광을 위하여!$B$B모르고르 대군주님의 명을 받들어 황천날개 마루의 거대한 평원의 안전을 지켜내야 한다. 황천날개 비룡을 모두 생포했거나 몰아냈더니 바위갈퀴들이 날뛰면서 수정 수집 임무를 위협하고 있다.$B$B여왕 바위갈퀴와 여왕의 배후자인 아르보아르를 없애라.$B$B대군주 모르고르 님의 명령이다!$B$B<이 손의 주인은 평원의 안전을 지켜내는 데 실패한 것 같습니다. 모르고르에게 당신의 가치를 증명해 보일 때입니다!>', },
    {'땅굴수정 바위갈퀴 10마리와 여왕 바위갈퀴 바라쉬를 처치한 후, 어둠달 골짜기의 용아귀 주둔지에 있는 대군주 모르고르에게 임무 완수를 보고해야 합니다.', },
  },
  [11042] = {
    '혼란스러운 환영',
    {'$C|1이여;여;, 이상한 환영이 날 괴롭히고 있네. 난 환영에서 끊임없이 하늘로 올라가는 연기구름을 보았다네. 매우 빠르게 움직이는 거대한 불길의 흔적을 남기고 말이야. 내가 아는 선에서는 이건 자연현상도 아니고 악마의 창조물도 아니라는 것일세.$B$B하지만 이런 현상이 어떻게 일어나는지에 대해서는 난 전혀 아는 바가 없다네. 많은 이들이 노력한 결과물이라는 것 말고는 말이야. 일손이 추가되지 않고서는 당분간은 일어나지 못할 일이라네.$B$B$C|1이여;여;, 황천의 폭풍으로 여정을 떠나게나. 이 현상에 대한 책임을 지고 있는 자들이 있는 곳을 찾아서 그들에게 지원을 해 주게나.', },
    {'52번 구역에 있는 로켓발사단장 퓨즐라지와 대화해야 합니다.', },
  },
  [11043] = {
    '더 강한 그리핀 훈련',
    {'듣자하니 와일드해머 드워프들이 남부 어둠달 골짜기 아래쪽에 있는 그들의 성채에 그리핀의 보금자리를 꾸리고 있다고 하더군요. 그리로 갈 생각이라면 말입니다만, 저를 대신해서 그들과 대화를 나눠봐 줄 수 있으실까요? 제게 그들의 그리핀을 강화할 좋은 생각이 있단 말입니다!$B$B왜 고도계, 뒷거울과 로켓을 장착할 수 있는데 그리핀 무장을 멈춘 거죠? 그리핀의 등에 가죽끈을 달고 로켓을 장착한 녀석과 함께 출격하면 얼마나 빠를지 상상해 보시라고요! 전 완전빠른 그리핀 8000이라고 부를 겁니다!', },
    nil,
  },
  [11044] = {
    '파괴의 환영',
    {'대혼란에 대한 환영은 자주 내 휴식을 방해한다네. 녹색 불의 비가 대지로 떨어지고, 거대한 요새의 씩씩한 수호자들이 쓰러지는 그런 환영 말일세. 그 환영이 몇 주간 나를 괴롭혔지만, 근래에 들어서 그 환영의 의미를 깨닫게 되었다네.$B$B이곳에서 멀리 동쪽으로 어둠달 골짜기에 있는 와일드해머 드워프들은 불타는 군단과 셀 수도 없는 다른 적들의 끊임없는 공격을 견뎌내고 있네. 그 드워프들은 용감하게 싸우고 있지만 구원의 손길이 없이는, 적이 승리를 거둘 것이야. 가서 그들의 전투를 도와주게나.', },
    {'와일드해머 성채에 있는 편대사령관 누아인과 대화해야 합니다.', },
  },
  [11045] = {
    '심판관 조러스',
    {'어둠달 골짜기는 오랜 분쟁의 역사를 대변하고 있습니다. 그곳에서 일어났던, 말할 수 없을 만큼 잔인한 일들이 되살아날까 두려워 그 역사를 파헤치려는 시도조차도 거의 없었지요.$B$B하지만 모두가 겁쟁이는 아니었습니다. 심판관 조러스가 과거 어둠달 골짜기의 유령에 얽힌 진실을 연구하고 있으니까요. 정말 어려운 일이 아닐 수 없지요. 아마도 조러스는 다른 이들의 도움을 기꺼이 받아들일 겁니다. 그렇고 말고요.$B$B와일드해머 성채에 있는 조러스를 찾으러 어둠달 골짜기로 여정을 떠나시려거든 골짜기 남쪽 지역의 중앙부로 가셔야 합니다.', },
    {'와일드해머 성채에 있는 심판관 조러스와 대화해야 합니다.', },
  },
  [11046] = {
    '수석 연금술사 힐다가드',
    {'어둠달 골짜기는 오랜 분쟁의 역사를 대변하고 있습니다. 그곳에서 일어났던, 말할 수 없을 만큼 잔인한 일들이 되살아날까 두려워 그 역사를 파헤치려는 시도조차도 거의 없었지요.$B$B하지만 모두가 겁쟁이는 아니었습니다. 수석 연금술사 힐다가드가 과거 어둠달 골짜기의 유령에 얽힌 진실을 연구하고 있으니까요. 정말 어려운 일이 아닐 수 없지요. 아마도 힐다가드는 다른 이들의 도움을 기꺼이 받아들일 겁니다. 그렇고 말고요.$B$B어둠달 마을에 힐다가드를 찾으러 가시려거든, 어둠달 골짜기의 북서쪽 지역으로 가시기 바랍니다.', },
    {'어둠달 마을에 있는 수석 연금술사 힐다가드와 대화해야 합니다.', },
  },
  [11047] = {
    '수습조련사의 요청',
    {'와이번 수습조련사의 여러 임무 중 하나는 임무에 투입되지 않은 와이번을 잘 쉬게 하는 것이라오. 문제는 와이번의 수가 적어 쉴 틈이 없다는 것이지.$B$B와이번의 수를 늘리지 않으면, 이곳의 와이번들이 모두 지쳐 죽을지도 모르겠소이다. 어둠달 골짜기 서편의 어둠달 마을에 커다란 마구간이 하나 있는데, 거기서 다마 와일드메인이 와이번을 돌보며 훈련을 시키고 있다오.$B$B우리 대신 그녀를 만나 상황을 알려주시겠소?', },
    nil,
  },
  [11048] = {
    '크로그한의 보고',
    {'처음에 내가 받은 명령은 가라다르에서 얼마간 머무른 후 어둠달 마을에 있는 혈투사 굴모크에게 보고하라는 것이었네. 하지만 이곳에 있다 보니 가라다르엔 호드 연합과 지속적으로 연락을 취할 수 있는 연락책이 필요하다는 걸 알았지.$B$B굴모크가 병력 충원을 기다리고 있다는 건 알지만, 그 역시 이곳에서 내 임무가 아직 끝나지 않았다는 걸 이해할 것이네. 어둠달 골짜기의 북서쪽에 있는 어둠달 마을로 가서 내가 좀 늦어진다는 소식을 굴모크에게 전해주게나.', },
    {'어둠달 마을에 있는 혈투사 굴모크와 대화해야 합니다.', },
  },
  [11049] = {
    '좋은 황천날개 알 수집',
    {'자네에게 줄 임무가 있네. 황천날개 마루를 탐험하면서 황천날개 알을 찾아서 가져오도록 하게나.$B$B황천날개 알은 찾기가 쉽지 않으니 눈을 크게 떠야 할 게야. 알의 위치를 찾기 어렵더라도 좌절하지 말라고. 이 용아귀 부족원들은 대부분의 황천날개 알을 수집하여 알려지지 않은 곳으로 실어 보낸다네.$B$B그 알은 이 섬의 생물들에게서는 물론이고 광산 내부, 수정 구조물 근처, 황천티끌 덤불과 황천연 광맥을 캘 때도 찾을 수 있을 거야.$B$B아, 알을 찾아다니는 걸 남들 눈에 띄지 않도록 하게. 모르고르까지 자네의 임무를 알 필요는 없으니까 말이야.', },
    {'어둠달 골짜기에 용아귀 주둔지에 있는 용병 야르질이 황천날개 알을 가져다 달라고 했습니다.', },
  },
  [11050] = {
    '황천날개 알 추가 수집',
    nil,
    nil,
  },
  [11051] = {
    '더 많은 악마 추방',
    {'우리가 악마에 대해 제대로 아는 거라면... 아니, 그동안 광범위하게 그들을 연구했기에 악마의 성향을 파악하고는 있소이다. 그들은 귀중한 순간이동기에 가해진 피해를 수리하는 데 서두르고 있소.$B$B그래서 말인데, 당신이 암흑룬을 얻을 기회를 얻고자 한다면, 이 추방의 수정을 가져가서 더 많은 악마를 처치하시오. 어차피 당신은 하늘경비대에 가해지는 악마의 위협을 줄여주고자 하고 있지 않소?$B$B우리가 \'기회\'라고 표현한 것은 우리가 진행하는 이 과정이 복합적인 결과를 낳기 때문이라오. 고로 당신은 암흑룬을 얻거나 완전히 다른 무언가를 얻을 것이오.', },
    nil,
  },
  [11052] = {
    nil,
    {'<손에 든 메달에 수정을 집어넣는 아카마>$B$B지금 내 힘은 전성기를 생각하면 보잘것없네. 수년간 악마와 아주 각별한 관계를 유지하고 있는 내가 이처럼 강력한 유물을 사용해도 되는지 모르겠군.$B$B때가 되어 자네가 일리단과 맞설 때 내가 자네 편에 서겠다는 약속의 증표로 이 메달을 주겠네. 강력하고도 믿을 만한 존재에게 전해주게나. 샤트라스의 아달에게 가져가면 되겠군.', },
    {'샤트라스에 있는 아달에게 카라보르의 메달을 가져가야 합니다.', },
  },
  [11053] = {
    '일어나라, 감독관이여!',
    {'당신만큼 오래 살아남은 일꾼이 있다니 드문 일이로군. 하찮은 일꾼이 새로운 직위를 얻게 되었다는 일이 더 희한하긴 하지만 말이야.$B$B<박수 치는 바르큘>$B$B대군주께서 당신을 찾고 계시오, 그런트 친구. 새로운 지위를 주시려 함이 틀림없으니 어서 가 보시오.', },
    {'어둠달 골짜기의 용아귀 주둔지에 있는 대군주 모르고르와 대화해야 합니다.', },
  },
  [11054] = {
    '사랑의 매와 감독관의 임무',
    nil,
    nil,
  },
  [11055] = {
    '부메랑 - 쓸모없는 일꾼들에 대한 방책',
    nil,
    {'어둠달 골짜기의 용아귀 주둔지에 있는 선임감독관 머드럼프가 20명의 게으른 용아귀 일꾼을 처벌한 후, 사용한 부메랑을 돌려달라고 했습니다.', },
  },
  [11056] = {
    '하지크의 거래',
    {'다른 자들이 우리 대화를 눈치채지 못하도록 조심해라. 아다리스가 알면 가만히 있지 않을 테니까 말이야.$B$B나는 아라코아 중에서도 가장 낮은 계급에 속해있다. 테로크의 지배 하에서라면 갈퀴사제들이 숭배하는 그 어두운 힘에 이미 제물로 바쳐졌을지도 모르지. 만일 네 말이 모두 사실이고 갈퀴사제들이 테로크의 귀환을 준비하고 있다면, 반드시 막아야만 한다. 네 지휘관이 뭐라고 하든지 간에 말이야!$B$B내가 제안을 하나 하지. 장막의 쉴라크 언덕에 내 오랜 거처가 있다. 거기에 있는 내 가방을 가져와다오.', },
    {'스케티스의 동쪽 하지크의 거처에서 하지크의 가방을 입수해 그에게 가져가야 합니다.', },
  },
  [11057] = {
    '저 아래 형제들의 문제',
    nil,
    nil,
  },
  [11058] = {
    '에펙시스 유물',
    {'오래전, 에펙시스라 알려진 아라코아의 사라진 분파가 이곳에 살았었다. 그들 문명의 잔존물이 아직 남아있지만 말이지. 우린 그 유물을 에펙시스 유물이라 부른다.$B$B우리가 그 유물에 대해 아는 건 유물을 적절히 사용하면 큰 이득을 얻을 수 있다는 것이다. 각 유물에 있는 수정을 수정이 보여주는 색깔에 따라 순서대로 누르면 되는데, 단계가 올라갈수록 그전 단계보다 더 어려워진다.$B$B그 유물을 가동하려면 에펙시스 결정이 필요하다. 에펙시스 직감에 대한 통찰을 얻으면 돌아와라.', },
    nil,
  },
  [11059] = {
    '유적의 수호자',
    nil,
    nil,
  },
  [11061] = {
    '아버지의 의무',
    {'다른 마법 영약에 대해서 말인데, 그 영약은 바시르 영지에서만 얻을 수 있소. 바시르 영지는 에테리얼 녀석들의 기지인데 에테리얼들은 외부인에게는 매우 적대적이라오. 그러니 영약을 얻기가 좀 위험할 수도 있소이다.$B$B그 영지가 어디냐고 물었소? 음, 날아다니는 탈것에 올라 북동쪽에서 북쪽으로 간다면, 격노의 괴철로 기지를 지나서 그 영지에 도착할 것이오. 바시르 수정괴철로는 영지의 북동쪽 구석에 있으니 잘 찾아보시오.$B$B이번 일을 맡아주어 고맙소. 우리 아이들에게 귀중한 선물이 될 것이오!', },
    nil,
  },
  [11062] = {
    '하늘경비대 전초기지',
    {'친구, 이곳에서 북쪽에 하늘경비대 전초기지에 있는 우리 친구들을 만나러 가 주겠나?$B$B하늘경비대 사령관인 켈러와 그의 하늘경비대는 양 진영 공통의 방어를 위한 임무를 수행 중인데, 켈러 사령관이 임무 지원에 탁월하리라 생각되는 친구들은 누구든 보내달라고 요청해 왔었다.$B$B그 탁월한 친구가 바로 당신이 아니겠나!$B$B될 수 있으면 빨리 하늘경비대 전초기지로 가서 그에게 우리의 안부를 전해 달라.', },
    nil,
  },
  [11063] = {
    '하늘파괴단의 일원',
    nil,
    nil,
  },
  [11064] = {
    '용아귀 경주: 노장의 연가',
    nil,
    {'날아다니는 탈것에 올라 "노장" 모르그 먹저를 쫓아가면서 탈것에서 떨어지지 않아야 합니다. "노장" 모르그 먹저와의 경주에서 이긴 후 승리의 소식을 어둠달 골짜기의 용아귀 주둔지에 있는 제이 노슬리우에게 전하십시오.', },
  },
  [11065] = {
    '에테르 가오리 생포!',
    nil,
    nil,
  },
  [11066] = {
    '더 많은 에테르 가오리 생포!',
    nil,
    nil,
  },
  [11067] = {
    '용아귀 경주: &quot;구린내&quot; 트로프',
    {'용아귀 부족이 잘 대해주지 않습니까? 나와 같이 지치고 저주받은 존재라 할지라도 기회를 주는 부족이 바로 용아귀랍니다. 자, 하늘파괴단에 당신의 가치를 증명해 보이고 싶다면, 날 이겨야만 할 겁니다.$B$B내가 저기 날아다니는 가오리들처럼 제치고 먼저 내빼서 당신이 내 꽁무니나 졸졸 따라다니게 해 드리지요... $B$B자, 준비가 되면 출발하도록 합시다.', },
    {'날아다니는 탈것에 올라 "구린내" 트로프를 쫓아가면서 탈것에서 떨어지지 않아야 합니다. "구린내" 트로프와의 경주에서 이긴 후 승리의 소식을 어둠달 골짜기의 용아귀 주둔지에 있는 제이 노슬리우에게 전하십시오.', },
  },
  [11068] = {
    '용아귀 경주: 최고참 코를록',
    {'나에게 도전하겠다는 건가? 이거 배꼽 잡겠군그래!$B$B자네 진심인가? 그렇다면 좋아, 받아들이지! 자네가 할 일은 죽지 않는 것일세. 그 정도는 할 수 있겠지? 내가 해골을 던지면서 형편없는 실력으로 비행하는 자네 주위를 뱅뱅 돌며 가지고 놀아도 살아 있어야 한단 말일세.$B$B자, 준비가 되면 바로 시작하도록 하지.', },
    {'날아다니는 탈것에 올라 최고참 코를록을 쫓아가면서 탈것에서 떨어지지 않아야 합니다. 최고참 코를록과의 경주에서 이긴 후 승리의 소식을 어둠달 골짜기의 용아귀 주둔지에 있는 제이 노슬리우에게 전하십시오.', },
  },
  [11069] = {
    '용아귀 경주: 편대사령관 이크만',
    nil,
    {'날아다니는 탈것에 올라 편대사령관 이크만을 쫓아가면서 탈것에서 떨어지지 않아야 합니다. 편대사령관 이크만과의 경주에서 이긴 후 승리의 소식을 어둠달 골짜기의 용아귀 주둔지에 있는 제이 노슬리우에게 전하십시오.', },
  },
  [11070] = {
    '용아귀 경주: 편대사령관 멀베릭',
    nil,
    nil,
  },
  [11071] = {
    '용아귀 경주: 대장 스카이쉐터',
    {'감히 이 스카이쉐터 님에게 도전을 하겠다는 건가? 하늘이 내 화려한 비행 솜씨로 산산이 갈라져, 자네 주위에 어둠만이 드리워진 걸 보게 될게야.$B$B병사, 자넨 승산이 없음에도 죽고자 덤비는군그래...$B$B자네처럼 어리석은 꿈을 꾸다가 같은 실수를 저지르는 녀석이 없도록 본때를 보여줘야겠군.$B$B자네가 준비되면 경주를 시작하자고!', },
    {'날아다니는 탈것에 올라 대장 스카이쉐터를 쫓아가면서 탈것에서 떨어지지 않아야 합니다. 대장 스카이쉐터와의 경주에서 이긴 후 승리의 소식을 어둠달 골짜기의 용아귀 주둔지에 있는 제이 노슬리우에게 전하십시오.', },
  },
  [11072] = {
    '적의 피',
    nil,
    nil,
  },
  [11073] = {
    '테로크의 몰락',
    {'당... 당신이 저 아라코아 포로와 협력하고 있다는 걸 알았지만 일부러 모른 척 했습니다. 당신의 직감이 옳았던 것 같군요.$B$B테로크를 이 세계로 불러 들이려는 당신의 노력을 지지했다면 아마 제 부하들은 반란이라도 일으켰겠지요... 그러니 제 입장을 이해해 주셨으면 합니다.$B$B하지크가 준비해 둔 그 물건을 가지고 가서 계획하신 대로 움직이십시오. 때가 되면 제 부하들을 보내 당신을 돕겠습니다. 이번이 아니면 기회가 없을지도 모르니 당신도 동료를 모아 가는 것이 좋을 겁니다.', },
    {'하지크가 준비한 잃어버린 시간의 제물을 스케티스의 가운데 지점에 있는 해골 더미로 가져가 테로크를 소환하여 처치해야 합니다. 임무를 마치면 하늘경비대 사령관 아다리스에게 가야 합니다.', },
  },
  [11074] = {
    '자손의 증거',
    {'갈퀴사제들에 의해 사로잡힌 자손의 이름이 여기 적혀 있군. 사냥꾼 게자라크, 수정거인 카로그, 이단으로 알려진 어둠의 비명소리 아카라이, 그리고 폭풍몰이 바키즈가 바로 그들이네. 테로크가 이 세계로 돌아오기 전에 그들을 처치해 버려야 해.$B$B잃어버린 시간의 아라코아들이 지닌 두루마리를 모은 다음 스케티스 소환 마법진에서 그들을 소환하도록. 그리고 그들을 처치한 증거를 하나씩 가져오게. 무척 강한 상대일 테니 동료를 모아서 가는 것이 좋을 거야.', },
    {'스케티스에서 소환 마법진과 해골 더미를 찾은 후, 잃어버린 시간의 두루마리 20개를 해골더미에 사용하여 자손들을 소환해야 합니다. 자손들을 처치한 후 그 증거로 아카라이의 갈퀴발톱, 카로그의 등뼈, 바키즈의 비늘, 게자라크의 발톱을 가지고 검은바람 영지에 있는 하지크에게 가져가야 합니다.', },
  },
  [11075] = {
    '황천날개 광산',
    {'황천날개 광산에 당신의 도움이 필요하오, 감독관 친구.$B$B이틀 전, 무시무시한 걸 발견하고는 우리 광산 작업이 삐걱대며 일시 정지되었소이다. 한 광부가 광산 깊숙한 곳을 채광하고 있었는데, 범상치 않은 수정 형성물에 곡괭이가 박혀서 빠지질 않았었지. 안타깝게도, 그 수정 형성물은 매우 화가 난 황천용에게 박혀 있던 것이었다오. 얕은 잠에서 깨어난 그 야수는 근처의 모든 일꾼들을 없애버렸다오.$B$B광산으로 가서 황천광산의 여군주에게 보고하시오. 이곳에서 남서쪽에 있는 광산의 입구에서 기다리고 있을 테니 말이야.', },
    {'황천날개 마루에 있는 황천광산의 여군주를 찾아야 합니다.', },
  },
  [11076] = {
    '화물 회수',
    {'우리 노동의 결과물이 이 광산 사방으로 흩어졌습니다. 짐수레에 가득한 화물이 이 광산의 구석구석에 어지러이 널려 있는 상황이란 말입니다! 현재로선 용의 공격 때문에 그 화물을 가지고 나올 수가 없으니 원... $B$B그래서 당신이 이곳에 온 것이지요. 이 광산에서 당신의 하루 일과는 광산 깊은 곳에서 일하는 일꾼이나 광부들이 남겨놓고 온 짐을 모아오는 겁니다.$B$B황천광산 화물을 모아서 나에게 가져오거나 내 자매들에게 가져가도록 하세요.', },
    {'어둠달 골짜기의 황천날개 광산에 있는 황천광산의 여군주가 황천광산 화물 15개를 가져다 달라고 했습니다.', },
  },
  [11077] = {
    '하찮은 골칫거리',
    {'잠에서 깨어난 그 용이 이 광산에 있는 더러운 다른 생물들을 성나게 했소이다. 황천수정 바위갈퀴와 황천수정 칼날발톱은 용아귀 부족에 있어선 불안의 씨앗임이 분명하오. 우릴 도와 녀석들을 없애 주시오. 내가 부탁하고 싶은 건 하루에 한 번씩 이 성가신 일을 해줬으면 하는 것이오.$B$B광산으로 들어가서 녀석들을 최대한 많이 없애주시오!', },
    {'어둠달 골짜기에 있는 황천날개 광산의 용아귀 현장감독이 황천수정 바위갈퀴 15마리와 황천수정 칼날발톱 5마리를 처치해 달라고 했습니다.', },
  },
  [11078] = {
    '하늘의 패권',
    nil,
    nil,
  },
  [11079] = {
    '가크를 위한 지옥 채찍',
    nil,
    nil,
  },
  [11080] = {
    '유물의 발산물',
    {'에펙시스 유물의 깨우침의 직감을 느꼈으니, 이제 유물의 작동에 관해 더 이해할 때가 되었다. 우린 당신의 조사가 우리에게 득이 되기를 몹시 바라고 있단 말이다!$B$B유물을 조율하는 과정에서 능력이 향상되면, 더 고급의 지식을 습득하게 될 것이다. 이러한 인식은 유물로부터 어떤 발산물의 형태로 올 것이야.$B$B그런 발산물을 얻으면 우리에게 돌아와라.', },
    nil,
  },
  [11081] = {
    '수렁피 일족 폭동',
    {'이 계획서는 뒤틀린 수렁피 일족 드레나이들이 계획한 폭동에 대한 개요가 기록되어 있습니다. 계획서에 의하면 광산에 있는 용은 그들의 탈출 계획을 진행하고자 주위를 딴 데로 돌리기 위한 수단이었던 것 같습니다. 광산에 흩어진 모든 시체들을 둘러보니, 그들의 계획이 실패로 돌아간 것 같습니다.$B$B탈출 계획서를 집어들고 황천광산의 여군주에게 가져가려고 자리를 뜹니다.', },
    {'황천광산의 여군주에게 수렁피 탈출 계획서를 가져가야 합니다.', },
  },
  [11082] = {
    '진실의 추종자',
    {'이 광산에서 일하는 광부들은 너무 멍청해서 이런 종류의 탈출 계획을 꾸밀 재간이 없습니다. 그러니 배후에 있는 자를 알아내서 처벌해야 합니다. 광산 깊숙이 들어가서 수렁피 감독관을 찾아보십시오. 만약 그들이 이 광산의 거주자들을 자기편으로 만들지 못했다면, 분명히 어딘가에 숨어있을 겁니다.$B$B그자의 위치를 찾으면 이 탈출의 배후에 있는 진정한 주모자를 말할 때까지 심문해야 할 겁니다. 배신자의 정체와 감독관의 장갑을 가지고 내게 돌아오십시오.', },
    {'수렁피 감독관을 찾아 그를 심문한 후, 수집한 정보와 감독관의 장갑을 가지고 황천광산의 여군주에게 가야 합니다.', },
  },
  [11083] = {
    '정신나간 수렁피 일족',
    {'그 용에 대해서는 걱정하지 마시오. 대책을 세워놨으니...$B$B이 광산의 깊은 곳에 정신이 이상한 수렁피 무리가 있소이다. 그 용이 나다닌 후부터 그 수렁피 일족이 출구로 나오지 못하고 갇혀 있었다오. 아무튼 거의 이틀간의 고립 상태 이후에, 품위나 체면이라곤 싹 다 잊어버렸지.$B$B이제 그들은 잔인성을 내비치며 폭력을 난무하는데만 의지하고 있소이다. 이 광산에서 우리에겐 그런 광기는 필요 없지 않겠소? 그들을 찾아 없애시오. 모두를 말이오.', },
    {'어둠달 골짜기에 있는 황천날개 광산의 노예감독 로나그가 정신나간 수렁피 현장감독과 5명의 정신나간 수렁피 광부를 처치해 달라고 했습니다.', },
  },
  [11084] = {
    '준비됐습니다, 대장님!',
    {'그래, 그래, 그래... 아직 살아 있단 말이지. 다행이군그래. 모르고르 대군주님께서 탑 안에서 당신을 기다리시니 어서 가 보시오!', },
    {'어둠달 골짜기의 용아귀 주둔지에 있는 대군주 모르고르와 대화해야 합니다.', },
  },
  [11085] = {
    '스케티스 탈출',
    {'안녕하십니까. 고맙게도 저를 도와주러 오셨군요. $B$B저 괴물 같은 아라코아 놈들이 황천의 가오리를 타고 정찰 임무를 수행하고 있던 저를 이렇게 잡아 놓았답니다.$B$B지금이 기회이니, 어서 이곳을 벗어납시다.', },
    {'하늘경비대 포로를 안전하게 호위한 후 하늘경비대 하사관 도린에게 보고해야 합니다.', },
  },
  [11086] = {
    '황혼의 차원문 방해하기',
    {'용아귀 부족의 대장으로서 자네의 임무 중 하나는 아웃랜드의 불타는 군단 무리를 진압하는 것이야. 일리단 님의 뜻을 거역하는 녀석들은 모두 숨도 못 쉬게 만들어 버려야지!$B$B그 어리석은 악마들이 나그란드의 서쪽 황혼의 마루에 차원문을 세우려 한다더군. 이제 배신의 대가를 치르게 해 줘야지 않겠나? 자네가 가서 우리 오크족의 빚을 받아 오게나.$B$B황혼의 마루로 간 다음 그곳에 있는 악마들을 처치하도록 하게. 최소한 하루에 한 번은 그들의 방어선을 무너뜨려야 하는 것이 자네의 임무일세.', },
    {'어둠달 골짜기의 용아귀 주둔지에 있는 대군주 모르고르가 죽음의 그림자 요원 20명을 처치하라고 했습니다.', },
  },
  [11089] = {
    '레스히드론의 영혼 대포',
    nil,
    {'어둠달 골짜기에 있는 용아귀 주둔지의 일리다리 군주 발사스에게 지옥강철 주괴 2개, 아다만타이트 형틀 1개, 코륨 동력핵 1개, 완전무결한 비전 정수 1개를 가져가야 합니다.', },
  },
  [11090] = {
    '정복자 제압',
    {'<이상해 보이는 장비를 들어 올리는 발사스>$B$B이것이 바로 영혼 대포라오. 이 대포를 잘 조절하면 그 위력은 가히 압도적이지. 이 대포는 내 피로 조절을 한 것이라오. 나와 똑같은 피가 내 형제인 레스히드론의 혈관에 흐르고 있으니... 그렇소, 그의 잘못된 충성심이 그를 끝장내 버릴 것이오.$B$B이 대포를 가지고 나그란드의 서쪽 지역에 있는 황혼의 마루로 날아가시오. 레스히드론이 있는 위치를 찾아서 그에게 이 대포를 발사하시오! 최고의 효력을 보려면 사거리 내에서 반드시 탈것에 오른 상태여야만 한다는 걸 잊지 마시오. 물러서지 않고 맞선다면 그자는 도망칠 수밖에 없을 것이오!', },
    {'어둠달 골짜기에 있는 용아귀 주둔지의 일리다리 군주 발사스가 나그란드에 있는 정복자 레스히드론에게 영혼 대포를 사용하라고 했습니다.$B$B또한 영혼 대포를 일리다리 군주 발사스에게 돌려줘야 합니다.', },
  },
  [11091] = {
    '특별한 감사',
    {'우리 오그릴라 오우거를 대신해서 당신의 노력에 존경을 표하는 바이다. 이제 당신에게 소개해 주고 싶은 자가 있으니 잘 들어라.$B$B조나스는 오그릴라에서 매우 신뢰할 만한 병참장교이지. 그는 오그릴라에 매우 헌신적인 친구들에게만 판매하는 물품을 보유하고 있다.$B$B당신이 바로 그런 친구가 아닌가?$B$B시간이 나면, 조나스에게 가서 대화를 나눠봐라. 우리가 알기에는 그 친구가 감사의 표시로 당신에게 작은 선물을 준비해 놓았다.', },
    nil,
  },
  [11092] = {
    nil,
    nil,
    {'어둠달 골짜기의 용아귀 주둔지에 있는 대군주 모르고르와 대화해야 합니다.', },
  },
  [11093] = {
    '배고픈 황천의 가오리',
    {'훈련이 잘 된 황천의 가오리는 놀랍도록 순종적이고 충직하다오. 안타깝게도 스케티스에 일어난 전쟁으로 인해 황천의 가오리들이 죽어가고 있소.$B$B우리가 하루에도 열 번 이상 출정을 나가기 때문에 불쌍한 황천의 가오리들은 쉴 틈이 없다오. 최근에는 먹이조차 부족해져서, 저 새인간들이 아니래도 황천의 가오리들이 다 굶어 죽을 지경이라오.$B$B우리를 도와 황천의 가오리 한 마리를 숲 남쪽으로 데려가 주시겠소? 가오리들은 신선한 고기라면 차원의 추격자라도 마다 않고 먹을 것이오.', },
    {'검은바람 비행기지의 남쪽 숲에서 황천의 가오리 우리를 사용하여 황천의 가오리를 불러낸 후 주변에 있는 검은바람 차원의 추격자를 처치하십시오. 임무를 마치면 하늘경비대 조련사 디사크에게 가야 합니다.', },
  },
  [11094] = {
    '모두 죽여라!',
    nil,
    nil,
  },
  [11095] = {
    '사령관 홉',
    nil,
    nil,
  },
  [11096] = {
    '하늘의 위협',
    {'하늘경비대에 합류하고 싶으시다고요? 음... 당신이 날 수 있다는 것은 저도 알고 있습니다만... 하늘에서 전투를 할 수 있겠습니까? 하늘경비대는 우리의 머리 위에 있는 크고 작은 위협으로부터 샤트라스를 안전하기 지켜낼 것을 맹세했습니다. 바로 북쪽 울타리 언덕에 살고 있는 골두니 오우거들도 우리를 위협하는 존재들이죠.$B$B샤트라스의 지상군은 그곳에 가기조차 어려운 상황입니다. 그래서 우리 하늘경비대가 조직된 것이랍니다. 오우거들을 저지하기 위해 그대가 무슨 일을 할 수 있나 보기로 하죠.', },
    {'울타리 언덕에 있는 골두니 오우거 20마리를 처치하고 샤트라스의 유울라에게 가야 합니다.', },
  },
  [11097] = {
    '무시무시한 함정',
    nil,
    nil,
  },
  [11098] = {
    '스케티스로!',
    {'울타리 언덕에만 우리 군대가 나가 있는 것은 아닙니다. 스키실 산맥 너머 스케티스라는 아라코아의 도시에는 훨씬 더 강력한 적들이 우리를 위협하고 있습니다.$B$B몇 달 전에 우리 정찰병들이 그 곳에서 아라코아의 거대한 무리를 발견했습니다. 아라코아들은 샤트라스까지 몇 차례 공격을 감행했지요. 그 일이 있은 후 우리는 아라코아의 도시 바깥 쪽에 군대를 주둔시키고 있습니다.$B$B이 폭약을 받아 스케티스 외곽에 있는 검은바람 비행기지의 하늘경비대 하사관 도린에게 가져다 주시겠습니까? 새로 고용된 병사라고 이야기하면 알아 들을 겁니다.', },
    {'폭약 꾸러미를 스케티스 외곽에 있는 검은바람 비행기지의 하늘경비대 하사관 도린에게 전해야 합니다.', },
  },
  [11099] = {
    '모두 죽여라!',
    nil,
    nil,
  },
  [11100] = {
    '사령관 아커스',
    nil,
    nil,
  },
  [11101] = {
    '무시무시한 함정',
    {'이 알도르 메달을 받아주시겠습니까? 전투가 끝나면 모르고르는 승리의 증거를 가지고 싶어할 테니까요. 모르고르는 이 메달을 죽은 알도르 사제회 병사들에게서 가져온 것으로 생각할 것입니다.$B$B이제 할 일은 섬광탄포를 사용하여 용아귀 부대에 신호를 보내는 것입니다. 준비가 되었다면 알려주세요. 우리는 그들을 무찌를 겁니다.', },
    {'알도르 사제회 군대와 사령관 아커스를 도와 샤타르 제단을 방어해야 합니다. 용아귀 군대를 무찌르면 알도르 메달을 가지고 용아귀 주둔지에 있는 대군주 모르고르에게 가야 합니다.', },
  },
  [11102] = {
    '폭격',
    {'까마귀 형상으로 날아다닐 수 있다... 그래서요? 날면서 땅에 있는 어떤 적을 전멸시켜 봤어요? 어때요, 해 보고 싶나요?$B$B북쪽으로 격노의 괴철로 기지와 남쪽으로 공포의 괴철로 기지는 내 후방뿐만 아니라 평화로운 오그릴라에 위협이 되고 있는 큰 골칫거리입니다. 타락한 대포들이 하늘경비대를 좌우 연타로 공격하고 있기에 그들의 기지에 우리 임무를 수행하러 갈 수가 없어요.$B$B당신 실력을 증명해 보이고 싶나요? 이 폭탄을 가져가서 괴철로 기지의 포탄 더미에 투하하세요. 물론 당신이 그들에게 맞아서는 안 되겠죠!', },
    nil,
  },
  [11105] = {
    '용사의 길 포기',
    nil,
    nil,
  },
  [11108] = {
    '군주 일리단 스톰레이지',
    {'이리 오게, 군주여. 일리단 님께서 기다리시네.', },
    {'일리단 스톰레이지를 만나야 합니다. 살아남는다면 샤트라스, 고난의 거리에 있는 바르타무스를 찾아가야 합니다.', },
