  [11529] = {
    '소를로프의 보물',
    {'우리 선원들이 그 왕대포를 잘 손질해 뒀네, 친구. 대포는 배의 다른 쪽 뱃머리에서 얌전히 자네를 기다린다네. 가서 소를로프가 시야에 들어오면 바로 쏠 수 있도록 준비하게!$B$B그 육지에서 어정거리는 바보 녀석을 처치하고 난 다음에 가서 보물을 가져와야겠어. 보다시피 우리는 이 빌어먹을 배를 떠날 수 없으니 말이야! 보물을 가져오면 자네 몫을 챙겨주지. 그 방패 말일세...', },
    {'왕대포를 사용하여 소를로프를 처치하고 소를로프의 보물을 되찾은 다음 자비로운 수녀호에 있는 선장 엘리스에게 가야 합니다.', },
  },
  [11530] = {
    '애시리테스의 방패',
    {'방패는 여기 있네. 자네도 방패 때문에 나랑 우리 선원들처럼 저주받지 않았으면 좋겠군. 꼭 집으로 돌아가라는 말은 안 하겠지만, 그렇다고 여기에 계속 머물 수는 없어... 그걸 되찾으려고 하는 사람이 분명히 있을 걸세.$B$B자, 그 방패를 가지고 당장 내 배에서 내리게!', },
    {'애시리테스의 방패를 울부짖는 협만의 고대 승강장에 있는 카마구아의 오르푸스에게 돌려줘야 합니다.', },
  },
  [11531] = {
    '이상한 엔진 부품',
    {'지금까지 본 것들과 전혀 다른 이 이상한 엔진 부품은 증기 양수기 부품이 아닌 것 같습니다. 어쩌면 K. 리 스몰프라이가 이 부품에 대해 뭔가 알지도 모릅니다.', },
    {'이상한 엔진 부품을 텔레도르의 K. 리 스몰프라이에게 가져가야 합니다.', },
  },
  [11532] = {
    '죽음의 흉터에서의 양동 작전',
    {'불타는 군단은 남쪽에 있는 병력이 확보되는 대로 우리를 향해 진격할 겁니다. 지금은 죽음의 흉터에 있는 스컬지가 녀석들의 발을 묶어두고 있지만, 오래가지는 않을 거예요.$B$B용매를 타고 죽음의 흉터로 날아가 이 비전 폭파물로 악마의 수를 조금이나마 줄여 줬으면 합니다.$B$B일이 잘 풀리면 우리가 캘타스의 추종자들에게서 무기고를 빼앗은 사실을 알아차리지도 못할 테니까요. 날아갈 준비가 되면 항구의 서쪽 지역에 있는 용매 조련사인 아이렌 클라우드브레이커와 대화하도록 하세요.', },
    {'전투마법사 아린나가 죽음의 흉터로 날아갈 준비가 되면 아이렌 클라우드브레이커와 대화해 달라고 했습니다. 죽음의 흉터에 도착하면 비전 폭파물을 사용해 지옥의 대군주 2명, 에레다르 마술사 3명, 격노의 집행자 12명을 처치해야 합니다.', },
  },
  [11533] = {
    '공습은 계속되어야 한다',
    {'죽음의 흉터에 있던 불타는 군단을 공습한 덕분에 우리 공격대에서 무기고를 점령했습니다.$B$B사실, 공격의 성과가 워낙 뛰어나 앞으로도 공습을 계속 진행하려고 합니다. 자, 이 비전 폭파물을 녀석들에게 퍼부어주세요.$B$B날아갈 준비가 되면 태양너울 항구에 있는 용매 조련사인 아이렌 클라우드브레이커와 대화하면 공습 임무를 지원해 줄 겁니다.', },
    {'전투마법사 아린나가 죽음의 흉터로 날아갈 준비가 되면 아이렌 클라우드브레이커와 대화해 달라고 했습니다. 죽음의 흉터에 도착하면 비전 폭파물을 사용해 지옥의 대군주 2명, 에레다르 마술사 3명, 격노의 집행자 12명을 처치해야 합니다.', },
  },
  [11534] = {
    '나순에게 보고',
    {'여기까지 와 주셔서 정말 기쁘군요! 성소를 차지했으니 이제 아웃랜드에 있는 샤트라스에서 이곳으로 쉽게 올 수 있도록 서둘러 차원문을 만들어야 합니다.$B$B저희 지도자이신 나순 총독께서는 공격대의 모든 힘을 차원문 제작에 쏟고 계십니다. 차원문을 만드는 데 어떻게든 도움이 될 만한 사람은 누구든지 보내라고 하셨죠.$B$B샤트라스에 가서 빛의 정원에 있는 아달의 방에서 나순 총독님과 이야기해 주십시오. 부탁합니다.', },
    {'포르타누스가 샤트라스 빛의 정원에 있는 총독 나순과 대화해 달라고 했습니다.', },
  },
  [11535] = {
    nil,
    nil,
    {'태양너울 무기고에 있는 대장장이 하우타가 동쪽의 어둠가시 미르미돈을 처치하고 그들의 열쇠를 사용해 상자에서 광석 3개를 훔쳐와 달라고 했습니다.', },
  },
  [11536] = {
    '이젠 멈추지 않는다...',
    {'모루가 갖춰져서 이제 일을 시작할 수 있겠어요.$B$B다른 보급품이 아무리 많더라도 어둠가시 철광석은 언제나 쓸 데가 많답니다. 이곳에서 싸워 이기려면 갑옷과 무기를 계속 공급해야 하니까요.$B$B동쪽 해안에 있는 나가들이 불타는 군단에 보낼 광석을 채굴해 상자에 담아놓았습니다.$B$B그 광석을 조달할 수만 있다면 여러 용도로 활용할 수 있을 텐데...', },
    {'태양너울 무기고에 있는 대장장이 하우타가 동쪽의 어둠가시 미르미돈을 처치하고 그들의 열쇠를 사용해 상자에서 광석 3개를 훔쳐와 달라고 했습니다.', },
  },
  [11537] = {
    '전투는 계속되어야 한다',
    {'우리는 계속해서 불타는 군단을 쳐부수고 사기를 꺾어야 합니다.$B$B증오의 사절은 전술상 군단의 요지를 지원하도록 배치된 군단 돌격병입니다. 이들은 자기편조차도 두려움에 떨게 만드는 공포의 대상이기 때문에, 증오의 사절이 나타나면 적들도 그 주위에 모이지 않을 수 없답니다.$B$B하지만, 그것도 오늘로 끝입니다. 이 깃발을 받으십시오. 그리고 가서 불타는 군단의 악마를 처치하세요. 마주치는 증오의 사절을 처치하고 우리 공격대 깃발을 꽂아 주십시오.', },
    {'선구자 이누로가 새벽 광장에서 불타는 군단 악마 여섯과 증오의 사절을 처치해 달라고 했습니다. 사절을 처치한 후 무너진 태양 깃발을 꽂아야 합니다.', },
  },
  [11538] = {
    '태양너울 무기고 습격 작전',
    nil,
    {'선구자 이누로가 새벽 광장이나 태양너울 무기고에서 불타는 군단 악마 여섯과 증오의 사절을 처치해 달라고 했습니다. 사절을 처치한 후 무너진 태양 깃발을 꽂아야 합니다.', },
  },
  [11539] = {
    '항구 공략전',
    {'캘타스의 정예부대 중 하나인 새벽칼날단이 태양너울 항구와 돈스타 마을에 주둔해 왔소.$B$B이 항구는 우리 계획을 실행하는 데 매우 중요한 요지이므로 당신이 새벽칼날단원을 처치해 주면 큰 도움이 될 것이오. 캘타스는 백성을 버리고 불타는 군단 편에 섰던 그날을 후회할 것이오.', },
    {'태양너울 무기고에 있는 마법학자 일라스타가 새벽칼날단 소환사 6명, 새벽칼날단 혈기사 6명, 새벽칼날단 명사수 3명을 각각 처치해 달라고 했습니다.', },
  },
  [11540] = {
    '새벽칼날단 격퇴',
    {'우리는 새벽칼날단을 태양너울 항구에서 몰아내는 데 성공했소. 이제 태양너울 항구는 완전히 우리 수중에 들어왔소. 하지만... 완전히 만족스럽진 않군.$B$B돈스타 마을로 가서 남아 있는 새벽칼날단 병력을 처치해 줬으면 하오. 그자들은 캘타스를 따라 이곳에 온 그날 이미 운이 다한 거요.', },
    {'태양너울 무기고에 있는 마법학자 일라스타가 새벽칼날단 소환사 6명, 새벽칼날단 혈기사 6명, 새벽칼날단 명사수 3명을 각각 처치해 달라고 했습니다.', },
  },
  [11541] = {
    '초록아가미 해안의 혼란 조성',
    {'나가들이 남동쪽에 있는 초록아가미 해안을 점령했습니다. 여성 나가인 어둠가시세이렌은 마법을 사용해 토착 멀록을 노예로 만들었더군요.$B$B그곳의 나가를 혼란에 빠트리는 임무를 수행해야 하는데, 그대가 이번 일에 힘써줬으면 합니다.$B$B여성 나가에게서 제어 구슬을 구해서 초록아가미 노예 곁에 던져, 구슬을 파괴하고 멀록을 해방시켜 주십시오.$B$B그러면 화난 멀록들이 아마 자기 주인들을 공격할 겁니다.', },
    {'초록아가미 노예 10마리를 풀어주고 태양너울 항구에 정박 중인 실버문의 긍지호에 있는 선장 발린드리아에게 가야 합니다.', },
  },
  [11542] = {
    '증원군 차단',
    {'이제 항구를 점령하기만 하면 태양너울에서 우리 통제권을 확고히 할 수 있습니다. 하지만 안타깝게도 새벽칼날단 보충병을 태운 선단이 항구에 있는 캘타스 군대를 도우러 오는 중입니다.$B$B이 기름을 받고 아이렌 클라우드브레이커에게 용매를 빌리세요. 그러면 선단의 돛 가까이 비행할 수 있을 겁니다. 웬만큼 가까이 다가가면 돛에 불을 지르세요.$B$B그리고 배에 내려 보충병들을 처치하세요. 돛에 불이 붙었으니 녀석들은 제대로 싸우지도 못할 겁니다.', },
    {'태양너울 무기고에 있는 구원자 카알란이 아이렌 클라우드브레이커와 대화하여 새벽칼날단 증원군 선단을 공격하라고 했습니다. 비행 상태에서 불타는 기름으로 돛에 불을 지르고, 배에 내려 새벽칼날단 보충병 6명을 처치해야 합니다.', },
  },
  [11543] = {
    '항구 차단 작전',
    {'우리 공격대가 드디어 태양너울을 점령했습니다. 하지만 안타깝게도 새벽칼날단 보충병을 태운 선단이 항구를 되찾으러 오고 있습니다.$B$B이 기름을 받고 아이렌 클라우드브레이커에게 용매를 빌리세요. 그 용매로 선단의 돛 가까이 비행할 수 있을 겁니다. 웬만큼 가까이 다가가면 돛에 불을 지르십시오.$B$B그리고 배에 내려 보충병을 처치하세요. 돛에 불이 붙었으니 녀석들은 제대로 싸우지도 못할 겁니다.', },
    {'태양너울 무기고에 있는 구원자 카알란이 아이렌 클라우드브레이커와 대화하여 새벽칼날단 증원군 선단을 공격하라고 했습니다. 비행 상태에서 불타는 기름으로 돛에 불을 지르고, 배에 내려 새벽칼날단 보충병 6명을 처치해야 합니다.', },
  },
  [11544] = {
    '아타말 병기',
    {'어둠달 골짜기 동쪽에 있는 아타말 언덕은 드레나이의 성지였지만 지금은 타락한 오크가 장악했습니다. 녀석들은 거기에서 특수한 광석을 채굴해 타락한 무기와 갑옷을 만들었지요.$B$B저는 그 병기를 나가의 광석과 함께 제련해 합금을 만들려고 합니다. 공격대에 속한 수도자들이 병기를 정화할 수 있도록 제 모루를 축복해 주었죠.$B$B어둠달 정예병, 학살자, 흑마술사의 병기를 빼앗아 이리 가져오세요. 제 모루에서 병기를 분해해 정화한 후 넘겨주시면 됩니다.', },
    {'쿠엘다나스 섬에 있는 태양너울 무기고의 대장장이 하우타가 그녀의 모루에서 아타말 병기를 분해해 정화된 아타말 금속 5개를 만들어 달라고 했습니다.', },
  },
  [11545] = {
    nil,
    nil,
    {'태양샘 섬의 태양너울에 있는 수도자 아유리가 10골드를 기부해 달라고 했습니다.', },
  },
  [11546] = {
    '영업 개시',
    {'어서 오세요, $N 님.$B$B제가 당신을 여러 가지 방법으로 도와드릴 수 있답니다.$B$B예를 들자면, 최근에 발견한 특이한 비약 조제법 같은 게 있으니까요.$B$B아제로스 곳곳에서 자라는 선홍딸기는 오랫동안 그저 아무짝에도 쓸모없는 물건이라고 알려졌어요. 하지만 저는 이 선홍딸기에 태양샘의 힘을 쪼이면 특수한 성질이 생겨난다는 사실을 알았답니다. 그걸 증류해서 먹으면 사람의 능력을 증폭시킬 수 있어요.$B$B가까운 덤불에서 조금 따서 가져와 주시면 무슨 말인지 직접 보여 드리죠.', },
    {'태양너울 항구에 있는 마르나가 쿠엘다나스의 덤불에서 선홍딸기 5개를 구해다 달라고 했습니다.', },
  },
  [11547] = {
    '너희가 순간이동을 알아?!',
    {'다들 공짜를 너무 좋아해!$B$B다들 거저 쓰는 것만 좋아했지, 정작 차원문을 만들어 내는 데 필요한 노력은 생각해 보지도 않는단 말이오.$B$B어쩌면 약간 교육을 해 줘야 내 쉴 새 없는 노력에 감사를 표할지도 모르겠군.$B$B이 수정을 가져가시오. 서쪽에 있는 핏빛수정, 여기 바로 남쪽에 있는 새벽 광장 차원문, 그리고 남동쪽에 있는 초록아가미 해안의 나가 제단에서 수정을 사용하시오.$B$B그러면 순간이동이 얼마나 귀중하고 고마운 건지 몸으로 느끼게 될 테지...', },
    {'태양너울 성소에 있는 점성술사 다르나리안이 핏빛수정, 새벽 광장 차원문, 초록아가미 해안의 나가 제단에서 점성술사의 수정을 사용해 달라고 했습니다.', },
  },
  [11548] = {
    '지속적인 지원',
    {'전쟁에서 죽어간 이들을 기리는 기념비를 세웠어요. 하지만 사랑하는 이를 잃은 가족들에게는 작은 위안에 불과하지요.$B$B저는 지금 쓰러진 영웅들의 배우자와 전쟁고아들을 돕고자 모금을 하고 있어요. 조금이나마 기부해 주신다면 정말 도움이 될 거예요.', },
    {'태양샘 섬의 태양너울에 있는 수도자 아유리가 10골드를 기부해 달라고 했습니다.', },
  },
  [11549] = {
    '관대한 후원가',
    {'정말 큰일을 해 주셨습니다, $N. 당신의 도움에 진심으로 감사드립니다.$B$B밖에서 수도자 아유리가 하는 일을 보셨나 모르겠습니다. 그녀는 따뜻한 마음씨에 다른 사람을 설득하는 재주까지 지녔는데도 기부금을 기대한 만큼 모으지는 못하고 있다더군요.$B$B당신이 그녀의 뜻에 크게 이바지해 주신다면 이 전쟁에서 당신이 이뤄낸 업적은 분명히 널리 알려질 것입니다. 어떻습니까?', },
    {'수도자 케어소스가 수도자 아유리의 선행을 도울 수 있도록 1000골드를 기부해 달라고 했습니다. 이 퀘스트를 완료하면 무너진 태양의 용사 $N|1으로;로; 알려지게 됩니다.', },
  },
  [11550] = {
    '기만자의 개입',
    {'온 힘을 다한 우리의 노력에도 캘타스의 음흉한 계획이 진행되었소. 기만자의 귀환을 준비하려고 태양샘이 가동된 거요.$B$B나와 아달의 최우선 과제는 태양샘의 현재 방어 상태를 알아내는 일이오. 이미 강력한 장벽이 설치되어 안으로 진입하는 길이 막혔소이다. 이 세상의 멸망을 막을 손길을 보내야 한다면 바로 지금일 거요.$B$B가서 쿠엘탈라스 북쪽의 쿠엘다나스 섬에 있는 대마법사 네툴에게 보고하시오.', },
    {'쿠엘탈라스 북쪽의 쿠엘다나스 섬에 있는 대마법사 네툴에게 보고해야 합니다.$B$B아이언포지와 실버문에 있는 비행 조련사가 목적지까지 데려다 줄 겁니다.', },
  },
