  [11585] = {
    '헬스크림의 경계',
    {'트롬카, $R|1이여;여;! 전쟁노래 요새에 온 것을 환영하네. 자네같이 뛰어난 호드의 영웅이 임무를 수행하고자 왔다는 사실이 매우 자랑스럽군!$B$B이 난공불락의 요새는 호드의 강인함을 나타내는 상징과도 같지. 우리는 노스렌드에서 이 근거지를 마련하려고 수많은 전투를 치러 왔네. 이곳에서 우리는 위대한 헬스크림의 아드님 지휘에 따라 얼음왕관을 향한 여정을 시작할 것이고 반드시 승리할 걸세!$B$B가로쉬 님께서 자네가 오기를 기다리시네.$B$B록타르 오가르!', },
    {'전쟁노래 요새에 있는 가로쉬 헬스크림과 대화해야 합니다.', },
  },
  [11586] = {
    '헬스크림의 경계',
    {'트롬카, $R|1이여;여;! 전쟁노래 요새에 온 것을 환영하네. 자네같이 뛰어난 호드의 영웅이 임무를 수행하고자 왔다는 사실이 매우 자랑스럽군!$B$B이 난공불락의 요새는 호드의 강인함을 나타내는 상징과도 같지. 우리는 노스렌드에서 이 근거지를 마련하려고 수많은 전투를 치러 왔네. 이곳에서 우리는 위대한 헬스크림의 아드님 지휘에 따라 얼음왕관을 향한 여정을 시작할 것이고 반드시 승리할 걸세!$B$B가로쉬 님께서 자네가 오기를 기다리시네.$B$B록타르 오가르!', },
    {'전쟁노래 요새에 있는 가로쉬 헬스크림과 대화해야 합니다.', },
  },
  [11587] = {
    '감옥 탈출',
    {'반갑습니다, $C님. 저는 달라란의 충직한 대변인인 도나단이라고 합니다.$B$B갑자기 이런 말을 해서 죄송하지만, 저흰 지금 대단한 위기를 겪고 있습니다. 아제로스 각지에서 마법사가 납치되는 사건이 일어나, 저는 이를 조사하는 데 누구든 동원할 수 있는 권한을 부여받았습니다.$B$B우리는 여기 바로 남쪽에 있는 거점부터 조사를 시작할 겁니다. 거기 붙잡혀 있는 마법사 한 사람을 구해줬으면 합니다. 일단 근처의 마법사 사냥꾼을 처치하고 감옥 열쇠를 얻어야 비전 감옥을 열 수 있습니다.', },
    {'호박석 절벽의 사서 도나단이 녹주석 거점에 있는 녹주석 마법사 사냥꾼을 처치하고 녹주석 감옥 열쇠를 얻어, 비전 감옥에 갇힌 마법사를 풀어달라고 했습니다.', },
  },
  [11590] = {
    '포획',
    {'$N 님, 문제가 생겼습니다.$B$B달라란에서 여기로 오던 대마법사 한 분이 사라졌다는 소식이 들어왔어요. 보아하니 마법사 사냥꾼이 꾸민 일인 듯합니다.$B$B재빨리 행동을 취해야 합니다. 에바노르 님이 어떻게 되었는지 당장 알아내야 구출할 희망이나마 얻을 수 있을 테니 말입니다.$B$B이 장치를 가져가세요. 그리고 녹주석 거점으로 돌아가 녹주석 마술사를 거의 죽을 지경에 이르게 한 다음 장치를 사용하십시오. 그러면 그를 묶어서 이곳으로 데려와 심문할 수 있을 겁니다.', },
    {'도나단이 녹주석 거점에서 상처입은 녹주석 마술사에게 비전 구속기를 사용하여 호박석 절벽으로 데려오라고 했습니다.', },
  },
  [11591] = {
    '스틸조의 짐마차로 보고하러 가기',
    {'그 머저리 같은 스틸조가 보급품을 실은 우리 마차를 끌고 지름길이랍시고 타운카 묘지로 갔네!$B$B우리는 타운카와 이제 막 협정을 맺은 참이란 말이야!$B$B그 마차는 지금 폐허 사이에 그냥 방치되어 있네. 놈이 밟고 지나간 무덤에서 일어난 타운카족 영혼만이 주변에 우글거릴 뿐이지.$B$B남쪽으로 가서 이 상황을 어떻게든 나아지게 할 수 있는지 살펴보게, $C. 그런트 레이지피스트와 그를 도울 몇 명을 딸려 보내 놨네.$B$B자네 손을 좀 빌렸으면 하니 도와줄 수 있는지 확인해 주게나.', },
    {'스틸조의 짐마차에서 그런트 레이지피스트와 대화해야 합니다.', },
  },
  [11592] = {
    '공격 개시!',
    {'스틸조라는 오크 하나가 시간을 아끼겠다면서 우리 묘지를 지름길로 택했습니다. 호드가 늘 이런 식으로 행동하는 게 아니었으면 좋겠습니다. 만약 그렇다면, 우리 타운카와 당신네는 끝입니다!$B$B하지만, 당신은 이 상황을 어떻게든 해 보려고 온 것 같군요. 다행입니다!$B$B스틸조의 저주받은 영혼이 이곳에 출몰하며 우리 조상의 유해를 더럽힙니다. 그놈을 어떻게든 끝장내야 합니다!$B$B이 일을 해결하고 나면, 북쪽 전초기지에 있는 대군주 보르고로크님께 알리십시오.$B$B아시겠습니까?', },
    {'수색꾼 프라우드후프와 전쟁노래 공격대원을 도와 스틸조의 짐마차를 공격하는 적을 막고 전투사령관 스틸조의 영혼을 처치해야 합니다.$B$B임무를 성공적으로 수행한 다음에, 보르고로크 전초기지에 있는 대군주 보르고로크에게 보고해야 합니다.', },
  },
  [11593] = {
    '명예로운 죽음',
    {'죽어간 전사들은 영웅에 걸맞은 장례를 치러줘야겠지만, 이 타운카 성지에 그들을 묻을 수는 없네.$B$B그렇다고 이곳까지 시체를 끌고 와 달라고 할 수도 없으니, 이걸 들고 묘지로 가 보게. 죽은 짐마차 경비병이나 일꾼이 보이면 횃불로 불을 붙여주게나.$B$B마지막 가는 길이 명예로울 수 있도록 도와주게, $C!', },
    {'스틸조의 짐마차에 있는 그런트 레이지피스트가 레이지피스트의 횃불을 사용해 죽은 짐마차 경비병이나 죽은 짐마차 일꾼의 시체 10구를 불태워 달라고 했습니다.', },
  },
  [11594] = {
    '영혼에 안식을',
    {'전투사령관 스틸조가 멍청한 탓에 일이 이 지경까지 이르렀네. 마차를 끌고 타운카 묘지를 지나가다니, 그 작자는 대체 무슨 생각을 했는지 모르겠네.$B$B어쨌든, 지나간 일은 지나간 일이지. 뒤처리는 이제 우리 손에 달렸고.$B$B대군주 보르고로크님께서 무슨 방법으로든 수색꾼 프라우드후프를 도우라고 하셨네. 수색꾼 프라우드후프는 조상 영혼이 안식을 취하도록 도와달라고 했거든.$B$B어떻게 하겠나, $C? 이 정도 일은 쉽게 해낼 수 있겠나?', },
    {'유령 현자나 되살아난 수색꾼 중에서 타운카 영혼 20명에게 안식을 찾아준 다음 스틸조의 짐마차에 있는 그런트 레이지피스트에게 보고해야 합니다.', },
  },
  [11595] = {
    '전쟁노래 요새 방어',
    {'헬스크림은 호드의 모든 희망을 그 어깨에 짊어지고 있네. 그가 흥분한다고 해서 자네까지 휩쓸려 임무를 포기하거나 하지는 말게나, $N.$B$B<끄덕이는 사울팽>$B$B내가 오그리마의 영웅이었던 자네 이름을 잊었으리라 생각했나? 네파리안의 머리에 창끝을 꽂아넣는 즐거움을 날마다 누렸던 것은 아니었어!$B$B스컬지가 이 요새를 공격하기 시작했네. 우리 전사들이 강하게 버티기는 하지만, 지원 병력이 절실하게 필요한 상태이지. 라즈고르에게 가 보게. 전쟁노래 요새 바로 밖에 있을 걸세.', },
    {'전쟁노래 요새 남쪽의 막사에 있는 대군주 라즈고르에게 보고해야 합니다.', },
  },
  [11596] = {
    '전쟁노래 요새 방어',
    {'$R, 헬스크림 님 때문에 호드의 병사로서의 임무를 저버리지 말게. 저분은 그 나름대로 큰 짐을 짊어지셨으니 말이네. 이 얼어붙은 황무지에서 아제로스의 모든 생명체의 사활을 걸고 스컬지와 싸워 이겨야 하는 사명을 안은 분일세. 그렇기에 우리가 자네의 힘과 지성을 더 요구하는 것이지.$B$B스컬지들이 이 요새를 공격하기 시작했네. 우리 병사들이 완강하게 버티곤 있지만, 지원 병력이 절실하다네. 전쟁노래 요새 바로 밖에 있는 라즈고르에게 가서 보고하게나.', },
    {'전쟁노래 요새 남쪽의 막사에 있는 대군주 라즈고르에게 보고해야 합니다.', },
  },
  [11597] = {
    '전쟁노래 요새 방어',
    nil,
    {'전쟁노래 요새 남쪽의 막사에 있는 대군주 라즈고르에게 보고해야 합니다.', },
  },
  [11598] = {
    '퇴마석 채석장 탈환',
    {'비록 전쟁노래 요새가 3미터가 넘는 강철과 암석으로 이루어진 방어선을 구축했다지만, 이 채석장은 상대적으로 약한데다가 네루비안의 공격에는 더욱 취약하네.$B$B<서쪽을 가리키는 라즈고르>$B$B이 채석장 너머에는 수천이나 되는 스컬지들이 공격 명령이 떨어지기만을 기다리고 있다네. 네루비안들이 우리 내부 방어선을 무너트린다면 곧 아서스의 언데드 군단이 우리를 짓밟겠지.$B$B그런 상황은 반드시 막아야 하네. 그러니 자네가 공격을 감행해 채석장에 있는 네루비안들을 없애줘야겠네.$B$B피와 번개의 힘을 취해 나아가게나!', },
    {'전쟁노래 요새의 대군주 라즈고르가 네룹아르 네루비안 15마리를 처치해 달라고 했습니다.', },
  },
  [11599] = {
    '나의 오빠, 타사리안',
    {'얼마 전에 우리 가족의 친구인 윌리엄 알러턴 씨에게서 우리 오빠가 이곳 용맹의 성채로 그와 같은 날에 입대했다는 소식을 들었어요. 그런데... 오빠는 이미 죽은 지 몇 년이나 지났거든요.$B$B전 오빠를 찾으려고 제가 가진 물건들을 다 팔아버렸어요. 하지만 관리들은 절 차갑게만 대했죠. 게다가 계속 캐묻고 다니면 체포할 거라고 협박하더군요.$B$B누군가... 더 높은 사람의 도움이 필요해요. 바로 당신 같은 사람이요. 윌리엄 씨는 아득골 광산에 배치되었다고 들었어요. 절 좀 도와주세요.', },
    {'아득골에 있는 광산 안에서 윌리엄 알러턴을 찾아야 합니다.', },
  },
  [11600] = {
    '윌리엄 알러턴의 최후',
    {'윌리엄의 목은 날카로운 물건에 베인 것 같습니다.$B$B더 자세히 살펴본 결과 레리사에게 전해 줄 단서를 하나 찾았습니다. 바로 윌리엄 알러턴의 이름이 적힌 군인증입니다.', },
    {'북풍의 땅의 용맹의 성채에 있는 레리사에게 군인증을 가져가야 합니다.', },
  },
  [11601] = {
    '분실물 보관소',
    {'윌리엄 씨라면 오빠를 찾는 일을 도와줄 수 있었을 텐데 안타깝네요.$B$B<군인 명부로 보이는 두툼한 책을 꺼내는 레리사>$B$B이건 빌려온 거예요, 아셨죠? 어디 보자, 그 군인증에 날짜 적혀 있나요?$B$B윌리엄 씨는 오빠를 입대자 대기 열에서 봤다고 했어요. 그러니 두 사람의 기록이 서로 비슷한 장에 쓰여 있을 거예요... 아, 여기 있네요!$B$B배치된 부대가... S 부대? 다른 부대들의 이름은 모두 얼라이언스 마을의 이름을 따서 지어졌어요.$B$B이 부대엔 누가 소속돼 있을까요? 배치된 곳은 어딜까요? 소문을 조사해야겠어요. 여관주인이 아는 게 없는지 알아봐 주세요.', },
    {'용맹의 성채의 여관에 있는 제임스 디콘과 대화해야 합니다.', },
  },
  [11602] = {
    '원천봉쇄',
    nil,
    {'전쟁노래 요새의 대군주 라즈고르가 네룹아르 알 주머니 10개를 없애 달라고 했습니다.', },
  },
  [11603] = {
    '포도주에 담긴 진실',
    {'저기 저 노인 콜번 씨에겐 군대가 생의 전부였답니다. 제대하고 나서도 자기 생명줄인양 집착하지요.$B$B콜번 씨는 장군, 부대, 여단, 파견대 할 것 없이... 뭐든지 안답니다. 바로 저분이 손님을 도와줄 분입니다.$B$B문제는 말입니다... 콜번 씨가 마음에 큰 상처를 입었다는 건데요. 쿨 티라스 포도주를 가져다주지 않으면 입도 뻥긋하지 않을 겁니다. 콜번 씨가 군인이었던 시절의 향수에 젖게 해 보세요.$B$B전 요 몇 달 동안 물건을 들여오지 못했답니다. 쿨 티라스에서 오던 상선이 이곳 북동쪽에서 침몰했거든요. 콜번 씨께 줄 포도주를 건져올 수 있는지 알아보시지요.', },
    {'용맹의 성채 북동쪽 물속에 있는 난파선에서 쿨 티라스 포도주 한 병을 구해 용맹의 성채 여관에 있는 노인 콜번에게 가져가야 합니다.', },
  },
  [11604] = {
    '탈영병',
    {'S 부대... S 부대라.. 흐음... 말이 안 되는걸... 부대 이름은 마을의 이름을 따서 짓게 되어 있는데...$B$B어둠골 병사들은 용의 안식처로 파견됐고, 황금골 애들은 협만으로 갔다네. 어디서 들었더라...$B$B그래, 맞아... 그 탈영병! 병영의 영창에 탈영병이 한 명 있었지. 갇힌 지 그리 오래되지 않았네. 계속 "S"가 자살을 뜻한다고 주절댔다지.$B$B그 탈영병을 찾아보게. 뭔가 아는 정보가 있을 테니 말이야.', },
    {'용맹의 성채의 내부 성채에 있는 탈영병과 대화해야 합니다.', },
  },
  [11605] = {
    '영예로운 선조',
    {'<석상을 바라보자 먼 곳에서 들려오는 것 같은 목소리가 머릿속에 울립니다.>$B$B놀라지 말지어다, 이방인이여. 나는 장로 아트카노크라고 한다. 투스카르의 선조이자 내 시대에는 숙련된 장인이었으며, 수세기에 걸쳐 채석장의 수호자였던 존재다.$B$B이 채석장은 침략을 받았고, 많은 석상이 완성되지 못한 채 방치되어 선조의 영혼들이 세상을 떠돌아다니게 되었다. 이 문제를 바로잡을 수 있게 도와주겠는가?$B$B우선 떠돌아다니는 선조의 이름을 알아야 한다. 그들의 조각상 근처에서 안표를 찾아보도록 하라.', },
    {'얼음바위 채석장 변두리에 있는 장로 아트카노크가 장로 케수크, 장로 사가니, 장로 타크릿의 조각상을 판독해 달라고 했습니다.', },
  },
  [11606] = {
    '인내심은 불필요한 미덕',
    {'눈에 보이는 네루비안들과 찾을 수 있는 알은 모조리 제거하고 있지만, 이놈들이 뚫은 동굴과 길목을 파괴하기 전까지는 끊임없이 기어나올 것이오. 그러자면 물론 고성능 폭약이 필요하지. 하지만 우리가 가진 재고 대부분은 고블린들과 대장장이들이 공성 전차와 무기를 만드는 데 사용해서 빼내기가 어렵소.$B$B퇴마석 채석장에 상당히 많은 양을 보관하고 있지만 그 벌레 녀석들 바로 밑에 있다는 게 문제라오. 가서 그 폭약들을 가져오시오.', },
    {'전쟁노래 요새의 병참장교 홀고스가 전쟁노래 군수품 15개를 되찾아 달라고 했습니다.', },
  },
  [11607] = {
    '구속된 영혼들',
    {'채석장이 공격을 받았을 때, 이곳에서 일하던 주술사들과 장인들이 살해되었노라. 무자비한 폭력이 난무하는 바람에 영혼 세계는 극심한 혼란에 빠졌고, 살해당한 이들의 영혼을 달래지 않으면 선조의 영혼들을 보금자리로 인도할 수조차 없기에 이르렀도다.$B$B침략자들은 그런 영혼들을 염두에 두지 않지만, 그자들이 데려온 녹주석 사냥개들은 영혼의 존재를 감지하고 그들을 구속하고 있다.$B$B녹주석 사냥개들을 제거하고 남은 힘을 사용해 저 무고한 영혼들의 속박을 풀어주도록 하라.', },
    {'얼음바위 채석장 변두리에 있는 장로 아트카노크가 카스칼라 장인 3명과 카스칼라 주술사 3명의 영혼을 해방해 달라고 했습니다.', },
  },
  [11608] = {
    '해충 박멸!',
    {'당신이 신경 써야 할 곳은 동, 서, 남, 북쪽이오. 요새 주변과 퇴마석 채석장 안쪽에 보면 완성된 네룹아르 구덩이 4개가 있소.$B$B<폭약 다발을 건네는 홀고스>$B$B조심해서 다루시오, $N. 잘못 다뤘다간 개죽음을 당할 테니 말이오.$B$B그 다발에는 시포리움 지하 폭탄이 들어 있소이다. 그걸 네룹아르 구덩이 중앙에 하나씩 장치해 놓으시오. 일이 잘 풀린다면 구덩이를 모두 무너트릴 수 있을 거요.$B$B어서 출발하시오!', },
    {'전쟁노래 요새의 병참장교 홀고스가 시포리움 지하 폭탄 묶음을 사용해 전쟁노래 요새의 동, 서, 남, 북쪽에 있는 네룹아르 구덩이를 파괴해 달라고 했습니다.', },
  },
  [11609] = {
    '유물 회수',
    {'살해당한 장인들과 주술사들의 영혼이 안식을 찾았으니, 공격으로 중단되었던 의식도 마음 놓고 계속 진행할 수 있게 되었구나. 그대에게는 본래 투스카르 주술사가 맡아야 하는 역할을 부탁하겠노라. 그리고 의식에 필요한 물건 모두 그대가 지니고 있어야 한다.$B$B새로 제작할 시간은 없으니, 채석장을 뒤져 공격에 희생된 이들이 남긴 물건들을 모으도록 하라. 수 세기나 지난 물건도 있으니 조심해서 다뤄야 할 것이다, $N. 혹시 침략자들에게 있다면 싸워서 얻어야 할지도 모르겠구나.', },
    {'얼음바위 채석장 변두리에 있는 장로 아트카노크에게 투스카르 의식용 화로 6개를 가져가야 합니다.', },
  },
  [11610] = {
    '선조에게 안식을',
    {'그대의 도움으로 선조의 영혼들이 본래의 안식처로 돌아갈 수 있게 되었도다. 바로 카스칼라의 장인들과 주술사들이 공들여 만든 조각상으로 말이다.$B$B채석장이 공격받아 선조의 영혼을 위한 의식이 중단되었으니, 그대가 그 의식을 마저 치러줘야겠다. 채석장에서 가져온 화로를 가지고 안표가 있는 조각상 앞에 서서 이 성가를 낭송하라.$B$B<이상한 언어로 된 말들이 머릿속으로 들어오지만 아무 어려움 없이 기억해낼 수 있습니다.>', },
    {'장로 아트카노크가 장로 케수크, 장로 사가니, 장로 타크릿의 영혼을 각자의 바위 기념비로 인도해 달라고 했습니다.', },
  },
  [11611] = {
    '스컬지의 거미줄에서',
    nil,
    {'전쟁노래 요새의 현장감독 모르투스가 전쟁노래 일꾼 5명을 풀어달라고 했습니다.', },
  },
  [11612] = {
    '채석장 탈환',
    {'잘 오셨습니다, $C|1이여;여;. 제 이름은 에타루크라 하고, 이 얼음바위 채석장의 장인이었습니다.$B$B저는 동료와 함께 의식에 필요한 조각상을 준비하고 있었는데, 갑자기 저 이상한 푸른 단이 고원 쪽에서 날아오더군요.$B$B단이 착륙하자 거기에 타고 있던 마법사들이 채석장을 덮쳐 내 동료를 죽이고 채석장을 약탈했습니다. 놈들을 막지 않으면 우리의 조각상을 모두 파괴하고 유물을 훔쳐 떠나버릴 겁니다. 침략자를 무찌르는 것을 도와주실 수 있을까요?', },
    {'얼음바위 채석장의 입구 근처에서 야영 중인 에타루크가 녹주석 보물 사냥꾼 12명을 제거해 달라고 했습니다.', },
  },
  [11613] = {
    '카루크의 맹세',
    {'놈들은 자기 종족을 크발디르라 부릅니다. 우리 종족에는 놈들에 대한 전설이 전해지고 있지요. 우린 놈들을 투크 하리크, 안갯속을 걷는 자라 부릅니다.$B$B제가 살던 마을도 저 안개 인간에게 파괴당했습니다. 제가 아는 모든 이들이 살해당했지요. 제 아버지 아리우트까지 말입니다.$B$B투스카르의 율법에 따라 저는 친족을 살해한 저들에게 반드시 복수해야 합니다.$B$B하지만 전 한낱 어부에 지나지 않고 아직 성년식조차 치르지 않았어요... 저 사악한 크발디르와는 도저히 맞서 싸울 수 없습니다.$B$B이 피비린내 나는 사명을 대신 맡아 주신다면 당신께 평생 감사하겠습니다.', },
    {'카루크가 스카디르 침략자 6명과 스카디르 구명요원 5명을 처치해 달라고 했습니다.', },
  },
  [11614] = {
    '숨겨진 진실',
    {'우리 언데드는 바리마트라스 님의 명령을 받고 이곳에 왔네! 그림자추적자의 임무란 간단하지. 바로 염탐이지. 뭐, 염탐하면서 간간이 암살도 하지만 말이야.$B$B내 최고의 현장 요원 중 한 친구가 행방불명되었네. 벌써 며칠째 보고가 없어 걱정이 이만저만이 아니라네. 아니, 죽지는 않았네! 죽었으면 그나마 낫겠지. 그 친구가 탈주했을까 걱정하는 걸세... 배신했을 가능성까지도 있지.$B$B그 친구가 마지막으로 보고한 위치는 퇴마석 채석장의 북쪽일세. 네루비안들의 정보를 모으는 중이었네. 그를 찾아봐 주게.$B$B차라리 죽은 거라면 좋겠군...', },
    {'퇴마석 채석장에서 그림자추적자 루터를 찾아야 합니다.', },
  },
  [11615] = {
    '네룹아르의 비밀',
    {'중요한 내용이 없나 일지를 훑어보다 마지막 장에서 미친 듯이 휘갈겨 쓴 다음 내용이 눈에 띕니다.$B$B\'강령군주다. 내 계산으로는 최소한 열둘은 된다.\'$B$B\'서쪽에 있는 농장은 일종의 스컬지 부화장으로 사용된다.\'$B$B\'희생자들이 즐비하다.\'$B$B\'아무래도 나를 노리는 것 같다. 요새로 황급히 달려갔다. 생포 당할 수는 없다.\'$B$B\'강력한 혈액독이다. 놈들이 날 물어뜯는다면 놀라게 될 거다.\'$B$B이 정보를 바르투스에게 전하려 발길을 돌립니다.', },
    {'루터의 일지를 전쟁노래 요새에 있는 그림자추적자 바르투스에게 가져가야 합니다.', },
  },
  [11616] = {
    '헬스크림에게 전해야 할 소식',
    {'<마지막 장을 찢더니 말아쥐는 바르투스>$B$B이 일지를 즉시 헬스크림 님께 전하게! 그동안 난 루터가 언급한 농장을 수색할 정찰대를 보내도록 하지.$B$B지원군이 필요하다는 사실을 헬스크림 님께 꼭 알리도록 하게.', },
    {'바르투스의 쪽지를 전쟁노래 요새에 있는 가로쉬 헬스크림에게 전해야 합니다.', },
  },
  [11617] = {
    '퇴로 차단',
    {'채석장 위에 둥둥 뜬 파랗고 하얀 단을 보셨을 겁니다. 마법사 사냥꾼들이 병력을 수송하는 데 사용한 운송 수단이지요. 그리고 지금은 우리 유물과 조각상 파편을 거기에 옮겨싣고 있습니다.$B$B놈들이 채석장에서 떠나지 못하게 막아야 합니다.$B$B채석장에서 곡괭이질을 하는 키 작은 생물들은 폭발물을 가지고 다닙니다. 녀석들을 잡아서 그 폭발물로 떠다니는 단을 파괴하세요. 지휘관은 땅에 내려온 네 번째 단에 있습니다만, 일단 나머지 단을 폭파하고 나서 그와 상대하도록 하지요.', },
    {'얼음바위 채석장 밖에 있는 에타루크가 북쪽 단, 동쪽 단, 서쪽 단을 파괴해 달라고 했습니다.', },
  },
  [11618] = {
    '증원군 접근 중...',
    {'<고개를 젓는 헬스크림>$B$B$R, 자네가 아는지는 모르겠지만, 우린 이 전쟁에서 이겨야 하네. 동원할 수 있는 병력은 모두 주요 전투 지역으로 투입해야 해. 말하자면 사소한 습격 임무 정도에는 정규 병력을 투입할 수 없다는 말일세.$B$B<잠시 말을 멈추더니 생각에 잠기는 헬스크림>$B$B바르투스가 일지에 언급된 농장에 증원군을 요청했다면...$B$B<손바닥을 마주치는 헬스크림>$B$B...보내 줘야지! 이곳 서쪽에 있는 농장으로 가 바르투스의 암살자들에게 가게. 그리고 증원군이 왔다는 사실을 알려주게나.', },
    {'전쟁노래 요새의 서쪽에 있는 전쟁노래 농장 전진기지에서 그림자추적자 이코리스를 찾아야 합니다.', },
  },
  [11619] = {
    '잔혹한 가멜',
    {'제 아버지는 이 도둑놈들의 두목인 잔혹한 가멜에게 살해당했습니다. 놈은 해변에 있는 동굴에 거처를 차린 것 같습니다.$B$B이미 놈들을 처리해 주셨으니 마무리도 지어주시면 감사하겠습니다, $N 님. 아버지의 원수를 갚아주신다면 남은 재산으로나마 보답하겠습니다.', },
    {'카루크가 채찍파도 해안에 있는 잔혹한 가멜을 처치해 달라고 했습니다.', },
  },
  [11620] = {
    '아버지의 유언',
    {'불가사의한 일이군요. 하지만 이 의문을 풀어줄 이가 누구인지 알 것 같습니다. 크발디르가 서쪽 해변에 있는 사원에 나가 여사제를 살려두고 있단 소문을 들었습니다.$B$B반갑지 않은 손님, 크발디르에게 나가 여사제도 잔뜩 화가 났을 게 분명합니다. 아버지께서는 그녀가 어떤 식으로든 당신에게 협조하리라 기대하시는 것 같아요.', },
    {'채찍파도 해안의 비늘 제단에 있는 비야를 찾아야 합니다.', },
  },
  [11621] = {
    '레비로스의 서판',
    {'서판에 새겨진 내용 중 이해할 수 있는 문장이 있는지 찬찬히 살펴봅니다. 마침내 이런 구절을 발견했습니다.$B$B"안개의 아들은 가장 어두운 물속에 사는 존재를 위해 천 명의 영혼을 모을 것이다. 그러면 그의 사절이 깨어나리라."$B$B카루크가 이 정보에 관심을 보일 것 같습니다.', },
    {'카루크에게 돌아가 서판에서 알아낸 정보를 알려줘야 합니다.', },
  },
  [11622] = {
    '채찍파도의 비밀',
    {'이 의문을 풀어줄 이가 있기는 합니다. 크발디르가 사원에 나가 여사제를 몇 명 살려두고 있단 소문을 들었습니다.$B$B여사제 비야도 반갑지 않은 크발디르 손님들 때문에 무척 화가 났을 겁니다. 어쩌면 이 서판의 내용이 무엇인지 더 자세히 알려줄 수도 있겠지요.', },
    {'북풍의 땅에 있는 채찍파도 해안의 폐허에서 비야를 찾아야 합니다.', },
  },
  [11623] = {
    '관리인 처치',
    {'마법사 사냥꾼 부대의 우두머리는 관리인 인시비우스라고 하는 무시무시한 놈입니다. 저희 종족은 여러 세대에 걸쳐 육지의 짐승과 바다의 짐승을 사냥해 왔지만, 이 관리자만큼 무시무시한 놈은 본 적이 없습니다.$B$B놈은 날아다니는 단 하나를 채석장 가장자리 높은 지대에 착륙시키고는, 아래를 내려다보며 작업이 잘 진행되는지 감시한답니다. $N 님, 제게 당신은 조상님들이 보내신 도움의 손길로밖에 생각되지 않습니다. 저 마법사 사냥꾼들을 채석장에서 완전히 몰아낼 수 있도록 도와주셨으면 좋겠습니다.', },
    {'얼음바위 채석장의 밖에 있는 에타루크가 관리인 인시비우스를 처치해 달라고 했습니다.', },
  },
  [11624] = {
    '하늘이 안다',
    {'스승이신 선견자 그림워커 님께 무슨 일이 일어났는지 알아내야 하네!$B$B이전에 스승님께서는 마그모스 근처에서 마그나타우르를 관찰하고 계셨지. 하지만, 스승님 휘하에 있던 병사 하나가 방금 돌아와서 전하기로는, 부대는 궤멸했고 선견자께서는 깊은 곳 어딘가로 끌려가셨다는군.$B$B자살이라 할 만큼 위험한 임무를 수행하도록 그대를 보내기 전에 정령에게 조언을 구해 보아야겠네.$B$B엘드라나스 폐허에 임페리안이라는 바람의 정령이 있네. 무척 현명한 존재이니 우리가 알고 싶은 바를 알려줄지도 모르네.', },
    {'엘드라나스 폐허에서 임페리안과 대화해야 합니다.', },
  },
  [11625] = {
    '나즈잔의 삼지창',
    {'500년 전에 아즈샤라께서는 레비로스라는 존재를 물속 깊은 곳에 가두셨지. 그런데 지금 이 짐승 같은 놈들이 여왕님께서 하신 일을 망치려고 해!$B$B놈들은 우리 도시를 점령하고 레비로스를 깨우려고 우리 형제들을 제물로 바치기 시작했어! 나가가 모두 없어지자 그다음에는 투스카르까지 손을 대기 시작했지. 그 영혼들은 결코 안식을 찾을 수 없을 것이야!$B$B내가 너를 돕겠다, $R. 널 돕는 게 결국은 내 원수를 갚는 데 도움이 될 테니 말이다. 놈들의 우두머리인 라그나는 채찍파도 폐허 북동쪽 멀리 살고 있는데, 황금 삼지창을 가지고 다니지. 그걸 내게 가져오도록 해!', },
    {'채찍파도에 있는 비야가 채찍파도 폐허 북동쪽 끝에서 라그나 드라카룬트를 처치하고 나즈잔의 삼지창을 가져다 달라고 했습니다.', },
  },
  [11626] = {
    '사절',
    {'레비로스가 갇힐 무렵 채찍파도 일족의 황제는 라즈 나즈잔이었다. 아즈샤라께서 나즈잔의 삼지창에 축복을 내려 그 짐승이 탈출하면 즉시 처치할 수 있게 했지.$B$B이제 네가 그걸 사용해야 한다, $R. 날 화나게 한 저 커다란 짐승에게 반드시 복수하고야 말겠어.$B$B삼지창을 가지고 채찍파도 폐허 북쪽에서 가장 가까이 있는 빙산을 찾아라. 빙산 아래로 깊숙이 잠수하면 레비로스를 찾을 수 있을 게다.$B$B놈의 몸뚱이 깊숙이 삼지창을 꽂아버려! 일을 마치면 원래 왔던 곳으로 돌아가라. 더는 네 얼굴을 보고 싶지 않으니까.', },
    {'채찍파도 폐허 북쪽 가장자리에 떠 있는 빙산 아래 물속에서 레비로스를 찾을 수 있습니다. 나즈잔의 삼지창으로 레비로스를 처치하고, 채찍파도 해안 북쪽에 있는 카루크에게 가야 합니다.', },
  },
  [11627] = {
    '물과 불의 격돌',
    {'아아, 그대가 열망하는 것은 정보로구나. \'선견자\'라는 자가 어디 있는지, 어떤 상태인지 알고 싶단 말인가. 그 선견자는 정작 자신 앞에 닥친 문제는 예견하지 못한 모양이다.$B$B그래도 정보를 어떻게 알아낼 수 있는지 가르쳐 주겠다. 하지만, 그전에 먼저 일을 한 가지 해줘야겠다.$B$B남쪽에서 벌어진 불과 물 사이에 벌어진 전쟁을 그대도 봤을 테지. 잿불바람은 끊임없이 물보라가 있는 웅덩이를 끓게 하는데, 둘은 오랫동안 계속 싸우고 있다.$B$B잿더미 마루와 부글거리는 웅덩이에 가서 싸움을 멈추도록 그 둘을 설득하고 와라.', },
    {'잿불바람과 물보라를 항복시킨 다음 엘드라나스 폐허에 있는 임페리안에게 가야 합니다.', },
  },
  [11628] = {
    '잠입 작전',
    {'북동쪽에 있는 엔킬라 사원은 스컬지가 북풍의 땅에서 본거지로 삼은 곳이라오. 그 벽 너머에서 스컬지 지휘관이 네루비안을 보내 우릴 포위했지.$B$B계속 방어만 한다면 전멸당하고 말 것은 분명하지만, 어떻게 놈들을 공격하느냐 하는 문제에서 내 조언자들은 서로 다른 의견을 내놓고 있소. 두름은 무조건 돌격하자는 의견이고, 현자 아이레는 잠입을 주장하고 있다오.$B$B사원 때문에 그 주변의 대지와 야생동물은 오염되었소. 아이레는 이렇게 오염된 동물인 스컬지 매머드 모피를 은폐물로 사용하면 침투요원을 몰래 들여보낼 수 있다고 생각한다오.', },
    {'타운카르 마을의 족장 윈터게일이 스컬지 매머드 모피 5개를 가져다 달라고 했습니다.', },
  },
  [11629] = {
    '영매에게 돌아가기',
    {'내 부탁을 들어주었으니, 이제 그대의 부탁을 들어주겠다.$B$B이 근원을 보르고로크 전초기지에 있는 영매에게 전해라. 현명한 영매라면, 그대가 원하는 정보를 얻도록 하는 의식을 알 것이다.', },
    {'보르고로크 전초기지에 있는 영매 스날팡에게 임페리안의 근원을 가져가야 합니다.', },
  },
  [11630] = {
    '오염된 흙',
    {'현자 아이레의 말로는 사원 밖의 오염된 흙으로 가죽을 손질해 망토를 만들면, 어지간한 하급 스컬지는 그것을 입은 자를 보지 못할 거라는군. 그러니 그대가 사원 안에 숨어들어 갈 수 있을 거요.$B$B그 말이 맞아야 할 텐데. 정보를 수집하고 반격을 계획할 기회는 이번이 마지막이오.$B$B그녀가 말한 흙은 요새의 서쪽과 남쪽 지역을 뒤져보면 찾을 수 있을 거요. 흙을 다 모으면 가져오도록 하시오.', },
    {'타운카르 마을의 족장 윈터게일에게 스컬지 흙 8개를 가져가야 합니다.', },
  },
  [11631] = {
    '바람이 전해 주는 환영',
    {'이걸 받게, $N. 근원을 자네에게 맞도록 조율했네.$B$B내 바람의 토템 앞에서 근원에 영혼을 맡기고 선견자 그림워커 님의 운명에 정신을 집중하게.$B$B바람의 정령은 어디든지 닿을 수 있지. 운이 좋으면 환영을 볼 수 있을 걸세.', },
    {'보르고로크 전초기지에 있는 스날팡의 토템 앞에서 임페리안의 근원을 사용하여 의식을 거행하고 선견자 그림워커에 관한 정보를 입수한 다음 영매 스날팡과 대화해야 합니다.', },
  },
  [11632] = {
    '차가운 바람에 실려...',
    {'영혼 착취자 이시릭스의 껍질 조각을 손에 들자 이제 시작에 불과하다는 생각이 듭니다. 바로 그 순간, 승리가 덧없다는 생각이 듭니다. 리치 왕에게는, 풀려나서 호드를 덮치려고 대기하는 이시릭스 같은 짐승이 천 마리는 더 있는 게 분명합니다.$B$B이 전리품을 전쟁노래 요새에 있는 대군주 사울팽에게 가져가십시오. 당신이 막강한 스컬지 대리인과 싸워 이겼다는 사실을 알려야 합니다.', },
    {'이시릭스의 단단한 등껍질을 전쟁노래 요새에 있는 대군주 사울팽에게 가져가야 합니다.', },
  },
  [11633] = {
    '은밀한 정찰',
    {'현자 아이레가 망토를 완성했소. 그걸 가지고 엔킬라 사원으로 가시오. 망토를 입으면 도시 안에 있는 스컬지들은 대부분 그대를 알아볼 수 없을 거요. 이번이야말로 적의 방어 상태와 지휘관에 관한 정보를 얻을 수 있는 최적의 기회요.$B$B망토를 입고 도시 안에 있는 큰 첨탑 세 곳을 정찰해 주시오. 망토를 입고 있으면 대부분의 스컬지는 그대를 보지 못할 테지만, 엔킬라 누더기골렘에 너무 가까이 다가가면 정체가 탄로 날 수 있으니 조심하시오.', },
    {'타운카르 마을의 족장 윈터게일이 엔킬라 사원에서 마력 깃든 스컬지 망토를 착용하고 피의 첨탑, 고통의 첨탑, 부패의 첨탑을 정찰해 달라고 했습니다.', },
  },
  [11634] = {
    '바람의 기수 토보르',
    {'자넨 정말로 강하군, $N. 내 생각에 자네가 능력을 발휘할 곳은 따로 있을 것 같군. 모든 문제의 근원이 되는 곳 말일세!$B$B<서쪽을 가리키는 라즈고르>$B$B스컬지 천 마리, 날아다니는 죽음의 기계가 적어도 네 대, 그 밖에 또 뭐가 있을지는 아무도 모르지.$B$B하지만 곧 자네가 그걸 알아내고 또 상대하게 될 걸세... 그렇지, 성가시게 우리 대문을 두드려대는 저 스컬지 놈들을 자네가 쓸어버리는 걸세.$B$B이곳 전쟁노래 요새의 꼭대기로 가서 바람의 기수 토보르에게 보고하게. 그 친구가 자넬 기다리고 있다네.', },
    {'전쟁노래 요새에 있는 바람의 기수 토보르에게 보고해야 합니다.', },
  },
  [11635] = {
    '선견자 그림워커의 영혼',
    {'동쪽에 있는 마그모스로 가야 하네.$B$B놈들의 동굴 속 깊숙이 들어가게.$B$B그리고 선견자 그림워커 님의 영혼과 이야기하게!', },
    {'마그모스 안에 있는 선견자 그림워커의 영혼과 대화해야 합니다.', },
  },
  [11636] = {
    '마법 융단을 타고',
    {'전차랍니다. 공성 전차를 말하는 겁니다! 서쪽 해안에 있는 가로쉬의 상륙지에 중장갑 전차가 배치돼 있습니다. 그 전차에 태워줄 테니 그걸로 스컬지들한테 호드의 정의를 보여주는 겁니다!$B$B문제는 당신을 그곳까지 데려가는 일입니다만, 와이번에 태워 보내드릴 순 없습니다. 너무 위험해서 말입니다.$B$B저기 저 친구 보이십니까? 저 금발 엘프 말입니다. 네, 야니라는 친군데 저 친구가 해안까지 안전하게 데려가 줄 겁니다. 도착하시면 시체분쇄자인 고지를 만나 보십시오!', },
    {'전쟁노래 요새에 있는 야니와 대화한 다음 해안으로 안전하게 이동할 방법을 찾아야 합니다. 가로쉬의 상륙지에 도착한 다음에는 시체분쇄자 고지와 대화해야 합니다.', },
  },
  [11637] = {
    '카가니슈',
    {'얼마 안 되는 우리 그런트가 다 죽고 나자 놈들은 날 이곳으로 끌고 내려와 의식을 치르며 살해했네. 이 모든 것을 행한 자는 카가니슈라 알려졌지.$B$B자네가 그를 돕는 주술사를 처치하기는 했지만 내 영혼은 아직도 이곳에 얽매여 있다네. 날 살해한 다음에 내 시체로 우상을 만들었는데, 그 때문에 내가 여기서 꼼짝달싹 못하는 걸세. 카가니슈는 우상을 써서 내가 아는 주술 지식을 익히고 있다네.$B$B그 우상을 찾아온 다음 내 영혼에 사용해 줬으면 하네. 카가니슈는 동굴 안 어딘가 가까운 곳에 있네.$B$B그렇게 해야만 비로소 나는 자유로워질 수 있을 걸세.', },
    {'카가니슈의 우상을 되찾아 선견자 그림워커의 유골에 사용하고 나서 마그모스 안에 있는 선견자 그림워커의 영혼에 전달해야 합니다.', },
  },
  [11638] = {
    '유골 돌려주기',
    {'$N, 이제 내 영혼이 자유를 얻었으니 유골을 영매 스날팡에게 전달하여 내 위엄을 되찾아주지 않겠나? 유골은 아래 바닥에 있네.$B$B영매를 마지막으로 봤을 때는 서쪽의 보르고로크 전초기지에서 있었는데, 아직 그곳에 있는 게 느껴지네.', },
    {'영혼 아래에 있는 선견자 그림워커의 유골을 보르고로크 전초기지에 있는 영매 스날팡에게 전달해야 합니다.', },
  },
  [11639] = {
    '마그모스에 복수를',
    {'대군주님께서 주술사가 내 상처를 치료하지 못하게 하셨네. 좋은 교훈이 될 것이라며 그동안 실패를 반성할 시간을 주시더군.$B$B하지만 자네라면 나나 마그모스에서 죽어간 이들을 대신하여 원수를 갚아줄 수 있네!$B$B영매가 자네를 마그모스로 보내 선견자를 구출하려 한다는 걸 아네.$B$B마그모스는 바로 동쪽에 있지. 어서 떠나게, $C... 가서 마그나타우르와 그에 딸린 스노볼트 하인들을 없애주게. 전초기지의 명예를 되찾아 주게나!', },
    {'마그모스 주술사 10명, 마그모스 채집꾼 5명, 마그모스 분쇄자 3명, 마그모스레가르의 짝 3명을 처치한 다음 보르고로크 전초기지에 있는 오르트로쉬에게 가야 합니다.', },
  },
  [11640] = {
    '힘이 깃든 말',
    {'그대가 설명한 첨탑에는 스컬지 지휘관의 부관인 대사제들이 살고 있소. 놈들은 스컬지 부대를 지휘할 뿐만 아니라 머리 위에 떠 있는 죽음의 요새와 스컬지 지휘관에게 갈 수 있는 열쇠를 쥐고 있다오.$B$B지휘관을 찾아 처치하지 않으면 우린 끊임없이 몰려드는 스컬지 놈들에게 휩쓸려버릴 거요. 두름이 아무리 적과 싸우고 싶어해도 우리는 이곳 주민을 무방비 상태로 남겨둘 수는 없소. 그러니 모두 그대에게 달렸다오, $N. 대사제 셋을 처치하고 놈들이 가진 두루마리를 가져오시오.', },
    {'대사제 내페세트의 두루마리, 대사제 탈레트카의 두루마리, 대사제 안도라스의 두루마리를 타운카르 마을에 있는 족장 윈터게일에게 가져가야 합니다.', },
  },
  [11641] = {
    '용맹한 공격',
    {'난 처음부터 우리 족장, 윈터게일에게 강력한 우리 전사들을 모아 놈들의 기지에 쳐들어가야 한다고 주장해 왔소. 하지만 그는 내 조언을 들은 체도 하지 않았고, 현자 아이레의 계획대로 쥐새끼처럼 살금살금 숨어들어 갈 생각이나 하고 있지.$B$B더는 못 기다리겠소. 족장에게는 전사를 출전시킬 용기조차 없으니, 직접 공격하는 게 얼마나 효과적인지 그대가 보여줬으면 하오. 엔킬라 사원을 습격해 적이 눈에 띄는 대로 모조리 없애버리시오, $N.', },
    {'엔킬라 구울 15마리와 엔킬라 강령술사 5명을 제거한 다음 타운카르 마을에 있는 두름 아이스하이드에게 보고해야 합니다.', },
  },
  [11642] = {
    '전차는 저절로 고쳐지는 게 아니야',
    {'저 전차들을 고쳐서 다시 가동시켜야 하네! 물론 전차가 저절로 고쳐지는 건 아니지. 그 일은 모부가 해 줄 걸세. 그에게 가서 필요한 게 뭔지 물어보게. 자네라면 나를 열받게 한 그 노움만한 녀석을 두 동강 내기 전에 입다물게 할 수 있을지도 모르겠군!$B$B난 노움이 싫단 말이야...', },
    {'가로쉬의 상륙지에 있는 모부와 대화해야 합니다.', },
  },
  [11643] = {
    '모부의 전공기압축식 자동조절 동력제어기',
    nil,
    nil,
  },
  [11644] = {
    '고강도 금속판!',
    {'전차를 땜질할 쇠붙이가 필요해! 가로쉬의 상륙지 주변이나 항구 아래쪽을 찾아보면 고강도 금속판을 찾을 수 있을 거야.$B$B나가서 금속판을 찾을 때는 조심해야 할 거야. 저 안개가... 그 뭐냐... 대략 공격적이거든! 저 안개 때문에 이 상륙지와 옆에 있던 투스카르 마을이 아주 박살이 났지. 불쌍한 투스카르들...', },
    {'북풍의 땅의 메아리 해안에 있는 모부가 고강도 금속판 10개를 모아달라고 했습니다.', },
  },
  [11645] = {
    '더럽고 역겨운 스노볼트!',
    {'저 더럽고 냄새 나는 스노볼트가... 제 보급품을 죄다 훔쳐갔어요!$B$B스노볼트가 뭐냐고요? 코볼트 비슷한 놈들인데, 마그나타우르를 섬기는 것들이에요. 보살핀다고 해야 하나... 아무튼 이상한 놈들이에요!$B$B날 도와줄 사람이 어디 없을까요? 그 조그만 놈들이 내 물건을 훔쳐간 게 분명해요. 비행장 북쪽에 두면 안전하리라 생각한 제가 바보였죠.$B$B제 부탁 좀 들어주세요. 북쪽에 있는 그 짜증 나는 마그모스로 가 주세요. 빨리 제 물건을 되찾아 주신다면 현금으로 보상할게요!', },
    {'크래프티의 보급품 상자 10개를 회수한 다음 피즈크랭크 비행장에 있는 크래프티 워블스프로킷에게 가져가야 합니다.', },
  },
  [11646] = {
    '심문할 시간',
    {'잘 들으십시오, $N 님. 자세히 설명할 시간이 없습니다.$B$B지금 탑에서 당신이 끌고 온 포로를 심문할 준비를 하고 있는데, 그놈이 순순히 불지 않으면 당신이 좀 도와줘야 할 것 같습니다.$B$B그놈이 잡혀 있는 곳에 가서, 사서 노르만티스에게 제가 보내서 왔다고 말씀하십시오.', },
    {'도나단이 호박석 절벽의 마법사 탑에 있는 사서 노르만티스를 도와주라고 했습니다.', },
  },
  [11647] = {
    '가마솥 무력화',
    {'엔킬라 사원에 있는 역병 가마솥들이 걱정이군요, $N 님. 윈터게일 족장님은 적에 대한 당신의 판단과 지식을 신뢰하고 계십니다. 사원에서 족장님의 계획을 진행하면서 그 가마솥들도 처리해 주시겠습니까?$B$B가마솥의 내용물을 중화할 토템을 준비해 놓았습니다. 가마솥을 찾아 그 근처에서 토템을 사용하기만 하면 됩니다. 스컬지의 무기를 하나 파괴할 때마다 승리에 한 발짝씩 다가간다는 사실을 명심하십시오.', },
    {'타운카르 마을에 있는 현자 아이레가 그녀의 토템을 사용해 엔킬라 사원의 동쪽 가마솥, 중앙 가마솥, 서쪽 가마솥을 중화시켜 달라고 했습니다.', },
  },
  [11648] = {
    '설득의 기술',
    {'와 줘서 감사합니다, $R|1이여;여;.$B$B아시다시피 키린 토의 행동강령은 \'극단적인 수단\'을 취하는 것과는 거리가 멉니다. 지금 같은 절박한 상황이라도 말입니다.$B$B하지만 외부인인 당신이라면 그런 제약을 따를 필요가 없으니 무슨 짓을 해서든 정보를 캐낼 수 있을 겁니다.$B$B필요하다면 어떤 방법을 써도 좋습니다. 에바노르 님이 어디에 잡혀 계신지 당장 알아내야 해요!$B$B전 여기서 선반이나 정리하고 있겠습니다. 참, 이걸 받으십시오. 낡은 물건이지만 쓸모 있을지도 모릅니다...', },
    {'호박석 절벽의 사서 노르만티스가 구속된 녹주석 마술사에게 신경 자극기를 사용해 대마법사 에바노르의 위치를 자백받아 달라고 했습니다.', },
  },
  [11649] = {
    '광포한 폭풍 파편',
    {'이제 사소한 다툼은 해결했지만, 그대와 영매를 돕는 일이 아직 남았구나.$B$B보다시피 나를 섬기는 자들이 불과 물 사이에 계속되는 싸움으로 분노한 상태였다. 한동안 분노가 누그러들지는 않겠지만, 그대들에게는 도움이 될 테지.$B$B그대를 도우려면 나를 섬기는 자들의 결정이 필요하다. 폐허 안으로 깊숙이 들어가 폭풍에게서 결정을 회수해오라.', },
    {'돌개바람 파편 5개를 구해 엘드라나스 폐허에 있는 임페리안에게 전달해야 합니다.', },
  },
  [11650] = {
    '몇 개만 더...',
    {'아시다시피 우린 마그나타우르 동굴 바로 남쪽에 비행장을 만들어 버렸어요!$B$B<입속으로 피즈크랭크를 욕하는 크래프티>$B$B그 늙은이가 나더러... 뭐랄까... 기발한 방법으로 이 상황을 해결하라고 떠넘겼지 뭐예요. 빨리 처리하지 않으면 녀석들이 여기 내려와 우릴 토막 내버릴 거예요!$B$B그러니까 당신이 좀 도와주셨으면 해요.이 목록에 있는 물건을 모두 구해주세요. 부탁해요!', },
    {'크래프티의 도구, 과충전된 축전기, 로크나르의 모래 한 줌을 모아 피즈크랭크 비행장에 있는 크래프티 워블스프로킷에게 전달해야 합니다.', },
  },
  [11651] = {
    '준비 끝!',
    nil,
    {'메아리 해안에 있는 시체분쇄자 고지와 대화해야 합니다.', },
  },
  [11652] = {
    '나삼의 평원',
    {'준비가 되면 전차를 몰고 남동쪽에 있는 나삼의 평원까지 가게.$B$B그리고 전차의 무기로 스컬지 놈들을 없애버리게나. 포위당하면 "고기톱날"과 지뢰로 박살 내버리면 될 걸세! 우리 병사를 보거든 건져서 싣고 말이야! 연료통은 아마 사방에 널려 있을 걸세.$B$B될 수 있으면 한가운데 있는 건물로 가서 무슨 일이 벌어지고 있는지 살펴보게.$B$B행여나 일을 다 마쳤는데도 살아있다면 헬스크림 님께 달려가도록 하게!', },
    {'나삼의 평원에서 호드 공성 전차를 사용해 스컬지 병사 100마리를 처치하고 부상당한 전쟁노래 전사 3명을 구해야 합니다. 또한 스컬지 공격을 지휘하는 자의 정체를 알아내야 합니다.$B$B임무를 완료하면 전쟁노래 요새에 있는 가로쉬 헬스크림에게 보고해야 합니다.', },
  },
  [11653] = {
    '허... 이젠 별로 크지도 않잖아!',
    {'그거면 될 거예요! 그럼 이번에 드릴 부탁은...$B$B이 광선총을 가지고 북쪽의 마그모스로 가세요. 맞설 만한 크기가 돼야 싸워보든가 하지 않겠어요? 말하자면...녀석들의 크기를 좀 줄여야 한단 말이죠.$B$B효과를 측정하려면 광선총 효과가 남아있는 동안 마그나타우르를 처치해야 해요. 마그모스 분쇄자든 마그모스레가르의 짝이든 상관없어요. 마그모스레가르의 자손은 신경 쓰지 마세요... 그것들은 너무 작아서 걱정할 필요도 없어요.', },
    {'마그모스 분쇄자 또는 마그모스레가르의 짝 5마리에게 잘못될 리 없는 크래프티의 초고급 축소 광선총을 시험하고, 피즈크랭크 비행장에 있는 크래프티 워블스프로킷에게 가야 합니다.', },
  },
  [11654] = {
    '피의 첨탑',
    {'혈액을 조사해 보니 아직 따뜻합니다. 피를 흘린 지 얼마 되지 않았으니 희생자가 아직 살아 있을지도 모릅니다.$B$B스컬지가 피로 무얼 하려는 걸까요? 이전에는 스컬지가 포로를 생포하는 데 관심을 보인 적이 한 번도 없습니다. 이 혈액의 출처를 알아낸다면 스컬지가 꾸미는 계획의 전모를 파악할 수 있을지도 모릅니다.', },
    {'피의 첨탑에서 신선한 피가 든 유리병에 관한 정보를 찾아야 합니다.', },
  },
  [11655] = {
    '안갯속으로',
    {'안개를 가로질러 북쪽으로 가면 가로쉬의 상륙지가 있습니다. 남쪽은 폐허가 된 제 고향인 팔에아입니다. 크발디르라 알려진 고대 세력이 양쪽 모두를 약탈했습니다.$B$B이 무시무시한 야만족은 무자비하게 우리 종족을 말살했고 당신네 종족을 내륙으로 몰아넣었습니다.$B$B팔에아 투스카르가 남긴 것은 어디까지나 개인 물품일 뿐인데 크발디르가 유물이라 생각하고 모두 가져갔습니다. 복수를 좋아하는 종족은 아니지만 이런 일을 당하고 참을 수는 없습니다. 투스카르 유물을 되찾아야 합니다. 안갯속으로 들어가 유물을 찾아 주십시오...', },
    {'북풍의 땅의 메아리 해안에 있는 팔에아의 왈토르가 크발디르를 제거하고 투스카르 유물 8개를 되찾아 달라고 했습니다.', },
  },
  [11656] = {
    '저항의 불꽃을 지펴라!',
    {'투스카르 마법은 당신들이 말하는 주술에 가깝습니다. 비록 당신들과는 다른 식으로 정령을 섬기긴 하지만, 꽤 강력한 힘을 불러낼 수 있지요.$B$B<횃불처럼 생긴 지휘봉을 눈앞에 흔들어 보이는 왈토르>$B$B이 투스카르 횃불을 가지고 안갯속으로 돌아가 크발디르 배를 불태워 주셨으면 합니다. 가로쉬의 상륙지를 뒤덮은 안갯속에 놈들의 배 중 세 척이 정박해 있고, 나머지 한 척은 남쪽에 있는 팔에아 바닷가에 정박해 있습니다. 놈들의 배를 불태워 주십시오! 모두가 볼 수 있도록 말입니다!', },
    nil,
  },
  [11657] = {
    '횃불을 잡아라!',
    {'이제 횃불을 제대로 던질 줄 아시니 잡을 수도 있나 보겠습니다!$B$B여기 불붙지 않은 횃불 한 묶음을 가져가십시오. 화톳불로 간 다음 횃불을 하늘 높이 던져 올리세요. 그리고 횃불이 땅에 닿기 전에 잡았다가... 다시 하늘로 던져 올리십시오! 연달아 4개를 던졌다 잡고는 돌아오시면 됩니다.$B$B조심하세요! 땅에 떨어지면 위험합니다! 하나라도 놓쳤다가는 화톳불로 돌아가서 처음부터 다시 해야 합니다. 행운을 빌겠습니다!', },
    {'횃불 4개를 연달아 던졌다 잡고 나서, 불꽃마술의 대가와 대화해야 합니다.', },
  },
  [11658] = {
    '차선책',
    {'알았어요, 진정해요! 광선총은 아무 효과도 없었을지 모르지만, 아직 다른 방법이 있어요.$B$B<손가락으로 딱 소리를 내는 크래프티>$B$B비행장으로 쏟아지는 관심을 다른 쪽으로 돌려야 해요! 그러려면 물건이 몇 가지 필요해요.$B$B<당신을 평가하려는 듯 위아래로 훑어보는 워블스프로킷>$B$B스틸조의 짐마차가 어리석게도 서쪽에 있는 타운카 묘지를 지름길 삼아 지나가려고 했대요. 그리고 타운카 영혼들한테 몰살당했지요.$B$B그곳에 가서 죽은 오크가 걸친 옷을 벗겨 오세요. 놈들의 깃발도 하나 가져오시고요!', },
    {'전쟁노래 의복 10개와 전쟁노래 깃발 1개를 모아 피즈크랭크 비행장에 있는 크래프티 워블스프로킷에게 가져가야 합니다.', },
  },
  [11659] = {
    '수정구를 파괴하라!',
    {'놈들은... 포로의 피를 뽑아... 그 수정구에... 부어 넣는다네.$B$B<잠시 눈을 감으며 고개를 뒤로 떨구는 그룸>$B$B피의 공작을 먹이려고... 피를... 죽음의 요새로 보내지...$B$B<크게 숨을 들이쉬는 그룸>$B$B야, 약속해 주게... 피의... 수정구를... 파괴하고... 주네크에게... 소식을 전하겠다고.$B$B<의식을 잃는 눈 추적자>', },
    {'피의 수정구 5개를 파괴한 다음 타운카르 마을의 눈 추적자 주네크에게 보고해야 합니다.', },
  },
  [11660] = {
    '고대 뱃사람의 뿔피리',
    {'팔에아에 있는 우리 동족을 처음으로 습격했던 지휘관은 오라부스라 알려진 크발디르 부관이었습니다. 놈은 두 마을이 무너지는 모습을 지켜보고 나서 안갯속으로 유유히 사라졌지요.$B$B그 무렵에 오라부스가 크발디르 안개마술사들에게 뿔피리를 주었다는 소문이 있었습니다. 이 뿔피리로 필요할 때 오라부스를 소환한다더군요.$B$B크발디르 안개마술사들로부터 그 뿔피리를 구해 오라부스를 불러내야 합니다. 놈을 처치한다면 이 야만인들도 물러나겠지요.$B$B남쪽에 있는 팔에아에서 안개마술사들을 찾아보십시오.', },
    {'북풍의 땅의 메아리 해안에 있는 팔에아의 왈토르가 고대 뱃사람의 뿔피리를 구해달라고 했습니다.', },
  },
  [11661] = {
    '키잡이 오라부스',
    {'뿔피리를 가지고 전쟁노래 부두로 가십시오. 부두 끝까지 가서 뿔피리를 부십시오. 뿔피리 소리를 듣고 오라부스가 나타날 겁니다.$B$B그 사악한 괴물을 처치하고 돌아오십시오!', },
    {'북풍의 땅, 메아리 해안에 있는 팔에아의 왈토르가 고대 뱃사람의 뿔피리를 사용해 키잡이 오라부스를 소환해 낸 다음 처치해 달라고 했습니다.', },
  },
  [11662] = {
    '카루크를 찾아라!',
    {'팔에아 생존자 중 하나인 카루크는 북풍의 땅에 있는 크발디르 본거지로 떠났습니다. 남쪽으로 멀리 한때는 눈이 많이 내리던 곳입니다. 카루크는 채찍파도 해안을 내려다보는 절벽에 숨어 있는 모양입니다.$B$B카루크를 찾아 당신이 이곳 메아리 해안에서 우리 동족을 위해 어떤 일을 했는지 알려주십시오.$B$B카루크에게 가장 안전하게 가려면 당신네 요새를 통과해 가십시오. 전쟁노래 요새에서 남쪽으로 난 길을 따라가 보면 채찍파도 해안에 도착합니다.', },
    {'채찍파도 해안에서 카루크를 찾아야 합니다.', },
  },
  [11663] = {
    '소식 전달',
    {'고맙습니다, $C님! 당신이 알아낸 정보는 키린 토에 더없이 중요한 정보랍니다$B$B우리는 에바노르 님을 구출할 계획을 세울 테니 어서 도나단에게 돌아가 보십시오.', },
    {'노르만티스가 호박석 절벽에 있는 사서 도나단에게 보고해 달라고 했습니다.', },
  },
  [11664] = {
    '안개를 헤치고',
    {'밖에서 안개 짐승들 소리가 들려요. 놈들이 울부짖는 소리 때문에 미칠 지경이지만 그렇다고 도망칠 생각은 못 하지요. 도망쳐봤자 어디로 가겠어요? 얼마 못 가고 다시 잡혀서 다른 이들처럼 처형될 텐데요.$B$B이방인이여, 저 안개는 대체 어디까지 뻗어 있나요? 이 대륙 전체를 벌써 다 뒤덮어버렸나요?$B$B제가 이 안개에서 빠져나가게 도와주지 않으시겠어요? 아버지가 계신 곳까지 길을 찾을 수 있을 것 같거든요. 아버지의 생령이 느껴져요. 여기에서 가까이에 계시네요.', },
    {'무투의 딸을 데리고 안전하게 안개에서 빠져나간 다음 장로 무투와 대화해야 합니다.', },
  },
  [11665] = {
    '도시에 나타난 악어',
    {'안녕하신가, 젊은이. 잠시 멈춰 이 늙은이에게 말을 걸어 주다니 어찌나 고마운지 모르겠군. 고향에 문제가 생겼거든.$B$B한 행상인이 최근 개구쟁이 어린애들한테 아기 악어를 한 무리 팔았다지 뭔가. 이 사나운 악어 여러 마리가 도망쳐서 도시의 수로에 숨어 있다네.$B$B스톰윈드나 오그리마로 가서 가장 튼튼한 낚싯대를 드리우고 이 조그만 악마를 잡아오게. 한 마리 꼭 보고 싶으니 말이야.', },
    {'샤트라스 북동쪽 실미르 호수에서 낚시를 하는 노인 발로에게 아기 악어 한 마리를 가져가야 합니다.', },
  },
  [11666] = {
    '미끼 도둑',
    {'황금 화살고기에 대해서는 이미 알고 있겠지? 테로카르 숲에 흐르는 강에서 흔히 볼 수 있고, 수도사들이 특히 좋아하는 물고기 말일세. 하지만 검은지느러미 화살고기에 대해서는 들어본 적이 없을걸.$B$B이 물고기는 교활하기로 악명이 높은데다 낚기 매우 어렵다네. 녀석들은 미끼를 잡아채기 전에 조금씩 물어뜯어 보기 때문에 마지막 순간에 낚싯대를 당겨야 하거든.$B$B테로카르 숲 강가에서 낚싯줄을 드리우고 검은지느러미 화살고기를 한 마리 잡아오게. 녀석들은 흐르는 물에서만 사니 호수는 피하게나.', },
    {'샤트라스 북동쪽 실미르 호수에서 낚시를 하는 노인 발로에게 검은지느러미 화살고기 한 마리를 가져가야 합니다.', },
  },
  [11667] = {
    '놓친 물고기',
    {'피글루스터의 미꾸라지는 나그란드 물속 바닥을 돌아다니는 엄청나게 빠른 물고기지. 사는 내내 몸집이 커지는 데다가, 사납기로 유명하다네.$B$B몇 년 전에 내가 세상에서 제일 큰 미꾸라지를 잡을 뻔했지... 녀석은 낚싯줄을 끊고 도망가 버렸다네.$B$B자네만한 재간이 있는 낚시꾼이라면 뭘 해야 하는지 알려줄 필요도 없겠지? 나그란드 호수에서 자네의 운명이 기다리고 있다네.', },
    {'샤트라스 북동쪽 실미르 호수에서 낚시를 하는 노인 발로에게 세상에서 제일 큰 미꾸라지를 가져가야 합니다.', },
  },
  [11668] = {
    '새우잡이',
    {'장가르 습지대 호수에는 커다란 물고기가 아주 많지. 녀석들이 뭘 먹는지 궁금하게 여긴 적은 없나?$B$B새우를 먹고 산다네. 큰 민물새우 말일세. 로크 말로는 새우로 할 수 있는 요리가 무궁무진하다더군.$B$B하지만 새우는 너무 작아서 낚싯줄을 드리워서는 잡을 수가 없다네. 내가 방법을 알려 주지. 불어오른 미늘아가미송어를 낚아다가 배를 갈라 보게. 운이 좋으면 새우를 한두 마리 발견할 수 있을 걸세.', },
    {'샤트라스 북동쪽 실미르 호수에서 낚시를 하는 노인 발로에게 큰 민물새우 10마리를 가져가야 합니다.', },
  },
  [11669] = {
    '지옥피퉁돔 분비선',
    {'이 늙은이가 곧 사냥을 떠날 거라네. 아즈샤라 해변에서 매머드 상어를 쓰러뜨릴 거라고.$B$B문제는 말일세, 물에 녹지 않는 특별한 독소를 작살 끝에 발라야 한다는 거지. 이런 종류의 독소를 만들려면 유독성 물고기에서 분비선을 구해야 한다네. 지옥피퉁돔을 잡아야 한단 말이지.$B$B이 무서운 물고기는 지옥불 반도와 어둠달 골짜기의 물속에서만 산다네. 큰 놈으로 하나 잡아 와서 이 늙은이를 기쁘게 해주게나.', },
    {'샤트라스 북동쪽 실미르 호수에서 낚시를 하는 노인 발로에게 기괴한 지옥피퉁돔 한 마리를 가져가야 합니다.', },
  },
  [11670] = {
    '오크라니까요, 정말이에요!',
    {'당신이 그 뭐냐... 얻어온 복장으로 전쟁노래 오크 위장복을 만들었어요. 그리고 그걸 지난번 할로윈 축제 때 쓰고 남은 것하고 합쳤지요. 정말 끝내주는 생각이죠? 그러니까 절대 실패할 걱정은 없어요!$B$B여기요, 이 주머니에 위장복과 당신이 가져온 깃발이 들어 있어요. 마그모스로 돌아가 마그나타우르 족장인 마그모스레가르를 처치해 주세요.$B$B놈들의 주의를 전쟁노래 오크에게 돌리려면 그 위장복을 입은 상태에서 깃발을 꽂아야 해요.', },
    {'전쟁노래 오크로 변장한 상태에서 전쟁노래 깃발을 마그모스레가르의 시체에 꽂은 다음, 위장복을 가지고 피즈크랭크 비행장에 있는 크래프티 워블스프로킷에게 가야 합니다.', },
  },
  [11671] = {
    '시간과의 싸움',
    {'사실이었군요. 놈들이 그분을 데리고 있습니다.$B$B$R|1이여;여;, 이제 뭘 해야 할지 분명해졌습니다. 반드시 그분을 구출해야 합니다.$B$B저는 이곳에 남아 구출 작전 준비를 돕겠습니다. 당신에게는 다시금 부탁을 해야겠군요. 이번에는 감옥 열쇠를 가져와 주십시오. 우리가 이 살란드라는 자에 대해 아는 것이라고는 그녀의 보호막을 해제하지 않고는 일체 피해를 줄 수 없다는 사실뿐입니다.$B$B보호막을 해제할 수 있는 특수 폭약을 만들어 두었습니다. 그걸 사용하면 임무를 완수할 수 있을 겁니다. 열쇠를 찾으면 제게 가져다주십시오.$B$B서둘러 주세요, $N 님. 에바노르 님이 고통받고 계십니다!', },
    {'호박석 절벽의 도나단이 녹주석 보호막 폭파기로 녹주석 거점의 방어막을 해제한 후, 살란드를 처치하고 열쇠를 입수해 달라고 했습니다.', },
  },
  [11672] = {
    '입대하는 날',
    {'입대하러 오신 게 맞습니까? 원칙대로라면 다른 보충병들처럼 줄을 서야 합니다. 그리고 지금은 사병 명부가 사라져서 원칙대로라면 새 명부가 오기 전까지 아무도 입영 처리를 할 수 없습니다.$B$B운이 좋으시군요, $N 씨. 아웃랜드에서의 당신의 활약상은 익히 들었으니 말이죠. 솔직히 저 얼간이들이랑은 비교가 안 되는 실력의 소유자잖습니까? 그러니 당신은 줄을 설 필요가 없습니다.$B$B병영으로 가서 알로스 장군님께 보고하십시오. 당신 같은 실력자를 보면 더없이 기뻐하실 겁니다.', },
    {'용맹의 성채 병영 안에 있는 장군 알로스를 만나야 합니다.', },
  },
  [11673] = {
    '날 구해줘요!',
    {'언젠가는 누군가 날 구하러 올 줄 알았다니까요... 물론 좀 늦은 감이 없잖아 있지만 말이죠.$B$B전 여기 스노볼트하고 친하게 좀 지내 보려고 왔는데, 놈들의 최고 주술사인 카가니슈라는 놈이 날 마그모스레가르의 먹이로 던져 주라고 지시했다지 뭡니까.$B$B잠깐... 뭐라고요?! 비행장에서 보내서 온 게 아니라고요?!$B$B난 완전히 사람들에게 잊힌 건가요?!$B$B알았어요. 어쨌거나 당신이 와 줬으니 일단은 그걸로 충분해요.$B$B이 쇠사슬만 끊고 바로 비행장으로 돌아가기로 하죠. 그리고 피즈크랭크에게 제가 무사히 돌아왔다고 전해주세요.$B$B절 기억이나 할는지 모르겠지만요.', },
    {'본커 토글볼트를 안전하게 호위한 다음 피즈크랭크 비행장에 있는 피즈크랭크 풀스로틀에게 보고해야 합니다.', },
  },
  [11674] = {
    '실종된 현자 하이메사',
    {'이 대륙에는 알 수 없는 것들이 지천으로 널렸네. 우리 현자인 하이메사는 어리석게도 수색꾼 단 한 명만 데리고 사자의 동굴 근처에 있는 이상한 무언가를 조사하러 북쪽으로 갔다네.$B$B그 동굴이 어떤 곳인지 궁금하겠지? 그곳은 늙은 마그나타우르가 죽음을 맞이하러 들어가는 일종의 성지와도 같은 곳일세. 하이메사 말로는 며칠 전에 뭔가 하늘에서 떨어졌다더군.$B$B자네가 타운카의 진정한 친구가 되고 싶다면 하이메사를 찾아 도와줬으면 하네. 도움의 손길을 거절할 만큼 고집불통은 아닐세.', },
    {'사자의 동굴 근처에서 현자 하이메사를 찾아야 합니다.', },
  },
  [11675] = {
    '합당한 안식',
    nil,
    {'역병 걸린 마그나타우르 10마리를 처치한 다음 사자의 동굴 남쪽에 있는 현자 하이메사에게 가야 합니다.', },
  },
  [11676] = {
    '자비로운 마음으로',
    {'놈들에게 살해되지 않은 병사들이 이 농장 여기저기에 있는 간이 감옥에 갇혀 있네. 우리는 간신히 열쇠 몇 개를 훔쳐내기는 했지만, 공격을 받기 때문에 철창을 열 만큼 가까이 다가갈 수가 없네.$B$B열쇠를 확보해서 포로들을 풀어주는 데 자네가 "재능"을 발휘해줬으면 하네. 저 강령군주와 돌연변이를 처치하고 구한 열쇠로 스컬지 포로를 풀어주게.', },
    {'전쟁노래 농장 전진기지에 있는 그림자추적자 카나리우스가 스컬지 포로 5명을 풀어달라고 했습니다.', },
  },
  [11677] = {
    '역병을 막아라',
    {'이 역병은 저 동굴 안에 있는 뭔가가 일으킨 것 같습니다. $B$B$C, 이 씨앗을 받으십시오. 보통은 스컬지에 대항하는 수호물로 사용하지만, 아마도 저 동굴 안에 있는 사악한 무언가를 막는 데도 효과가 있을 겁니다.$B$B역병의 근원을 찾아 그 씨앗을 던져넣으십시오.', },
    {'사자의 동굴 정남 쪽에 서 있는 현자 하이메사가 하이메사의 정화 씨앗으로 사자의 동굴 역병 가마솥을 중화해달라고 했습니다.', },
  },
  [11678] = {
    '브리슬혼 찾기',
    {'브리슬혼은 지금쯤 돌아와 있어야 합니다. 우리가 여기에 온 다른 이유는 서쪽에 있는 스컬지 구조물을 조사하고자 함입니다. 하늘에서 떨어진 게 바로 그 구조물이지요. 성격이 워낙 불같은 자라 곤경에 처하기 쉽답니다.$B$B$N, 그곳으로 가서 브리슬혼을 찾아주십시오, 부탁합니다.', },
    {'탈라마스에서 수색꾼 브리슬혼을 찾아야 합니다.', },
  },
  [11679] = {
    '열쇠 수리',
    {'이런, 이러면 늦어질 텐데... 지금같은 때 이런 식으로 일이 지연돼서는 안 됩니다!$B$B고귀한 술리스트라즈를 귀찮게 하고 싶지는 않지만, 에바노르 님을 구해내려면 그의 도움이 꼭 필요합니다.$B$B술리스트라즈에게 열쇠 조각을 가져다주고 마음을 좋게 쓰길 빌어 보십시오.$B$B서두르십시오, 공격대가 기다리고 있습니다.', },
    {'도나단이 호박석 절벽에 있는 술리스트라즈와 대화해 달라고 했습니다.', },
  },
  [11680] = {
    '비상',
    {'열쇠를 새로 벼렸다.$B$B내 말을 명심해라, $R. 추구하는 일에서 성공을 거두던가, 아니면 차라리 하는 데까지 해보고 죽어라. 내 비룡의 비늘 하나라도 다치는 날에는 너에게 직접 책임을 물을 테니 말이다.$B$B이제 물러가라.', },
    nil,
  },
  [11681] = {
    '에바노르 구출',
    {'마침내 공격할 수 있겠군요!$B$B저희가 주변 놈들을 처리하고 감옥을 열겠습니다.$B$B에바노르 님께서 풀려나면 당신이 안전하게 보호해 주십시오.$B$B서두르십시오, 바로 출발해야 합니다!', },
    {'호박석 절벽의 안짐이 전투마법사들과 함께 날아가 녹주석 거점에 있는 대마법사 에바노르를 풀어달라고 했습니다. 그런 다음 호박석 절벽의 마법사 탑에서 에바노르에게 보고해야 합니다.', },
  },
  [11682] = {
    '용의 전언',
    {'당신의 최근 활약은 익히 들어 알고 있어요, $N 님. 다시 한 번 감사의 말씀을 드립니다.$B$B이번 위기를 통해 적에 대한 정보는 대부분 알아냈어요. 머지않아 그들을 처리할 겁니다. 이제 좀 더 중요한 문제에 대해 얘기해야겠군요...$B$B아제로스에는 당신이 필요해요. 서쪽 지역에 시시각각 위기가 닥쳐오고 있습니다.$B$B자세한 내용은 고귀한 술리스트라즈에게 말해 두었어요. 즉시 그와 이야기해 주세요.$B$B행운을 빌어요, 달라란의 친구여.', },
    {'대마법사 에바노르가 호박석 절벽의 술리스트라즈와 대화해 달라고 했습니다.', },
  },
  [11683] = {
    '추락한 죽음의 요새',
    {'서쪽에 추락한 죽음의 요새는 아직도 스컬지가 가득하다고 추측만 해볼 수 있습니다.$B$B탈라마스에 가시면, 추락한 웅덩이 내부와 그 주변에 있는 리치 왕의 하수인을 모두 처리해주셨으면 합니다.', },
    {'탈라마스와 썩은 웅덩이에서 종류에 상관없이 스컬지 20마리를 처치하고 사자의 동굴 바로 남쪽에 있는 현자 하이메사에게 가야 합니다.', },
  },
  [11684] = {
    '구덩이 정찰',
    {'땅이 요동치는 것이 느껴지나?$B$B스컬지 네루비안이 땅속 깊은 곳에서 이동 중이라네. 놈들은 남서쪽의 간헐천 지대에 출몰하여 족장 윈터게일이 파견한 순찰대를 공격하지.$B$B<낡은 지도를 건네는 타운카 장로>$B$B한 명이라면 순찰대가 실패한 곳에서 임무를 마칠 수 있겠지. 이 지도를 간헐천 지대로 가져가 네루비안 구덩이의 위치를 표시해주게.', },
    {'타운카르 마을에 있는 대부 마한이 간헐천 지대 지도를 사용해 남쪽 구덩이, 북서쪽 구덩이, 북동쪽 구덩이의 위치를 표시해 달라고 했습니다.', },
  },
  [11685] = {
    '정령의 심장',
    {'이제 네루비안 구덩이가 정확히 어디 있는지 알아냈으니 그것을 무너트리면 스컬지가 공격하지 못하도록 막고 놈들 병력을 무력하게 할 수 있겠네. 바람의 조련사 바라라면 정령의 힘을 이용해 네루비안들이 사용하는 굴을 파괴할 방법을 알 테지만, 그래도 자네가 도와줘야 할 걸세.$B$B마을 남동쪽으로 가면, 얼어붙은 해안 너머 바닷가 근처에 얼어붙은 대지의 정령이 지배하는 지역이 있네. 그 정령의 심장이 바로 바람의 조련사 바라가 바라는 것이라네.', },
    {'정령의 심장 5개를 타운카르 마을에 있는 바람의 조련사 바라에게 가져가야 합니다.', },
  },
  [11686] = {
    '전쟁노래 농장',
    {'루터의 메모를 보니 스컬지 녀석들이 이젠 이곳을 번식처 비슷한 용도로 쓰면서 들판을 활보하는 저 괴물들을 만들어내는 모양이군.$B$B<부르르 떠는 이코리스>$B$B저 괴물들도 한때는 오크였고... 타우렌이었는데...$B$B계획을 세우기 전에 우선 정보를 더 수집해야겠네. 스컬지가 득실거리는 장소가 세 곳 있는데, 자네가 가서 조사해 주게나. 바로 남서쪽에 있는 전쟁노래 곡물창고, 정서쪽에 있는 토르프의 농장, 그리고 북쪽에 있는 오래된 전쟁노래 도살장이네.$B$B정찰을 마치고 내게 다시 돌아오게.', },
    {'전쟁노래 농장 전진기지의 그림자추적자 이코리스가 전쟁노래 곡물창고, 토르프의 농장, 그리고 전쟁노래 도살장을 정찰해 달라고 했습니다.', },
  },
  [11687] = {
    '의술사와 리치군주',
    {'두... 두 놈은... 죽어 마땅합니다.$B$B저 위에 있는... 의술사와... 리치군주 말입니다. 놈들을 찾아... 처치하십시오... 너무...$B$B...늦기... 전에...', },
    {'의술사 레이저그린과 리치군주 칠윈터를 처치한 다음, 사자의 동굴 바로 남쪽에 있는 현자 하이메사에게 가야 합니다.', },
  },
  [11688] = {
    '추잡한 돼지',
    {'저것 좀 보시오! 어여쁜 우리 코도가 추잡한 돼지에게 속수무책으로 당하고 있소! 당신이 시간을 벌어줬으면 좋겠소, $R. 병든 코도는 내가 치료할 테니, 그동안 죽지 못하는 돼지들이 가까이 오지 못하게 막아주시오. 안 그러면 코도가 남아나지 않을 거요!$B$B가서 저 돼지들을 무덤으로 돌려보내 주시오. 두 번 다시 못 기어나오게 말이오!', },
    {'전쟁노래 농장 전진기지에 있는 농부 토르프가 죽지 못하는 돼지 10마리를 처치해 달라고 했습니다.', },
  },
  [11689] = {
    '안타까운 소식',
    {'대모님께서 이 소식을 들으셔야 합니다, $N.$B$B타운카르로 다시 가 주시겠습니까? 저도 곧 따라간다고 전해주십시오.', },
    {'타운카르 마을에 있는 대모 타이가와 대화해야 합니다.', },
  },
  [11690] = {
    '살려서 데려오기',
    {'이 재갈을 가져가시오, $N. 보면 알겠지만, 입에 물리는 곳에 조그만 약품 분사기를 붙여 놨소. 코도에게 재갈을 물리면 약품 분사기로 치료가 될 거요. 녀석들을 치료한 다음 그 녀석을 타고 돌아오시오. 하지만, 쇠약한 상태이니 조심스럽게 몰고 와야 하오.$B$B귀여운 우리 코도를 데려다주시오, $N!$B$B하나 더 말하자면, 10분 안에 처리해야 하오. 그 이상 지나면 재갈에 달린 해독제 효과가 사라지니 말이오.', },
    {'전쟁노래 농장 전진기지에 있는 농부 토르프가 병든 야생 코도 8마리를 구해달라고 했습니다.', },
  },
  [11691] = {
    '아훈 소환',
    {'저와 우리 주술사들은 서리 군주에 대항하려 힘을 한데 모아왔습니다. $N, 그대가 대지모신의 오른팔로서 해 주었으면 하는 일이 있습니다.$B$B이 용암 토템을 가져가서 북동쪽에 있는 얼음 기둥에 사용하십시오. 아훈은 이 기둥을 통해 힘을 모으고 있습니다. 이 토템 때문에 아훈이 혼란스러워져서 정령 세계와 단절되기를 저희는 바란답니다.$B$B만약 운이 좋아서 그렇게 된다면, 아훈을 무찌를 수 있겠지요.$B$B마음을 단단히 하십시오, $N. 아훈이 우리와 맞설 때는 엄청나게 분노할 테니까요.', },
    {'대지 고리회 용암 토템을 얼음 기둥으로 가져가야 합니다.', },
  },
  [11692] = {
    '빅시를 찾아라',
    {'당신은 진짜 영웅이죠. 그러니 이 문제도 해결해 주세요!$B$B제 상사인 팅키 씨가 빅시 렌치섕커와 함께 동쪽으로 떠났는데, 저로서는 이 기계가 제대로 돌아가게 할 수가 없어요! 이게 제대로 작동해야만 하는데, 고칠 수 있는 이는 팅키 씨뿐이라고요!$B$B그들을 좀 찾아 주실래요? 빅시 말로는 늙은 마그나타우르를 쫓아 사자의 동굴이라는 곳으로 간다고 하던데요.$B$B아마 그곳에서 찾을 수 있을 거예요.$B$B날아서 거기 가고 싶거든 활주로 근처에 있는 카라 스리스스타랑 이야기하세요.', },
    {'모르들 코그스피너가 사자의 동굴 남쪽에서 빅시 렌치섕커를 찾아 달라고 했습니다.', },
  },
  [11693] = {
    '역병 걸린 마그나타우르라니!',
    {'팅키와 저는 늙은 마그나타우르를 쫓아 여기까지 왔어요. 저 위에 있는 동굴은 마그나타우르가 죽으러 가는 자리 같아요.$B$B그런데, 놀랍게도 녀석들이 저기 들어가자마자 역병에 걸리는 거 있죠. 역병에 걸린 것들이 그냥 돌아다니게 놔둘 수는 없어요. 비위생적이잖아요.$B$B제가 다음에 할 일을 생각하는 동안 저것들 수를 좀 줄이고 오시면 어떻겠어요?', },
    {'역병 걸린 마그나타우르 10마리를 처치하고 사자의 동굴 남쪽에 있는 빅시 렌치섕커에게 가야 합니다.', },
  },
  [11694] = {
    '동굴 안에서 벌어지는 일',
    {'당신이 저 역병 걸린 짐승들을 처리하는 동안, 저는 저 위에 있는 동굴을 들여다봤지요.$B$B이교도들이 마그나타우르를 감염시키고 있더군요!$B$B놈들은 가마솥으로 독성 혼합물 같은 걸 만들고 있었어요. 저한테 해독제로 쓰이는 가루가 좀 있어요. 거기다 다른 걸 좀 집어넣고 섞었죠. 아마 그 혼합물을 중화시킬 수 있을 거예요!$B$B이걸 동굴로 가져가 실제로 효과가 있는지 확인해 보세요.', },
    {'사자의 동굴 남쪽에 있는 빅시 렌치섕커가, 사자의 동굴 역병 가마솥에 빅시의 중화제를 사용해 그 내용물을 중화해 달라고 했습니다.', },
  },
  [11695] = {
    '자연 격노의 뿔피리',
    {'이 생명력 없는 돌멩이로 정령 비슷한 걸 만들어낼 수는 있지만, 거기에 생명을 불어넣는 건 완전히 다른 일입니다. 그래서 도움이 필요한데 운도 약간 따라야 하지요.$B$B전설에 따르면 위대한 영웅이 찾으려 했던, 자연 격노의 뿔피리라는 고대 유물이 있다고 합니다. 전설이 기록된 두루마리 사본을 하나 빌려 드리겠습니다. 그 전설이 사실인지는 모르겠지만, 저희가 할 수 있는 가장 안전하고 확실한 방법이니 말입니다. 전설이 맞는다면, 뿔피리 조각은 마을 북쪽과 남서쪽을 둘러싼 범람 평야 어딘 가에 있을 듯합니다.', },
    {'아래쪽 뿔피리 조각과 위쪽 뿔피리 조각을 타운카르 마을에 있는 바람의 조련사 바라에게 가져가야 합니다.', },
  },
  [11696] = {
    '여기에 아훈이!',
    {'$N, 그대가 있어서 얼마나 다행인지 모릅니다. 아훈은 이 동굴 안에서 반격할 힘을 모으고 있습니다. 대지 고리회도 앞서 병력을 보냈지만, 그들만으로는 아훈을 이겨낼 수 없었습니다.$B$B자 떠나십시오, $N. 우리 대지 고리회 주술사들을 찾고 그들을 이끄는 루마 스카이마더와 이야기를 나눠 보십시오. 아마 그대가 힘을 보탠다면 너무 늦지 않게 아훈을 물리칠 수 있을 것입니다.', },
    {'강제 노역소에서 루마 스카이마더를 찾아야 합니다.', },
  },
  [11697] = {
    '혼자서 어딜 간 거야, 팅키!',
    {'팅키에게 절대로 혼자 다니지 말라고 일렀는데도 서쪽에 떨어진 죽음의 요새를 조사하지 않고는 못 배겼던 모양이에요.$B$B저는 이교도가 거기서 나왔다고 확신해요. 바꿔 말하자면 팅키가 위험에 처했을 거라고도 확신한다는 거죠!$B$B그녀는 비행장에서 제일가는 기술자예요. 그녀가 없으면 우린 망한 거나 다름없다고요!$B$B제가 직접 나서서 찾아내고 싶지만, 저놈들한테는 도저히 이길 수가 없어요. $N 님, 팅키를 구해낼 수 있는 건 당신뿐이에요!', },
    {'탈라마스 안에서 팅키 윅휘슬을 찾아야 합니다.', },
  },
  [11698] = {
    '스컬지 소탕',
    {'저 동굴에 있는 이교도들이 그 추락에서 살아남았다면, 서쪽에 있는 죽음의 요새엔 스컬지가 우글우글할 게 분명해요. 아마 그 앞의 웅덩이에도 잔뜩 있겠죠!$B$B$C님, 탈라마스에 있는 동안에 저 리치 왕의 떨거지들을 보이는 대로 없애줄 수 있을까요?', },
    {'탈라마스와 썩은 웅덩이에서 스컬지 20마리를 처치한 다음, 사자의 동굴 바로 남쪽에 있는 빅시 렌치섕커에게 가야 합니다.', },
  },
  [11699] = {
    '갇히긴 했지만... 금방 나갈 수 있다고요!',
    {'이교도가 마법으로 문짝조차 없는 이 철창에 저를 가뒀어요. 하지만 제가 비행장의 순간전송기 선임 기술자라는 사실은 몰랐을 걸요!$B$B당신이 도와주면 제가 손을 좀 써서 여기서 금방 나갈 수 있을 거예요.$B$B근처에 돌아다니는 구울에게서 엔진 동력 수정을 하나 구하고, 의술사에게서 마법 회전의를 가져와야 해요. 그리고 저 위의 리치군주에게서 조종의 스컬지석도 빼앗아 오세요.$B$B그걸 다 모으면 피즈크랭크에 있는 순간전송기로 연결되는 이동 장치를 만들어서, 노움식으로 탈출할 수 있을 거예요!', },
    {'엔진 동력 수정, 마법 회전의, 조종의 스컬지석을 모아 탈라마스에 있는 팅키 윅휘슬에게 전달해야 합니다.', },
  },
  [11700] = {
    '빅시에게 알리기',
    {'제 악의 분신이 저 바깥 어딘가에 있을 거예요.$B$B아직 비행장으로 돌아가진 않았을 거라고 확신해요. 어쨌든 여기보다 추운 곳에 있는 건 분명해요. 아주 잠깐밖에 그 존재를 느끼지 못했지만요.$B$B어쨌거나 전 이걸 재구성해서 곧 비행장으로 돌아갈 거예요. 당신은 동쪽으로 가서 빅시에게 비행장에서 만나자고 전해주겠어요?$B$B고마워요, $N 님!', },
    {'사자의 동굴 바로 남쪽에 있는 빅시 렌치섕커와 대화해야 합니다.', },
  },
  [11701] = {
    '다시 비행장으로',
    {'좋아요. 그럼 저도 슬슬 비행장으로 돌아가 봐야겠네요. 다리를 다쳤으니 빨리 걷지는 못하겠지만요.$B$B먼저 가서 피즈크랭크 영감님한테 이 기쁜 소식을 전해주지 않겠어요? 분명히 보상을 받을 수 있을 거예요.$B$B고마워요, $N 님!', },
    {'북풍의 땅의 피즈크랭크 비행장에 있는 피즈크랭크 풀스로틀과 대화해야 합니다.', },
  },
  [11702] = {
    '멀록왕 아옳아옳',
    {'좀 이상하게 들릴지도 모르겠습니다, $C|1이여;여;. 우리더러 도와달라는 멀록이 있습디다!$B$B멀록왕 아옳아옳이라고 하던데, 거 뭐냐, 겨울지느러미 멀록의 지도자라고 그러더군요. 아옳아옳과 그가 다스리는 멀록은 이미 마을에서 도망 다니는 상태고, 당신이 도와주러 오기만 기다리는 모양입니다.$B$B서쪽으로 가서 절벽 아래로 내려가면 만날 수 있을 것입니다. 겨울지느러미 은신처의 남쪽에 있는 모양입니다.', },
    {'겨울지느러미 은신처에 있는 멀록왕 아옳아옳과 대화해야 합니다.', },
  },
  [11703] = {
    '게트리 찾아가기',
    {'호드 병사를 납치해서 언데드 괴물로 바꿔놓는 일을 감독하는 스컬지 대리인은 북서쪽 농장을 부화장으로 사용한다네. 지난주 내내 그림자추적자 게트리가 그놈을 추적해 이러한 일을 막을 수 있는 중요한 정보를 좀 확보해 놨지.$B$B스컬지를 생산하지 못하도록 도울 일이 없나 게트리에게 가보게. 게트리는 이 농장 북서쪽 끝에 있는 버려진 탑에 있네.', },
    {'전쟁노래 농장에 있는 그림자추적자 게트리와 대화해야 합니다.', },
  },
  [11704] = {
    '멀록왕 아옳아옳',
    {'이상한 말인 줄은 알지만, 멀록 하나가 우리한테 도움을 청하러 왔어요!$B$B이름이 멀록왕 아옳아옳이라고 하고, 겨울지느러미 멀록의 지도자라더군요. 보아하니 딸린 멀록과 함께 마을에서 쫓겨난 상태라 누군가의 도움이 필요한가 봐요.$B$B그 누군가라는 게 바로 당신이군요!$B$B서쪽으로 절벽이 나올 때까지 길을 따라가면 겨울지느러미 은신처가 나오고 그 멀록왕도 만날 수 있을 거예요. 부글거리는 웅덩이 근처의 물가로 가 보세요.', },
    {'겨울지느러미 은신처에 있는 멀록왕 아옳아옳과 대화해야 합니다.', },
  },
  [11705] = {
    '무모한 시도',
    nil,
    nil,
  },
  [11706] = {
    '붕괴',
    {'저는 알루비우스를 만들어 낸 다음 자연 격노의 뿔피리로 속박했습니다. 간헐천 지대의 가장 남쪽에 있는 구덩이에서 뿔피리를 불면 응답할 겁니다.$B$B알루비우스가 지진을 일으켜서 네루비안의 굴을 무너트리고 놈들의 측면 공격을 완전히 차단할 것입니다. 지진이 가라앉으면 네루비안 지휘관이 반드시 나타납니다. 굴만 무너뜨려서는 공격을 막을 수 없겠지만, 지휘관을 쓰러트린다면 분명히 좋은 결과가 있을 테지요.', },
    {'타운카르 마을에 있는 바람의 조련사 바라가 자연 격노의 뿔피리를 사용해 네루비안 구덩이를 무너트리고 군주 크릭식스를 처치해 달라고 했습니다.', },
  },
  [11707] = {
    '구원 요청',
    {'마침 잘 됐네요. 당신이 $N 님 맞죠?$B$B지금까지 여기 일을 도와주신 것처럼, 북쪽의 피즈크랭크 비행장도 좀 도와주셨으면 해요! 마그나타우르에, 스컬지에... 채굴 현장까지 말썽이니!$B$B완전히 난장판이에요, 난장판!$B$B당장 거기 가서 풀스로틀 영감님하고 얘기해 줘요!', },
    {'피즈크랭크 비행장에 있는 피즈크랭크 풀스로틀과 대화해야 합니다.', },
  },
  [11708] = {
    '기계노움',
    {'이제 일이 어떻게 돌아가고 있는지 처음부터 끝까지 설명해 드릴게요.$B$B얘기를 다 들으면 밖에 나가서 제 부관인 징키 윙넛을 찾으세요. 그때부턴 그녀가 전부 준비해 줄 겁니다.', },
    {'피즈크랭크 풀스로틀의 이야기를 듣고 피즈크랭크 비행장에서 징키 윙넛을 찾아야 합니다.', },
  },
  [11709] = {
    '노르크 블러드프렌지의 임무',
    {'자네가 이 얼어붙은 황무지에서 우리가 기반을 다지도록 많이 도와주긴 했지만, 저 어둠 속에는 또 다른 위험이 도사리고 있네. 감시자 노르크 블러드프렌지를 좀 도와주게. 전쟁노래 요새 밖으로 나가면 바로 남동쪽에 포로수용소가 있다네. 그곳에서 노르크 블러드프렌지를 찾아 포로 호송과 교환 임무를 돕겠다고 하게.$B$B그럼 가 봐도 좋네, $N.', },
    {'북풍의 땅의 전쟁노래 요새에 있는 감시자 노르크 블러드프렌지와 대화해야 합니다.', },
  },
  [11710] = {
    '고장 난 전송 장치',
    {'동쪽에 있는 끓어오르는 웅덩이랑 간헐천 지대에 예비 부품이 여기저기 흩어져 있어요... 생각하기도 싫지만, 어쩌면 채굴 현장에도 아직 남아 있을지 모르겠네요. 충분히 모아서 가져와 주세요. 그러면 여기서 다른 데로 전송하는 기능을 복구할 수 있을지도 몰라요. 물론 전송받는 기능도 좀 나아지겠죠.$B$B사고 터지는 건 이제 지긋지긋해요...', },
    {'피즈크랭크 예비 부품 15개를 모아 피즈크랭크 비행장에 있는 모르들 코그스피너에게 가져가야 합니다.', },
  },
  [11711] = {
    '겁쟁이 배달합니다... 30분 이상 걸리면 공짜!',
    nil,
    {'얼라이언스 탈영병을 전쟁노래 요새 동쪽의 교차로로 데려간 다음 전쟁노래 신호용 권총을 사용해 얼라이언스에 신호를 보내야 합니다.$B$B임무를 무사히 완수하면 정찰병 툰고크에게 보고하십시오.', },
  },
  [11712] = {
    '저주를 푸는 저주',
    {'그 노인네 말씀을 잘 들었다면, 바깥에 변해버린 노움들이 있다는 사실을 알고 있을 거예요. 톱니반장 메카조드가 \'육체의 저주\'를 풀어 준다면서 그렇게 만들었죠. 이제 그들은 로봇이 돼버렸다고요!$B$B아마 다시 저주를 걸면 저들을 원래 상태로 되돌릴 수 있을 거예요. 이 저주유발 순간전송 주사기를 가져가 기계노움 시체에 주사를 놓아 주세요.$B$B주사를 맞은 기계노움은 다시 저주에 걸려 치료되고, 그 좌표가 우리에게 전송된답니다. 그러면 우리가 그를 여기로 순간전송시킬 수 있어요.$B$B어떻게 생각하세요, $N 님?', },
    {'피즈크랭크 비행장에 있는 징키 윙넛이 저주유발 순간전송 주사기를 피즈크랭크 기계노움 6마리에 사용해 달라고 했습니다.', },
  },
  [11713] = {
    '구덩이 정찰',
    {'피즈크랭크가 간헐천 지대 남부에 있는 네루비안 구덩이를 조사할 공중 정찰대를 내보냈지만, 아직 아무도 돌아오지 않았어요. 그들이 돌아오기를 마냥 기다릴 수도 없으니 누군가 직접 가서 구덩이를 조사해 줬으면 해요.$B$B위험한 일이라는 건 나도 알아요. 그러니 그에 맞는 보상은 해 드릴게요. 이 지도를 가지고 가서 구덩이를 보는 대로 중요한 정보를 기록해 주세요.', },
    {'피즈크랭크 비행장의 아브너 피즐톨크가 간헐천 지대 지도를 사용해 남쪽 구덩이, 북서쪽 구덩이, 북동쪽 구덩이의 위치를 기록해 달라고 했습니다.', },
  },
  [11714] = {
    '쥐 완전 소탕',
    nil,
    {'북풍의 땅의 핏빛포자 평원에 있는 정찰병 툰고크가 핏빛포자 정원사 8마리, 핏빛포자 화염술사 5마리, 그리고 핏빛포자 요리사 2마리를 제거해 달라고 했습니다.', },
  },
  [11715] = {
    '연료 공급 작전',
    {'스컬지가 네루비안을 투입할 때 쓰는 굴을 무너트릴 기계를 만들려고 하는데, 주 유정 굴착 장치가 고장 난 상태라 다른 방법으로 연료를 찾아야 해요.$B$B남동쪽의 간헐천 지대 바깥쪽, 주 유정 굴착 장치에서 좀 먼 곳에 기름이 군데군데 고인 웅덩이가 많이 있어요. 여기서 기름을 충분히 채집해 온다면 연료를 대강 충당할 수 있을 거예요.$B$B기름이 고인 곳 근처에서 채집기를 사용하면 알아서 기름을 빨아올린 다음 연료 탱크에 담을 겁니다.', },
    {'피즈크랭크 비행장의 아브너 피즐톨크가 휴대용 기름 채집기를 사용해 기름 8통을 모아달라고 했습니다.', },
  },
  [11716] = {
    '신비로운 핏빛포자',
    {'저희는 이곳에서 정말 놀라운 것을 관찰하는 중입니다, $R. 전혀 알려진 바 없는 생태계가 바로 우리 눈앞에서 살아 숨 쉬고 있어요! 제 짐작으로는 이쪽 생태계를 활성화하는 것이 이 핏빛포자 식물이라 보입니다.$B$B근처에 있는 핏빛포자 수식물에서 꽃가루를 받아 놓기는 했지만 가루받이할 암식물 수용체가 부족합니다. 핏빛포자 암술잎을 많이 모아서 가져다주십시오. 주변에 있는 큰 식물 근처에서 찾을 수 있습니다.$B$B식물을 지키는 코볼트를 조심하십시오! 순순히 가져가게 내버려두지는 않을 테니 말입니다!', },
    {'북풍의 땅, 핏빛포자 평원에 있는 혈법사 라우리스가 핏빛포자 암술잎 10개를 모아달라고 했습니다.', },
  },
  [11717] = {
    '나방 꽃가루 수집',
    nil,
    nil,
  },
  [11718] = {
    '매머드의 탈을 쓴 로봇',
    {'오래된 로봇의 뼈대를 재구성해서, 굴을 무너트릴 만큼 큰 지진을 일으키는 기계를 만들었어요. 문제는 이 기계가 노스렌드의 혹독한 추위를 견디기엔 너무 섬세하고 민감하다는 거죠.$B$B토종 매머드의 가죽 중에서 가장 두꺼운 걸로 보온 처리를 하면 될 것 같아요. 매머드는 주로 비행장 남쪽과 간헐천 지대 근처의 평원을 어슬렁거리죠. 기계를 완성할 수 있게 가죽을 충분히 가져다주세요. 그런 다음에 저 네루비안을 해치워 버리자고요!', },
    {'피즈크랭크 비행장의 아브너 피즐톨크가 두꺼운 매머드 가죽 6장을 가져다 달라고 했습니다.', },
  },
  [11719] = {
    '적절한 실험 대상',
    {'<눈으로 당신을 재보듯이 훑는 라우리스>$B$B이제 가루받이를 한 핏빛포자 꽃은 준비되었지만, 정작 그 효과를 실험해 볼 대상이 없습니다. 당신이 이걸 피부에 문질러 본 다음 제가 관찰하도록 허락해주시지 않겠습니까? 뭐 크게 잘못되기야 하겠습니까?$B$B어쨌든 너무 걱정하지 마십시오, $N! 그냥 이 견본을 얼굴 전체에 문질러 보십시오.', },
    {'북풍의 땅, 핏빛포자 평원에 있는 혈법사 라우리스가 가루받이를 한 핏빛포자 꽃을 사용하고 그 결과를 관찰하게 해 달라고 했습니다.', },
  },
  [11720] = {
    '감모스 습격',
    nil,
    nil,
  },
  [11721] = {
    '심문관 감모스라',
    {'마그나타우르는 사자처럼 무리지어 산다오. 수컷 한두 마리가 암컷 여럿과 그 새끼를 거느리오. 서쪽에 있는 마그나타우르 동굴인 감모스에서는 감모스라가 우두머리라오. 무리를 이끄는 것이 감모스라인 만큼 마그나타우르들이 어떤 분란도 일으키지 못하게 막으려면 반드시 처치해야 하오.$B$B이 뭉개진 핏빛포자가 담긴 주머니를 가지고 감모스로 가시오. 그리고 그 주머니에 든 가루로 감모스라를 약하게 한 다음 처치하시오! 처치했다는 증거로 감모스라의 머리카락을 가져다 주시오.', },
    {'감모스라를 처치한 다음, 북풍의 땅에 있는 핏빛포자 평원으로 가서 주술사 마이트혼에게 감모스라의 머리카락을 가져가야 합니다.', },
  },
  [11722] = {
    '감모스에서 얻은 전리품',
    {'호드를 위해 거둔 이번 승리를 전부 우리 공으로만 돌릴 수는 없소, $N. 목숨을 바쳐 핏빛포자를 시험하고 결국 감모스라를 제거한 자는 바로 자네이니 말이오.$B$B전쟁노래 요새로 돌아가 감모스라의 머리카락을 헬스크림 님께 전달하고 우리 명예를 높여주기 바라오.', },
    {'감모스라의 머리카락을 전쟁노래 요새에 있는 가로쉬 헬스크림에게 가져가야 합니다.', },
  },
  [11723] = {
    '흔들흔들 5000!',
    {'보세요, 이 기계가 네루비안의 위협에서 우릴 지켜줄 거예요!$B$B<완성한 로봇을 선보이며 자랑스럽게 웃는 아브너>$B$B이 원격 조종 장치를 가지고 간헐천 지대 가장 남쪽에 있는 네루비안 구덩이로 가세요. 조종 장치를 사용하면 흔들흔들 5000을 가동할 수 있어요. 그다음에는 기계가 알아서 할 겁니다.$B$B기계로 발생한 지진 때문에 네루비안의 우두머리가 나타나더라도 놀라지 마세요. 어떻게 보면 네루비안의 공격을 뿌리 뽑을 수 있는 절호의 기회라고 할 수도 있죠.', },
    {'피즈크랭크 비행장에 있는 아브너 피즐톨크가 흔들흔들 5000을 사용해 네루비안 굴을 무너트리고 군주 크릭식스를 제거해 달라고 했습니다.', },
  },
  [11724] = {
    '거대 나방 오믈렛?',
    {'주변에 시체가 있는 것으로 보아 이 알은 누군가에게는 대단히 중요했던 것 같습니다. 알을 자세히 살펴보니 핏빛포자 평원에 있는 혈법사 라우리스에게 전달될 물건임을 알 수 있습니다. 바로 동쪽에 있는 곳입니다!$B$B알을 가져가서 원래 수취인인 혈법사에게 전달해야 합니다.', },
    {'빛나는 대형 알을 북풍의 땅에 있는 핏빛포자 평원으로 가지고 가서 혈법사 라우리스에게 전달해야 합니다.', },
  },
  [11725] = {
    '조종사 &quot;곡예사&quot; 찾기',
    {'이번 임무는 제가 직접 맡았어야 했는데. 곡예사 녀석을 신용하지 못한다는 말은 아니에요. 오히려 그 반대지요. 적어도 같이 날아본 조종사 중에는 그 친구가 단연 최고라고요.$B$B그저 이렇게 중요한 임무를 지휘하는 것은 제 책임이라는 말일 뿐이죠.$B$B간헐천 지대 한가운데에 출몰한다는 네루비안에 관한 정보를 모아오도록 곡예사를 우리 부대에서 가장 빠른 정찰 편대와 함께 내보냈어요.$B$B마지막으로 받은 보고에 따르면 그 편대의 위치는 한참 남쪽에 있는 네루비안 구덩이 바로 서쪽이었어요.$B$B곡예사를 찾아주세요, $N 님.', },
    {'간헐천 지대에서 "곡예사" 이기 코그토글을 찾아야 합니다.', },
  },
  [11726] = {
    '톡 쏘는 양념',
    {'추락한 이후로 늑대들이 계속 추락 지점을 어슬렁거리고 있는데, 이번 임무를 진행하며 만든 마이크로필름을 먹어버리지나 않았는지 걱정되네요.$B$B마이크로필름을 되찾을 계획이 있기는 있는데, 그러려면 당신이 좀 도와주셔야 해요.$B$B남쪽과 동쪽에 있는 범람 평야에는 골록이라는 생물이 살고 있어요. 놈들은 독성 양념을 써서 먹이를 사냥하는데, 이 양념에는 다른 생물을 약하게 만드는 효과가 있어요. 골록 트림꾼이나 뒤뚱발 골록에게서 양념 주머니를 충분히 모아 주신다면 제가 늑대 미끼를 만들도록 하죠.', },
    {'끓어오르는 웅덩이에 있는 "곡예사" 이기 코그토글이 골록 양념 주머니 4개를 모아달라고 했습니다.', },
  },
  [11727] = {
    '영웅의 시간',
    {'당신이 함께해 주다니 다행이군요, $N. 우리 모두 당신의 업적을 익히 압니다.$B$B당신도 알다시피 린 국왕께서 아서스의 군대와 맞서 싸울 대군을 이끌고 노스렌드로 오고 계십니다. 하지만 아무리 군대라 해도 그들에게 용기를 불어넣고 이끌어 나갈 영웅 없이는 아무 소용도 없지요.$B$B당신이 우리 병사들에게 그런 영웅이 되어주었으면 합니다. 그들과 함께 이 성채를 공격해오는 스컬지에 맞서 싸워주십시오.$B$B내부 요새 밖에 있는 하사관 해머힐에게 가 보세요. 용맹의 성채를 적들의 손에 넘길 수는 없습니다. 앞으로 실행할 모든 작전의 성공 여부는 이 성채에 달렸으니까요.', },
    {'용맹의 성채에 있는 하사관 해머힐에게 보고해야 합니다.', },
  },
  [11728] = {
    '늑대 응가',
    {'무슨 일을 하든 이 미끼에는 입도 대지 마세요. 그냥 기름 얼룩진 늑대 옆에 놔두기만 하세요. 마이크로필름을 먹으려 들 정도로 배가 고픈 상태라면 미끼 냄새를 맡자마자 달려오겠지요.$B$B그다음에는 효과가 나타날 때까지 기다렸다가 녀석들의 그... 부산물을 뒤져 마이크로필름을 찾으시면 돼요.$B$B제 특제 늑대 응가 처리기를 쓰시면 작업이 편하겠지만, 비행장에 놔두고 온 것 같군요. 그러니까, 아무래도 그... 뭐랄까... 재래식 방법으로 찾으셔야 할 거예요.', },
    {'끓어오르는 웅덩이에 있는 "곡예사" 이기 코그토글이 마이크로필름 8개를 가져다 달라고 했습니다.', },
  },
  [11729] = {
    '초음파 나사돌리개',
    {'막 분해한 기계의 부품 중에 이 물건이 있었습니다.$B$B아주 복잡하게 생겼습니다... 비행장에 있는 누군가에게 이것에 대해 물어보는 것이 좋을 것 같습니다. 크래프티 워블스프로킷은 알고 있지 않을까요?', },
    {'초음파 나사돌리개를 피즈크랭크 비행장에 있는 크래프티 워블스프로킷에게 전달해야 합니다.', },
  },
  [11730] = {
    '주인과 하인',
    {'초음파 나사돌리개는 말하자면 만능 도구랍니다. 기계를 다루는 일이라면 뭐든 할 수 있거든요!$B$B웅덩이로 돌아가 메카조드의 로봇을 여러 대 해체해 주세요. 그리고 이 나사돌리개를 사용해 주면 녀석들이 우리 편으로 넘어올 거예요. 비록 잠깐이긴 하지만요.$B$B아마도 기계노움한테는 효과가 없을 테니 그들한테는 시비를 걸지 않는 게 좋아요.$B$B아, 그리고 로봇 안에서 뭔가 꺼낼 게 있다면 나사돌리개를 쓰기 전에 꺼내두세요!', },
    {'초음파 나사돌리개를 사용해 로봇 15대를 개조한 다음 피즈크랭크 비행장에 있는 크래프티 워블스프로킷에게 가야 합니다.$B$B로봇을 개조하기 전에 전리품을 획득해야 합니다.', },
  },
  [11731] = {
    '횃불을 던져라!',
    nil,
    {'횃불 던지기 시험을 통과하고 불꽃마술의 대가와 대화해야 합니다.', },
