  [12411] = {
    '자매의 맹세',
    {'부탁이에요, $N. 당신이 아루갈을 뒤쫓고 있다는 걸 알아요. 아버지와의 추억에 맹세코 당신이 그를 쓰러트리는 걸 도와드리겠어요.$B$B그러니 제발 부탁이에요... 안야는 아무 잘못도 없어요. 그 아이를 저 짐승들이 우글거리는 곳에 놔둘 수는 없어요.$B$B동생을 구하도록 도와주신다면 당신이 아루갈을 상대할 때 힘이 되어드리겠어요.$B$B아나톨리가 말한 동굴이 어딘지 알아요. 산 반대편에 있지요. 어서 가요. 제 동생이 위험해요.', },
    {'극지 마을 근처의 그늘울음 동굴에서 안야를 찾아야 합니다.', },
  },
  [12412] = {
    '적의 친구',
    {'너에게 해 줄 말은 하나밖에 없다. 회색 구릉지에서 얼라이언스 놈들을 반드시 쓸어버려야 해.$B$B그곳에는 목재, 광물, 사냥감, 전략적 요충지가 널렸으니 한 치도 물러설 수 없는 곳이다.$B$B우리가 지금까지 잘해오긴 했으나... 한 순간에 적이 유리해질 수 있는 요소가 아직 있다.$B$B동쪽 먼 곳에 있는 숲 가장자리 초소의 덫사냥꾼들이 얼라이언스에 합세하려는 모양이더군. 그런 일이 벌어지기 전에 놈들을 모두 없애 버려라.', },
    {'정복의 요새에 있는 정복자 크레나가 숲 가장자리 초소에서 은빛시내 마을 사냥꾼 8명과 그들의 지도자인 블라데크를 처치해 달라고 했습니다.', },
  },
  [12413] = {
    '은빛시내 마을 공격',
    {'몇 시간 전만 해도 덫사냥꾼 놈들은 얼라이언스의 머릿수를 늘려주려는 잡역부에 지나지 않았다.$B$B하지만 놈들은 변해버렸다... 불확실한 뭔가로 말이다. 난 불확실한 건 좋아하지 않는다. 회색 구릉지 북부에 있는 놈들의 본거지인 은빛시내 마을 근처의 산으로 가라. 그리고 우리 병사들을 도와 놈들을 없애 버려.', },
    {'정복의 요새에 있는 정복자 크레나가 은빛시내 마을 언덕에서 은빛시내 마을 수호병 8명을 처치해 달라고 했습니다.', },
  },
  [12414] = {
    '야생마 길들이기',
    {'스타우트맨틀 대장님이 우리 가병... 거병... 아니, 기병대를 거의 다 데리고 투자개발 만으로 싸우러 가셨어요.$B$B이곳과 북동쪽에 있는 바위 언덕에는 산악 야생마가 워낙 많아서 우린 여기에 야영지를 차렸어요. 그리고 대장님은 저한테 야생마를 잡는 일을 맡기셨어요.$B$B도와주실 수 있나요? 저한테 막대기랑 실이 있고, 야영지 주변에는 야생 당근이 많이 자라요. 당근을 뽑아 막대기에 매달아서 야생마를 유인한 다음에 여기까지 타고 오시면 돼요.', },
    {'산악 야생마 5마리를 타고 서부 몰락지대 여단 야영지에 있는 종자 퍼시에게 가야 합니다.', },
  },
  [12415] = {
    '야생마 겁주기',
    {'얼라이언스의 \'서부 몰락지대 여단\'이 북쪽 멀리 떨어진 토르 모단에서 야영하고 있습니다. 그자들은 멍청하게도 우리에게서 투자개발 만을 빼앗으려고 병력을 나누는 바람에 야영지에는 기병대도 남기지 않았습니다.$B$B그래서 지금은 서부 몰락지대 여단 야영지와 바위 언덕 근처를 돌아다니는 산악 야생마를 잡아 길들이려 하더군요. 그자들이 군마를 보충하기 전에 막아야 합니다. 이걸 가져가서 야생마들 근처에 던지면 쫓아버릴 수 있을 겁니다.', },
    {'산악 야생마 15마리를 놀라게 하여 쫓아낸 후 원크와 야영지에 있는 소울로크 스톰퓨리에게 가야 합니다.', },
  },
  [12416] = {
    '치열한 전투',
    {'스컬지들이 제단을 장악했고 제단의 수호자인 달리아 선터치는 사라졌습니다. 게다가 제단을 보호하다 죽어간 붉은 고룡들은 뒤틀린 해골 잿불고룡이 되어 우리를 공격하고 있습니다. 상황이 매우 급합니다, $R|1이여;여;.$B$B지금 이스칸다르 부대장이 그대의 종족 한 무리를 이끌고 제단에서 밀려나오는 스컬지들을 막아내고 있습니다.$B$B가서 그를 도와주되... 되도록이면 후방에 있도록 하십시오. 스컬지들이 쉴 새 없이 밀려나오는 상황이니, 그대마저 저 협곡에서 죽게 할 수는 없으니까요.', },
    {'얼어붙은 구울 돌격병 12마리, 얼어붙은 외눈깨비 돌격병 8마리, 그리고 얼어붙은 누더기골렘 돌격병 1마리를 처치하여 협곡을 방어하는 부대장 이스칸다르를 지원하고 임무를 마치면 세리스트라즈에게 가야 합니다.', },
  },
  [12417] = {
    '대지의 품으로',
    {'해결해야 할 일이 하나 더 있습니다. 저 위에서 벌어지는 전투 때문에 형제들이 계속 목숨을 잃고 있습니다. 빨리 수습하지 않으면 잿불고룡의 수를 늘려주는 꼴이 될 겁니다.$B$B남쪽이나 북쪽 통로를 이용해서 제단으로 들어가십시오. 그곳이라면 스컬지의 수가 상대적으로 적을 겁니다.$B$B제단에 들어가면 나무에서 떨어진 루비 도토리를 찾아 죽어간 우리 동족의 시체 주변에 심어주십시오. 그렇게 하면 그들은 새로운 생명을 얻고... 우리 종족으로서 자랑스러운 최후를 맞이할 수 있을 겁니다.', },
    {'북쪽 또는 남쪽 통로를 이용하여 루비 용제단에 들어간 다음 루비 도토리를 찾으십시오. 죽은 붉은용에게 루비 도토리를 사용해 대지의 품으로 돌려보낸 후 세리스트라즈에게 가야 합니다.', },
  },
  [12418] = {
    '불타는 벌판을 지나',
    {'마지막 임무를 맡기겠습니다, $R|1이여;여;. 이번 일로 전세를 뒤집을 수 있을 겁니다.$B$B제단으로 돌아가 스컬지 병력을 생산해내는 강령술사들을 제거하십시오. 그들이 사라진다면 적들을 물리치고 제단을 되찾을 수 있을 겁니다.$B$B그리고 제단 아래에 있는 타락의 근원을 찾아 제거하십시오.$B$B그대에게 너무 많은 일을 맡겼다는 건 알지만, 이번 임무를 완수해 준다면 우리는 반드시 승리할 겁니다.', },
    {'북쪽 또는 남쪽 통로를 이용하여 루비 용제단에 들어간 다음 얼어붙은 강령술사 6명을 처치하고 제단 아래에 있는 타락의 근원을 제거한 후 세리스트라즈에게 가야 합니다.', },
  },
  [12419] = {
    '루비 용제단의 최후',
    {'브로치를 집어들자 약하던 빛이 조금 강해졌습니다.$B$B크라서스가 달리아의 운명을 알고자 할 겁니다. 이 브로치를 고룡쉼터 사원에 있는 그에게 가져가야 합니다.', },
    {'달리아 선터치의 루비 브로치를 고룡쉼터 사원의 지도 의회실에 있는 크라서스에게 전달하고 달리아에게 무슨 일이 있었는지 알려야 합니다.', },
  },
  [12420] = {
    '맥주 사랑 동호회',
    {'맥주 사랑 동호회에 가입하시면 특별한 혜택을 드립니다. 동호회에 가입하신 분들에게는 새로 나온 최고의 맥주를 구하는 곳 정보를 보내 드립니다.$B$B가입하시려면 아이언포지의 부싯돌 선술집으로 가셔서 라르킨 썬더브루에게 "맥주 사랑 동호회" 가입 신청서를 제출하시면 됩니다.', },
    {'"맥주 사랑 동호회" 가입 신청서를 아이언포지의 부싯돌 선술집에 있는 라르킨 썬더브루에게 가져가야 합니다.', },
  },
  [12421] = {
    '맥주 사랑 동호회',
    nil,
    nil,
  },
  [12422] = {
    '전략을 위한 자비',
    {'잘 듣게, $N. 자네가 신중하고 빠르게 행동해주길 바라네.$B$B크레나의 고위 하사관 한 명이 은빛시내 마을 밖에서 추방된 덫사냥꾼을 생포했네. 그들이 평소처럼 일을 진행한다면 포로는 해가 저물기 전에 처형될 걸세.$B$B그렇게 둬서는 안 되네. 자네도 알겠지만, 이 포로는 미크헤일의 일지에 묘사돼 있던 큰딸과 아주 비슷하다네.$B$B그녀를 풀어준 다음 잘만 미행한다면 덫사냥꾼들을 지휘하는 자에게 접근할 수 있을 걸세. 이 편지를 그녀에게 전해주게나.', },
    {'사절의 편지를 은빛시내 마을 언덕에 있는 사로잡힌 덫사냥꾼에게 전달해야 합니다.', },
  },
  [12423] = {
    '미크헤일의 일지',
    {'수집한 여러 가지 증거로 볼 때, 일지는 미크헤일이라는 은빛시내 마을 주민이 쓴 것으로 보입니다.$B$B이 일지는 평범한 인간들의 공용어로 기록되었습니다. 정복자 크레나에게 가져가는 게 좋겠습니다.', },
    {'미크헤일의 일지를 정복의 요새에 있는 정복자 크레나에게 가져가야 합니다.', },
  },
  [12424] = {
    '고르고나',
    {'이 일지엔 인간들이 쓰는 돼지 같은 말만 가득하군. 당장 태워버려야겠다.$B$B내 여동생 고르고나에게 가져다주도록. 그리고 내 시간을 낭비하지 마라.', },
    {'미크헤일의 일지를 정복의 요새에 있는 고르고나에게 가져가야 합니다.', },
  },
  [12425] = {
    '눈먼 루나',
    {'덫사냥꾼들 문제는 우리가 생각했던 것보다 더 중요할 수도 있네.$B$B그들이 얼라이언스와 손을 잡은 게 아니라면 스컬지와 한 패거리일 지도 모르고 그렇다면 문제가 되겠지.$B$B하지만 예전에 들였던 노력이 헛수고는 아니었던 것 같군. 전에 이 포로가 점쟁이로 보이는 인물과 만나는 장면을 목격했다네. 결국 놓쳤지만 말이야.$B$B우리가 그녀를 다시 찾을 수만 있다면 그들의 지도자에게 접근할 수 있을 걸세. 눈먼 루나를 찾아가서 뭘 알아낼 수 있는지 확인해 보게.', },
    {'볼드랏실 고개 근처에 있는 루나의 야영지에서 눈먼 루나와 대화해야 합니다.', },
  },
  [12426] = {
    '미사용',
    nil,
    nil,
  },
  [12427] = {
    '정복의 요새 투기장: 곰 씨름!',
    {'격투에 관심 좀 있나? 크레나 님은 병사들이 투기장에 참가하는 걸 금지하셨지. 하지만 자넨 엄밀하게 말해 계약 관계에 있으니 그런 금지 조항이랑 아무 상관도 없다네.$B$B괜찮은 구실 아닌가?$B$B어쨌거나 관심이 있다면 우선 쉬운 상대랑 맞붙여 주지. 한 마리 허약한 곰이라네. 친구를 데려오는 것도 허락해 줌세!', },
    {'정복의 요새 투기장에서 무쇠가죽과 싸워 이긴 후 사서 벨젠과 대화하여 보상을 받아야 합니다.', },
  },
  [12428] = {
    '정복의 요새 투기장: 미친 펄볼그 전투',
    nil,
    nil,
  },
  [12429] = {
    '정복의 요새 투기장: 피와 철',
    nil,
    nil,
  },
  [12430] = {
    '정복의 요새 투기장: 죽음의 문턱',
    nil,
    nil,
  },
  [12431] = {
    '정복의 요새 투기장: 최종 결전',
    {'자네 때문에 크레나 님이 화가 많이 나신 모양일세. 고르고나 님과 말다툼을 한 모양이던데, 자넬 당장 투기장으로 데려오라 했나 보네.$B$B얘길 듣자하니 이번 대결에서는 목숨을 내놓아야 한다더라고! 자네한테는 안 됐지만 우리 사업에는 더없이 좋은 소식이라네!$B$B어쨌든, 자넨 운명을 벗어날 수 없네... 그냥 들어가서 빨리 당하는 게 편할 거야.', },
    nil,
  },
  [12432] = {
    '붉은 로켓을 타고',
    {'거기 자네, $R! 냉큼 이리로 오게!$B$B얼라이언스의 목재를 운반할 배가 준비를 마치고 이곳 북동쪽에 정박 중인 모양일세. 그 목재를 차지하기는 어려울 테니 할 일은 하나뿐이지. 가서 부숴버리게!!$B$B이곳 부두 끝에 있는 고블린이 어뢰를 준비해 놨을 걸세. 그 고블린에게 북쪽에 정박 중인 고블린 선박의 어뢰에 쓸 연료가 있네.$B$B가서 연료를 받은 다음 어뢰를 타고 놈들 배로 돌진하게나.$B$B어서 가 보게!', },
    {'투자개발 만의 등대에 있는 사령관 고를로크가 얼라이언스의 목재 선박을 파괴해 달라고 했습니다.$B$B사령관 고를로크를 만나려면 호드가 등대를 점령해야 합니다.', },
  },
  [12433] = {
    '용해제를 찾아서',
    {'크악! 또 이러는군. 접합 부위가 또 녹아버렸잖아...$B$B한 번 이러고 나면 윤활유가 새나와서 온갖 기어를 뒤덮는다오. 적당한 용해제가 없으면 몇 시간이나 닦아야 할지 모르겠소.$B$B아, 맞아. 부두에 있는 투자개발회사 수송선에 가면 로켓 연료가 있을 거요. 녀석들은 그걸 원료 115라고 불렀던 것 같소. 용해제로는 그게 아주 딱이지.$B$B그걸 가져다주면 제대로 보상하겠소.', },
    {'투자개발 만의 호드 기지에 있는 구르토가 원료 115 한 통을 가져다 달라고 했습니다.', },
  },
  [12435] = {
    '군주 아프라사스트라즈에게 보고',
    {'난 생명의 불이 꺼지는 걸 싫어한다... 그 어떤 생명이라 해도 말이지. 하지만 말리고스가 우리를 죽이고 요새를 더럽히는 것은 그냥 두고 볼 수가 없구나!$B$B난 고룡쉼터 사원의 방어를 군주 아프라사스트라즈에게 일임했다.$B$B토라스트라자와 이야기하여 비룡을 타고 그에게 가보도록 해라.$B$B그의 힘이 되어준다면 좋겠구나, $N|1이여;여;. 푸른용군단에 맞서 고룡쉼터 사원을 방어하려면 그대의 도움이 꼭 필요하다.', },
    {'고룡쉼터 사원에 있는 군주 아프라사스트라즈와 대화해야 합니다.', },
  },
  [12436] = {
    '은밀한 수입',
    {'반갑네, $C|1이여;여;. 이곳 정복의 요새에서는 크레나 님께 허락받은 물건만 거래할 수 있네. 가격도 크레나 님이 정하시고 말이지...$B$B덕분에 사업하기 여간 어려운 게 아니라네. 우리 병사들을 배불리 먹이면서 동시에 내 주머니도 채울 방법이 없나 생각 중이라네. 나가서 순록들을 잡아 고기를 좀 가져다주지 않겠나?$B$B이 근방에는 순록이 널렸으니 육질이 좋은 걸로 골라서 가져 오게.', },
    {'정복의 요새에 있는 배급원 로르크란이 근처에 있는 긴뿔 순록에게서 육즙이 많은 사슴 고기 5개를 가져다 달라고 했습니다.', },
  },
  [12437] = {
    '붉은 로켓을 타고',
    {'잘 들으십시오, $N 님. 이 싸움에서는 전술적으로 우위에 서는 것만큼이나 자원을 확보하는 것도 중요합니다.$B$B호드와의 접전 양상을 확 바꿔 놓을 임무를 당신에게 맡기겠습니다.$B$B부두 끝에 어뢰가 준비되어 있는데, 그걸 사용하면 북서쪽에 정박한 호드의 목재 선박을 가라앉힐 수 있을 겁니다.$B$B먼저 이곳에서 북쪽에 정박 중인 고블린 선박에 실린 연료를 얻어야 해요. 그다음 어뢰를 타고 가서 놈들의 배를 날려버리십시오!!', },
    {'투자개발 만의 등대에 있는 사령관 하우저가 호드의 목재 선박을 파괴해 달라고 했습니다.$B$B사령관 하우저를 만나려면 얼라이언스가 등대를 점령해야 합니다.', },
  },
  [12438] = {
    '현상 수배: 크루그 오스브레이커',
    {'의뢰인: 일루시아 룬$B$B크루그 오스브레이커라 불리는 스컬지 거인 군주가 제 남편, 아이들, 그리고 친구 위스퍼베일을 살해했습니다. 놈을 처치해 주실 용사님께서는 부패의 벌판 남동쪽 언덕에서 크루그를 찾으세요.$B$B정의는 실현되어야 합니다! 크루그 오스브레이커를 없애주세요!$B$B현상금과 보상을 레오릭 본 젤디그 대영주님께 맡겨 두었으니, 크루그를 처치한 용사님은 그분을 찾아가시기 바랍니다.$B$B저희의 명예를 되찾아 주세요!', },
    {'크루그 오스브레이커를 처치한 다음 용의 안식처에 있는 윈터가드 성채의 대영주 레오릭 본 젤디그에게 돌아가 보상을 받아야 합니다.', },
  },
  [12439] = {
    '서쪽의 소란',
    {'우리가 이룩한 것들을 파괴하려는 놈들이 또 있습니다, $N. 서쪽에 있는 별의 쉼터로 가주십시오. 대마법사 모데라가 이 세계의 마법 지맥이 요동치고 있다는 걸 알아냈습니다. 당장 이 문제를 처리해야 합니다!$B$B어서 가십시오, $N! 이곳 윈터가드의 그리핀 기지에 있는 로드니 웰스에게 당신 임무를 설명해 주십시오. 별의 쉼터로 갈 수 있도록 제일 빠른 그리핀을 내줄 겁니다. 그곳에 도착하면 대마법사 모데라와 이야기하십시오.', },
    {'윈터가드 성채에 있는 로드니 웰스와 대화해 용의 안식처 서부에 있는 별의 쉼터로 가야 합니다.', },
  },
  [12440] = {
    '별의 쉼터로',
    nil,
    nil,
  },
  [12441] = {
    '현상 수배: 고위주술사 블러드포우',
    {'의뢰인: 드롤피$B$B사촌 스크로티, 브로테와 함께 으스스한 언덕에 올라갔을 때, 무시무시한 핏빛발일족 울바르 한 무리가 우리를 공격했습니다. 우리는 필사적으로 도망쳤지만 스크로티와 브로테는 붙잡힌 것 같습니다.$B$B복수하고 싶습니다! 용의 안식처 남동쪽 언덕에 있는 으스스한 언덕으로 가서 고위주술사 블러드포우와 졸개들을 없애주실 용사는 안 계십니까?$B$B레오릭 본 젤디그 대영주님께 현상금을 두둑이 맡겨 놓았으니 제 의뢰를 해결해 주시고 현상금을 타 가세요!', },
    {'고위주술사 블러드포우를 처치한 다음 용의 안식처에 있는 윈터가드 성채의 대영주 레오릭 본 젤디그에게 돌아가 보상을 받아야 합니다.', },
  },
  [12442] = {
    '현상 수배: 붉은돌격대 사령관 이우스투스',
    nil,
    nil,
  },
  [12443] = {
    '용해제를 찾아서',
    {'크악! 또 이러는군. 접합 부위가 또 녹아버렸잖아...$B$B한 번 이러고 나면 윤활유가 새나와서 온갖 기어를 뒤덮는다오. 적당한 용해제가 없으면 몇 시간이나 닦아야 할지 모르겠소.$B$B아, 맞아. 부두에 있는 투자개발회사 수송선에 가면 로켓 연료가 있을 거요. 녀석들은 그걸 원료 115라고 불렀던 것 같소. 용해제로는 그게 아주 딱이지.$B$B그걸 가져다주면 제대로 보상하겠소.', },
    {'투자개발 만의 얼라이언스 기지에 있는 바블핑크가 원료 115 한 통을 가져다 달라고 했습니다.', },
  },
  [12444] = {
    '검은강 쟁탈전',
    {'투자개발회사는 회색 구릉지를 떠나고 있습니다. 여기서 벌목 사업을 진행했는데 상황이 너무 위험해진 거지요.$B$B그들이 버리고 간 현장은 먼저 차지하는 사람이 임자라고 할 수 있는데, 호드 놈들에게는 절대 넘겨줄 수 없습니다.$B$B이 지역에 있는 벌목장 3개 중에 검은강 벌목지가 작긴 해도 가장 중요한 곳이랍니다. 가서 호드 병력을 몰아내 주십시오. 이곳의 나무가 놈들에게 넘어가면 언제 어디서 무기가 되어 우리에게 겨누어질지 모르니까요.', },
    {'정찰대장 카터가 검은강 벌목지에서 호드 병사 또는 전투원 10명을 처치해 달라고 했습니다.', },
  },
  [12447] = {
    '흑요석 용제단',
    {'스컬지가 밀려듭니다. 코리알스트라즈는 루비 용제단을 지키겠다고 헛되이 동맹에 도움을 청하고 있어요. 그러면서도 고귀하신 붉은용군단께서는 다른 용족의 제단을 지키는 데 도움을 줄 생각은 없는 것 같답니다.$B$B놀라운가요? 난 아니에요.$B$B지금 흑요석 용제단을 좀먹는 언데드를 퇴치하려고 우리 종족 중 하나가 그곳으로 떠났어요. 당신이 알렉스트라자보다 고귀하고 정의롭다는 사실을 증명하려면 그곳에 가서 칼을 몇 차례 휘두르기만 하면 됩니다.$B$B날 즐겁게 해 보시죠.', },
    {'고룡쉼터 사원의 북서쪽이자 분노의 관문 서쪽에 있는 흑요석 용제단으로 가서, 제단 안에 있는 세린나를 찾아 도움을 줘야 합니다.', },
  },
  [12448] = {
    '치열한 전투',
    {'스컬지들이 제단을 장악했고 제단의 수호자인 달리아 선터치는 사라졌습니다. 게다가 제단을 보호하다 죽어간 붉은 고룡들은 뒤틀린 해골 잿불고룡이 되어 우리를 공격하고 있습니다. 상황이 매우 급합니다, $R|1이여;여;.$B$B지금 드레이즌 부대장이 그대의 종족 한 무리를 이끌고 제단에서 밀려나오는 스컬지들을 막아내고 있습니다.$B$B가서 그를 도와주되... 되도록이면 후방에 있도록 하십시오. 스컬지들이 쉴 새 없이 밀려나오는 상황이니, 그대마저 저 협곡에서 죽게 할 수는 없으니까요.', },
    {'얼어붙은 구울 돌격병 12마리, 얼어붙은 외눈깨비 돌격병 8마리, 얼어붙은 누더기골렘 돌격병 1마리를 처치하여 협곡을 방어하는 부대장 드레이즌을 지원하고 임무를 마치면 바르가스트라즈에게 가야 합니다.', },
  },
  [12449] = {
    '대지의 품으로',
    {'해결해야 할 일이 하나 더 있습니다. 저 위에서 벌어지는 전투 때문에 형제들이 계속 목숨을 잃고 있습니다. 빨리 수습하지 않으면 잿불고룡의 수를 늘려주는 꼴이 될 겁니다.$B$B남쪽 통로를 이용하여 제단으로 들어가십시오. 그곳이라면 스컬지의 수가 상대적으로 적을 겁니다.$B$B제단에 들어가면 나무에서 떨어진 루비 도토리를 찾아 죽어간 우리 동족의 시체 주변에 심어주십시오. 그렇게 하면 그들은 새로운 생명을 얻고... 우리 종족으로서 자랑스러운 최후를 맞이할 수 있을 겁니다.', },
    {'남쪽 통로를 이용하여 루비 용제단에 들어간 다음 루비 도토리를 찾으십시오. 죽은 붉은용에게 루비 도토리를 사용해 대지의 품으로 돌려보낸 후 바르가스트라즈에게 가야 합니다.', },
  },
  [12450] = {
    '불타는 벌판을 지나',
    {'마지막 임무를 맡기겠습니다, $R|1이여;여;. 이번 일로 전세를 뒤집을 수 있을 겁니다.$B$B제단으로 돌아가 스컬지 병력을 생산해내는 강령술사들을 제거하십시오. 그들이 사라진다면 적들을 물리치고 제단을 되찾을 수 있을 겁니다.$B$B그리고 제단 아래에 있는 타락의 근원을 찾아 제거하십시오.$B$B그대에게 너무 많은 일을 맡겼다는 건 알지만, 이번 임무를 완수해 준다면 우리는 반드시 승리할 겁니다.', },
    {'남쪽 통로를 이용하여 루비 용제단에 들어간 다음 얼어붙은 강령술사 6명을 처치하고 제단 아래에 있는 타락의 근원을 제거한 후 바르가스트라즈에게 가야 합니다.', },
  },
  [12451] = {
    '원크와 야영지로',
    {'정복의 요새를 짓는 동안 척후병 보르타크를 보내 동쪽의 우리 타운카 동맹들과 연락을 취하도록 했네. 그들은 회색구렁 요새 너머 회색 구릉지 동쪽 끝에 있는 원크와 야영지에 머물고 있다네.$B$B그곳에 가서 보르타크의 보고를 들은 다음 그와 그곳 대족장을 도와주도록 하게나. 정복의 요새에 크게 이바지한 자네니 보르타크도 자네가 도와주겠다고 하면 기뻐할 걸세.', },
    {'원크와 야영지에 있는 척후병 보르타크와 대화해야 합니다.', },
  },
  [12453] = {
    '하늘의 눈',
    {'얼라이언스와의 싸움에 힘을 보태달라는 부탁을 받았습니다. 현재 적들과 우리의 전력은 비슷한 상태이니, 판도를 뒤집을 방법을 모색 중입니다.$B$B산악 지역과 숲 위를 날아다니는 흰죽지수리는 훌륭한 정찰병이 되어 줄 겁니다. 이 은빛 깃털을 가져가서 제 시야를 그들의 시야에 동화해 주십시오.$B$B적들의 동태를 살필 수 있다면 전략적인 이득을 얻을 수 있을 겁니다. 동쪽 산에 가면 흰죽지수리를 여럿 찾을 수 있을 겁니다.', },
    {'바람현자 그레이혼이 회색 구릉지에 있는 흰죽지수리 6마리에게 은빛 깃털을 사용해 달라고 했습니다.', },
  },
  [12454] = {
    '생명의 순환',
    {'에메랄드의 꿈속에 잠든 이세라는 꿈과 아제로스 둘 다를 돌보고 있어요. 하지만 끔찍한 악몽이 꿈속에 자리 잡았으니, 이세라마저 그 꿈에 영향을 받지 않을까 걱정이 되는군요.$B$B얼마 전에 저는 다른 용제단에서 서리고룡을 되살려내는 스컬지와 벌이는 싸움에 힘을 보태 달라고 그녀에게 부탁했어요. 하지만 그녀는 제 말을 잘못 이해했어요.$B$B우리를 돕는 대신 그녀는 용제단의 수호자들에게 녹색용군단이 아닌 자들은 모두 적으로 간주하라고 일렀어요.$B$B저 하늘발톱을 몇 마리 처치해 주셔야겠어요. 제단의 균형을 되찾아야 해요.', },
    {'에메랄드 용제단에 있는 정원의 수호자 니셰라가 에메랄드 하늘발톱 5마리를 처치해 달라고 했습니다.', },
  },
  [12455] = {
    '바람 속에 흩어지다',
    {'탄약이 이상한 곳에 떨어졌소, 친구. 저 위에 날아다니는 서리고룡 놈들이 방해하는 바람에 서부경비대 성채에서 탄약을 적당한 곳에 투하하지 못했다오. 비행기는 순식간에 파괴됐고, 거기에 실린 짐은 대부분 바람에 날려 흩어졌소. 대부분은 부패의 벌판 위쪽에 남아 있지.$B$B당신이 가서 그 빌어먹을 벌판 사방에 흩어진 탄약 상자를 회수해 줬으면 하오.$B$B서두르시오, $G멋진:예쁜; 친구! 총과 탄약이 없으면 공격을 시작할 수조차 없으니 말이오!', },
    {'용의 안식처에 있는 윈터가드 묘지의 앰보 캐시가 윈터가드 군수품 8개를 가져다 달라고 했습니다.', },
  },
  [12456] = {
    '알리스트로스의 깃털',
    {'아직 제단의 균형이 완전히 회복되지 않은 것 같군요. 신록의 수호자는 우리가 한 일 때문에 화가 나 있고, 이유 같은 건 들으려고도 하지 않을 거예요.$B$B알리스트로스는 에메랄드 용제단의 남쪽 끝에 있는 거대한 하늘발톱이에요. 용제단의 하늘을 수호하는 책임을 진 자이기도 하지요. 안타깝지만 그를 처치해야 할 것 같아요.$B$B이 하늘발톱 허물을 가져가 그가 볼 수 있는 곳에 내려놓으세요. 알리스트로스가 나타나거든 처치하고 그의 깃털을 가져다주세요.', },
    {'에메랄드 용제단의 정원의 수호자 니셰라가 알리스트로스의 깃털을 가져다 달라고 했습니다.', },
  },
  [12457] = {
    '퇴로를 뚫어라',
    {'바로 동쪽과 서쪽에 대포를 설치하고 방벽을 세워 놓았소. 부상당한 우리 병사들은 양쪽 방에 숨어 구울에게 들키지 않으려 애쓰고 있지.$B$B이제 탄약도 손에 들어왔으니 대포 하나를 맡아 우리 병사들이 돌아올 수 있도록 길을 뚫어주시오! 대포를 쏠 준비를 마치고 병사를 부른 다음 스컬지가 가까이 붙지 못하게 마구 퍼부어 주는 거요!$B$B어서 가시오!', },
    {'용의 안식처에 있는 윈터가드 묘지의 앰보 캐시가 부상당한 7군단 병사 8명을 구조해 달라고 했습니다.', },
  },
  [12458] = {
    '덩굴손 씨앗',
    {'스컬지와 싸우려면 더 많은 무기가 필요하지요. 남동쪽에 있는 에메랄드 용제단이 지닌 고유한 특성에서 해답을 찾을 수 있을 것 같군요.$B$B실바나스가 구원한 이를 제외한 모든 언데드는 용제단에 발을 들여놓는 순간 먼지가 되어 사라져버리고 맙니다.$B$B제게 가설이 하나 있는데 당신이 그 가설을 입증해줬으면 좋겠습니다, 젊은 $R|1이여;여;. 에메랄드 용제단에 가서 그곳에 있는 에메랄드 덩굴손에서 씨앗을 채취해 오십시오.', },
    {'고룡쉼터 사원 꼭대기에 있는 군주 이타리우스가 덩굴손 씨앗 3개를 가져다 달라고 했습니다.', },
  },
  [12459] = {
    '창조의 힘, 파괴의 힘',
    {'씨앗은 3개뿐이니 되도록 유용하게 써야 합니다.$B$B당신의 목표는 북쪽에 있는 갈라크론드의 안식처 주위를 맴도는 되살아난 서리고룡, 부패의 벌판에 있는 폐허가 된 병영의 흉측한 투르기드, 그리고 북동쪽 멀리 떨어진 곳의 진타칼라르에 있는 감독관 데스게이즈입니다.$B$B이 씨앗은 하급 스컬지라면 완전히 파괴할 수 있고, 그 셋 또한 크게 약화시킬 수 있을 것입니다. 그들을 약화시키면 당신 혼자서도 상대할 수 있을 테지요.', },
    {'고룡쉼터 사원 꼭대기에 있는 군주 이타리우스가 약해진 되살아난 서리고룡, 약해진 흉측한 투르기드, 그리고 약해진 감독관 데스게이즈를 처치해 달라고 했습니다.', },
  },
  [12460] = {
    '루비 용제단에 보고',
    {'루비 용제단에 당신의 도움이 필요해요.$B$B사방을 둘러싼 푸른용군단과 스컬지 때문에 그곳의 용족들이 어려움에 처했어요... 우리보다 나은 게 없는 처지랍니다. 상황이 긴박하긴 하지만, 값진 동맹을 얻을 수 있는 이런 기회는 흔치 않아요.$B$B제가 루비 용제단에서 붉은용군단과 스컬지가 벌이는 전투를 지원하려고 1개 연대 병력을 준비했어요. 그대가 싸우고자 한다면 그곳에 가서 제단의 동쪽 어귀에 있는 세리스트라즈를 찾아 가능한 한 그를 도와주세요.', },
    {'고룡쉼터 사원 서쪽의 루비 용제단 동쪽 산등성이에 있는 세리스트라즈에게 보고해야 합니다.', },
  },
  [12461] = {
    '루비 용제단에 보고',
    {'루비 용제단으로 가 줘야겠소. 부탁이 아니오.$B$B사방을 둘러싼 푸른용군단과 스컬지 때문에 그곳의 용족들이 어려움에 처했소. 우리보다 나은 게 없는 처지라오. 조상이 저지른 잘못을 갚을 기회는 그리 쉽게 찾아오지 않는 법이지.$B$B내가 루비 용제단에서 스컬지와 전투를 도우려고 1개 연대 병력을 준비해 놨소. 그들과 합세하여 스컬지들을 처치하시오!$B$B호드를 위하여!', },
    {'아그마르의 망치 동쪽의 루비 용제단 서쪽에 있는 산등성이의 바르가스트라즈에게 보고해야 합니다.', },
  },
  [12462] = {
    '침투 준비',
    {'저 아래에는 지옥으로 가는 문이 있습니다. 눈이 닿는 곳은 어디나 스컬지로 가득하고, 시간이 흘러갈수록 그 수는 늘어만 갈 뿐이지요. 그나마 수집한 정보에 의하면 스컬지 강령군주 두 놈이 지하 1층에 있는 두 납골당을 비워내느라 정신이 없는 모양이더군요. 텔잔이 있는 안쪽 방으로 침투하려면 우선 그 강령군주 두 놈과 낙스라마스 졸개들을 처리해야 합니다.$B$B묘지로 내려가 강령군주들과 낙스라마스 공포의 경비병, 그리고 망령들을 처치해 주십시오.', },
    nil,
  },
  [12463] = {
    '플런더비어드를 찾아라!',
    nil,
    {'용의 안식처에 있는 윈터가드 묘지의 앰보 캐시가 플런더비어드를 찾아달라고 했습니다.', },
  },
  [12464] = {
    '오랜 적수',
    {'내 오랜 숙적인 고위사령관 아벤디스가 노스렌드로 왔소.$B$B티르의 손 수도원 공격에서 살아남아 얼마 안 되는 병력을 데리고 북으로 올라온 모양이오. 남쪽에 신 하스글렌을 세워 놓았더군.$B$B스컬지와 싸우는 데 놈들이 유용할 가능성도 있겠지만, 아벤디스는 살려두기엔 너무 사악한 상대라오. 날 믿으시오. 놈들을 살려둔다면 먼지가 채 가라앉기도 전에 우리 목을 노리고 달려들 거요.$B$B그러니 놈들의 지휘관을 처치하고 다시 우리 일에 집중해야 하오. 그녀를 처치하고 증거를 가져오시오.', },
    {'고위사령관의 머리카락을 윈터가드 성채에 있는 사령관 엘리고르 돈브링어에게 가져가야 합니다.', },
  },
  [12465] = {
    '플런더비어드의 일지',
    {'플런더비어드의 일지의 다른 장들에 무슨 내용이 적혀 있든, 그것을 되찾아야 한다는 사실은 분명합니다. 터널 반대쪽에 있는 방들을 뒤져 플런더비어드를 공격한 자들을 찾아야 합니다. 그들이 일지를 찢어간 게 분명합니다!$B$B사라진 장들을 찾으면 윈터가드 묘지 입구에 있는 앰보 캐시에게 가져가야겠습니다.', },
    {'플런더비어드의 일지 제4장, 플런더비어드의 일지 제5장, 플런더비어드의 일지 제6장, 플런더비어드의 일지 제7장을 모아 용의 안식처에 있는 윈터가드 묘지의 앰보 캐시에게 가져가야 합니다.', },
  },
  [12466] = {
    '얼음폭풍 추적: 7군단 전초지',
    {'용의 안식처 북부에 있는 7군단 전초지까지 가 줘야겠소. 윈터가드 성채로 돌아간 다음 마을 북문으로 나가시오. 북쪽으로 계속 가다 보면 길이 나올 거요. 그 길에서 왼쪽으로 꺾으시오. 눈이 쌓여 길이 보이지 않아도 당황하지 말고 계속 서쪽으로 가다 보면 티탄의 길이 나온다오. 티탄의 길을 따라 북쪽으로 가면 7군단 전초지를 발견하게 될 거요.$B$B그곳에 도착하면 군단 사령관 티랄리온과 이야기하시오. 그가 당신을 기다리고 있을 거요.$B$B그리고 $N, 당신과 함께해서 영광이었소. 잘 가시오.', },
    {'용의 안식처 중심부의 7군단 전초지에 있는 군단 사령관 티랄리온에게 보고해야 합니다.', },
  },
  [12467] = {
    '얼음폭풍 추적: 텔잔의 성물함',
    nil,
    nil,
  },
  [12468] = {
    '정복자의 임무',
    {'호드가 거대한 전쟁 기계라면, 너는 그 기계의 톱니바퀴 하나에 지나지 않는다.$B$B내게는 이 기계의 연료를 확보할 책임이 있다. 내가 실패하면 이 기계 역시 돌아가지 않는다.$B$B목재, 광석, 물... 모든 게 다 이곳에 있고 우리에겐 필요하지. 얼라이언스 녀석들도 마찬가지다.$B$B인간 녀석들과 맞서 싸우기 전에 우선 서부 지역을 완전히 장악해야 한다.$B$B볼드룬의 브리쿨을 제거하는 일이 있으니 하사관 나즈그림과 얘기해 보도록 하라. 네가 아무짝에도 쓸모없는 존재가 아니라고 판단되면 임무를 더 맡기도록 하지.', },
    {'정복의 요새에 있는 하사관 나즈그림과 대화해야 합니다.', },
  },
  [12469] = {
    '되돌아온 폭탄',
    {'자세히 조사해 보니 이 고블린은 폭탄으로 가득한 꾸러미를 배달하려고 벌판을 가로지르고 있었던 모양입니다. 주변의 눈밭에 반쯤 파묻힌 하피 깃털이 그가 어떤 최후를 맞았는지 말해줍니다.$B$B아마도 이 고블린은 동쪽 어딘가에서 폭발물을 가져와 분노의 관문으로 배달하려 했던 모양입니다. 산등성이를 따라 동쪽으로 돌아가, 물건을 보낸 사람에게 배달부의 최후를 알려주는 것이 좋겠습니다.', },
    {'동쪽 산등성이를 따라 이동하여 용의 안식처 북부의 녹주둥이 전초기지에 있는 싱크에게 폭탄 꾸러미를 전달해야 합니다.', },
  },
  [12470] = {
    '무한의 비밀',
    {'북동쪽에 있는 청동 용제단에 시간의 폭풍이 불어 불멸의 존재들은 출입할 수 없게 되었답니다.$B$B무한의 용군단은 그 안에서 용제단의 통제권을 놓고 수호자들과 싸우고 있지요. 우린 이 기회를 틈타 정보를 수집해야 해요.$B$B당신은 필멸자이니 용제단에 들어가 영원의 모래시계를 사용할 수 있을 거예요. 이 모래시계를 오랫동안 지켜 낸다면 무한의 용군단 지도자가 모습을 드러낼 게 분명해요.$B$B조심하세요. 무한의 용군단이 당신을 막으려고 할 테니까요!', },
    {'고룡쉼터 사원 꼭대기에 있는 크로미가 청동 용제단으로 이동해 달라고 했습니다. 용제단에 도착하면 영원의 모래시계를 바닥에 놓아야 합니다.$B$B모래시계를 끝까지 보호한 다음 알아낸 정보를 가지고 돌아가십시오.', },
  },
  [12471] = {
    '크발디르의 잔인함',
    {'구해주셔서 고맙습니다, $N. 크발디르는 정말 짐승 같은 자들입니다!$B$B그자들은 우리 포로들끼리 서로 싸우도록 강요했습니다. 차라리 그냥 편하게 죽여줬더라면 좋았을 테지요.$B$B카루크라는 젊은이가 아직 살아 있습니다. 이곳에 있는 게 느껴지는군요. 그 젊은이에게 아버지는 죽는 순간까지 용감히 싸웠다고 전해 주십시오.', },
    {'카루크와 사로잡힌 투스카르 포로에 대해 대화해야 합니다.', },
  },
  [12472] = {
    '최후',
    {'당신이 훌륭하게 활약해준 덕에 폴드라곤 경께서 텔잔을 총공격하라는 명령을 내렸소. 7군단 병력은 묘지에 있는 소환의 방까지 진격했고, 거기서 지옥의 문으로 보이는 것을 발견했소. 지금 필요한 것은 바로 당신이라오.$B$B성물함과 보고서를 가지고 윈터가드 묘지 안에 있는 군단 사령관 요릭에게 가 보시오.$B$B성채 바깥에 스컬지가 파놓은 굴이 있으니, 그걸 통해 가면 빠르게 이동할 수 있을 거요. 어서 가시오, $N! 우리에게 승리를 안겨 주시오!', },
    {'텔잔의 성물함을 용의 안식처에 있는 윈터가드 묘지의 군단 사령관 요릭에게 가져가야 합니다.', },
  },
  [12473] = {
    '끝과 시작',
    {'좋은 이야기에는 반드시 시작과 끝이 있는 법이지요, $N. 당신의 이야기가 끝나려면 아직도 멀었지만, 이미 전설이 되기 시작했다는 데는 제 목을 걸 수 있습니다! 이 모든 일이 끝나고 우리가 어떻게 되든, 당신이 윈터가드에서 이뤄낸 업적은 오랜 세월을 전해 내려갈 겁니다.$B$B그럼 이제 당신만 준비되면 이 성물함을 사용해 텔잔을 은신처에서 끌어내겠습니다.$B$B성공하거든 윈터가드 성채에 계시는 할포드 웜베인 총사령관님께 승전보를 전해 주십시오.', },
    {'황혼의 인도자 텔잔을 처치한 다음 용의 안식처에 있는 윈터가드 성채의 총사령관 할포드 웜베인에게 보고해야 합니다.', },
  },
  [12474] = {
    '폴드라곤 요새로!',
    {'낙스라마스는 막아냈으니 이제 분노의 관문 앙그라타르에 있는 아군을 도와줘야겠소. 용의 안식처 북부의 산악 지대에는 우리 작전 기지인 폴드라곤 요새가 자리 잡고 있소. 폴드라곤 경께서 당신이 오기만을 기다리고 있을 거요! 어서 가시오, $N!$B$B마을의 북쪽 문으로 나가 목적지에 도착할 때까지 북서쪽으로 가시오.$B$B빛의 가호가 있기를!', },
    nil,
  },
  [12475] = {
    '붉은돌격대 지도',
    {'망각의 해변에서 사람들을 배신한 아서스 왕자는, 그 후 무라딘과 함께 서리한을 찾아 떠났습니다. 서리한이 있는 동굴은 가까운 곳에 있을까요? 거기에는 어떤 비밀이 있을까요? 수정구가 이러한 질문에 답을 주지는 못했지만, 그 대신 다른 뭔가를 보여줬습니다.$B$B윈터가드 성채의 북동쪽에 붉은돌격대가 세운 전초기지가 있습니다. 그 전초기지 어딘가에 지도가 있습니다. 수정구는 그 지도가 큰 도움이 될 것이라고 했습니다.$B$B그것을 가져오십시오!', },
    {'용의 안식처에 있는 윈터가드 성채의 예지자 젤리그가 붉은돌격대 지도를 찾아서 자신에게 가져와 달라고 했습니다.', },
  },
  [12476] = {
    '십자군의 귀환?',
    nil,
    {'용의 안식처에 있는 윈터가드 성채의 사령관 리노어 윈드스트라이크가 붉은돌격대 주둔지에 있는 붉은돌격대 20명을 처치해 달라고 했습니다.', },
  },
  [12477] = {
    '구원의 길',
    {'\'구원의 길\'이라는 말을 들어본 적이 있소? 듣자하니 붉은돌격대 고위사령관 아벤디스의 예언 나부랭이가 적힌 일지라고 하더군. 아마 그 책을 읽어 보면 붉은십자군 광신도의 동기와 목적을 알 수 있을 거요. 물론 대부분은 미친 소리겠지만 말이지.$B$B윈터가드 성채의 북동쪽에 있는 붉은돌격대 주둔지에 가서 \'구원의 길\' 사본을 구해 오시오. 병사 중 누군가가 가지고 있을 거요.', },
    {'용의 안식처에 있는 윈터가드 성채의 총사령관 할포드 웜베인에게 \'구원의 길\'을 가져가야 합니다.', },
  },
  [12478] = {
    '서리한 동굴',
    {'그들은 이 동굴을 서리한 동굴이라 이름 지었습니다. 왜 붉은돌격대가 이런 것에 관심을 두는지는 알 수 없지만, 어쨌거나 동굴 주변은 경계가 삼엄한 것 같습니다. 그렇다고 포기할 수야 없지요!$B$B이 수정구를 가지고 부패의 벌판에서 북쪽으로 나가 붉은돌격대 초소를 향해 북서쪽으로 가십시오. 동굴에 들어가 놈들을 물리치고 밑바닥까지 내려가야 합니다. 그곳에서 수정구를 사용한 다음 알아낸 정보를 가지고 돌아오십시오.', },
    {'용의 안식처에 있는 윈터가드 성채의 예지자 젤리그가 서리한 동굴 깊숙한 곳에서 젤리그의 수정구를 사용해 달라고 했습니다. 과거의 비밀을 파헤쳐야 합니다!', },
  },
  [12479] = {
    '노스렌드를 향하여!',
    {'\'리치 왕의 분노 친구와 가족 알파 테스트\'에 참여한 자네를 환영하네. 내 옆에 있는 차원문을 통해 노스렌드로 여정을 떠날 수 있다네.$B$B북풍의 땅에 있는 용맹의 성채나 울부짖는 협만에 있는 발가드 중 한 곳으로 가서 얼라이언스 징병관에게 보고하게나.', },
    {'북풍의 땅에 있는 용맹의 성채나 울부짖는 협만에 있는 복수의 상륙지로 가서 얼라이언스 징병관과 대화해야 합니다.', },
  },
  [12480] = {
    '노스렌드를 향하여!',
    {'\'리치 왕의 분노 친구와 가족 알파 테스트\'에 참여한 당신을 환영하는 바이오. 내 옆에 있는 차원문을 통해 노스렌드로 여정을 떠날 수 있소.$B$B북풍의 땅에 있는 전쟁노래 요새나 울부짖는 협만에 있는 복수의 상륙지 중 한 곳으로 가서 호드 징병관에게 보고하시오.', },
    {'북풍의 땅에 있는 전쟁노래 요새나 울부짖는 협만에 있는 복수의 상륙지로 가서 호드 징병관과 대화해야 합니다.', },
  },
  [12481] = {
    '상처에 소금 뿌리기',
    {'브리쿨 놈들이 잔머리를 굴리고 있네. 운 좋게도 나한테 묘안이 떠올랐어.$B$B놈들의 지도자인 비요른이란 놈은 성질이 그야말로 불같은 놈일세. 그래서 우린 말을 이것저것 짜깁기해서 놈들의 언어로 그럴싸한 욕설을 만들었네. 녀석의 어미와 마당에서 기르는 짐승에 관련된 얘긴데... 자세히는 모르겠네.$B$B이 종이를 들고 가서 놈에게 읽어주게. 놈이 쫓아오기 시작하면 이쪽으로 유도하게. 절대로 산 채로 돌려보내지 않겠네.', },
    {'베일하임에 있는 비요른 할구르드손에게 브리쿨어 욕설을 읽은 다음 리델의 매복지로 유인해서 처치해야 합니다.', },
  },
  [12482] = {
    '니플바르 전투',
    {'저희는 복수의 상륙지에 있는 증원군이 니플바르로 이동하기를 기다리고 있어요. 얼라이언스보다 먼저 그곳을 차지한다면 훨씬 유리한 입장에 서게 될 거예요.$B$B니플바르 마을로 가서 그놈들 숫자를 조금이라도 줄여보세요.', },
    {'순찰대장 아레이엘이 니플바르에서 용약탈 전사 5명, 용약탈 룬점쟁이 4명, 용약탈 사냥개 4마리를 제거해 달라고 했습니다.', },
  },
  [12483] = {
    '눈꽃버섯 스튜',
    {'저기, 여기 있는 동안 이 늙은 프리그몬을 좀 도와주지 않겠어요? 친구 중에 끝내주는 스튜를 만드는 녀석이 있는데, 당신은 그 녀석이 만드는 스튜 냄새도 맡아보지 못했을 겁니다!$B$B그 스튜에 필요한 재료가 이 근방에서 자라지요. 남서쪽에 있는 폐허나 바로 북쪽에 있는 야영지에서 구할 수 있을 겁니다.$B$B어때요, 도와주겠어요?', },
    {'화강암 웅덩이에 있는 프리그몬이 드락진 폐허에서 반짝이는 눈꽃버섯 5개와 얼음 천둥매 눈 5개, 제브할락에서 감초 5개를 가져다 달라고 했습니다.', },
  },
  [12484] = {
    '스컬지 화장',
    {'으윽.$B$B이 불쌍한 트롤들을 감고 있자니 마음이 아파지는군요. 하지만 내 허리는 그것보다 더 아픕니다!$B$B이 프리그몬을 돕는 셈치고 이 시체 중 하나만 맥에게 가져다주세요.$B$B빨리 태워버리지 않으면 금세 일어나 돌아다닐 테니까요.', },
    {'화강암 웅덩이에 있는 프리그몬이 스컬지 트롤 미라를 맥 피어센에게 가져가 불에 태워 달라고 했습니다.', },
  },
  [12486] = {
    '보르고로크 전초기지로!',
    {'당신도 그런 느낌이 들었소?$B$B<사우라노크가 혼란스러워하며 잠시 동안 가슴을 부여잡습니다. 손을 떼자 뜨거운 김이 납니다.>$B$B오랜 친구인 선견자 그림워커에게 무언가 나쁜 일이 일어났소. 방금 그런 느낌을 받았소.$B$B그는 북쪽으로 떠나면서 제자를 한 명 데려갔소. 숄라자르 분지 변두리에 대군주를 도와 전초기지를 세울 작정이었소.$B$B함께 간 제자의 이름은 스날팡이라 하오. 영매지.$B$B아마 무슨 일이 일어났는지 알고 있을 것이오. 아니면, 당신이 알아내도록 도와주기라도 할 거요.', },
    {'보르고로크 전초기지에 있는 영매 스날팡과 대화해야 합니다.', },
  },
  [12487] = {
    '정복의 요새로! 단, 조심해서!',
    {'회색 구릉지에 있는 아군 기지에서 지원 요청이 들어왔네. 자네를 보낼 테니 가서 정복자 크레나를 만나도록 하게.$B$B<적힌 내용에는 별 관심이 없다는 듯 양피지를 흔들어 보이는 고위집행관>$B$B그녀는 거기서 여러 가지 천연자원들을 확보하려고 하는 중일세. 그녀 근처에 있을 때는 조심하게나. 듣자하니 오크 중에서도 유난히 거칠다고 하더군.$B$B동쪽 길을 따라서 가다 남동쪽으로 가게. 용돌기 강에 있는 다리를 건넌 다음 사철나무 교역소에서 동쪽으로 가면 되네.', },
    {'회색 구릉지에 있는 정복의 요새에서 정복자 크레나를 만나야 합니다.', },
  },
  [12488] = {
    '고위집행관의 부름',
    {'고위집행관 워로스가 보낸 이 청원서를 아그마르 대군주님의 손에 들어가기 전에 가로채어 천만다행이로군. 워로스는 대체 무슨 생각을 하는 거지?$B$B<손에 든 편지를 찢어버리는 정예 수호병>$B$B$C, 가서 고위집행관이 대체 뭘 원하는지 알아보게.$B$B동쪽으로 난 길을 따라 용의 황무지를 가로지르게나. 황무지 남동쪽에 빠져나가는 길이 있는데, 그곳으로 가면 포세이큰 주둔지인 원한의 초소가 산 남쪽에 자리 잡고 있을 걸세.', },
    {'용의 안식처의 원한의 초소에 있는 고위집행관 워로스와 대화해야 합니다.', },
  },
  [12489] = {
    '숄라자르 분지에서의 환영 인사',
    {'그래, 어디서 오신 거죠? 그 바보 같은 대마법사가 당신까지 날려 보낸 거로 생각지 않습니다만?$B$B멀쩡하게 살아 있다니 운이 좋군요! 하마터면 저들이 당신을 날려버릴 뻔했잖습니까!$B$B습지는 위험한 곳이란 말입니다. 특히나 투자개발회사가 사방에 있다면 더더욱요!$B$B어리석은 분, 가서 대장을 만나 보세요... 그가 잘 알려줄 겁니다. 북서쪽 길 끝으로 가면 야영지가 있을 겁니다.$B$B잘 가세요!', },
    {'북서쪽 길을 따라가서 몬테가 말한 야영지로 가야 합니다.', },
  },
  [12491] = {
    '다이어브루의 독한 맥주',
    {'코렌 다이어브루의 시체는 이 작은 술통을 꼭 껴안고 있습니다. 악명이 자자한 그 \'독한 맥주\'임이 분명합니다!', },
    {'다이어브루의 독한 맥주를 아이언포지 근처의 가을 축제 광장에 있는 이펠코퍼 아이언케그에게 전달해야 합니다.', },
  },
  [12492] = {
    '다이어브루의 독한 맥주',
    {'코렌 다이어브루의 시체는 이 작은 술통을 꼭 껴안고 있습니다. 악명이 자자한 그 \'독한 맥주\'임이 분명합니다!', },
    {'다이어브루의 독한 맥주를 오그리마 근처의 가을 축제 광장에 있는 급사 스윈들케그에게 전달해야 합니다.', },
  },
  [12495] = {
    '용의 여왕 알현',
    {'앙그라타르를 공격하여 스컬지 녀석들의 진격을 멈추기는 했지만, 놈들은 여전히 무자비하게 날뛰고 있네. 놈들이 상공으로 서리고룡을 보내 전장을 공격하기 때문에 관문으로 진격하는 일이 실질적으로 불가능하네. 어떻게든 서리고룡의 공격을 차단해야 해. 하지만 고룡들의 해골을 처리하려면 먼저 용족 여왕의 허가를 받아야만 하네.$B$B남동쪽의 고룡쉼터 사원로 가서 알렉스트라자 여왕님을 찾게. 그분께 우리가 해야 할 일을 전하고 지시를 받도록 하게.$B$B어서 가게나, $N!', },
    {'용의 안식처에 있는 고룡쉼터 사원에서 생명의 어머니 알렉스트라자를 찾아야 합니다.', },
  },
  [12496] = {
    '용의 여왕 알현',
    {'자네가 분노의 관문을 가로막은 스컬지들을 쓸어버리고 싶어서 몸이 근질거린다는 건 알지만, 그전에 먼저 머리 위에 떠다니는 놈들부터 처리해야 하네. 서리고룡들의 공격을 막아내지 못하면 진격할 수가 없다네.$B$B하지만 용들의 무덤을 공격하려면 허가를 받아야 하네. 용족의 성지인 고룡쉼터 사원으로 가게. 그곳에 도착하면 알렉스트라자 여왕님을 알현하게.$B$B어서 가게, $N! 고룡쉼터 사원은 남동쪽에 있네!', },
    {'용의 안식처에 있는 고룡쉼터 사원에서 생명의 어머니 알렉스트라자를 찾아야 합니다.', },
  },
  [12497] = {
    '갈라크론드와 스컬지',
    {'악의 똬리 꼭대기에서, 스컬지의 대강령군주인 안티오크는 되살아난 폭풍 거인 군주인 티아시의 어깨에 앉아 있다. 그곳에서 언데드 군대를 지휘하여 갈라크론드의 거대한 뼈를 파내는 일에 밤낮으로 매달리고 있지. 그들이 갈라크론드를 되살린다면 그대가 지금까지 해온 노력은 모두 수포로 돌아가게 될 것이다.$B$B지금 붉은용군단을 불러모아 그대를 도와주라고 명령할 수는 없으나, 대신 내 그대에게 축복을 내리겠노라. 가서 내 청지기인 토라스트라자와 이야기해 보아라.', },
    {'용의 안식처에 있는 고룡쉼터 사원의 토라스트라자와 대화해야 합니다.', },
  },
  [12498] = {
    '루비의 날개 위에',
    {'이 보주를 사용해 고룡 제압자에게 신호를 보내세요. 그런 다음 그의 등에 올라타 북쪽에 있는 갈라크론드의 안식처로 향하세요. 도착하면 용에게 명령해서 갈라크론드를 파내려고 하는 스컬지 청소부들을 처치하세요. 그들을 처리한 다음에는 갈라크론드의 안식처를 내려다보는 곳에 있는 악의 똬리로 날아가 대강령군주 안티오크를 처치하고 그가 고룡을 조종하는 데 사용한 낫을 가져오세요.$B$B이 임무를 마치고 돌아오면 알렉스트라자 여왕께서 큰 상을 내리실 거예요!$B$B썩어가는 폭풍 거인이 주변을 돌아다니니 조심하세요, $N 님!', },
    {'황무지 청소부 30마리를 처치하고 안티오크의 낫을 되찾아야 합니다. 임무를 마친 다음, 고룡쉼터 사원에 있는 생명의 어머니 알렉스트라자에게 돌아가십시오.', },
  },
  [12499] = {
    '앙그라타르로 귀환',
    nil,
    nil,
  },
  [12500] = {
    '앙그라타르로 귀환',
    {'안티오크는 죽었고 그의 낫이 우리 손에 들어왔으니 앙그라타르에서 그대들을 공격하는 서리고룡도 곧 행동을 멈추리라. 용의 안식처에 남은 고룡들은 붙잡혀 최후를 맞을 것이고, 그 뼈는 대지의 품으로 돌아갈 테지.$B$B그대들이 최후의 저항에 나설 때는 바로 지금이다, $N. 코르크론 선봉기지로 돌아가 사울팽의 아들에게 이를 알리도록 하여라.', },
    nil,
  },
  [12501] = {
    '트롤 순찰',
    {'무기를 뽑으십시오, $R. 순찰을 나갈 시간입니다!$B$B나는 네 군데 초소를 책임지고 있습니다. 이 초소들의 통제권을 유지하는 건 우리 작전의 성공과도 직결된 일이지요. 그러니 초소를 돌며 각 초소의 대장을 도와주십시오.$B$B먼저 남동쪽에 있는 대장 브랜든부터 만나보십시오. 임무를 마치면 그가 다음 초소의 위치를 가르쳐줄 겁니다.$B$B초소 네 군데를 다 돈 다음에 다시 내게 보고하십시오. 빨리 돌아올수록 좋은 보상을 주겠습니다. 그러니 속도를 내세요!$B$B이제 가도 좋습니다!', },
    {'은빛십자군 격전지에 있는 사령관 쿤즈가 은빛십자군 초소를 방문해 각 대장이 주는 임무를 마치고 돌아와서 보고해 달라고 했습니다.$B$B대장 브랜든과 대장 루퍼트는 드락소트라에, 대장 그론델은 드락아갈에, 연금술사 핑클스타인은 헤브발로크에 있습니다.', },
  },
  [12502] = {
    '트롤 순찰: 깃발을 높이 올려라',
    {'잘 들으십시오. 우리는 은빛십자군입니다. 우리 병사들은 마지막까지 싸우고, 상황이 어떻든 불평 따위 하지 않지요.$B$B하지만 이번 군사 작전에서는 희생자가 대단히 많이 났고, 우리는 몇 달째 잠을 거의 못 자고 있습니다. 인정하고 싶지는 않지만 그 때문에 사기가 많이 떨어졌지요.$B$B크게 효과는 없을지 몰라도, 어쨌거나 우리 군기가 전장에 휘날리는 걸 본다면 병사들이 위안을 좀 얻을 것 같습니다. 이 깃발을 가져가서 드락소트라의 단상에 높이 올리십시오.', },
    {'대장 브랜든이 은빛십자군 깃발을 드락소트라의 드라카리 단상에 꽂아 달라고 했습니다.', },
  },
  [12503] = {
    '언덕을 사수하라',
    {'비록 줄드락 아래쪽 3분의 1에 달하는 지역에서 성공을 거두었지만, 우리는 여전히 스컬지의 맹공을 버텨내야 하네. 이 얼어붙은 지옥에서는 모든 일이 이런 식이지.$B$B우리 병사들도 좀 쉬어야 할 텐데 공격이 끊이지를 않는군. 괜찮다면 자네가 가서 병사들을 도와줬으면 하네.$B$B아래쪽 마당에서 벌어지는 싸움이 끝날 기미가 보이지 않는다네. 온 힘을 다해서 그곳의 스컬지를 처치해 주게.', },
    {'사령관 팔스타브가 은빛십자군 격전지 마당에서 종류에 관계없이 스컬지 10마리를 처치하라고 했습니다.', },
  },
  [12504] = {
    '은빛십자군, 여길 떠난다!',
    nil,
    {'세라투스의 제단에 있는 하사관 스택해머가 자신에게 보고하라는 명령을 은빛십자군 병사 10명에게 전해 달라고 했습니다.', },
  },
  [12505] = {
    '하사관 스택해머 만나기',
    {'당신, 우릴 도와주러 온 거 아닙니까? 그렇다면 북쪽에 있는 드라카리 제단에 파견한 부대에 새 명령을 전달해 주십시오.$B$B아주 찾기 쉬운 곳입니다. 큰길을 따라 곧장 가기만 하면 별 어려움 없이 하사관 스택해머를 찾을 수 있을 겁니다.', },
    {'사령관 쿤즈가 새 배치 명령서를 세라투스의 제단에 있는 하사관 스택해머에게 전달해 달라고 했습니다.', },
  },
  [12506] = {
    '세라투스 제단의 문제',
    {'뱀 신의 제단에서 문제가 발생했네.$B$B스컬지의 존재가 대지를 약화시켰다네. 그 때문에 야생의 신들도 약해져 버렸지.$B$B드라카리는 이것을 기회 삼아 신들에게 부정한 짓을 저지르고 있네.$B$B내 눈과 귀가 되어 주게. 세라투스의 제단은 북쪽으로 뚫린 큰길 끝에 있다네. 뒤로 몰래 돌아가서 중앙 건물 안을 살펴보게.$B$B뭘 하든지 간에 예언자는 최대한 피하게!', },
    {'은빛십자군 격전지에 있는 사술사 우분고가 세라투스의 제단의 중앙 건물 안을 조사해 달라고 했습니다.', },
  },
  [12507] = {
    '기묘한 모조',
    {'기묘한 모조가 담긴 이 병을 통해, 이곳에서 진정 어떤 일이 벌어지는지 알 수 있을지도 모릅니다.$B$B은빛십자군 격전지로 돌아가 잔달라 트롤인 사술사 우분고에게 이 병을 보여주는 것이 좋을 것 같습니다.', },
    {'기묘한 모조를 은빛십자군 격전지에 있는 사술사 우분고에게 전달해야 합니다.', },
  },
  [12508] = {
    '추종자 처단',
    {'자네와 스택해머 하사관이 하는 이야기를 나도 들었네. 격전지로 돌아오라는 명령을 받았다고 우리가 이곳에서 할 일이 끝나는 건 아니지.$B$B저기 나가 있는 동안 세라투스의 추종자 녀석들을 처치해 우릴 좀 도와줄 수 있겠나?', },
    {'세라투스의 제단에 있는 하사관 마가가 세라투스의 용사, 세라투스의 사제, 드라카리 뱀 조련사를 종류에 상관없이 총 10명 처치해 달라고 했습니다.', },
  },
  [12509] = {
    '트롤 순찰: 배짱 키우기',
    {'마침내 순찰대원이 와 줬군!$B$B상황은 이렇다네, $R. 우리는 이 전투에서 유능한 병사를 너무나도 많이 잃었고, 새로 모집한 보충병으로 그들의 빈자리를 채워야만 했지.$B$B방금도 치열한 전투가 한 판 벌어졌는데, 보충병들 대부분은 아직 적을 상대할 준비가 되지 않았더군. 앞으로도 더 많은 전투가 벌어질 테니, 그들은 명령을 따를 자세가 되어 있어야만 해.$B$B내가 눈 뜨고 못 보는 게 있다면 그건 바로 겁쟁이일세. 어쩌면 자네가 그들에게 용기를 불어넣어 줄 수 있을지도 모르겠군.', },
    {'드락소트라에 있는 대장 루퍼트가 겁먹은 십자군 보충병에게 용기를 북돋워 달라고 했습니다.', },
  },
  [12510] = {
    '고귀한 정령의 영액',
    {'<모조가 담긴 병을 살펴보는 사술사>$B$B세라투스의 예언자를 상대할 방법을 생각해 봤네. 하지만 먼저 재료가 몇 가지 필요해.$B$B바로 남쪽에 드락소트라 농장이 있네. 스컬지의 침입과 신들의 죽음이 대지에 영향을 미쳐, 차원을 나누는 벽이 약해지고 정령이 이쪽 세계로 쏟아져 오고 있지.$B$B그들의 군주에게서 뽑아낸 고귀한 영액이 필요해. 정령을 처치하고 얻는 고리를 세 개 합쳐 물세계 연결 사슬을 만들면, 그들의 군주 중 하나를 불러낼 수 있을 걸세.', },
    {'은빛십자군 격전지에 있는 사술사 우분고가 고귀한 정령의 영액을 가져다 달라고 했습니다.', },
  },
  [12511] = {
    '구릉지의 난관',
    nil,
    nil,
  },
  [12512] = {
    '죽어도 함께 죽는다',
    {'아, 좋습니다. 드디어 누군가 왔군요.$B$B잘 들으십시오, $R. 우린 지금 도움이 필요합니다. 우리 부대는 계속 후퇴했고, 드락소트라는 스컬지에게 빼앗기고 말았지요. 놈들이 어디서 왔는지는 아무도 모릅니다...$B$B문제는, 없는 사람도 긁어모아야 하는 이 판국에 우리 최정예 병사 몇몇이 아직도 전장에서 돌아오지 못했다는 겁니다.$B$B전투가 조금 잦아든 것 같으니 저 폐허로 가서 아군 부상자를 찾아 치료한 다음 제게 데려와 주십시오.$B$B드락소트라를 조사해 보십시오! 그들은 그리 멀리 가지 못했을 겁니다.', },
    {'드락소트라에 있는 박사 로저스가 성전사 라무프, 성전사 조나단, 성전사 조세핀을 찾아, 성전사의 붕대를 사용한 다음 그들을 안전하게 호위해 돌아와 달라고 했습니다.', },
  },
  [12513] = {
    '멋진 모자군요...',
    {'내 모자 말이오? 뭐, 좀 독특하게 생겼다는 건 나도 안다오... 물 건너온 물건이거든... 이걸 만들 수 있는 재봉사는 별로 없다오.$B$B피 끓던 젊은 시절에는 도전의 상징으로 이 모자를 썼었소. 많은 사람들이 이 모자를 빼앗으려고 내게 도전했지만... 아무도 날 이기지 못했소.$B$B아아, 뭐 다 옛날이야기라오. 난 평범한 늙은이라오. 그저 술이나 마시고 낚시하며 세월을 보낼 뿐, 더는 싸우지 않는다오.', },
    {'돈 카를로스가 무심결에 옛 언덕마루에 있는 젊은 돈 카를로스를 이겨보라고 제안했습니다. 싸움에 이긴 증거로 돈 카를로스의 모자를 타나리스에 있는 그에게 가져가야 합니다.', },
  },
  [12514] = {
    '버섯 섞기',
    {'이번에는 모조를 억누를 수 있는 뭔가가 필요하네. 예언자가 마시면 오히려 힘이 약해지게 할 수 있는 무언가가 말이지.$B$B동쪽에 드락아갈이 있네. 그곳에 가면 원주민들이 어리버섯이라 부르는 버섯이 있지. 지금 우리에게 필요한 게 바로 그 버섯일세.$B$B버섯을 잔뜩 가져와 주게. 버섯을 모조와 섞으면, 그걸 마시는 자는 정신이 혼탁해질 걸세.', },
    {'어리버섯 10개를 모아 은빛십자군 격전지에 있는 사술사 우분고에게 가져가야 합니다.', },
  },
  [12515] = {
    '멋진 모자군요...',
    {'내 모자 말이오? 뭐, 좀 독특하게 생겼다는 건 나도 안다오... 물 건너온 물건이거든... 이걸 만들 수 있는 재봉사는 별로 없다오.$B$B피 끓던 젊은 시절에는 도전의 상징으로 이 모자를 썼었소. 많은 사람들이 이 모자를 빼앗으려고 내게 도전했지만... 아무도 날 이기지 못했소.$B$B아아, 뭐 다 옛날이야기라오. 난 평범한 늙은이라오. 그저 술이나 마시고 낚시하며 세월을 보낼 뿐, 더는 싸우지 않는다오.', },
    {'돈 카를로스가 무심결에 옛 언덕마루에 있는 젊은 돈 카를로스를 이겨보라고 제안했습니다. 싸움에 이긴 증거로 돈 카를로스의 모자를 타나리스에 있는 그에게 가져가야 합니다.', },
  },
  [12516] = {
    '과유불급',
    {'이제 됐네. 모조를 좀 손봤지. 예언자가 이 모조를 마시면 그는 힘을 잃을 뿐만 아니라 버섯의 영향으로 정신마저 혼탁해질 걸세.$B$B적어도 이론상으로는 말일세.$B$B그에게 어떻게 먹이느냐가 문제인데, 한번 그에게 던져 보는 건 어떻겠나? 녀석이 제단에서 보인 불손함을 볼 때, 아마도 모조를 탐욕스럽게 잡아채서 마셔버릴 게 분명해.$B$B자네가 녀석을 처치하지 못한다면 은빛십자군은 처참한 최후를 맞게 될 걸세.', },
    {'혼란스러운 세라투스의 예언자를 처치한 다음 은빛십자군 격전지에 있는 사술사 우분고에게 가야 합니다.', },
  },
  [12517] = {
    '도적 카드 한 벌',
    {'도적 카드를 모두 모아 한 벌이 완성되자, 가장 위에 또 하나의 카드가 나타납니다. 카드를 어떻게 들든, 그 위에 그려진 점술사가 당신을 뚫어지게 쳐다보는 것 같습니다.$B$B왠지 카드를 사용하면 누군가와, 혹은 무언가와 이야기할 수 있을 것 같다는 기분이 듭니다.', },
    {'도적 카드 한 벌로 다크문 점술사를 소환한 다음 카드를 줘야 합니다.', },
  },
  [12518] = {
    '마법사 카드 한 벌',
    {'마법사 카드를 모두 모아 한 벌이 완성되자, 가장 위에 또 하나의 카드가 나타납니다. 카드를 어떻게 들든, 그 위에 그려진 점술사가 당신을 뚫어지게 쳐다보는 것 같습니다.$B$B왠지 카드를 사용하면 누군가와, 혹은 무언가와 이야기할 수 있을 것 같다는 기분이 듭니다.', },
    {'마법사 카드 한 벌로 다크문 점술사를 소환한 다음 카드를 줘야 합니다.', },
  },
  [12519] = {
    '트롤 순찰: 머릿수 세기',
    {'날 좀 보게, $R. 내가 보고서 따위나 작성할 만큼 한가해 보이나?$B$B빌어먹을 스컬지가 이 기지에 다가오지 못하게 하는 것만으로도 벅찬데, 이제 서류 작업까지 시키려 들다니!$B$B<한숨> 불평해봤자 소용없지. 사령관님께 지난번 전투에서 처치한 놈들 머릿수를 보고해야 하네.$B$B도와줄 수 있겠나? 주변에 있는 죽은 트롤에게서 드라카리 메달을 구해오게. 그러면 처치한 놈들 머릿수를 대강 알 수 있겠지...', },
    {'고뇌의 투기장 바깥에 있는 대장 그론델이 주변의 드라카리 시체에서 드라카리 메달 7개를 가져다 달라고 했습니다.', },
  },
  [12520] = {
    '코뿔소 사냥법 익히기: 시험',
    {'그 무엇도 이 헤멧이 숄라자르 최대의 사냥감을 잡는 일을 막지는 못하오. 야영지 북서쪽의 야생 지대는 수정뿔 코뿔소가 지배하는 곳이라오. 그 막강한 짐승들을 상대로 당신의 실력을 증명한다면 우리 원정대에 필요한 인물로 인정해 주겠소.', },
    {'수정뿔 코뿔소 15마리를 처치한 다음 네싱워리 주둔지에 있는 헤멧 네싱워리에게 가야 합니다.', },
  },
  [12521] = {
    '헤멧 네싱워리의 행방',
    {'그 전설적인 헤멧 네싱워리가 숄라자르 분지로 떠난다는 말을 들었을 때 전 그의 원정대에 자금을 지원했습니다. 숄라자르에 대해서는 별로 알려진 게 없지만, 얼어붙은 이 대륙 가운데에 있는 푸른 낙원이라고 하더군요.$B$B헤멧은 외래 식물, 시약, 수정 등의 견본을 발견하는 대로 제게 보내기로 약속했었습니다. 그런데 헤멧에게서 아무 소식도 없지 뭡니까. 몬테 머즐샷이 그들에게 보급품을 받아오기로 되어 있는데 그 친구도 돌아오지 않았습니다. 조사해보고 싶으면, 제 비행기로 당신을 숄라자르로 보내드리지요.', },
    {'숄라자르 분지로 떠날 준비가 되면 대마법사 펜타루스와 대화하십시오. 도착하면 숄라자르 분지의 야생수풀 늪지대에 있는 몬테 머즐샷과 대화해야 합니다.', },
  },
  [12522] = {
    '엔진이 필요하면 가지리라',
    {'비행기가 망가졌어요... 완전히 망가졌다고요! 선장에게 귀중한 비행기이니 조심해 달라고 얘기했었지만, 솔직히 그 여자가 제 말을 듣겠어요? 천만에요!$B$B배가 좌초하면서 제 비행기인 놈리건의 정신호가 망가졌어요. 덕분에 이제 보고서를 보낼 수도 없는 처지라고요!$B$B하지만 아직 희망은 있어요. 어쩌면 투자개발회사의 물건을 써먹을 수 있을지도 몰라요. 몬테가 착륙한 동쪽 늪지대 근처에서 그자들이 다른 비행기를 격추했다고 하더군요. 그곳에 가서 엔진을 가져다주세요. 그게 우리의 마지막 희망일지도 몰라요.', },
    {'네싱워리 주둔지에 있는 웨슬렉스 퀵렌치가 비행기 엔진을 가져다 달라고 했습니다.', },
  },
  [12523] = {
    '부품을 구하면 주리라',
    {'좋아요. 엔진을 구했으니 이제 다른 부품들만 있으면 비행기를 고쳐서 다시 날 수 있을 거예요. 걱정하지 마세요. 어떻게 해야 하는지는 아니까요.$B$B모름지기 좋은 기술자는 재료가 어떻든 가리지 않고 조립해야 하기도 하지만, 우린 그 지경은 아니니 다행 아닌가요.$B$B어쨌거나 그 투자개발회사의 멍청이들은 채광 장비에 쓸 예비 부품을 스윈들그린의 채굴현장에 보관하고 있어요. 북동쪽의 저 노란 수정 기둥 반대편에 있는 곳이죠. 그곳에 가서 예비 부품을 몇 개 가져다주세요.', },
    {'네싱워리 주둔지에 있는 웨슬렉스 퀵렌치에게 투자개발회사 예비 부품 7개를 가져가야 합니다.', },
  },
  [12524] = {
    '투자개발회사의 불운',
    {'우리가 이곳에 온 뒤로 투자개발회사는 우리가 야영지를 확보하고 주변 지역을 조사하지 못하게 줄곧 방해하고 있답니다. 평소 같으면 그들이 뭘 하든 상관하지 않았겠지만, 그자들이 우리 정찰병들을 적으로 돌리고 수송기들을 격추하는 상황입니다.$B$B이런 모욕을 그냥 넘길 수는 없습니다. 헤멧 네싱워리의 원정대는 사냥하러 이곳에 왔으니 반드시 사냥을 해야 됩니다.$B$B북동쪽의 수정 기둥 반대편에 있는 스윈들그린의 채굴현장에 가서 투자개발회사 직원들이 보이는 대로 처치해 주시기 바랍니다!', },
    {'네싱워리 주둔지에 있는 데바르가 투자개발회사 무법자나 발굴인부 15명을 처치해 달라고 했습니다.', },
  },
  [12525] = {
    '스윈들그린의 최후',
    {'저들의 광산을 완전히 폐쇄하려면 먼저 현장감독 스윈들그린을 쫓아버려야 합니다. 하지만 조심하세요. 그의 거대한 오우거 경호원 덩치 큰 아이와 싸우지 않고서는 현장감독을 만나지 못할 테니까요.$B$B그 둘은 아마 투자개발회사 스윈들그린의 채굴현장이 내려다보이는 발판에 있을 겁니다.$B$B채굴현장은 북동쪽의 해마루 봉우리 반대편에 있으니 잊지 마세요. 행운을 빌겠습니다, $N.', },
    {'현장감독 스윈들그린과 덩치 큰 아이를 처치한 다음 네싱워리 주둔지에 있는 데바르에게 가야 합니다.', },
  },
  [12526] = {
    '코뿔소 사냥법 익히기: 추적',
    {'그럼, 실력 문제는 해결된 셈치고 이제 이곳 토박이 현자를 찾아가 얘기해 봐야겠군. 하지만 그렇게 선뜻 도와주겠다고는 하지 않을 것이오.$B$B바로 남쪽의 강을 건너면 나오는 노즈론의 무덤 근처에 조그마한 움막을 짓고 산다고 들었소. 그를 찾아서 자기소개를 하고 어떻게 하면 코뿔소 우두머리를 찾도록 도와줄 건지 알아보시오.', },
    {'노즈론의 무덤에서 점쟁이 수람과 대화해야 합니다.', },
  },
  [12527] = {
    '먹보 바실리스크',
    {'제 스승이신 핑클스타인 님은 북서쪽에 있는 헤브발로크에서 진실의 물약을 만들 준비를 하고 계세요. 그 물약을 만들려면 바실리스크 수정이 좀 필요하지요. 북쪽으로 길을 따라가다 보면, 세라투스의 제단에 도착하기 전에 길 서쪽에서 헤브발로크를 발견하실 수 있을 거예요.$B$B바실리스크는 동쪽의 드락아갈에 많답니다.$B$B줄드락 쥐를 잡아 바실리스크에게 던져 주세요. 다섯 마리를 먹고 나면 녀석들은 배가 불러서 얌전해질 테니, 수정을 안전하게 얻을 수 있을 겁니다.$B$B바실리스크를 죽이는 건 좋은 방법이 아니에요... 수정이 깨지고 만다고요.', },
    {'바실리스크 수정 5개를 헤브발로크의 제단에 있는 연금술사 핑클스타인에게 전달해야 합니다.', },
  },
  [12528] = {
    '새로운 관계',
    {'내 사냥감! 네놈이 내가 찜해 놓은 사냥감을 훔쳤어!$B$B<창을 겨누며 욕설을 퍼붓는 게크게크>$B$B멍청한 $R 같으니. 이 게크게크한테 실수했어! 이제 넌 광란심장 일족의 노예야. 광란심장 일족을 위해 짐승들을 처치해.$B$B북동쪽에 있는 광란심장 언덕으로 가서 대주술사 라크자크 님께 네가 새로 온 노예라고 말씀드려.$B$B어서 가, $R! 가지 않으면 다시 욕을 퍼부어 줄 거야!$B$B<위협하듯 창을 휘두르는 게크게크>', },
    {'광란심장 언덕에 있는 대주술사 라크자크에게 새 "노예" 신분으로서 보고해야 합니다.', },
  },
  [12529] = {
    '고릴라 사냥꾼의 노예',
    {'스스로 대단한 사냥꾼이라고 생각하나 보지? 너는 광란심장 일족 아니다. 사냥이 뭔지 모른다.$B$B고어게크야말로 위대한 고릴라 사냥꾼이다. 사냥한 고릴라가 워낙 많아 여덟 발톱으로는 다 꼽을 수도 없다.$B$B이제 고어게크랑 동쪽으로 가서 바위돌기고릴라 사냥해라. 너 노예다, $R. 고어게크가 하라는 대로 해라.', },
    {'굶주린 바위돌기고릴라 8마리와 사나운 바위돌기고릴라 6마리를 처치하고 광란심장 언덕에 있는 대주술사 라크자크에게 가야 합니다.$B$B고어게크의 족쇄를 잃어버리면 광란심장 언덕에 있는 장로 하르케크와 이야기하십시오.', },
  },
  [12530] = {
    '비단돌기고릴라 괴롭히기',
    {'노예!$B$B라크자크 님께서 바위돌기고릴라를 사냥하면서 널 돌봐주라고 하셨다. 오늘은 내가 기분이 좋으니 너도 몇 마리 잡게 해주겠지만, 이 고어게크가 사냥하는 데 거치적거리지는 말아라!$B$B라크자크 님께서는 아직 모르시지만, 이 고어게크는 크고 못된 바위돌기고릴라들의 어미를 점찍어 두었다. 동쪽에 있는 바위돌기고릴라 폐허로 가서 꼬챙이로 비단돌기고릴라를 좀 찔러 대면, 그 어미가 당장 꽥꽥대면서 나타날 거다!$B$B<즐거운 듯 손을 비벼대는 고어게크>', },
    {'고어게크가 비단돌기고릴라 꼬챙이로 비단돌기고릴라를 찔러 숨어 있는 우두머리 바위돌기고릴라를 유인한 다음 처치하라고 했습니다. 일을 처리하고 난 다음 광란심장 언덕에 있는 대주술사 라크자크에게 보고해야 합니다.$B$B고어게크의 족쇄를 잃어버리면 광란심장 언덕의 장로 하르케크와 이야기하면 됩니다.', },
  },
  [12531] = {
    '땅속의 위협',
    {'사자를 죽이고 닭을 쫓더니 이제는 광란심장 일족의 새 노예가 되었구나.$B$B네가 노예로 있고 싶어하지 않다는 걸 안다. 어떻게 하면 좋을지 가르쳐 주겠다. 네가 다른 광란심장 일족 사냥꾼보다 낫다는 사실을 증명해 보이면 라크자크 님도 너를 인정하실 거다.$B$B남쪽 강의 모래밭 아래에는 말벌, 고릴라, 심지어 울바르까지 죽이는 짐승이 하나 있는데, 바로 서펙스라고 하는 놈이다. 서펙스의 발톱을 라크자크 님께 가져가면 널 노예신분에서 해방해 주실 테지.$B$B서펙스를 쉽게 잡을 수 있도록 다지크가 잘 가르쳐 줬으면 좋겠다.', },
    {'남쪽 강 모래밭 아래에 있는 서펙스를 추적해서 처치하고, 그 발톱을 광란심장 언덕에 있는 대주술사 라크자크에게 가져가야 합니다.$B$B다지크의 닳은 분필을 잃어버리면 광란심장 언덕에 있는 장로 하르케크과 이야기하십시오.', },
  },
  [12532] = {
    '닭 잡기!',
    {'멍청한 고어게크 녀석이 닭장에 걸려 자빠지는 바람에 닭들이 모조리 도망쳤다!$B$B닭은 언덕 주변 여기저기로 흩어졌다. 가서 다시 잡아와라!$B$B<가보라고 손짓하는 장로 하르케크>', },
    {'도망친 닭 12마리를 잡아 장로 하르케크에게 가야 합니다.$B$B다지크의 닳은 분필을 잃어버리면 광란심장 언덕에 있는 장로 하르케크와 이야기하면 됩니다.', },
  },
  [12533] = {
    '말벌 사냥꾼의 수습생',
    {'라크자크는 바보 아니다. 넌 죽이는 데 소질 있다. 어쩌면 광란심장 일족이 널 진짜 사냥꾼으로 만들어줄지도 모르겠다.$B$B다지크는 울바르 중에서 거대한 말벌을 제일 잘 잡는다. 다지크를 따라 남동쪽에 있는 사파이어 부화장이나 남쪽에 있는 강으로 가서 사냥하는 법 배워라.', },
    {'사파이어 부화장 말벌 6마리와 사파이어 부화장 일꾼 9마리를 제거한 다음 광란심장 언덕에 있는 대주술사 라크자크에게 돌아와야 합니다.$B$B다지크의 닳은 분필을 잃어버리면 광란심장 언덕의 장로 하르케크와 대화하면 됩니다.', },
  },
  [12534] = {
    '사파이어 여왕',
    {'가시털멧돼지는 위험하지만 대개 울바르와 멀리 떨어져 산다. 반면에 말벌은 광란심장 일족을 두려워하지 않는다... 우리 사냥꾼을 죽이기까지 할 정도니.$B$B라크자크는 여왕벌을 죽이고 싶어하지만, 부화장으로 들어가려는 울바르가 없다. 남동쪽에 있는 사파이어 부화장으로 가서 여왕을 죽이고 독침을 라크자크에게 가져다줘라. 라크자크 좋아할 거다.', },
    {'사파이어 여왕의 독침을 광란심장 언덕에 있는 대주술사 라크자크에게 가져가야 합니다.$B$B다지크의 닳은 분필을 잃어버리면 광란심장 언덕의 장로 하르케크와 이야기하십시오.', },
  },
  [12535] = {
    '수정 장난',
    {'넌 유능한 사냥꾼이지만 광란심장 일족이 더 강하게 만들어줄 수 있다. 하르케크는 네가 더 중요한 것을 맡을 준비가 돼 있다고 생각한다.$B$B라크자크가 더 중요한 임무를 맡기기 전에 몇 가지 물건을 라크자크에게 가져와야 한다. 남쪽 강가에는 푸른 수정이 있다. 라크자크는 푸른 수정 많이 필요하다.$B$B당장 가서 수정 가져와라.', },
    {'광란심장 언덕에 있는 대주술사 라크자크가 하늘길 수정 조각 8개를 가져와 달라고 했습니다.', },
  },
  [12536] = {
    '울퉁불퉁한 등에 올라',
    {'제피크는 광란심장 일족 최고 사냥꾼이다. 제피크는 날름이를 사냥하지... 늪지대 근처에 살면서 무엇이든 훔치려 드는 추악한 놈들 말이다.$B$B제피크는 멀리 돌아다니며 날름이 제단을 파괴하고 놈들을 쫓아 보내려 한다. 우린 날름이가 이 땅에서 사라졌으면 한다.$B$B언덕 북동쪽에 있는 나무에 악어가 매여 있다. 제피크와 함께 가라. 그리고 제피크가 시키는 대로 해라.', },
    {'사로잡힌 악어를 타고 안개속삭임 야영지로 간 다음 제피크와 대화하여 지시를 받아야 합니다.$B$B제피크의 사냥용 뿔피리를 잃어버리면 광란심장 언덕에 있는 장로 하르케크와 이야기하십시오.', },
  },
  [12537] = {
    '벼락은 반드시 두 번 친다',
    {'좋아, 수정이라는 거지...$B$B날름이들은 수정을 사용해 놈들의 비 제단에 힘을 넣어. 제피크 그 원리 잘 몰라. 하지만, 수정을 잘못 사용하면 구름과 비가 완전히 난리 난다는 건 알아!$B$B제피크는 이전에도 다른 비 제단에 살짝 들어가려고 했는데 잘 안 됐어. 두 명이면 훨씬 잘될 거야!$B$B수정을 가져가서 커다란 돌기둥 제단인가에 놓고 그 힘이 방출되도록 깨트려. 무슨 일이 일어날지는 제피크도 잘 몰라. 그러니까 수정을 깨트리고 나서 조금 떨어져 있는 게 좋아.', },
    {'제피크가 하늘길 수정 조각을 안개속삭임 기우 제단 앞에서 깨트려 달라고 했습니다.$B$B제피크의 사냥용 뿔피리를 잃어버리면 광란심장 언덕에 있는 장로 하르케크와 대화해야 합니다.', },
  },
  [12538] = {
    '즐거운 안개속삭임 골록 사냥',
    {'고릴라랑 말벌은 시시해! 날름이 사냥할 때만 재미있어.$B$B비 제단에서 장난을 좀 치는 동안 안개 날름이들을 많이 죽여야 해. 그러면 놈들도 함부로 마을까지 오지 못할 거야.$B$B<자신 있게 끄덕이는 제피크>', },
    {'제피크가 안개속삭임 골록 12마리를 처치해 달라고 했습니다.$B$B제피크의 사냥용 뿔피리를 잃어버리면 광란심장 언덕에 있는 장로 하르케크와 대화해야 합니다.', },
  },
  [12539] = {
    '이번엔 걸어서',
    {'좋아, 끝났어! 이제 돌아가야지. 하지만, 이번엔 타고 돌아갈 악어가 없어.$B$B동쪽으로 가서 산마루 근처를 돌다 보면 언덕 근처로 갈 수 있어.$B$B산등성이 아래로 너무 멀리까지 가지 않도록 조심해. 안 그러면 숲 속에 있는 날름이 나무 마을 같은 데로 걸어 들어갈 수도 있어. 제피크 생각에 놈들은 지금 우릴 좋아하지 않을 거야.', },
    {'길을 찾아 광란심장 언덕으로 돌아간 다음 대주술사 라크자크와 대화해야 합니다.', },
  },
  [12540] = {
    '명령 복종',
    {'좋아, 그럼 라크자크가 아주 중요한 임무를 맡기겠다. 추적자들이 북쪽 강에서 상처입은 날름이를 발견했으니 가서 잡아와라.$B$B그냥 죽여버릴지도 모르니 이번에는 사냥꾼을 데려가지 마라. 그놈을 숨겨 두고 정보를 말할 때까지 좀 찔러볼 생각이다.$B$B가서 그 날름이를 데리고 와라.', },
    nil,
  },
  [12541] = {
    '트롤 순찰: 연금술사의 제자',
    nil,
    {'헤브발로크에 있는 연금술사 핑클스타인이 진실의 물약 제조를 도와달라고 했습니다.$B$B시작할 준비가 되면 그에게 말을 걸고 지시를 따라야 합니다.', },
  },
  [12542] = {
    '은빛십자군의 부름',
    nil,
    nil,
  },
  [12543] = {
    '수람을 위한 공물',
    {'우리는 자네를 모르네, 이방인이여. 하지만 자네가 뭔가를 원한다는 건 안다네.$B$B자네를 위해 노즈론의 무덤에 도움을 청해는 보겠네만, 그러려면 우리에게 공물을 가져와야 한다네. 예언자의 목걸이를 완성하려면 강한 황제 코브라의 송곳니가 필요하지.$B$B우리 움막의 북동쪽에 있는 야생수풀 늪지대에서 코브라를 찾아 최고급 송곳니를 가져오게. 그러면 뼈가 자네에게 말하도록 우리가 돕겠네.', },
    {'온전한 코브라 송곳니 5개를 노즈론의 무덤 근처에 있는 점쟁이 수람에게 가져가야 합니다.', },
  },
  [12544] = {
    '노즈론의 무덤',
    {'노즈론이시여, 이 점쟁이 수람이 $N|1을;를; 당신께 보내나이다!$B$B자네를 위해 향을 준비해 두었네. 향에 불을 붙여 노즈론 님의 머리 근처에 놓으면 그분의 목소리가 들릴 것이네.$B$B<당신은 어리둥절해하며 수람에게 아직 질문이 뭔지 말하지도 않았다고 전합니다.>$B$B그렇네. 우리는 알지 못하네. 하지만 위대한 노즈론 님은 아신다네. 의심을 품지 말게. 노즈론 님께서는 자네 마음속 그 질문에 답해주실 걸세.$B$B가게나. 노즈론 님은 오래 기다려주시지 않을 걸세.', },
    {'노즈론의 무덤 근처에서 수람의 향료를 사용하여 정보를 얻은 다음 네싱워리 주둔지에 있는 헤멧 네싱워리에게 가야 합니다.', },
  },
  [12545] = {
    '진타칼라르 정화',
    {'은빛십자군은 밀어닥치는 스컬지 무리에게서 이 고대 드라카리 사원을 어렵사리 손에 넣었습니다. 우리가 있는 곳에서부터 백 걸음도 미치지 않는 곳이 폐허가 된 진타칼라르 도시입니다. 스컬지 강령술사들은 살아있는 드라카리 트롤을 리치 왕의 하수인으로 변모시키며 미친 듯이 날뛰고 있지요.$B$B이제 모두가 빛으로의 길을 잃었습니다. 모두가 정화되어야 합니다.$B$B<단호하게 당신을 바라보는 발루스>$B$B모두가요.', },
    {'용의 안식처, 믿음의 빛 거점에 있는 성전사 발루스가 진타칼라르 스컬지 15마리를 처치해 달라고 했습니다.', },
  },
  [12546] = {
    '길들이기',
    {'때가 되었다, $N|1이여;여;. 에티미디안은 티탄이 창조한 생명체 중에서 가장 파괴적인 존재 중 하나라 우리 땅에 불러내기가 너무나도 끔찍하다.$B$B그러나 선택의 여지가 없구나. 원하지는 않지만 적이 들어왔으니 어쩔 수 없다. 티탄의 분노로 적을 모두 쓸어버리리라.', },
    {'숄라자르 분지에 있는 눈사태 언덕에서 최후의 룬을 사용해 에티미디안을 불러내야 합니다. 에티미디안을 이용해 스컬지 부하 200마리, 살덩이형성자 비티우스, 그리고 대학살자 우르그레스와 냉소의 서리고룡을 처치하십시오.$B$B임무를 마치면 숄라자르 분지에 있는 프레이야의 화신과 대화해야 합니다.', },
  },
  [12547] = {
    '활성의 룬',
    {'이 거대한 물체는 작동하지 않는 것 같습니다. 가슴 언저리에 룬 모양으로 난 구멍을 발견했습니다.$B$B어쩌면 근처 전망대 안에서 잃어버린 룬을 찾을 수 있을지도 모릅니다.', },
    {'구체자의 정원 안에서 최후의 룬을 구해 에티미디안에게 가져가야 합니다.', },
  },
  [12548] = {
    '에티미디안',
    {'때가 되었구나, $N. 일곱 세기 만에 처음으로 차원문이 가동하였다. 그 문을 통해서 우리의 마지막 희망이 있는 곳으로 가거라.$B$B이 순수한 대지에 언데드의 타락을 몰고 오려는 자들은 사라져야 한다. 에티미디안을 풀어놓도록 하자.', },
    {'숄라자르 분지의 남쪽 끝에 있는 차원문으로 들어가 반대편에서 에티미디안으로 알려진 무기를 찾아야 합니다.', },
  },
  [12549] = {
    '공포퓨마 사냥법 익히기: 포식자 되기',
    {'헤멧과 함께 사냥해본 적이 있다면 그가 여러 종류의 고양잇과를 상대로 자기 기량을 시험해 봤다는 걸 알 거요. 하지만 이곳 숄라자르에 서식하는 공포퓨마들은 다르다오. 놈들은 내가 지금까지 보아 온 그 어떤 고양잇과 녀석들보다도 훨씬 큰 사냥감을 처리할 수 있소이다. 그런 포식자를 제압하고 처치할 수 있다면 그야말로 진정한 사냥꾼이라는 증거라오.$B$B시련을 극복하고 자신을 증명하고자 한다면 공포퓨마들을 상대로 당신의 실력을 시험해 보시오. 놈들은 야영지 주변과 강 남쪽에 서식한다오.', },
    {'공포퓨마 15마리를 처치한 다음 네싱워리 주둔지에 있는 버크 캔웰에게 가야 합니다.', },
  },
  [12550] = {
    '공포퓨마 사냥법 익히기: 먹잇감 추적',
    {'원주민들이 샨고라고 하는 거대한 공포퓨마에 대해 이야기하는 걸 들은 적이 있소. 이 샨고란 놈이 존재한다는 증거를 직접 본 적은 없지만, 상처받은 몸으로는 야영지를 벗어날 수가 없다오.$B$B당신이 추적자로서 실력에 자신이 있다면 샨고의 실체를 확인할 수 있을지도 모르오. 데바르의 말로는 지난번에 사냥을 나갔을 때 거대한 흔적을 몇 개 봤다고 하더군. 주둔지에서 북쪽 거센물결 호수 방향으로 가서 남쪽 호숫가에서 발자국을 찾아보시오.', },
    {'네싱워리 주둔지에 있는 버크 캔웰이 거센물결 호수 근처에 있는 공포퓨마 발자국 중에서 샨고의 발자국 3개를 찾아 달라고 했습니다.', },
  },
  [12551] = {
    '악어 사냥법 익히기: 시도',
    {'경고해 두겠어요, 이방인. 침몰한 배에 대해선 아무 말도 하지 마세요. 이미 다른 대원들에게서 들을 만큼 들었으니까요.$B$B하지만 사냥에 도전해 보고 싶다면 교활한 늪지대 악어들을 상대로 실력을 시험해 보세요. 놈들은 남쪽의 강가와 동쪽의 야생수풀 늪지대에서 서식한답니다. 혼자서 놈들을 상대할 수 있다는 사실을 증명해 보이면 그때 다시 이야기하도록 하지요.', },
    {'늪지대 악어 15마리를 처치한 다음 네싱워리 주둔지에 있는 데바르에게 가야 합니다.', },
  },
  [12552] = {
    '강령마법사에게 죽음을',
    {'트롤들이 격추한 콜라마스가 여기서 바로 남동쪽에 추락했습니다. 유감스럽게도 스컬지가 그 추락으로 다 죽은 건 아니랍니다.$B$B이제 놈들은 죽은 우리 병사들을 되살려 우리를 공격하게 하고 있습니다!$B$B그따위 짓을 계속하게 둘 수는 없지요. 가서 놈들의 강령마법사를 처단해 주십시오! 무슨 말인지 아시겠지요?', },
    {'드락소트라에 있는 하사관 문샤드가 하스아르 강령마법사 8마리를 처치해 달라고 했습니다.', },
  },
  [12553] = {
    '칼날거미 돌기',
    {'대체 왜 나는 늘 어려운 일만 떠맡는 걸까요?$B$B콜라마스의 스컬지 녀석들이 가진 역병 분사기가 우리에게 점점 다가오고 있잖아요. 역병 분사기가 여기 들이닥치면 어떻게 될지 상상도 하기 싫어요!$B$B내게 그것들을 부숴버릴 좋은 방법이 있어요. 하지만 재료를 좀 준비해야 한답니다... 놈들이 부리는 괴물에게서 구할 수 있지요.$B$B하스아르 칼날거미가 돌기에서 뽑아내는 거미줄은 굉장히 강력하답니다. 그걸 써먹을 수 있을 거예요. 온전한 돌기를 좀 구해 주세요.', },
    {'드락소트라에 있는 전문가 코그휠이 온전한 칼날거미 돌기 5개를 구해달라고 했습니다.', },
  },
  [12554] = {
    '타락자 말라스',
    nil,
    {'드락소트라에 있는 하사관 문샤드가 타락자의 머리를 가져다 달라고 했습니다.', },
  },
  [12555] = {
    '헝클어진 실',
    {'여기요! 얽힌 거미줄 뭉치 발사기가 완성됐으니, 이제 이걸로 저 역병 분사기를 마구 파괴하기만 하면 돼요.$B$B그냥 조준하고 쏘기만 하세요. 거미줄이 부유 마법을 엉망으로 만들어서 콜라마스가 그랬던 것처럼 추락하게 할 거예요.$B$B시적이군요. 상상만 해도 멋진데요!', },
    {'드락소트라에 있는 전문가 코그휠이 얽힌 거미줄 뭉치 발사기를 사용해 역병 분사기 5개를 파괴해 달라고 했습니다.', },
  },
  [12556] = {
    '코뿔소 사냥법 익히기: 처치',
    {'이제 파룬이 어디에 있는지 알아냈으니, 남은 일이라곤 그 거대한 짐승과 싸우는 것뿐이군. 코뿔소를 사냥할 준비가 됐소, $N? 난 분명히 그 짐승이 위험하다고 말했소이다.$B$B녀석을 상대할 준비가 되거든 북쪽의 해마루 봉우리를 지나 거센물결 호수의 북동쪽 해변에서 파룬을 찾아보시오. 어쩌면 녀석은 미명의 봉우리까지 돌아다닐 수도 있다오. 녀석을 잡은 전리품으로 내게 뿔을 가져오면 된다오.', },
    {'네싱워리 주둔지에 있는 헤멧 네싱워리에게 파룬의 뿔을 가져가야 합니다.', },
  },
  [12557] = {
    '연구실 작업',
    {'날 귀찮게 하는 걸 보니 자네도 내 제자 중 하나가 되고 싶은가 보군.$B$B꿈은 크게 가지는 게 좋긴 하지...$B$B내 제자가 되고 싶으면 먼저 내 연구실 어디에 뭐가 있는지 확실히 알아야 하네. 이곳엔 여러 재료가 갖춰져 있지. 넘치는 것도 있고, 부족한 것도 있다네.$B$B느긋하게 한 번 해보게나. 진흙투성이 늪 구더기, 말라버린 박쥐날개, 호박색 씨앗, 그리고 한기서린 뱀 점액을 찾아보게.$B$B제대로 찾아낸다면 자네 부탁을 진지하게 고려해 보겠네.', },
    {'헤브발로크에 있는 연금술사 핑클스타인이 자기 연구실에서 진흙투성이 늪 구더기, 말라버린 박쥐날개, 호박색 씨앗, 한기서린 뱀 점액을 찾아보라고 했습니다.', },
  },
  [12558] = {
    '공포퓨마 사냥법 익히기: 습격 준비',
    {'샨고가 기다리고 있소, $N. 제대로만 한다면 놈은 당신이 추적하는 걸 알아채지 못할 테니 그 기회를 잘 이용해야 할 거요.$B$B놈은 이 분지에 있는 다른 어떤 공포퓨마보다 크고 사납기로 유명하다오. 북쪽에 있는 거센물결 호수의 서쪽에 호수와 연결된 폭포가 있는데, 그 근처에서 샨고와 싸우도록 하시오. 성공한다면 놈의 모피를 가지고 돌아오시오.', },
    {'샨고의 모피를 네싱워리 주둔지에 있는 버크 캔웰에게 가져가야 합니다.', },
  },
  [12559] = {
    '관문 에너지 공급 - 창조주의 감시대',
    nil,
    nil,
  },
  [12560] = {
    '악어 사냥법 익히기: 계획',
    {'늪지대 악어는 매복에 아주 능하지요. 놈의 특기로 놈을 꺾어보려고 생각 중이랍니다. 그러려면 완벽한 위장이 필요하죠. 강둑과 호숫가의 모래밭에 자라는 식물을 사용할 생각이에요.$B$B모래고비는 이런 용도에 아주 적합하지요. 모래고비는 바로 남쪽에 있는 바닷길 여울, 북쪽의 거센물결 호수와 거센물결 폭포, 동쪽 늪지대 너머의 커다란 강의 심장부 호수 주변 모래밭에서 자라니 가서 가져오세요.', },
    {'네싱워리 주둔지에 있는 데바르에게 모래고비 5개를 가져가야 합니다.', },
  },
  [12561] = {
    '신용 문제',
    {'이런 일은 일어나지 말았어야 했다. 언데드 군대가 생명의 요람을 뒤흔들다니!$B$B리치 왕의 부하들을 물리치러 온 것이 아니라면 당장 떠나라. 내가 맡은 의무를 방해하여 분노를 사지 않도록 하라.', },
    {'잃어버린 땅에서 말라죽은 시체 6구와 해골사신 칼날발톱 10마리를 제거하여 프레이야의 신뢰를 얻어야 합니다.', },
  },
  [12562] = {
    '드라카리에게 물의 정령은 필요 없다!',
    {'정보에 따르면 드락소트라 농장에 물의 정령이 나타난 뒤로 수많은 주술사가 녀석들을 지배하러 줄드락 상층에서 내려왔다고 해요.$B$B얼마나 위험한 상황인지 아무리 강조해도 부족하군요. 물의 정령이 놈들 손아귀에 넘어가게 둬서는 안 돼요.$B$B사령관님께서 드라카리 물결속자를 없애라는 명령을 내리셨어요. 준비가 되거든 남쪽으로 가보세요.', },
    {'은빛십자군 격전지에 있는 부관대리 작스가 드라카리 물결속자 10명을 처치해 달라고 했습니다.', },
  },
  [12563] = {
    '트롤 순찰',
    {'무기를 뽑으십시오, $R. 순찰을 나갈 시간입니다!$B$B나는 네 군데 초소를 책임지고 있습니다. 이 초소들의 통제권을 유지하는 건 우리 작전의 성공과도 직결된 일이지요. 그러니 초소를 돌며 각 초소의 대장을 도와주십시오.$B$B먼저 남동쪽에 있는 대장 브랜든부터 만나보십시오. 임무를 마치면 그가 다음 초소의 위치를 가르쳐줄 겁니다.$B$B초소 네 군데를 다 돈 다음에 다시 내게 보고하십시오. 빨리 돌아올수록 좋은 보상을 주겠습니다. 그러니 속도를 내세요!$B$B이제 가도 좋습니다!', },
    {'은빛십자군 격전지에 있는 사령관 쿤즈가 은빛십자군 초소를 방문해 각 대장이 주는 임무를 마치고 돌아와서 보고해 달라고 했습니다.$B$B대장 브랜든과 대장 루퍼트는 드락소트라에, 대장 그론델은 드락아갈에, 연금술사 핑클스타인은 헤브발로크에 있습니다.', },
  },
  [12564] = {
    '트롤 순찰: 고통 경감',
    {'내 부하들은 매우 용감하지만, 그들 역시 뼈와 살로 이루어진 존재입니다.$B$B부상과 통증 때문에 임무를 수행할 능력이 많이 줄어들었지요. 아무리 훈련을 많이 받았다 한들 육체적인 한계를 벗어날 수는 없는 법입니다.$B$B다행히도 우리 연금술사 중 하나가 병사들의 고통을 대폭 줄여줄 수 있는 대단한 연고를 개발했습니다.$B$B이 연고를 만들려면 트롤이 이곳 서쪽에서 재배하는 특수한 양귀비가 필요합니다. 우리가 가진 걸 다 써버리기 전에 드락소트라 농장에 가서 그 양귀비를 좀 모아 오십시오.', },
    {'드락소트라에 있는 대장 브랜든이 드락소트라 농장에서 다 자란 물양귀비 5개를 가져다 달라고 했습니다.', },
  },
  [12565] = {
    '짐아브와의 축복',
    {'나는 은빛십자군이 열정적으로 스컬지에 맞서는 데 큰 감명을 받았네, $R. 자네들을 돕고 싶네.$B$B한때 거대했던 이 도시의 드라카리 트롤은 신들에게 주기적으로 공물을 바쳤지. 하지만 지금은 그것을 소홀히 한다네. 아니, 소홀히 하는 수준이 아니지.$B$B만약 자네가 신들에게 공물을 바친다면 그들은 감사하게 여길 걸세.$B$B남서쪽에 짐아브와 조각상이 있네. 가서 공물을 바치면, 짐아브와는 자네가 줄드락의 수호자라 여기며 기꺼이 공물을 받아들일 걸세.', },
    {'은빛십자군 격전지에 있는 사술사 우분고가 드라카리 공물 10개를 짐아브와 조각상에 바쳐 달라고 했습니다.', },
  },
  [12566] = {
    '겨울발굽 야영지의 도움',
    {'저는 타운카의 수색군 나니크라고 합니다. 브리쿨에 대항하는 전투에 도움을 줄 동맹을 찾아오라는 겨울발굽 야영지의 대족장께서 이리로 보내셨습니다. 브리쿨은 우리 마을 서쪽과 남쪽에 집결해 있습니다. 그러나 열 마리 브리쿨을 죽이려고 우리 모두가 죽는 일은 견뎌낼 수가 없습니다.$B$B연금술사 스킬리스는 용감한 $C들을 북서쪽으로 보내 우리를 돕겠다고 약속했습니다. 브리쿨의 공격을 물리치는 우리 타운카들을 도와 주시겠습니까?$B$B멀리 북서쪽으로 길을 따라가시다가 강을 건너고 산자락의 언덕으로 가면 겨울발굽 야영지에 도착할 겁니다.', },
    {'겨울발굽 야영지에 있는 족장 애쉬토템과 대화해야 합니다.', },
  },
  [12567] = {
    '짐아브와의 축복',
    nil,
    nil,
  },
  [12568] = {
    '트롤 순찰: 진정한 죽음',
    {'날 믿게, $R. 나도 안다네...$B$B시체가 불타는 냄새는 포세이큰인 나조차 역겹게 느껴질 정도야. 하지만 이것은 우리 형제가 스컬지로 바뀌는 비극을 막는 엄숙한 임무라네.$B$B죽어간 이들이 우리 적이 되어 전장에 나타나는 일이 없도록 이 기름을 그들의 시체에 부어주게.', },
    {'대장 루퍼트가 드락소트라에 있는 패배한 은빛십자군 보병 5명의 시체에 소각용 기름을 사용해 달라고 했습니다.', },
  },
  [12569] = {
    '악어 사냥법 익히기: 매복',
    {'분지에서 가장 큰 악어는 그 뛰어난 매복 능력 때문에 "덤불잠복꾼"이라는 이름이 붙었어요. 놈은 분지 한가운데에 있는 커다란 강의 심장부에 흐르는 얕은 물에서 햇볕을 쬐는 것을 좋아하죠. 그 호수는 우리 주둔지 동쪽에 있어요.$B$B자, 준비해 준 위장복을 가지고 강의 심장부 서쪽 호숫가에 숨어 있다가 덤불잠복꾼이 나타나면 기습하도록 하세요. 놈을 처치하면 그 강력한 턱을 제게 가져다주세요.', },
    {'네싱워리 주둔지에 있는 데바르에게 덤불잠복꾼의 턱을 가져가야 합니다.', },
  },
  [12570] = {
    '오해가 행운으로',
    {'나 미안하다! 곤란하게 할 생각 없었다!$B$B<한동안 멍하니 당신을 바라보는 상처입은 점쟁이>$B$B너가 나 구해줬다! 이제 마을로 돌아가자. 너도 같이... 우리랑 같이 지내자! 하룻강아지들은 어쨌든 좀 치사하다.$B$B갈 준비되면 나한테 말해라, 응?', },
    nil,
  },
  [12571] = {
    '사악한 뱀 처치',
    {'우리는 하룻강아지들이 오기 훨씬 전부터 여기 있었다. 아주 신성한 장소라서 우리는 떠나지 않는다.$B$B넌 악당처럼 안 보이지만, 만일을 대비해 라푸를 같이 보내겠다. 우리 처지를 이해해라.$B$B거기에 우리 새끼를 자꾸 먹어치우는 뱀이 있다. 라푸와 함께 가서 그러지 못하게 막아줬으면 한다, 알았나? 특히 북동쪽 폭포 옆에 있는 큰 뱀은 꼭 처리해줬으면 한다.', },
    {'구름몰이 거처에 있는 대점쟁이 수세이가 맹독혓바닥과 황제 코브라 10마리를 처치해 달라고 했습니다.$B$B라푸의 벌레 가방을 잃어버리면 구름몰이 거처의 대점쟁이 수세이와 대화해야 합니다.', },
  },
  [12572] = {
    '신은 반짝이를 좋아해',
    {'수세이가 우리 같이 간다고 했다. 잘 부탁한다. 응?$B$B우, 우! 우리 안다! 제단에 공물 많이 가져가면 수세이가 기뻐할 거다! 우리 멀리 갈 필요 없다. 마을 주변에도 파낼 게 많다... 그냥 땅에서 반짝이는 거 찾아봐라!$B$B가자! 가자! 공물 찾으러 가자!', },
    {'라푸가 파낸 보물 6개를 구름몰이 거처에 있는 대점쟁이 수세이에게 가져가야 합니다.$B$B라푸의 벌레 가방을 잃어버리면 구름몰이 거처의 대점쟁이 수세이와 이야기하십시오.', },
  },
  [12573] = {
    '평화 협정',
    {'좋은 생각이 있다. 너는 하룻강아지들과 친구니, 놈들이 네 말 들어줄지도 모르겠다.$B$B가서 주술사 베크지크에게 선물을 전해 줬으면 한다. 베크지크는 남서쪽, 강이 폭포로 바뀌는 곳에 있다. 하룻강아지들에게 우린 싸우고 싶지 않다고 전해줘라. 평화롭게 지내고 싶다고 말이지.$B$B넌 좋은 $R|1이다;다;. 이번 일을 잘 처리해 주리라 믿는다.', },
    {'구름몰이 거처에 있는 대점쟁이 수세이가 주술사 베크지크에게 구름몰이골록들이 보낸 평화의 선물을 건넨 다음 대답을 받아 오라고 했습니다.$B$B라푸의 벌레 가방을 잃어버리면 구름몰이 거처에 있는 대점쟁이 수세이와 대화해야 합니다.', },
  },
  [12574] = {
    '너무 빨리 온 거 아니오?',
    nil,
    nil,
  },
  [12575] = {
    '잃어버린 안개속삭임 보물',
    {'하룻강아지 몇 놈이 와서 위대한 안개 바위에서 푸른 수정을 깨트렸다! 폭우와 번개가 내리쳤고 나쁜 일이 일어났지... 푸른 수정은 거대한 안개 바위에 올려서는 안 된다! 멍청한 하룻강아지들 같으니.$B$B번개가 그친 후, 하룻강아지들이 우리가 바다호수에서 찾은 커다란 보물 훔쳐갔다. 그 보물을 되찾아 거대한 안개 바위에 바쳐야 한다! 하룻강아지들이 한 짓 때문에 안개 바위가 분노한다!$B$B그걸 훔쳐간 놈의 이름은 타르터... 아니, 티크탁... 아니지, 아니야... 타르텍. 그래, 타르텍이다! 타르텍이 북쪽에 있는 무슨 야영지인가로 갔다. 가서 보물을 되찾아 와라!', },
    {'대군주 타르텍을 처치하고 안개속삭임 보물을 안개속삭임 야영지에 있는 안개부름이 수간에게 가져가야 합니다.$B$B잘루트의 애지중지 수정을 잃어버리면 구름몰이 거처에 있는 대점쟁이 수세이와 대화하십시오.', },
  },
  [12576] = {
    '무력으로 해결',
    {'우리는 하룻강아지들이 처음 왔을 때부터 사이좋게 지내려고 했지만, 놈들은 치사하게만 굴었다.$B$B멍청한 것까지는 상관이 없다. 별로 위험하지는 않으니까. 그런데 놈들은 이제 성스러운 제단에 장난을 친다. 성스러운 제단은 놀이터가 아니다. 위대한 바위는 위대한 자들이 우리를 창조했을 때부터 여기에 있었다.$B$B거대한 안개 바위가 상처를 입으면 우리는 어찌해야 할지 모른다. 북쪽에 있는 그들 야영지까지 가라. 하룻강아지들을 물리쳐서 다시는 못 오게 해줘라!', },
    {'광란심장 창잡이 8마리와 광란심장 청소부 6마리를 처치하고 안개속삭임 야영지에 있는 안개부름이 수간에게 가야 합니다.$B$B잘루트의 애지중지 수정을 잃어버리면 구름몰이 거처에 있는 대점쟁이 수세이와 이야기하십시오.', },
  },
  [12577] = {
    '집에 갈 시간이야!',
    nil,
    nil,
  },
  [12578] = {
    '골록의 분노',
    {'다시 갈 수 있을 시간 안에 돌아오리라고는 생각하지 않았는데... 내 생각이 틀렸다. 가까운 곳에 사는 겁 많은 이끼걸음골록 친구들이 제단을 돌보러 오지 않아서 무들을 보내려던 참이었지. 이끼걸음 마을은 동쪽, 산마루 위쪽에 무너진 봉우리 북동쪽으로 가면 나온다.$B$B무들과 함께 떠나! 그는 최고로 똑똑한 구름몰이골록이지... 무들이 하는 말은 귀담아듣는 게 좋아.', },
    {'이끼걸음 마을로 가서 무들과 대화해야 합니다.$B$B무들의 족쇄공을 잃어버리면 구름몰이 거처의 대점쟁이 수세이와 대화하십시오.', },
  },
  [12579] = {
    '이끼걸음 제단 생명의 피',
    {'우리 날씨가 이상해지고 언데드와 떨어져 있게 된 주원인이 그 봉우리라고 항상 의심했었지. 이걸 보니 어느 정도 확신이 들어.$B$B그리 멀지 않은 옛날, 생명의 피 봉우리가 무너졌을 때 엄청난 폭발 소리가 났어. 거의 곧바로 이끼걸음골록이 있는 곳은 날씨가 바뀌었지... 눈이 내리기 시작했던 거야. 전에는 눈을 본 적이 없었거든.$B$B이끼걸음골록은 거대한 이끼 바위로 생명의 피 결정을 더 가져가서 눈을 멈출 수 있었어. 우리가 똑같이 할 수 있다면, 언데드를 내쫓는 데 도움이 될 거야.', },
    {'무들이 생명의 피 결정 10개를 모아오라고 했습니다. 봉우리 안쪽에 있는 동굴에는 가까이 가지 말아야 합니다.$B$B무들의 족쇄공을 잃어버리면 구름몰이 거처의 대점쟁이 수세이와 이야기하십시오.', },
  },
  [12580] = {
    '이끼걸음골록의 구원자',
    {'이끼걸음골록이 아직 다 죽지 않았을 가능성도 있어... 그리고 최소한, 이끼걸음골록이 이런 식으로 아무렇게나 죽어서는 안 돼.$B$B이런 학살 행위는 복수해주자. 그러는 동안 살아남은 골록을 발견할지도 몰라.', },
    {'무들이 이끼걸음 마을에서 이끼걸음골록 희생자 6명을 구해달라고 했습니다. 마을에는 이미 죽은 이끼걸음골록 희생자도 있습니다.$B$B무들의 족쇄공을 잃어버리면 구름몰이 거처의 대점쟁이 수세이와 대화해야 합니다.', },
  },
  [12581] = {
    '영웅의 짐',
    nil,
    nil,
  },
  [12582] = {
    '광란심장 용사',
    nil,
    nil,
  },
  [12583] = {
    '파괴된 분사기',
    {'역병 분사기 중 하나가 우리 서쪽으로 드락소트라 안에 추락했어요.$B$B가서 그걸 좀 찾아봐 주셨으면 좋겠네요. 분사기의 부품을 사용하면 다른 분사기들을 격추할 장치를 만들 수 있을 거예요!', },
    {'드락소트라에 있는 전문가 코그휠이 역병 분사기 부품을 가져다 달라고 했습니다.', },
  },
  [12584] = {
    '악의 정수',
    {'남동쪽에 추락한 죽음의 요새 콜라마스는 트롤에게 격추당할 당시 사로나이트 광석을 대량 수송하고 있었네.$B$B척후병들의 보고에 따르면 상당량의 사로나이트가 콜라마스 내부에 흩어져 있다는군.$B$B은빛십자군 격전지에 있는 아이트리그와 그 동료들이 연구를 하겠다며 이 광석을 잔뜩 보내 달라고 요청했네.$B$B콜라마스에 들어가 그들에게 보낼 광석을 충분히 모아 주게.', },
    {'대장 루퍼트가 사로나이트 덩어리 10개를 모아 은빛십자군 격전지에 있는 아이트리그에게 전달해 달라고 했습니다.', },
  },
  [12585] = {
    '트롤 순찰: 편안한 휴식',
    {'멋진 임무라고는 할 수 없지만 반드시 해야 하는 작업이 있다네.$B$B우리 병사들은 무릎까지 고인 물에서 사투를 벌이고 있네. 그게 몸에 좋을 리가 없지.$B$B그들에게는 몸을 말리고 투지를 북돋울 따뜻한 불이 필요하다네.$B$B도시의 이 지역은 온통 물에 잠겨 있지만. 죽은 가시나무는 어째서인지 물에 젖지 않더군.$B$B땔감을 좀 구해서 돌아와 준다면 대단히 고맙겠네.', },
    {'드락아갈에 있는 대장 그론델이 죽은 가시나무 20개를 가져다 달라고 했습니다.', },
  },
  [12586] = {
    '더 큰 사냥감을 찾아서',
    nil,
    nil,
  },
  [12587] = {
    '트롤 순찰',
    {'무기를 뽑으십시오, $R. 순찰을 나갈 시간입니다!$B$B나는 네 군데 초소를 책임지고 있습니다. 이 초소들의 통제권을 유지하는 건 우리 작전의 성공과도 직결된 일이지요. 그러니 초소를 돌며 각 초소의 대장을 도와주십시오.$B$B먼저 남동쪽에 있는 대장 브랜든부터 만나보십시오. 임무를 마치면 그가 다음 초소의 위치를 가르쳐줄 겁니다.$B$B초소 네 군데를 다 돈 다음에 다시 내게 보고하십시오. 빨리 돌아올수록 좋은 보상을 주겠습니다. 그러니 속도를 내세요!$B$B이제 가도 좋습니다!', },
    {'은빛십자군 격전지에 있는 사령관 쿤즈가 은빛십자군 초소를 방문해 각 대장이 주는 임무를 마치고 돌아와서 보고해 달라고 했습니다.$B$B대장 브랜든과 대장 루퍼트는 드락소트라에, 대장 그론델은 드락아갈에, 연금술사 핑클스타인은 헤브발로크에 있습니다.', },
  },
  [12588] = {
    '트롤 순찰: 보물 발굴',
    {'우리 병사 중 하나가 지난 전투에서 오래된 보물 지도 하나를 발견했습니다. 지도가 진짜라면 이 근처에 값을 매길 수 없을 만큼 귀중한 트롤 유물이 있다는 말이 되지요.$B$B드라카리와 스컬지를 한꺼번에 상대해야 하는 상황이라 내 부하들은 다른 데 신경 쓸 겨를이 없습니다. 그러니 당신이 이 정보를 추적해 줬으면 합니다.$B$B이 삽을 가지고 남서쪽에 있는 흙더미를 파헤쳐 보십시오. 발굴 작업을 마치면 돌아오시고요.', },
    {'드락소트라에 있는 대장 브랜든이 근처의 고대 흙더미에서 철제 부삽을 사용해 고대 드라카리 유물을 찾아달라고 했습니다.', },
  },
  [12589] = {
    '반동? 무슨 반동?',
    {'사격은 자신 있다 그건가요?$B$B그럼 어디 시험해 봅시다. 먼저 얌전한 녀석부터 시작하지요. 걱정하지 마세요. 반동 같은 건 전혀 없으니까요. 아, 명심하세요. 쏠 게 아니라면 절대 총구를 겨누지 마시고요.$B$B준비가 되면 저 운 좋은 빌헬름의 머리 위에 놓인 사과를 날려버리세요!', },
    {'RJR 라이플을 사용해 운 좋은 빌헬름의 머리 위에 있는 사과를 맞춘 다음 드로스탄과 대화해야 합니다.', },
  },
  [12591] = {
    '트롤 순찰: 폭발물 투척',
    {'꽤 영리하군...$B$B우리가 여기를 장악했다고 생각했는데, 놈들은 우리 밑으로 굴을 뚫어 제단을 점령해 버렸네!$B$B이제 우린 네루비안 군대 두 무리에 포위당했고, 상황은 점점 나빠져 가고만 있네.$B$B일단 급선무는 놈들의 증원군을 막는 걸세. 굴을 막아버려야 해.$B$B이 폭발물을 가져가서 북서쪽에 있는 놈들의 굴에 던지게.', },
    {'드락소트라에 있는 대장 루퍼트가 네루비안 굴 5개에 고충격 폭발물을 사용해 달라고 했습니다.', },
  },
  [12592] = {
    '위대한 사냥꾼의 대결',
    {'보아하니 당신한테도 사냥꾼의 본능 같은 게 있는 모양이구려. 사격 실력도 쓸만하고, 쫓겨나지 않는 걸 보니 아마 헤멧이 당신을 사냥에 끼워 주려나 보오.$B$B당신과 나 둘이서 경쟁을 해 보면 어떻겠소?$B$B헤멧과 다른 원정대원들이 당신에게 그들의 사냥을 도와달라고 할 거요. 사냥을 하며 당신과 나는 목표량에 기여한 사냥감 처치 수를 셀 것이고 말이오. 눈 깜짝할 사이에 당신이 이길 수 있는 그런 대결이 아니라오. 이번 대결을 통해 다양한 사냥감들을 처치할 수 있을 것이오. 행운을 빌겠소.', },
    {'헤멧과 그의 원정대원들의 사냥을 도와주면서 사냥감 60마리를 처치해야 합니다. 60마리를 제거하면 다음 네싱워리 주둔지에 있는 드로스탄에게 돌아가 숫자를 비교해 보십시오.', },
  },
  [12593] = {
    '리치 왕을 위하여',
    nil,
    nil,
  },
  [12594] = {
    '트롤 순찰: 그냥 두지 않겠다!',
    {'그 많은 초소 중에서 하필 이런 곳에 떨어지다니.$B$B냄새 나는 물이 가득 고인 걸로 모자라서, 하수도에서 튀어나온 듯한 정령이 넘쳐나는 지경이니!$B$B이놈들의 정체가 뭔지, 대체 왜 여기에 있는지는 알 수 없지만, 솔직히 내가 알 바는 아닐세. 한 가지 확실한 건 놈들이 내 구역을 엉망으로 만들고 있다는 사실이야! 그런 추악한 것들이 돌아다니고 있는데 대체 어떻게 신성한 군사 임무를 수행할 수 있겠나?$B$B자네가 처리하게. 우리 전쟁터에서 저 괴물들을 충분히 없애기 전까지는 돌아오지 말게!', },
    {'대장 그론델이 드락아갈에 있는 광포한 이끼 괴물 7마리를 처치해 달라고 했습니다.', },
  },
  [12595] = {
    '더 큰 사냥감을 찾아서',
    {'$N, 당신은 그 어떤 동물, 코뿔소, 살쾡이, 그리고 악어까지도 사냥할 수 있는 용기가 있다는 사실을 증명해 보였소. 그런 짐승들은 일부 원정대원들의 실력을 시험해 보는 데 아주 그만이지. 하지만 나 같은 사냥꾼들은 더 큰 목표를 찾게 마련이라오.$B$B도리안 드레이크스토커가 북쪽 거센물결 호수 너머에 야영지를 차렸소. 거대한 태고의 비룡에 대한 소문을 들었다고 하더군. 그게 얼마나 훌륭한 사냥감일지 상상해 보시오! 도리안을 만나서 어떻게 되었는지 알아봐 주시오.', },
    {'숄라자르 분지의 도리안의 전초기지에 있는 도리안 드레이크스토커와 대화해야 합니다.', },
  },
  [12596] = {
    '순찰 임무',
    {'은빛십자군 격전지에 온 것을 환영합니다. 나는 사령관 쿤즈라고 합니다. 사령관님이라는 호칭을 사용해 주십시오.$B$B내 구역 안에서는 한가롭게 노닥거린다거나, 임무를 입맛대로 고른다거나, 명령을 따르지 않는다거나 하는 일 따위는 꿈도 꾸지 마십시오.$B$B여기 있는 동안은 내게 보고하고 내 명령을 그대로 따라야 합니다. 아셨지요?!$B$B먼저 내 부하들을 만나보고 우리가 쓸어버릴 지역에 좀 익숙해지는 게 좋겠군요.$B$B그들을 각각 찾아가 보고하고 명령을 따르십시오. 순찰을 마치면 내게 돌아오십시오.', },
    {'은빛십자군 격전지에 있는 사령관 쿤즈가 은빛십자군 초소를 방문해 각 대장이 주는 임무를 마치고 돌아와서 보고해 달라고 했습니다.$B$B대장 브랜든과 대장 루퍼트는 남동쪽의 드락소트라에 있습니다.$B$B대장 그론델은 정동쪽의 드락아갈에 있습니다.$B$B연금술사 핑클스타인은 북쪽의 헤브발로크에 있습니다.', },
  },
  [12597] = {
    '고통 경감',
    {'내 부하들은 매우 용감하지만, 그들 역시 뼈와 살로 이루어진 존재입니다.$B$B부상과 통증 때문에 임무를 수행할 능력이 많이 줄어들었지요. 아무리 훈련을 많이 받았다 한들 육체적인 한계를 벗어날 수는 없는 법입니다.$B$B다행히도 우리 연금술사 중 하나가 병사들의 고통을 대폭 줄여줄 수 있는 대단한 연고를 개발했습니다.$B$B이 연고를 만들려면 트롤이 이곳 서쪽에서 재배하는 특수한 양귀비가 필요합니다. 우리가 가진 걸 다 써버리기 전에 드락소트라 농장에 가서 그 양귀비를 좀 모아 오십시오.', },
    {'드락소트라에 있는 대장 브랜든이 드락소트라 농장에서 다 자란 물양귀비 5개를 가져다 달라고 했습니다.', },
  },
  [12598] = {
    '폭발물 투척',
    {'꽤 영리하군...$B$B우리가 여기를 장악했다고 생각했는데, 놈들은 우리 밑으로 굴을 뚫어 제단을 점령해 버렸네!$B$B이제 우린 네루비안 군대에 두 무리에 포위당했고, 상황은 점점 나빠져 가고만 있네.$B$B일단 급선무는 놈들의 증원군을 막는 걸세. 굴을 막아버려야 해.$B$B이 폭발물을 가져가서 북서쪽에 있는 놈들의 굴에 던지게.', },
    {'드락소트라에 있는 대장 루퍼트가 네루비안 굴 5개에 고충격 폭발물을 사용해 달라고 했습니다.', },
  },
  [12599] = {
