  [13502] = {
    '소쿠리, 광주리, 귀족의 정원 바구니',
    {'여기요... 이 알 바구니를 가져가셔서 알록달록한 알을 모으세요. 귀족의 정원 초콜릿 10개를 찾거든 바구니를 가지고 돌아오세요.', },
    nil,
  },
  [13503] = {
    '소쿠리, 광주리, 귀족의 정원 바구니',
    {'여기요... 이 알 바구니를 가져가셔서 알록달록한 알을 모으세요. 귀족의 정원 초콜릿 10개를 찾거든 바구니를 가지고 돌아오세요.', },
    nil,
  },
  [13524] = {
    '은빛시내 마을 탈출',
    {'당신... 그놈들과 한 패가 아니군! 빨리! 여기서 꺼내 주시오!$B$B당장 호박빛소나무 오두막으로 가야 하오! 여기 있는 놈들이 어떤 존재인지 듀몬트 부관님께 알리면 마을을 쓸어버리실 거요!', },
    {'나무 우리를 연 다음, 풀려난 얼라이언스 정찰병이 은빛시내 마을에서 탈출할 수 있도록 도와야 합니다. 호박빛소나무 오두막에 도착하면 부관 듀몬트에게 보고하십시오.', },
  },
  [13538] = {
    '남쪽의 방해 공작',
    {'겨울손아귀 강 남쪽에 있는 경비탑 세 개가 겨울손아귀 요새에서 힘을 빨아들이고 있어요!$B$B그늘눈 경비탑이나 불꽃감시 경비탑, 아니면 겨울 칼날 경비탑을 파괴하고 제게 돌아오세요. 아시겠지만, 경비탑에는 공성 차량으로만 피해를 입힐 수 있어요. 그러니 호드가 공성 차량을 건드리지 못하게 잘 지키셔야 해요!', },
    {'그늘눈 경비탑이나 불꽃감시 경비탑 또는 겨울 칼날 경비탑을 파괴한 후 겨울손아귀에 있는 전술장교 아브라미스에게 가야 합니다.', },
  },
  [13539] = {
    '경비탑 무너뜨리기',
    {'얼라이언스가 우리 문앞에서 맹공격을 퍼붓는 이런 때라도, 진정한 전술가는 눈에 보이는 것 이상을 보는 법이라오.$B$B"방비가 허술한 곳을 공격하고, 예기치 못한 곳에 나타나라."$B$B$N, 당신이 겨울손아귀 강을 건너가, 남쪽 세 개 경비탑 중 하나를 파괴해 줬으면 하오. 우리 흑마법사들이 보고하길 그 경비탑들이 아군을 강화하는 데 유용할 힘을 빨아들이고 있다는군.$B$B경비탑을 하나라도 파괴하거든 내게 돌아오시오.', },
    {'그늘눈 경비탑이나 불꽃감시 경비탑 또는 겨울 칼날 경비탑을 파괴한 후 겨울손아귀에 있는 전술장교 킬라스에게 가야 합니다.', },
  },
  [13548] = {
    '사탕 바구니',
    nil,
    nil,
  },
  [13549] = {
    '꼬리를 들춰라',
    {'전 드라카리 유물뿐 아니라, 줄드락의 야생 동물도 좀 데리고 잔달라 부족에게 돌아가고 싶습니다. 다행히 서리 표범과 얼음발 곰 수컷은 구했습니다만, 암컷도 필요합니다!$B$B안타깝게도, 제가 고용한 덫사냥꾼은 매우 불운한 사고를 당해 죽고 말았지요. 혹시 당신이 그의 역할을 대신해주지 않겠습니까?$B$B이 바람총으로 표범과 곰을 재우고, 녀석들의 꼬리를 들춰 보세요. 이 방법으로 암컷을 몇 마리 찾아서 제게 데려와 주신다면 정말 감사하겠습니다. 루노크의 제단, 쿠에츠룬의 제단, 하르코아의 제단 근처에서 표범과 곰을 찾을 수 있을 겁니다.', },
    {'짐토르가에 있는 기록가 토키니가 서리 표범과 얼음발 곰에게 토키니의 바람총을 사용해 암컷을 3마리씩 구해 돌아와 달라고 했습니다.', },
  },
  [13556] = {
    '두브라진은 알을 좋아해',
    {'어떻게 지나시나요? 혹시 절 도와줄 시간 좀 있나요? 바키니가 두브라진께 제물로 바칠 랩터 알이 많이 필요하다는군요.$B$B어떻게 아직도 드라카리가 우릴 쓸어버리지 못했는지 알겠지요?! 두브라진께서 우릴 도와주신다고요!$B$B여기 군드락 바깥의 나무 주위에서 랩터 알을 찾아 주셨으면 합니다. 어떤 랩터는 알을 따뜻하게 하려고 품고 돌아다니기도 한다는군요.', },
    {'두브라진에 있는 하와나가 군드락 랩터 알 12개를 모아 달라고 했습니다.', },
  },
  [13559] = {
    '호디르께 공물을',
    nil,
    nil,
  },
  [13571] = {
    '주정뱅이 유실물 보관소',
    nil,
    nil,
  },
  [13592] = {
    '용맹전사의 실전 훈련',
    {'마상시합은 동료를 상대로 자신의 기술을 시험하는 기회이지만, 진짜 적인 스컬지가 그동안 잠잠히 있어 주진 않는다는 것을 잊지 말게나. 다른 이를 상대로 기술을 연마할 때도 적을 상대하는 전투 감각이 무뎌져선 안 되네.$B$B전사한 영웅의 계곡의 남서쪽, 코프레타르 북쪽으로 가게. 거기서 스컬지가 죽은 영웅들을 일으켜 우리를 상대하게 하고 있네. 스컬지의 졸개가 된 이들을 쓰러뜨리게. 전투 감각을 갈고 닦는 훈련도 되면서, 동료의 명예도 지킬 수 있을 거네.', },
    {'은빛십자군 마상시합 광장에 있는 마커스 발로 경이 조종 당하는 영웅 10명을 처치해 달라고 했습니다.', },
  },
  [13600] = {
    '훌륭한 무기',
    nil,
    {'은빛십자군 마상시합 광장에 있는 작전사령관 제이콥 알레리우스에게 드락마의 검을 가져가야 합니다.', },
  },
  [13603] = {
    '용사에게 걸맞은 검',
    {'품질 좋고 순수한, 잘 연마한 강철이면 다 되는 게 아니라오. 다른 경쟁자들은 마력이 깃든 검을 사용한단 말이오. 내겐 불리한 일이지만, 그렇다고 방법이 없는 건 아니지.$B$B회색 구릉지에서 전해 내려오는 오래된 전설에 따르면, 어떤 검을 오랫동안 수호했던 한 여신이 저주를 받아 개구리가 되어 버렸다더군. 그녀는 잿빛나무 초소 바로 동쪽의 잿빛나무 호숫가에서 다른 호수 개구리 사이에 섞여 산다고 들었소. 저주를 푸는 방법은 하나뿐이라오. 개구리가 된 여신에게 스스로 입맞춤을 해야 하지. 입맞춤을 하기 전에 이 보호제를 바르시오, $N. 그러지 않으면 버짐에 걸릴 테니.', },
    {'은빛십자군 마상시합 광장에 있는 작전사령관 제이콥 알레리우스에게 잿빛나무 검을 가져가야 합니다.', },
  },
  [13604] = {
    '고대 기록관 자료 원반',
    {'이 백금 원반은 놀라울 만큼 균형 잡힌 모양새에 긁힌 자국 하나 없이 완벽합니다. 가장자리를 따라 굵은 룬문자로 "고대 기록관"이란 글자가 새겨져 있습니다.$B$B울두아르 안에 이 원반을 읽을 수 있는 장치가 있을 겁니다.', },
    {'울두아르 안에 있는 고대 기록관 조작대로 고대 기록관 자료 원반을 가져가야 합니다.', },
  },
  [13606] = {
    '프레이야의 인장',
    {'프레이야의 인장을 얻기란 퍽 어려울 것 같군요.$B$B고대 기록관에 따르면, 인장의 정수는 그녀의 하인 중에서 세 장로에게 결속되어 있다는군요. 밝은잎, 무쇠줄기, 돌나무껍질이 그 셋입니다. 셋 중 누구라도 다치면 인장의 완전무결함이 해를 입을 겁니다.$B$B그렇다고 세 장로를 그냥 놔두면, 더욱 강력한 프레이야를 상대해야만 할 테고요.$B$B제가 무슨 말을 할 수 있겠습니까, $N 님. 이러기도 저러기도 싫은 상황입니다만, 어쨌든 하나를 택해야지요.', },
    {'울두아르의 고대 기록관에 있는 발굴조사단장 도렌이 프레이야의 인장을 가져와 달라고 했습니다.$B$B프레이야의 인장에 대해 더 많은 정보를 얻으려면 고대 기록관 조작대를 참고하십시오.', },
  },
  [13607] = {
    '별자리 투영관',
    {'잘 들으세요, 젊은 양반! 아제로스의 운명이 달린 일이라고요.$B$B시간 안에 별자리 투영관으로 가서 알갈론을 찾아, 그놈이 티탄 주인들에게 전갈을 쏘아 올리지 못하게 해야 합니다. 그게 유일한 희망이라고요.$B$B별자리 투영관을 찾아서 정보를 모아 돌아오세요. 아직 알갈론이 도착하기 전에 시간이 좀 있을지도 모릅니다.', },
    {'울두아르의 고대 기록관에 있는 발굴조사단장 도렌이 별자리 투영관의 입구를 찾아 달라고 했습니다.', },
  },
  [13609] = {
    '호디르의 인장',
    {'호디르의 인장도 쉽게 얻진 못할 겁니다. 호디르 자신을, 그것도 인장이 담긴 상자를 부숴버리기 전에 쓰러뜨려야 하니까요!$B$B정보가 더 필요하시면 고대 기록관 조작대를 확인해 보세요. 세계의 모든 도서관을 다 합친 것보다 더 많은 지식이 저기 들어 있답니다!', },
    {'울두아르의 고대 기록관에 있는 발굴조사단장 도렌이 호디르의 인장을 가져와 달라고 했습니다.$B$B호디르의 인장에 대해 더 많은 정보를 얻으려면 고대 기록관 조작대를 참고하십시오.', },
  },
  [13610] = {
    '토림의 인장',
    {'토림은 제정신이 아닙니다, $N 님. 암흑에 영향을 받아 자신을 잃었어요.$B$B사악한 존재가 토림의 죽은 아내, 시프로 변장하고 있습니다.$B$B토림이 이 사기꾼의 주문에 넘어가게 두면 안 됩니다. 만약 그런 일이 벌어지면, 토림의 인장이 있는 위치를 영영 알 수 없게 될 거라고요!$B$B어떻게든 그가 제정신을 좀 차리게 하셔야 합니다. 그것도 빨리요.$B$B언제나처럼 고대 기록관 조작대에서 끔찍한 세부 사항을 알아내실 수 있을 겁니다... 별로 알고 싶지 않은 것까지도요.', },
    {'울두아르의 고대 기록관에 있는 발굴조사단장 도렌이 토림의 인장을 가져와 달라고 했습니다.$B$B토림의 인장에 대해 더 많은 정보를 얻으려면 고대 기록관 조작대를 참고하십시오.', },
  },
  [13611] = {
    '미미론의 인장',
    {'미미론은 영리하답니다. 그의 처소에는 전체적으로 일종의 자폭 기능 같은 게 설치되어 있다고요.$B$B안타깝게도 미미론의 인장을 얻으려면 그 자폭 기능을 활성화해서 시간에 쫓기면서 전투를 치러야 합니다. 더 자세한 내용을 알고 싶으시면 고대 기록관 조작대를 참고하세요.', },
    {'울두아르의 고대 기록관에 있는 발굴조사단장 도렌이 미미론의 인장을 가져와 달라고 했습니다.$B$B미미론의 인장에 대해 더 많은 정보를 얻으려면 고대 기록관 조작대를 참고하십시오.', },
  },
  [13614] = {
    '알갈론',
    {'정말 해내셨군요, $N 님. 이 인장 묶음을 고대 기록관 조작대로 가져가셔서, 별자리 투영관 접근 허가를 받을 수 있는지 알아보세요.$B$B서두르는 게 좋을 겁니다. 시간이 가장 중요하니까요.', },
    {'울두아르 안에 있는 고대 기록관 조작대로 감시자의 인장 묶음을 가져가야 합니다.', },
  },
  [13616] = {
    '겨울의 칼날',
    {'내 검을 부러뜨리다니, 망할 죽음의 기사 같으니!$B$B수정노래 숲 남부, 수정 계곡의 출구를 내려다보는 곳에서 화염군주 에버블레이즈를 찾을 수 있을 거요. 그를 없애고 영원히 타오르는 불씨를 찾으시오.$B$B그걸 울부짖는 협만 북부에 있는 겨울의 숨결 호수 중앙으로 가져가서, 얼음에 갇힌 여신을 해방해 주시오. 그녀는 고대 검의 수호자로서 검의 새 주인이 나타나 자신을 해방해 주기를 기다리고 있다오.$B$B당신의 가치를 증명하고 그녀가 오랫동안 지켜 온 그 검을 받아 오시오, $N.', },
    {'은빛십자군 마상시합 광장에 있는 작전사령관 제이콥 알레리우스가 겨울 칼날을 가져다 달라고 했습니다.', },
  },
  [13622] = {
    '고대사',
    {'조각들을 합치면 격조 있는 망치가 만들어집니다. 구조를 살펴볼 때, 최소한 일부분은 티탄의 작품인 듯합니다.$B$B고대 기록관 내부의 조작대에서 이 유물에 대한 정보를 좀 얻을 수 있을지도 모릅니다.', },
    {'울두아르의 고대 기록관 조작대에 부서진 발아니르 조각을 가져가야 합니다.', },
  },
  [13625] = {
    '고삐 다루는 법 익히기',
    {'대부분의 마상시합 경기는 마상 전투에 숙련된 이만 참가할 수 있습니다. 마상 전투에 익숙해지실 수 있도록, 저희가 마상시합 광장 북부 여기저기에 훈련용 허수아비를 만들어 두었답니다.$B$B막사 바깥에 있는 마구간으로 가서, 창을 꺼내 들고 쿠엘도레이 군마에 올라타세요. 탈것에 탄 상태로 쓸 수 있는 기술들을 익히시고, 그 기술들을 이용해서 훈련용 허수아비 세 종류를 쓰러뜨리세요.', },
    {'근접 표적에 찌르기를 5번 사용하고, 약해진 원거리 표적 2개에 방패 부수기를 사용하고, 약해진 돌진 표적 2개에 돌진을 사용한 후 은빛십자군 마상시합 광장의 정찰병 샬린드리아에게 가야 합니다.$B$B창을 잃어버리면 마구간 근처의 아무 창 선반이나 은빛 서약단 막사에서 다른 창을 얻을 수 있습니다.', },
  },
  [13627] = {
    '불법 벌채',
    {'가까이 오라고. 아니, 너무 가까이는 말고. 쓸데없이 의심을 불러일으키고 싶진 않잖아, 그렇지?$B$B이 원형경기장엔 우리가 비축한 것보다 더 많은 목재가 드는군. 그런데 회색 구릉지에서 목재를 실어올 돈이 없다고. 수정노래 숲 서쪽 가장자리, 거대 고목 옆에 어린 수정노래 떡갈나무가 좀 자라지. 발판을 올리거나 건물을 짓는 데 필요한 종류의 판자를 그 나무로 만들 수 있다고. 이 도끼를 가져가서 나무를 벤 다음, 그걸 켜서 판자를 만들어 와. 아무한테도 들키지 말라고!', },
    {'은빛십자군 마상시합 광장의 베즐 블라스트볼트에게 거칠게 잘라낸 나무판자 12개를 가져가야 합니다.', },
  },
  [13629] = {
    '발아니르 - 고대 왕의 망치',
    {'고대 기록관 조작대의 말을 듣고 나니 발아니르 조각을 재결합할 가능성이 보입니다. 요그사론이라고 알려진 존재의 물리적 형태 내부에 조각을 넣어야 합니다.$B$B너무나도 힘든 모험이 되겠지만, 엄청난 힘을 지닌 유물을 복구하려면 그 정도의 위험부담은 당연하다는 생각도 듭니다.', },
    {'귀청이 터질듯한 포효를 시전하는 요그사론의 입속에 부서진 발아니르 조각을 던져넣고 그를 쓰러뜨려, 고대 왕의 재련된 망치를 회수해야 합니다.$B$B영웅 난이도에서 울두아르 감시자 셋 이하에게만 도움을 받아야 이 퀘스트를 완료할 수 있습니다.', },
  },
  [13631] = {
    '끝이 좋으면 다 좋다',
    {'이제 서두르는 게 좋겠어요, $N 님. 달라란에서 이 응답 코드를 전송해야 합니다.$B$B이걸 로닌에게 가져다주세요. 그럼 거기에서 만납시다.', },
    {'응답 코드 알파를 달라란에 있는 로닌에게 가져가야 합니다.', },
  },
  [13633] = {
    '서부 몰락지대의 흑기사?',
    {'사람들이 흑기사라고 부르는 참가자에 대해서 심란한 소문을 들어 보신 적이 있으시겠죠? 그와 경기 일정이 잡힌 선수는 모두 경기 전에 세상을 떠나고 말았다고들 합니다.$B$B당연한 얘기지만, 저희에겐 확실한 증거가 하나도 없답니다. 하지만 이 기사의 배경을 좀 조사해 봐도 나쁠 거 없겠죠.$B$B그는 자신이 서부 몰락지대 쿠퍼 가의 자손이라고 주장하고 있습니다. 서부 몰락지대 달빛시내 마을 서부에 있는 쿠퍼 저택을 방문해 주시겠어요? 그의 이야기가 사실이라는 증거를 찾을 수 있을지 보자고요.', },
    {'흑기사의 주장이 사실인지 조사하고 나서, 은빛십자군 마상시합 광장에 있는 성전사 라이달라에게 결과를 알려야 합니다.', },
  },
  [13634] = {
    '은빛소나무 숲의 흑기사?',
    {'사람들이 흑기사라고 부르는 참가자에 대해서 심란한 소문을 들어 보신 적이 있으시겠죠? 그와 경기 일정이 잡힌 선수는 모두 경기 전에 세상을 떠나고 말았다고들 합니다.$B$B당연한 얘기지만, 저희에겐 확실한 증거가 하나도 없답니다. 하지만 이 기사의 배경을 좀 조사해 봐도 나쁠 거 없겠죠.$B$B그는 자신이 은빛소나무 숲에 있는 발간 농장의 후계자라고 주장하고 있어요. 발간 농장은 로다미어 호수 서쪽 호숫가에 있는 작은 농장이죠. 그 농장을 찾아가서, 그의 이야기에 일말의 진실이라도 있는지 알아봐 주시겠어요?', },
    {'흑기사의 주장이 사실인지 조사하고 나서, 은빛십자군 마상시합 광장에 있는 성전사 라이달라에게 결과를 알려야 합니다.', },
  },
  [13641] = {
    '예언자의 수정',
    {'흑기사의 이야기가 순 거짓말이란 걸 알았으니, 이젠 목숨을 잃은 기사들과 흑기사를 연결지을 순서로군요.$B$B저는 기사들이 어떻게 죽었는지 밝힐 방법을 압니다만, 거기엔 희귀한 물건이 필요해요. 바로 예언자의 수정입니다. 이 수정은 수정노래 숲 중심부, 쓸쓸한 숲 내부의 나이트 엘프 폐허 근처에 모인 해방된 예언자에게서만 구할 수 있지요. 예언자를 찾으세요, $N 님. 그리고 놈에게서 수정을 구해, 제게 가져와 주세요.', },
    {'은빛십자군 마상시합 광장에 있는 성전사 라이달라에게 예언자의 수정을 가져가야 합니다.', },
  },
  [13643] = {
    '죽은 자의 이야기',
    {'이 수정이 마상시합 참가자 세 명이 어떻게 죽었는지 드러내 줄 겁니다. 마상시합 광장 동쪽의 묘지로 수정을 가져가서 웬델 발포어 경, 로리엔 선블레이즈, 코날 아이언그립의 무덤 앞에서 사용하세요.$B$B만약 이들이 타인의 손에 목숨을 잃은 게 사실이라면, 우리는 흑기사가 다시 일을 저지르기 전에 그와 사건들을 연결할 방법을 찾아야만 해요. 수정이 알려 주는 것을 주의 깊게 살펴보세요, $N 님. 이들의 죽음이 사고가 아님을 확실히 해야 합니다.', },
    {'웬델 발포어 경, 로리엔 선블레이즈, 코날 아이언그립의 무덤에서 예언자의 수정을 사용해 그들이 어떻게 죽었는지 알아보고 나서, 성전사 라이달라에게 가야 합니다.', },
  },
  [13649] = {
    '저스틴의 즐거운 테스트',
    {'거기, 여기 좀 보세요! 당신 맥주를 처리할 줄 아는 것 같네요. 하지만 당신 맥주잔을 처리할 줄도 아나요?$B$B검은무쇠 드워프 녀석들은 우리 축제를 망치려고 복수의 열망으로 가득 차서 돌아왔답니다. 녀석들을 막으려면 그들에게 맥주잔을 던지는 길이 최선이지요. 그전에 먼저, 여기 있는 오래된 주.정.뱅.이.에 맥주잔을 던질 수 있는지부터 알아보도록 하자고요.$B$B방법은 간단해요. 시음용 가을 맥주를 집어서 마시면 돼요. 그다음 주.정.뱅.이.를 마주하도록 하세요. 그 친구를 5번 맞히고 와서 저와 얘기하면 됩니다.', },
    {'시음용 가을 축제 맥주를 사용하여 주.정.뱅.이.를 5번 맞힌 후 복시 볼트스피너와 대화해야 합니다.', },
  },
  [13654] = {
    '흑기사의 종자에겐 뭔가 특별한 것이 있다',
    {'흑기사의 종자인 말로릭은 날이면 날마다 보란 듯이 도끼를 들고 야영지를 빠져나온답니다. 아마도 나무하러 가는 것처럼 보이고 싶나 본데, 정작 돌아올 때 장작은 조금밖에 들고 있지 않다고요.$B$B그는 보통 황혼의 거울 남서쪽에 있는 폐허로 향한답니다. 그리고 기사를 보필할 책임이 있는 종자치고는 거기서 너무 오랜 시간을 보내지요.$B$B어떻게든 그를 혼란에 빠뜨리거나 정신을 잃게 해서 조사해 보세요. 그가 무슨 일인가 꾸미고 있다면, 알아낼 수 있을 겁니다.', },
    {'해골 벌목꾼에게서 큰 다리뼈를 획득해 말로릭에게 사용해야 합니다. 말로릭을 조사해 증거를 찾아낸 후, 은빛십자군 마상시합 광장의 성전사 라이달라에게 보고해야 합니다.', },
  },
  [13662] = {
    '훨씬 더 인정받기',
    nil,
    nil,
  },
  [13663] = {
    '흑기사의 명령서',
    {'누가 흑기사를 마상시합에 내보냈는지, 그리고 참가자를 없앰으로써 그 인물이 무엇을 얻고자 했는지 알아내야 합니다.$B$B키린 토에서 우리를 돕겠다는 의사를 밝혔습니다. 그리고 우리의 조사에 도움이 될 만한 물건도 제공해 주었지요.$B$B마상시합 광장 동쪽 끝에 흑기사의 야영지가 있습니다. 이 마력 깃든 고삐를 거기로 가져가서 흑기사의 그리핀을 제압하세요. 고삐에 부여된 마법의 힘으로 그리핀이 당신을 자기 주인의 집으로 인도할 겁니다. 집을 수색하고 발견한 사실을 제게 알려 주세요.', },
    {'마력 깃든 고삐를 이용해 흑기사의 그리핀을 제압한 후, 목적지까지 타고 가십시오. 도착하면 도둑맞은 마상시합 초대장과 흑기사의 명령서를 입수해 은빛십자군 마상시합 광장의 성전사 라이달라에게 가져가야 합니다.', },
  },
  [13664] = {
    '흑기사의 최후',
    {'흑기사의 흉계에 종지부를 찍어야 합니다. 우리에게 맞서는 놈들에게, 우리가 용사들의 죽음을 그냥 두고 보지만은 않으리란 걸 보여줘야죠.$B$B지금쯤이면 흑기사도 누군가가 자신을 추적하는 건 아닌지 의심하고 있을 겁니다. 그가 거절하거나 어둠을 틈타 해를 끼칠 수 없도록, 훤한 대낮에 그에게 도전하세요.$B$B전투에서는 마구간에 있는 제 은빛십자군 군마를 사용하시면 됩니다.$B$B용사의 투기장에서 종자 카빈과 이야기하세요. 카빈이 흑기사를 전투에 불러내 줄 테니, 그가 범죄에 대한 대가를 치르게 하세요.', },
    {'흑기사를 쓰러뜨린 후 은빛십자군 마상시합 광장에 있는 성전사 라이달라와 대화해야 합니다.', },
  },
  [13665] = {
    '최고 마상전',
    nil,
    {'은빛십자군 마상시합 광장에 있는 대장 조셉 홀리에게 용맹전사의 징표 3개를 가져가야 합니다.$B$B창을 잃으면 마구간 근처나 은빛 서약단 막사에 있는 창 선반에서 다른 창을 구할 수 있습니다.', },
  },
  [13666] = {
    '용사에게 걸맞은 검',
    {'어떤 용사들은 노스렌드에서 생겨난 고대 무기가 마상시합에 참가할 때 행운을 가져다준다고 믿습니다. 저는 이 무기를 찾는데 도움을 주기로 했습니다.$B$B회색 구릉지에 전해 내려오는 오래된 전설에 따르면, 한 여신이 저주를 받아 개구리의 모습으로 변하여 오랫동안 지켜 왔던 검을 보호한다고 합니다. 지금은 잿빛나무 초소 바로 동쪽의 잿빛나무 호숫가에서 다른 호수 개구리 사이에 섞여 산다고 들었습니다. 저주를 푸는 유일한 방법은 서슴없이 입맞춤을 하는 것뿐입니다. 입맞춤을 하기 전에 이 보호제를 바르십시오, $N 님. 그렇지 않으면 버짐에 걸릴 테니까요.', },
    {'은빛십자군 마상시합 광장에 있는 신비술사 탤리스에게 잿빛나무 검을 가져가야 합니다.', },
  },
  [13667] = {
    '은빛십자군 마상시합',
    {'우리가 그대를 여기에 초대한 것은 실력을 시험해보고 스컬지에 용감하게 맞선 이들과 용기를 겨뤄보라는 뜻에서지요. 많은 이들이 우리 편에 섰었지만, 각자의 깃발을 걸고 싸우는 영웅들이 공동의 적에 맞설 때를 대비하여 서로 검날을 시험해볼 때가 왔어요.$B$B신비술사 탤리스는 이번 마상시합의 은빛 서약단 대표로서 그대 이름을 명단에 올린 다음 훈련을 시작할 거예요. 광장 북동쪽에 있는 커다란 얼라이언스 막사 안에서 그를 만나 보세요.', },
    {'은빛십자군 마상시합 광장에 있는 신비술사 탤리스와 대화하여 마상시합에 참여해야 합니다.', },
  },
  [13668] = {
    '은빛십자군 마상시합',
    nil,
    {'은빛십자군 마상시합 광장에 있는 마법학자 에디엔 선할로우와 대화하고 마상시합에 참여해야 합니다.', },
  },
  [13669] = {
    '훌륭한 무기',
    {'우리 용사들은 예전부터 여기 노스렌드에서 만들어진 고대 무기를 좋아합니다.$B$B저는 외로운 여신에게 겨울 히아신스를 바쳐 선물을 받는 여행자들의 이야기를 음유 시인들의 입으로 전해 들어왔지요. 히아신스는 수정노래 숲이 얼음왕관과 만나는 북서쪽 경계에 있는 무쇠벽 둑에서 떠내려오는 얼음 위에서만 자란답니다. 그 꽃을 꺾어다가 줄드락과 회색 구릉지 근처, 용의 안식처 북동쪽에 있는 드락마 호수에서 등불로 둘러싸인 곳으로 가져가 주십시오. 외로운 여신에게 훌륭한 검을 달라고 요청해보시기 바랍니다.', },
    {'드락마의 검을 은빛십자군 마상시합 광장에 있는 신비술사 탤리스에게 가져가야 합니다.', },
  },
  [13670] = {
    '겨울의 칼날',
    {'아이언포지 최고 용사의 종자가 제게 흥미로운 이야기를 들려주었습니다.$B$B수정노래 숲 남쪽, 수정 계곡 출구를 내려다보는 곳에 화염군주 에버블레이즈가 있습니다. 그를 처치하고 그 시체에서 영원히 타오르는 불씨를 얻으십시오.$B$B그 불씨를 울부짖는 협만 북쪽에 있는 겨울의 숨결 호수 한가운데로 가져가서, 얼음에 갇힌 여신을 풀어주는 데 사용하십시오. 그녀는 고대의 검의 수호자인데, 자신을 풀어주는 사람에게 그 검을 선물할 것입니다.$B$B당신의 진가를 보여주고, 그녀가 그렇게 오랫동안 지켜왔던 검을 받아 오십시오, $N|1이여;여;.', },
    {'은빛십자군 마상시합 광장에 있는 신비술사 탤리스가 겨울 칼날을 가져다 달라고 했습니다.', },
  },
  [13671] = {
    '전장에서 단련하기',
    {'마상시합은 동료를 상대로 자신의 기술을 시험하는 기회이지만, 진짜 적인 스컬지가 그동안 잠잠히 있어 주진 않는다는 것을 잊지 마십시오. 다른 이를 상대로 기술을 연마할 때도 적을 상대하는 전투 감각이 무뎌져선 안 됩니다. 당신이 명예와 용기로 스컬지를 상대할 수 있음을 보여주십시오. 그런 후에야 이 마상시합에 참여할 수 있는 자격을 얻게 될 것입니다.$B$B얼음왕관으로 가서 눈에 띄는 스컬지를 모조리 처치하되, 폭격지에는 가까이 가지 마십시오. 거긴 적의 수가 너무 많습니다.', },
    {'은빛십자군 마상시합 광장에 있는 아바레스 스위프트스트라이크가 얼음왕관 스컬지 8마리를 처치해 달라고 했습니다. 폭격지를 제외한 얼음왕관 전 지역에서 아무 스컬지나 처치하면 됩니다.', },
  },
  [13672] = {
    '도전에 맞서라',
    {'지원자로서의 기술을 완벽히 익혀야만 비로소 지원자의 도전에 응할 수 있습니다. 도전을 통해 당신의 마상전투 기술을 시험하고 용맹전사가 될 준비가 되었는지 가늠해 볼 수 있을 겁니다. 이 도전을 통과한다면 당신은 용맹전사로서 당신의 수도 대표단의 일원이 될 수 있습니다.$B$B도전에 임할 준비가 되었다면 저에게 돌아오십시오.', },
    {'지원자의 도전에 응할 준비가 되었음을 증명하기 위해, 은빛십자군 마상시합 광장에 있는 신비술사 탤리스에게 지원자의 문장 15개를 가져가야 합니다.', },
  },
  [13673] = {
    '용사에게 걸맞은 검',
    {'어떤 용사들은 노스렌드에서 생겨난 고대 무기가 마상시합에 참가할 때 행운을 가져다준다고 믿습니다. 저는 이 무기를 찾는데 도움을 주기로 했습니다.$B$B회색 구릉지에 전해 내려오는 오래된 전설에 따르면, 한 여신이 저주를 받아 개구리의 모습으로 변하여 오랫동안 지켜 왔던 검을 보호한다고 합니다. 지금은 잿빛나무 초소 바로 동쪽의 잿빛나무 호숫가에서 다른 호수 개구리 사이에 섞여 산다고 들었습니다. 저주를 푸는 유일한 방법은 서슴없이 입맞춤을 하는 것뿐입니다. 입맞춤을 하기 전에 이 보호제를 바르십시오, $N 님. 그렇지 않으면 버짐에 걸릴 테니까요.', },
    {'은빛십자군 마상시합 광장에 있는 마법학자 에디엔 선할로우에게 잿빛나무 검을 가져가야 합니다.', },
  },
  [13674] = {
    '훌륭한 무기',
    {'우리 용사들은 예전부터 여기 노스렌드에서 만들어진 고대 무기를 좋아합니다.$B$B저는 외로운 여신에게 겨울 히아신스를 바쳐 선물을 받는 여행자들의 이야기를 음유 시인들의 입으로 전해 들어왔지요. 히아신스는 수정노래 숲이 얼음왕관과 만나는 북서쪽 경계에 있는 무쇠벽 둑에서 떠내려오는 얼음 위에서만 자란답니다. 그 꽃을 꺾어다가 줄드락과 회색 구릉지 근처, 용의 안식처 북동쪽에 있는 드락마 호수에서 등불로 둘러싸인 곳으로 가져가 주십시오. 외로운 여신에게 훌륭한 검을 달라고 요청해보시기 바랍니다.', },
