  [13677] = {
    '고삐 다루는 법 익히기',
    nil,
    {'근접 표적에 찌르기를 5번 사용하고, 약해진 원거리 표적 2개에 방패 부수기를 사용하고, 약해진 돌진 표적 2개에 돌진을 사용한 후 은빛십자군 마상시합 광장의 갈라시아 브라이트돈에게 가야 합니다.$B$B창을 잃어버리면 마구간 근처의 아무 창 선반이나 선리버 막사에서 다른 창을 얻을 수 있습니다.', },
  },
  [13678] = {
    '도전에 맞서라',
    {'지원자로서의 기술을 완벽히 익혀야만 비로소 지원자의 도전에 응할 수 있습니다. 도전을 통해 당신의 마상전투 기술을 시험하고 용맹전사가 될 준비가 되었는지 가늠해 볼 수 있을 겁니다. 이 도전을 통과한다면 당신은 용맹전사로서 당신의 수도 대표단의 일원이 될 수 있습니다.$B$B도전에 임할 준비가 되었다면 저에게 돌아오십시오.', },
    {'지원자의 도전에 응할 준비가 되었음을 증명하기 위해, 은빛십자군 마상시합 광장에 있는 마법학자 에디엔 선할로우에게 지원자의 문장 15개를 가져가야 합니다.', },
  },
  [13679] = {
    '지원자의 도전',
    {'지원자가 되어 훈련하느라 수고 많으셨습니다, $N|1이여;여;. 이제 당신의 마상전투 기술을 숙련된 상대에게 시험해 볼 때가 왔습니다.$B$B당신의 창을 착용하고 막사 밖 마구간으로 가, 길들인 쿠엘도레이 군마에 올라타십시오.$B$B마상시합 광장 북쪽, 지원자의 투기장에 종자 데이비드가 있을 겁니다. 당신이 준비된 걸 확인하면 데이비드가 도전 상대를 불러줄 겁니다.$B$B행운을 빕니다, $N|1이여;여;. 부디 탈것의 움직임과 당신의 겨냥에 흐트러짐이 없기를.', },
    {'종자 데이비드와 대화하여 도전 상대를 소환해야 합니다. 은빛십자군 용맹전사를 물리친 다음 은빛십자군 마상시합 광장에 있는 신비술사 탤리스에게 가야 합니다.$B$B창을 잃으면 마구간 근처나 은빛 서약단 막사에 있는 창 선반에서 다른 창을 구할 수 있습니다.', },
  },
  [13680] = {
    '지원자의 도전',
    {'지원자가 되어 훈련하느라 수고 많으셨습니다, $N|1이여;여;. 이제 당신의 마상전투 기술을 숙련된 상대에게 시험해 볼 때가 왔습니다.$B$B당신의 창을 착용하고 막사 밖 마구간으로 가, 선리버 매타조에 올라타십시오. $B$B마상시합 광장 북쪽, 지원자의 투기장에 종자 데이비드가 있을 겁니다. 인간 꼬마 아이이죠. 그 아이가 당신이 준비가 끝났는지 확인한 뒤, 도전 상대를 불러줄 겁니다.$B$B행운을 빕니다, $N|1이여;여;. 부디 탈것의 움직임과 당신의 겨냥에 흐트러짐이 없기를.', },
    {'종자 데이비드와 대화하여 도전 상대를 소환해야 합니다. 은빛십자군 용맹전사를 물리친 다음 은빛십자군 마상시합 광장에 있는 마법학자 에디엔 선할로우에게 가야 합니다.', },
  },
  [13681] = {
    '이 돌덩어리는 어디에서 왔을까',
    {'건물 공사에 쓸 돌덩어리 구하는 일은 정말 끔찍해, 끔찍! 폐허 중에서도 안전한 곳에 가면 발에 차이는 게 돌덩어리인데, 저 고귀하신 성기사님들은 그걸 쓸 수는 없으시대요. "귀한 유적"이라나 뭐라나.$B$B그렇다고 허구한 날 불평만 늘어놓는다고 건물이 저절로 지어질 턱은 없고. 폭풍우 봉우리의 눈봉우리 분지와 발명가의 도서관에 가면 티탄의 유적 돌덩어리가 엄청 많아. 여기 나의, 그러니까, "끌"을 가져가서 큰 돌덩어리를 쓸만한 크기의 작은 돌덩어리로 깨뜨려 가져와.', },
    {'은빛십자군 마상시합 광장에 있는 보즐 블라스트볼트에게 작은 돌덩어리 15개를 가져가야 합니다.', },
  },
  [13682] = {
