  [13741] = {
    '용사에게 걸맞은 검',
    {'품질 좋고 순수한, 잘 연마한 강철이면 다 되는 게 아니라오. 다른 경쟁자들은 마력이 깃든 검을 사용한단 말이오. 내겐 불리한 일이지만, 그렇다고 방법이 없는 건 아니지.$B$B회색 구릉지에서 전해 내려오는 오래된 전설에 따르면, 어떤 검을 오랫동안 수호했던 한 여신이 저주를 받아 개구리가 되어 버렸다더군. 그녀는 잿빛나무 초소 바로 동쪽의 잿빛나무 호숫가에서 다른 호수 개구리 사이에 섞여 산다고 들었소. 저주를 푸는 방법은 하나뿐이라오. 개구리가 된 여신에게 스스로 입맞춤을 해야 하지. 입맞춤을 하기 전에 이 보호제를 바르시오, $N. 그러지 않으면 버짐에 걸릴 테니.', },
    {'잿빛나무 검을 구해 은빛십자군 마상시합 광장에 있는 라나 스타우트해머에게 가져가야 합니다.', },
  },
  [13742] = {
    '훌륭한 무기',
    nil,
    {'은빛십자군 마상시합 광장에 있는 라나 스타우트해머에게 드락마의 검을 가져가야 합니다.', },
  },
  [13743] = {
    '겨울의 칼날',
    {'내 검을 부러뜨리다니, 망할 죽음의 기사 같으니!$B$B수정노래 숲 남부, 수정 계곡의 출구를 내려다보는 곳에서 화염군주 에버블레이즈를 찾을 수 있을 거요. 그를 없애고 영원히 타오르는 불씨를 찾으시오.$B$B그걸 울부짖는 협만 북부에 있는 겨울의 숨결 호수 중앙으로 가져가서, 얼음에 갇힌 여신을 해방해 주시오. 그녀는 고대 검의 수호자로서 검의 새 주인이 나타나 자신을 해방해 주기를 기다리고 있다오.$B$B당신의 가치를 증명하고 그녀가 오랫동안 지켜 온 그 검을 받아 오시오, $N.', },
    {'은빛십자군 마상시합 광장에 있는 라나 스타우트해머가 겨울 칼날을 가져다 달라고 했습니다.', },
  },
  [13744] = {
    '용맹전사의 실전 훈련',
    {'마상시합은 동료를 상대로 자신의 기술을 시험하는 기회이지만, 진짜 적인 스컬지가 그동안 잠잠히 있어 주진 않는다는 것을 잊지 말게나. 다른 이를 상대로 기술을 연마할 때도 적을 상대하는 전투 감각이 무뎌져선 안 되네.$B$B전사한 영웅의 계곡의 남서쪽, 코프레타르 북쪽으로 가게. 거기서 스컬지가 죽은 영웅들을 일으켜 우리를 상대하게 하고 있네. 스컬지의 졸개가 된 이들을 쓰러뜨리게. 전투 감각을 갈고 닦는 훈련도 되면서, 동료의 명예도 지킬 수 있을 거네.', },
    {'은빛십자군 마상시합 광장에 있는 롤로 슈어샷이 조종 당하는 영웅 10명을 처치해 달라고 했습니다.', },
  },
  [13745] = {
    '최고 마상전',
    nil,
    {'은빛십자군 마상시합 광장에 있는 클라라 텀블브루에게 용맹전사의 징표 3개를 가져가야 합니다.$B$B창을 잃으면 마구간 근처나 은빛 서약단 막사에 있는 창 선반에서 다른 창을 구할 수 있습니다.', },
  },
  [13746] = {
    '용사에게 걸맞은 검',
    {'품질 좋고 순수한, 잘 연마한 강철이면 다 되는 게 아니라오. 다른 경쟁자들은 마력이 깃든 검을 사용한단 말이오. 내겐 불리한 일이지만, 그렇다고 방법이 없는 건 아니지.$B$B회색 구릉지에서 전해 내려오는 오래된 전설에 따르면, 어떤 검을 오랫동안 수호했던 한 여신이 저주를 받아 개구리가 되어 버렸다더군. 그녀는 잿빛나무 초소 바로 동쪽의 잿빛나무 호숫가에서 다른 호수 개구리 사이에 섞여 산다고 들었소. 저주를 푸는 방법은 하나뿐이라오. 개구리가 된 여신에게 스스로 입맞춤을 해야 하지. 입맞춤을 하기 전에 이 보호제를 바르시오, $N. 그러지 않으면 버짐에 걸릴 테니.', },
    {'잿빛나무 검을 구해 은빛십자군 마상시합 광장에 있는 암브로스 볼트스파크에게 가져가야 합니다.', },
  },
  [13747] = {
    '훌륭한 무기',
    nil,
    {'은빛십자군 마상시합 광장에 있는 암브로스 볼트스파크에게 드락마의 검을 가져가야 합니다.', },
  },
  [13748] = {
    '겨울의 칼날',
    {'앞으로 죽음의 기사와는 절대 연습 경기를 하지 않겠습니다.$B$B수정노래 숲 남쪽, 수정 계곡 출구를 내려다보는 곳에 화염군주 에버블레이즈가 있습니다. 그를 처치하고 그 시체에서 영원히 타오르는 불씨를 얻으십시오.$B$B그 불씨를 울부짖는 협만 북쪽에 있는 겨울의 숨결 호수 한가운데로 가져가서, 얼음에 갇힌 여신을 풀어주는 데 사용하십시오. 그녀는 고대의 검의 수호자인데, 자신을 풀어주는 사람에게 그 검을 선물할 것입니다.$B$B당신의 진가를 보여주고, 그녀가 그렇게 오랫동안 지켜왔던 검을 받아 오십시오, $N|1이여;여;.', },
    {'은빛십자군 마상시합 광장에 있는 암브로스 볼트스파크가 겨울 칼날을 가져다 달라고 했습니다.', },
  },
  [13749] = {
    '용맹전사의 실전 훈련',
    {'마상시합은 동료를 상대로 자신의 기술을 시험하는 기회이지만, 진짜 적인 스컬지가 그동안 잠잠히 있어 주진 않는다는 것을 잊지 말게나. 다른 이를 상대로 기술을 연마할 때도 적을 상대하는 전투 감각이 무뎌져선 안 되네.$B$B전사한 영웅의 계곡의 남서쪽, 코프레타르 북쪽으로 가게. 거기서 스컬지가 죽은 영웅들을 일으켜 우리를 상대하게 하고 있네. 스컬지의 졸개가 된 이들을 쓰러뜨리게. 전투 감각을 갈고 닦는 훈련도 되면서, 동료의 명예도 지킬 수 있을 거네.', },
    {'은빛십자군 마상시합 광장에 있는 티킨 기어스패너가 조종 당하는 영웅 10명을 처치해 달라고 했습니다.', },
  },
  [13750] = {
    '최고 마상전',
    nil,
    {'은빛십자군 마상시합 광장에 있는 플리킨 기어스패너에게 용맹전사의 징표 3개를 가져가야 합니다.$B$B창을 잃으면 마구간 근처나 은빛 서약단 막사에 있는 창 선반에서 다른 창을 구할 수 있습니다.', },
  },
  [13752] = {
    '용사에게 걸맞은 검',
    {'품질 좋고 순수한, 잘 연마한 강철이면 다 되는 게 아니라오. 다른 경쟁자들은 마력이 깃든 검을 사용한단 말이오. 내겐 불리한 일이지만, 그렇다고 방법이 없는 건 아니지.$B$B회색 구릉지에서 전해 내려오는 오래된 전설에 따르면, 어떤 검을 오랫동안 수호했던 한 여신이 저주를 받아 개구리가 되어 버렸다더군. 그녀는 잿빛나무 초소 바로 동쪽의 잿빛나무 호숫가에서 다른 호수 개구리 사이에 섞여 산다고 들었소. 저주를 푸는 방법은 하나뿐이라오. 개구리가 된 여신에게 스스로 입맞춤을 해야 하지. 입맞춤을 하기 전에 이 보호제를 바르시오, $N. 그러지 않으면 버짐에 걸릴 테니.', },
    {'잿빛나무 검을 구해 은빛십자군 마상시합 광장에 있는 콜로소스에게 가져가야 합니다.', },
  },
  [13753] = {
    '훌륭한 무기',
    nil,
    {'은빛십자군 마상시합 광장에 있는 콜로소스에게 드락마의 검을 가져가야 합니다.', },
  },
  [13754] = {
    '겨울의 칼날',
    {'앞으로 죽음의 기사와는 절대 연습 경기를 하지 않겠습니다.$B$B수정노래 숲 남쪽, 수정 계곡 출구를 내려다보는 곳에 화염군주 에버블레이즈가 있습니다. 그를 처치하고 그 시체에서 영원히 타오르는 불씨를 얻으십시오.$B$B그 불씨를 울부짖는 협만 북쪽에 있는 겨울의 숨결 호수 한가운데로 가져가서, 얼음에 갇힌 여신을 풀어주는 데 사용하십시오. 그녀는 고대의 검의 수호자인데, 자신을 풀어주는 사람에게 그 검을 선물할 것입니다.$B$B당신의 진가를 보여주고, 그녀가 그렇게 오랫동안 지켜왔던 검을 받아 오십시오, $N|1이여;여;.', },
    {'은빛십자군 마상시합 광장에 있는 콜로소스가 겨울 칼날을 가져다 달라고 했습니다.', },
  },
  [13755] = {
    '용맹전사의 실전 훈련',
    {'마상시합은 동료를 상대로 자신의 기술을 시험하는 기회이지만, 진짜 적인 스컬지가 그동안 잠잠히 있어 주진 않는다는 것을 잊지 말게나. 다른 이를 상대로 기술을 연마할 때도 적을 상대하는 전투 감각이 무뎌져선 안 되네.$B$B전사한 영웅의 계곡의 남서쪽, 코프레타르 북쪽으로 가게. 거기서 스컬지가 죽은 영웅들을 일으켜 우리를 상대하게 하고 있네. 스컬지의 졸개가 된 이들을 쓰러뜨리게. 전투 감각을 갈고 닦는 훈련도 되면서, 동료의 명예도 지킬 수 있을 거네.', },
    {'은빛십자군 마상시합 광장에 있는 산도스가 조종 당하는 영웅 10명을 처치해 달라고 했습니다.', },
  },
  [13756] = {
    '최고 마상전',
    {'최고 마상전은 마상시합에서 가장 중요한 경기입니다. 엑소다르 사람들은 기사 사랑이 각별해서 우리 선수들이 이 경기에서 우승하길 기대하고 있습니다. 우리 전사들이 다른 얼라이언스 수도를 대표하는 용맹전사와 대결하면서 실력을 갈고닦는 것도 바로 그 때문이지요.$B$B당신도 실력을 키우는 게 어떻겠습니까?$B$B막사 밖 마구간으로 가서, 당신의 창를 착용하십시오. 그러고 나서 길들인 하늘안개 엘레크를 타고 용맹전사들에게 도전하십시오. 우리 막사 바로 밖 얼라이언스 용맹전사의 투기장에서 용맹전사들이 기다리고 있습니다.', },
    {'은빛십자군 마상시합 광장에 있는 라니에게 용맹전사의 징표 3개를 가져가야 합니다.$B$B창을 잃으면 마구간 근처나 은빛 서약단 막사에 있는 창 선반에서 다른 창을 구할 수 있습니다.', },
  },
  [13757] = {
    '용사에게 걸맞은 검',
    {'품질 좋고 순수한, 잘 연마한 강철이면 다 되는 게 아니라오. 다른 경쟁자들은 마력이 깃든 검을 사용한단 말이오. 내겐 불리한 일이지만, 그렇다고 방법이 없는 건 아니지.$B$B회색 구릉지에서 전해 내려오는 오래된 전설에 따르면, 어떤 검을 오랫동안 수호했던 한 여신이 저주를 받아 개구리가 되어 버렸다더군. 그녀는 잿빛나무 초소 바로 동쪽의 잿빛나무 호숫가에서 다른 호수 개구리 사이에 섞여 산다고 들었소. 저주를 푸는 방법은 하나뿐이라오. 개구리가 된 여신에게 스스로 입맞춤을 해야 하지. 입맞춤을 하기 전에 이 보호제를 바르시오, $N. 그러지 않으면 버짐에 걸릴 테니.', },
    {'잿빛나무 검을 구해 은빛십자군 마상시합 광장에 있는 제일린 이븐송에게 가져가야 합니다.', },
  },
  [13758] = {
