  [13770] = {
    '겨울의 칼날',
    {'내 검을 부러뜨리다니, 망할 죽음의 기사 같으니!$B$B수정노래 숲 남부, 수정 계곡의 출구를 내려다보는 곳에서 화염군주 에버블레이즈를 찾을 수 있을 거요. 그를 없애고 영원히 타오르는 불씨를 찾으시오.$B$B그걸 울부짖는 협만 북부에 있는 겨울의 숨결 호수 중앙으로 가져가서, 얼음에 갇힌 여신을 해방해 주시오. 그녀는 고대 검의 수호자로서 검의 새 주인이 나타나 자신을 해방해 주기를 기다리고 있다오.$B$B당신의 가치를 증명하고 그녀가 오랫동안 지켜 온 그 검을 받아 오시오, $N.', },
    nil,
  },
  [13771] = {
    '용맹전사의 실전 훈련',
    {'마상시합은 동료를 상대로 자신의 기술을 시험하는 기회이지만, 진짜 적인 스컬지가 그동안 잠잠히 있어 주진 않는다는 것을 잊지 말게나. 다른 이를 상대로 기술을 연마할 때도 적을 상대하는 전투 감각이 무뎌져선 안 되네.$B$B전사한 영웅의 계곡의 남서쪽, 코프레타르 북쪽으로 가게. 거기서 스컬지가 죽은 영웅들을 일으켜 우리를 상대하게 하고 있네. 스컬지의 졸개가 된 이들을 쓰러뜨리게. 전투 감각을 갈고 닦는 훈련도 되면서, 동료의 명예도 지킬 수 있을 거네.', },
    {'은빛십자군 마상시합 광장에 있는 어둠사냥꾼 메질크리가 조종 당하는 영웅 10명을 처치해 달라고 했습니다.', },
  },
  [13772] = {
    '최고 마상전',
    nil,
    {'은빛십자군 마상시합 광장에 있는 가쥬에게 용맹전사의 징표 3개를 가져가야 합니다.$B$B창을 잃으면 마구간 근처나 선리버 막사에 있는 창 선반에서 다른 창을 구할 수 있습니다.', },
  },
  [13773] = {
    '용사에게 걸맞은 검',
    {'얼라이언스 용사들은 전사들을 위해 고대 검을 찾아다닌다고 들었어요. 그들이 그런 특혜를 누리도록 놔둘 수는 없지요.$B$B회색 구릉지에 전해 내려오는 오래된 전설에 따르면, 한 여신이 저주를 받아 개구리의 모습으로 변하여 오랫동안 지켜 왔던 검을 보호한다고 해요. 지금은 잿빛나무 초소 바로 동쪽의 잿빛나무 호숫가에서 다른 호수 개구리 사이에 섞여 산다고 들었어요. 저주를 푸는 유일한 방법은 서슴없이 입맞춤을 하는 것뿐이에요. 입맞춤을 하기 전에 이 보호제를 바르십시오, $N 님. 그렇지 않으면 버짐에 걸릴 테니까요.', },
    {'잿빛나무 검을 구해 은빛십자군 마상시합 광장에 있는 루노크 와일드메인에게 가져가야 합니다.', },
  },
  [13774] = {
    '훌륭한 무기',
    nil,
    {'은빛십자군 마상시합 광장에 있는 루노크 와일드메인에게 드락마의 검을 가져가야 합니다.', },
  },
  [13775] = {
    '겨울의 칼날',
    {'내 검을 부러뜨리다니, 망할 죽음의 기사 같으니!$B$B수정노래 숲 남부, 수정 계곡의 출구를 내려다보는 곳에서 화염군주 에버블레이즈를 찾을 수 있을 거요. 그를 없애고 영원히 타오르는 불씨를 찾으시오.$B$B그걸 울부짖는 협만 북부에 있는 겨울의 숨결 호수 중앙으로 가져가서, 얼음에 갇힌 여신을 해방해 주시오. 그녀는 고대 검의 수호자로서 검의 새 주인이 나타나 자신을 해방해 주기를 기다리고 있다오.$B$B당신의 가치를 증명하고 그녀가 오랫동안 지켜 온 그 검을 받아 오시오, $N.', },
    {'은빛십자군 마상시합 광장에 있는 루노크 와일드메인이 겨울 칼날을 가져다 달라고 했습니다.', },
  },
  [13776] = {
    '용맹전사의 실전 훈련',
    {'마상시합은 동료를 상대로 자신의 기술을 시험하는 기회이지만, 진짜 적인 스컬지가 그동안 잠잠히 있어 주진 않는다는 것을 잊지 말게나. 다른 이를 상대로 기술을 연마할 때도 적을 상대하는 전투 감각이 무뎌져선 안 되네.$B$B전사한 영웅의 계곡의 남서쪽, 코프레타르 북쪽으로 가게. 거기서 스컬지가 죽은 영웅들을 일으켜 우리를 상대하게 하고 있네. 스컬지의 졸개가 된 이들을 쓰러뜨리게. 전투 감각을 갈고 닦는 훈련도 되면서, 동료의 명예도 지킬 수 있을 거네.', },
    {'은빛십자군 마상시합 광장에 있는 던 레이지토템이 조종 당하는 영웅 10명을 처치해 달라고 했습니다.', },
  },
  [13777] = {
    '최고 마상전',
    {'최고 마상전은 마상시합에서 가장 중요한 경기지요. 우승이 쉽진 않겠지만, 우린 분명히 해낼 수 있을 거예요. 탈것과 일심동체가 될 때까지 훈련해야 해요.$B$B이 훈련은 냉엄한 승부의 세계를 경험하는 데 도움이 될 겁니다.$B$B막사 밖 마구간으로 가서, 그대의 창을 착용하세요. 그러고 나서 길들인 멀고어 코도를 타고 용맹전사들에게 도전하세요. 우리 막사 바로 밖 호드 용맹전사의 투기장에서 용맹전사들이 기다리고 있답니다.', },
    {'은빛십자군 마상시합 광장에 있는 안카 클로후프에게 용맹전사의 징표 3개를 가져가야 합니다.$B$B창을 잃으면 마구간 근처나 선리버 막사에 있는 창 선반에서 다른 창을 구할 수 있습니다.', },
  },
  [13778] = {
