  [13845] = {
    '봉인된 독약병',
    {'이 독약병은 두드러지게 위험해 보입니다. 아마도 달라란에 있는 누군가가 이 약병에 관심을 보일 겁니다.', },
    nil,
  },
  [13846] = {
    '대의에 기부하라',
    {'가장 최고의 용사들은 매일 은빛 고해사제님의 말씀을 듣고자 예배당으로 오신답니다.$B$B당신도 고해사제님께서 얼마나 용사들을 격려하는지 보셔야 해요, $N 님. 용사들은 은빛십자군을 위해 싸울 뿐만 아니라, 제가 기부금 바구니를 들고 돌아다니면 기부를 많이 하신답니다.$B$B당신도 함께 기부하고자 하시나요?', },
    {'종자 아티와 대화하여 은빛십자군의 대의를 위해 10골드를 기부해야 합니다.', },
  },
  [13847] = {
    '적의 관문에서',
    {'우리가 마상시합을 준비하는 동안, 은빛십자군 군대는 얼음왕관 성채의 적 요새에 공격을 가하려고 방법을 강구하고 있어요.$B$B얼음왕관 남서쪽 뼈의 궁정에 있는 소수의 용감한 성전사들이 코프레타르의 남쪽에 전진 기지를 마련했습니다. 창을 착용하고 기지에서 길들인 은빛십자군 군마를 탄 후 리치 왕의 추종자들을 처치하세요.$B$B해골경비대 보병 집단을 보고 주눅들지 마세요, $N. 당신 말의 발굽 아래 쉽게 뭉개질 자들이니까요.', },
    {'은빛십자군 마상시합 광장에 있는 대장 조셉 홀리가 해골경비대 보병 15마리, 해골경비대 정찰병 10마리, 해골경비대 부관 3마리를 처치해 달라고 했습니다.', },
  },
  [13850] = {
    '독에 대한 내성',
    nil,
    {'운고로 분화구에 있는 모르벡이 맹독가죽 라바사우루스 피에 20번 중독되면 탔을 때 죽지 않는다고 말했습니다. 독을 맞도록 맹독가죽 라바사우루스를 공격해야 합니다.', },
  },
  [13851] = {
    '적의 관문에서',
    {'우리가 마상시합을 준비하는 동안, 은빛십자군 군대는 얼음왕관 성채의 적 요새에 공격을 가하려고 방법을 강구하고 있어요.$B$B얼음왕관 남서쪽 뼈의 궁정에 있는 소수의 용감한 성전사들이 코프레타르의 남쪽에 전진 기지를 마련했습니다. 창을 착용하고 기지에서 길들인 은빛십자군 군마를 탄 후 리치 왕의 추종자들을 처치하세요.$B$B해골경비대 보병 집단을 보고 주눅들지 마세요, $N. 당신 말의 발굽 아래 쉽게 뭉개질 자들이니까요.', },
    {'은빛십자군 마상시합 광장에 있는 클라라 텀블브루가 해골경비대 보병 15마리, 해골경비대 정찰병 10마리, 해골경비대 부관 3마리를 처치해 달라고 했습니다.', },
