  [14084] = {
    '던 모로에서 탈것 타기를 배우세요!',
    {'타기 훈련과 탈것을 광고하는 이 전단지는 빈지 페더휘슬이 발송한 것입니다.$B$B빈지는 던 모로, 카라노스의 북동쪽에 있는 스틸그릴의 정비소에서 만날 수 있습니다.', },
    {'던 모로의 스틸그릴의 정비소에 있는 빈지 페더휘슬에게 이 전단지를 가져가야 합니다.', },
  },
  [14085] = {
    '다르나서스에서 탈것 타기를 배우세요!',
    {'타기 훈련과 탈것을 광고하는 이 전단지는 잘트삼이 발송한 것입니다.$B$B잘트삼은 다르나서스에 있는 세나리온 자치령에서 만날 수 있습니다.', },
    {'다르나서스의 세나리온 자치령에 있는 잘트삼에게 이 전단지를 가져가야 합니다.', },
  },
  [14086] = {
    '오그리마에서 탈것 타기를 배우세요!',
    {'타기 훈련과 탈것을 광고하는 이 전단지는 킬다르가 발송한 것입니다.$B$B킬다르는 오그리마에 있는 명예의 골짜기에서 만날 수 있습니다.', },
    {'오그리마의 명예의 골짜기에 있는 킬다르에게 이 전단지를 가져가야 합니다.', },
  },
  [14087] = {
    '멀고어에서 탈것 타기를 배우세요!',
    {'타기 훈련과 탈것을 광고하는 이 전단지는 카르 스톰싱어가 발송한 것입니다.$B$B카르는 멀고어에 있는 블러드후프 마을에서 만날 수 있습니다.', },
    {'멀고어의 블러드후프 마을에 있는 카르 스톰싱어에게 이 전단지를 가져가야 합니다.', },
  },
  [14088] = {
    '듀로타에서 탈것 타기를 배우세요!',
    {'타기 훈련과 탈것을 광고하는 이 전단지는 살티가 발송한 것입니다.$B$B살티는 듀로타 남쪽에 있는 센진 마을에서 만날 수 있습니다.', },
    {'듀로타 남쪽의 센진 마을에 있는 살티에게 이 전단지를 가져가야 합니다.', },
  },
  [14089] = {
    '티리스팔 숲에서 탈것 타기를 배우세요!',
    {'타기 훈련과 탈것을 광고하는 이 전단지는 벨마 워넘이 발송한 것입니다.$B$B벨마는 티리스팔 숲에 있는 브릴에서 만날 수 있습니다.', },
    {'티리스팔 숲의 브릴에 있는 벨마 워넘에게 이 전단지를 가져가야 합니다.', },
  },
  [14090] = {
    '스노볼트를 찾는 고르목',
    {'부하 두 명이 꿰뚫는 자 고르목이라 불리는 마그나타우르를 가둬 두러 갔다가 목숨을 잃었는데, 그 짐승은 아직도 날뛰고 있습니다. 좋아하는 스노볼트가 없다고 몇 시간째 울부짖는 중이지요.$B$B은빛십자군은 원형경기장에서 이 괴물을 쓰고 싶어하지 않아서, 제 손으로 처리할 생각입니다. 이 짐승에게 장난감을 가져다줄 시간인 듯합니다. 이 그물을 가지고 폭풍우 봉우리 남동쪽에 있는 가름의 파멸로 가십시오. K3에서 바로 북동쪽입니다. 스노볼트를 몇 마리 잡으면서 맞는 놈이 걸리면 빛에 감사 기도를 올리십시오.', },
    {'무거운 그물로 설맹의 추종자 8마리를 붙잡아 은빛십자군 마상시합 광장에 있는 사비나 로어송에게 가져가야 합니다.', },
  },
  [14092] = {
    '용사의 아침 식사',
    {'원형경기장이 완성되자, 은빛십자군은 저희에게 용사들이 싸울 괴물들을 돌봐 달라고 요청해왔습니다.$B$B그런데 이 거대한 요르문가르는 식성이 둘째 가라면 서러울 정도로 괴이하지 뭡니까. 녀석은 동족의 알을 먹고 튼튼하게 자라는데, 그중에서도 가장 좋은 건 바로 깊은굴 요르문가르의 알입니다.$B$B이 북을 들고 폭풍우 봉우리 중앙에 있는 눈봉우리 분지로 가십시오. 눈더미 근처에서 북을 치면, 북소리를 듣고 깊은굴 요르문가르가 깨어날 겁니다. 요르문가르를 처치하고 알 주머니를 모아 마상시합 광장으로 돌아오십시오.', },
    {'요르문가르 알 주머니 4개를 은빛십자군 마상시합 광장에 있는 틸로스 돈러너에게 가져가야 합니다.', },
  },
  [14095] = {
    '유해 식별',
    {'크라켄 시체에서 온전한 이빨을 구하기란 쉽지 않습니다. 성전사 실버돈이 이 이빨에 관심을 보일 것 같습니다.', },
    {'승리의 증거로 북해 크라켄 이빨을 성전사 실버돈에게 가져가야 합니다.', },
  },
  [14096] = {
    '제대로 한 건 했군, 쿨',
    {'성전사 쿨이 또다시 문제를 일으켰지 뭐예요. 게다가 이번엔 하나도 아닌 여럿이라네요. 마상시합 광장 서쪽에 있는 죽음예언자 감시초소에 야영지를 만들던 이교도를 보고선, 기습 공격에 나설 지원자들을 모았답니다.$B$B안 봐도 뻔하지요. 병력이 충분치 못했던 쿨과 지원자들은 손쉽게 제압당했어요. 우리가 구해내지 못한다면, 이교도 무리가 포로들을 제물로 바치고 말 거예요. 이교도 중에는 검은색 우리의 열쇠를 지닌 자들이 있으니, 열쇠를 찾아 사로잡힌 쿨과 지원자들을 풀어주세요.', },
    {'못 말리는 쿨과 4명의 사로잡힌 지원자들을 죽음예언자 감시초소에서 구하고 은빛십자군 마상시합 광장에 있는 나라시 스노우돈에게 가야 합니다.', },
  },
  [14101] = {
    '드로틴 흐로스가르',
    nil,
    {'은빛십자군 마상시합 광장에 있는 고위 성전사 아델라드가 드로틴 흐로스가르를 물리쳐 달라고 했습니다.', },
  },
  [14102] = {
    '안개부름이 잉그바르',
    {'크발디르는 노스렌드의 해안으로 철수했소. 하지만 이들을 가만 놔둔다면, 분명히 우리의 공급 물자와 지원 병력이 도착하지 못하도록 방해 공작을 펼 거요. 그들 중에서도 안개부름이 잉그바르라 하는 자는 흐로스가르 상륙지 서쪽의 동굴에 자신의 거처를 마련해 놓았소. 그곳에서 안개를 조종하여, 크발디르와 그들이 부리는 바다짐승을 보이지 않도록 은폐하고 있다오.$B$B이 부적을 가지고 마상시합 광장 북쪽의 해협을 가로질러 흐로스가르 상륙지로 가시오. 안개부름이의 동굴을 찾아 부적을 사용하면 그를 찾아낼 수 있소.', },
    {'은빛십자군 마상시합 광장에 있는 고위 성전사 아델라드가 안개부름이 잉그바르를 처치해 달라고 했습니다.', },
  },
  [14103] = {
    '티타늄 가루',
    nil,
    nil,
  },
  [14104] = {
    '칼부림 오르놀프',
    nil,
    nil,
  },
  [14105] = {
    '죽음예언자 카로스',
    {'저주받은 자들의 교단이 우리를 파괴하려는 공세를 한층 강화했소. 아주 맹랑하게도 자신들의 전력을 마상시합 광장 서쪽 마루에 있는 죽음예언자 감시초소에 집결하고 있지 뭐요.$B$B무자비한 죽음예언자 카로스와 그의 부하들은 무장을 강화하고 이교도의 무기를 있는 데로 휘두를 준비에 여념이 없소.$B$B내 부하들과 용맹전사들은 광장을 방어할 준비를 하고 있지만, 사실 우리에게 필요한 것은 그대와 같은 용사라오. $N, 저들의 공격을 무력화하는데 앞장서주었으면 하오. 죽음예언자 카로스를 찾아 처치해주시오.', },
    {'은빛십자군 마상시합 광장에 있는 고위 성전사 아델라드가 죽음예언자 카로스를 처치해 달라고 했습니다.', },
  },
  [14107] = {
    '죽은 자의 운명',
    {'은빛십자군이 리치 왕에게 도전한 최초 세력은 아니오. 적은 전사한 자들의 영혼으로 하여금 우리를 공격하도록 조종하고 있소.$B$B그 영혼들은 남서쪽의 알두르타르와 무쇠벽 성채 사이에 있는 전사한 영웅의 계곡을 떠돌아다닌다오. 계곡 전체에는 스컬지가 영혼을 조종할 때 사용하는 버려진 영혼의 수정이 흩어져 있소. 영혼의 수정을 모아 성스러운 축복의 유물과 함께 사용하면 영혼에게 평화의 축복을 선사할 수 있소. 그들에게는 영혼의 안식 그 이상의 의미가 있는 일이라오.', },
    {'성전사 실버돈이 전사한 영웅의 영혼 6명에게 안식을 되찾아 달라고 했습니다.', },
  },
  [14108] = {
    '크라켄을 잡아라!',
    {'선리버와 은빛 서약단은 흐로스가르 상륙지에 있는 크발디르의 전투를 위해 북쪽을 향해 배를 띄웠소. 하지만 도중에 크발디르 해적선의 방해를 받아 공격이 지체되고 있소. 설상가상으로 크발디르는 북해에 서식하는 무시무시한 크라켄을 불러내는 방법을 알아냈다 하오. 배에 탄 이들을 살려내려면 크라켄을 몰아내고 크발디르의 소환 의식을 저지하는 수밖에 없소.$B$B성전사여, 이 창을 들고 은빛십자군 히포그리프를 타면 전투 지역으로 곧장 태워다줄 것이오.', },
    nil,
  },
  [14112] = {
    '설인에게는 뭘 먹이세요?',
    {'십자군은 설인 한 마리를 잡아와 얼음울음이라는 이름을 지어주었지요. 그리고 원형경기장 전투에 내보내기 전에 우리에게 훈련과 관리를 맡아달라고 부탁했어요.$B$B녀석의 먹이로 남아도는 가축이나 다름없는 선리버 녀석들을 쓰자고 제안했지만, 십자군은 썩 내켜 하질 않더군요. 얼음울음은 상어 고기라면 사족을 못 쓴답니다. 그러니 부탁 좀 드려도 될까요? 북쪽 해협을 떠다니는 은빛 칼날호로 날아가세요. 배에 있는 양동이에서 신선한 미끼를 꺼내 물 위에 뿌리면, 곧 상어가 다가올 겁니다.', },
    {'은빛십자군 마상시합 광장에 있는 사비나 로어송이 북해 상어 고기 3개를 가져다 달라고 했습니다.', },
  },
  [14136] = {
    '해상 구조',
    {'크발디르가 북쪽에 있는 작은 섬인 흐로스가르 상륙지에 야영지를 세웠다고 합니다. 저희는 애서스 파이어호크 선장을 그 지역으로 보냈는데, 마상시합 광장으로 향하던 크발디르 약탈자 무리와 마주쳤다는군요! 불꽃매호는 지금 공격을 받는 중이며 수적으로도 아주 불리합니다. 지금 불꽃매호로 가셔서 선리버를 공격하면 어떻게 되는지 크발디르 놈들에게 확실히 보여주십시오.$B$B불꽃매호는 북쪽의 흐로스가르 상륙지와 마상시합 광장 사이에 있습니다. 바다 학살자의 터 서쪽이지요.', },
    {'은빛십자군 마상시합 광장에 있는 피의 여전사 지라나가 불꽃매호에 올라타서 크발디르 광전사 8명과 크발디르 작살병 3명을 처치하라고 했습니다.', },
  },
  [14140] = {
    '침략자를 저지하라',
    {'무시무시한 브리쿨 약탈자인 크발디르는 저희 배를 파괴하고 북쪽 섬에 거주하는 투스카르를 살해한 다음, 그곳에 흐로스가르 상륙지라는 이름을 붙였어요. 크발디르가 그곳에 기반을 마련한다면 저희가 다음 목표가 될 것이 불 보듯 뻔하답니다.$B$B십자군 병력은 스컬지를 상대하느라 여력이 없으니 저희가 마상시합 광장을 지켜야만 해요. 섬이 나올 때까지 북쪽으로 날아간 다음, 크발디르가 또 무언가 파괴하기 전에 처치해 주세요.', },
    {'은빛십자군 마상시합 광장에 있는 피의 여전사 지라나가 크발디르 10명을 처치해 달라고 했습니다.', },
  },
  [14141] = {
    '스노볼트를 찾는 고르목',
    {'부하 두 명이 꿰뚫는 자 고르목이라 불리는 마그나타우르를 가둬 두러 갔다가 목숨을 잃었는데, 그 짐승은 아직도 날뛰고 있습니다. 좋아하는 스노볼트가 없다고 몇 시간째 울부짖는 중이지요.$B$B은빛십자군은 원형경기장에서 이 괴물을 쓰고 싶어하지 않아서, 제 손으로 처리할 생각입니다. 이 짐승에게 장난감을 가져다줄 시간인 듯합니다. 이 그물을 가지고 폭풍우 봉우리 남동쪽에 있는 가름의 파멸로 가십시오. K3에서 바로 북동쪽입니다. 스노볼트를 몇 마리 잡으면서 맞는 놈이 걸리면 빛에 감사 기도를 올리십시오.', },
    {'무거운 그물로 설맹의 추종자 8마리를 붙잡아 은빛십자군 마상시합 광장에 있는 틸로스 돈러너에게 가져가야 합니다.', },
  },
  [14142] = {
    '제대로 한 건 했군, 쿨',
    nil,
    {'못 말리는 쿨과 사로잡힌 지원자 4명을 죽음예언자 감시초소에서 구한 다음 은빛십자군 마상시합 광장에 있는 피의 여전사 지라나에게 가야 합니다.', },
  },
  [14143] = {
    '다리 지원',
    nil,
    {'도난당한 타조 다리 10개를 되찾아 은빛십자군 마상시합 광장에 있는피의 여전사 지라나에게 가져가야 합니다.', },
  },
  [14144] = {
    '빛의 자비',
    {'저는 페일트리스 고해사제님께서 하시는 말씀을 귀담아들어 왔고, 그로써 여러 가지를 생각했어요. 우린 십자군 곁에서 싸우지만 그들에게서 배울 수도 있습니다.$B$B북쪽 해안은 흐로스가르 상륙지로, 크발디르가 옛 투스카르 마을을 침략하고 주민을 살해한 곳입니다. 이 기도서를 가지고 가 주민의 시체 근처에서 죽은 자를 위한 성사를 해 주세요. 그러면 그 영혼이 섬을 뒤덮은 안개를 벗어나 평안을 얻을 겁니다.', },
    {'고해사제의 기도서를 사용하여 살해된 투알리크 주민 8명에게 종부성사를 해 주고 은빛십자군 마상시합 광장에 있는 피의 여전사 지라나에게 돌아와야 합니다.', },
  },
  [14145] = {
    '설인에게는 뭘 먹이세요?',
    {'십자군은 설인을 잡아와 얼음울음이라는 이름을 지어주었지요. 원형경기장 전투에 내보내기 전에 우리에게 훈련과 관리를 맡아달라고 부탁했습니다.$B$B야수의 먹이로 은빛 서약단 녀석들을 활용하자고 제안했지만 십자군은 썩 내켜 하지 않더군요. 이 녀석은 상어 고기를 아주 좋아합니다. 그러니 부탁 좀 드려도 되겠습니까? 북쪽 해협을 떠다니는 진홍빛 서광호로 날아가십시오. 배에 있는 양동이에서 신선한 미끼를 꺼내 물 위에 뿌리면 상어가 곧 다가올 겁니다.', },
    {'은빛십자군 마상시합 광장에 있는 틸로스 돈러너가 북해 상어 고기 3개를 가져다 달라고 했습니다.', },
  },
  [14151] = {
    '선홍빛 루비',
    {'선홍빛 루비는 노스렌드에서 가장 좋은 보석이라고 전해진답니다. 선홍빛 루비는 태양볕 아래 수정 장미처럼 빛나지요.$B$B이 변환식을 가르쳐 드리기 전에, 당신이 마음의 준비할 수 있도록 다섯 개의 영웅급 보석을 변환해 오셨으면 합니다. 해보고 싶은 걸로 변환하시되, 여러 번 변환하셔도 괜찮습니다. 이번 일을 마치면 저에게 돌아오세요.', },
    {'달라란에 있는 린지 블랙볼트가 연금술로 다섯 개의 영웅급 보석을 성공적으로 변환해 달라고 했습니다. 허용할 수 있는 변환식은 자황수정, 줄의 눈, 공포석, 왕의 호박석, 귀족 지르콘입니다.', },
  },
  [14152] = {
    '해상 구조',
    {'흐로스가르 상륙지에 있는 크발디르를 물리치도록 물마루호와 웨이브크레스트 선장을 북쪽으로 보냈는데 마상시합 광장으로 향하던 크발디르 약탈자 무리와 마주쳤다고 해요. 불행하게도 크발디르가 물마루호 선원보다 수적으로 훨씬 우세하여 배를 점점 장악해가고 있다는군요.$B$B북쪽 바다 학살자의 터에 있는 물마루호로 가셔서 공격에 맞서는 선원들을 도와주셨으면 합니다. 가시는 길의 동쪽에서 물마루호를 찾으실 수 있을 거예요.', },
    {'북해로 가서 물마루호를 공격하는 크발디르 광전사 8명과 크발디르 작살병 3명을 처치해야 합니다.', },
  },
  [14160] = {
    '공로장',
    {'이 질좋은 양피지에는 보석세공 전통에 있어서 $N|1이;가; 달성한 다수의 업적이 기록되어 있습니다.$B$B적절한 보상을 받으려면 달라란에 있는 티모시 존스에게 이 공로장을 가져가야 합니다.', },
    {'달라란에 있는 티모시 존스에게 공로장을 가져가야 합니다.', },
  },
  [14163] = {
    '전장으로: 정복의 섬',
    {'얼음왕관에서 우리의 승리가 가까워질수록, 호드 놈들을 손봐줘야 할 필요 또한 커지고 있습니다. 놈들이 리치 왕을 상대할 만큼 크기 전에 말입니다.$B$B우리가 전진하는 걸 막으려고 호드는 코르크론이라는 정예병들을 정복의 섬에 배치했지요. 그 섬은 전략적으로 중요한 지역입니다, $N 님. 우리 군대가 꼭 이기도록 해주세요!', },
    {'정복의 섬 전장에서 승리를 거둔 후 얼라이언스 대도시나 겨울손아귀 호수, 달라란, 샤트라스에 있는 얼라이언스 준장에게 가야 합니다.', },
  },
  [14164] = {
    '전장으로: 정복의 섬',
    {'곧 우리는 노스렌드에서 승리를 거둘 것이오... 하지만 군대를 원활히 수송하고 보급품을 공급하려면 얼음왕관에 확고한 발판을 마련해야 하지.$B$B정복의 섬이 가장 이상적인 장소요. 수비하기는 쉽고 자원은 넘쳐나니까. 얼라이언스 놈들도 같은 결론을 내리고는 7군단을 보내 우리 손에서 통제권을 빼앗으려고 하오.$B$B가시오, $N. 호드는 어떻게 해서든 이긴다는 것을 보여주시오!', },
    {'정복의 섬 전장에서 승리를 거둔 후 호드 대도시나 겨울손아귀 호수, 달라란, 샤트라스에 있는 호드 전쟁인도자에게 가야 합니다.', },
  },
  [14166] = {
