  [24663] = {
    '왕관사 무너뜨리기',
    {'이 장부엔 위치가 가득 표시되어 있군. 이봐, 자네가 이 기록을 조사하고 조치를 해줬으면 해. 더는 이 일을 질질 끌 수 없어.$B$B내가 말한 곳을 조사해줘. 동부 내륙지에 있는 맹금의 봉우리 동쪽으로 가면 돼. 얼른 가서 놈들에게 본보기를 보여줘.$B$B아, 그리고 만약 크고 위험해 보여서 파괴해야 할 것 같은 무언가를 발견하면 이 폭탄을 던지라고.', },
    {'동부 내륙지의 맹금의 봉우리 동쪽에 있는 왕관 요원 5명을 처치하고 스내글볼트의 코륨 폭탄으로 화학약품 수레 하나를 파괴한 후 스톰윈드에 있는 수사반장 스닙 스내글볼트에게 가야 합니다.', },
  },
  [24664] = {
    '왕관사 무너뜨리기',
    {'이 장부엔 위치가 가득 표시되어 있군. 이봐, 자네가 이 기록을 조사하고 조치를 해줬으면 해. 더는 이 일을 질질 끌 수 없어.$B$B내가 말한 곳을 조사해줘. 여명의 설원에 있는 눈망루 마을로 가면 돼. 오, 이번 건은 개인적인 일이야. 얼른 가서 놈들에게 본보기를 보여줘.$B$B아, 그리고 만약 크고 위험해 보여서 파괴해야 할 것 같은 무언가를 발견하면 이 폭탄을 던지라고.', },
    {'여명의 설원에 있는 눈망루 마을의 왕관 조직원 5명을 처치하고 스내글볼트의 코륨 폭탄으로 화학약품 수레 하나를 파괴한 후 스톰윈드에 있는 수사반장 스닙 스내글볼트에게 돌아가야 합니다.', },
  },
  [24665] = {
    '왕관사 무너뜨리기',
    {'이 장부엔 위치가 가득 표시되어 있군. 이봐, 자네가 이 기록을 조사하고 조치를 해줬으면 해. 더는 이 일을 질질 끌 순 없어.$B$B내가 말한 곳을 조사해줘. 테로카르 숲에 있는 샤트라스의 동쪽으로 보이는군. 얼른 가서 놈들에게 본보기를 보여줘.$B$B아, 그리고 만약 크고 위험해 보여서 파괴해야 할 것 같은 무언가를 발견하면 이 폭탄을 던지라고.', },
    {'테로카르 숲에 있는 샤트라스 동쪽의 왕관 청부업자 5명을 처치하고 스내글볼트의 코륨 폭탄으로 화학약품 수레 하나를 파괴한 후 스톰윈드에 있는 수사반장 스닙 스내글볼트에게 가야 합니다.', },
  },
  [24666] = {
    '왕관사 무너뜨리기',
    {'이 장부엔 위치가 가득 표시되어 있군. 이봐, 자네가 이 기록을 조사하고 조치를 해줬으면 해. 더는 이 일을 질질 끌 순 없어.$B$B내가 말한 곳을 조사해줘. 황혼의 거울 북서쪽에 있는 수정노래 숲의 쓸쓸한 숲처럼 보이는군. 얼른 가서 놈들에게 본보기를 보여줘.$B$B아, 그리고 만약 크고 위험해 보여서 파괴해야 할 것 같은 무언가를 발견하면 이 폭탄을 던지라고.', },
    {'수정노래 숲의 쓸쓸한 숲에 있는 왕관 화학병 5명을 처치하고 스내글볼트의 코륨 폭탄으로 화학약품 수레 하나를 파괴한 후 스톰윈드에 있는 수사반장 스닙 스내글볼트에게 가야 합니다.', },
  },
  [24682] = {
    '사론의 구덩이',
    {'차원문으로 들어가라, 어서! 반대쪽에서 만나겠다.', },
