  [24800] = {
    '선리버의 승리',
    {'마침내, 선리버와 신도레이의 영광을 위해 쿠엘델라가 복원되었군요! 실버문과 호드 전체가 당신을 영웅으로 추대할 겁니다.$B$B선리버의 지도자, 대마법사 에이타스 선리버 님께서 당신이 태양샘에서 돌아오면 만나고 싶다고 하셨습니다. 쿠엘델라를 들고 최대한 빨리 달라란의 보랏빛 성채로 가십시오, $N 님. 대마법사님께서 당신을 친히 축하하고 싶어하십니다!', },
    {'달라란의 보랏빛 성채에 있는 대마법사 에이타스 선리버에게 복원된 쿠엘델라를 가져가야 합니다.', },
  },
  [24801] = {
    '선리버의 승리',
    {'마침내, 선리버와 호드의 영광을 위해 쿠엘델라가 복원되었군요! 이것은 리치 왕에 맞서는 우리의 노력에 내리는 축복입니다.$B$B선리버의 지도자, 대마법사 에이타스 선리버 님께서 당신이 태양샘에서 돌아오면 만나고 싶다고 하셨습니다. 쿠엘델라를 들고 최대한 빨리 달라란의 보랏빛 성채로 가십시오, $N 님. 대마법사님께서 당신을 친히 축하하고 싶어하십니다!', },
    {'달라란의 보랏빛 성채에 있는 대마법사 에이타스 선리버에게 복원된 쿠엘델라를 가져가야 합니다.', },
