  [25287] = {
    '연설문 전달',
    {'좋아, 병사! 이제 자네의 능력을 보여줄 때군!$B$B이 연설문을 가지고 \'천둥번개\'에 타게. 땜장이왕 멕카토크에게 가장 빨리 갈 수 있는 비행기이네. 멕카토크는 놈리건 작전이 시작되기 전에 이 연설문이 오기만을 애타게 기다리고 있네.$B$B서두르게!', },
    {'스틸그릴의 정비소 밖에 있는 \'천둥번개\'를 타고 땜장이왕 멕카토크에게 토비의 연설문을 가져가야 합니다.', },
  },
  [25295] = {
    '속사포 시험',
    {'안전 시스템, 작동 중!, 모터 기능, 이상 없음! 이제 속사포 발사 시험만 남았군.$B$B아주 어려운 건 아닐세, $N. 뒤쪽 언덕에 있는 기계보행전차에 인체에 무해한 탄알을 장전했으니까 주변 표적에 몇 발 쏘고 난 뒤 다시 이리로 오게.', },
    {'가까운 표적에 공격형 기계보행전차의 속사포를 시험해야 합니다.', },
  },
  [25306] = {
    '노움 군대!',
    {'[ph] 전쟁!', },
    nil,
  },
  [25393] = {
    '놈리건 작전',
    {'이제 곧 놈리건에 파상 공격이 시작되면 기계박사 텔마플러그와 그의 수하들은 정의의 심판을 받게 될 것일세! 기계박사 텔마플러그는 썩어빠지고 더러운 그의 수하들과 함께 너무나 긴 시간 동안 우리의 고향을 더럽혀 왔네.$B$B공격이 곧 시작될 테니 준비하게, 신병! 비겁자는 용서할 수 없네!', },
    {'멕카토크 왕을 도와 놈리건을 되찾아야 합니다.', },
  },
  [25444] = {
    '완벽한 비밀요원',
    {'무작정 섬에 들어가는 건 바보 같은 짓이지요. 그래서 볼진 님을 위해 정보를 수집할 아주 작은 비밀요원들을 만들 거예요. 볼진 님은 그 섬에서 무슨 일이 일어나는지 알고 싶어하시거든요.$B$B만약 이 일에 관심이 있다면 제가 방법을 설명드릴게요. 먼저 개구리를 잡아야 돼요. 이놈들은 센진 주변의 야자나무 그늘에 있는 걸 좋아해요.$B$B개구리를 다 잡았으면 주문을 걸 수 있게 내 감시의 토템으로 가져오세요.', },
    {'센진 개구리 5마리를 잡아 바니라의 감시의 토템의 주문에 걸리도록 해야 합니다.', },
  },
  [25445] = {
    '잘라제인의 최후',
    {'이제 전쟁이네! 곧 메아리 섬으로 진격할 테니 준비를 단단히 해 두게.$B$B잘라제인이 살 날도 얼마 남지 않았네.', },
    {'볼진이 메아리 섬을 되찾고 잘라제인을 처치하도록 도와야 합니다.', },
  },
  [25446] = {
    '개구리 투하!',
    {'개구리가 준비됐으니, 이제 이걸 섬으로 보내게 도와주세요.$B$B센진 마을에서 동쪽으로 가면 조련사 만렉을 만날 수 있을 거예요. 만렉은 볼진 정찰대가 사용하는 모든 박쥐를 관리하고 있으니까 메아리 섬에 갈 박쥐를 빌릴 수 있을 거예요.$B$B이 개구리 자루를 가지고 가세요. 정령들의 가호를 요청했더니 제 눈으로 봐야 할 곳을 그대에게 보여주려고 연기를 보내 줬어요. 박쥐를 타고 가는 동안 자루 안의 개구리를 꺼내 그 연기 안으로 던져 넣으면 돼요.', },
    {'박쥐를 타고 가는 동안 개구리 자루에 있는 주문에 걸린 개구리 12마리를 메아리 섬에 표시된 지역에 던져 넣어야 합니다.', },
  },
  [25483] = {
    '이벤트 던전 - 코렌 다이어브루',
    nil,
    nil,
  },
  [25484] = {
    '이벤트 던전 - 아훈',
    nil,
    nil,
  },
  [25495] = {
    '전투 준비',
    {'볼진 님은 군 장비와 전사들을 모으고 계시네. 자네보고 바니라와 함께 동쪽 섬에서 만나자고 전해 달라시더군.$B$B볼진 님과 전사들은 그곳에서 잘라제인에 대한 공격을 개시할 걸세. 선조들이 항상 함께하길 비네, $N. 자네가 없었다면 이 작전이 이렇게까지 성공적이지는 않았을 걸세. 남쪽으로 가서 조련사 만렉을 만나보게. 집결지까지 태워줄 박쥐를 내줄 걸세.', },
    {'조련사 만렉과 대화하고 집결지로 날아가십시오. 전투 야영지 동쪽에 있는 섬에서 볼진을 만나야 합니다.', },
  },
  [25500] = {
